Verse.create!([
  {id: 155167, book: "Matt", verse: "1.001", unformatted: "耶稣基督的家谱\n耶稣基督——大卫的后裔、亚伯拉罕的后代——他的家谱：", version_id: 6},
  {id: 155168, book: "Matt", verse: "1.002", unformatted: "从亚伯拉罕到大卫\n亚伯拉罕生以撒，\n以撒生雅各，\n雅各生犹大和他的兄弟们，", version_id: 6},
  {id: 155169, book: "Matt", verse: "1.003", unformatted: "犹大与塔玛尔生法勒斯和谢拉，\n法勒斯生希斯仑，\n希斯仑生亚兰，", version_id: 6},
  {id: 155170, book: "Matt", verse: "1.004", unformatted: "亚兰生亚米拿达，\n亚米拿达生拿顺，\n拿顺生撒门，", version_id: 6},
  {id: 155171, book: "Matt", verse: "1.005", unformatted: "撒门与瑞荷生波阿斯，\n波阿斯与路得生俄备得，\n俄备得生耶西，", version_id: 6},
  {id: 155172, book: "Matt", verse: "1.006", unformatted: "耶西生大卫王。\n从大卫到迁徙巴比伦\n大卫与乌利亚的妻子生所罗门，", version_id: 6},
  {id: 155173, book: "Matt", verse: "1.007", unformatted: "所罗门生罗波安，\n罗波安生亚比雅，\n亚比雅生亚撒，", version_id: 6},
  {id: 155174, book: "Matt", verse: "1.008", unformatted: "亚撒生约沙法，\n约沙法生约兰，\n约兰生乌西亚，", version_id: 6},
  {id: 155175, book: "Matt", verse: "1.009", unformatted: "乌西亚生约坦，\n约坦生亚哈斯，\n亚哈斯生希西家，", version_id: 6},
  {id: 155176, book: "Matt", verse: "1.01", unformatted: "希西家生玛拿西，\n玛拿西生阿蒙，\n阿蒙生约西亚，", version_id: 6},
  {id: 155177, book: "Matt", verse: "1.011", unformatted: "在被迫迁徙巴比伦的时候，\n约西亚生耶哥尼雅和他的兄弟们。", version_id: 6},
  {id: 155178, book: "Matt", verse: "1.012", unformatted: "从迁徙巴比伦到基督\n被迫迁徙巴比伦以后，\n耶哥尼雅生撒拉铁，\n撒拉铁生所罗巴伯，", version_id: 6},
  {id: 155179, book: "Matt", verse: "1.013", unformatted: "所罗巴伯生亚比玉，\n亚比玉生以利亚敬，\n以利亚敬生亚所，", version_id: 6},
  {id: 155180, book: "Matt", verse: "1.014", unformatted: "亚所生撒督，\n撒督生亚金，\n亚金生以律，", version_id: 6},
  {id: 155181, book: "Matt", verse: "1.015", unformatted: "以律生以利亚撒，\n以利亚撒生玛坦，\n玛坦生雅各，", version_id: 6},
  {id: 155182, book: "Matt", verse: "1.016", unformatted: "雅各生约瑟，就是玛丽亚的丈夫，\n那称为基督的耶稣是由玛丽亚所生的。", version_id: 6},
  {id: 155183, book: "Matt", verse: "1.017", unformatted: "这样，从亚伯拉罕到大卫的各世代，一共有十四代；从大卫到被迫迁徙巴比伦的时候，也有十四代；从被迫迁徙巴比伦的时候到基督，又有十四代。", version_id: 6},
  {id: 155184, book: "Matt", verse: "1.018", unformatted: "基督降生\n耶稣基督的诞生是这样的：耶稣的母亲玛丽亚已经许配给约瑟，在他们成婚以前，玛丽亚就由圣灵怀了孕。", version_id: 6},
  {id: 155185, book: "Matt", verse: "1.019", unformatted: "她的丈夫约瑟是个义人，不愿意公开羞辱她，想要暗中把她休了。", version_id: 6},
  {id: 155186, book: "Matt", verse: "1.02", unformatted: "约瑟思考这些事以后，忽然主的一位天使在约瑟梦中显现，说：“大卫的后裔约瑟，不要怕迎娶玛丽亚做你的妻子，因为她里面所怀的是由圣灵而来的。", version_id: 6},
  {id: 155187, book: "Matt", verse: "1.021", unformatted: "玛丽亚将要生一个儿子，你要给他起名叫耶稣，因为他将要把自己的子民从他们的罪孽中拯救出来。”", version_id: 6},
  {id: 155188, book: "Matt", verse: "1.022", unformatted: "不过这整个事的发生，是为要应验主藉着先知所说的话：", version_id: 6},
  {id: 155189, book: "Matt", verse: "1.023", unformatted: "“看哪，那童贞女要怀孕，她要生一个儿子，\n人们将称他的名为以马内利。”——\n“以马内利”翻译出来就是“神与我们同在”。", version_id: 6},
  {id: 155190, book: "Matt", verse: "1.024", unformatted: "约瑟从梦中醒过来，就依照主的天使所吩咐他的去做，把他的妻子迎娶过来，", version_id: 6},
  {id: 155191, book: "Matt", verse: "1.025", unformatted: "只是在她生儿子以前，一直没有与她同房。约瑟给孩子起名叫耶稣。", version_id: 6},
  {id: 155192, book: "Matt", verse: "2.001", unformatted: "博士寻访新王\n在希律王的那些日子里，耶稣诞生在犹太的伯利恒。当时，有几个博士从东方来到耶路撒冷。", version_id: 6},
  {id: 155193, book: "Matt", verse: "2.002", unformatted: "他们问：“生下来做犹太人之王的那一位在哪里？我们在东方看见了他的星，就来了要朝拜他。”", version_id: 6},
  {id: 155194, book: "Matt", verse: "2.003", unformatted: "希律王听说了，就惊慌不安，全耶路撒冷的人也与他一起不安。", version_id: 6},
  {id: 155195, book: "Matt", verse: "2.004", unformatted: "希律就召集了所有的祭司长和民间的经文士，向他们询问基督诞生在哪里。", version_id: 6},
  {id: 155196, book: "Matt", verse: "2.005", unformatted: "他们对希律说：“在犹太的伯利恒，因为藉着先知有这样的记载：", version_id: 6},
  {id: 155197, book: "Matt", verse: "2.006", unformatted: "‘犹大之地伯利恒啊！\n你在犹大的首要城乡中，绝不是最小的，\n因为将来有一位领袖要从你那里出来，\n他要牧养我的子民以色列。’”", version_id: 6},
  {id: 155198, book: "Matt", verse: "2.007", unformatted: "于是，希律暗暗地召见了那几个博士，向他们仔细询问那颗星出现的时间，", version_id: 6},
  {id: 155199, book: "Matt", verse: "2.008", unformatted: "然后打发他们前往伯利恒，说：“你们去仔细查问有关那孩子的事，一旦查出来了就向我报告，好让我也去拜他。”", version_id: 6},
  {id: 155200, book: "Matt", verse: "2.009", unformatted: "他们听了王的话就去了。看哪，他们在东方看见的那颗星在前面引导他们，直到那孩子所在的地方，就在上头停住了。", version_id: 6},
  {id: 155201, book: "Matt", verse: "2.01", unformatted: "他们看见那颗星，极其欢喜快乐。", version_id: 6},
  {id: 155202, book: "Matt", verse: "2.011", unformatted: "他们进了屋子，看见那孩子与他的母亲玛丽亚在一起，就俯伏拜那孩子，然后打开他们的宝盒，把黄金、乳香和没药做为礼物献给他。", version_id: 6},
  {id: 155203, book: "Matt", verse: "2.012", unformatted: "他们在梦中得了神的指示不要回到希律那里去，就从另一条路回到自己的家乡去了。", version_id: 6},
  {id: 155204, book: "Matt", verse: "2.013", unformatted: "逃往埃及\n那些博士离开以后，忽然主的一位天使在约瑟梦中显现，说：“起来，带着孩子和他的母亲逃往埃及，留在那里，直到我再指示你，因为希律要搜寻这孩子并杀害他。”", version_id: 6},
  {id: 155205, book: "Matt", verse: "2.014", unformatted: "约瑟就起来，连夜带着孩子和他的母亲逃往埃及，", version_id: 6},
  {id: 155206, book: "Matt", verse: "2.015", unformatted: "住在那里，直到希律死了。这是为要应验主藉着先知所说的话：“我从埃及召我的儿子出来。”", version_id: 6},
  {id: 155207, book: "Matt", verse: "2.016", unformatted: "屠杀婴孩\n希律发现自己受了博士们的愚弄，就十分恼怒。于是，他按照从博士们那里所询问到的时间，派人把伯利恒城里和周围各个地区内两岁以下的男孩，全都杀了。", version_id: 6},
  {id: 155208, book: "Matt", verse: "2.017", unformatted: "这就应验了藉着先知耶利米所说的话：", version_id: 6},
  {id: 155209, book: "Matt", verse: "2.018", unformatted: "“从拉玛听见了声音，\n是哀哭和极其伤痛的声音，\n是瑞秋为她的儿女哀哭，\n不肯接受安慰，\n因为他们不在了。”", version_id: 6},
  {id: 155210, book: "Matt", verse: "2.019", unformatted: "定居拿撒勒\n希律死了以后，在埃及，忽然主的一位天使在约瑟梦中显现，", version_id: 6},
  {id: 155211, book: "Matt", verse: "2.02", unformatted: "说：“起来，带着孩子和他的母亲回以色列地去，因为那些追索这孩子性命的人已经死了。”", version_id: 6},
  {id: 155212, book: "Matt", verse: "2.021", unformatted: "约瑟就起来，带着孩子和他的母亲进了以色列地。", version_id: 6},
  {id: 155213, book: "Matt", verse: "2.022", unformatted: "但是，听说亚基劳接续他父亲希律做了犹太王，约瑟就怕往那里去，而且他在梦中得了神的指示，就退到加利利地区，", version_id: 6},
  {id: 155214, book: "Matt", verse: "2.023", unformatted: "来到一个叫拿撒勒的镇，住了下来。这是为要应验那藉着先知们所说的话：“他将被称为拿撒勒人。”", version_id: 6},
  {id: 155215, book: "Matt", verse: "3.001", unformatted: "基督的先驱\n在那些日子里，施洗者约翰来了，在犹太的旷野传道，", version_id: 6},
  {id: 155216, book: "Matt", verse: "3.002", unformatted: "说：“你们应当悔改，因为天国近了。”", version_id: 6},
  {id: 155217, book: "Matt", verse: "3.003", unformatted: "这约翰就是藉着先知以赛亚所说的那个人。以赛亚说：\n“在旷野有声音呼喊着：\n‘你们当预备主的道，\n当修直他的路！’”", version_id: 6},
  {id: 155218, book: "Matt", verse: "3.004", unformatted: "约翰穿着骆驼毛做的衣服，腰束皮带。他的食物是蝗虫和野蜜。", version_id: 6},
  {id: 155219, book: "Matt", verse: "3.005", unformatted: "那时，耶路撒冷、全犹太地区和整个约旦河一带的人，都出来到他那里，", version_id: 6},
  {id: 155220, book: "Matt", verse: "3.006", unformatted: "承认自己的罪孽，在约旦河里受他的洗礼。", version_id: 6},
  {id: 155221, book: "Matt", verse: "3.007", unformatted: "约翰看见许多法利赛人和撒都该人也来要受他的洗礼，就对他们说：“你们这些毒蛇的子孙！谁指示你们逃避那将要来临的震怒呢？", version_id: 6},
  {id: 155222, book: "Matt", verse: "3.008", unformatted: "你们应当结出果子，与悔改的心相称！", version_id: 6},
  {id: 155223, book: "Matt", verse: "3.009", unformatted: "你们不要心里想‘我们有亚伯拉罕为先祖。’我告诉你们：神能从这些石头中给亚伯拉罕兴起儿女来。", version_id: 6},
  {id: 155224, book: "Matt", verse: "3.01", unformatted: "现在斧子已经放在树根上，所有不结好果子的树都要被砍下来，被丢进火里。", version_id: 6},
  {id: 155225, book: "Matt", verse: "3.011", unformatted: "“我用水给你们施洗，是为了你们的悔改。但在我之后来的那一位，是比我更强有力的，我就是为他提鞋子也不配。他要用圣灵和火给你们施洗。", version_id: 6},
  {id: 155226, book: "Matt", verse: "3.012", unformatted: "他手里拿着扬谷的锨，要扬净他的打谷场，把他的麦子收进仓库，却把糠秕用不灭的火烧掉。”", version_id: 6},
  {id: 155227, book: "Matt", verse: "3.013", unformatted: "耶稣受洗\n那时，耶稣从加利利来到约旦河，到了约翰那里，要受他的洗礼。", version_id: 6},
  {id: 155228, book: "Matt", verse: "3.014", unformatted: "约翰想要阻止他，说：“我需要受你的洗礼，你反而到我这里来吗？”", version_id: 6},
  {id: 155229, book: "Matt", verse: "3.015", unformatted: "耶稣回答他，说：“现在你就答应吧，因为我们如此成全一切的义，是合宜的。”于是约翰答应了他。", version_id: 6},
  {id: 155230, book: "Matt", verse: "3.016", unformatted: "耶稣受了洗，一从水里上来，看哪，诸天就为他开了，他看见神的灵好像鸽子降下，临到他身上。", version_id: 6},
  {id: 155231, book: "Matt", verse: "3.017", unformatted: "这时候，忽然有声音从诸天传来，说：\n“这是我的爱子，\n我所喜悦的。”", version_id: 6},
  {id: 155232, book: "Matt", verse: "4.001", unformatted: "耶稣受试探\n随后，耶稣被圣灵领到旷野去，受魔鬼的试探。", version_id: 6},
  {id: 155233, book: "Matt", verse: "4.002", unformatted: "耶稣禁食了四十昼夜，后来就饿了。", version_id: 6},
  {id: 155234, book: "Matt", verse: "4.003", unformatted: "那试探者前来对他说：“你如果是神的儿子，就吩咐这些石头变成食物吧！”", version_id: 6},
  {id: 155235, book: "Matt", verse: "4.004", unformatted: "耶稣回答说：“经上记着：\n‘人活着不是单靠食物，\n而是靠神口里所出的每一句话。’”", version_id: 6},
  {id: 155236, book: "Matt", verse: "4.005", unformatted: "接着，魔鬼带他到圣城，让他站在圣殿的顶端，", version_id: 6},
  {id: 155237, book: "Matt", verse: "4.006", unformatted: "对他说：“你如果是神的儿子，就跳下去吧！因为经上记着：\n‘他会为你吩咐他的天使；\n他们会用手托住你，\n免得你的脚撞在石头上。’”", version_id: 6},
  {id: 155238, book: "Matt", verse: "4.007", unformatted: "耶稣对他说：“经上又记着：‘不可试探主——你的神。’”", version_id: 6},
  {id: 155239, book: "Matt", verse: "4.008", unformatted: "接着，魔鬼带耶稣上了一座很高的山，给他看世界万国和万国的荣耀，", version_id: 6},
  {id: 155240, book: "Matt", verse: "4.009", unformatted: "对他说：“你如果俯伏拜我，我就把这一切都给你。”", version_id: 6},
  {id: 155241, book: "Matt", verse: "4.01", unformatted: "耶稣对他说：“撒旦，退去！因为经上记着：\n‘要敬拜主——你的神，\n唯独事奉他。’”", version_id: 6},
  {id: 155242, book: "Matt", verse: "4.011", unformatted: "于是，魔鬼离开了耶稣。看哪，有天使们前来伺候他。", version_id: 6},
  {id: 155243, book: "Matt", verse: "4.012", unformatted: "在加利利的服事\n耶稣听说约翰被逮捕了，就退到加利利地区。", version_id: 6},
  {id: 155244, book: "Matt", verse: "4.013", unformatted: "后来，他离开拿撒勒，来到西布伦和拿弗他利境内，住在湖边的迦百农。", version_id: 6},
  {id: 155245, book: "Matt", verse: "4.014", unformatted: "这是为要应验那藉着先知以赛亚所说的话：", version_id: 6},
  {id: 155246, book: "Matt", verse: "4.015", unformatted: "“西布伦地、拿弗他利地，\n沿湖的路，约旦河的对岸，\n外邦人的加利利——", version_id: 6},
  {id: 155247, book: "Matt", verse: "4.016", unformatted: "那坐在黑暗中的民众\n看到了大光；\n那坐在死亡之地和死亡阴影中的人们，\n曙光为他们升起。”", version_id: 6},
  {id: 155248, book: "Matt", verse: "4.017", unformatted: "从那时候起，耶稣开始传道，说：“你们应当悔改，因为天国近了。”", version_id: 6},
  {id: 155249, book: "Matt", verse: "4.018", unformatted: "第一批门徒\n耶稣在加利利湖边行走，看见兄弟两个人，就是那叫彼得的西门和他弟弟安得烈，正在湖里撒网；他们本来是渔夫。", version_id: 6},
  {id: 155250, book: "Matt", verse: "4.019", unformatted: "耶稣就对他们说：“来跟从我！我将要使你们成为得人的渔夫。”", version_id: 6},
  {id: 155251, book: "Matt", verse: "4.02", unformatted: "他们立刻舍弃渔网，跟从了耶稣。", version_id: 6},
  {id: 155252, book: "Matt", verse: "4.021", unformatted: "耶稣从那里往前走，看见另外两个兄弟，就是西庇太的儿子雅各和他弟弟约翰，正在船上与他们的父亲西庇太一起修补渔网。耶稣就召唤他们，", version_id: 6},
  {id: 155253, book: "Matt", verse: "4.022", unformatted: "他们立刻舍弃了船，告别他们的父亲，跟从了耶稣。", version_id: 6},
  {id: 155254, book: "Matt", verse: "4.023", unformatted: "教导、传道、治病\n耶稣走遍全加利利，在他们的会堂里教导人，传天国的福音，并且使民中各样的疾病和各样的症状痊愈。", version_id: 6},
  {id: 155255, book: "Matt", verse: "4.024", unformatted: "他的消息就在全叙利亚省传开了。人们把所有的病人，就是患各种疾病的、受疼痛折磨的、有鬼魔附身的、癫痫的、瘫痪的，都带到耶稣那里。耶稣就使他们痊愈。", version_id: 6},
  {id: 155256, book: "Matt", verse: "4.025", unformatted: "于是有一大群人从加利利、德卡波利斯、耶路撒冷、犹太和约旦河对岸来，跟随耶稣。", version_id: 6},
  {id: 155257, book: "Matt", verse: "5.001", unformatted: "登山宝训\n耶稣看见人群，就上了山，坐下来。他的门徒们来到他面前，", version_id: 6},
  {id: 155258, book: "Matt", verse: "5.002", unformatted: "他就开口教导他们，说：", version_id: 6},
  {id: 155259, book: "Matt", verse: "5.003", unformatted: "天国八福\n“灵里贫乏的人是蒙福的，\n因为天国是他们的。", version_id: 6},
  {id: 155260, book: "Matt", verse: "5.004", unformatted: "悲伤的人是蒙福的，\n因为他们将受到安慰。", version_id: 6},
  {id: 155261, book: "Matt", verse: "5.005", unformatted: "谦和的人是蒙福的，\n因为他们将继承那地。", version_id: 6},
  {id: 155262, book: "Matt", verse: "5.006", unformatted: "饥渴慕义的人是蒙福的，\n因为他们将得饱足。", version_id: 6},
  {id: 155263, book: "Matt", verse: "5.007", unformatted: "怜悯人的人是蒙福的，\n因为他们将蒙怜悯。", version_id: 6},
  {id: 155264, book: "Matt", verse: "5.008", unformatted: "心里洁净的人是蒙福的，\n因为他们将看见神。", version_id: 6},
  {id: 155265, book: "Matt", verse: "5.009", unformatted: "使人和睦的人是蒙福的，\n因为他们将被称为‘神的儿女’。", version_id: 6},
  {id: 155266, book: "Matt", verse: "5.01", unformatted: "为义受逼迫的人是蒙福的，\n因为天国是他们的。", version_id: 6},
  {id: 155267, book: "Matt", verse: "5.011", unformatted: "“当人们因我的缘故责骂你们、逼迫你们、用各种捏造出来的恶事毁谤你们的时候，你们就是蒙福的。", version_id: 6},
  {id: 155268, book: "Matt", verse: "5.012", unformatted: "你们当欢喜，当快乐，因为你们在天上的报偿是大的。要知道，那些人也这样逼迫了在你们以前的先知们。", version_id: 6},
  {id: 155269, book: "Matt", verse: "5.013", unformatted: "信徒是光是盐\n“你们是地上的盐。盐如果失去了味道，还能用什么来把它腌成咸的呢？它再也没有用处，只好被丢在外面，任人践踏。", version_id: 6},
  {id: 155270, book: "Matt", verse: "5.014", unformatted: "“你们是世界的光。建在山上的城是不能隐藏的；", version_id: 6},
  {id: 155271, book: "Matt", verse: "5.015", unformatted: "人点亮了油灯，也不会放在斗底下，而会放在灯台上，它就照亮屋子里所有的人。", version_id: 6},
  {id: 155272, book: "Matt", verse: "5.016", unformatted: "同样，你们的光也应当照耀在人前，使他们看见你们的美好工作，就荣耀你们在天上的父。", version_id: 6},
  {id: 155273, book: "Matt", verse: "5.017", unformatted: "基督成全律法\n“你们不要以为我来是要废除律法和先知，我来不是要废除，而是要成全。", version_id: 6},
  {id: 155274, book: "Matt", verse: "5.018", unformatted: "我确实地告诉你们：即使天和地都消逝了，律法的一点一画也绝不会消逝，直到一切都成就。", version_id: 6},
  {id: 155275, book: "Matt", verse: "5.019", unformatted: "所以，如果有人违犯了这些诫命中最小的一条，又教导人也这样做，他在天国里将被称为最小的；但无论谁遵行这些诫命，又教导人也这样做，这个人在天国里将被称为大的。", version_id: 6},
  {id: 155276, book: "Matt", verse: "5.02", unformatted: "我告诉你们：你们的义如果不胜过经文士们和法利赛人的义，就绝不能进入天国。", version_id: 6},
  {id: 155277, book: "Matt", verse: "5.021", unformatted: "凶杀始于心\n“你们听过那吩咐古人的话：‘不可杀人。杀人的，当遭受审判。’", version_id: 6},
  {id: 155278, book: "Matt", verse: "5.022", unformatted: "但是我告诉你们：所有向他弟兄发怒的，当遭受审判；骂他弟兄‘废物’的，当遭受议会的审判；骂‘蠢货’的，当遭受烈火的地狱。", version_id: 6},
  {id: 155279, book: "Matt", verse: "5.023", unformatted: "所以，如果你在祭坛献上祭物的时候，在那里想起弟兄向你怀着怨，", version_id: 6},
  {id: 155280, book: "Matt", verse: "5.024", unformatted: "就要把祭物留在祭坛前，先去与弟兄和好，然后再回来献上你的祭物。", version_id: 6},
  {id: 155281, book: "Matt", verse: "5.025", unformatted: "你要趁着与你的对头还在路上的时候，赶快与他和解，免得他把你交给审判官，审判官把你交给差役，你就会被投进监狱了。", version_id: 6},
  {id: 155282, book: "Matt", verse: "5.026", unformatted: "我确实地告诉你：除非你还清了最后的一个铜币，否则你绝不能从那里出来。", version_id: 6},
  {id: 155283, book: "Matt", verse: "5.027", unformatted: "内心的奸淫\n“你们听过那吩咐：‘不可通奸。’", version_id: 6},
  {id: 155284, book: "Matt", verse: "5.028", unformatted: "但是我告诉你们：所有怀着欲念看女人的，就已经在心里和她通奸了。", version_id: 6},
  {id: 155285, book: "Matt", verse: "5.029", unformatted: "如果你的右眼使你绊倒，就把它剜出来丢掉！因为对你来说，失去你身体的一部分，总比你全身被丢进地狱要好。", version_id: 6},
  {id: 155286, book: "Matt", verse: "5.03", unformatted: "如果你的右手使你绊倒，就把它砍下来丢掉！因为对你来说，失去你身体的一部分，总比你全身下地狱要好。", version_id: 6},
  {id: 155287, book: "Matt", verse: "5.031", unformatted: "谴责离婚行为\n“又有吩咐：‘如果有人休妻，要给妻子一份休书。’", version_id: 6},
  {id: 155288, book: "Matt", verse: "5.032", unformatted: "但是我告诉你们：凡是休妻的，除非为了淫乱的缘故，否则就是让她犯通奸罪了；凡是娶了被休的女人的，也是犯通奸罪。", version_id: 6},
  {id: 155289, book: "Matt", verse: "5.033", unformatted: "说诚实话\n“另外，你们听过那吩咐古人的话：‘不可起假誓。你所起的誓，总要向主偿还。’", version_id: 6},
  {id: 155290, book: "Matt", verse: "5.034", unformatted: "但是我告诉你们：根本不可起誓。不可指着天起誓，因为天是神的宝座；", version_id: 6},
  {id: 155291, book: "Matt", verse: "5.035", unformatted: "不可指着地起誓，因为地是神的脚凳；不可用耶路撒冷起誓，因为耶路撒冷是那大君王的城；", version_id: 6},
  {id: 155292, book: "Matt", verse: "5.036", unformatted: "也不可指着自己的头起誓，因为你不能使一根头发变白或变黑。", version_id: 6},
  {id: 155293, book: "Matt", verse: "5.037", unformatted: "你们的话语应该如此：是，就说是；不是，就说不是。再多说，就是出于那恶者。", version_id: 6},
  {id: 155294, book: "Matt", verse: "5.038", unformatted: "以德报怨\n“你们听过这吩咐：‘以眼还眼，以牙还牙。’", version_id: 6},
  {id: 155295, book: "Matt", verse: "5.039", unformatted: "但是我告诉你们：不要与恶人作对。有人打你的右脸，把左脸也转给他；", version_id: 6},
  {id: 155296, book: "Matt", verse: "5.04", unformatted: "有人想要控告你，并要拿走你的里衣，你就连外衣也由他拿去；", version_id: 6},
  {id: 155297, book: "Matt", verse: "5.041", unformatted: "有人强迫你走一里路，你就与他一起走两里；", version_id: 6},
  {id: 155298, book: "Matt", verse: "5.042", unformatted: "有人求你，你要给他；有人要向你借什么，你不要拒绝。", version_id: 6},
  {id: 155299, book: "Matt", verse: "5.043", unformatted: "爱敌人\n“你们听过这吩咐：‘要爱你的邻人，恨你的敌人。’", version_id: 6},
  {id: 155300, book: "Matt", verse: "5.044", unformatted: "但是我告诉你们：要爱你们的敌人，为那些逼迫你们的人祷告，", version_id: 6},
  {id: 155301, book: "Matt", verse: "5.045", unformatted: "好让你们成为你们天父的儿女。因为他使太阳升起，对着恶人，也对着好人；降雨给义人，也给不义的人。", version_id: 6},
  {id: 155302, book: "Matt", verse: "5.046", unformatted: "其实你们如果只爱那些爱你们的人，会有什么报偿呢？连税吏不也这样做吗？", version_id: 6},
  {id: 155303, book: "Matt", verse: "5.047", unformatted: "你们如果只问候自己的兄弟，这样做有什么特别呢？连外邦人不也这样做吗？", version_id: 6},
  {id: 155304, book: "Matt", verse: "5.048", unformatted: "所以，你们当成为完全的，就像你们的天父是完全的。", version_id: 6},
  {id: 155305, book: "Matt", verse: "6.001", unformatted: "论施舍\n“你们要谨慎，不可为了让别人看到而在人面前行你们的义，否则就得不到你们天父的报偿了。", version_id: 6},
  {id: 155306, book: "Matt", verse: "6.002", unformatted: "所以你施舍的时候，不要在自己前面吹号，像伪善的人那样。他们在会堂里和大街上这样做，是为了要得人的荣耀。我确实地告诉你们：他们已经得到了他们的报偿。", version_id: 6},
  {id: 155307, book: "Matt", verse: "6.003", unformatted: "你施舍的时候，不要让你的左手知道右手所做的，", version_id: 6},
  {id: 155308, book: "Matt", verse: "6.004", unformatted: "好使你的施舍行在隐秘中。这样，你那在隐秘中察看的父就将回报你。", version_id: 6},
  {id: 155309, book: "Matt", verse: "6.005", unformatted: "论祷告\n“你们祷告的时候，不要像伪善的人那样。他们喜爱站在会堂里和十字路口祷告，好让人看见。我确实地告诉你们：他们已经得到了他们的报偿。", version_id: 6},
  {id: 155310, book: "Matt", verse: "6.006", unformatted: "至于你，你祷告的时候，要进入你的内室，关上门，向你在隐秘中的父祷告。这样，你那在隐秘中察看的父就将回报你。", version_id: 6},
  {id: 155311, book: "Matt", verse: "6.007", unformatted: "你们祷告的时候，不要像外邦人那样，重复无意义的话。他们以为话多了就蒙垂听。", version_id: 6},
  {id: 155312, book: "Matt", verse: "6.008", unformatted: "不要像他们那样，因为在你们祈求以前，你们的父就已经知道你们有什么需要了。", version_id: 6},
  {id: 155313, book: "Matt", verse: "6.009", unformatted: "祷告的典范\n“因此，你们要这样祷告：\n我们在天上的父，\n愿你的名被尊为圣，", version_id: 6},
  {id: 155314, book: "Matt", verse: "6.01", unformatted: "愿你的国度来临。\n愿你的旨意行在地上，\n如同行在天上。", version_id: 6},
  {id: 155315, book: "Matt", verse: "6.011", unformatted: "我们日用的食粮，愿你今天赐给我们。", version_id: 6},
  {id: 155316, book: "Matt", verse: "6.012", unformatted: "愿你饶恕我们的亏欠，\n如同我们也饶恕了亏欠我们的人。", version_id: 6},
  {id: 155317, book: "Matt", verse: "6.013", unformatted: "不要让我们陷入试探，\n救我们脱离那恶者。\n因为国度、权柄、荣耀，\n全是你的，直到永远！阿们。", version_id: 6},
  {id: 155318, book: "Matt", verse: "6.014", unformatted: "“要知道，你们如果饶恕别人的过犯，你们的天父也会饶恕你们。", version_id: 6},
  {id: 155319, book: "Matt", verse: "6.015", unformatted: "你们如果不饶恕别人，你们的父也不会饶恕你们的过犯。", version_id: 6},
  {id: 155320, book: "Matt", verse: "6.016", unformatted: "论禁食\n“你们禁食的时候，不要像伪善的人那样愁眉苦脸。他们装出难看的样子，好让人看出他们在禁食。我确实地告诉你们：他们已经得到了他们的报偿。", version_id: 6},
  {id: 155321, book: "Matt", verse: "6.017", unformatted: "至于你，你禁食的时候，要梳头洗脸，", version_id: 6},
  {id: 155322, book: "Matt", verse: "6.018", unformatted: "免得被人看出你在禁食；你反而要让你在隐秘中的父看见。这样，你那在隐秘中察看的父就将回报你。", version_id: 6},
  {id: 155323, book: "Matt", verse: "6.019", unformatted: "论钱财\n“你们不要为自己在地上积蓄财宝，地上有虫蛀，会锈蚀，也有盗贼钻进来偷窃；", version_id: 6},
  {id: 155324, book: "Matt", verse: "6.02", unformatted: "而要为自己在天上积蓄财宝：天上既没有虫蛀，也不会锈蚀，也没有盗贼钻进来偷窃。", version_id: 6},
  {id: 155325, book: "Matt", verse: "6.021", unformatted: "要知道，你的财宝在哪里，你的心也在哪里。", version_id: 6},
  {id: 155326, book: "Matt", verse: "6.022", unformatted: "“眼睛是身体的灯，所以，如果你的眼睛纯净，你的全身就光明；", version_id: 6},
  {id: 155327, book: "Matt", verse: "6.023", unformatted: "如果你的眼睛污浊，你的全身就黑暗。因此，光如果在你里面成了黑暗，那是多么大的黑暗！", version_id: 6},
  {id: 155328, book: "Matt", verse: "6.024", unformatted: "“一个人不能服事两个主人。他不是厌恶这个、喜爱那个，就是忠于这个、轻视那个。你们不能既服事神，又服事财富。", version_id: 6},
  {id: 155329, book: "Matt", verse: "6.025", unformatted: "不要忧虑\n“所以我告诉你们：不要为你们的生命而忧虑吃什么、喝什么；也不要为你们的身体而忧虑穿什么；难道生命不比食物更重要，身体不比衣服更重要吗？", version_id: 6},
  {id: 155330, book: "Matt", verse: "6.026", unformatted: "应该看天空的飞鸟：它们不撒种、不收割，也不收集在仓库里，你们的天父尚且养活它们；难道你们不比飞鸟更重要吗？", version_id: 6},
  {id: 155331, book: "Matt", verse: "6.027", unformatted: "你们当中谁能因着忧虑使自己的寿命延长一刻呢？", version_id: 6},
  {id: 155332, book: "Matt", verse: "6.028", unformatted: "你们为什么为衣服忧虑呢？应该观察野地里的百合花是怎么生长的：它们不劳动，也不纺织。", version_id: 6},
  {id: 155333, book: "Matt", verse: "6.029", unformatted: "但是我告诉你们：就连所罗门在他最荣耀的时候，所穿戴的也不如这些花中的一朵。", version_id: 6},
  {id: 155334, book: "Matt", verse: "6.03", unformatted: "你们这些小信的人哪！野地里的草今天还在，明天就被丢进火炉里，神尚且这样装扮它，更何况你们呢！", version_id: 6},
  {id: 155335, book: "Matt", verse: "6.031", unformatted: "所以不要忧虑，说‘我们吃什么？喝什么？穿什么？’", version_id: 6},
  {id: 155336, book: "Matt", verse: "6.032", unformatted: "因为这一切都是外邦人所寻求的，而你们的天父已经知道你们需要这一切了。", version_id: 6},
  {id: 155337, book: "Matt", verse: "6.033", unformatted: "所以你们应当先寻求神的国和神的义，这一切都将加给你们了。", version_id: 6},
  {id: 155338, book: "Matt", verse: "6.034", unformatted: "因此，不要为明天忧虑，明天自有明天的忧虑。要知道，每一天自有它的难处，这已经够了。", version_id: 6},
  {id: 155339, book: "Matt", verse: "7.001", unformatted: "不要评断\n“不要评断人，免得你们被评断。", version_id: 6},
  {id: 155340, book: "Matt", verse: "7.002", unformatted: "因为，你们用什么标准来评断，也会同样地被评断；你们用什么量器来衡量，也会同样地被衡量。", version_id: 6},
  {id: 155341, book: "Matt", verse: "7.003", unformatted: "你为什么看见你弟兄眼里的木屑，却不想自己眼里的梁木呢？", version_id: 6},
  {id: 155342, book: "Matt", verse: "7.004", unformatted: "又怎么能对你弟兄说‘让我除掉你眼里的木屑’呢？看哪，你自己眼里还有梁木呢！", version_id: 6},
  {id: 155343, book: "Matt", verse: "7.005", unformatted: "你这伪善的人！先除掉你自己眼里的梁木，然后你才能看得清楚，好除掉你弟兄眼里的木屑。", version_id: 6},
  {id: 155344, book: "Matt", verse: "7.006", unformatted: "不要把神圣之物丢给狗，也不要把你们的珍珠丢在猪面前，免得它们用脚把这些践踏了，然后转身撕咬你们。", version_id: 6},
  {id: 155345, book: "Matt", verse: "7.007", unformatted: "不断祈求\n“不断祈求吧，就会给你们；不断寻找吧，就会找到；不断敲门吧，就会为你们开门。", version_id: 6},
  {id: 155346, book: "Matt", verse: "7.008", unformatted: "因为凡祈求的，就得到；寻找的，就找到；敲门的，就为他开门。", version_id: 6},
  {id: 155347, book: "Matt", verse: "7.009", unformatted: "你们当中有哪个人，儿子要饼，却给他石头呢？", version_id: 6},
  {id: 155348, book: "Matt", verse: "7.01", unformatted: "儿子要鱼，却给他蛇呢？", version_id: 6},
  {id: 155349, book: "Matt", verse: "7.011", unformatted: "所以，如果你们做为恶人也知道把好的东西给自己的儿女，更何况你们在天上的父要把美好的赐给求他的人呢！", version_id: 6},
  {id: 155350, book: "Matt", verse: "7.012", unformatted: "因此在任何事上，你们希望别人怎样对待你们，你们也应当怎样对待别人。要知道，这就是律法和先知的教导。", version_id: 6},
  {id: 155351, book: "Matt", verse: "7.013", unformatted: "要进窄门\n“你们要从窄门进去；因为那通向灭亡的门是大的，那条路是宽的，从那里进去的人也多；", version_id: 6},
  {id: 155352, book: "Matt", verse: "7.014", unformatted: "然而，那通向永生的门是多么小，路是多么窄，找到它的人是多么少。", version_id: 6},
  {id: 155353, book: "Matt", verse: "7.015", unformatted: "“你们要提防假先知。他们披着羊皮来到你们这里，里面却是凶残的狼。", version_id: 6},
  {id: 155354, book: "Matt", verse: "7.016", unformatted: "凭着他们的果子，你们就能认出他们来。难道从荆棘上能收葡萄，从蒺藜中能收无花果吗？", version_id: 6},
  {id: 155355, book: "Matt", verse: "7.017", unformatted: "同样，好树都结好果子，坏树就结坏果子；", version_id: 6},
  {id: 155356, book: "Matt", verse: "7.018", unformatted: "好树不能结坏果子，坏树也不能结好果子。", version_id: 6},
  {id: 155357, book: "Matt", verse: "7.019", unformatted: "所有不结好果子的树都要被砍下来，被丢进火里。", version_id: 6},
  {id: 155358, book: "Matt", verse: "7.02", unformatted: "因此凭着他们的果子，你们就能认出他们来。", version_id: 6},
  {id: 155359, book: "Matt", verse: "7.021", unformatted: "“不是每一个称呼我‘主啊，主啊’的人，都能进入天国；只有遵行我天父旨意的人，才能进去。", version_id: 6},
  {id: 155360, book: "Matt", verse: "7.022", unformatted: "在那一天，有许多人会对我说：‘主啊，主啊，我们不是奉你的名做先知传道，奉你的名驱赶鬼魔，奉你的名行了很多神迹吗？’", version_id: 6},
  {id: 155361, book: "Matt", verse: "7.023", unformatted: "那时候，我要向他们宣告：‘我从来不认识你们。离开我，你们这些作恶的人！’", version_id: 6},
  {id: 155362, book: "Matt", verse: "7.024", unformatted: "两种根基\n“因此，凡是听了我这些话而实行的人，就好比一个聪明的人把自己的房子建在磐石上。", version_id: 6},
  {id: 155363, book: "Matt", verse: "7.025", unformatted: "大雨降下，急流冲来，狂风刮起，撞击那房子，房子却没有倒塌，因为它在磐石上立定根基。", version_id: 6},
  {id: 155364, book: "Matt", verse: "7.026", unformatted: "可是，凡是听了我这些话而不实行的人，就好比一个愚拙的人把自己的房子建在沙滩上。", version_id: 6},
  {id: 155365, book: "Matt", verse: "7.027", unformatted: "大雨降下，急流冲来，狂风刮起，撞击那房子，房子就倒塌了，并且倒塌得非常厉害。”", version_id: 6},
  {id: 155366, book: "Matt", verse: "7.028", unformatted: "当耶稣讲完了这些话，众人都对他的教导惊叹不已，", version_id: 6},
  {id: 155367, book: "Matt", verse: "7.029", unformatted: "因为耶稣教导他们，就像有权柄的人，不像他们的经文士们。", version_id: 6},
  {id: 155368, book: "Matt", verse: "8.001", unformatted: "洁净麻风病人\n耶稣从山上下来，有一大群人跟随他。", version_id: 6},
  {id: 155369, book: "Matt", verse: "8.002", unformatted: "这时候，忽然有一个麻风病人前来拜他，说：“主啊，如果你愿意，你就能洁净我。”", version_id: 6},
  {id: 155370, book: "Matt", verse: "8.003", unformatted: "耶稣伸出手来摸他，说：“我愿意，你洁净了吧！”那人的麻风病立刻就被洁净了。", version_id: 6},
  {id: 155371, book: "Matt", verse: "8.004", unformatted: "耶稣对他说：“你要注意，不可告诉任何人，只要去把自己给祭司看，并且献上摩西所吩咐的祭物，好对他们做见证。”", version_id: 6},
  {id: 155372, book: "Matt", verse: "8.005", unformatted: "百夫长的信心\n耶稣进了迦百农，有个百夫长前来恳求他，", version_id: 6},
  {id: 155373, book: "Matt", verse: "8.006", unformatted: "说：“主啊，我的仆人瘫痪了，躺在家里，受剧烈的折磨。”", version_id: 6},
  {id: 155374, book: "Matt", verse: "8.007", unformatted: "耶稣对他说：“我去使他痊愈。”", version_id: 6},
  {id: 155375, book: "Matt", verse: "8.008", unformatted: "百夫长回答说：“主啊，我实在不配请你进我家。其实只要你说一句话，我的仆人就会痊愈的。", version_id: 6},
  {id: 155376, book: "Matt", verse: "8.009", unformatted: "事实上，我也在人的权下，也有士兵在我之下。我对这个说‘去’，他就去；对那个说‘来’，他就来；对我的奴仆说‘做这事’，他就去做。”", version_id: 6},
  {id: 155377, book: "Matt", verse: "8.01", unformatted: "耶稣听了，十分感慨，对跟随他的人说：“我确实地告诉你们：在以色列我没有见过有这么大信心的人。", version_id: 6},
  {id: 155378, book: "Matt", verse: "8.011", unformatted: "我告诉你们：将有许多人从东从西而来，在天国里与亚伯拉罕、以撒、雅各一同坐席。", version_id: 6},
  {id: 155379, book: "Matt", verse: "8.012", unformatted: "但那些‘天国的儿女’，却将被丢在外面的黑暗里。在那里将有哀哭和切齿。”", version_id: 6},
  {id: 155380, book: "Matt", verse: "8.013", unformatted: "于是耶稣对那百夫长说：“回去吧，照着你所信的，给你成全吧！”他的仆人就在那一刻痊愈了。", version_id: 6},
  {id: 155381, book: "Matt", verse: "8.014", unformatted: "在迦百农治病\n耶稣来到彼得家，看见彼得的岳母正发烧躺着。", version_id: 6},
  {id: 155382, book: "Matt", verse: "8.015", unformatted: "耶稣一摸她的手，烧就退了。于是她就起来服事耶稣。", version_id: 6},
  {id: 155383, book: "Matt", verse: "8.016", unformatted: "到了傍晚，人们把许多有鬼魔附身的人带到耶稣那里。耶稣话语一出就把那些邪灵都赶了出去，并且使所有患病的人痊愈了。", version_id: 6},
  {id: 155384, book: "Matt", verse: "8.017", unformatted: "这样，那藉着先知以赛亚所说的话就应验了：\n“他亲自代替了我们的软弱，\n担当了我们的疾病。”", version_id: 6},
  {id: 155385, book: "Matt", verse: "8.018", unformatted: "跟随耶稣\n耶稣看见一群人围着他，就吩咐渡到对岸去。", version_id: 6},
  {id: 155386, book: "Matt", verse: "8.019", unformatted: "有一个经文士前来对他说：“老师，无论你到哪里去，我都要跟从你。”", version_id: 6},
  {id: 155387, book: "Matt", verse: "8.02", unformatted: "耶稣对他说：“狐狸有洞，天空的飞鸟有窝，人子却没有枕头的地方。”", version_id: 6},
  {id: 155388, book: "Matt", verse: "8.021", unformatted: "耶稣的另一个门徒对他说：“主啊，请准许我先去埋葬我的父亲。”", version_id: 6},
  {id: 155389, book: "Matt", verse: "8.022", unformatted: "但是耶稣对他说：“你跟从我，让死人去埋葬他们的死人吧。”", version_id: 6},
  {id: 155390, book: "Matt", verse: "8.023", unformatted: "平静风浪\n耶稣上了船，他的门徒们跟着他。", version_id: 6},
  {id: 155391, book: "Matt", verse: "8.024", unformatted: "这时候，忽然湖上起了大风暴，以致波浪盖过了船，耶稣却睡着了。", version_id: 6},
  {id: 155392, book: "Matt", verse: "8.025", unformatted: "门徒们上前来叫醒他，说：“主啊，救救我们，我们没命了！”", version_id: 6},
  {id: 155393, book: "Matt", verse: "8.026", unformatted: "耶稣对他们说：“为什么胆怯呢？你们这些小信的人哪！”耶稣就起来，斥责风和浪，湖面就变得一片平静。", version_id: 6},
  {id: 155394, book: "Matt", verse: "8.027", unformatted: "他们都感到惊奇，说：“这个人究竟是什么人？连风和浪也听从他！”", version_id: 6},
  {id: 155395, book: "Matt", verse: "8.028", unformatted: "在格拉森驱赶鬼魔\n耶稣来到对岸格达拉人的地方，两个有鬼魔附身的人，从墓地迎着他出来。他们非常凶猛，以致没有人能从那条路经过。", version_id: 6},
  {id: 155396, book: "Matt", verse: "8.029", unformatted: "忽然，他们喊叫说：“神的儿子啊，我们与你有什么关系？时候来到之前，你就来这里折磨我们吗？”", version_id: 6},
  {id: 155397, book: "Matt", verse: "8.03", unformatted: "当时，离他们很远的地方，有一大群猪正在吃食。", version_id: 6},
  {id: 155398, book: "Matt", verse: "8.031", unformatted: "那些鬼魔就央求耶稣，说：“如果你要把我们赶出去，就叫我们进入这群猪里面去吧。”", version_id: 6},
  {id: 155399, book: "Matt", verse: "8.032", unformatted: "耶稣对它们说：“去吧！”它们就出来，进入猪里面去了。忽然，那整群猪从山崖冲到湖里，在水里淹死了。", version_id: 6},
  {id: 155400, book: "Matt", verse: "8.033", unformatted: "那些放猪的人就逃跑，进城去传报了这一切事，包括鬼魔附身之人的事。", version_id: 6},
  {id: 155401, book: "Matt", verse: "8.034", unformatted: "看哪，全城的人都出来看耶稣，见了耶稣以后，就央求他离开他们的地区。", version_id: 6},
  {id: 155402, book: "Matt", verse: "9.001", unformatted: "赦罪与治病\n耶稣上了船，渡过去，来到自己的城里。", version_id: 6},
  {id: 155403, book: "Matt", verse: "9.002", unformatted: "这时候，有几个人把一个躺在床榻上瘫痪的人抬到他面前。耶稣看见他们的信心，就对瘫痪的人说：“孩子，鼓起勇气吧！你的罪孽被赦免了。”", version_id: 6},
  {id: 155404, book: "Matt", verse: "9.003", unformatted: "但这时候，有些经文士心里说：“这个人说亵渎的话！”", version_id: 6},
  {id: 155405, book: "Matt", verse: "9.004", unformatted: "耶稣知道他们的想法，就说：“你们心里为什么怀着恶念呢？", version_id: 6},
  {id: 155406, book: "Matt", verse: "9.005", unformatted: "或说‘你的罪孽被赦免了’，或说‘你起来走路’，到底哪一样更容易呢？", version_id: 6},
  {id: 155407, book: "Matt", verse: "9.006", unformatted: "不过为要使你们知道人子在地上有赦免罪的权柄——”于是他对瘫痪的人说：“起来，拿起你的床榻，回家去吧！”", version_id: 6},
  {id: 155408, book: "Matt", verse: "9.007", unformatted: "那个人就起来，回家去了。", version_id: 6},
  {id: 155409, book: "Matt", verse: "9.008", unformatted: "众人看见就起了敬畏之心，就荣耀神，因为他赐给人这样的权柄。", version_id: 6},
  {id: 155410, book: "Matt", verse: "9.009", unformatted: "呼召马太\n耶稣从那里往前走，看见一个叫马太的人在税关坐着。耶稣对他说：“你跟从我！”马太就站起来，跟从了耶稣。", version_id: 6},
  {id: 155411, book: "Matt", verse: "9.01", unformatted: "耶稣在屋子里坐席，这时候来了许多税吏和罪人，与耶稣和他的门徒们一同坐席。", version_id: 6},
  {id: 155412, book: "Matt", verse: "9.011", unformatted: "有些法利赛人看见了，就对耶稣的门徒们说：“你们的老师为什么与那些税吏和罪人一起吃饭呢？”", version_id: 6},
  {id: 155413, book: "Matt", verse: "9.012", unformatted: "耶稣听见了就说：“健康的人不需要医生，有病的人才需要。", version_id: 6},
  {id: 155414, book: "Matt", verse: "9.013", unformatted: "你们去学‘我要的是怜悯，而不是祭祀’这话是什么意思吧！我来不是要召唤义人，而是要召唤罪人。”", version_id: 6},
  {id: 155415, book: "Matt", verse: "9.014", unformatted: "禁食的问题\n随后，约翰的门徒们前来问耶稣：“为什么我们和法利赛人经常禁食，而你的门徒们却不禁食呢？”", version_id: 6},
  {id: 155416, book: "Matt", verse: "9.015", unformatted: "耶稣对他们说：“新郎与宾客在一起的时候，难道宾客能悲伤吗？可是日子将要来到：当新郎从他们中间被带走的时候，他们那时就要禁食了。", version_id: 6},
  {id: 155417, book: "Matt", verse: "9.016", unformatted: "没有人把没缩过水的布块补在旧衣服上，因为所补上的布块会扯坏衣服，造成更大的裂口。", version_id: 6},
  {id: 155418, book: "Matt", verse: "9.017", unformatted: "也没有人把新酒装在旧皮袋里，否则皮袋会胀破，酒会流出来，皮袋也被糟蹋了；相反，他们是把新酒装在新皮袋里，这样两者都保全了。”", version_id: 6},
  {id: 155419, book: "Matt", verse: "9.018", unformatted: "少女复活、妇人痊愈\n耶稣正对他们讲这些事的时候，忽然有一个会堂主管来拜他，说：“我的女儿刚才死了，求你来按手在她身上，她就会活了。”", version_id: 6},
  {id: 155420, book: "Matt", verse: "9.019", unformatted: "于是耶稣起身跟着他去，他的门徒们也跟着去了。", version_id: 6},
  {id: 155421, book: "Matt", verse: "9.02", unformatted: "这时候，忽然有一个患血漏十二年的女人，从耶稣后面过来，摸了一下耶稣的衣服穗子，", version_id: 6},
  {id: 155422, book: "Matt", verse: "9.021", unformatted: "原来她心里想：“只要我摸到他的衣服，就会得救治。”", version_id: 6},
  {id: 155423, book: "Matt", verse: "9.022", unformatted: "耶稣转过身来看着她，说：“女儿，鼓起勇气吧！你的信救了你。”从那一刻起，那个女人就得了救治。", version_id: 6},
  {id: 155424, book: "Matt", verse: "9.023", unformatted: "耶稣来到那会堂主管的家，看见吹笛的人和慌乱的人群，", version_id: 6},
  {id: 155425, book: "Matt", verse: "9.024", unformatted: "就说：“你们退去吧！这女孩不是死了，而是睡了。”他们就讥笑他。", version_id: 6},
  {id: 155426, book: "Matt", verse: "9.025", unformatted: "那群人被赶出去以后，耶稣进去，握着女孩的手，那女孩就起来了。", version_id: 6},
  {id: 155427, book: "Matt", verse: "9.026", unformatted: "于是，这消息传遍了那整个地区。", version_id: 6},
  {id: 155428, book: "Matt", verse: "9.027", unformatted: "开盲人的眼睛\n耶稣从那里往前走，有两个瞎眼的跟着他，呼叫说：“大卫的后裔，可怜我们吧！”", version_id: 6},
  {id: 155429, book: "Matt", verse: "9.028", unformatted: "耶稣进了屋子，那两个瞎眼的来到他面前。耶稣问他们：“你们相信我能做这件事吗？”\n他们回答说：“主啊，是的，我们信。”", version_id: 6},
  {id: 155430, book: "Matt", verse: "9.029", unformatted: "于是耶稣摸他们的眼睛，说：“照着你们所信的，给你们成全吧。”", version_id: 6},
  {id: 155431, book: "Matt", verse: "9.03", unformatted: "他们的眼睛就开了。耶稣严厉地告诫他们：“你们要注意，不可让任何人知道。”", version_id: 6},
  {id: 155432, book: "Matt", verse: "9.031", unformatted: "但他们一出去，就把耶稣的消息传遍了那整个地区。", version_id: 6},
  {id: 155433, book: "Matt", verse: "9.032", unformatted: "驱赶鬼魔\n两个人离去了。这时候有几个人把一个有鬼魔附身的哑巴带到耶稣那里。", version_id: 6},
  {id: 155434, book: "Matt", verse: "9.033", unformatted: "鬼魔一被赶出去，哑巴就说出话来。众人都感到惊奇，他们说：“这样的事，在以色列从来没有见过。”", version_id: 6},
  {id: 155435, book: "Matt", verse: "9.034", unformatted: "但法利赛人却说：“他是靠鬼魔的王驱赶鬼魔的。”", version_id: 6},
  {id: 155436, book: "Matt", verse: "9.035", unformatted: "工作多工人少\n耶稣走遍各个城镇乡村，在他们的会堂里教导人，传天国的福音，并且使各样的疾病和各样的症状痊愈。", version_id: 6},
  {id: 155437, book: "Matt", verse: "9.036", unformatted: "耶稣看见众人，就对他们动了怜悯之心，因为他们困苦流离，像羊没有牧人那样。", version_id: 6},
  {id: 155438, book: "Matt", verse: "9.037", unformatted: "于是他对自己的门徒们说：“收割的工作多，而工人少。", version_id: 6},
  {id: 155439, book: "Matt", verse: "9.038", unformatted: "所以你们要祈求收割的主催促工人参与他的收割工作。”", version_id: 6},
  {id: 155440, book: "Matt", verse: "10.001", unformatted: "差派十二使徒\n耶稣召来他的十二个门徒，赐给他们权柄，能驱赶污灵，也能使各样的疾病和各样的症状痊愈。", version_id: 6},
  {id: 155441, book: "Matt", verse: "10.002", unformatted: "以下是十二使徒的名字：\n首先是西门——也叫彼得，\n然后是他的弟弟安得烈，\n西庇太的儿子雅各，\n雅各的弟弟约翰，", version_id: 6},
  {id: 155442, book: "Matt", verse: "10.003", unformatted: "腓力和巴多罗迈，\n多马和税吏马太，\n亚勒腓的儿子雅各，还有达太，", version_id: 6},
  {id: 155443, book: "Matt", verse: "10.004", unformatted: "激进派的西门，\n还有那出卖耶稣的加略人犹大。", version_id: 6},
  {id: 155444, book: "Matt", verse: "10.005", unformatted: "耶稣差派这十二使徒出去，吩咐他们说：“外邦人的路，你们不要走；撒马利亚人的城，你们不要进；", version_id: 6},
  {id: 155445, book: "Matt", verse: "10.006", unformatted: "宁愿到以色列家迷失的羊那里去。", version_id: 6},
  {id: 155446, book: "Matt", verse: "10.007", unformatted: "你们去的时候，要传讲说‘天国近了。’", version_id: 6},
  {id: 155447, book: "Matt", verse: "10.008", unformatted: "要使病人痊愈，使死人复活，使麻风病人洁净，把鬼魔赶出去。你们无偿地得来，也要无偿地给与。", version_id: 6},
  {id: 155448, book: "Matt", verse: "10.009", unformatted: "腰包里不要带金银或铜钱。", version_id: 6},
  {id: 155449, book: "Matt", verse: "10.01", unformatted: "在路上不要带行囊，不要带两件衣服，也不要带鞋子和手杖，因为工人配得自己的食物。", version_id: 6},
  {id: 155450, book: "Matt", verse: "10.011", unformatted: "“你们无论进哪个城镇或乡村，要查问其中配得的人，就住在他那里，直到你们离去。", version_id: 6},
  {id: 155451, book: "Matt", verse: "10.012", unformatted: "你们进了他家，要祝那家平安。", version_id: 6},
  {id: 155452, book: "Matt", verse: "10.013", unformatted: "如果那一家是配得的，你们的平安就临到那一家；如果那家是不配得的，你们的平安就归回你们。", version_id: 6},
  {id: 155453, book: "Matt", verse: "10.014", unformatted: "如果有人不接受你们，也不听你们的话语，你们离开那家那城的时候，要把脚上的尘土跺掉。", version_id: 6},
  {id: 155454, book: "Matt", verse: "10.015", unformatted: "我确实地告诉你们：在那审判的日子，所多玛和格摩拉之地所受的，将要比那城还容易受呢！", version_id: 6},
  {id: 155455, book: "Matt", verse: "10.016", unformatted: "预言受迫害\n“看哪，我差派你们出去，就像把羊送进狼群中，所以你们要像蛇那样聪明，像鸽子那样纯洁。", version_id: 6},
  {id: 155456, book: "Matt", verse: "10.017", unformatted: "你们要提防人们，因为他们将要把你们送交议会，并且在他们的会堂里鞭打你们。", version_id: 6},
  {id: 155457, book: "Matt", verse: "10.018", unformatted: "为我的缘故，你们将被带到总督和君王面前，好对他们和外邦人做见证。", version_id: 6},
  {id: 155458, book: "Matt", verse: "10.019", unformatted: "当他们把你们交出去的时候，不要忧虑怎样说或说什么。到时候，你们就会被赐予该说的话，", version_id: 6},
  {id: 155459, book: "Matt", verse: "10.02", unformatted: "因为说话的不是你们自己，而是你们父的灵在你们里面说话。", version_id: 6},
  {id: 155460, book: "Matt", verse: "10.021", unformatted: "“兄弟将要把兄弟出卖，置于死地；父亲对孩子也会这样。儿女会起来与父母作对，并且害死他们。", version_id: 6},
  {id: 155461, book: "Matt", verse: "10.022", unformatted: "为了我的名，你们将被所有的人憎恨，但是忍耐到底的，这个人将会得救。", version_id: 6},
  {id: 155462, book: "Matt", verse: "10.023", unformatted: "他们在这城逼迫你们，你们就逃到别的城去。我确实地告诉你们：在你们走遍以色列的城镇之前，人子必定来临。", version_id: 6},
  {id: 155463, book: "Matt", verse: "10.024", unformatted: "学生不高于他的老师，奴仆也不高于他的主人。", version_id: 6},
  {id: 155464, book: "Matt", verse: "10.025", unformatted: "学生能像他的老师一样，奴仆能像他的主人一样，就已经够了。如果人们称一家的主人为‘别西卜’，更何况他的家人呢！", version_id: 6},
  {id: 155465, book: "Matt", verse: "10.026", unformatted: "要惧怕神\n“所以不要怕他们，因为被遮盖的事没有将不被显露出来的，隐秘的事也没有将不被知道的。", version_id: 6},
  {id: 155466, book: "Matt", verse: "10.027", unformatted: "我在暗中告诉你们的，你们要在明处说出来；你们在耳边听到的，要在屋顶上宣扬出去。", version_id: 6},
  {id: 155467, book: "Matt", verse: "10.028", unformatted: "你们不要怕那些能杀死身体却不能杀死灵魂的；反而倒要惧怕那位能把灵魂和身体都毁灭在地狱里的。", version_id: 6},
  {id: 155468, book: "Matt", verse: "10.029", unformatted: "两只麻雀不是卖一个小钱吗？但如果没有你们父的允许，一只也不会掉在地上。", version_id: 6},
  {id: 155469, book: "Matt", verse: "10.03", unformatted: "至于你们，就是每一根头发也都被数过了。", version_id: 6},
  {id: 155470, book: "Matt", verse: "10.031", unformatted: "所以不要怕！你们比一大群麻雀还重要。", version_id: 6},
  {id: 155471, book: "Matt", verse: "10.032", unformatted: "要承认基督\n“凡是在人面前承认我的，我在我天上的父面前也将承认他；", version_id: 6},
  {id: 155472, book: "Matt", verse: "10.033", unformatted: "但无论谁在人面前不认我，我在我天上的父面前也将不认他。", version_id: 6},
  {id: 155473, book: "Matt", verse: "10.034", unformatted: "不要以为我来了，是给地上带来和平。我来并不是带来和平，而是刀剑，", version_id: 6},
  {id: 155474, book: "Matt", verse: "10.035", unformatted: "因为我来就是为了：\n使‘人与父亲对立，\n女儿与母亲对立，\n媳妇与婆婆对立，", version_id: 6},
  {id: 155475, book: "Matt", verse: "10.036", unformatted: "这样，一个人的敌人，\n就是自己家里的人。’", version_id: 6},
  {id: 155476, book: "Matt", verse: "10.037", unformatted: "“那爱父亲或母亲过于爱我的，不配属于我；那爱儿子或女儿过于爱我的，不配属于我；", version_id: 6},
  {id: 155477, book: "Matt", verse: "10.038", unformatted: "不背起自己的十字架来跟从我的，也不配属于我。", version_id: 6},
  {id: 155478, book: "Matt", verse: "10.039", unformatted: "寻得自己生命的，将失去生命；为我的缘故失去自己生命的，将寻得生命。", version_id: 6},
  {id: 155479, book: "Matt", verse: "10.04", unformatted: "一杯凉水\n“接受你们的，就是接受我；接受我的，就是接受差派我来的那一位。", version_id: 6},
  {id: 155480, book: "Matt", verse: "10.041", unformatted: "因着先知的名份接受先知的，将得到先知的报偿；因着义人的名份接受义人的，将得到义人的报偿。", version_id: 6},
  {id: 155481, book: "Matt", verse: "10.042", unformatted: "无论谁，因着门徒的名份，就是拿一杯凉水给这些卑微人中的一个喝，我确实地告诉你们：他绝不会失去他的报偿。”", version_id: 6},
  {id: 155482, book: "Matt", verse: "11.001", unformatted: "论施洗约翰\n当耶稣吩咐完了他的十二个门徒，就离开了那里，到各城镇去传道、教导人。", version_id: 6},
  {id: 155483, book: "Matt", verse: "11.002", unformatted: "约翰在监狱里听说了基督所做的事，就派自己的几个门徒去", version_id: 6},
  {id: 155484, book: "Matt", verse: "11.003", unformatted: "问耶稣：“将要来的那一位就是你吗？还是我们应该等候别人呢？”", version_id: 6},
  {id: 155485, book: "Matt", verse: "11.004", unformatted: "耶稣回答他们，说：“你们去把所听见、所看见的告诉约翰，", version_id: 6},
  {id: 155486, book: "Matt", verse: "11.005", unformatted: "就是：瞎眼的得以看见，瘸腿的行走，麻风病人被洁净，耳聋的听见，死人得以复活，穷人得以听到福音。", version_id: 6},
  {id: 155487, book: "Matt", verse: "11.006", unformatted: "凡不因我被绊倒的人，就蒙福了。”", version_id: 6},
  {id: 155488, book: "Matt", verse: "11.007", unformatted: "约翰的门徒们走了，耶稣就开始对众人谈论约翰，说：“你们曾经到旷野去，是要观看什么呢？一根被风吹动的芦苇吗？", version_id: 6},
  {id: 155489, book: "Matt", verse: "11.008", unformatted: "你们出去，究竟是要看什么呢？一个身穿细软衣服的人吗？看哪，那些穿细软衣服的人在王宫里。", version_id: 6},
  {id: 155490, book: "Matt", verse: "11.009", unformatted: "你们出去，究竟是要看什么呢？一位先知吗？是的，我告诉你们：他比先知还大。", version_id: 6},
  {id: 155491, book: "Matt", verse: "11.01", unformatted: "他就是经上记载的那个人：\n‘看哪，我要差派我的使者在你前头，\n他要在你之前预备你的道。’", version_id: 6},
  {id: 155492, book: "Matt", verse: "11.011", unformatted: "“我确实地告诉你们：在女人所生的人中，没有出现过一个比施洗者约翰更大的；然而在天国里，再小的也比约翰更大。", version_id: 6},
  {id: 155493, book: "Matt", verse: "11.012", unformatted: "从施洗者约翰的日子到现在，天国是努力争取的，努力争取的人就得着它。", version_id: 6},
  {id: 155494, book: "Matt", verse: "11.013", unformatted: "在约翰之前，所有的先知和律法都说预言。", version_id: 6},
  {id: 155495, book: "Matt", verse: "11.014", unformatted: "如果你们愿意接受，约翰就是那位要来的以利亚。", version_id: 6},
  {id: 155496, book: "Matt", verse: "11.015", unformatted: "凡是有耳的，就应当听！", version_id: 6},
  {id: 155497, book: "Matt", verse: "11.016", unformatted: "麻木的世代\n“我要把这世代比做什么呢？它就好比孩子们坐在街市上呼叫别的孩子，", version_id: 6},
  {id: 155498, book: "Matt", verse: "11.017", unformatted: "说：\n‘我们为你们吹笛，\n你们却不跳舞；\n我们唱哀歌，\n你们也不捶胸。’", version_id: 6},
  {id: 155499, book: "Matt", verse: "11.018", unformatted: "“因为约翰来了，不吃也不喝，他们就说：‘他有鬼魔附身！’", version_id: 6},
  {id: 155500, book: "Matt", verse: "11.019", unformatted: "人子来了，也吃也喝，他们就说：‘看，这个贪食好酒的人！税吏和罪人的朋友！’然而，智慧是凭着智慧的行为被证明为义的。”", version_id: 6},
  {id: 155501, book: "Matt", verse: "11.02", unformatted: "接着，耶稣开始斥责那些他曾经在其中行了许多神迹的城市，因为那里的人不肯悔改：", version_id: 6},
  {id: 155502, book: "Matt", verse: "11.021", unformatted: "“哥拉汛哪，你有祸了！伯赛达呀，你有祸了！因为在你们当中行过的神迹，如果行在提尔和西顿，那里的人早就会披麻蒙灰悔改了。", version_id: 6},
  {id: 155503, book: "Matt", verse: "11.022", unformatted: "不过我告诉你们：在那审判的日子，提尔和西顿所受的，将要比你们还容易受呢！", version_id: 6},
  {id: 155504, book: "Matt", verse: "11.023", unformatted: "而你——迦百农啊，你将被高举到天上吗？不，你将下到阴间。因为在你中间行过的神迹，如果行在所多玛，它就会存留到今天。", version_id: 6},
  {id: 155505, book: "Matt", verse: "11.024", unformatted: "不过我告诉你们：在那审判的日子，所多玛之地所受的，将要比你还容易受呢！”", version_id: 6},
  {id: 155506, book: "Matt", verse: "11.025", unformatted: "到主那里得安息\n那时候，耶稣说：“父啊，天地的主，我赞美你，因为你把这些事向有智慧的和有学问的人隐藏起来，而向小孩子们显明出来。", version_id: 6},
  {id: 155507, book: "Matt", verse: "11.026", unformatted: "是的，父啊！因为这正是你的美意。", version_id: 6},
  {id: 155508, book: "Matt", verse: "11.027", unformatted: "我父把一切都交给了我。除了父，没有人认识子；除了子和子所愿意启示的人，也没有人认识父。", version_id: 6},
  {id: 155509, book: "Matt", verse: "11.028", unformatted: "“所有劳苦和背负重担的人哪，到我这里来吧！我将使你们得到安息。", version_id: 6},
  {id: 155510, book: "Matt", verse: "11.029", unformatted: "你们当负起我的轭，向我学，因为我心地柔和、谦卑。这样，你们就将寻得灵魂的安息。", version_id: 6},
  {id: 155511, book: "Matt", verse: "11.03", unformatted: "要知道，我的轭是容易负的，我的担子是轻省的。”", version_id: 6},
  {id: 155512, book: "Matt", verse: "12.001", unformatted: "安息日的主\n在安息日那天，耶稣从麦田经过。他的门徒们饿了，就开始摘麦穗吃。", version_id: 6},
  {id: 155513, book: "Matt", verse: "12.002", unformatted: "有些法利赛人看见了，就对耶稣说：“你看，你的门徒们做了在安息日不可以做的事。”", version_id: 6},
  {id: 155514, book: "Matt", verse: "12.003", unformatted: "耶稣对他们说：“大卫和那些与他在一起的人饥饿时所做的事，你们难道没有读过吗？", version_id: 6},
  {id: 155515, book: "Matt", verse: "12.004", unformatted: "他不是进了神的殿，和他们吃了陈设饼吗？这饼是他和那些与他在一起的人不可以吃的，唯有祭司才可以吃。", version_id: 6},
  {id: 155516, book: "Matt", verse: "12.005", unformatted: "再者，你们难道没有在律法书上读过：当安息日，祭司们在圣殿里触犯了安息日，也是无罪的吗？", version_id: 6},
  {id: 155517, book: "Matt", verse: "12.006", unformatted: "但是我告诉你们：比圣殿更大的就在这里！", version_id: 6},
  {id: 155518, book: "Matt", verse: "12.007", unformatted: "你们如果明白‘我要的是怜悯，而不是祭祀’这话是什么意思，就不会把无罪的定为有罪了。", version_id: 6},
  {id: 155519, book: "Matt", verse: "12.008", unformatted: "因为人子是安息日的主。”", version_id: 6},
  {id: 155520, book: "Matt", verse: "12.009", unformatted: "治愈手萎缩的人\n耶稣离开那地方，进了他们的会堂。", version_id: 6},
  {id: 155521, book: "Matt", verse: "12.01", unformatted: "看哪，有个一只手枯萎的人。有些人就问耶稣，说：“在安息日使人痊愈，是否可以呢？”这是为要控告耶稣。", version_id: 6},
  {id: 155522, book: "Matt", verse: "12.011", unformatted: "耶稣对他们说：“你们当中有哪一个人，如果他有一只羊在安息日掉进坑里，他不会抓住羊，把它拉上来呢？", version_id: 6},
  {id: 155523, book: "Matt", verse: "12.012", unformatted: "人比羊重要多了！所以，在安息日做好事是可以的。”", version_id: 6},
  {id: 155524, book: "Matt", verse: "12.013", unformatted: "于是耶稣对那个人说：“伸出你的手！”他一伸出来，手就复原了，和另一只一样健全。", version_id: 6},
  {id: 155525, book: "Matt", verse: "12.014", unformatted: "那些法利赛人就出去，商议怎样对付耶稣，好除灭他。", version_id: 6},
  {id: 155526, book: "Matt", verse: "12.015", unformatted: "神的仆人\n耶稣知道了，就离开那里。有一大群人跟随他，耶稣使他们都痊愈了，", version_id: 6},
  {id: 155527, book: "Matt", verse: "12.016", unformatted: "并且告诫他们不要把他传扬出去。", version_id: 6},
  {id: 155528, book: "Matt", verse: "12.017", unformatted: "这是为要应验那藉着先知以赛亚所说的话：", version_id: 6},
  {id: 155529, book: "Matt", verse: "12.018", unformatted: "“看哪，我所拣选的仆人，\n我所爱的，心所喜悦的。\n我要把我的灵赐给他，\n他要向外邦人宣扬审判的事。", version_id: 6},
  {id: 155530, book: "Matt", verse: "12.019", unformatted: "他不争竞、不喧嚷，\n大街上也没有人听见他的声音。", version_id: 6},
  {id: 155531, book: "Matt", verse: "12.02", unformatted: "压伤的芦苇，他不折断；\n将熄的灯火，他不吹灭，\n直到他使公义得胜，", version_id: 6},
  {id: 155532, book: "Matt", verse: "12.021", unformatted: "外邦人将要仰望他的名。”", version_id: 6},
  {id: 155533, book: "Matt", verse: "12.022", unformatted: "自相纷争\n那时，一个有鬼魔附身、又瞎又哑的人被带到耶稣那里。耶稣使他痊愈，那哑巴就能说话，也能看见了。", version_id: 6},
  {id: 155534, book: "Matt", verse: "12.023", unformatted: "众人都十分惊讶，说：“难道这个人就是‘大卫的后裔’？”", version_id: 6},
  {id: 155535, book: "Matt", verse: "12.024", unformatted: "但法利赛人听见了却说：“这个人如果不是靠着鬼魔的王别西卜，就不能驱赶鬼魔！”", version_id: 6},
  {id: 155536, book: "Matt", verse: "12.025", unformatted: "耶稣知道他们的想法，就对他们说：“任何一个国家，如果自相纷争就成为荒芜；任何一个城或家庭，如果自相纷争就站立不住。", version_id: 6},
  {id: 155537, book: "Matt", verse: "12.026", unformatted: "如果撒旦驱赶撒旦，就是自相纷争，它的国怎么能站立得住呢？", version_id: 6},
  {id: 155538, book: "Matt", verse: "12.027", unformatted: "如果我是靠别西卜驱赶鬼魔，那么，你们的子弟是靠谁驱赶鬼魔呢？为此，你们的子弟就将要成为你们的审判者了。", version_id: 6},
  {id: 155539, book: "Matt", verse: "12.028", unformatted: "然而，如果我藉着神的灵驱赶鬼魔，那么，神的国就已经临到你们了。", version_id: 6},
  {id: 155540, book: "Matt", verse: "12.029", unformatted: "人怎么能进入一个壮士的家抢夺他的东西呢？如果不先把那壮士捆住，就不能抢夺他的家。", version_id: 6},
  {id: 155541, book: "Matt", verse: "12.03", unformatted: "不与我在一起的，就是在反对我；不与我一起收集的，就是在拆散。", version_id: 6},
  {id: 155542, book: "Matt", verse: "12.031", unformatted: "为此，我告诉你们：人一切的罪和亵渎的话都能被赦免，但亵渎圣灵却不能被赦免。", version_id: 6},
  {id: 155543, book: "Matt", verse: "12.032", unformatted: "凡是说话抵挡人子的，还能被赦免；但如果有人说话抵挡圣灵，无论在这世代，或在将要来的世代，都不能被赦免。", version_id: 6},
  {id: 155544, book: "Matt", verse: "12.033", unformatted: "树与果\n“你们让一棵树好，它的果子就好；你们让一棵树坏，它的果子就坏；这样，一棵树是凭着果子被认出来。", version_id: 6},
  {id: 155545, book: "Matt", verse: "12.034", unformatted: "你们这些毒蛇的子孙！你们做为恶人，怎么能说出善的来呢？因为心里所充满的，口里就说出来。", version_id: 6},
  {id: 155546, book: "Matt", verse: "12.035", unformatted: "好人从他心里所存的善，发出善来；坏人从他心里所存的恶，发出恶来。", version_id: 6},
  {id: 155547, book: "Matt", verse: "12.036", unformatted: "我告诉你们：人所说的每一句无益的话，在那审判的日子，句句都要做出交代，", version_id: 6},
  {id: 155548, book: "Matt", verse: "12.037", unformatted: "因为凭你的话，你将被称为义；也凭你的话，你将被定有罪。”", version_id: 6},
  {id: 155549, book: "Matt", verse: "12.038", unformatted: "约拿的神迹\n当时，有一些经文士和法利赛人对耶稣说：“老师，我们希望你显个神迹给我们看。”", version_id: 6},
  {id: 155550, book: "Matt", verse: "12.039", unformatted: "耶稣回答他们，说：“一个邪恶、淫乱的世代会寻求神迹；可是除了先知约拿的神迹以外，不会有神迹赐给它了。", version_id: 6},
  {id: 155551, book: "Matt", verse: "12.04", unformatted: "就是说，正如约拿在大鱼的肚子里三天三夜，人子也照样将要在地心三天三夜。", version_id: 6},
  {id: 155552, book: "Matt", verse: "12.041", unformatted: "在审判的时候，尼尼微人将要与这世代一起复活，并且要定这世代的罪，因为尼尼微人因着约拿的传道就悔改了。看，比约拿更大的就在这里！", version_id: 6},
  {id: 155553, book: "Matt", verse: "12.042", unformatted: "在审判的时候，南方的女王将要与这世代一起复活，并且要定这世代的罪，因为她从极远的地方来，要听所罗门智慧的话语。看，比所罗门更大的就在这里！", version_id: 6},
  {id: 155554, book: "Matt", verse: "12.043", unformatted: "污灵返回\n“有个污灵从一个人身上出来以后，走遍无水之地，寻找安歇的地方，却找不到，", version_id: 6},
  {id: 155555, book: "Matt", verse: "12.044", unformatted: "就说：‘我要回到我离开的那屋里去。’于是它回去，发现那里空着，已被打扫干净，摆设整齐，", version_id: 6},
  {id: 155556, book: "Matt", verse: "12.045", unformatted: "就去带了另外七个比自己更邪恶的灵，进去住在那里。那个人最后的情况就比先前更坏了。这邪恶的世代也将是这样。”", version_id: 6},
  {id: 155557, book: "Matt", verse: "12.046", unformatted: "真正的亲属\n耶稣正在向人群讲话，这时候他的母亲和弟弟们站在外面，想要和他说话。", version_id: 6},
  {id: 155558, book: "Matt", verse: "12.047", unformatted: "有人告诉他：“看，你母亲和弟弟们站在外面，想和你说话。”", version_id: 6},
  {id: 155559, book: "Matt", verse: "12.048", unformatted: "耶稣回答那对他说话的人，说：“我的母亲是谁呢？我的弟兄是谁呢？”", version_id: 6},
  {id: 155560, book: "Matt", verse: "12.049", unformatted: "他就伸手指着他的门徒们，说：“看，我的母亲、我的弟兄！", version_id: 6},
  {id: 155561, book: "Matt", verse: "12.05", unformatted: "因为凡是遵行我天父旨意的人，他才是我的弟兄、姐妹和母亲。”", version_id: 6},
  {id: 155562, book: "Matt", verse: "13.001", unformatted: "撒种的比喻\n那一天，耶稣从屋子里出来，在湖边坐下。", version_id: 6},
  {id: 155563, book: "Matt", verse: "13.002", unformatted: "有一大群人聚集到他那里，所以他上了一条船坐下来。众人都站在岸边。", version_id: 6},
  {id: 155564, book: "Matt", verse: "13.003", unformatted: "耶稣用比喻对他们讲了很多事。他说：“看哪，有一个撒种的出去撒种。", version_id: 6},
  {id: 155565, book: "Matt", verse: "13.004", unformatted: "他撒的时候，有些种子落在路边，飞鸟来把它们吃掉了。", version_id: 6},
  {id: 155566, book: "Matt", verse: "13.005", unformatted: "有些落在岩石地上，那里没有多少泥土，它们立刻就发芽了，因为土不深；", version_id: 6},
  {id: 155567, book: "Matt", verse: "13.006", unformatted: "当太阳升起，它们被暴晒，就枯萎了，因为没有根。", version_id: 6},
  {id: 155568, book: "Matt", verse: "13.007", unformatted: "有些落进荆棘丛里，荆棘长起来，就把它们挤住了。", version_id: 6},
  {id: 155569, book: "Matt", verse: "13.008", unformatted: "但是另有落在好土壤上的，就不断地结出果实来，有一百倍的，有六十倍的，有三十倍的。", version_id: 6},
  {id: 155570, book: "Matt", verse: "13.009", unformatted: "凡是有耳的，就应当听！”", version_id: 6},
  {id: 155571, book: "Matt", verse: "13.01", unformatted: "用比喻的原因\n门徒们上前来，问耶稣：“你对他们讲话，为什么用比喻呢？”", version_id: 6},
  {id: 155572, book: "Matt", verse: "13.011", unformatted: "耶稣回答他们，说：“天国的奥秘已经赐下来让你们知道，而没有赐给那些人；", version_id: 6},
  {id: 155573, book: "Matt", verse: "13.012", unformatted: "因为凡是有的，还要赐给他，使他丰足有余；凡是没有的，连他有的也将从他那里被拿走。", version_id: 6},
  {id: 155574, book: "Matt", verse: "13.013", unformatted: "我之所以用比喻对他们说，是因为他们虽然看了，却看不见；听了，却听不进，也不领悟。", version_id: 6},
  {id: 155575, book: "Matt", verse: "13.014", unformatted: "在他们身上，正应验了以赛亚的预言，他说：\n‘你们将听了又听，但绝不会领悟；\n你们将看了又看，但绝不会明白；", version_id: 6},
  {id: 155576, book: "Matt", verse: "13.015", unformatted: "因为这子民的心麻木了，\n他们充耳不闻，眼睛闭着；\n免得他们眼睛看见，耳朵听见，心里领悟，\n回转过来，我就使他们痊愈。’", version_id: 6},
  {id: 155577, book: "Matt", verse: "13.016", unformatted: "“但你们的眼睛是蒙福的，因为看见了；你们的耳朵是蒙福的，因为听见了。", version_id: 6},
  {id: 155578, book: "Matt", verse: "13.017", unformatted: "我确实地告诉你们：曾经有许多先知和义人渴望看你们所看见的，却没有看到；渴望听你们所听见的，却没有听到。", version_id: 6},
  {id: 155579, book: "Matt", verse: "13.018", unformatted: "解明撒种的比喻\n“所以，你们应当听这撒种的比喻：", version_id: 6},
  {id: 155580, book: "Matt", verse: "13.019", unformatted: "无论谁听了天国的话语而不领悟，那恶者就来，把那撒在他心里的夺走；这就是那被撒上种子的‘路边之地’。", version_id: 6},
  {id: 155581, book: "Matt", verse: "13.02", unformatted: "那被撒上种子的‘岩石地’是这样的人：他听了这话语，立刻怀着喜乐的心接受了它；", version_id: 6},
  {id: 155582, book: "Matt", verse: "13.021", unformatted: "只是他里面没有根而不能持久，一旦为这话语受患难或逼迫，他立刻就放弃了。", version_id: 6},
  {id: 155583, book: "Matt", verse: "13.022", unformatted: "那被撒进种子的‘荆棘丛’是这样的人：他听了这话语，但今世的忧虑和财富的迷惑把话语挤住了，他就成了不结果实的。", version_id: 6},
  {id: 155584, book: "Matt", verse: "13.023", unformatted: "那被撒上种子的‘好土壤’是这样的人：他听了这话语，就领悟了，必然结出果实来，结出的有一百倍的、有六十倍的、有三十倍的。”", version_id: 6},
  {id: 155585, book: "Matt", verse: "13.024", unformatted: "稗子的比喻\n耶稣对他们讲了另一个比喻说：“天国好比一个人把好种子撒在自己的田里。", version_id: 6},
  {id: 155586, book: "Matt", verse: "13.025", unformatted: "但是当人们睡觉的时候，他的仇敌来把稗子撒在麦子中间，然后离开了。", version_id: 6},
  {id: 155587, book: "Matt", verse: "13.026", unformatted: "到了麦苗长大结穗的时候，稗子也显出来了。", version_id: 6},
  {id: 155588, book: "Matt", verse: "13.027", unformatted: "奴仆前来问那家的主人，说：‘主人，你不是把好种子撒在你的田里吗？从哪里来的这些稗子呢？’", version_id: 6},
  {id: 155589, book: "Matt", verse: "13.028", unformatted: "“主人告诉他们：‘这是仇敌做的。’\n“奴仆问：‘那么，你要我们去把稗子拔掉吗？’", version_id: 6},
  {id: 155590, book: "Matt", verse: "13.029", unformatted: "“主人说：‘不，免得你们把稗子拔掉的时候，也把麦子一同连根拔出来。", version_id: 6},
  {id: 155591, book: "Matt", verse: "13.03", unformatted: "让两样一起长吧，直到收割的季节。在收割的时候，我要吩咐收割的人先把稗子拔掉，把它们绑成一捆一捆的好去烧掉，然后把麦子收进我的仓库里。’”", version_id: 6},
  {id: 155592, book: "Matt", verse: "13.031", unformatted: "芥菜种与面酵\n耶稣对他们讲了另一个比喻说：“天国好比一粒芥菜种子，有人拿去种在他的田里。", version_id: 6},
  {id: 155593, book: "Matt", verse: "13.032", unformatted: "它虽然比所有的种子更小，长大以后，却比各种蔬菜更大，成了一棵树，结果天空的飞鸟来了，在它的枝子上搭窝。”", version_id: 6},
  {id: 155594, book: "Matt", verse: "13.033", unformatted: "耶稣又对他们讲了另一个比喻：“天国好比酵母，一个妇人拿去拌在三斗面里，直到整团面都发了酵。”", version_id: 6},
  {id: 155595, book: "Matt", verse: "13.034", unformatted: "使用比喻\n耶稣用比喻向人群讲了这一切；他向他们讲话，没有不用比喻的。", version_id: 6},
  {id: 155596, book: "Matt", verse: "13.035", unformatted: "这是为要应验那藉着先知所说的话：\n“我要开口，\n用比喻说出创世以来被隐藏的事。”", version_id: 6},
  {id: 155597, book: "Matt", verse: "13.036", unformatted: "解明稗子的比喻\n然后，耶稣离开了人群，进了屋子。他的门徒们上前来，说：“请给我们解释一下田里稗子的比喻。”", version_id: 6},
  {id: 155598, book: "Matt", verse: "13.037", unformatted: "耶稣回答说：“那撒好种子的是人子。", version_id: 6},
  {id: 155599, book: "Matt", verse: "13.038", unformatted: "田地是这世界，这些好种子就是天国的儿女，稗子是那恶者的儿女，", version_id: 6},
  {id: 155600, book: "Matt", verse: "13.039", unformatted: "撒稗子的仇敌是魔鬼，收割的季节是世代的终结，收割的人是天使。", version_id: 6},
  {id: 155601, book: "Matt", verse: "13.04", unformatted: "就像稗子被拔出来用火烧掉，在这世代的终结也将是这样。", version_id: 6},
  {id: 155602, book: "Matt", verse: "13.041", unformatted: "人子要差派他的天使们，从他的国度里挑出来所有绊脚的和作恶的，", version_id: 6},
  {id: 155603, book: "Matt", verse: "13.042", unformatted: "把他们丢进火炉里。在那里将有哀哭和切齿。", version_id: 6},
  {id: 155604, book: "Matt", verse: "13.043", unformatted: "那时，义人会在他们父的国度里像太阳一样发光。凡是有耳的，就应当听！", version_id: 6},
  {id: 155605, book: "Matt", verse: "13.044", unformatted: "藏宝与寻珠\n“天国好比被藏在田里的财宝。有人发现了，就把它再藏起来，然后高高兴兴地去卖掉他所拥有的一切，买下了那块地。", version_id: 6},
  {id: 155606, book: "Matt", verse: "13.045", unformatted: "“天国又好比一个商人寻找精美的珍珠。", version_id: 6},
  {id: 155607, book: "Matt", verse: "13.046", unformatted: "当他发现了一颗极其贵重的珍珠，就去卖掉他所拥有的一切，买下了那颗珍珠。", version_id: 6},
  {id: 155608, book: "Matt", verse: "13.047", unformatted: "渔网的比喻\n“天国又好比一张渔网撒在海里，网到了各种鱼类。", version_id: 6},
  {id: 155609, book: "Matt", verse: "13.048", unformatted: "网一满了，渔夫们就把它拉上岸来，然后坐下，把好的收进筐里，把坏的丢在外面。", version_id: 6},
  {id: 155610, book: "Matt", verse: "13.049", unformatted: "在这世代的终结也将是这样。天使们要出去，把恶人从义人中分别出来，", version_id: 6},
  {id: 155611, book: "Matt", verse: "13.05", unformatted: "丢进火炉里。在那里将有哀哭和切齿。”", version_id: 6},
  {id: 155612, book: "Matt", verse: "13.051", unformatted: "真理的宝库\n耶稣问他们：“这一切你们都领悟了吗？”\n他们说：“是的，领悟了。”", version_id: 6},
  {id: 155613, book: "Matt", verse: "13.052", unformatted: "耶稣对他们说：“为此，凡是成为天国门徒的经文士，就好比一家的主人，从他的宝库里拿出新的和旧的东西来。”", version_id: 6},
  {id: 155614, book: "Matt", verse: "13.053", unformatted: "当耶稣讲完了这些比喻，就离开了那地方。", version_id: 6},
  {id: 155615, book: "Matt", verse: "13.054", unformatted: "在拿撒勒被拒绝\n他来到自己的家乡，在他们的会堂里教导人，以致他们惊叹不已，说：“这个人的这种智慧和能力是从哪里来的呢？", version_id: 6},
  {id: 155616, book: "Matt", verse: "13.055", unformatted: "这不就是那木匠的儿子吗？他的母亲不是叫玛丽亚吗？他的弟弟们不是雅各、约西、西门和犹大吗？", version_id: 6},
  {id: 155617, book: "Matt", verse: "13.056", unformatted: "他的妹妹们不是都和我们在一起的吗？那么，他这一切是从哪里来的呢？”", version_id: 6},
  {id: 155618, book: "Matt", verse: "13.057", unformatted: "这样，他们就对耶稣很反感。\n耶稣对他们说：“先知除了在本乡、本家以外，没有不受尊重的。”", version_id: 6},
  {id: 155619, book: "Matt", verse: "13.058", unformatted: "因为他们的不信，耶稣在那里就没有行很多神迹。", version_id: 6},
  {id: 155620, book: "Matt", verse: "14.001", unformatted: "施洗约翰之死\n那时候，分封王希律听到耶稣的消息，", version_id: 6},
  {id: 155621, book: "Matt", verse: "14.002", unformatted: "就对他的臣仆说：“这个人是施洗者约翰从死人中复活了！所以这些能力在他里面做工。”", version_id: 6},
  {id: 155622, book: "Matt", verse: "14.003", unformatted: "原来，希律为了他弟弟菲利普的妻子希罗迪娅的缘故，拘捕了约翰，把他捆起来关进监狱。", version_id: 6},
  {id: 155623, book: "Matt", verse: "14.004", unformatted: "这是因为约翰一直对他说：“你占有这妇人是不可以的。”", version_id: 6},
  {id: 155624, book: "Matt", verse: "14.005", unformatted: "希律想要杀约翰，可是怕民众，因为他们认为约翰是先知。", version_id: 6},
  {id: 155625, book: "Matt", verse: "14.006", unformatted: "到了希律生日的时候，希罗迪娅的女儿在众人面前跳舞，使希律非常喜悦，", version_id: 6},
  {id: 155626, book: "Matt", verse: "14.007", unformatted: "故此王就起誓承诺，无论她求什么都给她。", version_id: 6},
  {id: 155627, book: "Matt", verse: "14.008", unformatted: "女儿受母亲的指使，就说：“请把施洗者约翰的头放在盘子里给我。”", version_id: 6},
  {id: 155628, book: "Matt", verse: "14.009", unformatted: "王就忧忧愁愁，但因所起的誓，又因一同坐席的人，就下令给她。", version_id: 6},
  {id: 155629, book: "Matt", verse: "14.01", unformatted: "于是派人去，在监狱里砍下了约翰的头，", version_id: 6},
  {id: 155630, book: "Matt", verse: "14.011", unformatted: "把他的头放在盘子上，端给女孩，女孩就拿去给她母亲。", version_id: 6},
  {id: 155631, book: "Matt", verse: "14.012", unformatted: "后来，约翰的门徒们前来把遗体领去埋葬了，又去告诉耶稣。", version_id: 6},
  {id: 155632, book: "Matt", verse: "14.013", unformatted: "五饼二鱼\n耶稣听了这事，就上船离开那里，独自到一个荒僻的地方去。众人听到了，就从各城镇徒步来跟随他。", version_id: 6},
  {id: 155633, book: "Matt", verse: "14.014", unformatted: "耶稣下了船，看见一大群人，就对他们动了怜悯之心，使其中的病人痊愈。", version_id: 6},
  {id: 155634, book: "Matt", verse: "14.015", unformatted: "黄昏的时候，门徒们来到耶稣面前，说：“这是个荒僻的地方，而且天已经晚了。请遣散人群，好让他们到村庄去为自己买些食物。”", version_id: 6},
  {id: 155635, book: "Matt", verse: "14.016", unformatted: "但耶稣说：“他们不需要离开，你们给他们吃的吧。”", version_id: 6},
  {id: 155636, book: "Matt", verse: "14.017", unformatted: "门徒们对耶稣说：“我们这里除了五个饼和两条鱼，就没有什么了。”", version_id: 6},
  {id: 155637, book: "Matt", verse: "14.018", unformatted: "耶稣说：“拿到我这里来。”", version_id: 6},
  {id: 155638, book: "Matt", verse: "14.019", unformatted: "于是耶稣吩咐众人坐在草地上，就拿起这五个饼和两条鱼，抬头望天，祝福了，然后掰开饼，递给门徒们，他们又分给众人。", version_id: 6},
  {id: 155639, book: "Matt", verse: "14.02", unformatted: "大家都吃了，并且吃饱了。他们把剩下的碎块收拾起来，装满了十二个篮子。", version_id: 6},
  {id: 155640, book: "Matt", verse: "14.021", unformatted: "吃的人，不算妇女和孩子，约有五千。", version_id: 6},
  {id: 155641, book: "Matt", verse: "14.022", unformatted: "在水面上行走\n耶稣立刻催促门徒们上船，要他们先往对岸去；这期间，他自己遣散了人群。", version_id: 6},
  {id: 155642, book: "Matt", verse: "14.023", unformatted: "他遣散了人群以后，就独自上山去祷告。到了晚上，只有他一个人在那里。", version_id: 6},
  {id: 155643, book: "Matt", verse: "14.024", unformatted: "那时，船已经离岸有几百公尺，由于逆风，被波浪冲击。", version_id: 6},
  {id: 155644, book: "Matt", verse: "14.025", unformatted: "天快亮的时候，耶稣在湖面上行走，向他们而去。", version_id: 6},
  {id: 155645, book: "Matt", verse: "14.026", unformatted: "门徒们看见耶稣在湖面上行走，就惊慌不安，说：“是幽灵！”他们害怕得喊叫起来。", version_id: 6},
  {id: 155646, book: "Matt", verse: "14.027", unformatted: "耶稣立刻对他们说：“鼓起勇气吧！是我，不要怕。”", version_id: 6},
  {id: 155647, book: "Matt", verse: "14.028", unformatted: "彼得对他说：“主啊，如果是你，请吩咐我，从水面上走到你那里去。”", version_id: 6},
  {id: 155648, book: "Matt", verse: "14.029", unformatted: "耶稣说：“你来吧！”\n彼得就从船上下去，在水面上行走向耶稣那里去。", version_id: 6},
  {id: 155649, book: "Matt", verse: "14.03", unformatted: "但是，他一看到风极大，就惧怕起来，开始往下沉，于是呼叫说：“主啊，救救我！”", version_id: 6},
  {id: 155650, book: "Matt", verse: "14.031", unformatted: "耶稣立刻伸手拉住他，说：“小信的人哪，你为什么疑惑呢？”", version_id: 6},
  {id: 155651, book: "Matt", verse: "14.032", unformatted: "他们一上船，风就停了。", version_id: 6},
  {id: 155652, book: "Matt", verse: "14.033", unformatted: "船上的人都拜耶稣，说：“你真是神的儿子！”", version_id: 6},
  {id: 155653, book: "Matt", verse: "14.034", unformatted: "在革尼撒勒治病\n他们渡过去，到革尼撒勒上岸。", version_id: 6},
  {id: 155654, book: "Matt", verse: "14.035", unformatted: "那里的人一认出是耶稣，就派人到周围整个地区，把所有患病的都带到耶稣那里，", version_id: 6},
  {id: 155655, book: "Matt", verse: "14.036", unformatted: "恳求耶稣只要让他们摸一下他的衣服穗子。凡是摸着的人，都得了救治。", version_id: 6},
  {id: 155656, book: "Matt", verse: "15.001", unformatted: "传统与诫命\n那时，有些法利赛人和经文士从耶路撒冷来到耶稣那里，说：", version_id: 6},
  {id: 155657, book: "Matt", verse: "15.002", unformatted: "“你的门徒们为什么违犯古人的传统呢？他们吃饭的时候不洗手！”", version_id: 6},
  {id: 155658, book: "Matt", verse: "15.003", unformatted: "耶稣回答他们，说：“你们为什么为了你们的传统而违犯神的命令呢？", version_id: 6},
  {id: 155659, book: "Matt", verse: "15.004", unformatted: "神吩咐过\n‘你要孝敬你的父母’，又说\n‘咒骂父亲或母亲的人，必须处死。’", version_id: 6},
  {id: 155660, book: "Matt", verse: "15.005", unformatted: "你们却说‘如果有人对父亲或母亲说：我本该给你的已经做了圣殿奉献，", version_id: 6},
  {id: 155661, book: "Matt", verse: "15.006", unformatted: "他就可以完全不孝敬父母。’这样，为了你们的传统，你们就废弃了神的话语。", version_id: 6},
  {id: 155662, book: "Matt", verse: "15.007", unformatted: "你们这些伪善的人哪！以赛亚指着你们所说的预言是对的，他说：", version_id: 6},
  {id: 155663, book: "Matt", verse: "15.008", unformatted: "‘这子民用嘴唇尊重我，\n他们的心却远离我。", version_id: 6},
  {id: 155664, book: "Matt", verse: "15.009", unformatted: "他们敬拜我也是徒然的，\n因为他们把人的规条当做教义教导人。’”", version_id: 6},
  {id: 155665, book: "Matt", verse: "15.01", unformatted: "污秽由心而出\n耶稣召来众人，对他们说：“你们当听，也当领悟：", version_id: 6},
  {id: 155666, book: "Matt", verse: "15.011", unformatted: "不是进到口里的使人污秽；而是从口里出来的，这才使人污秽。”", version_id: 6},
  {id: 155667, book: "Matt", verse: "15.012", unformatted: "那时，门徒们前来对他说：“法利赛人听了这话很反感，你知道吗？”", version_id: 6},
  {id: 155668, book: "Matt", verse: "15.013", unformatted: "耶稣回答说：“凡不是我天父所栽种的，都将被连根拔起。", version_id: 6},
  {id: 155669, book: "Matt", verse: "15.014", unformatted: "随他们吧！他们是给瞎子领路的瞎子。如果瞎子给瞎子领路，两个人都会掉进坑里。”", version_id: 6},
  {id: 155670, book: "Matt", verse: "15.015", unformatted: "彼得对耶稣说：“请把这个比喻解释给我们听。”", version_id: 6},
  {id: 155671, book: "Matt", verse: "15.016", unformatted: "耶稣说：“到如今，你们也无知吗？", version_id: 6},
  {id: 155672, book: "Matt", verse: "15.017", unformatted: "你们难道不明白：一切进入口里的，要进到肚子，然后被排到厕所；", version_id: 6},
  {id: 155673, book: "Matt", verse: "15.018", unformatted: "而从口里出来的，是由心里发出的，这些才使人污秽。", version_id: 6},
  {id: 155674, book: "Matt", verse: "15.019", unformatted: "因为从心里发出种种恶念：杀人、通奸、淫乱、偷窃、做伪证、毁谤，", version_id: 6},
  {id: 155675, book: "Matt", verse: "15.02", unformatted: "这些才会使人污秽；而不洗手就吃饭，不会使人污秽。”", version_id: 6},
  {id: 155676, book: "Matt", verse: "15.021", unformatted: "外邦妇人的信心\n耶稣离开那地方，退到提尔和西顿地区。", version_id: 6},
  {id: 155677, book: "Matt", verse: "15.022", unformatted: "这时候，忽然来了一个住在那地区的迦南妇人，她呼叫说：“主啊，大卫的后裔，可怜我吧！我的女儿有鬼魔附身，痛苦万分。”", version_id: 6},
  {id: 155678, book: "Matt", verse: "15.023", unformatted: "耶稣一句话也不回应，他的门徒们上前来求他，说：“这妇人一直跟在我们后面喊叫，请让她走吧。”", version_id: 6},
  {id: 155679, book: "Matt", verse: "15.024", unformatted: "耶稣回答说：“我奉差派，只到以色列家迷失的羊那里去。”", version_id: 6},
  {id: 155680, book: "Matt", verse: "15.025", unformatted: "那妇人来拜他，说：“主啊，求你帮助我！”", version_id: 6},
  {id: 155681, book: "Matt", verse: "15.026", unformatted: "耶稣回答说：“拿儿女的饼扔给小狗，是不合宜的。”", version_id: 6},
  {id: 155682, book: "Matt", verse: "15.027", unformatted: "妇人说：“是的，主啊！不过连小狗也吃得到主人桌子上掉下来的碎渣！”", version_id: 6},
  {id: 155683, book: "Matt", verse: "15.028", unformatted: "于是耶稣回答她，说：“哦，妇人哪，你这信心是大的！照你所愿的，给你成全吧。”从那一刻起，她的女儿就痊愈了。", version_id: 6},
  {id: 155684, book: "Matt", verse: "15.029", unformatted: "在加利利湖边治病\n耶稣离开那地方，来到加利利湖边，上了山坐在那里。", version_id: 6},
  {id: 155685, book: "Matt", verse: "15.03", unformatted: "有一大群人来到他面前，带着瘸腿的、瞎眼的、残疾的、聋哑的还有许多其他人，把他们放在耶稣脚前；耶稣就使他们痊愈了。", version_id: 6},
  {id: 155686, book: "Matt", verse: "15.031", unformatted: "结果众人看见聋哑的说话，残疾的康复，瘸腿的行走，瞎眼的看见，都感到惊奇，就荣耀以色列的神。", version_id: 6},
  {id: 155687, book: "Matt", verse: "15.032", unformatted: "使四千人吃饱\n耶稣召来他的门徒们，说：“我怜悯这群人，因为他们与我在一起已经三天了，也没有什么东西吃。我不愿意让他们饿着肚子回去，恐怕他们在路上晕倒。”", version_id: 6},
  {id: 155688, book: "Matt", verse: "15.033", unformatted: "门徒们说：“我们在旷野，从哪里得这么多的食物让这么大一群人吃饱呢？”", version_id: 6},
  {id: 155689, book: "Matt", verse: "15.034", unformatted: "耶稣问他们：“你们有多少饼？”\n他们回答说：“七个，还有几条小鱼。”", version_id: 6},
  {id: 155690, book: "Matt", verse: "15.035", unformatted: "耶稣就吩咐众人坐在地上，", version_id: 6},
  {id: 155691, book: "Matt", verse: "15.036", unformatted: "于是拿起这七个饼和几条小鱼，祝谢以后，掰开，不断递给门徒们，他们又分给众人。", version_id: 6},
  {id: 155692, book: "Matt", verse: "15.037", unformatted: "大家都吃了，并且吃饱了。然后，他们把剩下的碎块收拾起来，装满了七个筐子。", version_id: 6},
  {id: 155693, book: "Matt", verse: "15.038", unformatted: "吃的人，不算妇女和孩子，就有四千。", version_id: 6},
  {id: 155694, book: "Matt", verse: "15.039", unformatted: "耶稣遣散了人群以后，就上了船，来到茉加丹地区。", version_id: 6},
  {id: 155695, book: "Matt", verse: "16.001", unformatted: "法利赛人与撒都该人的酵\n有些法利赛人和撒都该人前来试探耶稣，要求他把一个从天上来的神迹显给他们看。", version_id: 6},
  {id: 155696, book: "Matt", verse: "16.002", unformatted: "耶稣回答他们，说：“黄昏的时候你们说：‘天要晴了，因为天色通红’；", version_id: 6},
  {id: 155697, book: "Matt", verse: "16.003", unformatted: "清晨的时候你们说：‘今天会有风雨，因为天色通红又变得阴沉。’你们能分辨天象，却不能分辨时代的征兆。", version_id: 6},
  {id: 155698, book: "Matt", verse: "16.004", unformatted: "一个邪恶、淫乱的世代会寻求神迹，可是除了约拿的神迹以外，不会有神迹赐给它了。”然后耶稣就离开他们走了。", version_id: 6},
  {id: 155699, book: "Matt", verse: "16.005", unformatted: "门徒们到了对岸，忘了带饼。", version_id: 6},
  {id: 155700, book: "Matt", verse: "16.006", unformatted: "耶稣对他们说：“你们要注意，要提防法利赛人和撒都该人的酵母。”", version_id: 6},
  {id: 155701, book: "Matt", verse: "16.007", unformatted: "门徒们就彼此讨论说：“这是因为我们没有带饼吧。”", version_id: 6},
  {id: 155702, book: "Matt", verse: "16.008", unformatted: "耶稣知道了，就说：“小信的人哪，你们为什么彼此讨论没有带饼的事呢？", version_id: 6},
  {id: 155703, book: "Matt", verse: "16.009", unformatted: "你们还不明白吗？难道不记得那五个饼给五千人，你们收拾了几个篮子的碎块吗？", version_id: 6},
  {id: 155704, book: "Matt", verse: "16.01", unformatted: "也不记得那七个饼给四千人，你们收拾了几个筐子的碎块吗？", version_id: 6},
  {id: 155705, book: "Matt", verse: "16.011", unformatted: "你们怎么不明白，我对你们说的不是饼的事，而是要你们提防法利赛人和撒都该人的酵母呢？”", version_id: 6},
  {id: 155706, book: "Matt", verse: "16.012", unformatted: "门徒们这才领悟耶稣所说的不是要他们提防饼的酵母，而是要他们提防法利赛人和撒都该人的教导。", version_id: 6},
  {id: 155707, book: "Matt", verse: "16.013", unformatted: "彼得认耶稣为基督\n耶稣来到凯撒里亚菲利彼地区，就问他的门徒们，说：“人们说人子是谁？”", version_id: 6},
  {id: 155708, book: "Matt", verse: "16.014", unformatted: "他们回答说：“有的说是施洗者约翰，有的说是以利亚，另有的说是耶利米或是先知中的一位。”", version_id: 6},
  {id: 155709, book: "Matt", verse: "16.015", unformatted: "耶稣问他们：“那么你们呢？你们说我是谁？”", version_id: 6},
  {id: 155710, book: "Matt", verse: "16.016", unformatted: "西门彼得回答说：“你是基督，是永生神的儿子！”", version_id: 6},
  {id: 155711, book: "Matt", verse: "16.017", unformatted: "耶稣对他说：“约拿的儿子西门，你是蒙福的，因为这不是任何人启示你的，而是我在天上的父启示你的。", version_id: 6},
  {id: 155712, book: "Matt", verse: "16.018", unformatted: "我还告诉你：你是彼得，我要在这磐石上建立我的教会，阴间的门不能胜过它。", version_id: 6},
  {id: 155713, book: "Matt", verse: "16.019", unformatted: "我要把天国的钥匙赐给你，你在地上所捆绑的，在天上将是已经被捆绑了的；你在地上所释放的，在天上将是被释放了的。”", version_id: 6},
  {id: 155714, book: "Matt", verse: "16.02", unformatted: "接着耶稣吩咐门徒们，不要对任何人说他就是基督。", version_id: 6},
  {id: 155715, book: "Matt", verse: "16.021", unformatted: "预言受难与复活\n从那时候起，耶稣开始向他的门徒们指示他必须到耶路撒冷去，经受长老们、祭司长们和经文士们的很多苦害，并且被杀，然后在第三天要复活。", version_id: 6},
  {id: 155716, book: "Matt", verse: "16.022", unformatted: "彼得把耶稣拉到一边，开始劝阻他，说：“主啊，千万不可，这事绝不能临到你身上。”", version_id: 6},
  {id: 155717, book: "Matt", verse: "16.023", unformatted: "但耶稣转身对彼得说：“撒旦，退到我后面去！你是我的绊脚石，因为你不是思想神的事，而是思想人的事。”", version_id: 6},
  {id: 155718, book: "Matt", verse: "16.024", unformatted: "背起你的十字架\n于是，耶稣对他的门徒们说：“如果有人想要来跟从我，他就当舍弃自己，背起自己的十字架，然后跟从我。", version_id: 6},
  {id: 155719, book: "Matt", verse: "16.025", unformatted: "因为凡想要保全自己生命的，将失去生命；凡为我的缘故失去自己生命的，将寻得生命。", version_id: 6},
  {id: 155720, book: "Matt", verse: "16.026", unformatted: "一个人就是赚得了全世界，却赔上了自己的生命，到底有什么益处呢？人还能拿什么来换回自己的生命呢？", version_id: 6},
  {id: 155721, book: "Matt", verse: "16.027", unformatted: "人子将要在他父的荣耀中，与他的天使们一起来临。那时候，他要按照各人的所作所为回报每个人。", version_id: 6},
  {id: 155722, book: "Matt", verse: "16.028", unformatted: "我确实地告诉你们：站在这里的有些人，在还没有尝到死的滋味之前，必定见到人子在他的国度中来临。”", version_id: 6},
  {id: 155723, book: "Matt", verse: "17.001", unformatted: "登山变相\n过了六天，耶稣带着彼得、雅各和雅各的弟弟约翰，领他们悄悄地上了一座高山。", version_id: 6},
  {id: 155724, book: "Matt", verse: "17.002", unformatted: "在他们面前，耶稣变了形像，脸面光耀如日，衣服洁白如光。", version_id: 6},
  {id: 155725, book: "Matt", verse: "17.003", unformatted: "这时候，忽然摩西和以利亚向他们显现，与耶稣谈话。", version_id: 6},
  {id: 155726, book: "Matt", verse: "17.004", unformatted: "彼得对耶稣说：“主啊，我们在这里真好！如果你愿意，我就在这里搭三座帐幕，一座为你，一座为摩西，一座为以利亚。”", version_id: 6},
  {id: 155727, book: "Matt", verse: "17.005", unformatted: "彼得还在说话的时候，忽然有一朵灿烂的云彩笼罩了他们，看哪，有声音从云中出来，说：\n“这是我的爱子，\n我所喜悦的。\n你们当听从他！”", version_id: 6},
  {id: 155728, book: "Matt", verse: "17.006", unformatted: "门徒们听见了，就把脸伏在地上，极其惧怕。", version_id: 6},
  {id: 155729, book: "Matt", verse: "17.007", unformatted: "耶稣前来，拍拍他们说：“起来，不要怕！”", version_id: 6},
  {id: 155730, book: "Matt", verse: "17.008", unformatted: "他们抬头一看，只看见耶稣一个人，没有别人。", version_id: 6},
  {id: 155731, book: "Matt", verse: "17.009", unformatted: "他们下山的时候，耶稣吩咐他们：“人子从死人中复活以前，不要把这异象告诉任何人。”", version_id: 6},
  {id: 155732, book: "Matt", verse: "17.01", unformatted: "门徒们问耶稣，说：“那么，经文士们为什么说以利亚必须先来呢？”", version_id: 6},
  {id: 155733, book: "Matt", verse: "17.011", unformatted: "耶稣回答说：“以利亚确实要来，并且要恢复万事。", version_id: 6},
  {id: 155734, book: "Matt", verse: "17.012", unformatted: "但是我告诉你们：以利亚已经来了，人们却不认他，反而任意对待他；照样，人子也将受到他们的苦害。”", version_id: 6},
  {id: 155735, book: "Matt", verse: "17.013", unformatted: "门徒们这才领悟，耶稣向他们说的是指施洗者约翰。", version_id: 6},
  {id: 155736, book: "Matt", verse: "17.014", unformatted: "信心的力量\n他们来到众人那里，有一个人来到耶稣面前，跪下来，", version_id: 6},
  {id: 155737, book: "Matt", verse: "17.015", unformatted: "说：“主啊，可怜我的儿子吧！他患了癫痫病，十分痛苦，经常跌进火里，也经常跌进水里。", version_id: 6},
  {id: 155738, book: "Matt", verse: "17.016", unformatted: "我带他到你的门徒们那里，可是他们不能使他痊愈。”", version_id: 6},
  {id: 155739, book: "Matt", verse: "17.017", unformatted: "耶稣回答说：“唉，这个不信、歪曲的世代！我还要与你们在一起多久呢？我还要容忍你们多久呢？把孩子带到我这里来吧！”", version_id: 6},
  {id: 155740, book: "Matt", verse: "17.018", unformatted: "耶稣斥责那鬼魔，鬼魔就从孩子身上出来；从那一刻起，孩子就痊愈了。", version_id: 6},
  {id: 155741, book: "Matt", verse: "17.019", unformatted: "门徒们悄悄地前来问耶稣：“我们为什么不能赶出那鬼魔呢？”", version_id: 6},
  {id: 155742, book: "Matt", verse: "17.02", unformatted: "耶稣回答说：“因为你们小信。我确实地告诉你们：如果你们有像一粒芥菜种子那样的信仰，就是对这座山说‘从这里移到那里！’它也将移开；而且在你们，将没有不可能的事。", version_id: 6},
  {id: 155743, book: "Matt", verse: "17.021", unformatted: "不过对这一类的鬼魔，如果不藉着祷告和禁食，它就不出来。”", version_id: 6},
  {id: 155744, book: "Matt", verse: "17.022", unformatted: "再次预言受难\n他们聚集在加利利的时候，耶稣对门徒们说：“人子将要被交在人的手中。", version_id: 6},
  {id: 155745, book: "Matt", verse: "17.023", unformatted: "他们要杀害他，然后在第三天，他要复活。”门徒们就极其忧伤。", version_id: 6},
  {id: 155746, book: "Matt", verse: "17.024", unformatted: "缴纳圣殿税\n他们来到迦百农，那些收圣殿税的人来到彼得那里，说：“你们的老师不纳圣殿税吗？”", version_id: 6},
  {id: 155747, book: "Matt", verse: "17.025", unformatted: "彼得说：“当然纳。”\n彼得进了屋子，耶稣先问他：“西门，这件事你怎么看？地上的众君王向谁征收关税和丁税呢？向自己的儿女，还是向外人呢？”", version_id: 6},
  {id: 155748, book: "Matt", verse: "17.026", unformatted: "彼得回答说：“向外人。”\n耶稣说：“因此儿女可以免了。", version_id: 6},
  {id: 155749, book: "Matt", verse: "17.027", unformatted: "但为了不绊倒他们，你到湖边去投下鱼钩，拿起钓上来的第一条鱼，打开鱼的嘴，会发现一个大银币。你就把它拿去，为我和你交给他们吧。”", version_id: 6},
  {id: 155750, book: "Matt", verse: "18.001", unformatted: "谁是更大的\n那时，门徒们前来问耶稣：“在天国里究竟谁更大？”", version_id: 6},
  {id: 155751, book: "Matt", verse: "18.002", unformatted: "耶稣叫来一个小孩子，让他站在他们当中，", version_id: 6},
  {id: 155752, book: "Matt", verse: "18.003", unformatted: "说：“我确实地告诉你们：你们如果不回转，变得像小孩子一样，绝不能进入天国。", version_id: 6},
  {id: 155753, book: "Matt", verse: "18.004", unformatted: "所以，谁像这小孩子自我降卑，谁在天国里就更大。", version_id: 6},
  {id: 155754, book: "Matt", verse: "18.005", unformatted: "无论谁奉我的名接受这样一个小孩子，就是接受我。", version_id: 6},
  {id: 155755, book: "Matt", verse: "18.006", unformatted: "“但无论谁使信我的这些卑微人中的一个绊倒，对那人来说，倒不如脖子上被拴上大磨石，沉到深海里。", version_id: 6},
  {id: 155756, book: "Matt", verse: "18.007", unformatted: "这世界有祸了，因为有使人绊脚的事；要知道，那些使人绊脚的事必然出现，不过使它出现的人有祸了。", version_id: 6},
  {id: 155757, book: "Matt", verse: "18.008", unformatted: "如果你的一只手或一只脚使你绊倒，就把它砍下来丢掉！对你来说，缺一只手或缺一只脚进入永生，要比手脚双全被丢进永远的火里好多了。", version_id: 6},
  {id: 155758, book: "Matt", verse: "18.009", unformatted: "如果你的一只眼使你绊倒，就把它剜出来丢掉！对你来说，缺一只眼进入永生，要比双眼齐全被丢进烈火的地狱里好多了。", version_id: 6},
  {id: 155759, book: "Matt", verse: "18.01", unformatted: "迷羊的比喻\n“你们要注意，不可轻视这些卑微人中的一个。我告诉你们：他们的天使在天上常常看见我天父的面。", version_id: 6},
  {id: 155760, book: "Matt", verse: "18.011", unformatted: "要知道人子来，是为了拯救迷失的人。", version_id: 6},
  {id: 155761, book: "Matt", verse: "18.012", unformatted: "你们怎么看以下的事？如果一个人有一百只羊，其中一只迷失了，难道他不把九十九只留在山上，去寻找那一只迷失的吗？", version_id: 6},
  {id: 155762, book: "Matt", verse: "18.013", unformatted: "如果找到了，我确实地告诉你们：他为这一只羊欢喜，会比为那九十九只没有迷失的羊更欢喜。", version_id: 6},
  {id: 155763, book: "Matt", verse: "18.014", unformatted: "照样，你们在天上的父也不愿意失去这些卑微人中的一个。", version_id: 6},
  {id: 155764, book: "Matt", verse: "18.015", unformatted: "挽回弟兄\n“如果你的弟兄对你犯了罪，你就要去，只在你和他之间责备他。如果他听你的，你就赢得了你的弟兄；", version_id: 6},
  {id: 155765, book: "Matt", verse: "18.016", unformatted: "如果他不听，你就另外带一个或两个人一起去，为要使“任何事，凭两个或三个见证人的口，才能成立。”", version_id: 6},
  {id: 155766, book: "Matt", verse: "18.017", unformatted: "如果他不听他们的，就告诉教会；如果他连教会也不听，就应当把他看做像外邦人或税吏一样。", version_id: 6},
  {id: 155767, book: "Matt", verse: "18.018", unformatted: "我确实地告诉你们：你们在地上所捆绑的，在天上将是已经被捆绑了的；你们在地上所释放的，在天上将是被释放了的。", version_id: 6},
  {id: 155768, book: "Matt", verse: "18.019", unformatted: "我再确实地告诉你们：如果你们当中有两个人，在地上同心地为任何事祈求，我在天上的父就会为你们成全；", version_id: 6},
  {id: 155769, book: "Matt", verse: "18.02", unformatted: "因为哪里有两三个人奉我的名聚集，我就在哪里，在他们当中。”", version_id: 6},
  {id: 155770, book: "Matt", verse: "18.021", unformatted: "恶仆的比喻\n那时，彼得前来问耶稣：“主啊，如果弟兄对我犯罪，我该饶恕多少次呢？到七次可以吗？”", version_id: 6},
  {id: 155771, book: "Matt", verse: "18.022", unformatted: "耶稣对他说：“我告诉你：不是七次，而是七十个七次。", version_id: 6},
  {id: 155772, book: "Matt", verse: "18.023", unformatted: "为此，天国就好比一个君王，要与他的奴仆们清算账目。", version_id: 6},
  {id: 155773, book: "Matt", verse: "18.024", unformatted: "他开始算的时候，一个欠了一千万两银子的奴仆被带到他面前。", version_id: 6},
  {id: 155774, book: "Matt", verse: "18.025", unformatted: "因为那奴仆没有可偿还的，主人就下令把他和他的妻子儿女，以及他所拥有的一切都卖掉来偿还。", version_id: 6},
  {id: 155775, book: "Matt", verse: "18.026", unformatted: "“那奴仆就俯伏拜他，说：‘请宽容我吧，将来我会把一切偿还给你。’", version_id: 6},
  {id: 155776, book: "Matt", verse: "18.027", unformatted: "那奴仆的主人动了怜悯之心，把他释放了，并且免了他的债。", version_id: 6},
  {id: 155777, book: "Matt", verse: "18.028", unformatted: "“但那奴仆出去以后，遇见一个与他同做奴仆的；这人欠他一百个银币。他就抓住这人，掐住这人的喉咙，说：‘把欠我的还给我。’", version_id: 6},
  {id: 155778, book: "Matt", verse: "18.029", unformatted: "“这人就俯伏在地央求他，说：‘请宽容我吧，我会还给你的。’", version_id: 6},
  {id: 155779, book: "Matt", verse: "18.03", unformatted: "可是他不肯，反而把这人投进监狱，直到这人能还清所欠的债。", version_id: 6},
  {id: 155780, book: "Matt", verse: "18.031", unformatted: "那些与他同做奴仆的看见所发生的事，就极其忧伤，去把一切都告诉他们的主人。", version_id: 6},
  {id: 155781, book: "Matt", verse: "18.032", unformatted: "“于是主人把那奴仆叫来，对他说：‘你这恶奴！因为你求我，我就免了你所有的债。", version_id: 6},
  {id: 155782, book: "Matt", verse: "18.033", unformatted: "难道你不也该怜悯与你同做奴仆的，像我怜悯了你一样吗？’", version_id: 6},
  {id: 155783, book: "Matt", verse: "18.034", unformatted: "主人就发怒，把他交给掌刑官，直到他能还清所欠的一切债。", version_id: 6},
  {id: 155784, book: "Matt", verse: "18.035", unformatted: "如果你们每个人不从心里饶恕自己的弟兄，我的天父也会照样对待你们。”", version_id: 6},
  {id: 155785, book: "Matt", verse: "19.001", unformatted: "论婚姻\n当耶稣讲完了这些话，就离开加利利，来到约旦河对岸的犹太地区。", version_id: 6},
  {id: 155786, book: "Matt", verse: "19.002", unformatted: "有一大群人跟随他，他就在那里使他们痊愈。", version_id: 6},
  {id: 155787, book: "Matt", verse: "19.003", unformatted: "有些法利赛人前来试探耶稣，说：“无论出于什么缘故，人是否可以休妻呢？”", version_id: 6},
  {id: 155788, book: "Matt", verse: "19.004", unformatted: "耶稣回答说：“你们难道没有读过吗？造物主从起初就把人造成男的和女的。", version_id: 6},
  {id: 155789, book: "Matt", verse: "19.005", unformatted: "并且说：\n‘为这缘故，人将离开父母，\n与妻子结合，\n两个人就成为一体。’", version_id: 6},
  {id: 155790, book: "Matt", verse: "19.006", unformatted: "这样，夫妻不再是两个人，而是一体了。因此，神所配合的，人不可分开。”", version_id: 6},
  {id: 155791, book: "Matt", verse: "19.007", unformatted: "他们问耶稣：“那么，为什么摩西吩咐说，给一份休书，就可以休妻呢？”", version_id: 6},
  {id: 155792, book: "Matt", verse: "19.008", unformatted: "耶稣对他们说：“摩西因着你们的心里刚硬，才准许你们休妻，但并不是从起初就这样的。", version_id: 6},
  {id: 155793, book: "Matt", verse: "19.009", unformatted: "我告诉你们：如果有人不是由于淫乱的缘故而休妻，另娶别人，就是犯通奸罪。”", version_id: 6},
  {id: 155794, book: "Matt", verse: "19.01", unformatted: "他的门徒们对耶稣说：“如果丈夫与妻子的关系是这样，结婚就没有益处了。”", version_id: 6},
  {id: 155795, book: "Matt", verse: "19.011", unformatted: "耶稣就对他们说：“这话不是所有人都能接受的，只有谁被赐予了，谁才能接受：", version_id: 6},
  {id: 155796, book: "Matt", verse: "19.012", unformatted: "就是说，有些独身者是生来如此的；有些独身者是别人使他们成为独身的；还有些独身者是为了天国的缘故自己成为独身的。谁能接受，就接受吧。”", version_id: 6},
  {id: 155797, book: "Matt", verse: "19.013", unformatted: "祝福小孩子\n那时，有一些小孩子被带到耶稣那里，好让耶稣按手在他们身上，给他们祷告。门徒们却责备那些人。", version_id: 6},
  {id: 155798, book: "Matt", verse: "19.014", unformatted: "耶稣就说：“让小孩子到我这里来，不要阻止他们，因为天国正属于这样的人。”", version_id: 6},
  {id: 155799, book: "Matt", verse: "19.015", unformatted: "于是，耶稣按手在他们身上，然后离开那地方。", version_id: 6},
  {id: 155800, book: "Matt", verse: "19.016", unformatted: "富有的青年官员\n看哪，有一个人上前来对耶稣说：“老师，我该做什么良善的事，才能得到永恒的生命呢？”", version_id: 6},
  {id: 155801, book: "Matt", verse: "19.017", unformatted: "耶稣对他说：“你为什么问我有关良善的事呢？只有一位是良善的。如果你想进入永生，就应该遵守诫命。”", version_id: 6},
  {id: 155802, book: "Matt", verse: "19.018", unformatted: "那个人就问：“哪些诫命呢？”\n耶稣回答说：\n“不可杀人、\n不可通奸、\n不可偷窃、\n不可做伪证、", version_id: 6},
  {id: 155803, book: "Matt", verse: "19.019", unformatted: "要孝敬父母、\n要爱邻如己。”", version_id: 6},
  {id: 155804, book: "Matt", verse: "19.02", unformatted: "那年轻人对耶稣说：“这一切我都遵守了，我还缺少什么呢？”", version_id: 6},
  {id: 155805, book: "Matt", verse: "19.021", unformatted: "耶稣对他说：“如果你想要成为完全的，就当去卖掉你所拥有的，分给穷人，这样你将有财宝在天上；然后你来跟从我。”", version_id: 6},
  {id: 155806, book: "Matt", verse: "19.022", unformatted: "那年轻人听了这话，就忧忧愁愁地走开了，因为他有很多财产。", version_id: 6},
  {id: 155807, book: "Matt", verse: "19.023", unformatted: "财富与天国\n耶稣对他的门徒们说：“我确实地告诉你们：富有的人进入天国会很困难。", version_id: 6},
  {id: 155808, book: "Matt", verse: "19.024", unformatted: "我再告诉你们：骆驼穿过针眼，比富有的人进入神的国更容易呢！”", version_id: 6},
  {id: 155809, book: "Matt", verse: "19.025", unformatted: "门徒们听了这话，极其惊讶，就说：“究竟谁能得救呢？”", version_id: 6},
  {id: 155810, book: "Matt", verse: "19.026", unformatted: "耶稣注视着他们，说：“在人，这是不可能的；但是在神，凡事都可能。”", version_id: 6},
  {id: 155811, book: "Matt", verse: "19.027", unformatted: "这时候，彼得对耶稣说：“看，我们舍弃一切跟从了你，我们将来究竟会怎么样呢？”", version_id: 6},
  {id: 155812, book: "Matt", verse: "19.028", unformatted: "耶稣对他们说：“我确实地告诉你们：在新的世界里，当人子坐在他荣耀宝座上的时候，你们这些跟从我的人也要坐在十二个宝座上，审判以色列的十二个支派。", version_id: 6},
  {id: 155813, book: "Matt", verse: "19.029", unformatted: "凡是为我名的缘故而舍弃房屋，或兄弟，或姐妹，或父亲，或母亲，或儿女，或田产的人，都将得到百倍，并且继承永恒的生命。", version_id: 6},
  {id: 155814, book: "Matt", verse: "19.03", unformatted: "不过许多在前的，将要在后；许多在后的，将要在前。", version_id: 6},
  {id: 155815, book: "Matt", verse: "20.001", unformatted: "葡萄园工人的比喻\n“天国好比一家的主人，一大早出去雇人到他的葡萄园工作。", version_id: 6},
  {id: 155816, book: "Matt", verse: "20.002", unformatted: "他与工人彼此说好一天一个银币，就派他们进他的葡萄园去。", version_id: 6},
  {id: 155817, book: "Matt", verse: "20.003", unformatted: "大约上午九点，他又出去，看见还有人闲站在街市上，", version_id: 6},
  {id: 155818, book: "Matt", verse: "20.004", unformatted: "就对他们说：‘你们也进葡萄园去吧，理当给的，我会给你们。’", version_id: 6},
  {id: 155819, book: "Matt", verse: "20.005", unformatted: "他们就去了。大约中午十二点和下午三点，主人又出去，也是这么做。", version_id: 6},
  {id: 155820, book: "Matt", verse: "20.006", unformatted: "下午五点左右，他出去看见还有人站着，就问他们：‘你们为什么整天闲站在这里呢？’", version_id: 6},
  {id: 155821, book: "Matt", verse: "20.007", unformatted: "“他们回答说：‘因为没有人雇用我们。’\n“他就说：‘你们也进葡萄园去吧。’", version_id: 6},
  {id: 155822, book: "Matt", verse: "20.008", unformatted: "到了傍晚，葡萄园的主人对管事的说：‘把工人叫来，发给他们工钱，从最后来的开始，到最先来的为止。’", version_id: 6},
  {id: 155823, book: "Matt", verse: "20.009", unformatted: "“那些下午五点左右被雇的人来了，每人领了一个银币。", version_id: 6},
  {id: 155824, book: "Matt", verse: "20.01", unformatted: "那些最先被雇的人也来了，以为会得到更多，可是他们每人也只领到一个银币。", version_id: 6},
  {id: 155825, book: "Matt", verse: "20.011", unformatted: "他们拿到钱以后，就对那家的主人抱怨，", version_id: 6},
  {id: 155826, book: "Matt", verse: "20.012", unformatted: "说：‘这些最后来的人只工作了一个小时，而我们经受了一天的劳累和炎热，你却把他们和我们同等看待！’", version_id: 6},
  {id: 155827, book: "Matt", verse: "20.013", unformatted: "“主人回答他们其中的一个人，说：‘朋友，我并没有亏负你。我们不是彼此说好了一个银币吗？", version_id: 6},
  {id: 155828, book: "Matt", verse: "20.014", unformatted: "你拿自己的走吧！我愿意给那最后来的，就像我也给你一样。", version_id: 6},
  {id: 155829, book: "Matt", verse: "20.015", unformatted: "难道我不可以用我的东西，做我愿意做的事吗？还是因为我好心，你就嫉妒了吗？’", version_id: 6},
  {id: 155830, book: "Matt", verse: "20.016", unformatted: "“这样，那些在后的，将要在前；那些在前的，将要在后。”", version_id: 6},
  {id: 155831, book: "Matt", verse: "20.017", unformatted: "第三次预言受难\n耶稣上耶路撒冷去的时候，在路上单独把十二个门徒带到一边，对他们说：", version_id: 6},
  {id: 155832, book: "Matt", verse: "20.018", unformatted: "“看，我们现在上耶路撒冷去，人子将被出卖给祭司长们和经文士们。他们要定他死罪，", version_id: 6},
  {id: 155833, book: "Matt", verse: "20.019", unformatted: "并且要把他交给外邦人去戏弄、鞭打、钉上十字架；然后在第三天，他要复活。”", version_id: 6},
  {id: 155834, book: "Matt", verse: "20.02", unformatted: "受苦与服事\n那时，西庇太儿子的母亲带着她的两个儿子上前来拜耶稣，向他求一件事。", version_id: 6},
  {id: 155835, book: "Matt", verse: "20.021", unformatted: "耶稣问她：“你要什么呢？”\n她说：“请准许我这两个儿子，在你的国里，一个坐在你的右边，一个坐在你的左边。”", version_id: 6},
  {id: 155836, book: "Matt", verse: "20.022", unformatted: "耶稣就回答说：“你们不知道自己在求什么。我将要喝的杯，你们能喝吗？”\n他们说：“我们能。”", version_id: 6},
  {id: 155837, book: "Matt", verse: "20.023", unformatted: "耶稣对他们说：“我的杯你们固然要喝，但坐在我的右边或左边，不是我所赐的，而是我父已经预备给谁，就赐给谁。”", version_id: 6},
  {id: 155838, book: "Matt", verse: "20.024", unformatted: "其他十个使徒听了，就对这兄弟两个人很不满。", version_id: 6},
  {id: 155839, book: "Matt", verse: "20.025", unformatted: "于是耶稣把他们召来，说：“你们知道，外邦人有首领压制他们，也有为大的管辖他们。", version_id: 6},
  {id: 155840, book: "Matt", verse: "20.026", unformatted: "你们当中却不是这样；相反，谁想在你们当中为大，谁就该做你们的仆人；", version_id: 6},
  {id: 155841, book: "Matt", verse: "20.027", unformatted: "无论谁想在你们当中为首，谁就该做你们的奴仆，", version_id: 6},
  {id: 155842, book: "Matt", verse: "20.028", unformatted: "就像人子来不是为了受人的服事，而是为了服事人，并且献上自己的生命，替许多人做救赎的代价。”", version_id: 6},
  {id: 155843, book: "Matt", verse: "20.029", unformatted: "治愈耶利哥的盲人\n他们离开耶利哥的时候，有一大群人跟随耶稣。", version_id: 6},
  {id: 155844, book: "Matt", verse: "20.03", unformatted: "这时候有两个瞎眼的坐在路边，他们听说是耶稣经过那里，就呼叫说：“主啊，大卫的后裔，可怜我们吧！”", version_id: 6},
  {id: 155845, book: "Matt", verse: "20.031", unformatted: "众人责备他们，叫他们不要做声。可是他们越发呼叫说：“主啊，大卫的后裔，可怜我们吧！”", version_id: 6},
  {id: 155846, book: "Matt", verse: "20.032", unformatted: "耶稣就停住，叫他们过来，说：“你们要我为你们做什么呢？”", version_id: 6},
  {id: 155847, book: "Matt", verse: "20.033", unformatted: "他们说：“主啊，愿你开我们的眼睛。”", version_id: 6},
  {id: 155848, book: "Matt", verse: "20.034", unformatted: "耶稣动了怜悯之心，摸了他们的眼睛。他们立刻重见光明，于是跟随了耶稣。", version_id: 6},
  {id: 155849, book: "Matt", verse: "21.001", unformatted: "光荣进圣城\n当耶稣和门徒们临近耶路撒冷，来到橄榄山附近的伯法其的时候，耶稣派了两个门徒，", version_id: 6},
  {id: 155850, book: "Matt", verse: "21.002", unformatted: "对他们说：“你们到对面的村子里去，会立刻发现一头驴拴在那里，还有一头驴驹与它在一起。把它们解开，牵来给我。", version_id: 6},
  {id: 155851, book: "Matt", verse: "21.003", unformatted: "如果有人对你们说什么，你们就说‘主需要它们，会很快把它们送来的。’”", version_id: 6},
  {id: 155852, book: "Matt", verse: "21.004", unformatted: "这件事发生，是为要应验那藉着先知所说的话：", version_id: 6},
  {id: 155853, book: "Matt", verse: "21.005", unformatted: "“要对锡安的女儿说\n‘看哪，你的王来到你这里，\n是谦和的，骑着驴——\n一头小驴，\n就是驴的驹子。’”", version_id: 6},
  {id: 155854, book: "Matt", verse: "21.006", unformatted: "两个门徒就去，照着耶稣所吩咐的做了。", version_id: 6},
  {id: 155855, book: "Matt", verse: "21.007", unformatted: "他们牵来了驴和驴驹，把外衣搭在它们背上，耶稣就坐在上面。", version_id: 6},
  {id: 155856, book: "Matt", verse: "21.008", unformatted: "有一大群人把自己的外衣铺在路上，也有人从树上砍下枝子铺在路上。", version_id: 6},
  {id: 155857, book: "Matt", verse: "21.009", unformatted: "前行后随的人群呼喊说：\n“和散那归于大卫的后裔！\n奉主名而来的那一位——\n是蒙祝福的！\n在至高之处——和散那！”", version_id: 6},
  {id: 155858, book: "Matt", verse: "21.01", unformatted: "耶稣进了耶路撒冷，全城都轰动了，人们问：“这个人是谁？”", version_id: 6},
  {id: 155859, book: "Matt", verse: "21.011", unformatted: "众人说：“这就是从加利利的拿撒勒来的先知耶稣。”", version_id: 6},
  {id: 155860, book: "Matt", verse: "21.012", unformatted: "洁净圣殿\n耶稣进了圣殿，把所有在圣殿里做买卖的人都赶了出去。他推翻了兑换银币之人的桌子和卖鸽子之人的凳子，", version_id: 6},
  {id: 155861, book: "Matt", verse: "21.013", unformatted: "对他们说：“经上记着：‘我的殿将被称为祷告的殿’，而你们却使它成为贼窝了！”", version_id: 6},
  {id: 155862, book: "Matt", verse: "21.014", unformatted: "小孩子赞美耶稣\n耶稣在圣殿里，一些瞎眼的和瘸腿的来到他面前，耶稣就使他们痊愈了。", version_id: 6},
  {id: 155863, book: "Matt", verse: "21.015", unformatted: "祭司长们和经文士们看见耶稣所行的神迹，又见孩子们在圣殿里呼喊说：“和散那归于大卫的后裔”，就很生气。", version_id: 6},
  {id: 155864, book: "Matt", verse: "21.016", unformatted: "他们问耶稣：“你听见这些小孩在说什么吗？”\n耶稣回答他们，说：“是的，听见了。难道你们从来没有读过‘从孩子和吃奶婴儿的口中，你预备了赞美’这话吗？”", version_id: 6},
  {id: 155865, book: "Matt", verse: "21.017", unformatted: "然后，耶稣离开他们，出城到伯大尼去，在那里过夜。", version_id: 6},
  {id: 155866, book: "Matt", verse: "21.018", unformatted: "不结果子的无花果树\n清晨，回城的时候，耶稣饿了。", version_id: 6},
  {id: 155867, book: "Matt", verse: "21.019", unformatted: "他看见路边有一棵无花果树，就上前去，可是树上除了叶子，什么也找不到。于是他对那棵树说：“你永远不会再结果子了！”那棵无花果树立刻就枯萎了。", version_id: 6},
  {id: 155868, book: "Matt", verse: "21.02", unformatted: "门徒们看见了，感到惊奇，说：“这棵无花果树怎么立刻枯萎了呢？”", version_id: 6},
  {id: 155869, book: "Matt", verse: "21.021", unformatted: "耶稣回答他们，说：“我确实地告诉你们：你们如果存着信而不疑惑，不仅能做我对这无花果树所做的，即使对这座山说‘愿你被挪开，被丢进海里去！’事情也将成就。", version_id: 6},
  {id: 155870, book: "Matt", verse: "21.022", unformatted: "你们在祷告中无论求什么，只要信，就必得着。”", version_id: 6},
  {id: 155871, book: "Matt", verse: "21.023", unformatted: "质疑耶稣的权柄\n耶稣进了圣殿，正教导人的时候，祭司长们和民间的长老们前来问他：“你凭什么权柄做这些事？是谁给你这权柄的？”", version_id: 6},
  {id: 155872, book: "Matt", verse: "21.024", unformatted: "耶稣回答他们，说：“我也要问你们一个问题，如果你们回答我，我就告诉你们我凭什么权柄做这些事。", version_id: 6},
  {id: 155873, book: "Matt", verse: "21.025", unformatted: "约翰的洗礼是从哪里来的？是来自天上，还是来自人间呢？”\n他们就彼此讨论，说：“如果我们说‘是来自天上’，他就会问我们‘那么你们为什么不相信他呢？’", version_id: 6},
  {id: 155874, book: "Matt", verse: "21.026", unformatted: "但如果说‘是来自人间’——我们又怕民众，因为大家都认为约翰是先知。”", version_id: 6},
  {id: 155875, book: "Matt", verse: "21.027", unformatted: "于是他们回答耶稣，说：“我们不知道。”\n耶稣就对他们说：“我也不告诉你们我凭什么权柄做这些事。”", version_id: 6},
  {id: 155876, book: "Matt", verse: "21.028", unformatted: "两个儿子的比喻\n“这件事你们怎么看？一个人有两个儿子，他前来对大儿子说：‘孩子，你今天到葡萄园去做工吧。’", version_id: 6},
  {id: 155877, book: "Matt", verse: "21.029", unformatted: "“大儿子回答说：‘我不愿意去。’可是后来他悔改，就去了。", version_id: 6},
  {id: 155878, book: "Matt", verse: "21.03", unformatted: "父亲前来对小儿子也说同样的话。\n“小儿子回答说：‘主啊，我去。’可是他没有去。", version_id: 6},
  {id: 155879, book: "Matt", verse: "21.031", unformatted: "“这两个儿子中，哪一个遵行父亲的意愿呢？”\n他们回答说：“大儿子。”\n耶稣说：“我确实地告诉你们：税吏和妓女要在你们的前面进神的国，", version_id: 6},
  {id: 155880, book: "Matt", verse: "21.032", unformatted: "因为约翰来到你们这里指明义的道路，你们不相信他，而那些税吏和妓女们却相信了他。你们虽然看见了，后来还是没有悔改去相信他。", version_id: 6},
  {id: 155881, book: "Matt", verse: "21.033", unformatted: "葡萄园主的比喻\n“你们当听另一个比喻：有一个人，是一家的主人，他栽种了一个葡萄园，四面围上篱笆，里面挖了一个榨酒池，盖了一座守望楼，然后把葡萄园租给一些农夫，就出外旅行。", version_id: 6},
  {id: 155882, book: "Matt", verse: "21.034", unformatted: "当收果子的季节近了，主人就派了奴仆们到那些农夫那里去收取他的果子。", version_id: 6},
  {id: 155883, book: "Matt", verse: "21.035", unformatted: "但那些农夫抓住他的奴仆，打了一个，杀了一个，还用石头砸死一个。", version_id: 6},
  {id: 155884, book: "Matt", verse: "21.036", unformatted: "主人又派别的奴仆去，比先前的更多，那些农夫也照样对待他们。", version_id: 6},
  {id: 155885, book: "Matt", verse: "21.037", unformatted: "后来他差派自己的儿子到他们那里，说：‘他们一定会尊重我的儿子。’", version_id: 6},
  {id: 155886, book: "Matt", verse: "21.038", unformatted: "“可是那些农夫看见他的儿子，就彼此说：‘这是继承人。来，我们把他杀了，占有他的继业！’", version_id: 6},
  {id: 155887, book: "Matt", verse: "21.039", unformatted: "于是他们抓住他，把他扔出葡萄园外，杀了。", version_id: 6},
  {id: 155888, book: "Matt", verse: "21.04", unformatted: "那么，葡萄园的主人回来的时候，会怎么对待那些农夫呢？”", version_id: 6},
  {id: 155889, book: "Matt", verse: "21.041", unformatted: "他们回答说：“他会狠狠地除灭那些恶人，把葡萄园另租给按时交果子的农夫们。”", version_id: 6},
  {id: 155890, book: "Matt", verse: "21.042", unformatted: "耶稣对他们说：“难道你们从来没有读过这段经文吗？\n‘工匠所弃绝的石头，\n它已经成了房角的头块石头；\n这是主所成就的，\n在我们眼中实在奇妙！’", version_id: 6},
  {id: 155891, book: "Matt", verse: "21.043", unformatted: "为此，我告诉你们：神的国要从你们中间被夺走，赐给那结神国果子的人。", version_id: 6},
  {id: 155892, book: "Matt", verse: "21.044", unformatted: "那跌落在这石头上的人，就将被破碎；这石头落在谁的身上，就会把谁砸得稀烂。”", version_id: 6},
  {id: 155893, book: "Matt", verse: "21.045", unformatted: "祭司长们和法利赛人听了耶稣的这些比喻，知道他是针对他们而说的，", version_id: 6},
  {id: 155894, book: "Matt", verse: "21.046", unformatted: "就想拘捕他，可是怕民众，因为他们都认为耶稣是先知。", version_id: 6},
  {id: 155895, book: "Matt", verse: "22.001", unformatted: "婚宴的比喻\n耶稣又用比喻对他们说：", version_id: 6},
  {id: 155896, book: "Matt", verse: "22.002", unformatted: "“天国好比一个君王，为自己的儿子预备婚宴。", version_id: 6},
  {id: 155897, book: "Matt", verse: "22.003", unformatted: "他派了奴仆们去叫那些被邀请的人前来赴宴，可是他们不肯来。", version_id: 6},
  {id: 155898, book: "Matt", verse: "22.004", unformatted: "王又派了别的奴仆，说：‘你们去对那些被邀请的人说，看哪，我的午餐我已经预备好了，公牛和肥畜已经宰杀了，一切都预备好了，请你们来赴婚宴。’", version_id: 6},
  {id: 155899, book: "Matt", verse: "22.005", unformatted: "“但那些人不理，就走开了，有的到自己的田里去，有的做自己的生意去。", version_id: 6},
  {id: 155900, book: "Matt", verse: "22.006", unformatted: "其余的竟抓住王的奴仆们，凌辱他们，并且把他们杀了。", version_id: 6},
  {id: 155901, book: "Matt", verse: "22.007", unformatted: "王就发怒，派军兵除灭那些凶手，烧毁了他们的城。", version_id: 6},
  {id: 155902, book: "Matt", verse: "22.008", unformatted: "“然后对奴仆们说：‘婚宴预备好了，但那些被邀请的人不配。", version_id: 6},
  {id: 155903, book: "Matt", verse: "22.009", unformatted: "所以你们要到大街小巷去，把所见到的人都请来赴宴。’", version_id: 6},
  {id: 155904, book: "Matt", verse: "22.01", unformatted: "那些奴仆就出去，到大街上，把所见到的，无论好人坏人都召集起来，婚宴上就坐满了宾客。", version_id: 6},
  {id: 155905, book: "Matt", verse: "22.011", unformatted: "王进来会见宾客，发现有一个人没有穿婚宴的礼服，", version_id: 6},
  {id: 155906, book: "Matt", verse: "22.012", unformatted: "就对他说：‘朋友，你进到这里来怎么没有穿婚宴的礼服呢？’那个人哑口无言。", version_id: 6},
  {id: 155907, book: "Matt", verse: "22.013", unformatted: "“于是王吩咐仆人们：‘把他的手脚捆起来，丢到外面的黑暗里去！在那里将有哀哭和切齿。’", version_id: 6},
  {id: 155908, book: "Matt", verse: "22.014", unformatted: "“要知道，蒙召唤的人多，而蒙拣选的人少。”", version_id: 6},
  {id: 155909, book: "Matt", verse: "22.015", unformatted: "神与凯撒\n法利赛人就去商议，怎样找耶稣的话柄来陷害他。", version_id: 6},
  {id: 155910, book: "Matt", verse: "22.016", unformatted: "他们派了自己的门徒们与希律党的人一同去见耶稣，说：“老师，我们知道你是真诚的，并且按真理教导神的道。你不顾忌任何人，因为你不看人的情面。", version_id: 6},
  {id: 155911, book: "Matt", verse: "22.017", unformatted: "请告诉我们，你认为向凯撒纳税，可以不可以呢？”", version_id: 6},
  {id: 155912, book: "Matt", verse: "22.018", unformatted: "耶稣看出他们的恶意，就说：“你们这些伪善的人！为什么试探我呢？", version_id: 6},
  {id: 155913, book: "Matt", verse: "22.019", unformatted: "拿一个纳税的钱币给我看。”他们就拿来了一个银币给他。", version_id: 6},
  {id: 155914, book: "Matt", verse: "22.02", unformatted: "耶稣问他们：“这是谁的像和名号？”", version_id: 6},
  {id: 155915, book: "Matt", verse: "22.021", unformatted: "他们回答说：“是凯撒的。”\n耶稣对他们说：“那么，凯撒的归给凯撒；神的归给神。”", version_id: 6},
  {id: 155916, book: "Matt", verse: "22.022", unformatted: "他们听了，感到惊奇，就离开耶稣走了。", version_id: 6},
  {id: 155917, book: "Matt", verse: "22.023", unformatted: "撒都该人与复活\n在那一天，撒都该人来到耶稣那里，他们一向说没有复活的事。他们问耶稣，", version_id: 6},
  {id: 155918, book: "Matt", verse: "22.024", unformatted: "说：“老师，摩西说：如果一个人死了，没有儿女，他的一个兄弟就要娶他的妻子，为他兄弟留后裔。", version_id: 6},
  {id: 155919, book: "Matt", verse: "22.025", unformatted: "我们这里曾经有兄弟七人，第一个结了婚，死了，没有后裔，留下妻子给他的一个兄弟。", version_id: 6},
  {id: 155920, book: "Matt", verse: "22.026", unformatted: "第二个、第三个、一直到第七个，都是如此。", version_id: 6},
  {id: 155921, book: "Matt", verse: "22.027", unformatted: "到了最后，这妇人也死了。", version_id: 6},
  {id: 155922, book: "Matt", verse: "22.028", unformatted: "既然他们都娶过她，那么，在复活的时候，她将是这七个人中哪一个的妻子呢？”", version_id: 6},
  {id: 155923, book: "Matt", verse: "22.029", unformatted: "耶稣回答说：“你们错了，因为你们不明白经上的话，也不明白神的大能。", version_id: 6},
  {id: 155924, book: "Matt", verse: "22.03", unformatted: "复活的时候，人既不娶也不嫁，而是像天上的天使一样。", version_id: 6},
  {id: 155925, book: "Matt", verse: "22.031", unformatted: "关于死人复活的事，你们难道没有读过神对你们所说的话吗？神说", version_id: 6},
  {id: 155926, book: "Matt", verse: "22.032", unformatted: "‘我是亚伯拉罕的神、以撒的神、雅各的神。’神不是死人的神，而是活人的神。”", version_id: 6},
  {id: 155927, book: "Matt", verse: "22.033", unformatted: "众人听了这话，对他的教导惊叹不已。", version_id: 6},
  {id: 155928, book: "Matt", verse: "22.034", unformatted: "最大的诫命\n法利赛人听说耶稣使撒都该人哑口无言，就聚集在一起。", version_id: 6},
  {id: 155929, book: "Matt", verse: "22.035", unformatted: "他们当中有一个是律法师，来试探耶稣，问他：", version_id: 6},
  {id: 155930, book: "Matt", verse: "22.036", unformatted: "“老师，律法中最大的诫命是哪一条呢？”", version_id: 6},
  {id: 155931, book: "Matt", verse: "22.037", unformatted: "耶稣对他说：“‘你要以全心、全灵、全意爱主——你的神’，", version_id: 6},
  {id: 155932, book: "Matt", verse: "22.038", unformatted: "这是最大的，也是最重要的诫命。", version_id: 6},
  {id: 155933, book: "Matt", verse: "22.039", unformatted: "其次的也和它类似，‘要爱邻如己。’", version_id: 6},
  {id: 155934, book: "Matt", verse: "22.04", unformatted: "全部律法和先知书都是以这两条诫命为依据的。”", version_id: 6},
  {id: 155935, book: "Matt", verse: "22.041", unformatted: "有关基督的问题\n法利赛人聚集在一起的时候，耶稣问他们", version_id: 6},
  {id: 155936, book: "Matt", verse: "22.042", unformatted: "说：“关于基督，你们怎么看？他是谁的后裔呢？”\n他们说：“是大卫的后裔。”", version_id: 6},
  {id: 155937, book: "Matt", verse: "22.043", unformatted: "耶稣问他们：“那么，大卫藉着圣灵，怎么还称他为‘主’呢？大卫说：", version_id: 6},
  {id: 155938, book: "Matt", verse: "22.044", unformatted: "‘主对我主说：\n你坐在我的右边，\n等我把你的敌人放在你的脚下。’", version_id: 6},
  {id: 155939, book: "Matt", verse: "22.045", unformatted: "“因此，大卫如果称基督为‘主’，基督怎么会是大卫的后裔呢？”", version_id: 6},
  {id: 155940, book: "Matt", verse: "22.046", unformatted: "没有人能回答他一句话。从那天起，再也没有人敢质问耶稣了。", version_id: 6},
  {id: 155941, book: "Matt", verse: "23.001", unformatted: "谴责宗教的伪善\n那时，耶稣对众人和自己的门徒们说：", version_id: 6},
  {id: 155942, book: "Matt", verse: "23.002", unformatted: "“经文士和法利赛人坐在摩西的位子上，", version_id: 6},
  {id: 155943, book: "Matt", verse: "23.003", unformatted: "所以，他们所吩咐你们的，你们都要去做，也要遵守；但是不要效法他们的行为，因为他们说而不做。", version_id: 6},
  {id: 155944, book: "Matt", verse: "23.004", unformatted: "他们把沉重难挑的担子捆起来，放在人们肩上，但自己却连一根指头也不肯动它们一下。", version_id: 6},
  {id: 155945, book: "Matt", verse: "23.005", unformatted: "他们所做的一切事，都是为了给人看：他们把自己的经文盒加宽，把衣服的穗子加长；", version_id: 6},
  {id: 155946, book: "Matt", verse: "23.006", unformatted: "他们喜爱宴席中的上座和会堂里的首位，", version_id: 6},
  {id: 155947, book: "Matt", verse: "23.007", unformatted: "喜爱街市上的致敬问候，又喜爱被人们称为‘老师’。", version_id: 6},
  {id: 155948, book: "Matt", verse: "23.008", unformatted: "“但你们不可被称为‘老师’，因为你们的老师只有一位，而你们都是弟兄。", version_id: 6},
  {id: 155949, book: "Matt", verse: "23.009", unformatted: "不要称地上的任何人为‘父’，因为你们的父只有一位，就是天父。", version_id: 6},
  {id: 155950, book: "Matt", verse: "23.01", unformatted: "你们也不要被人称为‘主人’，因为你们的主人只有一位，就是基督。", version_id: 6},
  {id: 155951, book: "Matt", verse: "23.011", unformatted: "你们当中谁更大，谁就该做你们的仆人，", version_id: 6},
  {id: 155952, book: "Matt", verse: "23.012", unformatted: "因为凡是自我高举的都会被降卑，而自我降卑的都会被高举。", version_id: 6},
  {id: 155953, book: "Matt", verse: "23.013", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们在人的面前封闭了天国。你们自己不进去，也不让正要进的人进去。", version_id: 6},
  {id: 155954, book: "Matt", verse: "23.014", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们侵吞寡妇的家产，又假意做很长的祷告。因此，你们将受到更重的惩罚。", version_id: 6},
  {id: 155955, book: "Matt", verse: "23.015", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们为了使一个人入教，走遍海洋和陆地，一旦那人成了教徒，你们就使他成为比你们加倍坏的地狱之子。", version_id: 6},
  {id: 155956, book: "Matt", verse: "23.016", unformatted: "“你们这些瞎眼的领路人有祸了！你们说：‘如果有人指着圣所起誓，算不得什么；但如果有人指着圣所里的金子起誓，就必须遵守。’", version_id: 6},
  {id: 155957, book: "Matt", verse: "23.017", unformatted: "你们这些又愚拙又瞎眼的人哪，到底哪一样更重要呢？是金子，还是使金子成圣的圣所呢？", version_id: 6},
  {id: 155958, book: "Matt", verse: "23.018", unformatted: "你们还说：‘如果有人指着祭坛起誓，算不得什么；但如果有人指着祭坛上的祭物起誓，就必须遵守。’", version_id: 6},
  {id: 155959, book: "Matt", verse: "23.019", unformatted: "你们这些瞎眼的人哪，到底哪一样更重要呢？是祭物，还是使祭物成圣的祭坛呢？", version_id: 6},
  {id: 155960, book: "Matt", verse: "23.02", unformatted: "所以，指着祭坛起誓的，就是指着祭坛和祭坛上的一切起誓；", version_id: 6},
  {id: 155961, book: "Matt", verse: "23.021", unformatted: "指着圣所起誓的，就是指着圣所和住在里面的那一位起誓；", version_id: 6},
  {id: 155962, book: "Matt", verse: "23.022", unformatted: "指着天起誓的，就是指着神的宝座和坐在宝座上的那一位起誓。", version_id: 6},
  {id: 155963, book: "Matt", verse: "23.023", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们奉献薄荷、莳萝、香芹的十分之一，却放弃了律法上更重要的：就是公义、怜悯、信实。这些才是你们应该做的，至于前者也不可放弃。", version_id: 6},
  {id: 155964, book: "Matt", verse: "23.024", unformatted: "你们这些瞎眼的领路人哪，你们滤掉蠓虫，却吞下骆驼！", version_id: 6},
  {id: 155965, book: "Matt", verse: "23.025", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们洗净杯子、盘子的外面，里面却盛满了贪心和放纵。", version_id: 6},
  {id: 155966, book: "Matt", verse: "23.026", unformatted: "你这瞎眼的法利赛人哪！当先洗净杯子的里面，好使外面也洁净。", version_id: 6},
  {id: 155967, book: "Matt", verse: "23.027", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们好像粉饰的坟墓，外面固然显得美丽，里面却装满了死人骨头和一切污秽。", version_id: 6},
  {id: 155968, book: "Matt", verse: "23.028", unformatted: "你们也是这样，你们外面对人显出公义，里面却充满了伪善和罪恶。", version_id: 6},
  {id: 155969, book: "Matt", verse: "23.029", unformatted: "“经文士和法利赛人哪，你们这些伪善的人有祸了！因为你们建造先知的坟墓，装饰义人的墓碑，", version_id: 6},
  {id: 155970, book: "Matt", verse: "23.03", unformatted: "说：‘如果我们活在我们祖先的时代，我们就不会与他们合伙流先知们的血。’", version_id: 6},
  {id: 155971, book: "Matt", verse: "23.031", unformatted: "这样，你们就见证了自己是杀害先知们的那些人的后代。", version_id: 6},
  {id: 155972, book: "Matt", verse: "23.032", unformatted: "你们去充满你们祖先的恶贯吧！", version_id: 6},
  {id: 155973, book: "Matt", verse: "23.033", unformatted: "“你们这些蛇类，这些毒蛇的子孙！你们怎么能逃避地狱的惩罚呢？", version_id: 6},
  {id: 155974, book: "Matt", verse: "23.034", unformatted: "看，我为此差派先知、智者、经师到你们这里来；有些你们要杀害、要钉上十字架，有些要在会堂里鞭打，从一个城追逼到另一个城。", version_id: 6},
  {id: 155975, book: "Matt", verse: "23.035", unformatted: "所以，从义人亚伯的血起，直到你们在圣所和祭坛之间所杀的巴拉加的儿子撒迦利亚的血为止，在地上所流的义人的血都要归在你们身上。", version_id: 6},
  {id: 155976, book: "Matt", verse: "23.036", unformatted: "我确实地告诉你们：这一切都要临到这世代。", version_id: 6},
  {id: 155977, book: "Matt", verse: "23.037", unformatted: "为耶路撒冷哀叹\n“耶路撒冷啊，耶路撒冷！这城杀害先知们，又用石头砸死被差到她这里的人！我多次想聚集你的儿女，像母鸡把自己的小鸡聚集在翅膀下，可是你们不愿意！", version_id: 6},
  {id: 155978, book: "Matt", verse: "23.038", unformatted: "看哪，你们的家要被废弃成为荒场。", version_id: 6},
  {id: 155979, book: "Matt", verse: "23.039", unformatted: "我告诉你们：今后你们绝不会再见到我了，直到你们说：‘奉主名而来的那一位，是蒙祝福的。’”", version_id: 6},
  {id: 155980, book: "Matt", verse: "24.001", unformatted: "预言圣殿被毁\n耶稣从圣殿出来，往前走的时候，他的门徒们上前来，把圣殿的建筑指给他看。", version_id: 6},
  {id: 155981, book: "Matt", verse: "24.002", unformatted: "耶稣回应说：“你们不是看见了这一切吗？我确实地告诉你们：这里将绝不会有一块石头留在另一块石头上而不被拆下。”", version_id: 6},
  {id: 155982, book: "Matt", verse: "24.003", unformatted: "末日的预兆\n耶稣在橄榄山上坐着，门徒们悄悄地前来问他：“请告诉我们，这些事什么时候会发生呢？你的来临和这世代的终结会有什么预兆呢？”", version_id: 6},
  {id: 155983, book: "Matt", verse: "24.004", unformatted: "耶稣回答他们，说：“你们要当心，免得有人迷惑你们。", version_id: 6},
  {id: 155984, book: "Matt", verse: "24.005", unformatted: "因为许多人会以我的名而来，声称‘我是基督’，并且会迷惑许多人。", version_id: 6},
  {id: 155985, book: "Matt", verse: "24.006", unformatted: "你们也将会听见战争和战争的消息。要注意，不可惊慌，因为这些事必须发生，不过结局还没有到。", version_id: 6},
  {id: 155986, book: "Matt", verse: "24.007", unformatted: "一个民族要起来反对另一个民族，一个国家要起来反对另一个国家；到处都会有饥荒和地震。", version_id: 6},
  {id: 155987, book: "Matt", verse: "24.008", unformatted: "这些都像临产阵痛的开始。", version_id: 6},
  {id: 155988, book: "Matt", verse: "24.009", unformatted: "预言受迫害\n“那时，他们将要出卖你们，使你们受患难，甚至杀害你们。为了我的名，你们将被万民憎恨。", version_id: 6},
  {id: 155989, book: "Matt", verse: "24.01", unformatted: "那时，许多人将被绊倒，彼此出卖，彼此憎恨；", version_id: 6},
  {id: 155990, book: "Matt", verse: "24.011", unformatted: "并且会有许多假先知起来，迷惑许多人；", version_id: 6},
  {id: 155991, book: "Matt", verse: "24.012", unformatted: "由于罪恶增多，许多人的爱心就会冷淡。", version_id: 6},
  {id: 155992, book: "Matt", verse: "24.013", unformatted: "但是忍耐到底的，这个人将会得救。", version_id: 6},
  {id: 155993, book: "Matt", verse: "24.014", unformatted: "这天国的福音将被传遍天下，好对万国做见证，然后结局才会到来。", version_id: 6},
  {id: 155994, book: "Matt", verse: "24.015", unformatted: "大患难的日子\n“当你们看见藉着先知但以理所说的‘那带来毁灭的亵渎者’站在圣地的时候——读者应当思考——", version_id: 6},
  {id: 155995, book: "Matt", verse: "24.016", unformatted: "那时，在犹太的人应当逃到山里；", version_id: 6},
  {id: 155996, book: "Matt", verse: "24.017", unformatted: "在屋顶上的人，不要下来拿屋子里的东西；", version_id: 6},
  {id: 155997, book: "Matt", verse: "24.018", unformatted: "在田里的人，不要返回去拿自己的衣服。", version_id: 6},
  {id: 155998, book: "Matt", verse: "24.019", unformatted: "在那些日子里，孕妇和哺乳的女人有祸了！", version_id: 6},
  {id: 155999, book: "Matt", verse: "24.02", unformatted: "你们要祷告，好让你们逃难的时候不在冬天，也不在安息日。", version_id: 6},
  {id: 156000, book: "Matt", verse: "24.021", unformatted: "因为那时将有大患难。这样的患难从世界开始直到如今，从来没有发生过，也绝不再发生。", version_id: 6},
  {id: 156001, book: "Matt", verse: "24.022", unformatted: "如果那些日子不被减少，就没有一个人能得救。可是为了那些蒙拣选的人，那些日子将被减少。", version_id: 6},
  {id: 156002, book: "Matt", verse: "24.023", unformatted: "“那时候，如果有人对你们说‘看哪，基督在这里！’或‘他在那里！’你们不要相信。", version_id: 6},
  {id: 156003, book: "Matt", verse: "24.024", unformatted: "因为假基督们和假先知们会出现，行大神迹和奇事，如果有可能，甚至迷惑那些蒙拣选的人。", version_id: 6},
  {id: 156004, book: "Matt", verse: "24.025", unformatted: "看哪，我已经预先告诉你们了！", version_id: 6},
  {id: 156005, book: "Matt", verse: "24.026", unformatted: "所以，如果有人对你们说‘看哪，基督在旷野里！’你们不要出去；有人说：‘看哪，他在内室里！’你们也不要相信；", version_id: 6},
  {id: 156006, book: "Matt", verse: "24.027", unformatted: "因为就像闪电从东边发出，直照到西边，人子的来临也将要这样。", version_id: 6},
  {id: 156007, book: "Matt", verse: "24.028", unformatted: "尸体在哪里，秃鹰就聚集在哪里。", version_id: 6},
  {id: 156008, book: "Matt", verse: "24.029", unformatted: "人子的来临\n“那些日子的患难一过去，\n‘太阳就要变黑，\n月亮也不发光，\n星辰要从天上坠落，\n诸天的各势力也将被震动。’", version_id: 6},
  {id: 156009, book: "Matt", verse: "24.03", unformatted: "“那时候，人子的征兆将要显现在天上，地上的万族都要捶胸哀哭。他们将要看见人子带着极大的权能和荣耀，在天上的云彩中来临。", version_id: 6},
  {id: 156010, book: "Matt", verse: "24.031", unformatted: "在嘹亮的号角声中，他要差派他的天使们从四方，从天这边到天那边，召集他所拣选的人。", version_id: 6},
  {id: 156011, book: "Matt", verse: "24.032", unformatted: "无花果树的比喻\n“你们要从无花果树那里学个比喻：当无花果树的枝子发嫩长出叶子的时候，你们就知道夏天快到了。", version_id: 6},
  {id: 156012, book: "Matt", verse: "24.033", unformatted: "照样，当你们看见这一切的时候，你们就知道人子快到了，就在门口了。", version_id: 6},
  {id: 156013, book: "Matt", verse: "24.034", unformatted: "我确实地告诉你们：在这一切事发生之前，这世代绝不会过去。", version_id: 6},
  {id: 156014, book: "Matt", verse: "24.035", unformatted: "天和地将要消逝，但是我的话绝不会消逝。", version_id: 6},
  {id: 156015, book: "Matt", verse: "24.036", unformatted: "无人知道那日子和那时刻\n“至于那日子和那时刻，没有人知道，连诸天之上的天使们也不知道，子也不知道，唯有父知道。", version_id: 6},
  {id: 156016, book: "Matt", verse: "24.037", unformatted: "挪亚的那些日子怎样，人子的来临也将要怎样。", version_id: 6},
  {id: 156017, book: "Matt", verse: "24.038", unformatted: "在洪水以前的那些日子里，人们继续吃、喝、嫁、娶，直到挪亚进方舟的那一天。", version_id: 6},
  {id: 156018, book: "Matt", verse: "24.039", unformatted: "他们毫不察觉，直到洪水到来，把一切冲走。人子的来临，也将是这样。", version_id: 6},
  {id: 156019, book: "Matt", verse: "24.04", unformatted: "那时，两个人在田里，一个被接去，一个被留下。", version_id: 6},
  {id: 156020, book: "Matt", verse: "24.041", unformatted: "两个女人在磨石那里推磨，一个被接去，一个被留下。", version_id: 6},
  {id: 156021, book: "Matt", verse: "24.042", unformatted: "所以你们要警醒，因为你们不知道你们的主哪一天回来。", version_id: 6},
  {id: 156022, book: "Matt", verse: "24.043", unformatted: "不过你们应当知道这一点：一家的主人如果知道贼什么时刻来，他就会警醒，不让他的房子被人钻进。", version_id: 6},
  {id: 156023, book: "Matt", verse: "24.044", unformatted: "你们为了这缘故也应当做好准备，因为在你们意想不到的时候，人子就来了。", version_id: 6},
  {id: 156024, book: "Matt", verse: "24.045", unformatted: "忠仆与恶仆\n“究竟谁是那又忠心又聪明的奴仆——受主人委任统管他家里的人、按时给他们分粮的人呢？", version_id: 6},
  {id: 156025, book: "Matt", verse: "24.046", unformatted: "主人回来的时候，看见哪个奴仆这样做，那个奴仆就蒙福了。", version_id: 6},
  {id: 156026, book: "Matt", verse: "24.047", unformatted: "我确实地告诉你们：主人会委任他统管自己所拥有的一切。", version_id: 6},
  {id: 156027, book: "Matt", verse: "24.048", unformatted: "但如果这恶奴心里说‘我的主人会迟延回来’，", version_id: 6},
  {id: 156028, book: "Matt", verse: "24.049", unformatted: "就动手殴打与他同做奴仆的，并且与醉酒的人一起吃喝，", version_id: 6},
  {id: 156029, book: "Matt", verse: "24.05", unformatted: "那么，这奴仆的主人就要在意想不到的日子、在他不知道的时刻回来，", version_id: 6},
  {id: 156030, book: "Matt", verse: "24.051", unformatted: "并且会严厉惩罚他，使他与那些伪善的人有同样的下场。在那里将有哀哭和切齿。", version_id: 6},
  {id: 156031, book: "Matt", verse: "25.001", unformatted: "十个童女的比喻\n“那时，天国好比十个童女，拿着她们的灯出去迎接新郎。", version_id: 6},
  {id: 156032, book: "Matt", verse: "25.002", unformatted: "她们当中有五个是愚拙的，五个是聪明的。", version_id: 6},
  {id: 156033, book: "Matt", verse: "25.003", unformatted: "那些愚拙的童女带着她们的灯，却没有带油；", version_id: 6},
  {id: 156034, book: "Matt", verse: "25.004", unformatted: "而那些聪明的带着瓶子里的油，以及自己的灯。", version_id: 6},
  {id: 156035, book: "Matt", verse: "25.005", unformatted: "后来新郎迟延不到，她们都打盹，睡着了。", version_id: 6},
  {id: 156036, book: "Matt", verse: "25.006", unformatted: "“半夜里，有人喧嚷：‘看哪，是新郎！你们出来迎接他！’", version_id: 6},
  {id: 156037, book: "Matt", verse: "25.007", unformatted: "“十个童女都醒过来，修整她们的灯。", version_id: 6},
  {id: 156038, book: "Matt", verse: "25.008", unformatted: "愚拙的对聪明的说：‘请把你们的油分一点给我们，因为我们的灯要灭了。’", version_id: 6},
  {id: 156039, book: "Matt", verse: "25.009", unformatted: "“但那些聪明的回答说：‘不行，绝不够我们和你们用的。你们还是到卖油的那里去，为自己买吧。’", version_id: 6},
  {id: 156040, book: "Matt", verse: "25.01", unformatted: "“可是她们去买的时候，新郎就来了。那些预备好了的，与新郎一同进去赴婚宴，门就关上了。", version_id: 6},
  {id: 156041, book: "Matt", verse: "25.011", unformatted: "“后来其余的童女也来了，说：‘主啊，主啊，请给我们开门！’", version_id: 6},
  {id: 156042, book: "Matt", verse: "25.012", unformatted: "“但是新郎回答说：‘我确实地告诉你们：我不认识你们。’", version_id: 6},
  {id: 156043, book: "Matt", verse: "25.013", unformatted: "“所以你们要警醒，因为你们不知道那日子和那时刻。", version_id: 6},
  {id: 156044, book: "Matt", verse: "25.014", unformatted: "受托银子的比喻\n“天国又好比一个人要出外旅行，就叫来自己的奴仆们，把他所拥有的交托给他们。", version_id: 6},
  {id: 156045, book: "Matt", verse: "25.015", unformatted: "他按照每个人自己的能力，一个给了五千两银子，一个给了两千两，一个给了一千两，然后就出外旅行。", version_id: 6},
  {id: 156046, book: "Matt", verse: "25.016", unformatted: "那领了五千的，立刻用这些钱去做生意，另外赚了五千。", version_id: 6},
  {id: 156047, book: "Matt", verse: "25.017", unformatted: "那领了两千的，也照样另赚了两千。", version_id: 6},
  {id: 156048, book: "Matt", verse: "25.018", unformatted: "可是那领了一千的，却出去挖地，把他主人的银子藏起来。", version_id: 6},
  {id: 156049, book: "Matt", verse: "25.019", unformatted: "“过了很久，那些奴仆的主人回来，与他们清算账目。", version_id: 6},
  {id: 156050, book: "Matt", verse: "25.02", unformatted: "那领了五千两银子的，带着另外的五千两上前来，说：‘主啊，你交托给我五千两银子，请看，我另外赚了五千两。’", version_id: 6},
  {id: 156051, book: "Matt", verse: "25.021", unformatted: "“主人对他说：‘做得好，忠心的好奴仆！你在少许的事上忠心，我要委任你统管很多的事。进来分享你主人的快乐吧！’", version_id: 6},
  {id: 156052, book: "Matt", verse: "25.022", unformatted: "“那领了两千两银子的也上前来，说：‘主啊，你交托给我两千两银子，请看，我另外赚了两千两。’", version_id: 6},
  {id: 156053, book: "Matt", verse: "25.023", unformatted: "“主人对他说：‘做得好，忠心的好奴仆！你在少许的事上忠心，我要委任你统管很多的事。进来分享你主人的快乐吧！’", version_id: 6},
  {id: 156054, book: "Matt", verse: "25.024", unformatted: "“接着，那领了一千两银子的也上前来，说：‘主啊，我知道你是个严厉的人，不是你播种的地方，你收获；不是你投放的地方，你收集。", version_id: 6},
  {id: 156055, book: "Matt", verse: "25.025", unformatted: "我惧怕，就去把你的银子藏在地里。请看，你的银子在这里。’", version_id: 6},
  {id: 156056, book: "Matt", verse: "25.026", unformatted: "“主人回答他，说：‘你这又恶又懒的奴仆！你既然知道：不是我播种的地方，我收获；不是我投放的地方，我收集，", version_id: 6},
  {id: 156057, book: "Matt", verse: "25.027", unformatted: "你就应该把我的银子存到钱庄里，这样我回来的时候，可以连本带利得回来。", version_id: 6},
  {id: 156058, book: "Matt", verse: "25.028", unformatted: "“所以，你们把那一千两银子从他那里拿走，给那个有一万两的；", version_id: 6},
  {id: 156059, book: "Matt", verse: "25.029", unformatted: "因为凡是有的，还要赐给他，使他丰足有余；那没有的，连他有的也将从他那里被拿走。", version_id: 6},
  {id: 156060, book: "Matt", verse: "25.03", unformatted: "把这个无用的奴仆丢到外面的黑暗里去！在那里将有哀哭和切齿。’", version_id: 6},
  {id: 156061, book: "Matt", verse: "25.031", unformatted: "绵羊与山羊\n“当人子在他的荣耀中，与所有的天使一起来临的时候，他那时就要坐在他荣耀的宝座上。", version_id: 6},
  {id: 156062, book: "Matt", verse: "25.032", unformatted: "万民都将被召集到他面前。他要把他们互相分别，就像牧人把绵羊从山羊中分别出来那样，", version_id: 6},
  {id: 156063, book: "Matt", verse: "25.033", unformatted: "把绵羊放在他的右边，山羊放在左边。", version_id: 6},
  {id: 156064, book: "Matt", verse: "25.034", unformatted: "那时王要对在他右边的人说：‘来吧，蒙我父所祝福的人哪，来继承创世以来已经为你们所预备好的国度吧！", version_id: 6},
  {id: 156065, book: "Matt", verse: "25.035", unformatted: "因为我饿了，\n你们给我吃；\n我渴了，\n你们给我喝；\n我在异乡时，\n你们收留了我；", version_id: 6},
  {id: 156066, book: "Matt", verse: "25.036", unformatted: "我衣不蔽体，你们给我穿；\n我患了病，你们照顾我；\n我在监狱里，你们探望我。’", version_id: 6},
  {id: 156067, book: "Matt", verse: "25.037", unformatted: "“那时义人将回答说：‘主啊，我们什么时候见你饿了就给你吃，见你渴了就给你喝呢？", version_id: 6},
  {id: 156068, book: "Matt", verse: "25.038", unformatted: "我们什么时候见你在异乡就收留了你，见你衣不蔽体就给你穿呢？", version_id: 6},
  {id: 156069, book: "Matt", verse: "25.039", unformatted: "又什么时候见你患病或在监狱里，就到你那里去呢？’", version_id: 6},
  {id: 156070, book: "Matt", verse: "25.04", unformatted: "“王要回答他们，说：‘我确实地告诉你们：你们为我这些弟兄中最小的一个所做的事，就是为我做了。’", version_id: 6},
  {id: 156071, book: "Matt", verse: "25.041", unformatted: "那时王要对在左边的人说：‘你们这些被诅咒的人！离开我！进到那已经为魔鬼和他的使者们所预备好的永远的火里去！", version_id: 6},
  {id: 156072, book: "Matt", verse: "25.042", unformatted: "因为我饿了，\n你们没有给我吃；\n我渴了，\n你们没有给我喝；", version_id: 6},
  {id: 156073, book: "Matt", verse: "25.043", unformatted: "我在异乡时，\n你们没有收留我；\n我衣不蔽体，\n你们没有给我穿；\n我患了病、在监狱里，\n你们没有照顾我。’", version_id: 6},
  {id: 156074, book: "Matt", verse: "25.044", unformatted: "“那时他们也要回答说：‘主啊，我们什么时候见你饿了，或渴了，或在异乡，或衣不蔽体，或患了病，或在监狱里，而没有服事你呢？’", version_id: 6},
  {id: 156075, book: "Matt", verse: "25.045", unformatted: "“那时王要回答他们，说：‘我确实地告诉你们：你们没有为我这些弟兄中最小的一个所做的事，也就是没有为我做。’", version_id: 6},
  {id: 156076, book: "Matt", verse: "25.046", unformatted: "“结果这些人将离去，进入永远的刑罚。然而，义人将进入永恒的生命。”", version_id: 6},
  {id: 156077, book: "Matt", verse: "26.001", unformatted: "阴谋杀害耶稣\n当耶稣讲完了这一切话，就对自己的门徒们说：", version_id: 6},
  {id: 156078, book: "Matt", verse: "26.002", unformatted: "“你们知道，过两天就是逾越节，人子要被交出去钉上十字架。”", version_id: 6},
  {id: 156079, book: "Matt", verse: "26.003", unformatted: "那时，祭司长们和民间的长老们聚集在叫该亚法的大祭司的宅院里，", version_id: 6},
  {id: 156080, book: "Matt", verse: "26.004", unformatted: "商议要用诡计拘捕耶稣，把他杀掉。", version_id: 6},
  {id: 156081, book: "Matt", verse: "26.005", unformatted: "不过他们说：“不可在节日期间，免得民中发生骚乱。”", version_id: 6},
  {id: 156082, book: "Matt", verse: "26.006", unformatted: "在伯大尼被膏抹\n耶稣在伯大尼，在麻风病人西门家里的时候，", version_id: 6},
  {id: 156083, book: "Matt", verse: "26.007", unformatted: "有一个女人拿着一个盛了极其贵重香液的玉瓶上前来，在耶稣坐席的时候，把香液浇在他的头上。", version_id: 6},
  {id: 156084, book: "Matt", verse: "26.008", unformatted: "门徒们看见了就很不满，说：“为什么这样浪费？", version_id: 6},
  {id: 156085, book: "Matt", verse: "26.009", unformatted: "这香液本来可以卖很多钱，分给穷人。”", version_id: 6},
  {id: 156086, book: "Matt", verse: "26.01", unformatted: "耶稣知道了，就对他们说：“你们为什么为难这妇人呢？她为我做了一件美好的事。", version_id: 6},
  {id: 156087, book: "Matt", verse: "26.011", unformatted: "要知道，你们总是有穷人与你们在一起，但你们不总是有我。", version_id: 6},
  {id: 156088, book: "Matt", verse: "26.012", unformatted: "她把这香液浇在我身上，是为了预备安葬我而做的。", version_id: 6},
  {id: 156089, book: "Matt", verse: "26.013", unformatted: "我确实地告诉你们：在全世界，福音无论传到什么地方，这女人所做的事也将被述说，做为对她的记念。”", version_id: 6},
  {id: 156090, book: "Matt", verse: "26.014", unformatted: "那时候，十二使徒中的一个，就是那称为加略人犹大的，到祭司长们那里去，", version_id: 6},
  {id: 156091, book: "Matt", verse: "26.015", unformatted: "说：“如果我把他交给你们，你们愿意给我什么？”他们就约定给他三十个银钱。", version_id: 6},
  {id: 156092, book: "Matt", verse: "26.016", unformatted: "从那时候起，犹大就开始找机会要把耶稣交出去。", version_id: 6},
  {id: 156093, book: "Matt", verse: "26.017", unformatted: "在逾越节被出卖\n除酵节的第一日，门徒们前来问耶稣：“你要我们到哪里去预备，好让你吃逾越节的晚餐呢？”", version_id: 6},
  {id: 156094, book: "Matt", verse: "26.018", unformatted: "耶稣说：“你们进城去，到某人那里，告诉他：‘老师说，我的时候快到了。我要在你那里与我的门徒们一起守逾越节。’”", version_id: 6},
  {id: 156095, book: "Matt", verse: "26.019", unformatted: "门徒们就依照耶稣所吩咐的去做，预备了逾越节的晚餐。", version_id: 6},
  {id: 156096, book: "Matt", verse: "26.02", unformatted: "到了晚上，耶稣与十二使徒一同坐席。", version_id: 6},
  {id: 156097, book: "Matt", verse: "26.021", unformatted: "在他们吃的时候，耶稣说：“我确实地告诉你们：你们当中有一个人要出卖我。”", version_id: 6},
  {id: 156098, book: "Matt", verse: "26.022", unformatted: "他们极其忧伤，就一个一个地开始问耶稣：“主啊，不会是我吧？”", version_id: 6},
  {id: 156099, book: "Matt", verse: "26.023", unformatted: "耶稣回答说：“那与我一同把手蘸在盘子里的，就是要出卖我的人。", version_id: 6},
  {id: 156100, book: "Matt", verse: "26.024", unformatted: "人子确实要离去，正如经上指着他所写的，但是出卖人子的那个人有祸了。对他来说，没有出生倒好。”", version_id: 6},
  {id: 156101, book: "Matt", verse: "26.025", unformatted: "那出卖耶稣的犹大应声说：“拉比，不会是我吧？”\n耶稣对他说：“你已经说了。”", version_id: 6},
  {id: 156102, book: "Matt", verse: "26.026", unformatted: "第一次圣餐\n在他们吃的时候，耶稣拿起饼来，祝福了，就掰开，递给门徒们，说：“你们领受吃吧，这是我的身体。”", version_id: 6},
  {id: 156103, book: "Matt", verse: "26.027", unformatted: "接着，他拿起杯来，祝谢了，递给他们，说：“你们都来喝，", version_id: 6},
  {id: 156104, book: "Matt", verse: "26.028", unformatted: "因为这是我的血，是为立约的，为许多人所流的，使罪得赦免。", version_id: 6},
  {id: 156105, book: "Matt", verse: "26.029", unformatted: "我告诉你们：从今以后我绝不喝这葡萄汁，直到我在我父的国里与你们一起喝新的那一天。”", version_id: 6},
  {id: 156106, book: "Matt", verse: "26.03", unformatted: "他们唱了赞美诗，就出来，往橄榄山去了。", version_id: 6},
  {id: 156107, book: "Matt", verse: "26.031", unformatted: "预言彼得不认主\n那时，耶稣对他们说：“今天晚上，你们都将因我被绊倒，因为经上记着：\n‘我将击打牧人，\n群羊就被分散。’", version_id: 6},
  {id: 156108, book: "Matt", verse: "26.032", unformatted: "但是我复活以后，要在你们之前到加利利去。”", version_id: 6},
  {id: 156109, book: "Matt", verse: "26.033", unformatted: "彼得对耶稣说：“即使所有的人因你被绊倒，我也绝不会被绊倒。”", version_id: 6},
  {id: 156110, book: "Matt", verse: "26.034", unformatted: "耶稣对他说：“我确实地告诉你：今天晚上鸡叫以前，你会三次不认我。”", version_id: 6},
  {id: 156111, book: "Matt", verse: "26.035", unformatted: "彼得对耶稣说：“即使我必须与你同死，我也绝不会不认你。”所有的门徒也都如此说。", version_id: 6},
  {id: 156112, book: "Matt", verse: "26.036", unformatted: "园中的祷告\n随后，耶稣与他们来到一个叫客西马尼的地方。耶稣对门徒们说：“我走开在那里祷告的时候，你们坐在这里。”", version_id: 6},
  {id: 156113, book: "Matt", verse: "26.037", unformatted: "他带了彼得和西庇太的两个儿子一起去，开始感到忧虑，极其难过，", version_id: 6},
  {id: 156114, book: "Matt", verse: "26.038", unformatted: "就对他们说：“我的灵魂很忧伤，几乎要死。你们留在这里，与我一同警醒。”", version_id: 6},
  {id: 156115, book: "Matt", verse: "26.039", unformatted: "耶稣稍往前走，把脸伏在地上祷告说：“我父啊，如果有可能，让这杯离开我吧！但不要照我的意愿，而要照你的意愿。”", version_id: 6},
  {id: 156116, book: "Matt", verse: "26.04", unformatted: "耶稣回到门徒们那里，看见他们睡着了，就对彼得说：“你们怎么就不能与我一同警醒一个小时呢？", version_id: 6},
  {id: 156117, book: "Matt", verse: "26.041", unformatted: "要警醒祷告，免得陷入试探。你们心灵虽然愿意，肉体却是软弱的。”", version_id: 6},
  {id: 156118, book: "Matt", verse: "26.042", unformatted: "耶稣第二次又去祷告说：“我父啊，如果这杯不能离开我，一定要我喝下，那就愿你的旨意成就！”", version_id: 6},
  {id: 156119, book: "Matt", verse: "26.043", unformatted: "他又回来，看见他们在睡觉，因为他们的眼皮发沉。", version_id: 6},
  {id: 156120, book: "Matt", verse: "26.044", unformatted: "耶稣又离开他们，第三次去祷告，又说了同样的话。", version_id: 6},
  {id: 156121, book: "Matt", verse: "26.045", unformatted: "然后，他回到门徒们那里，对他们说：“你们还在睡觉、休息吗？看哪，时候快到了！人子被交在罪人的手里了。", version_id: 6},
  {id: 156122, book: "Matt", verse: "26.046", unformatted: "起来，我们走吧！看，那出卖我的人快到了！”", version_id: 6},
  {id: 156123, book: "Matt", verse: "26.047", unformatted: "犹大亲吻的暗号\n耶稣还在说话的时候，忽然十二使徒之一的犹大来了。有一大群人拿着刀剑和棍棒与他一起来，他们是祭司长们和民间的长老们所派来的。", version_id: 6},
  {id: 156124, book: "Matt", verse: "26.048", unformatted: "那出卖耶稣的人已经和他们定了一个暗号，说：“我如果亲吻谁，谁就是那个人。你们把他抓住。”", version_id: 6},
  {id: 156125, book: "Matt", verse: "26.049", unformatted: "犹大立刻上前来对耶稣说：“拉比，愿你欢喜！”然后亲吻了他。", version_id: 6},
  {id: 156126, book: "Matt", verse: "26.05", unformatted: "耶稣对他说：“朋友，你为什么来到这里？”\n于是，那些人上前来，下手拘捕了耶稣。", version_id: 6},
  {id: 156127, book: "Matt", verse: "26.051", unformatted: "这时候，忽然有一个与耶稣在一起的人伸手拔出刀来，向大祭司的奴仆砍去，削掉了他的一个耳朵。", version_id: 6},
  {id: 156128, book: "Matt", verse: "26.052", unformatted: "耶稣就对他说：“把你的刀收回原处！因为凡动刀的，必死在刀下。", version_id: 6},
  {id: 156129, book: "Matt", verse: "26.053", unformatted: "难道你想我不能求我父，现在就为我派来十二个军团以上的天使吗？", version_id: 6},
  {id: 156130, book: "Matt", verse: "26.054", unformatted: "如果这样，‘事情必须如此发生’的经文怎么应验呢？”", version_id: 6},
  {id: 156131, book: "Matt", verse: "26.055", unformatted: "这时候，耶稣对那群人说：“你们带着刀剑和棍棒出来抓我，就像对付强盗那样吗？我天天坐在圣殿里教导人，你们并没有抓我。", version_id: 6},
  {id: 156132, book: "Matt", verse: "26.056", unformatted: "不过这整个事的发生，是为要应验先知书的那些经文。”这时候，所有的门徒都离开他，逃跑了。", version_id: 6},
  {id: 156133, book: "Matt", verse: "26.057", unformatted: "在议员面前受审\n拘捕耶稣的人把他带到大祭司该亚法那里。经文士们和长老们已经聚集在那里了。", version_id: 6},
  {id: 156134, book: "Matt", verse: "26.058", unformatted: "彼得远远地跟着耶稣，一直到大祭司的院子。他进到里面，与差役们坐在一起，想看看结果如何。", version_id: 6},
  {id: 156135, book: "Matt", verse: "26.059", unformatted: "当时，祭司长们和全议会的人为了要处死耶稣，都在寻找伪证来控告他。", version_id: 6},
  {id: 156136, book: "Matt", verse: "26.06", unformatted: "虽然有许多假见证人上前来，可是他们找不出什么。后来有两个人上前来，", version_id: 6},
  {id: 156137, book: "Matt", verse: "26.061", unformatted: "说：“这个人说过：‘我能拆毁神的圣所，三天内又建起来。’”", version_id: 6},
  {id: 156138, book: "Matt", verse: "26.062", unformatted: "于是大祭司就站起来，问耶稣：“对这些人控告你的事，你什么都不回应吗？”", version_id: 6},
  {id: 156139, book: "Matt", verse: "26.063", unformatted: "耶稣却沉默不语。大祭司又对他说：“我命令你指着永生的神起誓，告诉我们你是不是基督——神的儿子？”", version_id: 6},
  {id: 156140, book: "Matt", verse: "26.064", unformatted: "耶稣回答他，说：“你已经说了。不过我告诉你们：将来你们会看见人子坐在全能者的右边，在天上的云彩中来临。”", version_id: 6},
  {id: 156141, book: "Matt", verse: "26.065", unformatted: "大祭司就撕裂自己的衣服，说：“他说了亵渎的话！难道我们还需要什么见证人吗？看，现在你们都听见了这亵渎的话。", version_id: 6},
  {id: 156142, book: "Matt", verse: "26.066", unformatted: "你们怎么看？”\n他们回答说：“他是该死的！”", version_id: 6},
  {id: 156143, book: "Matt", verse: "26.067", unformatted: "当时他们就往他脸上吐唾沫，用拳头打他，也有人用手掌打他，", version_id: 6},
  {id: 156144, book: "Matt", verse: "26.068", unformatted: "说：“给我们说预言吧！基督！打你的是谁？”", version_id: 6},
  {id: 156145, book: "Matt", verse: "26.069", unformatted: "彼得不认主\n彼得坐在外面的院子里，有一个女仆过来对他说：“你也与那个加利利人耶稣是一伙的。”", version_id: 6},
  {id: 156146, book: "Matt", verse: "26.07", unformatted: "彼得却在众人面前否认说：“我不知道你在说什么。”", version_id: 6},
  {id: 156147, book: "Matt", verse: "26.071", unformatted: "他出去到门口，另有一个女仆看见他，对那里的人说：“这个人与拿撒勒人耶稣是一伙的。”", version_id: 6},
  {id: 156148, book: "Matt", verse: "26.072", unformatted: "彼得又起誓否认说：“我不认识那个人！”", version_id: 6},
  {id: 156149, book: "Matt", verse: "26.073", unformatted: "过了一会儿，旁边站着的人过来对彼得说：“你真的也是与他们一伙的，因为连你的口音也把你暴露了。”", version_id: 6},
  {id: 156150, book: "Matt", verse: "26.074", unformatted: "彼得就开始赌咒并起誓说：“我不认识那个人！”立时，鸡就叫了。", version_id: 6},
  {id: 156151, book: "Matt", verse: "26.075", unformatted: "彼得想起耶稣说过的话：“鸡叫以前，你会三次不认我”，就到外面去，痛哭起来。", version_id: 6},
  {id: 156152, book: "Matt", verse: "27.001", unformatted: "提交彼拉多\n到了清晨，所有的祭司长和民间的长老商议对付耶稣，为要处死他。", version_id: 6},
  {id: 156153, book: "Matt", verse: "27.002", unformatted: "他们把耶稣捆起来带走，交给总督彼拉多。", version_id: 6},
  {id: 156154, book: "Matt", verse: "27.003", unformatted: "犹大自缢\n这时候，那出卖耶稣的犹大看见耶稣被定了死罪，就后悔，去把三十个银钱退还给祭司长们和长老们，", version_id: 6},
  {id: 156155, book: "Matt", verse: "27.004", unformatted: "说：“我出卖了无辜人的血，我有罪了。”\n但是他们说：“这与我们有什么关系？你自己看着办吧！”", version_id: 6},
  {id: 156156, book: "Matt", verse: "27.005", unformatted: "犹大把银钱丢在圣所里就退了出来，去上吊自杀了。", version_id: 6},
  {id: 156157, book: "Matt", verse: "27.006", unformatted: "祭司长们捡起银钱，说：“这既然是血钱，不可以放到圣殿奉献里。”", version_id: 6},
  {id: 156158, book: "Matt", verse: "27.007", unformatted: "他们商议之后，就用那钱买了陶匠的一块地，做为外国人的坟地。", version_id: 6},
  {id: 156159, book: "Matt", verse: "27.008", unformatted: "因此，那块地就被称为“血田”，直到今天。", version_id: 6},
  {id: 156160, book: "Matt", verse: "27.009", unformatted: "这就应验了藉着先知耶利米所说的话：\n“他们拿了那三十个银钱，就是那人被定的价钱，是以色列子民所定的；", version_id: 6},
  {id: 156161, book: "Matt", verse: "27.01", unformatted: "他们用这钱买了陶匠的那块地，正如主所指示我的。”", version_id: 6},
  {id: 156162, book: "Matt", verse: "27.011", unformatted: "在彼拉多面前受审\n耶稣站在总督面前，总督问他，说：“你是犹太人的王吗？”\n耶稣说：“是你说的。”", version_id: 6},
  {id: 156163, book: "Matt", verse: "27.012", unformatted: "可是当他被祭司长们和长老们控告的时候，什么都不回应。", version_id: 6},
  {id: 156164, book: "Matt", verse: "27.013", unformatted: "彼拉多对他说：“他们控告你这么多的事，你没有听见吗？”", version_id: 6},
  {id: 156165, book: "Matt", verse: "27.014", unformatted: "耶稣连一句话也不回答，以致总督感到非常惊奇。", version_id: 6},
  {id: 156166, book: "Matt", verse: "27.015", unformatted: "耶稣或巴拉巴\n每逢这节日，总督会照惯例，给民众释放一个他们所要的囚犯。", version_id: 6},
  {id: 156167, book: "Matt", verse: "27.016", unformatted: "那时有一个出名的囚犯，叫巴拉巴。", version_id: 6},
  {id: 156168, book: "Matt", verse: "27.017", unformatted: "当民众聚集的时候，彼拉多问他们：“你们要我给你们释放哪一个呢？是巴拉巴，还是那称为基督的耶稣？”", version_id: 6},
  {id: 156169, book: "Matt", verse: "27.018", unformatted: "其实彼拉多知道他们是出于嫉妒才把耶稣交给他的。", version_id: 6},
  {id: 156170, book: "Matt", verse: "27.019", unformatted: "彼拉多正坐在审判席上，他的妻子派人到他那里说：“这义人的事，你什么也不要管。要知道，我今天因他的缘故，在梦中受了很多苦。”", version_id: 6},
  {id: 156171, book: "Matt", verse: "27.02", unformatted: "祭司长们和长老们挑唆众人，叫他们要巴拉巴，而除灭耶稣。", version_id: 6},
  {id: 156172, book: "Matt", verse: "27.021", unformatted: "总督问他们，说：“这两个人中，你们要我给你们释放哪一个呢？”\n他们回答说：“巴拉巴！”", version_id: 6},
  {id: 156173, book: "Matt", verse: "27.022", unformatted: "彼拉多对他们说：“那么，我该怎么处理这称为基督的耶稣呢？”\n他们都说：“把他钉上十字架！”", version_id: 6},
  {id: 156174, book: "Matt", verse: "27.023", unformatted: "彼拉多说：“他到底做了什么恶事呢？”\n但他们却越发喊叫说：“把他钉上十字架！”", version_id: 6},
  {id: 156175, book: "Matt", verse: "27.024", unformatted: "彼拉多知道自己无济于事，反而骚乱倒要发生，就拿水在众人面前洗手，说：“流这人的血，罪不在我，你们自己看着办吧！”", version_id: 6},
  {id: 156176, book: "Matt", verse: "27.025", unformatted: "全体民众都回答说：“他的血归在我们和我们儿女的身上！”", version_id: 6},
  {id: 156177, book: "Matt", verse: "27.026", unformatted: "于是彼拉多给他们释放了巴拉巴，然后将耶稣鞭打了，把他交出去钉上十字架。", version_id: 6},
  {id: 156178, book: "Matt", verse: "27.027", unformatted: "被士兵戏弄\n那时，总督的士兵们把耶稣带进总督府，召集了全队士兵在他周围。", version_id: 6},
  {id: 156179, book: "Matt", verse: "27.028", unformatted: "他们剥下耶稣的衣服，给他套上一件深红色的袍子，", version_id: 6},
  {id: 156180, book: "Matt", verse: "27.029", unformatted: "用荆棘编了冠冕，戴在他的头上，又把一根芦苇放在他的右手里，然后跪在他面前，戏弄他说：“万岁，犹太人的王！”", version_id: 6},
  {id: 156181, book: "Matt", verse: "27.03", unformatted: "接着向他吐唾沫，拿芦苇打他的头。", version_id: 6},
  {id: 156182, book: "Matt", verse: "27.031", unformatted: "他们戏弄完了，就脱下他深红色的袍子，给他穿上他自己的衣服，然后把他带走，要钉上十字架。", version_id: 6},
  {id: 156183, book: "Matt", verse: "27.032", unformatted: "被钉在罪犯之间\n他们出去的时候，遇见一个名叫西门的古利奈人，就强迫这个人来背耶稣的十字架。", version_id: 6},
  {id: 156184, book: "Matt", verse: "27.033", unformatted: "他们来到一个地方，叫“各各他”——这意思是“骷髅地”；", version_id: 6},
  {id: 156185, book: "Matt", verse: "27.034", unformatted: "他们把胆汁调和的酒给耶稣喝。耶稣尝了，不肯喝。", version_id: 6},
  {id: 156186, book: "Matt", verse: "27.035", unformatted: "他们把耶稣钉上十字架以后，抽签分了他的衣服，", version_id: 6},
  {id: 156187, book: "Matt", verse: "27.036", unformatted: "然后坐在那里看守着他，", version_id: 6},
  {id: 156188, book: "Matt", verse: "27.037", unformatted: "又在他的头上方，安了他的罪状牌，上面写道：“这是耶稣，犹太人的王。”", version_id: 6},
  {id: 156189, book: "Matt", verse: "27.038", unformatted: "那时，有两个强盗与耶稣一起被钉十字架，一个在右边，一个在左边。", version_id: 6},
  {id: 156190, book: "Matt", verse: "27.039", unformatted: "那些路过的人辱骂耶稣，摇着头，", version_id: 6},
  {id: 156191, book: "Matt", verse: "27.04", unformatted: "说：“你这个要拆毁圣所、三天内又建起来的人哪，救救你自己吧！你如果是神的儿子，就从十字架上下来吧！”", version_id: 6},
  {id: 156192, book: "Matt", verse: "27.041", unformatted: "同样，祭司长们也与经文士们和长老们一起嘲笑，说：", version_id: 6},
  {id: 156193, book: "Matt", verse: "27.042", unformatted: "“他救了别人，却不能救自己！他是以色列的王！现在让他从十字架上下来吧，这样我们就信靠他！", version_id: 6},
  {id: 156194, book: "Matt", verse: "27.043", unformatted: "他依靠神，如果神喜悦他，就让神现在救他吧！因为他说过‘我是神的儿子。’”", version_id: 6},
  {id: 156195, book: "Matt", verse: "27.044", unformatted: "与耶稣一起被钉十字架的强盗也同样地责骂他。", version_id: 6},
  {id: 156196, book: "Matt", verse: "27.045", unformatted: "耶稣之死\n从中午十二点起，黑暗笼罩了整个大地，一直到下午三点。", version_id: 6},
  {id: 156197, book: "Matt", verse: "27.046", unformatted: "下午三点左右，耶稣大声呼叫：“以利，以利！拉玛撒巴克达尼？”——这意思是：“我的神，我的神！你为什么离弃我？”", version_id: 6},
  {id: 156198, book: "Matt", verse: "27.047", unformatted: "站在那里的一些人听见了，就说：“这个人在呼唤以利亚呢！”", version_id: 6},
  {id: 156199, book: "Matt", verse: "27.048", unformatted: "其中有一个人立刻跑去，拿了海绵蘸满了酸酒，套在芦苇上，要给他喝。", version_id: 6},
  {id: 156200, book: "Matt", verse: "27.049", unformatted: "其他的人说：“等着吧，我们看以利亚会不会来救他。”", version_id: 6},
  {id: 156201, book: "Matt", verse: "27.05", unformatted: "耶稣又大喊一声，就交出了灵魂。", version_id: 6},
  {id: 156202, book: "Matt", verse: "27.051", unformatted: "忽然，圣所里的幔子从上到下裂成两半。大地震动，岩石裂开。", version_id: 6},
  {id: 156203, book: "Matt", verse: "27.052", unformatted: "坟墓也开了，许多已睡了的圣徒的身体复活了。", version_id: 6},
  {id: 156204, book: "Matt", verse: "27.053", unformatted: "耶稣复活以后，他们从坟墓里出来，进了圣城，向许多人显现。", version_id: 6},
  {id: 156205, book: "Matt", verse: "27.054", unformatted: "百夫长和那些与他一起看守耶稣的人，看见地震和所发生的事，就极其惧怕，说：“这个人真是神的儿子！”", version_id: 6},
  {id: 156206, book: "Matt", verse: "27.055", unformatted: "那里有许多妇女从远处观看，她们从加利利开始就跟随耶稣，服事他。", version_id: 6},
  {id: 156207, book: "Matt", verse: "27.056", unformatted: "其中有茉大拉的玛丽亚、雅各和约西的母亲玛丽亚，以及西庇太儿子们的母亲。", version_id: 6},
  {id: 156208, book: "Matt", verse: "27.057", unformatted: "安葬\n到了傍晚，来了一个亚利马太的财主，名叫约瑟，他自己也是耶稣的门徒。", version_id: 6},
  {id: 156209, book: "Matt", verse: "27.058", unformatted: "他来到彼拉多面前，请求要耶稣的遗体，彼拉多就下令给他。", version_id: 6},
  {id: 156210, book: "Matt", verse: "27.059", unformatted: "约瑟领了耶稣的遗体，用洁白的细麻布裹好，", version_id: 6},
  {id: 156211, book: "Matt", verse: "27.06", unformatted: "安放在自己的新墓穴里，这墓穴是他凿在岩石里的。他又滚来一块大石头，堵住墓穴口，就离开了。", version_id: 6},
  {id: 156212, book: "Matt", verse: "27.061", unformatted: "茉大拉的玛丽亚和另一个玛丽亚在那里，对着坟墓坐着。", version_id: 6},
  {id: 156213, book: "Matt", verse: "27.062", unformatted: "严守坟墓\n第二天，就是预备日的后一天，祭司长们和法利赛人在彼拉多那里聚集，", version_id: 6},
  {id: 156214, book: "Matt", verse: "27.063", unformatted: "说：“大人，我们想起当那个迷惑人的还活着时曾经说过：‘在第三天我要复活。’", version_id: 6},
  {id: 156215, book: "Matt", verse: "27.064", unformatted: "所以，请你下令严密看守那坟墓，一直到第三天，免得他的门徒们来把他偷走，然后对民众说他从死人中复活了，这样，那最后的迷惑就要比先前的更厉害了。”", version_id: 6},
  {id: 156216, book: "Matt", verse: "27.065", unformatted: "彼拉多对他们说：“你们有一队卫兵，尽你们所能，严密看守吧！”", version_id: 6},
  {id: 156217, book: "Matt", verse: "27.066", unformatted: "于是，他们带着卫兵去，用印封上墓口的石头，严密看守坟墓。", version_id: 6},
  {id: 156218, book: "Matt", verse: "28.001", unformatted: "复活的清晨\n过了安息日，在一周的头一天，黎明的时候，茉大拉的玛丽亚和另一个玛丽亚来看坟墓。", version_id: 6},
  {id: 156219, book: "Matt", verse: "28.002", unformatted: "忽然发生了强烈的地震，主的一位天使从天上降下来，上前把那石头滚开，坐在上面。", version_id: 6},
  {id: 156220, book: "Matt", verse: "28.003", unformatted: "他的容貌像闪电，衣服洁白如雪。", version_id: 6},
  {id: 156221, book: "Matt", verse: "28.004", unformatted: "看守的卫兵们因怕天使就浑身发抖，变得像死人那样。", version_id: 6},
  {id: 156222, book: "Matt", verse: "28.005", unformatted: "天使对妇女们说：“你们不要怕！我知道你们是在寻找被钉十字架的耶稣。", version_id: 6},
  {id: 156223, book: "Matt", verse: "28.006", unformatted: "他不在这里，因为他已经复活了，正如他所说过的。你们来看安放他的地方。", version_id: 6},
  {id: 156224, book: "Matt", verse: "28.007", unformatted: "赶快去告诉他的门徒们：‘他从死人中复活了。看哪，他要在你们之前到加利利去。在那里，你们将见到他。’看哪，我已经告诉你们了！”", version_id: 6},
  {id: 156225, book: "Matt", verse: "28.008", unformatted: "她们立即离开墓穴，又惧怕，又大大欢喜，跑去告诉耶稣的门徒们。", version_id: 6},
  {id: 156226, book: "Matt", verse: "28.009", unformatted: "这时候，忽然耶稣迎面而来，说：“愿你们欢喜！”她们就上前来抱住他的脚拜他。", version_id: 6},
  {id: 156227, book: "Matt", verse: "28.01", unformatted: "耶稣对她们说：“不要怕！去告诉我的弟兄们，要他们到加利利去。在那里，他们将见到我。”", version_id: 6},
  {id: 156228, book: "Matt", verse: "28.011", unformatted: "士兵受贿造谣\n她们离去的时候，正好有一些卫兵进了城，把所发生的事都报告给祭司长们。", version_id: 6},
  {id: 156229, book: "Matt", verse: "28.012", unformatted: "祭司长们就与长老们一起聚集商议，给了士兵们许多银钱，", version_id: 6},
  {id: 156230, book: "Matt", verse: "28.013", unformatted: "说：“你们要说：‘他的门徒们夜里来，趁我们睡着的时候，把他偷走了。’", version_id: 6},
  {id: 156231, book: "Matt", verse: "28.014", unformatted: "如果这事被总督听见了，我们会说服他，保证你们无事。”", version_id: 6},
  {id: 156232, book: "Matt", verse: "28.015", unformatted: "士兵们收了银钱，就照所指示的去做。于是这说法就流传在犹太人当中，直到今天。", version_id: 6},
  {id: 156233, book: "Matt", verse: "28.016", unformatted: "大使命\n十一个门徒往加利利去，到了耶稣给他们指定的那座山上。", version_id: 6},
  {id: 156234, book: "Matt", verse: "28.017", unformatted: "他们看见耶稣，就拜他，不过还有人疑惑。", version_id: 6},
  {id: 156235, book: "Matt", verse: "28.018", unformatted: "耶稣前来，对他们说：“天上和地上所有的权柄都赐给我了。", version_id: 6},
  {id: 156236, book: "Matt", verse: "28.019", unformatted: "所以你们要去，使万民成为我的门徒；奉父、子、圣灵的名给他们施洗；", version_id: 6},
  {id: 156237, book: "Matt", verse: "28.02", unformatted: "凡我所吩咐你们的，都教导他们遵守。看哪，我就天天都与你们同在，直到这世代的终结。”", version_id: 6},
  {id: 156238, book: "Mark", verse: "1.001", unformatted: "基督的先驱\n神的儿子耶稣基督福音的开始。", version_id: 6},
  {id: 156239, book: "Mark", verse: "1.002", unformatted: "正如先知以赛亚书上所记载的：\n“看哪，我要差派我的使者在你前头，\n他要预备你的道。", version_id: 6},
  {id: 156240, book: "Mark", verse: "1.003", unformatted: "在旷野有声音呼喊着：\n‘你们当预备主的道，\n当修直他的路！’”", version_id: 6},
  {id: 156241, book: "Mark", verse: "1.004", unformatted: "于是约翰就来了。他在旷野施洗，并宣讲为罪得赦免而悔改的洗礼。", version_id: 6},
  {id: 156242, book: "Mark", verse: "1.005", unformatted: "全犹太地区和整个耶路撒冷的人，都出来到他那里，承认自己的罪孽，在约旦河里受他的洗礼。", version_id: 6},
  {id: 156243, book: "Mark", verse: "1.006", unformatted: "约翰穿的是骆驼毛的衣服，腰束皮带，吃的是蝗虫和野蜜。", version_id: 6},
  {id: 156244, book: "Mark", verse: "1.007", unformatted: "他宣讲，说：“在我之后，有一位比我更强有力的要来。我就是弯腰为他解鞋带也不配。", version_id: 6},
  {id: 156245, book: "Mark", verse: "1.008", unformatted: "我用水给你们施洗，但他要用圣灵给你们施洗。”", version_id: 6},
  {id: 156246, book: "Mark", verse: "1.009", unformatted: "耶稣受洗\n在那些日子里，耶稣从加利利的拿撒勒来，在约旦河里受约翰的洗礼。", version_id: 6},
  {id: 156247, book: "Mark", verse: "1.01", unformatted: "耶稣一从水里上来，就看见诸天裂开了，圣灵好像鸽子降临在他身上。", version_id: 6},
  {id: 156248, book: "Mark", verse: "1.011", unformatted: "这时候，有声音从诸天传来，说：\n“你是我的爱子，\n我喜悦你。”", version_id: 6},
  {id: 156249, book: "Mark", verse: "1.012", unformatted: "耶稣受试探\n圣灵立刻催促耶稣到旷野里去。", version_id: 6},
  {id: 156250, book: "Mark", verse: "1.013", unformatted: "他在旷野里四十天，受撒旦的试探。他与野兽在一起，并有天使们伺候他。", version_id: 6},
  {id: 156251, book: "Mark", verse: "1.014", unformatted: "在加利利的服事\n约翰被逮捕以后，耶稣来到加利利，传神的福音，", version_id: 6},
  {id: 156252, book: "Mark", verse: "1.015", unformatted: "说：“日期满了，神的国近了！你们应当悔改，相信福音！”", version_id: 6},
  {id: 156253, book: "Mark", verse: "1.016", unformatted: "第一批门徒\n耶稣从加利利湖边经过，看见西门和他弟弟安得烈，正在湖里撒网；他们本来是渔夫。", version_id: 6},
  {id: 156254, book: "Mark", verse: "1.017", unformatted: "耶稣就对他们说：“来跟从我！我将要使你们成为得人的渔夫。”", version_id: 6},
  {id: 156255, book: "Mark", verse: "1.018", unformatted: "他们立刻舍弃渔网，跟从了耶稣。", version_id: 6},
  {id: 156256, book: "Mark", verse: "1.019", unformatted: "稍往前走，耶稣看见西庇太的儿子雅各和他弟弟约翰。他们正在船上修补渔网。", version_id: 6},
  {id: 156257, book: "Mark", verse: "1.02", unformatted: "耶稣随即召唤他们。他们就离开了在船上的父亲西庇太和雇工们，并且跟着耶稣去了。", version_id: 6},
  {id: 156258, book: "Mark", verse: "1.021", unformatted: "驱赶污灵\n他们来到迦百农。安息日一到，耶稣就进了会堂教导人。", version_id: 6},
  {id: 156259, book: "Mark", verse: "1.022", unformatted: "众人对耶稣的教导都惊叹不已，因为他教导他们，就像有权柄的人，不像经文士们。", version_id: 6},
  {id: 156260, book: "Mark", verse: "1.023", unformatted: "忽然，在他们的会堂里，有一个被污灵附身的人喊叫，", version_id: 6},
  {id: 156261, book: "Mark", verse: "1.024", unformatted: "说：“拿撒勒人耶稣，我们与你有什么关系？你来毁灭我们吗？我知道你是谁，你是神的那位圣者。”", version_id: 6},
  {id: 156262, book: "Mark", verse: "1.025", unformatted: "耶稣斥责污灵说：“住口！从这个人里面出来！”", version_id: 6},
  {id: 156263, book: "Mark", verse: "1.026", unformatted: "污灵使那个人抽疯，然后大声喊叫着从他里面出来了。", version_id: 6},
  {id: 156264, book: "Mark", verse: "1.027", unformatted: "结果大家都惊讶不已，彼此谈论说：“这是怎么回事？！一个带有权柄的新的教导！他命令污灵，污灵就都听从他！”", version_id: 6},
  {id: 156265, book: "Mark", verse: "1.028", unformatted: "于是耶稣的消息很快地传遍了加利利周围所有的地区。", version_id: 6},
  {id: 156266, book: "Mark", verse: "1.029", unformatted: "在迦百农治病\n他们从会堂出来，随即到西门和安得烈的家去，雅各和约翰也一起去。", version_id: 6},
  {id: 156267, book: "Mark", verse: "1.03", unformatted: "西门的岳母正发烧躺着，他们立刻把她的事告诉耶稣。", version_id: 6},
  {id: 156268, book: "Mark", verse: "1.031", unformatted: "耶稣上前去，握着她的手，扶她起来，烧就退了。于是她就服事他们。", version_id: 6},
  {id: 156269, book: "Mark", verse: "1.032", unformatted: "到了傍晚，日落的时候，人们带着所有患病的、有鬼魔附身的，到耶稣那里去。", version_id: 6},
  {id: 156270, book: "Mark", verse: "1.033", unformatted: "全城的人都聚集在门前。", version_id: 6},
  {id: 156271, book: "Mark", verse: "1.034", unformatted: "耶稣就使许多患有各种疾病的人痊愈，又驱赶了很多鬼魔。他不准鬼魔说话，因为那些鬼魔知道他是谁。", version_id: 6},
  {id: 156272, book: "Mark", verse: "1.035", unformatted: "在加利利传道\n大清早，天还没亮，耶稣就起来，走出去，到一个荒僻的地方，在那里祷告。", version_id: 6},
  {id: 156273, book: "Mark", verse: "1.036", unformatted: "西门和那些与他在一起的人去寻找耶稣，", version_id: 6},
  {id: 156274, book: "Mark", verse: "1.037", unformatted: "找到了，就对他说：“大家都在找你呢！”", version_id: 6},
  {id: 156275, book: "Mark", verse: "1.038", unformatted: "耶稣对他们说：“我们往别的地方去，到邻近的乡村去吧。我也好在那里传道，因为我是为此而来的。”", version_id: 6},
  {id: 156276, book: "Mark", verse: "1.039", unformatted: "于是耶稣在全加利利往来，在他们的会堂里传道，并且驱赶鬼魔。", version_id: 6},
  {id: 156277, book: "Mark", verse: "1.04", unformatted: "洁净麻风病人\n有一个麻风病人来到耶稣面前，跪下来恳求他说：“如果你愿意，你就能洁净我。”", version_id: 6},
  {id: 156278, book: "Mark", verse: "1.041", unformatted: "耶稣动了怜悯之心，就伸出手来摸他，说：“我愿意，你洁净了吧！”", version_id: 6},
  {id: 156279, book: "Mark", verse: "1.042", unformatted: "麻风病立刻离开他，他就被洁净了。", version_id: 6},
  {id: 156280, book: "Mark", verse: "1.043", unformatted: "耶稣当下催他离开，并严厉地告诫他，", version_id: 6},
  {id: 156281, book: "Mark", verse: "1.044", unformatted: "说：“你要注意，对任何人都不要说什么，只要去把自己给祭司看，并且为了你的洁净，献上摩西所吩咐的祭物，好对他们做见证。”", version_id: 6},
  {id: 156282, book: "Mark", verse: "1.045", unformatted: "但那个人一出去，就开始大加宣扬，把这件事传开了，结果耶稣不能再公开进城，只好留在外面荒僻的地方。可是人们还是从各处来到他那里。", version_id: 6},
  {id: 156283, book: "Mark", verse: "2.001", unformatted: "赦罪与治病\n过了些日子，耶稣又进了迦百农。人们听说他在房子里，", version_id: 6},
  {id: 156284, book: "Mark", verse: "2.002", unformatted: "许多人就聚集，甚至连门前都没有空处了。于是耶稣就向他们讲道。", version_id: 6},
  {id: 156285, book: "Mark", verse: "2.003", unformatted: "这时候，一些人带了一个瘫痪的人来到耶稣那里，是被四个人抬来的；", version_id: 6},
  {id: 156286, book: "Mark", verse: "2.004", unformatted: "但由于人多，无法抬到耶稣面前，他们就把耶稣上方的屋顶拆了。拆通之后，就把躺在垫子上那瘫痪的人缒了下去。", version_id: 6},
  {id: 156287, book: "Mark", verse: "2.005", unformatted: "耶稣看见他们的信心，就对瘫痪的人说：“孩子，你的罪孽被赦免了。”", version_id: 6},
  {id: 156288, book: "Mark", verse: "2.006", unformatted: "可是，有一些经文士坐在那里，心里想：", version_id: 6},
  {id: 156289, book: "Mark", verse: "2.007", unformatted: "“这个人怎么这样说话呢？他说亵渎的话！除了神一位之外，谁能赦免罪呢？”", version_id: 6},
  {id: 156290, book: "Mark", verse: "2.008", unformatted: "耶稣灵里立刻知道他们心里这样想，就对他们说：“你们心里为什么想这些事呢？", version_id: 6},
  {id: 156291, book: "Mark", verse: "2.009", unformatted: "对这瘫痪的人说‘你的罪孽被赦免了’，或说‘你起来，拿起垫子走路’，哪一样更容易呢？", version_id: 6},
  {id: 156292, book: "Mark", verse: "2.01", unformatted: "不过为要使你们知道人子在地上有赦免罪的权柄——”耶稣就对瘫痪的人说：", version_id: 6},
  {id: 156293, book: "Mark", verse: "2.011", unformatted: "“我吩咐你：起来，拿起你的垫子，回家去吧！”", version_id: 6},
  {id: 156294, book: "Mark", verse: "2.012", unformatted: "那个人就立刻起来，拿起垫子在大家面前出去了。结果大家都惊讶，就荣耀神，说：“我们从来没有见过这样的事。”", version_id: 6},
  {id: 156295, book: "Mark", verse: "2.013", unformatted: "呼召马太\n后来，耶稣又出去，到了加利利湖边。众人都来到他那里，他就教导他们。", version_id: 6},
  {id: 156296, book: "Mark", verse: "2.014", unformatted: "耶稣往前走，看见亚勒腓的儿子利未在税关坐着。耶稣对他说：“你跟从我！”利未就站起来，跟从了耶稣。", version_id: 6},
  {id: 156297, book: "Mark", verse: "2.015", unformatted: "与罪人吃饭\n耶稣在利未家里坐席的时候，许多税吏和罪人与耶稣及他的门徒们一同坐席，因为有许多这样的人也跟随了耶稣。", version_id: 6},
  {id: 156298, book: "Mark", verse: "2.016", unformatted: "有些法利赛派的经文士见耶稣与罪人和税吏一起吃饭，就对耶稣的门徒们说：“他怎么与那些税吏和罪人一起吃饭呢？”", version_id: 6},
  {id: 156299, book: "Mark", verse: "2.017", unformatted: "耶稣听见了，就对他们说：“健康的人不需要医生，有病的人才需要。我来不是要召唤义人，而是要召唤罪人。”", version_id: 6},
  {id: 156300, book: "Mark", verse: "2.018", unformatted: "禁食的问题\n约翰的门徒们和法利赛人正禁食的时候，有些人来问耶稣：“为什么约翰的门徒们和法利赛人的门徒们都禁食，而你的门徒们却不禁食呢？”", version_id: 6},
  {id: 156301, book: "Mark", verse: "2.019", unformatted: "耶稣对他们说：“新郎与宾客在一起的时候，难道宾客能禁食吗？只要他们与新郎在一起，他们就不能禁食。", version_id: 6},
  {id: 156302, book: "Mark", verse: "2.02", unformatted: "可是日子将要来到：当新郎从他们中间被带走的时候，他们那时——在那一天就要禁食了。", version_id: 6},
  {id: 156303, book: "Mark", verse: "2.021", unformatted: "没有人把没缩过水的布块补在旧衣服上。否则所补上的新布会扯坏旧衣服，造成更大的裂口。", version_id: 6},
  {id: 156304, book: "Mark", verse: "2.022", unformatted: "同样，也没有人把新酒装在旧皮袋里，否则酒会胀破皮袋，酒和皮袋都糟蹋了。因此，新酒必须装在新皮袋里。”", version_id: 6},
  {id: 156305, book: "Mark", verse: "2.023", unformatted: "安息日的主\n有一次在安息日，耶稣从麦田经过，他的门徒们开始边走边摘麦穗。", version_id: 6},
  {id: 156306, book: "Mark", verse: "2.024", unformatted: "有些法利赛人对耶稣说：“你看，他们为什么做安息日不可以做的事呢？”", version_id: 6},
  {id: 156307, book: "Mark", verse: "2.025", unformatted: "耶稣对他们说：“大卫和那些与他在一起的人饥饿缺乏时所做的事，难道你们从来没有读过吗？", version_id: 6},
  {id: 156308, book: "Mark", verse: "2.026", unformatted: "在亚比亚达任大祭司的时候，他难道不是进了神的殿，吃了陈设饼，还分给与他在一起的人吗？这饼除了祭司，谁都不可以吃。”", version_id: 6},
  {id: 156309, book: "Mark", verse: "2.027", unformatted: "接着，耶稣对他们说：“安息日是为人而存在的，人不是为安息日而存在的。", version_id: 6},
  {id: 156310, book: "Mark", verse: "2.028", unformatted: "所以，人子是主，也是安息日的主。”", version_id: 6},
  {id: 156311, book: "Mark", verse: "3.001", unformatted: "治愈手萎缩的人\n耶稣又进了会堂。那里有个人，他的一只手枯萎了。", version_id: 6},
  {id: 156312, book: "Mark", verse: "3.002", unformatted: "有些人就密切注意耶稣会不会在安息日使那个人痊愈，这是为要控告耶稣。", version_id: 6},
  {id: 156313, book: "Mark", verse: "3.003", unformatted: "耶稣对那手枯萎的人说：“起来，站到中间来！”", version_id: 6},
  {id: 156314, book: "Mark", verse: "3.004", unformatted: "耶稣问他们：“在安息日可以行善还是作恶，救命还是害命？”他们却不做声。", version_id: 6},
  {id: 156315, book: "Mark", verse: "3.005", unformatted: "耶稣愤怒地环视他们，又为他们心里刚硬而忧伤，就对那个人说：“伸出手来！”他一伸出来，手就复原了。", version_id: 6},
  {id: 156316, book: "Mark", verse: "3.006", unformatted: "那些法利赛人就出去，立刻与希律党的人一起商议怎样对付耶稣，好除灭他。", version_id: 6},
  {id: 156317, book: "Mark", verse: "3.007", unformatted: "服事群众\n耶稣与他的门徒们退到湖边，有一大群从加利利来的人跟随他；还有来自犹太、", version_id: 6},
  {id: 156318, book: "Mark", verse: "3.008", unformatted: "耶路撒冷、以杜迈亚、约旦河对岸，以及提尔和西顿周围的一大群人来到他那里，因为他们听说了耶稣所做的事。", version_id: 6},
  {id: 156319, book: "Mark", verse: "3.009", unformatted: "由于人多，耶稣就吩咐他的门徒为他预备一条小船，免得众人拥挤他。", version_id: 6},
  {id: 156320, book: "Mark", verse: "3.01", unformatted: "因为他使许多人痊愈，所以有病痛的人都向他挤过来，想要摸到他。", version_id: 6},
  {id: 156321, book: "Mark", verse: "3.011", unformatted: "污灵每当见到耶稣，就俯伏在他面前，喊叫说：“你是神的儿子！”", version_id: 6},
  {id: 156322, book: "Mark", verse: "3.012", unformatted: "可是，耶稣严厉地斥责它们不要把他传扬出去。", version_id: 6},
  {id: 156323, book: "Mark", verse: "3.013", unformatted: "选定十二使徒\n耶稣上了山，召来那些他想要的人，他们就来到他那里。", version_id: 6},
  {id: 156324, book: "Mark", verse: "3.014", unformatted: "于是，耶稣委任了十二个人，称他们为使徒，是要他们常与他在一起，并要差派他们出去传道，", version_id: 6},
  {id: 156325, book: "Mark", verse: "3.015", unformatted: "又使他们有权柄驱赶鬼魔。", version_id: 6},
  {id: 156326, book: "Mark", verse: "3.016", unformatted: "这样，他委任了十二使徒：\n西门——耶稣给他起名叫彼得；", version_id: 6},
  {id: 156327, book: "Mark", verse: "3.017", unformatted: "西庇太的儿子雅各，\n雅各的弟弟约翰\n——耶稣给他们起名叫“波阿内格”——\n这意思是“雷霆之子”；", version_id: 6},
  {id: 156328, book: "Mark", verse: "3.018", unformatted: "又有安得烈，\n腓力和巴多罗迈，\n马太和多马，\n亚勒腓的儿子雅各和达太，\n激进派的西门，", version_id: 6},
  {id: 156329, book: "Mark", verse: "3.019", unformatted: "还有那出卖耶稣的加略人犹大。", version_id: 6},
  {id: 156330, book: "Mark", verse: "3.02", unformatted: "自相纷争\n耶稣进了房子，人群又聚集而来，以致他和门徒们连饭都不能吃。", version_id: 6},
  {id: 156331, book: "Mark", verse: "3.021", unformatted: "耶稣的亲属听说了，就来制止他，因为有人说他发疯了。", version_id: 6},
  {id: 156332, book: "Mark", verse: "3.022", unformatted: "从耶路撒冷下来的一些经文士说：“他有别西卜附身，他是靠鬼魔的王驱赶鬼魔的。”", version_id: 6},
  {id: 156333, book: "Mark", verse: "3.023", unformatted: "耶稣把他们召来，用比喻对他们说：“撒旦怎么能够驱赶撒旦呢？", version_id: 6},
  {id: 156334, book: "Mark", verse: "3.024", unformatted: "一个国家如果自相纷争，这个国家就站立不住；", version_id: 6},
  {id: 156335, book: "Mark", verse: "3.025", unformatted: "一个家庭如果自相纷争，这个家庭就站立不住；", version_id: 6},
  {id: 156336, book: "Mark", verse: "3.026", unformatted: "撒旦如果起来反对自己而纷争，他不但站立不住，而且要完了。", version_id: 6},
  {id: 156337, book: "Mark", verse: "3.027", unformatted: "“没有人能进入一个壮士的家抢夺他的东西，如果不先把那壮士捆住，就不能抢夺他的家。", version_id: 6},
  {id: 156338, book: "Mark", verse: "3.028", unformatted: "我确实地告诉你们：人类的一切罪和亵渎的话，无论怎样亵渎，都能被赦免。", version_id: 6},
  {id: 156339, book: "Mark", verse: "3.029", unformatted: "但如果有人亵渎圣灵，他永远得不到赦免，而且必担当永远的罪。”", version_id: 6},
  {id: 156340, book: "Mark", verse: "3.03", unformatted: "这话是因为他们说：“他有污灵附身。”", version_id: 6},
  {id: 156341, book: "Mark", verse: "3.031", unformatted: "真正的亲属\n后来，耶稣的母亲和弟弟们来了，站在外面，派人去叫耶稣。", version_id: 6},
  {id: 156342, book: "Mark", verse: "3.032", unformatted: "有一群人正围着耶稣坐着，他们对他说：“看，你母亲和弟弟妹妹们在外面找你。”", version_id: 6},
  {id: 156343, book: "Mark", verse: "3.033", unformatted: "耶稣回答他们，说：“我的母亲、我的弟兄是谁呢？”", version_id: 6},
  {id: 156344, book: "Mark", verse: "3.034", unformatted: "他环视坐在他周围的人，说：“看，我的母亲、我的弟兄！", version_id: 6},
  {id: 156345, book: "Mark", verse: "3.035", unformatted: "因为凡是遵行神旨意的人，他才是我的弟兄、姐妹和母亲。”", version_id: 6},
  {id: 156346, book: "Mark", verse: "4.001", unformatted: "撒种的比喻\n又一次，耶稣开始在湖边教导人。有一大群人聚集到他那里，所以他上了湖中的一条船坐下来。众人都在岸边，面对湖站着。", version_id: 6},
  {id: 156347, book: "Mark", verse: "4.002", unformatted: "耶稣用比喻教导他们很多事，在教导时对他们说：", version_id: 6},
  {id: 156348, book: "Mark", verse: "4.003", unformatted: "“你们当听好：看哪，有一个撒种的出去撒种。", version_id: 6},
  {id: 156349, book: "Mark", verse: "4.004", unformatted: "他撒的时候，有的种子落在路边，飞鸟来了，就把它吃掉了。", version_id: 6},
  {id: 156350, book: "Mark", verse: "4.005", unformatted: "另有落在岩石地上的，那里没有多少泥土，它立刻就发芽了，因为土不深；", version_id: 6},
  {id: 156351, book: "Mark", verse: "4.006", unformatted: "当太阳升起，它被暴晒，就枯萎了，因为没有根。", version_id: 6},
  {id: 156352, book: "Mark", verse: "4.007", unformatted: "另有落进荆棘丛里的，荆棘长起来把它挤住，它就结不出果实了。", version_id: 6},
  {id: 156353, book: "Mark", verse: "4.008", unformatted: "可是另有落进好土壤里的，就发芽、生长，不断地结出果实，结出的有三十倍的、有六十倍的、有一百倍的。”", version_id: 6},
  {id: 156354, book: "Mark", verse: "4.009", unformatted: "耶稣说：“凡是有耳可听的，就应当听！”", version_id: 6},
  {id: 156355, book: "Mark", verse: "4.01", unformatted: "用比喻的原因\n耶稣独自一人的时候，跟随他的人与十二使徒一起问他这些比喻的意思。", version_id: 6},
  {id: 156356, book: "Mark", verse: "4.011", unformatted: "耶稣对他们说：“神国的奥秘已经赐给你们了，但是对外人，一切都是用比喻的。", version_id: 6},
  {id: 156357, book: "Mark", verse: "4.012", unformatted: "这是为要使\n‘他们看了又看，却不明白，\n听了又听，却不领悟，\n免得他们回转过来，\n就被赦免。’”", version_id: 6},
  {id: 156358, book: "Mark", verse: "4.013", unformatted: "解明撒种的比喻\n耶稣对他们说：“你们不明白这个比喻，又怎么能领会所有的比喻呢？", version_id: 6},
  {id: 156359, book: "Mark", verse: "4.014", unformatted: "撒种的人所撒的是话语。", version_id: 6},
  {id: 156360, book: "Mark", verse: "4.015", unformatted: "那被撒上话语的‘路边之地’是这样一些人：他们听了话语以后，撒旦就立刻来把那撒在他们心里的话语夺走。", version_id: 6},
  {id: 156361, book: "Mark", verse: "4.016", unformatted: "那被撒上话语的‘岩石地’是这样一些人：他们一旦听了这话语，就立刻怀着喜乐的心接受了它；", version_id: 6},
  {id: 156362, book: "Mark", verse: "4.017", unformatted: "可是他们里面没有根而不能持久，一旦为这话语受患难或逼迫，他们立刻就放弃了。", version_id: 6},
  {id: 156363, book: "Mark", verse: "4.018", unformatted: "那被撒进话语的‘荆棘丛’是另外一些人，那些人是这样的：他们听了这话语，", version_id: 6},
  {id: 156364, book: "Mark", verse: "4.019", unformatted: "但今世的各种忧虑、财富的迷惑，以及其他各种欲望纷纷进来把这话语挤住了，他就成了不结果实的。", version_id: 6},
  {id: 156365, book: "Mark", verse: "4.02", unformatted: "而那被撒上话语的‘好土壤’是这样的人：他们听了这话语就接受，并且结出果实来，有三十倍的，有六十倍的，也有一百倍的。”", version_id: 6},
  {id: 156366, book: "Mark", verse: "4.021", unformatted: "油灯的比喻\n耶稣又对他们说：“油灯拿进来，难道是为了放在斗底下，或床底下吗？不是为了放在灯台上吗？", version_id: 6},
  {id: 156367, book: "Mark", verse: "4.022", unformatted: "这样，就算是隐秘的事，也没有不显明出来的；即使是隐藏的事，也没有不显露出来的。", version_id: 6},
  {id: 156368, book: "Mark", verse: "4.023", unformatted: "凡是有耳可听的，就应当听！”", version_id: 6},
  {id: 156369, book: "Mark", verse: "4.024", unformatted: "耶稣又说：“你们要留心所听到的。你们用什么量器来衡量，也会同样地被衡量，而且还将加添给你们；", version_id: 6},
  {id: 156370, book: "Mark", verse: "4.025", unformatted: "因为凡是有的，还要赐给他；凡是没有的，连他有的也将从他那里被拿走。”", version_id: 6},
  {id: 156371, book: "Mark", verse: "4.026", unformatted: "种子生长的比喻\n耶稣又说：“神的国就像这样：有人在地上撒了种子，", version_id: 6},
  {id: 156372, book: "Mark", verse: "4.027", unformatted: "黑夜白昼，或睡或起，他却不知道那种子怎样发芽和长大。", version_id: 6},
  {id: 156373, book: "Mark", verse: "4.028", unformatted: "大地自然长出五谷——先长苗，再吐穗，然后穗上结出饱满的子粒。", version_id: 6},
  {id: 156374, book: "Mark", verse: "4.029", unformatted: "当果实成熟的时候，他立刻伸出镰刀，因为收割的季节到了。”", version_id: 6},
  {id: 156375, book: "Mark", verse: "4.03", unformatted: "芥菜种的比喻\n耶稣又说：“我们把神的国比做什么呢？用什么比喻来表明它呢？", version_id: 6},
  {id: 156376, book: "Mark", verse: "4.031", unformatted: "它好像一粒芥菜种子，被种入地里的时候，比地上所有的种子更小，", version_id: 6},
  {id: 156377, book: "Mark", verse: "4.032", unformatted: "可是被种下去以后，它就长起来，长得比所有蔬菜更大，并且长出大枝子，以致天空的飞鸟能在它的荫下搭窝。”", version_id: 6},
  {id: 156378, book: "Mark", verse: "4.033", unformatted: "使用比喻\n耶稣照着他们所能听的，用许多这样的比喻，向他们讲道。", version_id: 6},
  {id: 156379, book: "Mark", verse: "4.034", unformatted: "他向众人讲话，没有不用比喻的。可是，他却单独给自己的门徒们解释一切。", version_id: 6},
  {id: 156380, book: "Mark", verse: "4.035", unformatted: "平静风浪\n那天晚上，耶稣对门徒们说：“我们渡到对岸去吧。”", version_id: 6},
  {id: 156381, book: "Mark", verse: "4.036", unformatted: "门徒离开众人，耶稣仍然在船上，他们就把他一同带去；也有别的船与他同行。", version_id: 6},
  {id: 156382, book: "Mark", verse: "4.037", unformatted: "这时候，起了大风暴，以致波浪打进船里，船几乎灌满了水。", version_id: 6},
  {id: 156383, book: "Mark", verse: "4.038", unformatted: "耶稣却在船尾靠着枕头睡着了。门徒们叫醒他，说：“老师！我们没命了，你不管吗？”", version_id: 6},
  {id: 156384, book: "Mark", verse: "4.039", unformatted: "耶稣就起来，斥责了风，对浪说：“不许做声！平静下来！”于是风就停了，湖面就变得一片平静。", version_id: 6},
  {id: 156385, book: "Mark", verse: "4.04", unformatted: "耶稣对门徒们说：“为什么胆怯呢？你们还没有信吗？”", version_id: 6},
  {id: 156386, book: "Mark", verse: "4.041", unformatted: "门徒们就大为惊恐，彼此说：“这个人究竟是谁？连风和浪也听从他！”", version_id: 6},
  {id: 156387, book: "Mark", verse: "5.001", unformatted: "在格拉森驱赶污灵\n他们来到湖的对岸，进了格拉森人的地方。", version_id: 6},
  {id: 156388, book: "Mark", verse: "5.002", unformatted: "耶稣刚下船，就有一个被污灵附身的人，从墓地迎着他来。", version_id: 6},
  {id: 156389, book: "Mark", verse: "5.003", unformatted: "这个人住在墓地里，没有人能捆住他，就是用铁链也不行。", version_id: 6},
  {id: 156390, book: "Mark", verse: "5.004", unformatted: "他经常被脚镣和铁链捆起来，然而他总是挣断铁链、打碎脚镣。没有人能制伏他。", version_id: 6},
  {id: 156391, book: "Mark", verse: "5.005", unformatted: "不管黑夜白昼，他常常在墓地和山上喊叫，又用石头砍自己。", version_id: 6},
  {id: 156392, book: "Mark", verse: "5.006", unformatted: "他远远地看见耶稣，就跑过去，向他下拜，", version_id: 6},
  {id: 156393, book: "Mark", verse: "5.007", unformatted: "大声喊叫说：“至高神的儿子耶稣，我与你有什么关系？我藉着神恳求你，不要折磨我！”", version_id: 6},
  {id: 156394, book: "Mark", verse: "5.008", unformatted: "这是因为耶稣对他说：“你出来，污灵！离开这个人！”", version_id: 6},
  {id: 156395, book: "Mark", verse: "5.009", unformatted: "耶稣问他：“你叫什么名字？”\n他回答说：“我名叫‘军团’，因为我们众多。”", version_id: 6},
  {id: 156396, book: "Mark", verse: "5.01", unformatted: "他一再央求耶稣不要把它们赶出那个地方。", version_id: 6},
  {id: 156397, book: "Mark", verse: "5.011", unformatted: "当时，那里有一大群猪正在附近的山上吃食。", version_id: 6},
  {id: 156398, book: "Mark", verse: "5.012", unformatted: "那些污灵就央求耶稣，说：“求你打发我们到猪群里，好让我们进入猪里面去吧！”", version_id: 6},
  {id: 156399, book: "Mark", verse: "5.013", unformatted: "耶稣准许了它们，污灵就出来，进入猪里面。那群猪约有两千头，它们从山崖冲到湖里，在湖里淹死了。", version_id: 6},
  {id: 156400, book: "Mark", verse: "5.014", unformatted: "那些放猪的人就逃跑了，去把这事传到城里城外。于是人们就来看发生了什么事。", version_id: 6},
  {id: 156401, book: "Mark", verse: "5.015", unformatted: "他们来到耶稣那里，看见那曾经有鬼魔附身的，就是被‘军团’所附的那个人，穿着衣服，神志清醒地坐着，他们就惧怕起来。", version_id: 6},
  {id: 156402, book: "Mark", verse: "5.016", unformatted: "那些看见的人，把发生在有鬼魔附身的人身上和有关猪群的事，都告诉了大家。", version_id: 6},
  {id: 156403, book: "Mark", verse: "5.017", unformatted: "他们就开始央求耶稣离开他们的地区。", version_id: 6},
  {id: 156404, book: "Mark", verse: "5.018", unformatted: "耶稣上船的时候，那曾被鬼魔附身的人恳求他，要与他在一起。", version_id: 6},
  {id: 156405, book: "Mark", verse: "5.019", unformatted: "耶稣没有答应，却对他说：“你回家去吧，到你的亲友那里，告诉他们主为你做了什么，以及他怎样怜悯你。”", version_id: 6},
  {id: 156406, book: "Mark", verse: "5.02", unformatted: "于是那个人离开了，开始在德卡波利斯地区传扬耶稣为他做了什么样的事。众人都感到惊奇。", version_id: 6},
  {id: 156407, book: "Mark", verse: "5.021", unformatted: "少女复活、妇人痊愈\n耶稣又上船渡到对岸。有一大群人聚集到他那里，他就留在湖边。", version_id: 6},
  {id: 156408, book: "Mark", verse: "5.022", unformatted: "这时候，来了一个名叫睚鲁的会堂主管。他一见到耶稣，就俯伏在耶稣脚前，", version_id: 6},
  {id: 156409, book: "Mark", verse: "5.023", unformatted: "迫切地恳求他，说：“我的小女儿快要死了。求你来按手在她身上，好让她得救治，能活下去。”", version_id: 6},
  {id: 156410, book: "Mark", verse: "5.024", unformatted: "耶稣就与他一起去，有一大群人跟随耶稣，并拥挤他。", version_id: 6},
  {id: 156411, book: "Mark", verse: "5.025", unformatted: "有一个妇人，患了十二年的血漏病。", version_id: 6},
  {id: 156412, book: "Mark", verse: "5.026", unformatted: "她在许多医生手里受了很多苦，花尽了她所有的一切，没有任何效果，反而倒变得更严重了。", version_id: 6},
  {id: 156413, book: "Mark", verse: "5.027", unformatted: "她听说了耶稣的事，就夹在人群中，从后面摸了一下耶稣的衣服，", version_id: 6},
  {id: 156414, book: "Mark", verse: "5.028", unformatted: "原来她想：“只要我摸到他的衣服，就会得救治。”", version_id: 6},
  {id: 156415, book: "Mark", verse: "5.029", unformatted: "立刻，她的血漏就止住了，她也感觉到身体从病痛中痊愈了。", version_id: 6},
  {id: 156416, book: "Mark", verse: "5.03", unformatted: "耶稣自己里面立刻知道有能力从他而出，就在人群中转过身来，问：“谁摸了我的衣服？”", version_id: 6},
  {id: 156417, book: "Mark", verse: "5.031", unformatted: "他的门徒们对他说：“你看这群人拥挤着你，你还问‘谁摸了我’吗？”", version_id: 6},
  {id: 156418, book: "Mark", verse: "5.032", unformatted: "可是耶稣环视周围，要知道是谁做了这事。", version_id: 6},
  {id: 156419, book: "Mark", verse: "5.033", unformatted: "那妇人知道发生在自己身上的事，就惧怕起来，战战兢兢地上前俯伏在耶稣面前，把真实情况全告诉了他。", version_id: 6},
  {id: 156420, book: "Mark", verse: "5.034", unformatted: "耶稣对她说：“女儿，你的信救了你，平平安安地去吧。你从病痛中痊愈了吧！”", version_id: 6},
  {id: 156421, book: "Mark", verse: "5.035", unformatted: "耶稣还在说话的时候，有些人从会堂主管的家里来，对睚鲁说：“你的女儿已经死了，何必再麻烦老师呢？”", version_id: 6},
  {id: 156422, book: "Mark", verse: "5.036", unformatted: "耶稣却当做没听见他们所说的话，对会堂主管说：“不要怕，只要信！”", version_id: 6},
  {id: 156423, book: "Mark", verse: "5.037", unformatted: "然后他除了彼得、雅各和雅各的弟弟约翰以外，不准别的人跟着。", version_id: 6},
  {id: 156424, book: "Mark", verse: "5.038", unformatted: "他们来到会堂主管的家里，耶稣看见一片混乱，有人哭泣，有人哀号。", version_id: 6},
  {id: 156425, book: "Mark", verse: "5.039", unformatted: "耶稣进去对他们说：“为什么慌乱哭泣呢？孩子不是死了，而是睡了。”", version_id: 6},
  {id: 156426, book: "Mark", verse: "5.04", unformatted: "他们就讥笑耶稣。耶稣把他们都赶出去，只带着孩子的父母和一起来的人，进入孩子所在的地方。", version_id: 6},
  {id: 156427, book: "Mark", verse: "5.041", unformatted: "他握着孩子的手，对她说：“塔利达，库莫！”——这翻译出来就是“小女孩，我吩咐你起来！”", version_id: 6},
  {id: 156428, book: "Mark", verse: "5.042", unformatted: "那女孩随即起来，开始走动。她那时十二岁。大家立刻目瞪口呆，大为惊讶。", version_id: 6},
  {id: 156429, book: "Mark", verse: "5.043", unformatted: "耶稣郑重地吩咐他们，不要让任何人知道这事，又吩咐给孩子吃东西。", version_id: 6},
  {id: 156430, book: "Mark", verse: "6.001", unformatted: "在拿撒勒被拒绝\n耶稣离开那地方，来到自己的家乡，他的门徒们也跟着他。", version_id: 6},
  {id: 156431, book: "Mark", verse: "6.002", unformatted: "到了安息日，他开始在会堂里教导人。许多人听了就惊叹不已，说：“这个人从哪里得来这些呢？赐给这个人的到底是什么样的智慧？藉着他的手所行出来的到底是怎么样的神迹？", version_id: 6},
  {id: 156432, book: "Mark", verse: "6.003", unformatted: "这不就是那木匠吗？不就是玛丽亚的儿子，雅各、约西、犹大和西门的哥哥吗？他的妹妹们不也和我们在这里吗？”这样，他们就对耶稣很反感。", version_id: 6},
  {id: 156433, book: "Mark", verse: "6.004", unformatted: "耶稣对他们说：“先知除了在本乡、本族、本家以外，没有不受尊重的。”", version_id: 6},
  {id: 156434, book: "Mark", verse: "6.005", unformatted: "所以，耶稣无法在那里行什么神迹，只是按手在几个病人身上，使他们痊愈。", version_id: 6},
  {id: 156435, book: "Mark", verse: "6.006", unformatted: "因为他们的不信，耶稣十分感慨。\n差派十二使徒\n于是，他就走遍各村庄四处教导人。", version_id: 6},
  {id: 156436, book: "Mark", verse: "6.007", unformatted: "耶稣召来十二使徒，开始差派他们两个两个地出去，赐给他们权柄胜过污灵，", version_id: 6},
  {id: 156437, book: "Mark", verse: "6.008", unformatted: "并且吩咐他们说：“在路上除了手杖什么都不要带。不要带食物，不要带行囊，腰包里也不要带钱。", version_id: 6},
  {id: 156438, book: "Mark", verse: "6.009", unformatted: "要穿上鞋子。不要穿两件衣服。”", version_id: 6},
  {id: 156439, book: "Mark", verse: "6.01", unformatted: "他又对他们说：“无论在哪里，你们进了一家，就要住在哪里，直到离开那地方。", version_id: 6},
  {id: 156440, book: "Mark", verse: "6.011", unformatted: "如果有什么地方的人不接受你们，也不听你们，你们离开那里的时候，要把脚底的尘土跺掉，做为对他们的见证。”", version_id: 6},
  {id: 156441, book: "Mark", verse: "6.012", unformatted: "使徒们就出去宣讲道：人人都当悔改。", version_id: 6},
  {id: 156442, book: "Mark", verse: "6.013", unformatted: "他们驱赶了很多鬼魔，又用油来膏抹许多病人，使他们痊愈。", version_id: 6},
  {id: 156443, book: "Mark", verse: "6.014", unformatted: "施洗约翰之死\n耶稣的名声被传扬出去，希律王也听见了。有些人说：“施洗者约翰从死人中复活了，所以这些能力在他里面做工。”", version_id: 6},
  {id: 156444, book: "Mark", verse: "6.015", unformatted: "也有些人说：“他是以利亚。”还有些人说：“他是个先知，像古代先知中的一位。”", version_id: 6},
  {id: 156445, book: "Mark", verse: "6.016", unformatted: "但希律听了却说：“是被我砍了头的约翰！这个人复活了！”", version_id: 6},
  {id: 156446, book: "Mark", verse: "6.017", unformatted: "原来，希律为了他弟弟菲利普的妻子希罗迪娅的缘故，曾经派人去拘捕约翰，把他捆锁在监狱里。希律娶了这个女人，", version_id: 6},
  {id: 156447, book: "Mark", verse: "6.018", unformatted: "而约翰一直对希律说：“你占有自己兄弟的妻子是不可以的。”", version_id: 6},
  {id: 156448, book: "Mark", verse: "6.019", unformatted: "所以希罗迪娅对约翰怀恨在心，想要杀他，只是不能。", version_id: 6},
  {id: 156449, book: "Mark", verse: "6.02", unformatted: "这是因为希律敬畏约翰，知道他是义人，是圣人，所以保护着他。希律听约翰讲说的时候，虽然心里十分困惑，还是乐意听他的。", version_id: 6},
  {id: 156450, book: "Mark", verse: "6.021", unformatted: "可是时机来了。希律在自己生日那天预备宴席，招待他的大臣和军官们，以及加利利的首领们。", version_id: 6},
  {id: 156451, book: "Mark", verse: "6.022", unformatted: "希罗迪娅的女儿进来跳舞，使希律和一同坐席的人都非常喜悦。王就对女孩说：“你无论想要什么，向我求，我就会给你。”", version_id: 6},
  {id: 156452, book: "Mark", verse: "6.023", unformatted: "并且对她郑重地起誓：“你无论向我求什么，即使是我国家的一半，我也会给你！”", version_id: 6},
  {id: 156453, book: "Mark", verse: "6.024", unformatted: "于是女孩出去问她母亲说：“我应该要什么呢？”\n她母亲说：“要施洗者约翰的头！”", version_id: 6},
  {id: 156454, book: "Mark", verse: "6.025", unformatted: "女孩随即赶快进到王那里，要求说：“我愿王立即把施洗者约翰的头放在盘子里给我。”", version_id: 6},
  {id: 156455, book: "Mark", verse: "6.026", unformatted: "王就变得很忧愁，但因所起的誓，又因坐席的人，就不好拒绝她。", version_id: 6},
  {id: 156456, book: "Mark", verse: "6.027", unformatted: "于是王立刻派了刽子手，命令把约翰的头拿来。刽子手就去，在监狱里砍下了约翰的头，", version_id: 6},
  {id: 156457, book: "Mark", verse: "6.028", unformatted: "把他的头放在盘子上，端给女孩，女孩就把它交给了母亲。", version_id: 6},
  {id: 156458, book: "Mark", verse: "6.029", unformatted: "约翰的门徒们听到了，就来把他的遗体领去，安放在坟墓里。", version_id: 6},
  {id: 156459, book: "Mark", verse: "6.03", unformatted: "五饼二鱼\n使徒们聚集到耶稣那里，把他们所做的和教导的一切告诉了他。", version_id: 6},
  {id: 156460, book: "Mark", verse: "6.031", unformatted: "耶稣对他们说：“来，你们自己单独到一个荒僻的地方休息一会儿吧。”这是因为来往的人多，他们连吃饭的时间也没有。", version_id: 6},
  {id: 156461, book: "Mark", verse: "6.032", unformatted: "于是他们悄悄地坐船出发，往一个荒僻的地方去。", version_id: 6},
  {id: 156462, book: "Mark", verse: "6.033", unformatted: "可是众人看见他们离开了，并且有许多人认出他们，就从各城镇一起徒步赶往那里，而且比他们先到了。", version_id: 6},
  {id: 156463, book: "Mark", verse: "6.034", unformatted: "耶稣下了船，看见一大群人，就对他们动了怜悯之心，因为他们像羊没有牧人那样。耶稣就开始教导他们很多事。", version_id: 6},
  {id: 156464, book: "Mark", verse: "6.035", unformatted: "天已经晚了，他的门徒们来到耶稣面前，说：“这是个荒僻的地方，而且天已经晚了。", version_id: 6},
  {id: 156465, book: "Mark", verse: "6.036", unformatted: "请遣散大家，好让他们到周围的田舍和村庄去，为自己买些东西吃。”", version_id: 6},
  {id: 156466, book: "Mark", verse: "6.037", unformatted: "但耶稣回答他们，说：“你们给他们吃的吧。”\n门徒们说：“要我们去买两百个银币的饼给他们吃吗？”", version_id: 6},
  {id: 156467, book: "Mark", verse: "6.038", unformatted: "耶稣问他们：“你们有多少饼呢？去看看。”\n他们察看后，就说：“有五个，还有两条鱼。”", version_id: 6},
  {id: 156468, book: "Mark", verse: "6.039", unformatted: "耶稣吩咐他们，叫众人一组一组地坐在青草地上。", version_id: 6},
  {id: 156469, book: "Mark", verse: "6.04", unformatted: "众人就一群一群地坐了下来，有的一百人，有的五十人。", version_id: 6},
  {id: 156470, book: "Mark", verse: "6.041", unformatted: "耶稣拿起这五个饼和两条鱼，抬头望天，祝福了，然后把饼掰开，不断递给他的门徒们，让他们分给众人。他把两条鱼也分给大家。", version_id: 6},
  {id: 156471, book: "Mark", verse: "6.042", unformatted: "大家都吃了，并且吃饱了。", version_id: 6},
  {id: 156472, book: "Mark", verse: "6.043", unformatted: "他们把饼和鱼的碎块收拾起来，装满了十二个篮子。", version_id: 6},
  {id: 156473, book: "Mark", verse: "6.044", unformatted: "吃饼的人，男人就有五千。", version_id: 6},
  {id: 156474, book: "Mark", verse: "6.045", unformatted: "在水面上行走\n耶稣随即催促他的门徒们上船，要他们先往对岸的伯赛达去；这期间，他自己遣散了人群。", version_id: 6},
  {id: 156475, book: "Mark", verse: "6.046", unformatted: "他告别了他们，就上山去祷告。", version_id: 6},
  {id: 156476, book: "Mark", verse: "6.047", unformatted: "到了晚上，船在湖中，只有耶稣一个人在岸上。", version_id: 6},
  {id: 156477, book: "Mark", verse: "6.048", unformatted: "他看见门徒们由于逆风，划船非常艰苦。天快亮的时候，耶稣在湖面上行走，向他们而去，想要从他们旁边走过去。", version_id: 6},
  {id: 156478, book: "Mark", verse: "6.049", unformatted: "门徒们看见耶稣在湖面上行走，以为是幽灵，于是喊叫起来，", version_id: 6},
  {id: 156479, book: "Mark", verse: "6.05", unformatted: "因为他们都看见了他，就惊慌不安。耶稣立刻与他们讲话，对他们说：“鼓起勇气吧！是我，不要怕。”", version_id: 6},
  {id: 156480, book: "Mark", verse: "6.051", unformatted: "接着，耶稣上了船和他们在一起。这时候风就停了。门徒们心里感到极其惊讶，", version_id: 6},
  {id: 156481, book: "Mark", verse: "6.052", unformatted: "因为他们不领悟分饼的事，他们还是硬着心。", version_id: 6},
  {id: 156482, book: "Mark", verse: "6.053", unformatted: "在革尼撒勒治病\n他们渡过去，到革尼撒勒上岸，把船拴好。", version_id: 6},
  {id: 156483, book: "Mark", verse: "6.054", unformatted: "一下船，众人就认出了耶稣。", version_id: 6},
  {id: 156484, book: "Mark", verse: "6.055", unformatted: "人们跑遍那整个地区，听到耶稣在哪里，就把有病的人用垫子抬到哪里。", version_id: 6},
  {id: 156485, book: "Mark", verse: "6.056", unformatted: "无论耶稣进到哪里，或村庄、或城镇、或乡下，人们就把病人放在街市上，恳求耶稣只要让他们摸一下他的衣服穗子。凡是摸着的人，都得了救治。", version_id: 6},
  {id: 156486, book: "Mark", verse: "7.001", unformatted: "传统与诫命\n有些法利赛人和一些从耶路撒冷来的经文士聚集到耶稣那里。", version_id: 6},
  {id: 156487, book: "Mark", verse: "7.002", unformatted: "他们看见耶稣的一些门徒用不洁净的手，就是没有洗过的手吃饭。", version_id: 6},
  {id: 156488, book: "Mark", verse: "7.003", unformatted: "原来，法利赛人和所有的犹太人都拘守古人的传统：如果不按规矩洗手就不吃饭；", version_id: 6},
  {id: 156489, book: "Mark", verse: "7.004", unformatted: "他们从街市上回来，如果不行洗净礼，也就不吃饭；他们还有很多其他的传统要拘守，例如洗杯子、瓶子、铜器，甚至床等。", version_id: 6},
  {id: 156490, book: "Mark", verse: "7.005", unformatted: "于是，法利赛人和经文士就问耶稣：“你的门徒们为什么不照着古人的传统行事，用不洁净的手吃饭呢？”", version_id: 6},
  {id: 156491, book: "Mark", verse: "7.006", unformatted: "耶稣对他们说：“以赛亚指着你们这些伪善的人所说的预言是对的，正如经上所记：\n‘这子民用嘴唇尊重我，\n他们的心却远离我。", version_id: 6},
  {id: 156492, book: "Mark", verse: "7.007", unformatted: "他们敬拜我也是徒然的，\n因为他们把人的规条当做教义教导人。’", version_id: 6},
  {id: 156493, book: "Mark", verse: "7.008", unformatted: "你们离弃了神的命令，而拘守人的传统。”", version_id: 6},
  {id: 156494, book: "Mark", verse: "7.009", unformatted: "耶稣又对他们说：“你们为了要守住你们的传统，竟然弃绝了神的命令！", version_id: 6},
  {id: 156495, book: "Mark", verse: "7.01", unformatted: "摩西吩咐过\n‘你要孝敬你的父母’，又说\n‘咒骂父亲或母亲的人，必须处死。’", version_id: 6},
  {id: 156496, book: "Mark", verse: "7.011", unformatted: "你们却说‘如果一个人对父亲或母亲说：我本该给你的，已经做了“各尔板”——这意思是“圣殿奉献”，", version_id: 6},
  {id: 156497, book: "Mark", verse: "7.012", unformatted: "就准许这个人可以不再为父母做什么了。’", version_id: 6},
  {id: 156498, book: "Mark", verse: "7.013", unformatted: "这样，你们就藉着所继承的传统，废弃了神的话语。你们还做很多类似这样的事。”", version_id: 6},
  {id: 156499, book: "Mark", verse: "7.014", unformatted: "耶稣又召来众人，对他们说：“你们每个人都当听我说，也当领悟：", version_id: 6},
  {id: 156500, book: "Mark", verse: "7.015", unformatted: "从人外面进到他里面的，没有一样能使人污秽；相反，从人里面出来的，才会使人污秽。", version_id: 6},
  {id: 156501, book: "Mark", verse: "7.016", unformatted: "凡是有耳可听的，就应当听！”", version_id: 6},
  {id: 156502, book: "Mark", verse: "7.017", unformatted: "耶稣离开人群，一进了房子，他的门徒们就问他这比喻的意思。", version_id: 6},
  {id: 156503, book: "Mark", verse: "7.018", unformatted: "耶稣说：“你们也是这样无知吗？你们难道不明白一切从外面进到人里面的，不能使人污秽吗？", version_id: 6},
  {id: 156504, book: "Mark", verse: "7.019", unformatted: "因为这些东西不是进到人的心里，而是进入肚子，然后排到厕所里去。”耶稣这样就使一切食物都洁净。", version_id: 6},
  {id: 156505, book: "Mark", verse: "7.02", unformatted: "他接着说：“从人里面出来的，那才使人污秽。", version_id: 6},
  {id: 156506, book: "Mark", verse: "7.021", unformatted: "因为从里面，就是从人心里发出种种恶念：淫乱、偷窃、杀人、", version_id: 6},
  {id: 156507, book: "Mark", verse: "7.022", unformatted: "通奸、贪心、恶意、欺诈、好色、嫉妒、毁谤、骄傲、愚妄，", version_id: 6},
  {id: 156508, book: "Mark", verse: "7.023", unformatted: "这一切邪恶都是从人里面出来的，而且使人污秽。”", version_id: 6},
  {id: 156509, book: "Mark", verse: "7.024", unformatted: "外邦妇人的信心\n耶稣起身离开那地方到提尔和西顿地区。他进了一户人家，本来不愿意任何人知道，却还是不能避开众人。", version_id: 6},
  {id: 156510, book: "Mark", verse: "7.025", unformatted: "有一个妇人，她的女儿有污灵附着。这妇人一听说耶稣的事，就赶来俯伏在他的脚前。", version_id: 6},
  {id: 156511, book: "Mark", verse: "7.026", unformatted: "这妇人是希腊人，出生在叙利亚的腓尼基。她请求耶稣把鬼魔从她女儿身上赶出去。", version_id: 6},
  {id: 156512, book: "Mark", verse: "7.027", unformatted: "耶稣对她说：“应该先让儿女吃饱，因为拿儿女的饼扔给小狗，是不合宜的。”", version_id: 6},
  {id: 156513, book: "Mark", verse: "7.028", unformatted: "妇人回答说：“主啊！连桌子底下的小狗，也吃得到孩子们的碎渣！”", version_id: 6},
  {id: 156514, book: "Mark", verse: "7.029", unformatted: "耶稣对她说：“凭你这句话，你可以回去了。鬼魔已经离开了你的女儿。”", version_id: 6},
  {id: 156515, book: "Mark", verse: "7.03", unformatted: "妇人回到家，发现孩子躺在床上，鬼魔已经离去了。", version_id: 6},
  {id: 156516, book: "Mark", verse: "7.031", unformatted: "耶稣做事都美好\n耶稣离开提尔地区，经过西顿，从德卡波利斯境内又来到加利利湖边。", version_id: 6},
  {id: 156517, book: "Mark", verse: "7.032", unformatted: "有人把一个又聋又哑的人带到耶稣面前，恳求耶稣按手在他身上。", version_id: 6},
  {id: 156518, book: "Mark", verse: "7.033", unformatted: "耶稣把他从人群中单独带到一边去，用指头伸进他的耳朵，吐唾沫来抹他的舌头，", version_id: 6},
  {id: 156519, book: "Mark", verse: "7.034", unformatted: "然后望天嘘了一口气，对他说：“以法达！”——这意思是“开了吧”。", version_id: 6},
  {id: 156520, book: "Mark", verse: "7.035", unformatted: "他的耳朵立刻开了，舌结也解了，说话也清楚了。", version_id: 6},
  {id: 156521, book: "Mark", verse: "7.036", unformatted: "耶稣吩咐他们不要告诉任何人。但是，他越是吩咐，他们越是大大传扬。", version_id: 6},
  {id: 156522, book: "Mark", verse: "7.037", unformatted: "人们极其惊讶，说：“他所做的一切都好！他甚至使聋子听见，使哑巴说话。”", version_id: 6},
  {id: 156523, book: "Mark", verse: "8.001", unformatted: "使四千人吃饱\n在那些日子里，又一次，有一大群人，他们没有什么东西吃。耶稣召来门徒们，对他们说：", version_id: 6},
  {id: 156524, book: "Mark", verse: "8.002", unformatted: "“我怜悯这群人，因为他们与我在一起已经三天了，也没有什么东西吃。", version_id: 6},
  {id: 156525, book: "Mark", verse: "8.003", unformatted: "如果我让他们饿着肚子回家，他们会在路上晕倒，因为有些人从远处而来。”", version_id: 6},
  {id: 156526, book: "Mark", verse: "8.004", unformatted: "他的门徒们回答：“在这旷野，从哪里能得食物让这些人吃饱呢？”", version_id: 6},
  {id: 156527, book: "Mark", verse: "8.005", unformatted: "耶稣问他们：“你们有多少饼呢？”\n他们回答说：“七个。”", version_id: 6},
  {id: 156528, book: "Mark", verse: "8.006", unformatted: "耶稣就吩咐众人坐在地上，于是拿起这七个饼，祝谢以后，掰开，不断递给他的门徒们，让他们分发。门徒们就分给众人；", version_id: 6},
  {id: 156529, book: "Mark", verse: "8.007", unformatted: "他们还有几条小鱼，耶稣祝福了，就吩咐把它们也分给众人。", version_id: 6},
  {id: 156530, book: "Mark", verse: "8.008", unformatted: "他们吃了，并且吃饱了。然后，他们把剩下的碎块收拾起来，装满了七个筐子。", version_id: 6},
  {id: 156531, book: "Mark", verse: "8.009", unformatted: "当时约有四千人。耶稣遣散了他们，", version_id: 6},
  {id: 156532, book: "Mark", verse: "8.01", unformatted: "随即与他的门徒们一起上船，来到达玛努达地区。", version_id: 6},
  {id: 156533, book: "Mark", verse: "8.011", unformatted: "法利赛人与希律的酵\n有些法利赛人出来，开始和耶稣辩论。他们试探耶稣，向他要求一个从天上来的神迹。", version_id: 6},
  {id: 156534, book: "Mark", verse: "8.012", unformatted: "耶稣灵里深深地叹息，就说：“这世代为什么在寻求神迹呢？我确实地告诉你们：绝不会有神迹赐给这世代。”", version_id: 6},
  {id: 156535, book: "Mark", verse: "8.013", unformatted: "于是他就离开他们，又上船往对岸去。", version_id: 6},
  {id: 156536, book: "Mark", verse: "8.014", unformatted: "门徒们忘了带饼，船上除了一个饼，再没有别的了。", version_id: 6},
  {id: 156537, book: "Mark", verse: "8.015", unformatted: "耶稣吩咐他们说：“你们要注意，要当心法利赛人的酵母和希律的酵母。”", version_id: 6},
  {id: 156538, book: "Mark", verse: "8.016", unformatted: "门徒们因为没有饼，就彼此讨论。", version_id: 6},
  {id: 156539, book: "Mark", verse: "8.017", unformatted: "耶稣知道了，就问他们：“你们为什么讨论没有饼的事呢？你们还不明白，还不领悟，还是硬着心吗？", version_id: 6},
  {id: 156540, book: "Mark", verse: "8.018", unformatted: "你们有眼睛却看不见，有耳朵却听不见吗？难道不记得吗？", version_id: 6},
  {id: 156541, book: "Mark", verse: "8.019", unformatted: "当我掰开那五个饼给五千人的时候，你们收拾的碎块装满了几个篮子呢？”\n门徒们回答说：“十二个。”", version_id: 6},
  {id: 156542, book: "Mark", verse: "8.02", unformatted: "“那七个饼给四千人的时候，你们收拾的碎块装满了几个筐子呢？”\n门徒们回答说：“七个。”", version_id: 6},
  {id: 156543, book: "Mark", verse: "8.021", unformatted: "耶稣对他们说：“你们还不领悟吗？”", version_id: 6},
  {id: 156544, book: "Mark", verse: "8.022", unformatted: "开盲人的眼睛\n他们来到伯赛达，有人带来一个瞎眼的，恳求耶稣摸他。", version_id: 6},
  {id: 156545, book: "Mark", verse: "8.023", unformatted: "耶稣牵着瞎眼之人的手，领他到村子外面，吐唾沫在他的眼睛上，按手在他身上，问他：“你是否看见什么？”", version_id: 6},
  {id: 156546, book: "Mark", verse: "8.024", unformatted: "他抬头一看，说：“我看见一些人！看起来好像树在走路！”", version_id: 6},
  {id: 156547, book: "Mark", verse: "8.025", unformatted: "然后耶稣又按手在他的眼睛上，他就看得清清楚楚了。他痊愈了，就清楚地看见了一切。", version_id: 6},
  {id: 156548, book: "Mark", verse: "8.026", unformatted: "耶稣叫他回家，并说：“不要进这村子。”", version_id: 6},
  {id: 156549, book: "Mark", verse: "8.027", unformatted: "彼得认耶稣为基督\n耶稣和他的门徒们往凯撒里亚菲利彼的那些村庄去。在路上，耶稣问他的门徒们，说：“人们说我是谁？”", version_id: 6},
  {id: 156550, book: "Mark", verse: "8.028", unformatted: "他们回答他，说：“是施洗者约翰，有的说是以利亚，也有的说是先知中的一位。”", version_id: 6},
  {id: 156551, book: "Mark", verse: "8.029", unformatted: "耶稣又问他们：“那么你们呢？你们说我是谁？”\n彼得回答说：“你是基督！”", version_id: 6},
  {id: 156552, book: "Mark", verse: "8.03", unformatted: "耶稣就告诫他们不要把有关他的事告诉任何人。", version_id: 6},
  {id: 156553, book: "Mark", verse: "8.031", unformatted: "预言受难与复活\n耶稣开始教导他们说：“人子必须受很多苦害，被长老们、祭司长们和经文士们弃绝，并且被杀，然后在第三天要复活。”", version_id: 6},
  {id: 156554, book: "Mark", verse: "8.032", unformatted: "耶稣公开地讲这些事，彼得把耶稣拉到一边，开始劝阻他。", version_id: 6},
  {id: 156555, book: "Mark", verse: "8.033", unformatted: "但耶稣转过身，看了看他的门徒们，就斥责彼得，说：“撒旦，退到我后面去！因为你不是思想神的事，而是思想人的事。”", version_id: 6},
  {id: 156556, book: "Mark", verse: "8.034", unformatted: "背起你的十字架\n于是，耶稣把众人和他的门徒们一起召来，对他们说：“如果有人想要跟从我，他就当舍弃自己，背起自己的十字架，然后跟从我。", version_id: 6},
  {id: 156557, book: "Mark", verse: "8.035", unformatted: "因为凡想要保全自己生命的，将失去生命；凡为我和福音的缘故失去自己生命的，将保全生命。", version_id: 6},
  {id: 156558, book: "Mark", verse: "8.036", unformatted: "一个人就是赚得了全世界，却赔上了自己的生命，到底有什么益处呢？", version_id: 6},
  {id: 156559, book: "Mark", verse: "8.037", unformatted: "人到底能拿什么来换回自己的生命呢？", version_id: 6},
  {id: 156560, book: "Mark", verse: "8.038", unformatted: "在这淫乱、罪恶的世代，一个人如果以我和我的话为耻，当人子在他父的荣耀中，与圣天使们一起来临的时候，也要以这个人为耻。”", version_id: 6},
  {id: 156561, book: "Mark", verse: "9.001", unformatted: "耶稣又对他们说：“我确实地告诉你们：站在这里的有些人，在还没有尝到死的滋味之前，必定见到神的国带着权能来临。”", version_id: 6},
  {id: 156562, book: "Mark", verse: "9.002", unformatted: "登山变相\n过了六天，耶稣只带着彼得、雅各、约翰，领他们悄悄地上了一座高山。在他们面前，耶稣变了形像。", version_id: 6},
  {id: 156563, book: "Mark", verse: "9.003", unformatted: "他的衣服变得光亮耀眼，极其洁白，地上漂布的人无法漂得那样白。", version_id: 6},
  {id: 156564, book: "Mark", verse: "9.004", unformatted: "这时候，以利亚与摩西一起向他们显现，并且和耶稣谈话。", version_id: 6},
  {id: 156565, book: "Mark", verse: "9.005", unformatted: "彼得对耶稣说：“拉比，我们在这里真好！让我们来搭三座帐幕，一座为你，一座为摩西，一座为以利亚。”", version_id: 6},
  {id: 156566, book: "Mark", verse: "9.006", unformatted: "其实彼得不知道说什么才好，因为他们都非常害怕。", version_id: 6},
  {id: 156567, book: "Mark", verse: "9.007", unformatted: "这时候，有一朵云彩出现笼罩他们，并且有声音从云中传来：\n“这是我的爱子，\n你们当听从他！”", version_id: 6},
  {id: 156568, book: "Mark", verse: "9.008", unformatted: "于是门徒们环视周围，忽然间，再也看不到别的人，只有耶稣与他们在一起。", version_id: 6},
  {id: 156569, book: "Mark", verse: "9.009", unformatted: "他们下山的时候，耶稣吩咐他们，在人子从死人中复活之前，不要把所看到的告诉任何人。", version_id: 6},
  {id: 156570, book: "Mark", verse: "9.01", unformatted: "他们牢记这话，互相谈论“从死人中复活”是什么意思。", version_id: 6},
  {id: 156571, book: "Mark", verse: "9.011", unformatted: "他们问耶稣，说：“经文士们为什么说以利亚必须先来呢？”", version_id: 6},
  {id: 156572, book: "Mark", verse: "9.012", unformatted: "耶稣告诉他们：“以利亚确实要先来恢复万事。至于人子，经上怎么还记载说，他必然受很多苦害，并且被人拒绝呢？", version_id: 6},
  {id: 156573, book: "Mark", verse: "9.013", unformatted: "不过我告诉你们：其实以利亚已经来了，人们却任意对待他，正如经上指着他所写的。”", version_id: 6},
  {id: 156574, book: "Mark", verse: "9.014", unformatted: "信心的力量\n他们回到其他的门徒那里，看见一大群人围着他们，而且有几个经文士正在和他们辩论。", version_id: 6},
  {id: 156575, book: "Mark", verse: "9.015", unformatted: "众人一见耶稣，都大吃一惊，就跑过去欢迎他。", version_id: 6},
  {id: 156576, book: "Mark", verse: "9.016", unformatted: "耶稣问：“你们和他们在辩论什么？”", version_id: 6},
  {id: 156577, book: "Mark", verse: "9.017", unformatted: "人群中有一个人回答：“老师，我带了我的儿子到你这里。他有一个哑巴的邪灵附身。", version_id: 6},
  {id: 156578, book: "Mark", verse: "9.018", unformatted: "无论在哪里，只要邪灵抓住他，把他摔倒在地，他就口吐白沫，紧咬牙关，全身僵硬。我告诉你的门徒们，要他们驱赶那邪灵，可是他们不能。”", version_id: 6},
  {id: 156579, book: "Mark", verse: "9.019", unformatted: "耶稣回答他们，说：“唉，这个不信的世代！我还要和你们在一起多久呢？我还要容忍你们多久呢？把孩子带到我这里来吧！”", version_id: 6},
  {id: 156580, book: "Mark", verse: "9.02", unformatted: "他们就把他带到耶稣面前来。邪灵一见耶稣，立刻使孩子剧烈地抽疯，倒在地上打滚，口吐白沫。", version_id: 6},
  {id: 156581, book: "Mark", verse: "9.021", unformatted: "耶稣问孩子的父亲：“这事发生在他身上有多久了？”\n他回答说：“从小就这样。", version_id: 6},
  {id: 156582, book: "Mark", verse: "9.022", unformatted: "邪灵经常把他扔进火里或水里，要害死他。但你如果能做什么，就求你怜悯我们，帮助我们！”", version_id: 6},
  {id: 156583, book: "Mark", verse: "9.023", unformatted: "耶稣对他说：“‘如果你能’？——对信的人，一切都能。”", version_id: 6},
  {id: 156584, book: "Mark", verse: "9.024", unformatted: "那孩子的父亲立刻喊着说：“我信！但我的不信之处，求你帮助！”", version_id: 6},
  {id: 156585, book: "Mark", verse: "9.025", unformatted: "耶稣看见众人都跑上来，就斥责那污灵，说：“你这聋哑的邪灵，我命令你：从他里面出来，不可再进去！”", version_id: 6},
  {id: 156586, book: "Mark", verse: "9.026", unformatted: "邪灵喊叫一声，使孩子剧烈地抽疯，就出来了。孩子变得像死人那样，所以许多人说：“他死了。”", version_id: 6},
  {id: 156587, book: "Mark", verse: "9.027", unformatted: "但是，耶稣握着他的手，扶他起来，他就站起来了。", version_id: 6},
  {id: 156588, book: "Mark", verse: "9.028", unformatted: "耶稣进了房子，他的门徒们就悄悄地问他：“我们为什么不能赶出那邪灵呢？”", version_id: 6},
  {id: 156589, book: "Mark", verse: "9.029", unformatted: "耶稣对他们说：“对这一类的邪灵，如果不藉着祷告和禁食，就不能赶出去。”", version_id: 6},
  {id: 156590, book: "Mark", verse: "9.03", unformatted: "再次预言受难\n他们离开那里，经过加利利，耶稣不愿意让人知道。", version_id: 6},
  {id: 156591, book: "Mark", verse: "9.031", unformatted: "那时，他教导自己的门徒们，对他们说：“人子被交在人的手中，他们要杀害他。然后被杀之后，在第三天他要复活。”", version_id: 6},
  {id: 156592, book: "Mark", verse: "9.032", unformatted: "可是门徒们不明白这话，又不敢问他。", version_id: 6},
  {id: 156593, book: "Mark", verse: "9.033", unformatted: "谁是最大的\n他们来到了迦百农，进了屋子，耶稣问门徒们：“你们在路上讨论什么呢？”", version_id: 6},
  {id: 156594, book: "Mark", verse: "9.034", unformatted: "门徒们却不做声，因为他们在路上彼此争论谁更大。", version_id: 6},
  {id: 156595, book: "Mark", verse: "9.035", unformatted: "耶稣坐下，叫十二使徒来，对他们说：“如果有人想做首先的，他就该做众人当中最后的，做大家的仆人。”", version_id: 6},
  {id: 156596, book: "Mark", verse: "9.036", unformatted: "耶稣找来一个小孩子，让他站在他们当中，又把他搂在怀中，对门徒们说：", version_id: 6},
  {id: 156597, book: "Mark", verse: "9.037", unformatted: "“无论谁奉我的名接受这样一个小孩子，就是接受我；接受我的，不是接受我，而是接受差派我来的那一位。”", version_id: 6},
  {id: 156598, book: "Mark", verse: "9.038", unformatted: "奉耶稣的名\n约翰说：“老师，我们看见一个人奉你的名驱赶鬼魔，我们就去阻止他，因为他不是跟从我们的。”", version_id: 6},
  {id: 156599, book: "Mark", verse: "9.039", unformatted: "耶稣说：“不要阻止他。因为没有人奉我的名行了神迹以后，会立即诽谤我。", version_id: 6},
  {id: 156600, book: "Mark", verse: "9.04", unformatted: "不反对我们的，就是支持我们的。", version_id: 6},
  {id: 156601, book: "Mark", verse: "9.041", unformatted: "如果有人因为你们属于基督，就奉我的名给你们一杯水喝，我确实地告诉你们：他绝不会失去他的报偿。", version_id: 6},
  {id: 156602, book: "Mark", verse: "9.042", unformatted: "耶稣的警告\n“无论谁使信我的这些卑微人中的一个绊倒，对那人来说，脖子上拴着大磨石，被丢进海里反而更好。", version_id: 6},
  {id: 156603, book: "Mark", verse: "9.043", unformatted: "如果你的一只手使你绊倒，就把它砍掉！对你来说，缺一只手进入永生，要比双手齐全下地狱，到那永不熄灭的火里好多了。", version_id: 6},
  {id: 156604, book: "Mark", verse: "9.044", unformatted: "在那里\n‘虫是不死的，\n火是不灭的。’", version_id: 6},
  {id: 156605, book: "Mark", verse: "9.045", unformatted: "如果你的一只脚使你绊倒，就把它砍掉！对你来说，缺一只脚进入永生，要比双脚齐全被丢进地狱，到那永不熄灭的火里好多了。", version_id: 6},
  {id: 156606, book: "Mark", verse: "9.046", unformatted: "在那里\n‘虫是不死的，\n火是不灭的。’", version_id: 6},
  {id: 156607, book: "Mark", verse: "9.047", unformatted: "如果你的一只眼使你绊倒，就把它挖出来！对你来说，缺一只眼进入神的国，要比双眼齐全被丢进地狱好多了。", version_id: 6},
  {id: 156608, book: "Mark", verse: "9.048", unformatted: "在那里\n‘虫是不死的，\n火是不灭的。’", version_id: 6},
  {id: 156609, book: "Mark", verse: "9.049", unformatted: "要知道，每个人都将被火腌成咸的。", version_id: 6},
  {id: 156610, book: "Mark", verse: "9.05", unformatted: "盐是好的，但如果那盐也不咸了，还能用什么来调味呢？你们里面要有盐，要彼此和睦。”", version_id: 6},
  {id: 156611, book: "Mark", verse: "10.001", unformatted: "论离婚\n后来，耶稣从那里起身，来到犹太地区和约旦河对岸。人群又聚集到他那里，他又照常教导他们。", version_id: 6},
  {id: 156612, book: "Mark", verse: "10.002", unformatted: "有些法利赛人前来试探耶稣，问他：“人是否可以休妻呢？”", version_id: 6},
  {id: 156613, book: "Mark", verse: "10.003", unformatted: "耶稣回答他们，说：“摩西是怎么吩咐你们的？”", version_id: 6},
  {id: 156614, book: "Mark", verse: "10.004", unformatted: "他们说：“摩西准许人写一份休书就可以休妻。”", version_id: 6},
  {id: 156615, book: "Mark", verse: "10.005", unformatted: "耶稣对他们说：“摩西因着你们的心里刚硬，才给你们定下这条诫命。", version_id: 6},
  {id: 156616, book: "Mark", verse: "10.006", unformatted: "从创世之初，‘神把人造成男的和女的’，", version_id: 6},
  {id: 156617, book: "Mark", verse: "10.007", unformatted: "‘为这缘故，\n人将离开他的父母，\n与妻子结合，", version_id: 6},
  {id: 156618, book: "Mark", verse: "10.008", unformatted: "两个人就成为一体。’\n这样，夫妻不再是两个人，而是一体了。", version_id: 6},
  {id: 156619, book: "Mark", verse: "10.009", unformatted: "因此，神所配合的，人不可分开。”", version_id: 6},
  {id: 156620, book: "Mark", verse: "10.01", unformatted: "进了屋子，门徒们又向耶稣问起这件事。", version_id: 6},
  {id: 156621, book: "Mark", verse: "10.011", unformatted: "耶稣就说：“如果有人休妻，另娶别人，就是犯通奸罪触犯妻子。", version_id: 6},
  {id: 156622, book: "Mark", verse: "10.012", unformatted: "女人如果离弃丈夫，另嫁别人，也是犯通奸罪。”", version_id: 6},
  {id: 156623, book: "Mark", verse: "10.013", unformatted: "祝福小孩子\n有些人把小孩子带到耶稣那里，好让耶稣摸他们一下。门徒们却责备那些人。", version_id: 6},
  {id: 156624, book: "Mark", verse: "10.014", unformatted: "耶稣看见了就很不满，对门徒们说：“让小孩子到我这里来，不要阻止他们，因为神的国正属于这样的人。", version_id: 6},
  {id: 156625, book: "Mark", verse: "10.015", unformatted: "我确实地告诉你们：无论谁不像小孩子一样接受神的国，就绝不能进去。”", version_id: 6},
  {id: 156626, book: "Mark", verse: "10.016", unformatted: "于是，他把孩子们搂在怀中，按手在他们身上，祝福了他们。", version_id: 6},
  {id: 156627, book: "Mark", verse: "10.017", unformatted: "富有的青年官员\n耶稣刚出门上路，有一个人跑过去向他跪下，问他：“良善的老师，我该做什么才能继承永恒的生命呢？”", version_id: 6},
  {id: 156628, book: "Mark", verse: "10.018", unformatted: "耶稣对他说：“你为什么称我是良善的呢？除了神一位之外，没有一个是良善的。", version_id: 6},
  {id: 156629, book: "Mark", verse: "10.019", unformatted: "你是知道这些诫命的：\n‘不可杀人、\n不可通奸、\n不可偷窃、\n不可做伪证、\n不可亏负人、\n要孝敬父母。’”", version_id: 6},
  {id: 156630, book: "Mark", verse: "10.02", unformatted: "那个人对耶稣说：“老师，这一切我从年少就遵守了。”", version_id: 6},
  {id: 156631, book: "Mark", verse: "10.021", unformatted: "耶稣注视着他，就爱他，对他说：“你还缺少一件：去卖掉你所拥有的，分给穷人，这样你将有财宝在天上；然后你来跟从我。”", version_id: 6},
  {id: 156632, book: "Mark", verse: "10.022", unformatted: "那个人因这话，脸色就变得阴沉，忧忧愁愁地走开了，因为他有很多财产。", version_id: 6},
  {id: 156633, book: "Mark", verse: "10.023", unformatted: "财富与天国\n耶稣环视周围，对他的门徒们说：“有钱财的人进入神的国，是多么困难！”", version_id: 6},
  {id: 156634, book: "Mark", verse: "10.024", unformatted: "门徒们因这些话，就惊讶不已。耶稣又对他们说：“孩子们，进入神的国是多么困难！", version_id: 6},
  {id: 156635, book: "Mark", verse: "10.025", unformatted: "骆驼穿过针眼，比富有的人进入神的国更容易呢！”", version_id: 6},
  {id: 156636, book: "Mark", verse: "10.026", unformatted: "门徒们越发惊讶，彼此说：“那么，谁能得救呢？”", version_id: 6},
  {id: 156637, book: "Mark", verse: "10.027", unformatted: "耶稣注视着他们，说：“在人不可能，但在神却不然；因为在神，凡事都可能。”", version_id: 6},
  {id: 156638, book: "Mark", verse: "10.028", unformatted: "彼得就对耶稣说：“看，我们舍弃一切跟从了你！”", version_id: 6},
  {id: 156639, book: "Mark", verse: "10.029", unformatted: "耶稣说：“我确实地告诉你们：无论谁为我的缘故、为福音的缘故而舍弃房屋，或兄弟，或姐妹，或母亲，或父亲，或儿女，或田产，", version_id: 6},
  {id: 156640, book: "Mark", verse: "10.03", unformatted: "都必定在今世得到百倍，就是房屋、兄弟、姐妹、母亲、儿女和田产，也会有逼迫；但在来世得到永恒的生命。", version_id: 6},
  {id: 156641, book: "Mark", verse: "10.031", unformatted: "不过许多在前的，将要在后；许多在后的，将要在前。”", version_id: 6},
  {id: 156642, book: "Mark", verse: "10.032", unformatted: "第三次预言受难\n在上耶路撒冷的路途中，耶稣一直走在前头，门徒们惊讶不已，跟着的人也惧怕了。耶稣又把十二使徒带到一边，开始告诉他们将要发生在他身上的事，", version_id: 6},
  {id: 156643, book: "Mark", verse: "10.033", unformatted: "说：“看，我们现在上耶路撒冷去，人子将被出卖给祭司长们和经文士们。他们要定他死罪，并且要把他交给外邦人。", version_id: 6},
  {id: 156644, book: "Mark", verse: "10.034", unformatted: "他们要戏弄他、向他吐唾沫、鞭打他，并杀害他；然后在第三天，他要复活。”", version_id: 6},
  {id: 156645, book: "Mark", verse: "10.035", unformatted: "受苦与服事\n这时候，西庇太的儿子雅各和约翰来到耶稣面前，说：“老师，如果我们向你求什么，希望你为我们成全。”", version_id: 6},
  {id: 156646, book: "Mark", verse: "10.036", unformatted: "耶稣问他们：“要我为你们做什么呢？”", version_id: 6},
  {id: 156647, book: "Mark", verse: "10.037", unformatted: "他们说：“请准许我们在你的荣耀里，一个坐在你的右边，一个坐在你的左边。”", version_id: 6},
  {id: 156648, book: "Mark", verse: "10.038", unformatted: "耶稣就说：“你们不知道自己在求什么。我所喝的杯，你们能喝吗？我所受的洗礼，你们能受吗？”", version_id: 6},
  {id: 156649, book: "Mark", verse: "10.039", unformatted: "他们回答说：“我们能。”\n耶稣就说：“我所喝的杯你们将要喝，我所受的洗礼你们将要受。", version_id: 6},
  {id: 156650, book: "Mark", verse: "10.04", unformatted: "但是坐在我的右边或左边，不是我所赐的，而是已经预备给谁，就赐给谁。”", version_id: 6},
  {id: 156651, book: "Mark", verse: "10.041", unformatted: "其他十个使徒听了，就开始对雅各和约翰很不满。", version_id: 6},
  {id: 156652, book: "Mark", verse: "10.042", unformatted: "于是耶稣把他们召来，对他们说：“你们知道，外邦人有所谓为首的压制他们，也有为大的管辖他们。", version_id: 6},
  {id: 156653, book: "Mark", verse: "10.043", unformatted: "但你们当中却不是这样；相反，无论谁想在你们当中为大，谁就该做你们的仆人；", version_id: 6},
  {id: 156654, book: "Mark", verse: "10.044", unformatted: "无论谁想在你们当中为首，谁就该做大家的奴仆。", version_id: 6},
  {id: 156655, book: "Mark", verse: "10.045", unformatted: "要知道，就是人子来，也不是为了受人的服事，而是为了服事人，并且献上自己的生命，替许多人做救赎的代价。”", version_id: 6},
  {id: 156656, book: "Mark", verse: "10.046", unformatted: "治愈耶利哥的盲人\n他们来到了耶利哥。当耶稣与他的门徒们和一大群人要离开那里的时候，有一个瞎眼的乞丐坐在路边，他是提迈的儿子巴提迈。", version_id: 6},
  {id: 156657, book: "Mark", verse: "10.047", unformatted: "他一听说是拿撒勒人耶稣，就开始呼叫说：“大卫的后裔——耶稣，可怜我吧！”", version_id: 6},
  {id: 156658, book: "Mark", verse: "10.048", unformatted: "许多人责备他，叫他不要做声。可是他更加大声呼叫：“大卫的后裔，可怜我吧！”", version_id: 6},
  {id: 156659, book: "Mark", verse: "10.049", unformatted: "耶稣就停住，说：“叫他过来吧。”\n他们就叫那瞎眼的说：“鼓起勇气吧！起来，他叫你呢。”", version_id: 6},
  {id: 156660, book: "Mark", verse: "10.05", unformatted: "他就丢下外衣跳起来，来到耶稣面前。", version_id: 6},
  {id: 156661, book: "Mark", verse: "10.051", unformatted: "耶稣问他说：“你要我为你做什么呢？”\n那瞎眼的说：“拉波尼，我要重见光明！”", version_id: 6},
  {id: 156662, book: "Mark", verse: "10.052", unformatted: "耶稣对他说：“你去吧！你的信救了你。”他立刻重见光明，于是一路跟随了耶稣。", version_id: 6},
  {id: 156663, book: "Mark", verse: "11.001", unformatted: "光荣进圣城\n当耶稣和门徒们临近耶路撒冷，到橄榄山附近的伯法其和伯大尼的时候，耶稣派了他的两个门徒，", version_id: 6},
  {id: 156664, book: "Mark", verse: "11.002", unformatted: "对他们说：“你们到对面的村子里去。你们一进村，就会发现一头驴驹拴在那里，是从来没有人骑过的。你们把它解开牵过来。", version_id: 6},
  {id: 156665, book: "Mark", verse: "11.003", unformatted: "如果有人问你们为什么这么做，你们就说‘主需要它，会很快把它送回这里来的。’”", version_id: 6},
  {id: 156666, book: "Mark", verse: "11.004", unformatted: "两个门徒去了，果然发现一头驴驹拴在岔路口一个门外，他们就解开驴驹。", version_id: 6},
  {id: 156667, book: "Mark", verse: "11.005", unformatted: "站在那里的一些人问：“你们解开驴驹做什么呢？”", version_id: 6},
  {id: 156668, book: "Mark", verse: "11.006", unformatted: "他们就照着耶稣所吩咐的回答，那些人就让他们牵走了。", version_id: 6},
  {id: 156669, book: "Mark", verse: "11.007", unformatted: "他们牵着驴驹到耶稣那里，把自己的外衣垫在驴驹上，耶稣就骑了上去。", version_id: 6},
  {id: 156670, book: "Mark", verse: "11.008", unformatted: "许多人把自己的外衣铺在路上，也有人从田野里砍来树枝铺在路上。", version_id: 6},
  {id: 156671, book: "Mark", verse: "11.009", unformatted: "前行后随的人都呼喊：\n“和散那！\n奉主名而来的那一位——是蒙祝福的！", version_id: 6},
  {id: 156672, book: "Mark", verse: "11.01", unformatted: "那将要来的，我们先祖大卫的国度——是蒙祝福的！\n在至高之处——和散那！”", version_id: 6},
  {id: 156673, book: "Mark", verse: "11.011", unformatted: "耶稣进了耶路撒冷，来到圣殿，环视周围的一切。天已经晚了，他就与十二使徒一同离开，往伯大尼去了。", version_id: 6},
  {id: 156674, book: "Mark", verse: "11.012", unformatted: "不结果子的无花果树\n第二天，当他们离开伯大尼的时候，耶稣饿了。", version_id: 6},
  {id: 156675, book: "Mark", verse: "11.013", unformatted: "他远远地看见一棵长满叶子的无花果树，就上前去，要看看树上是否有什么。他来到树下，除了叶子什么也找不到，因为不是结无花果的季节。", version_id: 6},
  {id: 156676, book: "Mark", verse: "11.014", unformatted: "于是他对那棵树说：“永远不再有任何人吃到你的果子了！”他的门徒都听见了。", version_id: 6},
  {id: 156677, book: "Mark", verse: "11.015", unformatted: "洁净圣殿\n他们来到耶路撒冷。耶稣进了圣殿，开始把那些在圣殿里做买卖的人赶出去。他推翻了兑换银币之人的桌子和卖鸽子之人的凳子，", version_id: 6},
  {id: 156678, book: "Mark", verse: "11.016", unformatted: "也不准任何人搬运东西穿过圣殿。", version_id: 6},
  {id: 156679, book: "Mark", verse: "11.017", unformatted: "耶稣教导他们说：“经上不是记着‘我的殿将被称为万国祷告的殿’吗？而你们却使它成为贼窝了！”", version_id: 6},
  {id: 156680, book: "Mark", verse: "11.018", unformatted: "祭司长们和经文士们听了，就图谋怎样来除灭耶稣。原来他们怕耶稣，因为众人都对他的教导惊叹不已。", version_id: 6},
  {id: 156681, book: "Mark", verse: "11.019", unformatted: "到了傍晚的时候，耶稣和门徒们就到城外去。", version_id: 6},
  {id: 156682, book: "Mark", verse: "11.02", unformatted: "无花果树枯萎\n清晨，他们路过那棵无花果树的时候，看见它连根都枯萎了。", version_id: 6},
  {id: 156683, book: "Mark", verse: "11.021", unformatted: "彼得想起来就对耶稣说：“拉比，你看！你所诅咒的无花果树枯萎了！”", version_id: 6},
  {id: 156684, book: "Mark", verse: "11.022", unformatted: "耶稣对他们说：“你们当对神有信心。", version_id: 6},
  {id: 156685, book: "Mark", verse: "11.023", unformatted: "我确实地告诉你们：无论谁对这座山说‘愿你被挪开，被丢进海里去！’只要他心里不疑惑，相信自己所说的会成就，事情就将为他成就。", version_id: 6},
  {id: 156686, book: "Mark", verse: "11.024", unformatted: "所以，我告诉你们：凡是你们所祷告和祈求的，当相信已经得着了。这样，事情就将为你们成就。", version_id: 6},
  {id: 156687, book: "Mark", verse: "11.025", unformatted: "当你们站着祷告的时候，如果向什么人怀怨，就要饶恕他。这样，你们在天上的父也会饶恕你们的过犯。", version_id: 6},
  {id: 156688, book: "Mark", verse: "11.026", unformatted: "你们如果不饶恕别人，你们在天上的父也不会饶恕你们的过犯。”", version_id: 6},
  {id: 156689, book: "Mark", verse: "11.027", unformatted: "质疑耶稣的权柄\n他们又来到耶路撒冷。当耶稣在圣殿里行走的时候，祭司长们、经文士们和长老们来到他那里。", version_id: 6},
  {id: 156690, book: "Mark", verse: "11.028", unformatted: "他们问：“你凭什么权柄做这些事？谁给你这权柄，让你做这些事？”", version_id: 6},
  {id: 156691, book: "Mark", verse: "11.029", unformatted: "耶稣对他们说：“我要问你们一个问题，你们回答我，我就告诉你们我凭什么权柄做这些事。", version_id: 6},
  {id: 156692, book: "Mark", verse: "11.03", unformatted: "约翰的洗礼是来自天上，还是来自人间呢？你们回答我！”", version_id: 6},
  {id: 156693, book: "Mark", verse: "11.031", unformatted: "他们就彼此讨论，说：“如果我们说‘是来自天上’，他就会问‘那么你们为什么不相信他呢？’", version_id: 6},
  {id: 156694, book: "Mark", verse: "11.032", unformatted: "可是我们能说‘是来自人间’吗？”原来他们怕民众，因为大家都认为约翰的确是先知。", version_id: 6},
  {id: 156695, book: "Mark", verse: "11.033", unformatted: "于是他们回答耶稣，说：“我们不知道。”\n耶稣就对他们说：“我也不告诉你们我凭什么权柄做这些事。”", version_id: 6},
  {id: 156696, book: "Mark", verse: "12.001", unformatted: "葡萄园主的比喻\n耶稣开始用比喻对他们说：“有一个人栽种了一个葡萄园，四面围上篱笆，挖了一个榨酒池，盖了一座守望楼，然后把葡萄园租给一些农夫，就出外旅行。", version_id: 6},
  {id: 156697, book: "Mark", verse: "12.002", unformatted: "到了收获的季节，他就派了一个奴仆到那些农夫那里去，好从他们收取葡萄园的果子。", version_id: 6},
  {id: 156698, book: "Mark", verse: "12.003", unformatted: "但他们抓住那奴仆，打了他，叫他空手回去。", version_id: 6},
  {id: 156699, book: "Mark", verse: "12.004", unformatted: "园主又派另一个奴仆到他们那里去。那些农夫打伤了他的头，并且侮辱他。", version_id: 6},
  {id: 156700, book: "Mark", verse: "12.005", unformatted: "园主再派另一个去，他们把他杀了。另派了许多去，有的被打，有的被杀。", version_id: 6},
  {id: 156701, book: "Mark", verse: "12.006", unformatted: "“那园主还有一位——是他的爱子。最后他差派儿子到他们那里，说：‘他们一定会尊重我的儿子。’", version_id: 6},
  {id: 156702, book: "Mark", verse: "12.007", unformatted: "“可是那些农夫彼此说：‘这是继承人。来，我们把他杀了，那继业就归我们了。’", version_id: 6},
  {id: 156703, book: "Mark", verse: "12.008", unformatted: "于是他们抓住他，把他杀了，并扔出葡萄园外。", version_id: 6},
  {id: 156704, book: "Mark", verse: "12.009", unformatted: "“那么，葡萄园的主人会做什么呢？他会来除灭那些农夫，把葡萄园交给别人。", version_id: 6},
  {id: 156705, book: "Mark", verse: "12.01", unformatted: "你们难道也没有读过这段经文吗？\n‘工匠所弃绝的石头，\n它已经成了房角的头块石头；", version_id: 6},
  {id: 156706, book: "Mark", verse: "12.011", unformatted: "这是主所成就的，\n在我们眼中实在奇妙！’”", version_id: 6},
  {id: 156707, book: "Mark", verse: "12.012", unformatted: "祭司长们、律法师们和长老们知道这比喻是针对他们而说的，就想拘捕耶稣。可是他们怕民众，于是离开耶稣走了。", version_id: 6},
  {id: 156708, book: "Mark", verse: "12.013", unformatted: "神与凯撒\n后来，他们派了一些法利赛人和希律党的人到耶稣那里，要找话柄来陷害他。", version_id: 6},
  {id: 156709, book: "Mark", verse: "12.014", unformatted: "那些人来对耶稣说：“老师，我们知道你是真诚的。你不顾忌任何人，因为你不看人的情面，而是按真理教导神的道。请问，向凯撒纳税，可以不可以呢？我们该交还是不该交呢？”", version_id: 6},
  {id: 156710, book: "Mark", verse: "12.015", unformatted: "耶稣看出他们的假意，就对他们说：“你们为什么试探我呢？拿一个银币来给我看。”", version_id: 6},
  {id: 156711, book: "Mark", verse: "12.016", unformatted: "他们就拿来了。耶稣问他们：“这是谁的像和名号？”\n他们回答说：“是凯撒的。”", version_id: 6},
  {id: 156712, book: "Mark", verse: "12.017", unformatted: "耶稣对他们说：“凯撒的归给凯撒；神的归给神。”这使他们感到惊奇。", version_id: 6},
  {id: 156713, book: "Mark", verse: "12.018", unformatted: "撒都该人与复活\n接着，撒都该人来到耶稣那里，他们一向说没有复活的事。他们问耶稣，说：", version_id: 6},
  {id: 156714, book: "Mark", verse: "12.019", unformatted: "“老师，摩西为我们写道：如果一个人的兄弟死了，撇下妻子而没有留下孩子，他就应该娶这妇人，为兄弟留后裔。", version_id: 6},
  {id: 156715, book: "Mark", verse: "12.02", unformatted: "曾经有兄弟七人，第一个娶了妻子，死去了，没有留下后裔。", version_id: 6},
  {id: 156716, book: "Mark", verse: "12.021", unformatted: "第二个娶了这寡妇，也死了，没有留下后裔。第三个也一样。", version_id: 6},
  {id: 156717, book: "Mark", verse: "12.022", unformatted: "结果，兄弟七个都没有留下后裔。到了最后，这妇人也死了。", version_id: 6},
  {id: 156718, book: "Mark", verse: "12.023", unformatted: "既然这七个人都娶过她为妻，在复活的时候，当他们复活了，她将是谁的妻子呢？”", version_id: 6},
  {id: 156719, book: "Mark", verse: "12.024", unformatted: "耶稣说：“你们之所以错了，不就是因为你们不明白经上的话，也不明白神的大能吗？", version_id: 6},
  {id: 156720, book: "Mark", verse: "12.025", unformatted: "当人从死人中复活以后，他们既不娶也不嫁，而是像诸天之上的天使一样。", version_id: 6},
  {id: 156721, book: "Mark", verse: "12.026", unformatted: "关于死人复活的事，你们难道没有读过摩西书‘荆棘篇’上，神怎样对摩西说的吗？神说‘我是亚伯拉罕的神、以撒的神、雅各的神。’", version_id: 6},
  {id: 156722, book: "Mark", verse: "12.027", unformatted: "神不是死人的神，而是活人的神。你们完全错了！”", version_id: 6},
  {id: 156723, book: "Mark", verse: "12.028", unformatted: "最重要的诫命\n有一个经文士前来，听见他们辩论，见耶稣回答得好，就问耶稣：“所有的诫命中，哪一条是最重要的呢？”", version_id: 6},
  {id: 156724, book: "Mark", verse: "12.029", unformatted: "耶稣回答：“最重要的是：\n‘以色列啊，你当听好！主——我们的神，是独一的主。", version_id: 6},
  {id: 156725, book: "Mark", verse: "12.03", unformatted: "你要以全心、全灵、全意、全力爱主——你的神。’", version_id: 6},
  {id: 156726, book: "Mark", verse: "12.031", unformatted: "“其次就是‘要爱邻如己。’没有别的诫命比这两条更大的了。”", version_id: 6},
  {id: 156727, book: "Mark", verse: "12.032", unformatted: "那经文士就对耶稣说：“不错，老师，你真的讲了：神只有一位，除他以外没有别的神。", version_id: 6},
  {id: 156728, book: "Mark", verse: "12.033", unformatted: "以全心、全智、全力来爱神，并且要爱邻如己，这是比所有的燔祭和各种祭祀都重要的。”", version_id: 6},
  {id: 156729, book: "Mark", verse: "12.034", unformatted: "耶稣见他回答得有智慧，就对他说：“你离神的国不远了。”于是，没有人敢再质问耶稣了。", version_id: 6},
  {id: 156730, book: "Mark", verse: "12.035", unformatted: "有关基督的问题\n耶稣在圣殿里教导人的时候问：“经文士们怎么说基督是大卫的后裔呢？", version_id: 6},
  {id: 156731, book: "Mark", verse: "12.036", unformatted: "大卫藉着圣灵，自己说过：\n‘主对我主说：\n你坐在我的右边，\n等我把你的敌人放在你的脚下。’", version_id: 6},
  {id: 156732, book: "Mark", verse: "12.037", unformatted: "大卫自己称基督为‘主’，基督又怎么会是大卫的后裔呢？”那一大群人都乐意听耶稣讲说。", version_id: 6},
  {id: 156733, book: "Mark", verse: "12.038", unformatted: "要防备经文士\n耶稣在他的教导中说：“你们要当心经文士们。他们喜欢穿着长袍走来走去，喜欢街市上的致敬问候，", version_id: 6},
  {id: 156734, book: "Mark", verse: "12.039", unformatted: "又喜欢会堂里的首位、宴席中的上座。", version_id: 6},
  {id: 156735, book: "Mark", verse: "12.04", unformatted: "他们侵吞寡妇的家产，又假意做很长的祷告。这些人将受到更重的惩罚。”", version_id: 6},
  {id: 156736, book: "Mark", verse: "12.041", unformatted: "寡妇的奉献\n耶稣面对奉献箱坐着，看众人怎样把钱投入奉献箱里。许多富有的人投进了很多钱。", version_id: 6},
  {id: 156737, book: "Mark", verse: "12.042", unformatted: "有一个穷寡妇来，投进两个小钱，值一个铜币。", version_id: 6},
  {id: 156738, book: "Mark", verse: "12.043", unformatted: "耶稣把他的门徒们召来，对他们说：“我确实地告诉你们：这穷寡妇所投入奉献箱的，比所有的人投的更多。", version_id: 6},
  {id: 156739, book: "Mark", verse: "12.044", unformatted: "因为大家是从自己的富余中拿出来投进去；而这寡妇是从自己的缺乏中，把她所拥有的一切，就是她全部的养生费用都投进去了。”", version_id: 6},
  {id: 156740, book: "Mark", verse: "13.001", unformatted: "预言圣殿被毁\n耶稣走出圣殿的时候，他的一个门徒对他说：“老师，你看！多好的石头，多好的建筑啊！”", version_id: 6},
  {id: 156741, book: "Mark", verse: "13.002", unformatted: "耶稣对他说：“你在看这些雄伟的建筑吗？这里将绝不会有一块石头留在另一块石头上而不被拆下。”", version_id: 6},
  {id: 156742, book: "Mark", verse: "13.003", unformatted: "末日的预兆\n耶稣在橄榄山上，面对圣殿坐着。彼得、雅各、约翰和安得烈悄悄地问他：", version_id: 6},
  {id: 156743, book: "Mark", verse: "13.004", unformatted: "“请告诉我们，这些事什么时候会发生呢？这一切将要成就的时候，会有什么预兆呢？”", version_id: 6},
  {id: 156744, book: "Mark", verse: "13.005", unformatted: "耶稣就开始对他们说：“你们要当心，免得有人迷惑你们。", version_id: 6},
  {id: 156745, book: "Mark", verse: "13.006", unformatted: "许多人会以我的名而来，声称‘我就是那一位’，并且会迷惑许多人。", version_id: 6},
  {id: 156746, book: "Mark", verse: "13.007", unformatted: "当你们听见战争和战争消息的时候，不可惊慌。这些事必须发生，不过结局还没有到。", version_id: 6},
  {id: 156747, book: "Mark", verse: "13.008", unformatted: "一个民族要起来反对另一个民族，一个国家要起来反对另一个国家；到处都会有地震和饥荒。这些都像临产阵痛的开始。", version_id: 6},
  {id: 156748, book: "Mark", verse: "13.009", unformatted: "预言受迫害\n“你们自己要当心。他们将要把你们送交议会；你们会在会堂里受鞭打。为我的缘故，你们将站在总督和君王面前，好对他们做见证。", version_id: 6},
  {id: 156749, book: "Mark", verse: "13.01", unformatted: "然而，福音必须先传给万国。", version_id: 6},
  {id: 156750, book: "Mark", verse: "13.011", unformatted: "当他们把你们交出去、带走的时候，不要预先忧虑说什么。到时候，你们被赐予什么话，就说什么话，因为说话的不是你们自己，而是圣灵。", version_id: 6},
  {id: 156751, book: "Mark", verse: "13.012", unformatted: "兄弟将要把兄弟出卖，置于死地；父亲对孩子也会这样。儿女会起来与父母作对，并且害死他们。", version_id: 6},
  {id: 156752, book: "Mark", verse: "13.013", unformatted: "为了我的名，你们将被所有的人憎恨，但是忍耐到底的，这个人将会得救。", version_id: 6},
  {id: 156753, book: "Mark", verse: "13.014", unformatted: "大患难的日子\n“当你们看见‘那带来毁灭的亵渎者’站在不该站的地方时——读者应当思考——那时，在犹太的人应当逃到山里；", version_id: 6},
  {id: 156754, book: "Mark", verse: "13.015", unformatted: "在屋顶上的人，不要下来，也不要进屋子里去拿什么东西；", version_id: 6},
  {id: 156755, book: "Mark", verse: "13.016", unformatted: "在田里的人，不要转回到背后的事上，去拿自己的衣服。", version_id: 6},
  {id: 156756, book: "Mark", verse: "13.017", unformatted: "在那些日子里，孕妇和哺乳的女人有祸了！", version_id: 6},
  {id: 156757, book: "Mark", verse: "13.018", unformatted: "你们要祷告，好让这事不在冬天发生，", version_id: 6},
  {id: 156758, book: "Mark", verse: "13.019", unformatted: "因为在那些日子里将有患难。这样的患难从神创世之初直到如今，从来没有发生过，也绝不再发生。", version_id: 6},
  {id: 156759, book: "Mark", verse: "13.02", unformatted: "如果不是主减少了那些日子，就没有一个人能得救。然而，为了那些蒙拣选的人，就是他所拣选的，他减少了那些日子。", version_id: 6},
  {id: 156760, book: "Mark", verse: "13.021", unformatted: "“那时候，如果有人对你们说‘看哪，基督在这里！’‘看哪，他在那里！’你们不要相信。", version_id: 6},
  {id: 156761, book: "Mark", verse: "13.022", unformatted: "因为假基督们和假先知们会出现，行神迹和奇事，如果有可能，甚至迷惑那些蒙拣选的人。", version_id: 6},
  {id: 156762, book: "Mark", verse: "13.023", unformatted: "所以你们要当心。我已经预先把一切都告诉你们了。", version_id: 6},
  {id: 156763, book: "Mark", verse: "13.024", unformatted: "人子的来临\n“在那些日子里，随着那患难，\n‘太阳就要变黑，\n月亮也不发光，", version_id: 6},
  {id: 156764, book: "Mark", verse: "13.025", unformatted: "星辰要从天上坠落，\n诸天之上的各势力也将被震动。’", version_id: 6},
  {id: 156765, book: "Mark", verse: "13.026", unformatted: "那时候，人们将要看见人子带着极大的权能和荣耀，在云彩中来临。", version_id: 6},
  {id: 156766, book: "Mark", verse: "13.027", unformatted: "他要差派天使们，并且从四方，从地极到天边，召集他所拣选的人。", version_id: 6},
  {id: 156767, book: "Mark", verse: "13.028", unformatted: "无花果树的比喻\n“你们要从无花果树那里学个比喻：当无花果树的枝子发嫩长出叶子的时候，你们就知道夏天快到了。", version_id: 6},
  {id: 156768, book: "Mark", verse: "13.029", unformatted: "照样，当你们看见这些事发生的时候，你们应当知道：人子快到了，就在门口了。", version_id: 6},
  {id: 156769, book: "Mark", verse: "13.03", unformatted: "我确实地告诉你们：在这一切事发生之前，这世代绝不会过去。", version_id: 6},
  {id: 156770, book: "Mark", verse: "13.031", unformatted: "天和地将要消逝，但是我的话绝不会消逝。", version_id: 6},
  {id: 156771, book: "Mark", verse: "13.032", unformatted: "无人知道那日子和那时刻\n“至于那日子或那时刻，没有人知道，连天上的天使们也不知道，子也不知道，唯有父知道。", version_id: 6},
  {id: 156772, book: "Mark", verse: "13.033", unformatted: "你们要当心，要警醒；因为你们不知道那时刻是什么时候。", version_id: 6},
  {id: 156773, book: "Mark", verse: "13.034", unformatted: "这就像一个人出外旅行，离开了家，把权柄交给了他的奴仆们，又把工作分给了每个人，他吩咐看门的人要警醒。", version_id: 6},
  {id: 156774, book: "Mark", verse: "13.035", unformatted: "所以你们要警醒，因为你们不知道这家的主人什么时候回来，或傍晚，或半夜，或鸡叫时，或凌晨；", version_id: 6},
  {id: 156775, book: "Mark", verse: "13.036", unformatted: "免得他忽然回来，看见你们在睡觉。", version_id: 6},
  {id: 156776, book: "Mark", verse: "13.037", unformatted: "我对你们说的，也对所有的人说：你们要警醒！”", version_id: 6},
  {id: 156777, book: "Mark", verse: "14.001", unformatted: "阴谋杀害耶稣\n过两天就是逾越节和除酵节了。祭司长们和经文士们在图谋怎样用诡计拘捕耶稣，把他杀掉。", version_id: 6},
  {id: 156778, book: "Mark", verse: "14.002", unformatted: "不过他们说：“不可在节日期间，免得民众发生骚乱。”", version_id: 6},
  {id: 156779, book: "Mark", verse: "14.003", unformatted: "在伯大尼被膏抹\n耶稣在伯大尼，在麻风病人西门家里坐席的时候，有一个女人拿着一个玉瓶进来。瓶里盛着极其贵重的纯哪哒香液。她打破玉瓶，把香液浇在耶稣的头上。", version_id: 6},
  {id: 156780, book: "Mark", verse: "14.004", unformatted: "有些人心里很不满，说：“为什么要这样浪费香液呢？", version_id: 6},
  {id: 156781, book: "Mark", verse: "14.005", unformatted: "这香液本来可以卖三百多个银币，分给穷人。”他们就责备那女人。", version_id: 6},
  {id: 156782, book: "Mark", verse: "14.006", unformatted: "耶稣说：“由她吧！你们为什么为难她呢？她为我做了一件美好的事。", version_id: 6},
  {id: 156783, book: "Mark", verse: "14.007", unformatted: "要知道，你们总是有穷人与你们在一起，只要你们愿意，随时可以为他们做好事。但你们不总是有我。", version_id: 6},
  {id: 156784, book: "Mark", verse: "14.008", unformatted: "她尽她所能的做了。她是为我的安葬，预先膏抹了我的身体。", version_id: 6},
  {id: 156785, book: "Mark", verse: "14.009", unformatted: "我确实地告诉你们：在全世界，福音无论传到什么地方，这女人所做的事也将被述说，做为对她的记念。”", version_id: 6},
  {id: 156786, book: "Mark", verse: "14.01", unformatted: "那时候，加略人犹大，就是十二使徒中的一个，到祭司长们那里去，要把耶稣交给他们。", version_id: 6},
  {id: 156787, book: "Mark", verse: "14.011", unformatted: "他们听了就欢喜，并且许诺给他银钱。于是犹大就图谋怎样找机会把耶稣交出去。", version_id: 6},
  {id: 156788, book: "Mark", verse: "14.012", unformatted: "预备逾越节的晚餐\n除酵节的第一日，就是人们宰杀逾越节羔羊的那一天，耶稣的门徒们问他：“你要我们到哪里去预备，好让你吃逾越节的晚餐呢？”", version_id: 6},
  {id: 156789, book: "Mark", verse: "14.013", unformatted: "于是耶稣差派了他的两个门徒，对他们说：“你们进城去，会有一个人提着一罐水迎面而来，你们就跟着他。", version_id: 6},
  {id: 156790, book: "Mark", verse: "14.014", unformatted: "他进哪一家，你们就对那家的主人说：‘老师问：我的客房，就是我与我的门徒们一起吃逾越节晚餐的客房在哪里呢？’", version_id: 6},
  {id: 156791, book: "Mark", verse: "14.015", unformatted: "他会给你们看楼上一间预备好的大房间。你们就在那里为我们预备吧。”", version_id: 6},
  {id: 156792, book: "Mark", verse: "14.016", unformatted: "两个门徒出去，进了城，所看到的正如耶稣对他们所说的。他们就预备了逾越节的晚餐。", version_id: 6},
  {id: 156793, book: "Mark", verse: "14.017", unformatted: "在逾越节被出卖\n到了晚上，耶稣与十二使徒一起来了。", version_id: 6},
  {id: 156794, book: "Mark", verse: "14.018", unformatted: "他们坐着吃饭的时候，耶稣说：“我确实地告诉你们：你们当中有一个人要出卖我，他正与我一起吃饭。”", version_id: 6},
  {id: 156795, book: "Mark", verse: "14.019", unformatted: "他们开始感到忧伤，就一个接一个地问耶稣：“不会是我吧？”", version_id: 6},
  {id: 156796, book: "Mark", verse: "14.02", unformatted: "耶稣对他们说：“是你们十二使徒中的一个。他正与我一同在盘子里蘸饼。", version_id: 6},
  {id: 156797, book: "Mark", verse: "14.021", unformatted: "人子确实要离去，正如经上指着他所写的，但是出卖人子的那个人有祸了。对他来说，没有出生倒好。”", version_id: 6},
  {id: 156798, book: "Mark", verse: "14.022", unformatted: "第一次圣餐\n在他们吃的时候，耶稣拿起饼来，祝福了，就掰开，递给门徒们，说：“你们领受吧，这是我的身体。”", version_id: 6},
  {id: 156799, book: "Mark", verse: "14.023", unformatted: "接着，他拿起杯来，祝谢了，递给门徒们。他们都喝了。", version_id: 6},
  {id: 156800, book: "Mark", verse: "14.024", unformatted: "耶稣对他们说：“这是我的血，是为立约的，为许多人所流的。", version_id: 6},
  {id: 156801, book: "Mark", verse: "14.025", unformatted: "我确实地告诉你们：我绝不再喝这葡萄汁，直到我在神的国里喝新的那一天。”", version_id: 6},
  {id: 156802, book: "Mark", verse: "14.026", unformatted: "他们唱了赞美诗，就出来，往橄榄山去了。", version_id: 6},
  {id: 156803, book: "Mark", verse: "14.027", unformatted: "预言彼得不认主\n耶稣对他们说：“你们都将被绊倒，因为经上记着：\n‘我将击打牧人，\n羊群就被分散。’", version_id: 6},
  {id: 156804, book: "Mark", verse: "14.028", unformatted: "但是我复活以后，要在你们之前到加利利去。”", version_id: 6},
  {id: 156805, book: "Mark", verse: "14.029", unformatted: "彼得对耶稣说：“即使所有的人被绊倒，我还是不会。”", version_id: 6},
  {id: 156806, book: "Mark", verse: "14.03", unformatted: "可是耶稣对他说：“我确实地告诉你：今天，就在今夜，鸡叫两遍以前，你会三次不认我。”", version_id: 6},
  {id: 156807, book: "Mark", verse: "14.031", unformatted: "彼得却坚决地说：“就算我必须与你同死，我也绝不会不认你。”所有的门徒也都这么说。", version_id: 6},
  {id: 156808, book: "Mark", verse: "14.032", unformatted: "园中的祷告\n他们来到一个名叫客西马尼的地方。耶稣对他的门徒们说：“我祷告的时候，你们坐在这里。”", version_id: 6},
  {id: 156809, book: "Mark", verse: "14.033", unformatted: "他带了彼得、雅各和约翰一起去。这时候，他开始忧惧起来，极其难过，", version_id: 6},
  {id: 156810, book: "Mark", verse: "14.034", unformatted: "对他们说：“我的灵魂很忧伤，几乎要死。你们留在这里，要警醒。”", version_id: 6},
  {id: 156811, book: "Mark", verse: "14.035", unformatted: "耶稣稍往前走，俯伏在地，祷告说，如果有可能，让那时刻离开他。", version_id: 6},
  {id: 156812, book: "Mark", verse: "14.036", unformatted: "他说：“阿爸！父啊！在你，没有不可能的事，请把这杯从我这里拿走吧！但不要照我的意愿，而要照你的意愿。”", version_id: 6},
  {id: 156813, book: "Mark", verse: "14.037", unformatted: "耶稣回来，看见他们睡着了，就对彼得说：“西门，你睡着了？难道你不能警醒一个小时吗？", version_id: 6},
  {id: 156814, book: "Mark", verse: "14.038", unformatted: "要警醒祷告，免得陷入试探。你们心灵虽然愿意，肉体却是软弱的。”", version_id: 6},
  {id: 156815, book: "Mark", verse: "14.039", unformatted: "耶稣又去祷告，说了同样的话。", version_id: 6},
  {id: 156816, book: "Mark", verse: "14.04", unformatted: "他又回来，看见他们在睡觉，因为他们的眼皮发沉，也不知道要回答耶稣什么。", version_id: 6},
  {id: 156817, book: "Mark", verse: "14.041", unformatted: "耶稣第三次回来，对他们说：“你们还在睡觉、休息吗？已经够了，时候到了！看哪，人子被交在罪人的手里了。", version_id: 6},
  {id: 156818, book: "Mark", verse: "14.042", unformatted: "起来，我们走吧！看，那出卖我的人快到了！”", version_id: 6},
  {id: 156819, book: "Mark", verse: "14.043", unformatted: "犹大亲吻的暗号\n耶稣还在说话的时候，忽然十二使徒之一的犹大来了。有一群人拿着刀剑和棍棒与他一起来，他们是祭司长们、经文士们和长老们所派来的。", version_id: 6},
  {id: 156820, book: "Mark", verse: "14.044", unformatted: "那出卖耶稣的人已经和他们定了一个暗号，说：“我如果亲吻谁，谁就是那个人。你们把他抓住，小心地带走。”", version_id: 6},
  {id: 156821, book: "Mark", verse: "14.045", unformatted: "犹大一到，就上前对耶稣说：“拉比！”然后亲吻了他。", version_id: 6},
  {id: 156822, book: "Mark", verse: "14.046", unformatted: "他们就下手拘捕了耶稣。", version_id: 6},
  {id: 156823, book: "Mark", verse: "14.047", unformatted: "站在旁边的一个门徒拔出刀来，向大祭司的奴仆砍去，削掉了他的一个耳朵。", version_id: 6},
  {id: 156824, book: "Mark", verse: "14.048", unformatted: "耶稣对他们说：“你们带着刀剑和棍棒出来抓我，就像对付强盗那样吗？", version_id: 6},
  {id: 156825, book: "Mark", verse: "14.049", unformatted: "我天天在圣殿里教导人，和你们在一起，而你们不抓我。不过这是为要应验那些经文。”", version_id: 6},
  {id: 156826, book: "Mark", verse: "14.05", unformatted: "这时候，所有的门徒都离开他逃跑了。", version_id: 6},
  {id: 156827, book: "Mark", verse: "14.051", unformatted: "有一个年轻人，赤身披上一块细麻布，跟在耶稣后面。那些人就抓住他，", version_id: 6},
  {id: 156828, book: "Mark", verse: "14.052", unformatted: "可是他却丢下麻布，赤着身子逃跑了。", version_id: 6},
  {id: 156829, book: "Mark", verse: "14.053", unformatted: "在议员面前受审\n他们把耶稣带到大祭司那里。所有的祭司长、长老和经文士都聚在一起。", version_id: 6},
  {id: 156830, book: "Mark", verse: "14.054", unformatted: "彼得远远地跟着耶稣，直到进了大祭司的院子里。他与差役们一起坐在火边取暖。", version_id: 6},
  {id: 156831, book: "Mark", verse: "14.055", unformatted: "当时，祭司长们和全议会的人为了要处死耶稣，都在寻找证据来控告他。可是他们找不出什么。", version_id: 6},
  {id: 156832, book: "Mark", verse: "14.056", unformatted: "许多人做伪证诬告他，可是这些证词互相矛盾。", version_id: 6},
  {id: 156833, book: "Mark", verse: "14.057", unformatted: "有一些人站起来做伪证控告他，说：", version_id: 6},
  {id: 156834, book: "Mark", verse: "14.058", unformatted: "“我们听他说过‘我要拆毁这座人手所造的圣所，三天内另建一座不是人手所造的。’”", version_id: 6},
  {id: 156835, book: "Mark", verse: "14.059", unformatted: "可是他们这个证词也是互相矛盾的。", version_id: 6},
  {id: 156836, book: "Mark", verse: "14.06", unformatted: "于是大祭司就起来站到中间问耶稣，说：“对这些人控告你的事，你什么都不回应吗？”", version_id: 6},
  {id: 156837, book: "Mark", verse: "14.061", unformatted: "耶稣沉默不语，什么也不回答。大祭司又问他，说：“你是基督——那当受颂赞者的儿子吗？”", version_id: 6},
  {id: 156838, book: "Mark", verse: "14.062", unformatted: "耶稣说：“我就是。而且你们将看见人子坐在全能者的右边，在天上的云彩中来临。”", version_id: 6},
  {id: 156839, book: "Mark", verse: "14.063", unformatted: "大祭司就撕裂自己的衣服，说：“难道我们还需要什么见证人吗？", version_id: 6},
  {id: 156840, book: "Mark", verse: "14.064", unformatted: "你们都听见了这亵渎的话。你们怎么看？”\n他们都判定他是该死的。", version_id: 6},
  {id: 156841, book: "Mark", verse: "14.065", unformatted: "接着，有些人开始向他吐唾沫，蒙住他的脸，用拳头打他，对他说：“你说预言吧！”差役们也用手掌打他。", version_id: 6},
  {id: 156842, book: "Mark", verse: "14.066", unformatted: "彼得不认主\n彼得正在下边院子里的时候，大祭司的一个女仆过来了。", version_id: 6},
  {id: 156843, book: "Mark", verse: "14.067", unformatted: "她看见彼得在取暖，就注视着他，说：“你也与那个拿撒勒人耶稣是一伙的。”", version_id: 6},
  {id: 156844, book: "Mark", verse: "14.068", unformatted: "彼得却否认说：“我既不明白，也不知道你在说什么。”于是他就出去，到了门廊。这时候，鸡叫了。", version_id: 6},
  {id: 156845, book: "Mark", verse: "14.069", unformatted: "那女仆看见他，又开始对站在旁边的人说：“这个人是与他们一伙的。”", version_id: 6},
  {id: 156846, book: "Mark", verse: "14.07", unformatted: "彼得再次否认。过了一会儿，站在旁边的人又对彼得说：“你真的是与他们一伙的，因为你也是加利利人。”", version_id: 6},
  {id: 156847, book: "Mark", verse: "14.071", unformatted: "他就开始赌咒并起誓说：“我不认识你们所讲的这个人。”", version_id: 6},
  {id: 156848, book: "Mark", verse: "14.072", unformatted: "立时，鸡叫了第二遍。彼得想起耶稣对他说过的话：“鸡叫两遍以前，你会三次不认我”，他就忍不住痛哭起来。", version_id: 6},
  {id: 156849, book: "Mark", verse: "15.001", unformatted: "在彼拉多面前受审\n一到清晨，祭司长们就与长老们、经文士们以及全议会的人商议对策，然后把耶稣捆起来带走，交给彼拉多。", version_id: 6},
  {id: 156850, book: "Mark", verse: "15.002", unformatted: "彼拉多问他：“你是犹太人的王吗？”\n耶稣回答他，说：“是你说的。”", version_id: 6},
  {id: 156851, book: "Mark", verse: "15.003", unformatted: "祭司长们控告了耶稣很多事。", version_id: 6},
  {id: 156852, book: "Mark", verse: "15.004", unformatted: "所以彼拉多又问他，说：“你什么都不回应吗？你看，他们控告你这么多的事。”", version_id: 6},
  {id: 156853, book: "Mark", verse: "15.005", unformatted: "可是，耶稣仍然什么也不回答，以致彼拉多感到惊奇。", version_id: 6},
  {id: 156854, book: "Mark", verse: "15.006", unformatted: "耶稣或巴拉巴\n每逢这节日，彼拉多都会给民众释放一个他们所要求的囚犯。", version_id: 6},
  {id: 156855, book: "Mark", verse: "15.007", unformatted: "那时，有一个叫巴拉巴的，和一同叛乱的人被关在牢里，他们在暴乱中杀过人。", version_id: 6},
  {id: 156856, book: "Mark", verse: "15.008", unformatted: "众人就上前去，开始要求彼拉多按照他的惯例给他们办。", version_id: 6},
  {id: 156857, book: "Mark", verse: "15.009", unformatted: "彼拉多问他们，说：“你们要我给你们释放这犹太人的王吗？”", version_id: 6},
  {id: 156858, book: "Mark", verse: "15.01", unformatted: "其实彼拉多知道祭司长们是出于嫉妒才把耶稣交给他的。", version_id: 6},
  {id: 156859, book: "Mark", verse: "15.011", unformatted: "但是，祭司长们煽动众人，宁愿要彼拉多给他们释放巴拉巴。", version_id: 6},
  {id: 156860, book: "Mark", verse: "15.012", unformatted: "彼拉多又问他们，说：“那么，你们要我怎么处理你们称为犹太人之王的那一位呢？”", version_id: 6},
  {id: 156861, book: "Mark", verse: "15.013", unformatted: "他们再次喊叫：“把他钉上十字架！”", version_id: 6},
  {id: 156862, book: "Mark", verse: "15.014", unformatted: "彼拉多对他们说：“他到底做了什么恶事呢？”\n但他们却越发喊叫：“把他钉上十字架！”", version_id: 6},
  {id: 156863, book: "Mark", verse: "15.015", unformatted: "彼拉多想要讨好众人，就给他们释放了巴拉巴，然后将耶稣鞭打了，把他交出去钉上十字架。", version_id: 6},
  {id: 156864, book: "Mark", verse: "15.016", unformatted: "被士兵戏弄\n士兵们把耶稣带进大院，那里就是总督府，并召集了全队士兵。", version_id: 6},
  {id: 156865, book: "Mark", verse: "15.017", unformatted: "他们给他穿上紫色袍子，套上用荆棘编的冠冕，", version_id: 6},
  {id: 156866, book: "Mark", verse: "15.018", unformatted: "然后开始向他致敬说：“万岁，犹太人的王！”", version_id: 6},
  {id: 156867, book: "Mark", verse: "15.019", unformatted: "他们用芦苇打他的头，向他吐唾沫，又跪下来拜他。", version_id: 6},
  {id: 156868, book: "Mark", verse: "15.02", unformatted: "他们戏弄完了，就脱下他紫色袍子，给他穿上他自己的衣服，然后带他出去，要把他钉上十字架。", version_id: 6},
  {id: 156869, book: "Mark", verse: "15.021", unformatted: "被钉在罪犯之间\n有一个古利奈人西门，是亚历山大和鲁弗斯的父亲，正从乡下来，经过那里。他们就强迫他来背耶稣的十字架。", version_id: 6},
  {id: 156870, book: "Mark", verse: "15.022", unformatted: "他们把耶稣带到一个叫各各他的地方——“各各他”翻译出来就是“骷髅地”；", version_id: 6},
  {id: 156871, book: "Mark", verse: "15.023", unformatted: "然后把调了没药的酒给耶稣，但他不接受。", version_id: 6},
  {id: 156872, book: "Mark", verse: "15.024", unformatted: "于是，他们把耶稣钉上十字架，又分他的衣服，抽签看谁得什么。", version_id: 6},
  {id: 156873, book: "Mark", verse: "15.025", unformatted: "在上午九点，他们把耶稣钉上了十字架。", version_id: 6},
  {id: 156874, book: "Mark", verse: "15.026", unformatted: "他的罪状牌上写的是：“犹太人的王。”", version_id: 6},
  {id: 156875, book: "Mark", verse: "15.027", unformatted: "同时，他们把两个强盗与耶稣一起钉上十字架，一个在右边，一个在左边。", version_id: 6},
  {id: 156876, book: "Mark", verse: "15.028", unformatted: "这就应验了经上所说的：“他被列在不法之徒当中。”", version_id: 6},
  {id: 156877, book: "Mark", verse: "15.029", unformatted: "那些路过的人辱骂耶稣，摇着头，说：“咳！你这个要拆毁圣所、三天内又建起来的人哪，", version_id: 6},
  {id: 156878, book: "Mark", verse: "15.03", unformatted: "从十字架上下来，救救你自己吧！”", version_id: 6},
  {id: 156879, book: "Mark", verse: "15.031", unformatted: "同样，祭司长们也与经文士们一起嘲笑，相互说：“他救了别人，却不能救自己！", version_id: 6},
  {id: 156880, book: "Mark", verse: "15.032", unformatted: "这个基督，以色列的王，现在让他从十字架上下来吧，好让我们看了就相信！”与耶稣一起被钉十字架的人也责骂他。", version_id: 6},
  {id: 156881, book: "Mark", verse: "15.033", unformatted: "耶稣之死\n到了中午十二点，黑暗笼罩了整个大地，一直到下午三点。", version_id: 6},
  {id: 156882, book: "Mark", verse: "15.034", unformatted: "下午三点，耶稣大声呼喊：“以罗伊，以罗伊！拉玛撒巴克达尼？”——这翻译出来就是“我的神，我的神！你为什么离弃我？”", version_id: 6},
  {id: 156883, book: "Mark", verse: "15.035", unformatted: "站在旁边的一些人听见了，就说：“看，他在呼唤以利亚呢！”", version_id: 6},
  {id: 156884, book: "Mark", verse: "15.036", unformatted: "有一个人跑过去，用海绵蘸满了酸酒，套在芦苇上，要给他喝，然后说：“等着吧，我们看以利亚会不会来把他放下来。”", version_id: 6},
  {id: 156885, book: "Mark", verse: "15.037", unformatted: "耶稣大叫一声，就断了气。", version_id: 6},
  {id: 156886, book: "Mark", verse: "15.038", unformatted: "同时，圣所里的幔子从上到下裂成两半。", version_id: 6},
  {id: 156887, book: "Mark", verse: "15.039", unformatted: "站在耶稣对面的百夫长，看见他这样断了气，就说：“这个人真是神的儿子！”", version_id: 6},
  {id: 156888, book: "Mark", verse: "15.04", unformatted: "还有一些妇女从远处观看，其中有茉大拉的玛丽亚、小雅各和约西的母亲玛丽亚，以及莎罗美。", version_id: 6},
  {id: 156889, book: "Mark", verse: "15.041", unformatted: "她们就是耶稣在加利利的时候，跟随他并服事他的人；还有许多别的跟耶稣一同上耶路撒冷的妇女。", version_id: 6},
  {id: 156890, book: "Mark", verse: "15.042", unformatted: "安葬\n因为那天是预备日，也就是安息日的前一天，所以到了傍晚，", version_id: 6},
  {id: 156891, book: "Mark", verse: "15.043", unformatted: "亚利马太的约瑟来了，鼓起勇气去向彼拉多请求要耶稣的遗体。约瑟是犹太议会中一位受人尊敬的议员，他也期待着神的国。", version_id: 6},
  {id: 156892, book: "Mark", verse: "15.044", unformatted: "彼拉多听见耶稣已经死了就感到惊奇，于是叫来了百夫长，问他耶稣是否已经死了。", version_id: 6},
  {id: 156893, book: "Mark", verse: "15.045", unformatted: "他向百夫长了解情况后，就把遗体给了约瑟。", version_id: 6},
  {id: 156894, book: "Mark", verse: "15.046", unformatted: "于是，约瑟买了细麻布，把遗体取下来，用细麻布包好，安放在一座岩石凿成的墓穴里，又滚来一块石头，堵住墓穴口。", version_id: 6},
  {id: 156895, book: "Mark", verse: "15.047", unformatted: "茉大拉的玛丽亚和约西的母亲玛丽亚，都看到了安放耶稣的地方。", version_id: 6},
  {id: 156896, book: "Mark", verse: "16.001", unformatted: "复活的清晨\n安息日一过，茉大拉的玛丽亚、雅各的母亲玛丽亚和莎罗美就买了香料，要去膏抹耶稣。", version_id: 6},
  {id: 156897, book: "Mark", verse: "16.002", unformatted: "在一周的头一天，一大清早，太阳刚出来，她们就来到墓穴那里。", version_id: 6},
  {id: 156898, book: "Mark", verse: "16.003", unformatted: "她们互相说：“谁能替我们滚开墓穴口的石头呢？”", version_id: 6},
  {id: 156899, book: "Mark", verse: "16.004", unformatted: "原来那块石头非常大。可是她们抬头一看，见石头已经被滚开了。", version_id: 6},
  {id: 156900, book: "Mark", verse: "16.005", unformatted: "她们进了墓穴，看见一个年轻人身穿白袍坐在右边，就大吃一惊。", version_id: 6},
  {id: 156901, book: "Mark", verse: "16.006", unformatted: "那年轻人对她们说：“不要惊慌！你们是在寻找被钉十字架的拿撒勒人耶稣，他已经复活了，不在这里。看，这就是他们安放他的地方。", version_id: 6},
  {id: 156902, book: "Mark", verse: "16.007", unformatted: "你们去，告诉他的门徒们和彼得：‘他要在你们之前到加利利去。在那里，你们将见到他，正如他告诉过你们的。’”", version_id: 6},
  {id: 156903, book: "Mark", verse: "16.008", unformatted: "她们浑身颤抖、惊慌失措，从墓穴出来就逃跑了。她们因为感到惧怕，什么也没有对任何人说。", version_id: 6},
  {id: 156904, book: "Mark", verse: "16.009", unformatted: "复活的主显现\n在一周头一天的清晨，耶稣复活以后，首先向茉大拉的玛丽亚显现。耶稣曾经从她身上赶出了七个鬼魔。", version_id: 6},
  {id: 156905, book: "Mark", verse: "16.01", unformatted: "她去告诉那些向来与耶稣在一起的人，他们正在悲伤、哀哭。", version_id: 6},
  {id: 156906, book: "Mark", verse: "16.011", unformatted: "他们听见耶稣活了，又被玛丽亚看到了，却是不相信。", version_id: 6},
  {id: 156907, book: "Mark", verse: "16.012", unformatted: "这些事以后，门徒中有两个人往乡下去。在路上，耶稣以另一种形像向他们显现。", version_id: 6},
  {id: 156908, book: "Mark", verse: "16.013", unformatted: "他们去告诉其他的门徒，那些门徒也不相信他们。", version_id: 6},
  {id: 156909, book: "Mark", verse: "16.014", unformatted: "大使命\n后来那十一个使徒吃饭的时候，耶稣向他们显现，并斥责他们的不信和心里刚硬，因为他们不相信那些在他复活后看到他的人。", version_id: 6},
  {id: 156910, book: "Mark", verse: "16.015", unformatted: "耶稣对他们说：“你们要到全世界去，向万人传福音。", version_id: 6},
  {id: 156911, book: "Mark", verse: "16.016", unformatted: "信而受洗的人，将会得救；不信的人，将被定罪。", version_id: 6},
  {id: 156912, book: "Mark", verse: "16.017", unformatted: "这些神迹将伴随着信的人：他们将奉我的名驱赶鬼魔；说各种新的语言；", version_id: 6},
  {id: 156913, book: "Mark", verse: "16.018", unformatted: "对付蛇类；即使喝了致命的东西也绝不受害；按手在病人身上，病人就将痊愈。”", version_id: 6},
  {id: 156914, book: "Mark", verse: "16.019", unformatted: "升天\n这样，主耶稣对他们说完话以后，就被接到天上，坐在神的右边。", version_id: 6},
  {id: 156915, book: "Mark", verse: "16.02", unformatted: "门徒们就出去，到处传道。主与他们一同工作，并且藉着伴随的神迹证实这话语。", version_id: 6},
  {id: 156916, book: "Luke", verse: "1.001", unformatted: "献给提阿费罗\n提阿费罗大人哪，既然有许多人着手编录关于在我们中间所成就的事，", version_id: 6},
  {id: 156917, book: "Luke", verse: "1.002", unformatted: "就是照着从起初以来的目击者和做福音仆人的那些人所传给我们的，", version_id: 6},
  {id: 156918, book: "Luke", verse: "1.003", unformatted: "我也已经从头仔细地追踪了这一切，我就认为应该按着次序写给您，", version_id: 6},
  {id: 156919, book: "Luke", verse: "1.004", unformatted: "好让您了解到您所领受的话语都是确实的。", version_id: 6},
  {id: 156920, book: "Luke", verse: "1.005", unformatted: "预言约翰诞生\n在犹太王希律的那些日子里，有一个祭司名叫撒迦利亚，属于亚比雅班组。他的妻子也是亚伦的子孙，名叫伊莉莎白。", version_id: 6},
  {id: 156921, book: "Luke", verse: "1.006", unformatted: "他们两个人在神面前都是义人，遵行主的一切诫命和公义规定，无可指责。", version_id: 6},
  {id: 156922, book: "Luke", verse: "1.007", unformatted: "可是他们没有孩子，因为伊莉莎白不能生育，两个人又都上了年纪。", version_id: 6},
  {id: 156923, book: "Luke", verse: "1.008", unformatted: "有一次，当撒迦利亚的班组轮值的时候，撒迦利亚在神面前履行祭司的职务，", version_id: 6},
  {id: 156924, book: "Luke", verse: "1.009", unformatted: "照着祭司职务的规矩，抽中了签就进入主的圣所烧香。", version_id: 6},
  {id: 156925, book: "Luke", verse: "1.01", unformatted: "烧香的时候，全体民众都在外面祷告。", version_id: 6},
  {id: 156926, book: "Luke", verse: "1.011", unformatted: "有主的一位天使站在香坛的右边向他显现。", version_id: 6},
  {id: 156927, book: "Luke", verse: "1.012", unformatted: "撒迦利亚看见后，就惊慌不安，恐惧就临到了他。", version_id: 6},
  {id: 156928, book: "Luke", verse: "1.013", unformatted: "天使对他说：\n“撒迦利亚，不要怕，\n因为你的祈祷已蒙垂听。\n你的妻子伊莉莎白要为你生一个儿子，\n你要给他起名叫约翰。", version_id: 6},
  {id: 156929, book: "Luke", verse: "1.014", unformatted: "他将使你欢喜、快乐，\n许多人也会因他的诞生而欢欣，", version_id: 6},
  {id: 156930, book: "Luke", verse: "1.015", unformatted: "因为他将要在主面前为大。\n淡酒和烈酒他绝不能喝，\n还在母腹中的时候，\n他就会被圣灵充满。", version_id: 6},
  {id: 156931, book: "Luke", verse: "1.016", unformatted: "他将使许多以色列子民\n归向主——他们的神。", version_id: 6},
  {id: 156932, book: "Luke", verse: "1.017", unformatted: "他将藉着以利亚的灵和能力，\n在主的面前先行，\n使父亲的心\n转向儿女，\n使悖逆的人\n转向义人的智慧，\n好为主准备一群预备好了的子民。”", version_id: 6},
  {id: 156933, book: "Luke", verse: "1.018", unformatted: "撒迦利亚对天使说：“我凭着什么能知道这事呢？我是个老人，我的妻子也上了年纪。”", version_id: 6},
  {id: 156934, book: "Luke", verse: "1.019", unformatted: "天使回答他，说：“我是站在神面前的加百列，奉差派来对你说话，向你传达这些好消息。", version_id: 6},
  {id: 156935, book: "Luke", verse: "1.02", unformatted: "看哪，你要成为哑巴，不能说话，一直到这些事成就的日子，因为你不相信我的话！到了时候，我的话就会应验。”", version_id: 6},
  {id: 156936, book: "Luke", verse: "1.021", unformatted: "民众等候着撒迦利亚，对他在圣所里的迟延不出感到惊奇。", version_id: 6},
  {id: 156937, book: "Luke", verse: "1.022", unformatted: "当他出来不能和他们说话的时候，他们才了解他在圣所里看到了异象。撒迦利亚一直向他们打手势，却始终不能说话。", version_id: 6},
  {id: 156938, book: "Luke", verse: "1.023", unformatted: "他服事的日子满了以后，就回家去了。", version_id: 6},
  {id: 156939, book: "Luke", verse: "1.024", unformatted: "这些日子以后，他的妻子伊莉莎白怀了孕，隐居了五个月。她说：", version_id: 6},
  {id: 156940, book: "Luke", verse: "1.025", unformatted: "“为了除去我在人间的耻辱，主在这些眷顾的日子里，这样看待了我。”", version_id: 6},
  {id: 156941, book: "Luke", verse: "1.026", unformatted: "预言基督诞生\n在第六个月，天使加百列从神那里奉差派，去加利利一个叫拿撒勒的城，", version_id: 6},
  {id: 156942, book: "Luke", verse: "1.027", unformatted: "到一个童贞女那里。这童贞女名叫玛丽亚，已经许配给大卫家族一个名叫约瑟的人。", version_id: 6},
  {id: 156943, book: "Luke", verse: "1.028", unformatted: "天使来到她面前，说：“蒙大恩的女子，愿你欢喜，主与你同在！”", version_id: 6},
  {id: 156944, book: "Luke", verse: "1.029", unformatted: "玛丽亚因这话就惊慌不安，思索这问候到底是什么意思。", version_id: 6},
  {id: 156945, book: "Luke", verse: "1.03", unformatted: "天使对她说：\n“玛丽亚，不要怕，\n因为你在神面前蒙恩了。", version_id: 6},
  {id: 156946, book: "Luke", verse: "1.031", unformatted: "看哪，\n你将要怀孕生一个儿子，\n你要给他起名叫耶稣。", version_id: 6},
  {id: 156947, book: "Luke", verse: "1.032", unformatted: "他将要为大，\n被称为至高者的儿子；\n主、神要把他先祖大卫的王位赐给他。", version_id: 6},
  {id: 156948, book: "Luke", verse: "1.033", unformatted: "他要做王统管雅各家，直到永远，\n他的国度没有穷尽。”", version_id: 6},
  {id: 156949, book: "Luke", verse: "1.034", unformatted: "玛丽亚对天使说：“我既然没有出嫁，怎么会有这事呢？”", version_id: 6},
  {id: 156950, book: "Luke", verse: "1.035", unformatted: "天使回答她，说：\n“圣灵将要临到你，\n至高者的大能要荫庇你，\n因此，那要诞生的圣者\n将被称为神的儿子。", version_id: 6},
  {id: 156951, book: "Luke", verse: "1.036", unformatted: "你看，你的亲戚伊莉莎白虽然年老，也怀了男胎；那被称为不能生育的，现在已有六个月的身孕了。", version_id: 6},
  {id: 156952, book: "Luke", verse: "1.037", unformatted: "因为在神没有什么事是不可能的。”", version_id: 6},
  {id: 156953, book: "Luke", verse: "1.038", unformatted: "玛丽亚说：“看哪，我是主的婢女，愿这事照着你的话成就在我身上。”于是天使离开了她。", version_id: 6},
  {id: 156954, book: "Luke", verse: "1.039", unformatted: "玛丽亚与伊莉莎白\n就在那些日子里，玛丽亚起身到山区去，赶往犹大的一个城，", version_id: 6},
  {id: 156955, book: "Luke", verse: "1.04", unformatted: "进了撒迦利亚的家，问候伊莉莎白。", version_id: 6},
  {id: 156956, book: "Luke", verse: "1.041", unformatted: "伊莉莎白一听到玛丽亚的问候，胎儿就在她的腹中跳动，伊莉莎白也被圣灵充满，", version_id: 6},
  {id: 156957, book: "Luke", verse: "1.042", unformatted: "大声呼喊说：\n“你在妇女中是蒙祝福的，\n你腹中的胎儿也是蒙祝福的！", version_id: 6},
  {id: 156958, book: "Luke", verse: "1.043", unformatted: "我主的母亲竟然到我这里来，这事怎么会临到我呢？", version_id: 6},
  {id: 156959, book: "Luke", verse: "1.044", unformatted: "看哪，原来你问候的声音一传入我的耳朵，胎儿就在我的腹中欢喜跳跃。", version_id: 6},
  {id: 156960, book: "Luke", verse: "1.045", unformatted: "这相信的女子是蒙福的，因为主对她说的话都会实现。”", version_id: 6},
  {id: 156961, book: "Luke", verse: "1.046", unformatted: "玛丽亚的颂赞\n玛丽亚说：\n“我的心尊主为大，", version_id: 6},
  {id: 156962, book: "Luke", verse: "1.047", unformatted: "我的灵以神我的救主为乐，", version_id: 6},
  {id: 156963, book: "Luke", verse: "1.048", unformatted: "因为他看顾他婢女的卑微。\n看哪，从今以后，\n世世代代都要称我有福，", version_id: 6},
  {id: 156964, book: "Luke", verse: "1.049", unformatted: "因为那全能者\n为我行了大事；\n他的名是神圣的。", version_id: 6},
  {id: 156965, book: "Luke", verse: "1.05", unformatted: "他的怜悯，\n世世代代临到敬畏他的人。", version_id: 6},
  {id: 156966, book: "Luke", verse: "1.051", unformatted: "他用膀臂施展大能，\n驱散那些心思意念骄傲的人。", version_id: 6},
  {id: 156967, book: "Luke", verse: "1.052", unformatted: "他把权能者从高位上拉下，\n又把卑微的人高举。", version_id: 6},
  {id: 156968, book: "Luke", verse: "1.053", unformatted: "他用美物满足饥饿的人，\n让富有的人空手而去。", version_id: 6},
  {id: 156969, book: "Luke", verse: "1.054", unformatted: "他扶助他的仆人以色列，\n不忘怜悯，", version_id: 6},
  {id: 156970, book: "Luke", verse: "1.055", unformatted: "正如他对我们祖先，\n对亚伯拉罕和他的后裔所说的，直到永远。”", version_id: 6},
  {id: 156971, book: "Luke", verse: "1.056", unformatted: "玛丽亚与伊莉莎白一起住了大约三个月，就回家去了。", version_id: 6},
  {id: 156972, book: "Luke", verse: "1.057", unformatted: "约翰的出生与命名\n伊莉莎白产期到了，生了一个儿子。", version_id: 6},
  {id: 156973, book: "Luke", verse: "1.058", unformatted: "她的邻居和亲戚听说主向她大施怜悯，就与她一同欢乐。", version_id: 6},
  {id: 156974, book: "Luke", verse: "1.059", unformatted: "到了第八天，他们来给孩子行割礼，要按照他父亲的名字叫他撒迦利亚。", version_id: 6},
  {id: 156975, book: "Luke", verse: "1.06", unformatted: "可是孩子的母亲应声说：“不！他要被称为约翰。”", version_id: 6},
  {id: 156976, book: "Luke", verse: "1.061", unformatted: "他们对她说：“你的亲族中没有叫这名字的。”", version_id: 6},
  {id: 156977, book: "Luke", verse: "1.062", unformatted: "于是他们向孩子的父亲打手势，到底他愿意叫他什么名字。", version_id: 6},
  {id: 156978, book: "Luke", verse: "1.063", unformatted: "撒迦利亚要了一块写字板，写上：“他的名字是约翰。”大家都感到惊奇。", version_id: 6},
  {id: 156979, book: "Luke", verse: "1.064", unformatted: "撒迦利亚的口立刻开了，舌头也松了，就说出话来，颂赞神。", version_id: 6},
  {id: 156980, book: "Luke", verse: "1.065", unformatted: "于是住在他们周围的人都感到惧怕，整个犹太山区的人也都在谈论这一切事。", version_id: 6},
  {id: 156981, book: "Luke", verse: "1.066", unformatted: "所有听见的人都把这一切存在心里，说：“这孩子将来究竟会怎么样呢？”因为显然有主的手与他同在。", version_id: 6},
  {id: 156982, book: "Luke", verse: "1.067", unformatted: "撒迦利亚的预言\n他的父亲撒迦利亚被圣灵充满，就预言说：", version_id: 6},
  {id: 156983, book: "Luke", verse: "1.068", unformatted: "“主——以色列的神是当受颂赞的，\n因为他临到了他的子民，\n并施行了救赎。", version_id: 6},
  {id: 156984, book: "Luke", verse: "1.069", unformatted: "他在他的仆人大卫家中，\n为我们兴起了一只救恩的角，", version_id: 6},
  {id: 156985, book: "Luke", verse: "1.07", unformatted: "正如他自古以来藉着圣先知们的口所说的，", version_id: 6},
  {id: 156986, book: "Luke", verse: "1.071", unformatted: "拯救我们脱离敌人、脱离一切恨我们之人的手，", version_id: 6},
  {id: 156987, book: "Luke", verse: "1.072", unformatted: "向我们的祖先施怜悯，\n不忘他神圣的约，", version_id: 6},
  {id: 156988, book: "Luke", verse: "1.073", unformatted: "就是向我们的先祖亚伯拉罕所起的誓，", version_id: 6},
  {id: 156989, book: "Luke", verse: "1.074", unformatted: "使我们从敌人的手中被救出，", version_id: 6},
  {id: 156990, book: "Luke", verse: "1.075", unformatted: "在我们所有的日子里无所惧怕，以圣洁和公义事奉他；", version_id: 6},
  {id: 156991, book: "Luke", verse: "1.076", unformatted: "而你——孩子啊！\n你将被称为至高者的先知，\n因为你将走在主的前面，\n预备他的路，", version_id: 6},
  {id: 156992, book: "Luke", verse: "1.077", unformatted: "藉着罪得赦免，\n赐给他的子民救恩的知识。", version_id: 6},
  {id: 156993, book: "Luke", verse: "1.078", unformatted: "因着我们神怜悯的心肠，\n黎明的曙光将从高天临到我们，", version_id: 6},
  {id: 156994, book: "Luke", verse: "1.079", unformatted: "照亮那些坐在黑暗\n和死亡阴影中的人，\n引导我们的脚步进入平安之路。”", version_id: 6},
  {id: 156995, book: "Luke", verse: "1.08", unformatted: "那孩子渐渐长大，灵里刚强。他住在旷野，直到他向以色列公开出现的那天。", version_id: 6},
  {id: 156996, book: "Luke", verse: "2.001", unformatted: "耶稣基督降生\n就在那些日子里，凯撒奥古斯都颁布谕令，要普天下的人都登记户口。", version_id: 6},
  {id: 156997, book: "Luke", verse: "2.002", unformatted: "这是头一次户口登记，在居里尼乌斯任叙利亚总督的时候进行的。", version_id: 6},
  {id: 156998, book: "Luke", verse: "2.003", unformatted: "于是众人都各回自己的城镇去登记。", version_id: 6},
  {id: 156999, book: "Luke", verse: "2.004", unformatted: "约瑟也从加利利的拿撒勒城上犹太去，到了那叫做伯利恒的大卫之城，因为他是大卫一家一族的人。", version_id: 6},
  {id: 157000, book: "Luke", verse: "2.005", unformatted: "他和已经许配给他、怀有身孕的玛丽亚一起去登记。", version_id: 6},
  {id: 157001, book: "Luke", verse: "2.006", unformatted: "他们在伯利恒的时候，玛丽亚的产期到了，", version_id: 6},
  {id: 157002, book: "Luke", verse: "2.007", unformatted: "就生了她的头胎儿子。她用布把他包起来放在马槽里，因为客店里没有地方给他们。", version_id: 6},
  {id: 157003, book: "Luke", verse: "2.008", unformatted: "牧羊人与天使们\n当时在同一个地区，有些牧人露宿在野地里，夜间守更看守羊群。", version_id: 6},
  {id: 157004, book: "Luke", verse: "2.009", unformatted: "这时候，主的一位天使站在他们旁边，主的荣光四面照射他们，他们就大为惊恐。", version_id: 6},
  {id: 157005, book: "Luke", verse: "2.01", unformatted: "那位天使对他们说：“不要怕！因为看哪，我传给你们一个大喜的信息，是要给万民的。", version_id: 6},
  {id: 157006, book: "Luke", verse: "2.011", unformatted: "今天，在大卫的城里，为你们诞生了一位救主，他就是主基督。", version_id: 6},
  {id: 157007, book: "Luke", verse: "2.012", unformatted: "你们会看见一个婴孩，用布包着，躺在马槽里；这就是给你们的标记。”", version_id: 6},
  {id: 157008, book: "Luke", verse: "2.013", unformatted: "忽然有一大队天军出现，与那位天使一起赞美神，说：", version_id: 6},
  {id: 157009, book: "Luke", verse: "2.014", unformatted: "“在至高之处，荣耀归于神！\n在地上，平安临到他所喜悦的人！”", version_id: 6},
  {id: 157010, book: "Luke", verse: "2.015", unformatted: "天使们离开他们往天上去了，牧人就彼此说：“来，我们到伯利恒去，看看这件已成就的事，就是主让我们明白的事！”", version_id: 6},
  {id: 157011, book: "Luke", verse: "2.016", unformatted: "于是他们急忙赶去，找到了玛丽亚、约瑟和那躺在马槽里的婴孩。", version_id: 6},
  {id: 157012, book: "Luke", verse: "2.017", unformatted: "他们看见以后，就把天使告诉他们有关这孩子的话传开了。", version_id: 6},
  {id: 157013, book: "Luke", verse: "2.018", unformatted: "所有听见的人，对那些牧人告诉他们的事，都感到惊奇。", version_id: 6},
  {id: 157014, book: "Luke", verse: "2.019", unformatted: "玛丽亚却把这一切事存在心里，反复思想。", version_id: 6},
  {id: 157015, book: "Luke", verse: "2.02", unformatted: "那些牧人因为所见所闻的一切正如天使向他们所说的，就荣耀、赞美神，于是回去了。", version_id: 6},
  {id: 157016, book: "Luke", verse: "2.021", unformatted: "耶稣受割礼\n满了八天，为孩子行割礼的时候，他们就给他起名叫耶稣。这名字是他在母腹成胎以前天使所起的。", version_id: 6},
  {id: 157017, book: "Luke", verse: "2.022", unformatted: "按照摩西的律法，洁净的日子满了以后，约瑟和玛丽亚领着孩子上耶路撒冷去，要把他献给主。", version_id: 6},
  {id: 157018, book: "Luke", verse: "2.023", unformatted: "正如主的律法上所记载的：“所有头胎的男孩都将被称为圣归给主；”", version_id: 6},
  {id: 157019, book: "Luke", verse: "2.024", unformatted: "并按照主的律法上所说的，献上了“一对斑鸠或两只雏鸽”为祭物。", version_id: 6},
  {id: 157020, book: "Luke", verse: "2.025", unformatted: "西面的颂赞\n看哪，在耶路撒冷有一个名叫西面的人。这个人又公义又虔诚，一直期待着以色列得蒙安慰；圣灵也在他身上。", version_id: 6},
  {id: 157021, book: "Luke", verse: "2.026", unformatted: "他得了圣灵的启示：自己在经历死亡以前会看见主的基督。", version_id: 6},
  {id: 157022, book: "Luke", verse: "2.027", unformatted: "他受了圣灵的感动，来到圣殿。这时候，耶稣的父母抱着孩子进来，要按照律法的规矩为他办事。", version_id: 6},
  {id: 157023, book: "Luke", verse: "2.028", unformatted: "西面把孩子抱在怀里，颂赞神说：", version_id: 6},
  {id: 157024, book: "Luke", verse: "2.029", unformatted: "“主啊，\n现在可以照你的话，\n让你的奴仆平平安安地离去，", version_id: 6},
  {id: 157025, book: "Luke", verse: "2.03", unformatted: "因为我的眼睛已经看见你的救恩，", version_id: 6},
  {id: 157026, book: "Luke", verse: "2.031", unformatted: "就是你为万民所预备的：", version_id: 6},
  {id: 157027, book: "Luke", verse: "2.032", unformatted: "他是启示外邦人的光，\n是你子民以色列的荣耀。”", version_id: 6},
  {id: 157028, book: "Luke", verse: "2.033", unformatted: "他的父母对这些关于孩子的话，感到惊奇。", version_id: 6},
  {id: 157029, book: "Luke", verse: "2.034", unformatted: "西面祝福他们，并对孩子的母亲玛丽亚说：“看哪，这孩子注定要使以色列中许多人跌倒、许多人兴起，并要成为一个遭受反对的标记，", version_id: 6},
  {id: 157030, book: "Luke", verse: "2.035", unformatted: "这样，许多人的心思意念将被显露出来；你自己的灵魂也要被剑刺透。”", version_id: 6},
  {id: 157031, book: "Luke", verse: "2.036", unformatted: "安娜的见证\n有一个女先知安娜，是亚设支派法努埃的女儿。她已经上了年纪，是出嫁以后与丈夫生活了七年的，", version_id: 6},
  {id: 157032, book: "Luke", verse: "2.037", unformatted: "后来守寡了，现在已经八十四岁。她从不离开圣殿，禁食祈祷，日夜事奉神。", version_id: 6},
  {id: 157033, book: "Luke", verse: "2.038", unformatted: "就在这时候，她也上前来赞颂神，向所有期待着耶路撒冷得救赎的人讲说这孩子的事。", version_id: 6},
  {id: 157034, book: "Luke", verse: "2.039", unformatted: "返回拿撒勒\n约瑟和玛丽亚按照主的律法办完了一切事以后，就返回加利利，到自己的城拿撒勒去了。", version_id: 6},
  {id: 157035, book: "Luke", verse: "2.04", unformatted: "那孩子渐渐长大，刚强起来，充满智慧，并且有神的恩典在他身上。", version_id: 6},
  {id: 157036, book: "Luke", verse: "2.041", unformatted: "耶稣在父家\n每年逾越节，耶稣的父母都上耶路撒冷去。", version_id: 6},
  {id: 157037, book: "Luke", verse: "2.042", unformatted: "当耶稣十二岁的时候，他们按照节日的规矩上去。", version_id: 6},
  {id: 157038, book: "Luke", verse: "2.043", unformatted: "守完了节期，他们回去的时候，孩童耶稣仍然留在耶路撒冷，他的父母却不知道，", version_id: 6},
  {id: 157039, book: "Luke", verse: "2.044", unformatted: "以为他在同行的人群中。他们走了一天的路，才开始在亲戚和熟人中找他。", version_id: 6},
  {id: 157040, book: "Luke", verse: "2.045", unformatted: "他们找不到他，就回到耶路撒冷去找。", version_id: 6},
  {id: 157041, book: "Luke", verse: "2.046", unformatted: "到了第三天，他们发现耶稣在圣殿里，坐在教师们当中，一边听，一边问。", version_id: 6},
  {id: 157042, book: "Luke", verse: "2.047", unformatted: "所有听他讲说的人，都对他的悟性和对答十分惊讶。", version_id: 6},
  {id: 157043, book: "Luke", verse: "2.048", unformatted: "他的父母看见他，感到吃惊，他的母亲就对他说：“孩子啊，为什么对我们这样做呢？你看，你父亲和我一直在焦急地找你呢！”", version_id: 6},
  {id: 157044, book: "Luke", verse: "2.049", unformatted: "耶稣对他们说：“你们为什么找我呢？难道不知道我必须在我父的家里吗？”", version_id: 6},
  {id: 157045, book: "Luke", verse: "2.05", unformatted: "可是他们没有领悟他对他们说的话。", version_id: 6},
  {id: 157046, book: "Luke", verse: "2.051", unformatted: "神与人的喜爱\n于是耶稣与他们一起下去，回到拿撒勒，一直服从他们。他的母亲把这一切事都珍藏在心里。", version_id: 6},
  {id: 157047, book: "Luke", verse: "2.052", unformatted: "耶稣的智慧和身量，以及神和人对他的喜爱，都不断增长。", version_id: 6},
  {id: 157048, book: "Luke", verse: "3.001", unformatted: "基督的先驱\n凯撒提比略在位第十五年，本丢彼拉多任犹太总督，希律做加利利的分封王，他的弟弟菲利普做以土利亚和特拉克尼地区的分封王，卢萨尼亚做阿比利尼的分封王，", version_id: 6},
  {id: 157049, book: "Luke", verse: "3.002", unformatted: "亚纳和该亚法任大祭司——那时，神的话语在旷野临到撒迦利亚的儿子约翰。", version_id: 6},
  {id: 157050, book: "Luke", verse: "3.003", unformatted: "他就来到约旦河周围所有的地区宣讲为罪得赦免而悔改的洗礼。", version_id: 6},
  {id: 157051, book: "Luke", verse: "3.004", unformatted: "正如先知以赛亚书上所记载的话：\n“在旷野有声音呼喊着：\n‘你们当预备主的道，\n当修直他的路！", version_id: 6},
  {id: 157052, book: "Luke", verse: "3.005", unformatted: "一切山谷将被填满，\n山岭小丘将被削平；\n扭曲的道将要修直，\n崎岖的路将要铺平。", version_id: 6},
  {id: 157053, book: "Luke", verse: "3.006", unformatted: "所有的人都将看见神的救恩。’”", version_id: 6},
  {id: 157054, book: "Luke", verse: "3.007", unformatted: "约翰对那些出来受他洗礼的人群说：“你们这些毒蛇的子孙！谁指示你们逃避那将要来临的震怒呢？", version_id: 6},
  {id: 157055, book: "Luke", verse: "3.008", unformatted: "你们应当结出果子，与悔改的心相称！你们不要自己里面一开始就说‘我们有亚伯拉罕为先祖。’我告诉你们：神能从这些石头中给亚伯拉罕兴起儿女来。", version_id: 6},
  {id: 157056, book: "Luke", verse: "3.009", unformatted: "现在斧子已经放在树根上，所有不结好果子的树都要被砍下来，被丢进火里。”", version_id: 6},
  {id: 157057, book: "Luke", verse: "3.01", unformatted: "众人问他，说：“那么，我们该做什么呢？”", version_id: 6},
  {id: 157058, book: "Luke", verse: "3.011", unformatted: "约翰回答说：“有两件衣服的，当分给那没有的；有食物的，也当如此做。”", version_id: 6},
  {id: 157059, book: "Luke", verse: "3.012", unformatted: "一些税吏也来受洗。他们问约翰：“老师，我们该做什么呢？”", version_id: 6},
  {id: 157060, book: "Luke", verse: "3.013", unformatted: "约翰对他们说：“除了给你们规定的以外，不要多收。”", version_id: 6},
  {id: 157061, book: "Luke", verse: "3.014", unformatted: "有些当兵的也问他，说：“那么我们呢？我们该做什么呢？”\n约翰说：“不要敲诈任何人，也不要勒索人，当以自己的粮饷为满足。”", version_id: 6},
  {id: 157062, book: "Luke", verse: "3.015", unformatted: "当时，民众都在心里对约翰有期待并猜想，或许他就是基督。", version_id: 6},
  {id: 157063, book: "Luke", verse: "3.016", unformatted: "约翰就回答大家，说：“我用水给你们施洗，但有一位比我更强有力的要来，我就是为他解鞋带也不配。他要用圣灵和火给你们施洗。", version_id: 6},
  {id: 157064, book: "Luke", verse: "3.017", unformatted: "他手里拿着扬谷的锨，要扬净他的打谷场，把麦子收进他的仓库，却把糠秕用不灭的火烧掉。”", version_id: 6},
  {id: 157065, book: "Luke", verse: "3.018", unformatted: "这样，约翰又用很多别的话劝民众，向他们传福音。", version_id: 6},
  {id: 157066, book: "Luke", verse: "3.019", unformatted: "分封王希律为了他弟弟的妻子希罗迪娅的事，以及自己所做的一切恶事，受到约翰的责备。", version_id: 6},
  {id: 157067, book: "Luke", verse: "3.02", unformatted: "希律在这一切事上又加了一件，就是把约翰关在监狱里。", version_id: 6},
  {id: 157068, book: "Luke", verse: "3.021", unformatted: "耶稣受洗\n有一次，当民众都来受洗的时候，耶稣也受了洗。他正在祷告，天开了，", version_id: 6},
  {id: 157069, book: "Luke", verse: "3.022", unformatted: "圣灵好像鸽子，有形有体地降临在他身上，并且有声音从天上传来，说：\n“你是我的爱子，\n我喜悦你。”", version_id: 6},
  {id: 157070, book: "Luke", verse: "3.023", unformatted: "耶稣基督的家谱\n耶稣开始传道的时候，大约三十岁。照人看来，\n他是约瑟的儿子，约瑟是希里的儿子，", version_id: 6},
  {id: 157071, book: "Luke", verse: "3.024", unformatted: "希里是玛塔特的儿子，玛塔特是利未的儿子，\n利未是麦基的儿子，麦基是雅拿的儿子，\n雅拿是约瑟的儿子，", version_id: 6},
  {id: 157072, book: "Luke", verse: "3.025", unformatted: "约瑟是玛塔提亚的儿子，\n玛塔提亚是亚摩斯的儿子，亚摩斯是拿鸿的儿子，\n拿鸿是以斯利的儿子，以斯利是拿该的儿子，", version_id: 6},
  {id: 157073, book: "Luke", verse: "3.026", unformatted: "拿该是玛押的儿子，玛押是玛塔提亚的儿子，\n玛塔提亚是西美的儿子，西美是约瑟的儿子，\n约瑟是犹大的儿子，", version_id: 6},
  {id: 157074, book: "Luke", verse: "3.027", unformatted: "犹大是约亚拿的儿子，\n约亚拿是利撒的儿子，利撒是所罗巴伯的儿子，\n所罗巴伯是撒拉铁的儿子，撒拉铁是尼利的儿子，", version_id: 6},
  {id: 157075, book: "Luke", verse: "3.028", unformatted: "尼利是麦基的儿子，麦基是亚底的儿子，\n亚底是哥桑的儿子，哥桑是以摩当的儿子，\n以摩当是珥的儿子，", version_id: 6},
  {id: 157076, book: "Luke", verse: "3.029", unformatted: "珥是约细的儿子，\n约细是以利以谢的儿子，以利以谢是约令的儿子，\n约令是玛塔特的儿子，玛塔特是利未的儿子，", version_id: 6},
  {id: 157077, book: "Luke", verse: "3.03", unformatted: "利未是西缅的儿子，西缅是犹大的儿子，\n犹大是约瑟的儿子，约瑟是约南的儿子，\n约南是以利亚敬的儿子，", version_id: 6},
  {id: 157078, book: "Luke", verse: "3.031", unformatted: "以利亚敬是米利亚的儿子，\n米利亚是迈南的儿子，迈南是玛塔达的儿子，\n玛塔达是拿单的儿子，拿单是大卫的儿子，", version_id: 6},
  {id: 157079, book: "Luke", verse: "3.032", unformatted: "大卫是耶西的儿子，耶西是俄备得的儿子，\n俄备得是波阿斯的儿子，波阿斯是撒门的儿子，\n撒门是拿顺的儿子，", version_id: 6},
  {id: 157080, book: "Luke", verse: "3.033", unformatted: "拿顺是亚米拿达的儿子，\n亚米拿达是亚特民的儿子，亚特民是亚兰的儿子，\n亚兰是希斯仑的儿子，希斯仑是法勒斯的儿子，\n法勒斯是犹大的儿子，", version_id: 6},
  {id: 157081, book: "Luke", verse: "3.034", unformatted: "犹大是雅各的儿子，\n雅各是以撒的儿子，以撒是亚伯拉罕的儿子，\n亚伯拉罕是塔拉的儿子，塔拉是拿鹤的儿子，", version_id: 6},
  {id: 157082, book: "Luke", verse: "3.035", unformatted: "拿鹤是西鹿的儿子，西鹿是拉吴的儿子，\n拉吴是法勒的儿子，法勒是希伯的儿子，\n希伯是沙拉的儿子，", version_id: 6},
  {id: 157083, book: "Luke", verse: "3.036", unformatted: "沙拉是该南的儿子，\n该南是亚法撒的儿子，亚法撒是闪的儿子，\n闪是挪亚的儿子，挪亚是拉迈的儿子，", version_id: 6},
  {id: 157084, book: "Luke", verse: "3.037", unformatted: "拉迈是玛土撒拉的儿子，玛土撒拉是以诺的儿子，\n以诺是雅列的儿子，雅列是玛勒列的儿子，\n玛勒列是该南的儿子，", version_id: 6},
  {id: 157085, book: "Luke", verse: "3.038", unformatted: "该南是以挪士的儿子，\n以挪士是塞特的儿子，塞特是亚当的儿子，\n亚当是神的儿子。", version_id: 6},
  {id: 157086, book: "Luke", verse: "4.001", unformatted: "耶稣受试探\n耶稣充满圣灵，从约旦河回来，被圣灵带到旷野，", version_id: 6},
  {id: 157087, book: "Luke", verse: "4.002", unformatted: "受魔鬼的试探四十天。在那些日子里，耶稣什么也没有吃，四十天结束的时候，他就饿了。", version_id: 6},
  {id: 157088, book: "Luke", verse: "4.003", unformatted: "魔鬼对他说：“你如果是神的儿子，就吩咐这石头变成食物吧！”", version_id: 6},
  {id: 157089, book: "Luke", verse: "4.004", unformatted: "耶稣回答：“经上记着：‘人活着不是单靠食物。’”", version_id: 6},
  {id: 157090, book: "Luke", verse: "4.005", unformatted: "魔鬼就把耶稣领上高处，在一瞬间给他看天下万国，", version_id: 6},
  {id: 157091, book: "Luke", verse: "4.006", unformatted: "对他说：“这一切权柄和荣耀我都将给你，因为这已经交给了我，我愿意给谁就给谁。", version_id: 6},
  {id: 157092, book: "Luke", verse: "4.007", unformatted: "所以，你如果在我面前下拜，这一切就都是你的了。”", version_id: 6},
  {id: 157093, book: "Luke", verse: "4.008", unformatted: "耶稣回答说：“经上记着：\n‘要敬拜主——你的神，\n唯独事奉他。’”", version_id: 6},
  {id: 157094, book: "Luke", verse: "4.009", unformatted: "魔鬼又带耶稣到耶路撒冷，让他站在圣殿的顶端，对他说：“你如果是神的儿子，就从这里跳下去吧！", version_id: 6},
  {id: 157095, book: "Luke", verse: "4.01", unformatted: "因为经上记着：\n‘他会为你吩咐他的天使保护你；", version_id: 6},
  {id: 157096, book: "Luke", verse: "4.011", unformatted: "他们会用手托住你，\n免得你的脚撞在石头上。’”", version_id: 6},
  {id: 157097, book: "Luke", verse: "4.012", unformatted: "耶稣回答说：“经上说：‘不可试探主——你的神。’”", version_id: 6},
  {id: 157098, book: "Luke", verse: "4.013", unformatted: "魔鬼用尽种种试探以后，就暂时离开了耶稣。", version_id: 6},
  {id: 157099, book: "Luke", verse: "4.014", unformatted: "在加利利的服事\n耶稣带着圣灵的能力回到加利利，他的消息传遍了周围所有的地区。", version_id: 6},
  {id: 157100, book: "Luke", verse: "4.015", unformatted: "他在他们的会堂里教导人，受到所有人的赞扬。", version_id: 6},
  {id: 157101, book: "Luke", verse: "4.016", unformatted: "在拿撒勒被拒绝\n耶稣来到拿撒勒，就是他长大的地方。在安息日那天，他照自己的习惯进了会堂，站起来要诵读经文。", version_id: 6},
  {id: 157102, book: "Luke", verse: "4.017", unformatted: "有人就把先知以赛亚的书递给他。他展开书卷，找到一处，那里写道：", version_id: 6},
  {id: 157103, book: "Luke", verse: "4.018", unformatted: "“主的灵在我身上，\n因为他膏立我\n去向贫穷的人传福音，\n他差派我去宣告：\n被掳的得释放，\n瞎眼的得看见，\n让受欺压的得自由，", version_id: 6},
  {id: 157104, book: "Luke", verse: "4.019", unformatted: "宣告主所悦纳的禧年。”", version_id: 6},
  {id: 157105, book: "Luke", verse: "4.02", unformatted: "然后耶稣把书卷好，还给会堂助理，坐了下来。会堂里，众人的眼睛都注视着他。", version_id: 6},
  {id: 157106, book: "Luke", verse: "4.021", unformatted: "耶稣开始对他们说：“今天，在你们耳中的这段经文，已经应验了。”", version_id: 6},
  {id: 157107, book: "Luke", verse: "4.022", unformatted: "大家都赞许他，对他口里所发出的恩典的话语感到惊奇，又说：“这不就是约瑟的儿子吗？”", version_id: 6},
  {id: 157108, book: "Luke", verse: "4.023", unformatted: "耶稣对他们说：“你们一定会用这句谚语对我说：‘医生啊，使你自己痊愈吧！’，‘我们听见在迦百农所发生的事，你在你的家乡这里也应该做！’”", version_id: 6},
  {id: 157109, book: "Luke", verse: "4.024", unformatted: "耶稣又说：“我确实地告诉你们：没有一位先知在自己的家乡是受欢迎的。", version_id: 6},
  {id: 157110, book: "Luke", verse: "4.025", unformatted: "我真的告诉你们：在以利亚的那些日子里，天空闭塞了三年六个月，遍地有大饥荒，那时，在以色列有许多寡妇；", version_id: 6},
  {id: 157111, book: "Luke", verse: "4.026", unformatted: "可是以利亚并没有被派遣到她们中的任何一个人那里，只被派遣到西顿的撒勒法一个寡妇那里。", version_id: 6},
  {id: 157112, book: "Luke", verse: "4.027", unformatted: "还有，在先知以利沙的时代，在以色列有许多麻风病人；可是除了叙利亚人奈曼以外，没有一个人被洁净。”", version_id: 6},
  {id: 157113, book: "Luke", verse: "4.028", unformatted: "会堂里的人听了这些话，都满腔怒火。", version_id: 6},
  {id: 157114, book: "Luke", verse: "4.029", unformatted: "他们就起来把耶稣赶出城外，拉到山崖上，要把他推下去。他们的城是建在山上的。", version_id: 6},
  {id: 157115, book: "Luke", verse: "4.03", unformatted: "但耶稣从他们中间穿过去，走开了。", version_id: 6},
  {id: 157116, book: "Luke", verse: "4.031", unformatted: "驱赶鬼魔\n耶稣下到加利利的一个城迦百农去，在安息日教导人。", version_id: 6},
  {id: 157117, book: "Luke", verse: "4.032", unformatted: "众人对他的教导都惊叹不已，因为他的话具有权柄。", version_id: 6},
  {id: 157118, book: "Luke", verse: "4.033", unformatted: "在会堂里，有一个被污秽的鬼魔之灵附身的人。他大声喊叫：", version_id: 6},
  {id: 157119, book: "Luke", verse: "4.034", unformatted: "“别管我们！拿撒勒人耶稣，我们与你有什么关系？你来毁灭我们吗？我知道你是谁，你是神的那位圣者。”", version_id: 6},
  {id: 157120, book: "Luke", verse: "4.035", unformatted: "耶稣斥责污灵说：“住口！从他身上出来！”\n鬼魔把那个人摔倒在众人中间，就从他身上出来了，但没有伤害他。", version_id: 6},
  {id: 157121, book: "Luke", verse: "4.036", unformatted: "大家都惊讶不已，彼此谈论说：“这是什么事呢？他带着权柄和能力命令那些污灵，它们就出来了！”", version_id: 6},
  {id: 157122, book: "Luke", verse: "4.037", unformatted: "于是耶稣的名声传遍了那一带所有的地方。", version_id: 6},
  {id: 157123, book: "Luke", verse: "4.038", unformatted: "在迦百农治病\n耶稣起身离开会堂，进了西门的家。西门的岳母正发高烧，他们就为她求耶稣。", version_id: 6},
  {id: 157124, book: "Luke", verse: "4.039", unformatted: "耶稣就站在她旁边，斥责那高烧，烧就退了。她立刻起来，服事他们。", version_id: 6},
  {id: 157125, book: "Luke", verse: "4.04", unformatted: "日落的时候，众人把患有各种疾病的人都带到耶稣那里。他按手在他们每一个人身上，使他们痊愈。", version_id: 6},
  {id: 157126, book: "Luke", verse: "4.041", unformatted: "也有鬼魔从许多人身上出来，喊叫说：“你是神的儿子！”耶稣斥责它们，不让它们说话，因为它们知道他是基督。", version_id: 6},
  {id: 157127, book: "Luke", verse: "4.042", unformatted: "在加利利传道\n天亮的时候，耶稣出去，走到一个荒僻的地方。众人寻找他，来到他那里，想留住他，不让他离开。", version_id: 6},
  {id: 157128, book: "Luke", verse: "4.043", unformatted: "但是耶稣对他们说：“我还必须到别的城去传神国的福音，因为我是为此奉差派的。”", version_id: 6},
  {id: 157129, book: "Luke", verse: "4.044", unformatted: "于是耶稣继续在加利利地区的各会堂里传道。", version_id: 6},
  {id: 157130, book: "Luke", verse: "5.001", unformatted: "第一批门徒\n有一次，当人群拥挤着耶稣，听神的话语的时候，他正站在革尼撒勒湖边。", version_id: 6},
  {id: 157131, book: "Luke", verse: "5.002", unformatted: "他看见湖边停着两条船，渔夫们下了船，正在洗网。", version_id: 6},
  {id: 157132, book: "Luke", verse: "5.003", unformatted: "有一条船是西门的，耶稣上去，请他把船划出去，稍微离岸。耶稣就坐下来，从船上教导众人。", version_id: 6},
  {id: 157133, book: "Luke", verse: "5.004", unformatted: "讲完了以后，他对西门说：“把船划到水深的地方，下网捕鱼吧。”", version_id: 6},
  {id: 157134, book: "Luke", verse: "5.005", unformatted: "西门回答说：“老师，我们劳碌了一整夜，什么也没有捕到。不过按照你的话，我就下网吧。”", version_id: 6},
  {id: 157135, book: "Luke", verse: "5.006", unformatted: "他们这样做了，网住了一大群鱼，渔网几乎要撑破。", version_id: 6},
  {id: 157136, book: "Luke", verse: "5.007", unformatted: "他们就招呼另一条船上的伙伴来帮忙；他们过来，装满了两条船，甚至两条船都几乎要沉下去了。", version_id: 6},
  {id: 157137, book: "Luke", verse: "5.008", unformatted: "西门彼得看见，就俯伏在耶稣脚前，说：“主啊，离开我吧，因为我是个罪人！”", version_id: 6},
  {id: 157138, book: "Luke", verse: "5.009", unformatted: "那时，他和所有与他一起的人，都对这一网捕到的鱼惊讶不已。", version_id: 6},
  {id: 157139, book: "Luke", verse: "5.01", unformatted: "西门的伙伴，就是西庇太的儿子雅各和约翰，也同样惊讶。\n耶稣对西门说：“不要怕！从今以后，你将得人了。”", version_id: 6},
  {id: 157140, book: "Luke", verse: "5.011", unformatted: "他们把两条船靠了岸，舍弃一切跟从了耶稣。", version_id: 6},
  {id: 157141, book: "Luke", verse: "5.012", unformatted: "洁净麻风病人\n有一次，耶稣在一个城里，忽然有个浑身长满麻风的人，看见耶稣就把脸伏在地上，央求他说：“主啊，如果你愿意，你就能洁净我。”", version_id: 6},
  {id: 157142, book: "Luke", verse: "5.013", unformatted: "耶稣伸出手来摸他，说：“我愿意，你洁净了吧！”麻风病立刻离开了他。", version_id: 6},
  {id: 157143, book: "Luke", verse: "5.014", unformatted: "耶稣吩咐他不要告诉任何人，说：“只要去把自己给祭司看，并且为了你的洁净，照着摩西所吩咐的去献上祭物，好对他们做见证。”", version_id: 6},
  {id: 157144, book: "Luke", verse: "5.015", unformatted: "耶稣的消息反而越发传开了。有一大群人聚集而来，听他传道，也要他们的病得痊愈。", version_id: 6},
  {id: 157145, book: "Luke", verse: "5.016", unformatted: "耶稣却常常退到旷野去祷告。", version_id: 6},
  {id: 157146, book: "Luke", verse: "5.017", unformatted: "赦罪与治病\n就在那些日子里，有一天耶稣在教导人的时候，也有些法利赛人和律法教师坐在那里，他们是从加利利和犹太地区的各村庄，以及耶路撒冷来的。主的大能与耶稣同在，使他能医病。", version_id: 6},
  {id: 157147, book: "Luke", verse: "5.018", unformatted: "这时候，有几个人用床榻抬来一个瘫痪的人，想把他带进去放在耶稣面前。", version_id: 6},
  {id: 157148, book: "Luke", verse: "5.019", unformatted: "但由于人多，他们没有办法把他带进去，就爬上屋顶，从屋瓦中间把他和床榻一起缒到众人当中，正在耶稣面前。", version_id: 6},
  {id: 157149, book: "Luke", verse: "5.02", unformatted: "耶稣看见他们的信心，就对瘫痪的人说：“朋友，你的罪孽已经被赦免了。”", version_id: 6},
  {id: 157150, book: "Luke", verse: "5.021", unformatted: "那些经文士和法利赛人就开始想：“这个人是谁？竟说亵渎的话。除了神一位之外，谁能赦免罪呢？”", version_id: 6},
  {id: 157151, book: "Luke", verse: "5.022", unformatted: "耶稣知道他们的意念，就对他们说：“你们心里为什么这样想呢？", version_id: 6},
  {id: 157152, book: "Luke", verse: "5.023", unformatted: "或说‘你的罪孽已经被赦免了’，或说‘你起来走路’，哪一样更容易呢？", version_id: 6},
  {id: 157153, book: "Luke", verse: "5.024", unformatted: "不过为要使你们知道人子在地上有赦免罪的权柄——”耶稣就对瘫痪的人说：“我吩咐你：起来，拿起你的床榻，回家去吧！”", version_id: 6},
  {id: 157154, book: "Luke", verse: "5.025", unformatted: "那个人立刻在大家面前站了起来，拿起他所躺的床榻回家去，一路上荣耀神。", version_id: 6},
  {id: 157155, book: "Luke", verse: "5.026", unformatted: "于是大家都惊讶不已，就不住地荣耀神，并且满怀敬畏之心说：“我们今天看到奇妙的事了！”", version_id: 6},
  {id: 157156, book: "Luke", verse: "5.027", unformatted: "呼召利未\n这些事以后，耶稣出去，看到一个名叫利未的税吏，在税关坐着。耶稣对他说：“你跟从我！”", version_id: 6},
  {id: 157157, book: "Luke", verse: "5.028", unformatted: "利未就撇下一切，站起来跟从了耶稣。", version_id: 6},
  {id: 157158, book: "Luke", verse: "5.029", unformatted: "与罪人吃饭\n利未在自己家里为耶稣预备盛大的宴席，有一大群税吏和其他人与他们一同坐席。", version_id: 6},
  {id: 157159, book: "Luke", verse: "5.03", unformatted: "法利赛人和他们的经文士就向耶稣的门徒们抱怨，说：“你们为什么与那些税吏和罪人一起吃喝呢？”", version_id: 6},
  {id: 157160, book: "Luke", verse: "5.031", unformatted: "耶稣对他们说：“健康的人不需要医生，有病的人才需要。", version_id: 6},
  {id: 157161, book: "Luke", verse: "5.032", unformatted: "我来不是要召唤义人，而是要召唤罪人来悔改。”", version_id: 6},
  {id: 157162, book: "Luke", verse: "5.033", unformatted: "禁食的问题\n他们对耶稣说：“约翰的门徒们经常禁食、祈祷，法利赛人的门徒们也是如此，而你的门徒们却又吃又喝。”", version_id: 6},
  {id: 157163, book: "Luke", verse: "5.034", unformatted: "耶稣对他们说：“新郎与宾客在一起的时候，难道你们能叫宾客禁食吗？", version_id: 6},
  {id: 157164, book: "Luke", verse: "5.035", unformatted: "可是日子将要来到：当新郎从他们中间被带走的时候，他们那时——在那些日子里就要禁食了。”", version_id: 6},
  {id: 157165, book: "Luke", verse: "5.036", unformatted: "耶稣又对他们讲了一个比喻：“没有人从新衣服上撕下一块布来缝在旧衣服上，否则，他不但撕破了新衣服，而且来自新衣服的那块布与旧衣服不相称。", version_id: 6},
  {id: 157166, book: "Luke", verse: "5.037", unformatted: "也没有人把新酒装在旧皮袋里，否则新酒会胀破旧皮袋，酒会流出来，皮袋也会糟蹋了。", version_id: 6},
  {id: 157167, book: "Luke", verse: "5.038", unformatted: "因此，新酒必须装在新皮袋里。", version_id: 6},
  {id: 157168, book: "Luke", verse: "5.039", unformatted: "没有人喝了陈酒以后想喝新酒，因为他总说：‘还是陈酒好。’”", version_id: 6},
  {id: 157169, book: "Luke", verse: "6.001", unformatted: "安息日的主\n有一次在安息日，耶稣从麦田经过，他的门徒们摘下麦穗，用手搓着吃。", version_id: 6},
  {id: 157170, book: "Luke", verse: "6.002", unformatted: "有一些法利赛人说：“你们为什么做安息日不可以做的事呢？”", version_id: 6},
  {id: 157171, book: "Luke", verse: "6.003", unformatted: "耶稣回答他们，说：“大卫和那些与他在一起的人饥饿时所做的事，你们难道这个也没有读过吗？", version_id: 6},
  {id: 157172, book: "Luke", verse: "6.004", unformatted: "他怎样进了神的殿，拿了陈设饼吃，还分给与他在一起的人呢？这饼除了祭司一个人之外，谁都不可以吃。”", version_id: 6},
  {id: 157173, book: "Luke", verse: "6.005", unformatted: "于是耶稣对他们说：“人子是安息日的主。”", version_id: 6},
  {id: 157174, book: "Luke", verse: "6.006", unformatted: "治愈手萎缩的人\n在另一个安息日的时候，耶稣进了会堂教导人。那里有一个人，右手枯萎了。", version_id: 6},
  {id: 157175, book: "Luke", verse: "6.007", unformatted: "经文士们和法利赛人就密切注意耶稣会不会在安息日使人痊愈，为要找把柄控告他。", version_id: 6},
  {id: 157176, book: "Luke", verse: "6.008", unformatted: "耶稣知道他们的意念，就对那手枯萎的人说：“起来，站到中间来！”那人就起来，站着。", version_id: 6},
  {id: 157177, book: "Luke", verse: "6.009", unformatted: "耶稣对他们说：“我问你们，在安息日是否可以行善或作恶，救命或害命？”", version_id: 6},
  {id: 157178, book: "Luke", verse: "6.01", unformatted: "他环视周围所有的人，然后对那个人说：“伸出你的手！”那个人一伸，他的手就复原了。", version_id: 6},
  {id: 157179, book: "Luke", verse: "6.011", unformatted: "他们就满腔怒火，彼此商量到底怎么对付耶稣。", version_id: 6},
  {id: 157180, book: "Luke", verse: "6.012", unformatted: "选定十二使徒\n就在那些日子里，耶稣有一次出去到山上祷告；他整夜向神祷告。", version_id: 6},
  {id: 157181, book: "Luke", verse: "6.013", unformatted: "一到天亮，他就招呼他的门徒，从他们中间拣选了十二个人，称他们为使徒：", version_id: 6},
  {id: 157182, book: "Luke", verse: "6.014", unformatted: "西门——耶稣又称他为彼得，\n他的弟弟安得烈，\n又有雅各、约翰、\n腓力、巴多罗迈、", version_id: 6},
  {id: 157183, book: "Luke", verse: "6.015", unformatted: "马太、多马，\n亚勒腓的儿子雅各，\n称为激进派的西门，", version_id: 6},
  {id: 157184, book: "Luke", verse: "6.016", unformatted: "雅各的儿子犹大，\n还有那成为出卖者的加略人犹大。", version_id: 6},
  {id: 157185, book: "Luke", verse: "6.017", unformatted: "教导、治病\n耶稣与他们一起下了山，站在一块平地上。那里有他的一大群门徒，还有来自整个犹太地区、耶路撒冷以及沿海的提尔和西顿的一大群人。", version_id: 6},
  {id: 157186, book: "Luke", verse: "6.018", unformatted: "他们来是要听他讲说，要他们的病得痊愈。那些被污灵折磨的，都得了痊愈。", version_id: 6},
  {id: 157187, book: "Luke", verse: "6.019", unformatted: "众人都想要摸到他，因为有能力从他身上发出，使所有的人得痊愈。", version_id: 6},
  {id: 157188, book: "Luke", verse: "6.02", unformatted: "论福\n耶稣举目看着他的门徒们，说：\n“贫乏的人是蒙福的，\n因为神的国是你们的。", version_id: 6},
  {id: 157189, book: "Luke", verse: "6.021", unformatted: "如今饥饿的人是蒙福的，\n因为你们将得饱足。\n如今哭泣的人是蒙福的，\n因为你们将要欢笑。", version_id: 6},
  {id: 157190, book: "Luke", verse: "6.022", unformatted: "当人们因人子的缘故憎恨你们，\n排斥你们，责骂你们，\n把你们当做恶人革除你们的名，\n你们就是蒙福的。", version_id: 6},
  {id: 157191, book: "Luke", verse: "6.023", unformatted: "“在那一天，你们当欢喜，当欢然跳跃，因为看哪，你们在天上的报偿是大的。要知道，那些人的祖先也是同样对待先知们的。", version_id: 6},
  {id: 157192, book: "Luke", verse: "6.024", unformatted: "论祸\n“不过富有的人，你们有祸了，\n因为你们已经得到了你们的安慰。", version_id: 6},
  {id: 157193, book: "Luke", verse: "6.025", unformatted: "如今饱足的人，你们有祸了，\n因为你们将要饥饿。\n如今欢笑的人，你们有祸了，\n因为你们将要悲伤、哭泣。", version_id: 6},
  {id: 157194, book: "Luke", verse: "6.026", unformatted: "当所有的人都说你们好的时候，\n你们有祸了，\n因为他们的祖先也是同样对待假先知们的。", version_id: 6},
  {id: 157195, book: "Luke", verse: "6.027", unformatted: "爱敌人\n“然而我告诉你们这些正在听的人：要爱你们的敌人，善待那些恨你们的人；", version_id: 6},
  {id: 157196, book: "Luke", verse: "6.028", unformatted: "要祝福那些诅咒你们的人，为那些诋毁你们的人祷告。", version_id: 6},
  {id: 157197, book: "Luke", verse: "6.029", unformatted: "有人打你这边的脸，把另一边也让他打；有人拿走你的外衣，连里衣也不要阻止他拿；", version_id: 6},
  {id: 157198, book: "Luke", verse: "6.03", unformatted: "任何人求你，都要给他；有人拿走你的东西，不要讨回来。", version_id: 6},
  {id: 157199, book: "Luke", verse: "6.031", unformatted: "你们希望别人怎样对待你们，你们也应当怎样对待别人。", version_id: 6},
  {id: 157200, book: "Luke", verse: "6.032", unformatted: "如果你们只爱那些爱你们的人，难道有什么可称赞的吗？其实连罪人也爱那些爱他们的人。", version_id: 6},
  {id: 157201, book: "Luke", verse: "6.033", unformatted: "如果你们只善待那些善待你们的人，难道有什么可称赞的吗？连罪人也这样做。", version_id: 6},
  {id: 157202, book: "Luke", verse: "6.034", unformatted: "如果你们借东西，只借给那些有指望能从他们收取的人，那有什么可称赞的呢？就是罪人也借给罪人，为要如数收回。", version_id: 6},
  {id: 157203, book: "Luke", verse: "6.035", unformatted: "不过你们要爱你们的敌人，要善待人；借出去，不指望任何偿还。这样，你们的报偿就大了，而且你们将成为至高者的儿女，因为他以仁慈待那些忘恩负义和邪恶的人。", version_id: 6},
  {id: 157204, book: "Luke", verse: "6.036", unformatted: "你们要仁慈，正如你们的父是仁慈的。", version_id: 6},
  {id: 157205, book: "Luke", verse: "6.037", unformatted: "不要评断\n“不要评断人，你们就绝不被评断；不要定人的罪，你们就绝不被定罪；要饶恕人，你们就会被饶恕。", version_id: 6},
  {id: 157206, book: "Luke", verse: "6.038", unformatted: "你们要给与，就会被赐予，而且用十足的量器，连摇带按、满满当当地倒在你们怀里，因为，你们用什么量器来衡量，也会同样地被衡量。”", version_id: 6},
  {id: 157207, book: "Luke", verse: "6.039", unformatted: "耶稣又对他们讲了一个比喻：“难道瞎子能给瞎子领路吗？两个人不都会掉进坑里吗？", version_id: 6},
  {id: 157208, book: "Luke", verse: "6.04", unformatted: "学生不高于他的老师，但所有学成的人，将会像他的老师一样。", version_id: 6},
  {id: 157209, book: "Luke", verse: "6.041", unformatted: "“你为什么看见你弟兄眼里的木屑，却不想自己眼里的梁木呢？", version_id: 6},
  {id: 157210, book: "Luke", verse: "6.042", unformatted: "你看不见自己眼里的梁木，怎么能对你弟兄说‘弟兄啊，让我除掉你眼里的木屑’呢？你这伪善的人！先除掉你自己眼里的梁木，然后你才能看得清楚，好除掉你弟兄眼里的木屑。", version_id: 6},
  {id: 157211, book: "Luke", verse: "6.043", unformatted: "树与果\n“好树不结坏果子，坏树也不结好果子。", version_id: 6},
  {id: 157212, book: "Luke", verse: "6.044", unformatted: "这样，每一棵树都是凭着它的果子被认出来。人不从蒺藜中收无花果，也不从荆棘上摘葡萄。", version_id: 6},
  {id: 157213, book: "Luke", verse: "6.045", unformatted: "好人从他心里所存的善，发出善来；坏人从他心里所存的恶，发出恶来；因为人心里所充满的，口里就说出来。", version_id: 6},
  {id: 157214, book: "Luke", verse: "6.046", unformatted: "两种根基\n“你们为什么称呼我‘主啊，主啊！’却不遵行我所说的话呢？", version_id: 6},
  {id: 157215, book: "Luke", verse: "6.047", unformatted: "我要指示你们，凡是到我这里来听了我的话而实行的人是什么样的人：", version_id: 6},
  {id: 157216, book: "Luke", verse: "6.048", unformatted: "他好比一个人建造房子，深深地挖掘，把根基立在磐石上。洪水泛滥，急流冲击那房子，也不能使它摇动，因为建造得稳固。", version_id: 6},
  {id: 157217, book: "Luke", verse: "6.049", unformatted: "然而，那听见而不实行的人，就好比一个人把房子建在地面上，没有根基。急流一冲，立刻倒塌了，并且那房子毁坏得非常厉害。”", version_id: 6},
  {id: 157218, book: "Luke", verse: "7.001", unformatted: "百夫长的信心\n当耶稣向民众说完了这一切话，就进了迦百农。", version_id: 6},
  {id: 157219, book: "Luke", verse: "7.002", unformatted: "有个百夫长所重视的奴仆病得快要死了。", version_id: 6},
  {id: 157220, book: "Luke", verse: "7.003", unformatted: "百夫长听说了耶稣的事，就托几个犹太人的长老到耶稣那里，求耶稣来救他的奴仆。", version_id: 6},
  {id: 157221, book: "Luke", verse: "7.004", unformatted: "他们来到耶稣那里，殷切地求他说：“这人配得你的帮助，", version_id: 6},
  {id: 157222, book: "Luke", verse: "7.005", unformatted: "因为他爱我们的同胞，还为我们建了会堂。”", version_id: 6},
  {id: 157223, book: "Luke", verse: "7.006", unformatted: "于是耶稣与他们一起去了。当耶稣离他家不远的时候，百夫长又托了几个朋友来对耶稣说：“主啊，不用麻烦了，我实在不配请你进我家，", version_id: 6},
  {id: 157224, book: "Luke", verse: "7.007", unformatted: "我自己也觉得不配到你那里去。只要你说一句话，我的仆人就会痊愈的。", version_id: 6},
  {id: 157225, book: "Luke", verse: "7.008", unformatted: "事实上，我也被放在人的权下，也有士兵在我之下。我对这个说‘去’，他就去；对那个说‘来’，他就来；对我的奴仆说‘做这事’，他就去做。”", version_id: 6},
  {id: 157226, book: "Luke", verse: "7.009", unformatted: "耶稣听了这些话，十分感慨，转过身来对跟随他的那群人说：“我告诉你们：在以色列我没有见过这么大的信心。”", version_id: 6},
  {id: 157227, book: "Luke", verse: "7.01", unformatted: "那些受托的人回到家里时，发现那奴仆已经痊愈了。", version_id: 6},
  {id: 157228, book: "Luke", verse: "7.011", unformatted: "寡妇之子复活\n第二天，耶稣往一个叫做拿因的城去，他的门徒们和一大群人也与他同行。", version_id: 6},
  {id: 157229, book: "Luke", verse: "7.012", unformatted: "当他接近城门口的时候，看哪，有一个死人被抬出来了。这人是独生儿子，他的母亲又是寡妇。一大群城里的人陪着她。", version_id: 6},
  {id: 157230, book: "Luke", verse: "7.013", unformatted: "主看见那寡妇，就对她动了怜悯之心，向她说：“不要哭了！”", version_id: 6},
  {id: 157231, book: "Luke", verse: "7.014", unformatted: "于是耶稣上前去，按着棺架，抬棺架的人就停住了。耶稣说：“年轻人，我吩咐你起来！”", version_id: 6},
  {id: 157232, book: "Luke", verse: "7.015", unformatted: "那死人就坐了起来，开始说话。耶稣把他交给他的母亲。", version_id: 6},
  {id: 157233, book: "Luke", verse: "7.016", unformatted: "每个人敬畏不已，就不住地荣耀神，说：“我们中间兴起了一位伟大的先知！”又说：“神临到他的子民了！”", version_id: 6},
  {id: 157234, book: "Luke", verse: "7.017", unformatted: "于是有关耶稣的这消息传遍了整个犹太和周围所有的地区。", version_id: 6},
  {id: 157235, book: "Luke", verse: "7.018", unformatted: "论施洗约翰\n约翰的门徒们把这一切事都告诉了约翰。他就叫来他的两个门徒，", version_id: 6},
  {id: 157236, book: "Luke", verse: "7.019", unformatted: "派他们到主那里去问：“将要来的那一位就是你吗？还是我们应该等候另一位呢？”", version_id: 6},
  {id: 157237, book: "Luke", verse: "7.02", unformatted: "那两个人来到耶稣那里，说：“施洗者约翰派我们来问你：‘将要来的那一位就是你吗？还是我们应该等候另一位呢？’”", version_id: 6},
  {id: 157238, book: "Luke", verse: "7.021", unformatted: "就在那时候，耶稣治愈了许多有疾病的、有苦痛的、被邪灵附着的人，又施恩给许多瞎眼的使他们能看见。", version_id: 6},
  {id: 157239, book: "Luke", verse: "7.022", unformatted: "于是耶稣回答那两个人，说：“你们去把所看见、所听见的告诉约翰，就是：瞎眼的得以看见，瘸腿的行走，麻风病人被洁净，耳聋的听见，死人得以复活，穷人得以听到福音。", version_id: 6},
  {id: 157240, book: "Luke", verse: "7.023", unformatted: "凡不因我被绊倒的人，就蒙福了。”", version_id: 6},
  {id: 157241, book: "Luke", verse: "7.024", unformatted: "约翰的使者离开了，耶稣就开始对众人谈起约翰，说：“你们曾经到旷野去，是要观看什么呢？一根被风吹动的芦苇吗？", version_id: 6},
  {id: 157242, book: "Luke", verse: "7.025", unformatted: "你们出去，究竟是要看什么呢？一个身穿细软衣服的人吗？看哪，那些穿华丽衣服、生活放荡的人在王宫里。", version_id: 6},
  {id: 157243, book: "Luke", verse: "7.026", unformatted: "你们出去，究竟是要看什么呢？一位先知吗？是的，我告诉你们：他比先知还大。", version_id: 6},
  {id: 157244, book: "Luke", verse: "7.027", unformatted: "他就是经上记载的那个人：\n‘看哪，我要差派我的使者在你前头，\n他要在你之前预备你的道。’", version_id: 6},
  {id: 157245, book: "Luke", verse: "7.028", unformatted: "“我告诉你们：在女人所生的人中，没有一个比约翰更大的；然而在神的国里，再小的也比约翰更大。”", version_id: 6},
  {id: 157246, book: "Luke", verse: "7.029", unformatted: "所有已经受过约翰洗礼的民众和税吏，听了这话，都承认神是义的；", version_id: 6},
  {id: 157247, book: "Luke", verse: "7.03", unformatted: "而没有受过他洗礼的法利赛人和律法师们，就弃绝了神对他们的计划。", version_id: 6},
  {id: 157248, book: "Luke", verse: "7.031", unformatted: "麻木的世代\n主又说：“我要把这世代的人比做什么呢？他们好比什么呢？", version_id: 6},
  {id: 157249, book: "Luke", verse: "7.032", unformatted: "他们就好比：孩子们坐在街市上，彼此呼叫，说：\n‘我们为你们吹笛，\n你们却不跳舞；\n我们唱哀歌，\n你们也不哭泣。’", version_id: 6},
  {id: 157250, book: "Luke", verse: "7.033", unformatted: "“因为施洗者约翰来了，不吃饼，也不喝酒，你们就说：‘他有鬼魔附身！’", version_id: 6},
  {id: 157251, book: "Luke", verse: "7.034", unformatted: "人子来了，也吃也喝，你们就说：‘看，这个贪食好酒的人！税吏和罪人的朋友！’", version_id: 6},
  {id: 157252, book: "Luke", verse: "7.035", unformatted: "然而，智慧是凭着智慧的众儿女被证明为义的。”", version_id: 6},
  {id: 157253, book: "Luke", verse: "7.036", unformatted: "赦免多爱就多\n有一个法利赛人请耶稣与他一起吃饭，耶稣就进到那法利赛人的家里坐席。", version_id: 6},
  {id: 157254, book: "Luke", verse: "7.037", unformatted: "当时，城里有一个女人，是个罪人，她知道耶稣在那法利赛人家里坐席，就拿着一个盛了香液的玉瓶来。", version_id: 6},
  {id: 157255, book: "Luke", verse: "7.038", unformatted: "她站在耶稣后面，在他的脚边哭泣，开始用泪水沾湿耶稣的脚，再用自己的头发擦干，又亲吻他的脚，用香液膏抹。", version_id: 6},
  {id: 157256, book: "Luke", verse: "7.039", unformatted: "那邀请耶稣的法利赛人看见了，心里说：“这个人如果是先知，就会知道摸他的是谁，是个什么样的女人，因为她是个罪人。”", version_id: 6},
  {id: 157257, book: "Luke", verse: "7.04", unformatted: "耶稣对他说：“西门，我有话要对你说。”\n西门说：“老师，请说。”", version_id: 6},
  {id: 157258, book: "Luke", verse: "7.041", unformatted: "“有一个债主有两个欠债人。一个欠五百个银币，另一个欠五十个银币。", version_id: 6},
  {id: 157259, book: "Luke", verse: "7.042", unformatted: "他们无力偿还，债主就免了他们两个人的债。那么，你说他们哪一个会更爱那债主呢？”", version_id: 6},
  {id: 157260, book: "Luke", verse: "7.043", unformatted: "西门回答说：“我想是债主免债更多的那一个。”\n耶稣说：“你判断得对。”", version_id: 6},
  {id: 157261, book: "Luke", verse: "7.044", unformatted: "于是，他转向那女人，对西门说：“你看见这个女人吗？我进了你的家，你没有给我水洗脚，但她却用泪水沾湿我的脚，又用她的头发擦干。", version_id: 6},
  {id: 157262, book: "Luke", verse: "7.045", unformatted: "你没有向我行亲吻礼，而她从我进来的时候起，就不停地亲我的脚。", version_id: 6},
  {id: 157263, book: "Luke", verse: "7.046", unformatted: "你没有用油来膏抹我的头，她却用香液膏抹我的脚。", version_id: 6},
  {id: 157264, book: "Luke", verse: "7.047", unformatted: "故此，我告诉你：她的罪孽被赦免得多，所以她爱得多；而那被赦免少的，就爱得少。”", version_id: 6},
  {id: 157265, book: "Luke", verse: "7.048", unformatted: "接着，耶稣对那女人说：“你的罪孽已经被赦免了。”", version_id: 6},
  {id: 157266, book: "Luke", verse: "7.049", unformatted: "那些与他一同坐席的人就心里想：“这个人是谁，竟然赦免罪？”", version_id: 6},
  {id: 157267, book: "Luke", verse: "7.05", unformatted: "但耶稣对那女人说：“你的信救了你，平平安安地去吧。”", version_id: 6},
  {id: 157268, book: "Luke", verse: "8.001", unformatted: "供应耶稣的妇女们\n不久以后，耶稣走遍各个城镇乡村，讲道、传神国的福音。与他在一起的有十二使徒。", version_id: 6},
  {id: 157269, book: "Luke", verse: "8.002", unformatted: "还有一些从邪灵和疾病中被解脱出来、得了痊愈的妇女，其中有那称为茉大拉的玛丽亚，从她身上曾经有七个鬼魔被赶出来，", version_id: 6},
  {id: 157270, book: "Luke", verse: "8.003", unformatted: "又有希律的管家苦撒的妻子约亚娜，以及苏珊娜和许多别的妇女，她们都用自己所拥有的来服事耶稣和门徒们。", version_id: 6},
  {id: 157271, book: "Luke", verse: "8.004", unformatted: "撒种的比喻\n那时，有一大群人聚集，又有人从各城镇来到耶稣那里，耶稣就用比喻说：", version_id: 6},
  {id: 157272, book: "Luke", verse: "8.005", unformatted: "“有一个撒种的出去撒种。他撒的时候，有的种子落在路边，被人践踏，天空的飞鸟也把它吃掉了。", version_id: 6},
  {id: 157273, book: "Luke", verse: "8.006", unformatted: "有的落在岩石上，一长出来就枯萎了，因为得不到水份。", version_id: 6},
  {id: 157274, book: "Luke", verse: "8.007", unformatted: "有的落进荆棘丛里，荆棘与它一起生长，把它挤住了。", version_id: 6},
  {id: 157275, book: "Luke", verse: "8.008", unformatted: "但另有落进好土壤里的，长起来，结出百倍的果实。”耶稣讲完这些话，就大声说：“凡是有耳可听的，就应当听！”", version_id: 6},
  {id: 157276, book: "Luke", verse: "8.009", unformatted: "用比喻的原因\n耶稣的门徒们问他这比喻到底是什么意思。", version_id: 6},
  {id: 157277, book: "Luke", verse: "8.01", unformatted: "他说：“神国的奥秘已经赐下来让你们知道，但是对其他的人就用比喻。这是为要使\n‘他们看，却看不见；\n听，却不领悟。’", version_id: 6},
  {id: 157278, book: "Luke", verse: "8.011", unformatted: "解明撒种的比喻\n“原来，这个比喻是这样的。那种子就是神的话语。", version_id: 6},
  {id: 157279, book: "Luke", verse: "8.012", unformatted: "那‘路边之地’，是这样的人：他们听了这话语，然后魔鬼就来，从他们心里把话语夺走，免得他们信而得救。", version_id: 6},
  {id: 157280, book: "Luke", verse: "8.013", unformatted: "那‘岩石’，是这样的人：他们一旦听了，就怀着喜乐的心接受了这话语；可是这些人没有根，他们暂时相信，而在试炼的时候，就离开信仰了。", version_id: 6},
  {id: 157281, book: "Luke", verse: "8.014", unformatted: "种子落在其中的那‘荆棘丛’，是这样的人：他们听了，却走自己的路，被今生的忧虑、财富和享乐挤住了，结不出成熟的果实。", version_id: 6},
  {id: 157282, book: "Luke", verse: "8.015", unformatted: "那‘好土壤’，是这样的人：他们听了这话语，以诚实美善的心持守它，并且在忍耐中结出果实。", version_id: 6},
  {id: 157283, book: "Luke", verse: "8.016", unformatted: "油灯的比喻\n“没有人点上油灯，用器皿盖住或放在床底下，而是放在灯台上，好让进来的人能看见光。", version_id: 6},
  {id: 157284, book: "Luke", verse: "8.017", unformatted: "隐秘的事没有不显露的，隐藏的事绝没有不被知道、不显露出来的。", version_id: 6},
  {id: 157285, book: "Luke", verse: "8.018", unformatted: "所以，你们要留心怎样去听；因为凡是有的，还要赐给他；凡是没有的，连他自以为有的也将从他那里被拿走。”", version_id: 6},
  {id: 157286, book: "Luke", verse: "8.019", unformatted: "真正的亲属\n耶稣的母亲和弟弟们来到他那里，可是由于人多，不能靠近他。", version_id: 6},
  {id: 157287, book: "Luke", verse: "8.02", unformatted: "有人告诉他：“你母亲和弟弟们站在外面，想要见你。”", version_id: 6},
  {id: 157288, book: "Luke", verse: "8.021", unformatted: "耶稣回答他们，说：“我的母亲、我的弟兄，是那些听神的话语而遵行的人。”", version_id: 6},
  {id: 157289, book: "Luke", verse: "8.022", unformatted: "平静风浪\n就在那些日子里，有一天耶稣和他的门徒们上了船，耶稣对他们说：“我们渡到湖对岸去吧。”他们就出发了。", version_id: 6},
  {id: 157290, book: "Luke", verse: "8.023", unformatted: "船行的时候，耶稣睡着了。这时候，暴风降临到湖上，他们不断地被水浸灌，情况危险。", version_id: 6},
  {id: 157291, book: "Luke", verse: "8.024", unformatted: "门徒们上前来叫醒耶稣，说：“老师，老师，我们没命了！”耶稣就起来，斥责风和浪，风浪就止息，湖面就变得平静。", version_id: 6},
  {id: 157292, book: "Luke", verse: "8.025", unformatted: "耶稣对他们说：“你们的信仰在哪里？”\n他们又惧怕，又惊奇，彼此说：“这个人究竟是谁？他竟然命令风和水，连风和水也听从他！”", version_id: 6},
  {id: 157293, book: "Luke", verse: "8.026", unformatted: "在格拉森驱赶鬼魔\n他们行船到了格拉森人的地方，就是加利利的对岸。", version_id: 6},
  {id: 157294, book: "Luke", verse: "8.027", unformatted: "耶稣刚上岸，那城里一个有鬼魔附身的人就迎面而来。这个人已经很长时间不穿衣服，不住在家里，而住在墓地里。", version_id: 6},
  {id: 157295, book: "Luke", verse: "8.028", unformatted: "他一看见耶稣，就喊叫起来，俯伏在耶稣面前，大声说：“至高神的儿子耶稣，我与你有什么关系？求求你不要折磨我！”", version_id: 6},
  {id: 157296, book: "Luke", verse: "8.029", unformatted: "这是因为耶稣吩咐污灵从那个人身上出来。原来污灵曾多次抓住那个人，虽然他被铁链和脚镣捆锁，被看守着，可是他挣断锁链，被鬼魔驱逐到旷野去。", version_id: 6},
  {id: 157297, book: "Luke", verse: "8.03", unformatted: "耶稣问他：“你叫什么名字？”\n他回答说：“军团。”因为有许多鬼魔进到他里面。", version_id: 6},
  {id: 157298, book: "Luke", verse: "8.031", unformatted: "那些鬼魔央求耶稣不要命令它们进无底坑去。", version_id: 6},
  {id: 157299, book: "Luke", verse: "8.032", unformatted: "当时，那里有一大群猪正在山上吃食。那些鬼魔就央求耶稣准许它们进入那些猪里，耶稣准许了它们。", version_id: 6},
  {id: 157300, book: "Luke", verse: "8.033", unformatted: "鬼魔就从那个人身上出来，进入猪里面。于是那群猪从山崖冲到湖里淹死了。", version_id: 6},
  {id: 157301, book: "Luke", verse: "8.034", unformatted: "那些放猪的人看见所发生的事，就逃跑了，去把这事传到城里城外。", version_id: 6},
  {id: 157302, book: "Luke", verse: "8.035", unformatted: "人们就出来，要看所发生的事，他们来到耶稣那里，看见鬼魔所离开的那人，穿着衣服，神志清醒地坐在耶稣脚前，他们就惧怕起来。", version_id: 6},
  {id: 157303, book: "Luke", verse: "8.036", unformatted: "那些看见的人，就告诉大家被鬼魔附身的人怎样得到救治。", version_id: 6},
  {id: 157304, book: "Luke", verse: "8.037", unformatted: "格拉森一带的全体民众请求耶稣离开他们，因为他们十分惧怕。耶稣就上船回去了。", version_id: 6},
  {id: 157305, book: "Luke", verse: "8.038", unformatted: "鬼魔所离开的那人央求耶稣，要和他在一起，但耶稣让他走，说：", version_id: 6},
  {id: 157306, book: "Luke", verse: "8.039", unformatted: "“你回家去吧，述说神为你做了什么。”于是他就离开了，在全城里传扬耶稣为他做了什么样的事。", version_id: 6},
  {id: 157307, book: "Luke", verse: "8.04", unformatted: "少女复活、妇人痊愈\n耶稣回来的时候，一群人来欢迎他，原来大家一直在等候他。", version_id: 6},
  {id: 157308, book: "Luke", verse: "8.041", unformatted: "这时候，来了一个名叫睚鲁的人，他是一个会堂主管。他俯伏在耶稣脚前，恳求耶稣到他家里去，", version_id: 6},
  {id: 157309, book: "Luke", verse: "8.042", unformatted: "因为他有一个约十二岁的独生女儿，快要死了。\n耶稣去的时候，人群拥挤着他。", version_id: 6},
  {id: 157310, book: "Luke", verse: "8.043", unformatted: "有一个妇人，患了十二年的血漏病，在医生那里花尽了她所有的养生费用，却没有一个人能使她痊愈。", version_id: 6},
  {id: 157311, book: "Luke", verse: "8.044", unformatted: "她从耶稣后面过来，摸了一下耶稣的衣服穗子，立刻，她的血漏就止住了。", version_id: 6},
  {id: 157312, book: "Luke", verse: "8.045", unformatted: "耶稣说：“谁摸了我？”\n大家都否认。彼得就说：“老师，人群正围着拥挤你呢！”", version_id: 6},
  {id: 157313, book: "Luke", verse: "8.046", unformatted: "但是耶稣说：“有人摸了我，因为我知道有能力从我身上出去了。”", version_id: 6},
  {id: 157314, book: "Luke", verse: "8.047", unformatted: "那妇人知道瞒不过了，就战战兢兢地上前俯伏在耶稣面前，在全体民众面前说出她是因什么理由摸了耶稣，以及怎样立刻得了痊愈。", version_id: 6},
  {id: 157315, book: "Luke", verse: "8.048", unformatted: "耶稣对她说：“女儿，你的信救了你，平平安安地去吧。”", version_id: 6},
  {id: 157316, book: "Luke", verse: "8.049", unformatted: "耶稣还在说话的时候，有人从会堂主管的家来，对睚鲁说：“你的女儿已经死了，不要再麻烦老师了。”", version_id: 6},
  {id: 157317, book: "Luke", verse: "8.05", unformatted: "耶稣听了，就对睚鲁说：“不要怕，只要信！她就会得救治。”", version_id: 6},
  {id: 157318, book: "Luke", verse: "8.051", unformatted: "耶稣来到他的家，除了彼得、约翰、雅各和孩子的父母以外，不准任何人与他一起进去。", version_id: 6},
  {id: 157319, book: "Luke", verse: "8.052", unformatted: "大家都在为那女孩捶胸哀哭。耶稣说：“不要哭！她不是死了，而是睡了。”", version_id: 6},
  {id: 157320, book: "Luke", verse: "8.053", unformatted: "他们知道女孩已经死了，就讥笑耶稣。", version_id: 6},
  {id: 157321, book: "Luke", verse: "8.054", unformatted: "但是耶稣握着女孩的手，呼唤说：“孩子，起来！”", version_id: 6},
  {id: 157322, book: "Luke", verse: "8.055", unformatted: "于是她的灵魂回来了，她就立刻起来。耶稣吩咐给她吃东西。", version_id: 6},
  {id: 157323, book: "Luke", verse: "8.056", unformatted: "她的父母十分惊讶，耶稣吩咐他们不要把所发生的事告诉任何人。", version_id: 6},
  {id: 157324, book: "Luke", verse: "9.001", unformatted: "差派十二使徒\n耶稣召集了十二使徒，赐给他们能力和权柄胜过一切鬼魔，又能治愈各样疾病，", version_id: 6},
  {id: 157325, book: "Luke", verse: "9.002", unformatted: "然后差派他们去宣讲神的国，并且使病人痊愈。", version_id: 6},
  {id: 157326, book: "Luke", verse: "9.003", unformatted: "耶稣对他们说：“你们在路上什么都不要带。不要带手杖，不要带行囊，不要带食物，不要带银子，每个人也不要带两件衣服。", version_id: 6},
  {id: 157327, book: "Luke", verse: "9.004", unformatted: "你们无论进哪一家，就要住在那里，也要从那里再出发。", version_id: 6},
  {id: 157328, book: "Luke", verse: "9.005", unformatted: "如果他们不接受你们，你们离开那城的时候，要把脚上的尘土跺掉，做为对他们的见证。”", version_id: 6},
  {id: 157329, book: "Luke", verse: "9.006", unformatted: "使徒们就出去，走遍各村庄到处传福音，使人痊愈。", version_id: 6},
  {id: 157330, book: "Luke", verse: "9.007", unformatted: "希律的困惑\n分封王希律听说了所发生的这一切，就感到困惑，因为有些人说：“约翰从死人中复活了。”", version_id: 6},
  {id: 157331, book: "Luke", verse: "9.008", unformatted: "也有些人说：“以利亚显现了。”还有些人说：“古时候的一位先知复活了。”", version_id: 6},
  {id: 157332, book: "Luke", verse: "9.009", unformatted: "但希律却说：“我已经砍了约翰的头，而这个人又是谁呢？我竟听到关于他这样的事！”于是希律就想要见他。", version_id: 6},
  {id: 157333, book: "Luke", verse: "9.01", unformatted: "五饼二鱼\n使徒们回来，把他们所做的事都告诉了耶稣。耶稣就带着他们，悄悄地退往一个叫做伯赛达的城去。", version_id: 6},
  {id: 157334, book: "Luke", verse: "9.011", unformatted: "可是众人知道了，就跟着他去。耶稣欢迎他们，向他们讲说有关神国的事，并且使那些需要医治的人得痊愈。", version_id: 6},
  {id: 157335, book: "Luke", verse: "9.012", unformatted: "太阳开始平西的时候，十二使徒前来对耶稣说：“请遣散人群，好让他们到周围的村庄和田舍去借宿、找食物，因为我们这里是个荒僻的地方。”", version_id: 6},
  {id: 157336, book: "Luke", verse: "9.013", unformatted: "但耶稣对他们说：“你们给他们吃的吧。”\n他们说：“我们只有五个饼和两条鱼，除非我们去为全体民众买食物。”", version_id: 6},
  {id: 157337, book: "Luke", verse: "9.014", unformatted: "当时，男人就约有五千。\n耶稣对他的门徒们说：“叫他们分组坐下，每组约五十人。”", version_id: 6},
  {id: 157338, book: "Luke", verse: "9.015", unformatted: "门徒们就如此行，叫大家都坐下。", version_id: 6},
  {id: 157339, book: "Luke", verse: "9.016", unformatted: "耶稣拿起这五个饼和两条鱼，抬头望天，祝福了，然后掰开，不断递给门徒们，让他们分给众人。", version_id: 6},
  {id: 157340, book: "Luke", verse: "9.017", unformatted: "大家吃了，并且都吃饱了。他们把剩下的碎块收拾起来，装满了十二个篮子。", version_id: 6},
  {id: 157341, book: "Luke", verse: "9.018", unformatted: "彼得认耶稣为基督\n有一次，耶稣独自祷告的时候，门徒们和他在一起。耶稣问他们，说：“众人说我是谁？”", version_id: 6},
  {id: 157342, book: "Luke", verse: "9.019", unformatted: "他们回答说：“是施洗者约翰，有的说是以利亚，也有的说是古时候的一位先知复活了。”", version_id: 6},
  {id: 157343, book: "Luke", verse: "9.02", unformatted: "耶稣问他们：“那么你们呢？你们说我是谁？”\n彼得回答说：“是神的基督！”", version_id: 6},
  {id: 157344, book: "Luke", verse: "9.021", unformatted: "预言受难与复活\n耶稣告诫他们，吩咐他们不要把这事告诉任何人。", version_id: 6},
  {id: 157345, book: "Luke", verse: "9.022", unformatted: "又说：“人子必须受很多苦害，被长老们、祭司长们和经文士们弃绝，并且被杀，然后在第三天复活。”", version_id: 6},
  {id: 157346, book: "Luke", verse: "9.023", unformatted: "背起你的十字架\n耶稣又对大家说：“如果有人想要来跟从我，他就当否定自己，天天背起自己的十字架，然后跟从我。", version_id: 6},
  {id: 157347, book: "Luke", verse: "9.024", unformatted: "因为凡想要保全自己生命的，将失去生命；凡为我的缘故失去自己生命的，这个人将保全生命。", version_id: 6},
  {id: 157348, book: "Luke", verse: "9.025", unformatted: "一个人就是赚得了全世界，却丧失了自己，或赔上了自己，到底有什么益处呢？", version_id: 6},
  {id: 157349, book: "Luke", verse: "9.026", unformatted: "一个人如果以我和我的话为耻，当人子在自己、父，和圣天使们的荣耀中来临的时候，也要以这个人为耻。", version_id: 6},
  {id: 157350, book: "Luke", verse: "9.027", unformatted: "我确实地告诉你们：站在这里的有些人，在还没有尝到死的滋味之前，必定见到神的国。”", version_id: 6},
  {id: 157351, book: "Luke", verse: "9.028", unformatted: "登山变相\n说了这些话以后约八天，耶稣带着彼得、约翰、雅各上山去祷告。", version_id: 6},
  {id: 157352, book: "Luke", verse: "9.029", unformatted: "正在祷告的时候，耶稣的相貌改变了，衣服也洁白发光。", version_id: 6},
  {id: 157353, book: "Luke", verse: "9.03", unformatted: "这时候，忽然有两个人和耶稣谈话——他们是摩西和以利亚——", version_id: 6},
  {id: 157354, book: "Luke", verse: "9.031", unformatted: "他们在荣光中显现，谈论耶稣离世的事，就是他将要在耶路撒冷成就的事。", version_id: 6},
  {id: 157355, book: "Luke", verse: "9.032", unformatted: "彼得和他的同伴正在沉睡着。可是他们清醒过来时，看见了耶稣的荣光和同他站着的那两个人。", version_id: 6},
  {id: 157356, book: "Luke", verse: "9.033", unformatted: "那两个人正要离开耶稣的时候，彼得对耶稣说：“老师，我们在这里真好！让我们来搭三座帐幕，一座为你，一座为摩西，一座为以利亚。”他不知道自己在说什么。", version_id: 6},
  {id: 157357, book: "Luke", verse: "9.034", unformatted: "他正说这些话，有一朵云彩出现，笼罩着他们。他们一没入云彩中，门徒们就惧怕起来。", version_id: 6},
  {id: 157358, book: "Luke", verse: "9.035", unformatted: "有声音从云中传来，说：\n“这是我的儿子，我所拣选的。\n你们当听从他！”", version_id: 6},
  {id: 157359, book: "Luke", verse: "9.036", unformatted: "声音过后，门徒们发现只有耶稣一个人了。在那些日子里，他们保持沉默，没有告诉任何人他们所看到的事。", version_id: 6},
  {id: 157360, book: "Luke", verse: "9.037", unformatted: "信心的力量\n第二天，他们从山上下来，有一大群人迎着耶稣而来。", version_id: 6},
  {id: 157361, book: "Luke", verse: "9.038", unformatted: "这时候，忽然人群中有一个人呼喊说：“老师，求求你看顾我的儿子吧！因为他是我的独生儿子。", version_id: 6},
  {id: 157362, book: "Luke", verse: "9.039", unformatted: "你看，邪灵抓住他，忽然喊叫起来，又使他抽疯，口吐白沫；邪灵折磨着他，就是不离开。", version_id: 6},
  {id: 157363, book: "Luke", verse: "9.04", unformatted: "我求过你的门徒们，要他们驱赶那邪灵，可是他们不能。”", version_id: 6},
  {id: 157364, book: "Luke", verse: "9.041", unformatted: "耶稣回答说：“唉，这个不信、歪曲的世代！我还要和你们在一起多久呢？我还要容忍你们多久呢？把你的儿子带到这里来吧！”", version_id: 6},
  {id: 157365, book: "Luke", verse: "9.042", unformatted: "那孩子上前来的时候，鬼魔把他摔倒，使他剧烈地抽疯。耶稣就斥责那污灵，使孩子痊愈，把孩子交给他父亲。", version_id: 6},
  {id: 157366, book: "Luke", verse: "9.043", unformatted: "大家都对神的威荣惊叹不已。\n再次预言受难\n正当大家对耶稣所做的一切感到惊奇的时候，耶稣对他的门徒们说：", version_id: 6},
  {id: 157367, book: "Luke", verse: "9.044", unformatted: "“你们要把这话存在耳中：人子将要被交在人的手中。”", version_id: 6},
  {id: 157368, book: "Luke", verse: "9.045", unformatted: "可是他们不明白这话，因为这话的意思对他们是隐藏的，使他们不能领悟。他们也不敢问耶稣这话的意思。", version_id: 6},
  {id: 157369, book: "Luke", verse: "9.046", unformatted: "谁是最大的\n门徒之间起了争论：他们当中到底谁更大。", version_id: 6},
  {id: 157370, book: "Luke", verse: "9.047", unformatted: "耶稣看出他们心中的意念，就领来一个小孩子，让他站在自己身旁，", version_id: 6},
  {id: 157371, book: "Luke", verse: "9.048", unformatted: "然后对他们说：“无论谁奉我的名接受这小孩子，就是接受我；接受我的，就是接受差派我来的那一位。要知道，在你们当中谁更小，他才为大。”", version_id: 6},
  {id: 157372, book: "Luke", verse: "9.049", unformatted: "奉耶稣的名\n约翰回答说：“老师，我们看见一个人奉你的名驱赶鬼魔，我们就去阻止他，因为他不与我们一起跟从你。”", version_id: 6},
  {id: 157373, book: "Luke", verse: "9.05", unformatted: "耶稣对他说：“不要阻止他，因为不反对你们的，就是支持你们的。”", version_id: 6},
  {id: 157374, book: "Luke", verse: "9.051", unformatted: "前往耶路撒冷\n耶稣升天的日子快到了，他就坚定地面向耶路撒冷而去。", version_id: 6},
  {id: 157375, book: "Luke", verse: "9.052", unformatted: "他派了几个使者在他前头。他们就去了，进到撒马利亚人的一个村子，要为他做预备。", version_id: 6},
  {id: 157376, book: "Luke", verse: "9.053", unformatted: "但那里的人不接受他，因为他是往耶路撒冷去的。", version_id: 6},
  {id: 157377, book: "Luke", verse: "9.054", unformatted: "门徒雅各和约翰看见了，就说：“主啊，你要我们吩咐火从天上降下来烧灭他们吗？”", version_id: 6},
  {id: 157378, book: "Luke", verse: "9.055", unformatted: "耶稣转过身来责备他们。", version_id: 6},
  {id: 157379, book: "Luke", verse: "9.056", unformatted: "于是他们往别的村子去了。", version_id: 6},
  {id: 157380, book: "Luke", verse: "9.057", unformatted: "跟随耶稣\n他们在路上走的时候，有一个人对耶稣说：“无论你到哪里去，我都要跟从你。”", version_id: 6},
  {id: 157381, book: "Luke", verse: "9.058", unformatted: "耶稣对他说：“狐狸有洞，天空的飞鸟有窝，人子却没有枕头的地方。”", version_id: 6},
  {id: 157382, book: "Luke", verse: "9.059", unformatted: "耶稣对另一个人说：“你跟从我！”\n可是他说：“主啊，请准许我先去埋葬我的父亲。”", version_id: 6},
  {id: 157383, book: "Luke", verse: "9.06", unformatted: "耶稣对他说：“让死人去埋葬他们的死人吧。至于你，你要去宣讲神的国。”", version_id: 6},
  {id: 157384, book: "Luke", verse: "9.061", unformatted: "另有一个人说：“主啊，我要跟从你，但是请准许我先向我家里的人告别。”", version_id: 6},
  {id: 157385, book: "Luke", verse: "9.062", unformatted: "耶稣对他说：“手扶着犁又看背后之事的人，不配神的国。”", version_id: 6},
  {id: 157386, book: "Luke", verse: "10.001", unformatted: "差派七十人\n这些事以后，主另外指定了七十个人，差派他们两个两个地在他前头，到他要去的各城各地去。", version_id: 6},
  {id: 157387, book: "Luke", verse: "10.002", unformatted: "耶稣对他们说：“收割的工作多，而工人少。所以你们要祈求收割的主催促工人参与他的收割工作。", version_id: 6},
  {id: 157388, book: "Luke", verse: "10.003", unformatted: "你们去吧！看哪，我差派你们出去，就像把羊羔送进狼群中。", version_id: 6},
  {id: 157389, book: "Luke", verse: "10.004", unformatted: "你们不要带钱包，不要带行囊，不要带鞋子，在路上也不要问候任何人。", version_id: 6},
  {id: 157390, book: "Luke", verse: "10.005", unformatted: "你们无论进哪一家，首先要说‘愿这一家平安。’", version_id: 6},
  {id: 157391, book: "Luke", verse: "10.006", unformatted: "如果那里有平安之子，你们的平安就会临到他；否则，那平安就归回你们。", version_id: 6},
  {id: 157392, book: "Luke", verse: "10.007", unformatted: "你们当住在那一家，吃喝他们所供给的，因为工人配得自己的酬报。不要从这家搬到那家。", version_id: 6},
  {id: 157393, book: "Luke", verse: "10.008", unformatted: "无论进哪一个城，如果人们接受你们，给你们摆上什么，你们就吃什么。", version_id: 6},
  {id: 157394, book: "Luke", verse: "10.009", unformatted: "你们要使那城里的病人痊愈，并且要对他们说‘神的国临近你们了。’", version_id: 6},
  {id: 157395, book: "Luke", verse: "10.01", unformatted: "无论进哪一个城，如果人们不接受你们，你们就到大街上去，说", version_id: 6},
  {id: 157396, book: "Luke", verse: "10.011", unformatted: "‘连那沾在我们脚上的你们城里的尘土，我们也要当着你们擦掉，不过你们应当知道：神的国近了。’", version_id: 6},
  {id: 157397, book: "Luke", verse: "10.012", unformatted: "我告诉你们：在那日，所多玛所受的，将要比那城还容易受呢！", version_id: 6},
  {id: 157398, book: "Luke", verse: "10.013", unformatted: "不悔改的城\n“哥拉汛哪，你有祸了！伯赛达呀，你有祸了！因为在你们当中行过的神迹，如果行在提尔和西顿，那里的人早就会披麻蒙灰，坐在地上悔改了。", version_id: 6},
  {id: 157399, book: "Luke", verse: "10.014", unformatted: "不过在审判的时候，提尔和西顿所受的，将要比你们还容易受呢！", version_id: 6},
  {id: 157400, book: "Luke", verse: "10.015", unformatted: "而你——迦百农啊，你将被高举到天上吗？不，你将下到阴间。", version_id: 6},
  {id: 157401, book: "Luke", verse: "10.016", unformatted: "“听从你们的，就是听从我；拒绝你们的，就是拒绝我；拒绝我的，就是拒绝差派我来的那一位。”", version_id: 6},
  {id: 157402, book: "Luke", verse: "10.017", unformatted: "七十人归来\n那七十个人怀着喜乐的心回来，说：“主啊，奉你的名，连鬼魔也服从了我们。”", version_id: 6},
  {id: 157403, book: "Luke", verse: "10.018", unformatted: "耶稣对他们说：“我看见撒旦像闪电一样从天上坠落。", version_id: 6},
  {id: 157404, book: "Luke", verse: "10.019", unformatted: "看，我已经给了你们权柄去践踏蛇和蝎子，又胜过仇敌一切的势力，绝不会有什么能伤害你们了。", version_id: 6},
  {id: 157405, book: "Luke", verse: "10.02", unformatted: "但不要因为邪灵服从了你们就欢喜，要因为你们的名字已经被记录在天上而欢喜。”", version_id: 6},
  {id: 157406, book: "Luke", verse: "10.021", unformatted: "父的美意\n那时候，耶稣受了圣灵的感动就快乐，说：“父啊，天地的主，我赞美你，因为你把这些事向有智慧的和有学问的人隐藏起来，而向小孩子们显明出来。是的，父啊！因为这正是你的美意。”", version_id: 6},
  {id: 157407, book: "Luke", verse: "10.022", unformatted: "“我父把一切都交给了我。除了父，没有人知道子是谁；除了子和子所愿意启示的人，也没有人知道父是谁。”", version_id: 6},
  {id: 157408, book: "Luke", verse: "10.023", unformatted: "耶稣转过身来，悄悄地对门徒们说：“看见你们所看见的，那眼睛是蒙福的！", version_id: 6},
  {id: 157409, book: "Luke", verse: "10.024", unformatted: "我告诉你们：曾经有许多先知和君王想要看你们所看见的，却没有看到；想要听你们所听见的，却没有听到。”", version_id: 6},
  {id: 157410, book: "Luke", verse: "10.025", unformatted: "好撒马利亚人\n这时候，忽然有一个律法师站起来试探耶稣，说：“老师，我该做什么才会继承永恒的生命呢？”", version_id: 6},
  {id: 157411, book: "Luke", verse: "10.026", unformatted: "耶稣对他说：“律法上是怎么写的？你是怎么读的呢？”", version_id: 6},
  {id: 157412, book: "Luke", verse: "10.027", unformatted: "他回答说：\n“你要以全心、全灵、全力、全意爱主——你的神，并且要爱邻如己。”", version_id: 6},
  {id: 157413, book: "Luke", verse: "10.028", unformatted: "耶稣说：“你回答得对。你这样去做，就将活着。”", version_id: 6},
  {id: 157414, book: "Luke", verse: "10.029", unformatted: "那个人却要显示自己为义，就对耶稣说：“那么，谁是我的邻人呢？”", version_id: 6},
  {id: 157415, book: "Luke", verse: "10.03", unformatted: "耶稣回答说：“有一个人从耶路撒冷下到耶利哥去，途中遇到强盗。他们剥去他的衣服，把他打得半死，丢下他走了。", version_id: 6},
  {id: 157416, book: "Luke", verse: "10.031", unformatted: "恰好有一个祭司从那条路下来，看见他，就从另一边走了过去。", version_id: 6},
  {id: 157417, book: "Luke", verse: "10.032", unformatted: "有一个利未人也正好来到那地方，看见他，也同样地从另一边走了过去。", version_id: 6},
  {id: 157418, book: "Luke", verse: "10.033", unformatted: "可是，有一个撒马利亚人行路来到他那里，看见他，就动了怜悯之心，", version_id: 6},
  {id: 157419, book: "Luke", verse: "10.034", unformatted: "上前来，在他的伤处倒上油和酒，包扎好，然后把他扶上自己的牲口，带到旅店里照顾他。", version_id: 6},
  {id: 157420, book: "Luke", verse: "10.035", unformatted: "第二天，他拿出两个银币交给店家，说‘请你照顾他，一切额外的费用，我回来的时候将还给你。’", version_id: 6},
  {id: 157421, book: "Luke", verse: "10.036", unformatted: "“你认为，这三个人中，哪一个是落在强盗手中那人的邻人呢？”", version_id: 6},
  {id: 157422, book: "Luke", verse: "10.037", unformatted: "律法师说：“是那个怜悯他的人。”\n耶稣对他说：“你去如此做吧。”", version_id: 6},
  {id: 157423, book: "Luke", verse: "10.038", unformatted: "玛妲与玛丽亚\n他们继续前行，耶稣进了一个村子，有一个名叫玛妲的女人接待他。", version_id: 6},
  {id: 157424, book: "Luke", verse: "10.039", unformatted: "玛妲有个妹妹，称为玛丽亚，坐在主的脚前听他的话语。", version_id: 6},
  {id: 157425, book: "Luke", verse: "10.04", unformatted: "玛妲为了很多伺候的事就心里烦乱，上前来说：“主啊，我妹妹丢下我一个人伺候，你不管吗？请叫她来帮我的忙吧。”", version_id: 6},
  {id: 157426, book: "Luke", verse: "10.041", unformatted: "主回答她，说：“玛妲，玛妲，你为很多事忧虑、烦恼，", version_id: 6},
  {id: 157427, book: "Luke", verse: "10.042", unformatted: "但是有一件事是需要的，而玛丽亚选择了那美好的一份，是不能从她那里被拿去的。”", version_id: 6},
  {id: 157428, book: "Luke", verse: "11.001", unformatted: "祷告的典范\n有一次，耶稣在一个地方祷告。祷告完了，他的一个门徒对他说：“主啊，求你教我们祷告，就像约翰教导他的门徒们那样。”", version_id: 6},
  {id: 157429, book: "Luke", verse: "11.002", unformatted: "耶稣就对他们说：“你们祷告的时候，要说：\n‘父啊，\n愿你的名被尊为圣，\n愿你的国度来临。", version_id: 6},
  {id: 157430, book: "Luke", verse: "11.003", unformatted: "我们日用的食粮，愿你天天赐给我们。", version_id: 6},
  {id: 157431, book: "Luke", verse: "11.004", unformatted: "愿你饶恕我们的罪，\n因为我们也饶恕所有亏欠我们的人。\n不要让我们陷入试探。’”", version_id: 6},
  {id: 157432, book: "Luke", verse: "11.005", unformatted: "不断祈求\n耶稣又对他们说：“假如你们当中谁有一个朋友，就半夜到朋友那里去，对他说：‘朋友，请借给我三个饼，", version_id: 6},
  {id: 157433, book: "Luke", verse: "11.006", unformatted: "因为有一个朋友旅途中来到我这里，我没有什么可以招待他。’", version_id: 6},
  {id: 157434, book: "Luke", verse: "11.007", unformatted: "那个人从里面回答说：‘请不要烦扰我。门已经关上了，我与孩子们也已经上了床，不能起来拿给你。’", version_id: 6},
  {id: 157435, book: "Luke", verse: "11.008", unformatted: "我告诉你们：即使不因着是朋友而起来拿给他，还是会因着他毫无羞愧地求，就起来把他所要的拿给他。", version_id: 6},
  {id: 157436, book: "Luke", verse: "11.009", unformatted: "“我还告诉你们：不断祈求吧，就会给你们；不断寻找吧，就会找到；不断敲门吧，就会为你们开门。", version_id: 6},
  {id: 157437, book: "Luke", verse: "11.01", unformatted: "因为凡祈求的，就得到；寻找的，就找到；敲门的，就为他开门。", version_id: 6},
  {id: 157438, book: "Luke", verse: "11.011", unformatted: "你们当中有哪个做父亲的，儿子要鱼，反而拿蛇当做鱼给他呢？", version_id: 6},
  {id: 157439, book: "Luke", verse: "11.012", unformatted: "儿子要鸡蛋，却给他蝎子呢？", version_id: 6},
  {id: 157440, book: "Luke", verse: "11.013", unformatted: "所以，如果你们做为恶人也知道把好的东西给自己的儿女，更何况天上的父要赐圣灵给求他的人呢！”", version_id: 6},
  {id: 157441, book: "Luke", verse: "11.014", unformatted: "自相纷争\n有一次，耶稣正在驱赶一个使人成为哑巴的鬼魔。鬼魔一出去，哑巴就说出话来，众人都感到惊奇。", version_id: 6},
  {id: 157442, book: "Luke", verse: "11.015", unformatted: "但其中有些人却说：“他是靠鬼魔的王别西卜驱赶鬼魔的。”", version_id: 6},
  {id: 157443, book: "Luke", verse: "11.016", unformatted: "另有些人试探耶稣，向他要求一个从天上来的神迹。", version_id: 6},
  {id: 157444, book: "Luke", verse: "11.017", unformatted: "耶稣知道他们的想法，就对他们说：“任何一个国家，如果自相分裂就成为荒芜；一个家庭，如果自相分裂也会败落。", version_id: 6},
  {id: 157445, book: "Luke", verse: "11.018", unformatted: "同样，如果撒旦自相纷争，它的国怎么能站立得住呢？尽管这样，你们竟然说我是靠别西卜驱赶鬼魔的。", version_id: 6},
  {id: 157446, book: "Luke", verse: "11.019", unformatted: "如果我是靠别西卜驱赶鬼魔，那么，你们的子弟是靠谁驱赶鬼魔呢？为此，你们的子弟就将要成为你们的审判者了。", version_id: 6},
  {id: 157447, book: "Luke", verse: "11.02", unformatted: "可是，如果我藉着神的能力驱赶鬼魔，那么，神的国就已经临到你们了。", version_id: 6},
  {id: 157448, book: "Luke", verse: "11.021", unformatted: "当一个壮士全副武装地看守自己家园的时候，他所拥有的就平安无事。", version_id: 6},
  {id: 157449, book: "Luke", verse: "11.022", unformatted: "可是比他更强的人一旦来到，并且胜过了他，就会夺走他所依靠的盔甲兵器，又把掠夺物分给别人。", version_id: 6},
  {id: 157450, book: "Luke", verse: "11.023", unformatted: "不与我在一起的，就是在反对我；不与我一起收集的，就是在拆散。", version_id: 6},
  {id: 157451, book: "Luke", verse: "11.024", unformatted: "污灵返回\n“有个污灵从一个人身上出来以后，走遍无水之地，寻找安歇的地方，却找不到，就说：‘我要回到我离开的那屋里去。’", version_id: 6},
  {id: 157452, book: "Luke", verse: "11.025", unformatted: "于是它回去，发现那里已被打扫干净，摆设整齐，", version_id: 6},
  {id: 157453, book: "Luke", verse: "11.026", unformatted: "就去带了另外七个比自己更邪恶的灵，进去住在那里。那个人最后的情况就比先前更坏了。”", version_id: 6},
  {id: 157454, book: "Luke", verse: "11.027", unformatted: "真正的福气\n耶稣正说这些话的时候，人群中有一个妇人高声对他说：“那怀你胎又哺育你的是蒙福的！”", version_id: 6},
  {id: 157455, book: "Luke", verse: "11.028", unformatted: "耶稣说：“那听了神的话语又遵守的人，才真是蒙福的！”", version_id: 6},
  {id: 157456, book: "Luke", verse: "11.029", unformatted: "约拿的神迹\n那时，人群越聚越多，耶稣就开始说：“这世代是个邪恶的世代，它在寻求神迹；可是除了约拿的神迹以外，不会有神迹赐给它了。", version_id: 6},
  {id: 157457, book: "Luke", verse: "11.03", unformatted: "就是说，约拿怎样成了对尼尼微人的一个神迹，人子也将照样成为对这世代的一个神迹。", version_id: 6},
  {id: 157458, book: "Luke", verse: "11.031", unformatted: "在审判的时候，南方的女王将要与这世代的人一起复活，并且要定他们的罪，因为她从极远的地方来，要听所罗门智慧的话语。看，比所罗门更大的就在这里！", version_id: 6},
  {id: 157459, book: "Luke", verse: "11.032", unformatted: "在审判的时候，尼尼微人将要与这世代一起复活，并且要定这世代的罪，因为尼尼微人因着约拿的传道就悔改了。看，比约拿更大的就在这里！", version_id: 6},
  {id: 157460, book: "Luke", verse: "11.033", unformatted: "身体的灯\n“没有人点上油灯，放在隐藏的地方或斗底下，而是放在灯台上，好让进来的人能看见光。", version_id: 6},
  {id: 157461, book: "Luke", verse: "11.034", unformatted: "你的眼睛是身体的灯。当你的眼睛纯净的时候，你的全身就光明；你的眼睛一旦污浊，你的全身就黑暗。", version_id: 6},
  {id: 157462, book: "Luke", verse: "11.035", unformatted: "所以你要留心，免得你里面的光成为黑暗。", version_id: 6},
  {id: 157463, book: "Luke", verse: "11.036", unformatted: "因此，如果你全身光明，没有任何黑暗的部分，你就全然光明，好像明灯照亮着你。”", version_id: 6},
  {id: 157464, book: "Luke", verse: "11.037", unformatted: "谴责宗教的伪善\n耶稣说话的时候，有一个法利赛人请耶稣到他家里吃饭，于是耶稣进去坐席。", version_id: 6},
  {id: 157465, book: "Luke", verse: "11.038", unformatted: "那个法利赛人看见耶稣午餐之前不行洗净礼，感到惊奇。", version_id: 6},
  {id: 157466, book: "Luke", verse: "11.039", unformatted: "主就对他说：“如今你们法利赛人洗净杯子、盘子的外面，但你们里面却充满着贪心和恶意。", version_id: 6},
  {id: 157467, book: "Luke", verse: "11.04", unformatted: "你们这些愚妄的人！那位造了外面的，不也造了里面吗？", version_id: 6},
  {id: 157468, book: "Luke", verse: "11.041", unformatted: "不过你们当把里面的施舍给人，看哪，对你们来说一切就都洁净了。", version_id: 6},
  {id: 157469, book: "Luke", verse: "11.042", unformatted: "“你们法利赛人有祸了！因为你们奉献薄荷、芸香和各种蔬菜的十分之一，却废弃了公义和属神的爱。这些事是你们应该做的，至于前者也不可忽略。", version_id: 6},
  {id: 157470, book: "Luke", verse: "11.043", unformatted: "“你们法利赛人有祸了！因为你们喜爱会堂里的首位，喜爱街市上的致敬问候。", version_id: 6},
  {id: 157471, book: "Luke", verse: "11.044", unformatted: "“你们有祸了！因为你们好像没有标志的坟墓，人走在上面也不知道。”", version_id: 6},
  {id: 157472, book: "Luke", verse: "11.045", unformatted: "有一个律法师对耶稣说：“老师，你说这些话，把我们也侮辱了。”", version_id: 6},
  {id: 157473, book: "Luke", verse: "11.046", unformatted: "耶稣说：“你们这些律法师也有祸了！因为你们使人背负难挑的重担，但自己却连一根指头也不肯碰它们一下。", version_id: 6},
  {id: 157474, book: "Luke", verse: "11.047", unformatted: "“你们有祸了！因为你们建造先知的坟墓，而你们的祖先是杀害他们的。", version_id: 6},
  {id: 157475, book: "Luke", verse: "11.048", unformatted: "因此对你们祖先的行为，你们是见证人，你们也赞同，因为他们杀害了先知们，你们就建造先知的坟墓。", version_id: 6},
  {id: 157476, book: "Luke", verse: "11.049", unformatted: "为此，神的智慧也曾说：‘我将要差派先知和使徒到他们那里去。有些他们要杀害，有些他们要逼迫。’", version_id: 6},
  {id: 157477, book: "Luke", verse: "11.05", unformatted: "这是为要向这世代追讨那从创世以来所流的众先知的血，", version_id: 6},
  {id: 157478, book: "Luke", verse: "11.051", unformatted: "就是从亚伯的血起，直到在祭坛和圣所之间被杀的撒迦利亚的血为止。\n“是的，我告诉你们：这世代将要为此被追讨。", version_id: 6},
  {id: 157479, book: "Luke", verse: "11.052", unformatted: "“你们这些律法师有祸了！因为你们拿去了知识的钥匙，你们自己不进去，还阻止正要进去的人。”", version_id: 6},
  {id: 157480, book: "Luke", verse: "11.053", unformatted: "耶稣从那里出来，法利赛人和经文士们就开始激烈地敌视他，还盘问他许多问题，", version_id: 6},
  {id: 157481, book: "Luke", verse: "11.054", unformatted: "暗中等机会，要从他的口中抓住把柄。", version_id: 6},
  {id: 157482, book: "Luke", verse: "12.001", unformatted: "要防备宗教的伪善\n那时有上万的人群聚集，甚至彼此践踏。耶稣一开始先对他的门徒们说：“你们要提防法利赛人的酵母，就是伪善。", version_id: 6},
  {id: 157483, book: "Luke", verse: "12.002", unformatted: "被掩盖的事没有将不被显露出来的，隐秘的事也没有将不被知道的。", version_id: 6},
  {id: 157484, book: "Luke", verse: "12.003", unformatted: "所以你们在暗中所说的，会在明处被听见；你们在内室里耳语的，将要在屋顶上被宣扬出去。", version_id: 6},
  {id: 157485, book: "Luke", verse: "12.004", unformatted: "要惧怕神\n“我的朋友，我告诉你们：不要怕那些杀了身体以后不能再做什么的人。", version_id: 6},
  {id: 157486, book: "Luke", verse: "12.005", unformatted: "我要指示你们该惧怕的是谁：你们当惧怕那杀了身体以后，又有权把人扔进地狱的那一位。是的，我告诉你们：当惧怕这一位。", version_id: 6},
  {id: 157487, book: "Luke", verse: "12.006", unformatted: "五只麻雀不是卖两个小钱吗？可是在神面前，一只也不会被忘记；", version_id: 6},
  {id: 157488, book: "Luke", verse: "12.007", unformatted: "而且，就是你们的每一根头发也都被数过了。不要怕！你们比一大群麻雀还重要。", version_id: 6},
  {id: 157489, book: "Luke", verse: "12.008", unformatted: "要承认基督\n“我告诉你们：无论谁在人面前承认我，人子在神的天使们面前也将承认他；", version_id: 6},
  {id: 157490, book: "Luke", verse: "12.009", unformatted: "而那在人面前不认我的，在神的天使们面前也将被否认。", version_id: 6},
  {id: 157491, book: "Luke", verse: "12.01", unformatted: "所有说话抵挡人子的，还能被赦免；但那亵渎圣灵的，不能被赦免。", version_id: 6},
  {id: 157492, book: "Luke", verse: "12.011", unformatted: "当他们把你们带到会堂，或带到统治者和掌权者面前的时候，不要忧虑怎样申辩或说什么。", version_id: 6},
  {id: 157493, book: "Luke", verse: "12.012", unformatted: "因为就在那时候，圣灵会教导你们该说的话。”", version_id: 6},
  {id: 157494, book: "Luke", verse: "12.013", unformatted: "无知财主的比喻\n人群中有一个人对耶稣说：“老师，请吩咐我的哥哥与我分继业吧！”", version_id: 6},
  {id: 157495, book: "Luke", verse: "12.014", unformatted: "耶稣对他说：“朋友，谁委任了我做你们的审判官或仲裁者呢？”", version_id: 6},
  {id: 157496, book: "Luke", verse: "12.015", unformatted: "接着耶稣对他们说：“你们要注意，要提防一切贪心，因为一个人的生命不在于他财产的丰厚。”", version_id: 6},
  {id: 157497, book: "Luke", verse: "12.016", unformatted: "然后对他们讲了一个比喻，说：“有一个富有的人，田地丰收了。", version_id: 6},
  {id: 157498, book: "Luke", verse: "12.017", unformatted: "他就心里盘算：‘我没有地方储存我的产物了，做什么呢？’", version_id: 6},
  {id: 157499, book: "Luke", verse: "12.018", unformatted: "他就说：‘我要这么做：拆掉我的这些仓库，另建更大的，把我所有的麦子和美物收集在那里。", version_id: 6},
  {id: 157500, book: "Luke", verse: "12.019", unformatted: "然后，我要对我的灵魂说：灵魂哪，你拥有了很多好东西，足以享用多年，你就休息、吃喝、庆祝吧。’", version_id: 6},
  {id: 157501, book: "Luke", verse: "12.02", unformatted: "“可是神对他说：‘你这愚妄的人哪！今夜，他们就要召回你的灵魂了。那么，你所预备的要归谁呢？’", version_id: 6},
  {id: 157502, book: "Luke", verse: "12.021", unformatted: "“那为自己积蓄财富而在神面前不富足的人，也是这样。”", version_id: 6},
  {id: 157503, book: "Luke", verse: "12.022", unformatted: "不要忧虑\n耶稣又对他的门徒们说：“所以我告诉你们：不要为你们的生命而忧虑吃什么，也不要为身体而忧虑穿什么。", version_id: 6},
  {id: 157504, book: "Luke", verse: "12.023", unformatted: "因为生命比食物更重要，身体比衣服更重要。", version_id: 6},
  {id: 157505, book: "Luke", verse: "12.024", unformatted: "应该想一想乌鸦：它们不撒种，也不收割，没有仓也没有库，神尚且养活它们；更何况你们，你们比飞鸟还重要呢！", version_id: 6},
  {id: 157506, book: "Luke", verse: "12.025", unformatted: "你们当中谁能因着忧虑使自己的寿命延长一刻呢？", version_id: 6},
  {id: 157507, book: "Luke", verse: "12.026", unformatted: "因此，你们如果连最小的事也不能做，为什么还要为其他的事忧虑呢？", version_id: 6},
  {id: 157508, book: "Luke", verse: "12.027", unformatted: "“应该想一想百合花是怎么生长的：它不劳动，也不纺织。但是我告诉你们：就连所罗门在他最荣耀的时候，所穿戴的也不如这些花中的一朵。", version_id: 6},
  {id: 157509, book: "Luke", verse: "12.028", unformatted: "你们这些小信的人哪！野地里的草今天还在，明天就被丢进火炉里，神尚且这样装扮它，更何况你们呢！", version_id: 6},
  {id: 157510, book: "Luke", verse: "12.029", unformatted: "你们不要追求吃什么、喝什么，也不要挂虑。", version_id: 6},
  {id: 157511, book: "Luke", verse: "12.03", unformatted: "因为这一切都是世界外邦人所寻求的，而你们的父已经知道你们需要这些了。", version_id: 6},
  {id: 157512, book: "Luke", verse: "12.031", unformatted: "“不过你们应当寻求神的国，这些都将加给你们了。", version_id: 6},
  {id: 157513, book: "Luke", verse: "12.032", unformatted: "你们这一小群哪，不要怕，因为你们的父乐意把国度赐给你们。", version_id: 6},
  {id: 157514, book: "Luke", verse: "12.033", unformatted: "你们当卖掉自己所拥有的，施舍给人，为自己预备不会破旧的钱包和没有穷尽的财宝在天上；在那里盗贼不能靠近，也没有虫咬蚀。", version_id: 6},
  {id: 157515, book: "Luke", verse: "12.034", unformatted: "要知道，你们的财宝在哪里，你们的心也在哪里。", version_id: 6},
  {id: 157516, book: "Luke", verse: "12.035", unformatted: "要警醒\n“你们要束上腰带，点亮油灯，", version_id: 6},
  {id: 157517, book: "Luke", verse: "12.036", unformatted: "要像期待着主人从婚宴上回来的人那样；好在主人回来敲门的时候，能立刻为他开门。", version_id: 6},
  {id: 157518, book: "Luke", verse: "12.037", unformatted: "主人回来的时候，看见哪些奴仆警醒着，那些奴仆就蒙福了。我确实地告诉你们：主人会束上腰带，请他们坐席，并来伺候他们。", version_id: 6},
  {id: 157519, book: "Luke", verse: "12.038", unformatted: "即使主人在二更天或在三更天才回来，也看见他们仍然警醒着，那些人就蒙福了。", version_id: 6},
  {id: 157520, book: "Luke", verse: "12.039", unformatted: "不过你们应当知道这一点：一家的主人如果知道贼什么时候来，就不会让他的房子被人钻进。", version_id: 6},
  {id: 157521, book: "Luke", verse: "12.04", unformatted: "你们也应当做好准备，因为在你们意想不到的时候，人子就来了。”", version_id: 6},
  {id: 157522, book: "Luke", verse: "12.041", unformatted: "忠仆与恶仆\n彼得说：“主啊，你这个比喻是对我们说的，还是也对大家说的呢？”", version_id: 6},
  {id: 157523, book: "Luke", verse: "12.042", unformatted: "主说：“究竟谁是那又忠心又聪明的管家——受主人委任统管他家人、按时分发粮食的人呢？", version_id: 6},
  {id: 157524, book: "Luke", verse: "12.043", unformatted: "主人回来的时候，看见哪个奴仆如此行，那个奴仆就蒙福了。", version_id: 6},
  {id: 157525, book: "Luke", verse: "12.044", unformatted: "我确实地告诉你们：主人会委任他统管自己所拥有的一切。", version_id: 6},
  {id: 157526, book: "Luke", verse: "12.045", unformatted: "但如果这奴仆心里说‘我的主人会迟延回来’，就动手殴打其他的男仆和女仆，并且吃喝、醉酒，", version_id: 6},
  {id: 157527, book: "Luke", verse: "12.046", unformatted: "那么，这奴仆的主人就要在意想不到的日子、在他不知道的时刻回来，并且会严厉惩罚他，使他与那些不信的人有同样的下场。", version_id: 6},
  {id: 157528, book: "Luke", verse: "12.047", unformatted: "那知道主人的意思，却没有预备，也不照着主人的意思去做的奴仆，将会受很多鞭打；", version_id: 6},
  {id: 157529, book: "Luke", verse: "12.048", unformatted: "但那因为不知道而做了该打之事的人，将会受很少鞭打。所以谁被赐予的多，将来向谁索取的也多；托给谁多，将来向谁要求的也更多。", version_id: 6},
  {id: 157530, book: "Luke", verse: "12.049", unformatted: "不是和平而是分裂\n“我来是要把火投在地上，我多么希望这火已经点燃了！", version_id: 6},
  {id: 157531, book: "Luke", verse: "12.05", unformatted: "不过我有该受的洗礼，在完成之前，我是多么困苦！", version_id: 6},
  {id: 157532, book: "Luke", verse: "12.051", unformatted: "你们以为我来了，是给地上带来和平吗？不是的！我告诉你们：其实还是分裂。", version_id: 6},
  {id: 157533, book: "Luke", verse: "12.052", unformatted: "从今以后，一家五口将要分裂：三个与两个，两个与三个分裂。", version_id: 6},
  {id: 157534, book: "Luke", verse: "12.053", unformatted: "他们将要分裂：父亲反对儿子，\n儿子反对父亲而分裂；\n母亲反对女儿，\n女儿反对母亲而分裂；\n婆婆反对媳妇，\n媳妇反对婆婆而分裂。”", version_id: 6},
  {id: 157535, book: "Luke", verse: "12.054", unformatted: "分辨时代\n然后，耶稣又对众人说：“你们一旦看见云彩在西边升起，就立刻说：‘要下大雨了’，果然就如此。", version_id: 6},
  {id: 157536, book: "Luke", verse: "12.055", unformatted: "南风一吹起，你们就说：‘要热起来了’，果然就如此。", version_id: 6},
  {id: 157537, book: "Luke", verse: "12.056", unformatted: "你们这些伪善的人！你们懂得分辨天地的气象，怎么不懂得分辨这个时代呢？", version_id: 6},
  {id: 157538, book: "Luke", verse: "12.057", unformatted: "要尽力和解\n“你们为什么自己不判断公义的事呢？", version_id: 6},
  {id: 157539, book: "Luke", verse: "12.058", unformatted: "你和你的对头去见官的时候，在路上就当尽力与他和解，免得他把你拉到审判官面前，审判官把你交给狱卒，狱卒就把你投进监狱。", version_id: 6},
  {id: 157540, book: "Luke", verse: "12.059", unformatted: "我告诉你：除非你还清了最后的一个小钱，否则你绝不能从那里出来。”", version_id: 6},
  {id: 157541, book: "Luke", verse: "13.001", unformatted: "不悔改则灭亡\n正在那时候，有些人前来告诉耶稣有关加利利人的事，就是彼拉多把加利利人的血与他们的祭物混在一起的事。", version_id: 6},
  {id: 157542, book: "Luke", verse: "13.002", unformatted: "耶稣回答他们，说：“你们以为这些加利利人如此受害，是因为他们比其他所有加利利人更有罪吗？", version_id: 6},
  {id: 157543, book: "Luke", verse: "13.003", unformatted: "不是的。我告诉你们：你们如果不悔改，都会同样地灭亡。", version_id: 6},
  {id: 157544, book: "Luke", verse: "13.004", unformatted: "或者，西罗亚楼倒塌压死的那十八个人，你们以为他们比所有住在耶路撒冷的人更有罪吗？", version_id: 6},
  {id: 157545, book: "Luke", verse: "13.005", unformatted: "不是的。我告诉你们：你们如果不悔改，都会同样灭亡。”", version_id: 6},
  {id: 157546, book: "Luke", verse: "13.006", unformatted: "无花果树的比喻\n于是耶稣讲了这个比喻：“一个人有一棵无花果树栽在自己的葡萄园里。当他过来，在那树上找果子时，却找不到，", version_id: 6},
  {id: 157547, book: "Luke", verse: "13.007", unformatted: "就对园丁说：‘你看，一连三年，我在这无花果树上找果子，都找不到。把它砍了吧！何必让它白占土地？’", version_id: 6},
  {id: 157548, book: "Luke", verse: "13.008", unformatted: "“但是园丁回答，对他说：‘主人，今年再留着它吧。等我在它周围挖掘，施些粪肥，", version_id: 6},
  {id: 157549, book: "Luke", verse: "13.009", unformatted: "或许它来年真会结出果子，否则，你就砍了它。’”", version_id: 6},
  {id: 157550, book: "Luke", verse: "13.01", unformatted: "治愈驼背的妇人\n在安息日，耶稣在一个会堂里教导人，", version_id: 6},
  {id: 157551, book: "Luke", verse: "13.011", unformatted: "看哪，有一个妇人，被邪灵附身病了十八年，一直弯着腰，完全直不起身来。", version_id: 6},
  {id: 157552, book: "Luke", verse: "13.012", unformatted: "耶稣看见她，就招呼她，对她说：“妇人，你已经从你的疾病中被释放了！”", version_id: 6},
  {id: 157553, book: "Luke", verse: "13.013", unformatted: "然后耶稣按手在她身上，她的腰立刻直了，她就不住地荣耀神。", version_id: 6},
  {id: 157554, book: "Luke", verse: "13.014", unformatted: "因为耶稣在安息日使人痊愈，会堂主管就很生气，对那群人说：“应该做工的日子有六天，所以你们在那些日子来得痊愈吧！但不要在安息日这一天。”", version_id: 6},
  {id: 157555, book: "Luke", verse: "13.015", unformatted: "主回答他，说：“你们这些伪善的人！难道你们每个人在安息日，不也是把自己的牛或驴从槽边解开，牵去饮水吗？", version_id: 6},
  {id: 157556, book: "Luke", verse: "13.016", unformatted: "何况这妇人，做为亚伯拉罕的子孙，被撒旦所捆绑竟然有十八年，难道不应该在安息日这一天从捆锁中被释放吗？”", version_id: 6},
  {id: 157557, book: "Luke", verse: "13.017", unformatted: "耶稣说了这些话，所有反对他的人都感到蒙羞，可是众人都因他所行的一切荣耀的事而感到欢喜。", version_id: 6},
  {id: 157558, book: "Luke", verse: "13.018", unformatted: "芥菜种与面酵的比喻\n于是耶稣说：“神的国好比什么呢？我要把它比做什么呢？", version_id: 6},
  {id: 157559, book: "Luke", verse: "13.019", unformatted: "它好比一粒芥菜种子，有人拿去种在自己的园子里，种子长大，成了一棵树，天空的飞鸟也在它的枝子上搭窝。”", version_id: 6},
  {id: 157560, book: "Luke", verse: "13.02", unformatted: "耶稣又说：“我要把神的国比做什么呢？", version_id: 6},
  {id: 157561, book: "Luke", verse: "13.021", unformatted: "它好比酵母，一个妇人拿去拌在三斗面里，直到整团面都发了酵。”", version_id: 6},
  {id: 157562, book: "Luke", verse: "13.022", unformatted: "要进窄门\n耶稣走遍许多城镇、乡村教导人，并继续往耶路撒冷前行。", version_id: 6},
  {id: 157563, book: "Luke", verse: "13.023", unformatted: "有一个人问他：“主啊，得救的人很少吗？”\n耶稣对他们说：", version_id: 6},
  {id: 157564, book: "Luke", verse: "13.024", unformatted: "“你们要为进窄门而争战，因为我告诉你们：将有许多人想要进去，却是不能。", version_id: 6},
  {id: 157565, book: "Luke", verse: "13.025", unformatted: "等到那家的主人起来关了门，你们才开始站在外面敲门，说：‘主啊，请给我们开门！’主人就会回答你们，说：‘我不知道你们是从哪里来的。’", version_id: 6},
  {id: 157566, book: "Luke", verse: "13.026", unformatted: "那时你们就开始说：‘我们曾经在你面前吃过、喝过，你也曾经在我们的大街上教导过！’", version_id: 6},
  {id: 157567, book: "Luke", verse: "13.027", unformatted: "他要对你们说：‘我不知道你们是从哪里来的。离开我，你们所有不义的工人！’", version_id: 6},
  {id: 157568, book: "Luke", verse: "13.028", unformatted: "当你们看见亚伯拉罕、以撒、雅各和所有的先知在神的国里，而你们却被丢在外面的时候，在那里将有哀哭和切齿。", version_id: 6},
  {id: 157569, book: "Luke", verse: "13.029", unformatted: "人们将从东、西、南、北而来，在神的国里坐席。", version_id: 6},
  {id: 157570, book: "Luke", verse: "13.03", unformatted: "看哪，有些在后的，将要在前；有些在前的，将要在后。”", version_id: 6},
  {id: 157571, book: "Luke", verse: "13.031", unformatted: "耶稣与希律\n就在这时候，有一些法利赛人前来对耶稣说：“你离开吧，从这里走开！因为希律想要杀你。”", version_id: 6},
  {id: 157572, book: "Luke", verse: "13.032", unformatted: "耶稣对他们说：“你们去告诉那狐狸：看哪！今天明天我驱赶鬼魔、治愈疾病，然后在第三天，我的事就得以完成了。", version_id: 6},
  {id: 157573, book: "Luke", verse: "13.033", unformatted: "不过今天、明天、后天，我必须往前走，因为先知不能在耶路撒冷之外被杀！", version_id: 6},
  {id: 157574, book: "Luke", verse: "13.034", unformatted: "为耶路撒冷哀叹\n“耶路撒冷啊，耶路撒冷！这城杀害先知们，又用石头砸死被差到她这里的人！我多次想聚集你的儿女，像母鸡把自己的小鸡聚集在翅膀下，可是你们不愿意！", version_id: 6},
  {id: 157575, book: "Luke", verse: "13.035", unformatted: "看哪，你们的家要被废弃。我告诉你们：你们绝不会再见到我了，直到那日子来临，就是你们说‘奉主名而来的那一位是蒙祝福的’那时候。”", version_id: 6},
  {id: 157576, book: "Luke", verse: "14.001", unformatted: "安息日的辩论\n有一个安息日，耶稣到一个法利赛人的首领家里去吃饭，他们密切注意着耶稣。", version_id: 6},
  {id: 157577, book: "Luke", verse: "14.002", unformatted: "当时，有一个患水肿病的人在他面前。", version_id: 6},
  {id: 157578, book: "Luke", verse: "14.003", unformatted: "于是耶稣问律法师们和法利赛人，说：“在安息日使人痊愈，可以不可以？”", version_id: 6},
  {id: 157579, book: "Luke", verse: "14.004", unformatted: "他们都安静下来。耶稣就扶着那人，使他痊愈，让他走了。", version_id: 6},
  {id: 157580, book: "Luke", verse: "14.005", unformatted: "然后耶稣对他们说：“你们当中谁有儿子或牛在安息日这一天掉到井里，而不立刻把他拉上来呢？”", version_id: 6},
  {id: 157581, book: "Luke", verse: "14.006", unformatted: "他们对这些话不能答辩。", version_id: 6},
  {id: 157582, book: "Luke", verse: "14.007", unformatted: "论谦卑\n耶稣留意到被邀请的人怎样在那里挑选上座，就对他们讲了一个比喻说：", version_id: 6},
  {id: 157583, book: "Luke", verse: "14.008", unformatted: "“你被人邀请赴婚宴的时候，不要坐在上座，恐怕有比你更尊贵的人也被邀请，", version_id: 6},
  {id: 157584, book: "Luke", verse: "14.009", unformatted: "那邀请你们的会来对你说：‘请让座给这一位！’那时候，你就要羞惭地去坐末位了。", version_id: 6},
  {id: 157585, book: "Luke", verse: "14.01", unformatted: "“你被邀请的时候，要去坐在末位上，好让邀请你的人一旦来，就会对你说：‘朋友，请来上座。’那时候，你就在所有一同坐席的人面前有光彩了。", version_id: 6},
  {id: 157586, book: "Luke", verse: "14.011", unformatted: "因为每一个自我高举的都会被降卑；而自我降卑的都会被高举。”", version_id: 6},
  {id: 157587, book: "Luke", verse: "14.012", unformatted: "耶稣又对邀请他的人说：“你预备午餐或晚餐的时候，不要叫你的朋友、兄弟、亲戚或富有的邻居，免得他们也回请你，你就得了你的回报了。", version_id: 6},
  {id: 157588, book: "Luke", verse: "14.013", unformatted: "相反，你预备宴席的时候，要邀请那些贫穷的、残疾的、瘸腿的、瞎眼的，", version_id: 6},
  {id: 157589, book: "Luke", verse: "14.014", unformatted: "这样你就蒙福了，因为他们没有可回报你的，而在义人复活的时候，你将得到回报。”", version_id: 6},
  {id: 157590, book: "Luke", verse: "14.015", unformatted: "大筵席的比喻\n一同坐席的有一个人听了这些话，就说：“将来能在神的国里吃饭的人是蒙福的！”", version_id: 6},
  {id: 157591, book: "Luke", verse: "14.016", unformatted: "耶稣对他说：“有一个人预备盛大的宴席，邀请了许多人。", version_id: 6},
  {id: 157592, book: "Luke", verse: "14.017", unformatted: "到了宴席的时刻，他派奴仆去对那些被邀请的人说：‘请来吧，一切都已经预备好了！’", version_id: 6},
  {id: 157593, book: "Luke", verse: "14.018", unformatted: "“可是他们都开始一致地推辞。第一个说：‘我买了一块地，必须去看看，请你容我推辞吧。’", version_id: 6},
  {id: 157594, book: "Luke", verse: "14.019", unformatted: "“另一个说：‘我买了五对牛，正要去试试它们，请你容我推辞吧。’", version_id: 6},
  {id: 157595, book: "Luke", verse: "14.02", unformatted: "“另有人说：‘我刚娶了妻子，所以不能去。’", version_id: 6},
  {id: 157596, book: "Luke", verse: "14.021", unformatted: "“奴仆回来把这些事报告给主人。那家的主人就发怒，吩咐奴仆说：‘赶快出去，到城里的大街小巷，把那些贫穷的、残疾的、瞎眼的、瘸腿的，都带到这里来。’", version_id: 6},
  {id: 157597, book: "Luke", verse: "14.022", unformatted: "“奴仆说：‘主人，你所吩咐的已经做了，可是还有位子。’", version_id: 6},
  {id: 157598, book: "Luke", verse: "14.023", unformatted: "“主人就对奴仆说：‘出去，到大街和小巷上去，强求人进来，好坐满我的房子。", version_id: 6},
  {id: 157599, book: "Luke", verse: "14.024", unformatted: "我告诉你们：原本被邀请的那些人，没有一个能尝到我的宴席。’”", version_id: 6},
  {id: 157600, book: "Luke", verse: "14.025", unformatted: "跟随主的代价\n有一大群人与耶稣同行。他转过身来对他们说：", version_id: 6},
  {id: 157601, book: "Luke", verse: "14.026", unformatted: "“如果有人到我这里来，而不恨自己的父亲、母亲、妻子、儿女、兄弟、姐妹，甚至自己的生命，他就不能做我的门徒。", version_id: 6},
  {id: 157602, book: "Luke", verse: "14.027", unformatted: "凡是不背着自己的十字架来跟从我的，也不能做我的门徒。", version_id: 6},
  {id: 157603, book: "Luke", verse: "14.028", unformatted: "“你们当中到底有谁想盖一座楼，不先坐下来计算费用，看自己是否能够完成呢？", version_id: 6},
  {id: 157604, book: "Luke", verse: "14.029", unformatted: "免得立好了地基，却不能完工，看见的人都开始嘲笑他，", version_id: 6},
  {id: 157605, book: "Luke", verse: "14.03", unformatted: "说：‘这个人开始盖了，却不能完工！’", version_id: 6},
  {id: 157606, book: "Luke", verse: "14.031", unformatted: "“或者，有哪一个君王要出去和别的君王交战，不先坐下来考虑是否能够用一万兵去迎战那用两万兵来攻打他的呢？", version_id: 6},
  {id: 157607, book: "Luke", verse: "14.032", unformatted: "否则，就会趁着他还远的时候，差派特使去求问和平的事宜。", version_id: 6},
  {id: 157608, book: "Luke", verse: "14.033", unformatted: "同样，你们当中任何人，如果不向自己所拥有的一切告别，就不能做我的门徒。", version_id: 6},
  {id: 157609, book: "Luke", verse: "14.034", unformatted: "“因此盐是好的，但如果连盐也失去了味道，还能用什么来调味呢？", version_id: 6},
  {id: 157610, book: "Luke", verse: "14.035", unformatted: "把它用在田地里或在粪肥里都不合适，只好把它丢在外面。凡是有耳可听的，就应当听！”", version_id: 6},
  {id: 157611, book: "Luke", verse: "15.001", unformatted: "迷羊的比喻\n当时，税吏和罪人都接近耶稣，听他讲说。", version_id: 6},
  {id: 157612, book: "Luke", verse: "15.002", unformatted: "法利赛人和经文士抱怨，说：“这个人欢迎罪人，又与他们一起吃饭。”", version_id: 6},
  {id: 157613, book: "Luke", verse: "15.003", unformatted: "于是耶稣对他们讲了这个比喻：", version_id: 6},
  {id: 157614, book: "Luke", verse: "15.004", unformatted: "“你们当中的哪一个人拥有一百只羊，而丢失其中的一只，就不会把九十九只留在旷野，去找那一只丢失的，直到找到了它呢？", version_id: 6},
  {id: 157615, book: "Luke", verse: "15.005", unformatted: "一旦找到了，他就快快乐乐地扛在自己的肩上，", version_id: 6},
  {id: 157616, book: "Luke", verse: "15.006", unformatted: "回到家里请朋友和邻居来，对他们说：‘你们与我一同欢乐吧，因为我找到了那只丢失的羊。’", version_id: 6},
  {id: 157617, book: "Luke", verse: "15.007", unformatted: "我告诉你们：同样，一个罪人的悔改，比九十九个不需要悔改的义人，会让天上更有喜乐。", version_id: 6},
  {id: 157618, book: "Luke", verse: "15.008", unformatted: "失钱的比喻\n“或者，如果一个妇人有十个银币，丢失了一个，难道她不点上油灯，打扫屋子，仔细寻找，直到找到了它吗？", version_id: 6},
  {id: 157619, book: "Luke", verse: "15.009", unformatted: "一旦找到了，她就会请朋友和邻居来，说：‘你们与我一同欢乐吧，因为我找到了那个丢失的银币。’", version_id: 6},
  {id: 157620, book: "Luke", verse: "15.01", unformatted: "同样，我告诉你们：一个罪人的悔改，会让神的天使们面前有喜乐。”", version_id: 6},
  {id: 157621, book: "Luke", verse: "15.011", unformatted: "浪子的比喻\n耶稣又说：“某人有两个儿子。", version_id: 6},
  {id: 157622, book: "Luke", verse: "15.012", unformatted: "小儿子对父亲说：‘父亲，请把我应得的那份家产给我。’父亲就把财产分给两个儿子。", version_id: 6},
  {id: 157623, book: "Luke", verse: "15.013", unformatted: "没过几天，那小儿子就收拾了一切，往一个遥远的地方去旅行了。他在那里过着放荡的生活，挥霍他的钱财。", version_id: 6},
  {id: 157624, book: "Luke", verse: "15.014", unformatted: "当他花尽了自己的一切，那地方又发生了严重的饥荒，他就开始穷困起来。", version_id: 6},
  {id: 157625, book: "Luke", verse: "15.015", unformatted: "于是他去投靠当地的一个居民，那人打发他到自己的田里去放猪。", version_id: 6},
  {id: 157626, book: "Luke", verse: "15.016", unformatted: "他就是渴望拿猪吃的豆荚来充饥，也没有人给他。", version_id: 6},
  {id: 157627, book: "Luke", verse: "15.017", unformatted: "可是他醒悟过来的时候，就说：‘我父亲那么多的雇工都食物充足，我却要在这里饿死吗？", version_id: 6},
  {id: 157628, book: "Luke", verse: "15.018", unformatted: "我要起来，到我父亲那里去，对他说：‘父亲，我对天犯了罪，也在你面前犯了罪。", version_id: 6},
  {id: 157629, book: "Luke", verse: "15.019", unformatted: "我再也不配称为你的儿子，请把我当做你的一个雇工吧。’", version_id: 6},
  {id: 157630, book: "Luke", verse: "15.02", unformatted: "于是他起来往自己的父亲那里去。他离得还远的时候，他父亲就看到他，动了怜悯之心，跑上去搂住他的脖子亲吻他。", version_id: 6},
  {id: 157631, book: "Luke", verse: "15.021", unformatted: "儿子说：‘父亲，我对天犯了罪，也在你面前犯了罪。我再也不配称为你的儿子。’", version_id: 6},
  {id: 157632, book: "Luke", verse: "15.022", unformatted: "“父亲却对奴仆们说：‘赶快把那最好的袍子拿来给他穿上，把戒指戴在他手上，把鞋子穿在他脚上，", version_id: 6},
  {id: 157633, book: "Luke", verse: "15.023", unformatted: "把那只养肥的牛犊牵来宰杀了，让我们吃喝、庆祝吧！", version_id: 6},
  {id: 157634, book: "Luke", verse: "15.024", unformatted: "因为我这个儿子是死而复生、失而复得的。’于是他们开始庆祝。", version_id: 6},
  {id: 157635, book: "Luke", verse: "15.025", unformatted: "“那时，大儿子正在田里。当他往回走，快到家的时候，听见音乐和跳舞的声音，", version_id: 6},
  {id: 157636, book: "Luke", verse: "15.026", unformatted: "就叫来一个仆人，问这些到底是什么事。", version_id: 6},
  {id: 157637, book: "Luke", verse: "15.027", unformatted: "仆人对他说：‘你弟弟回来了。你父亲因他平安无恙地回来，就宰杀了那只养肥的牛犊。’", version_id: 6},
  {id: 157638, book: "Luke", verse: "15.028", unformatted: "“大儿子发怒，不肯进去，他父亲就出来劝他。", version_id: 6},
  {id: 157639, book: "Luke", verse: "15.029", unformatted: "他回答父亲，说：‘你看，我服事你这么多年，从来没有违背过你的命令，可是你从来没有给过我一只小山羊，好让我与朋友们一同庆祝。", version_id: 6},
  {id: 157640, book: "Luke", verse: "15.03", unformatted: "而你这个儿子，这个与妓女们一起吞尽了你财产的，他一来了，你就为他宰杀了养肥的牛犊！’", version_id: 6},
  {id: 157641, book: "Luke", verse: "15.031", unformatted: "“父亲对他说：‘孩子啊，你一直与我在一起，我的一切都是你的。", version_id: 6},
  {id: 157642, book: "Luke", verse: "15.032", unformatted: "现在，我们应该庆祝、欢喜，因为你这个弟弟是死而复生、失而复得的。’”", version_id: 6},
  {id: 157643, book: "Luke", verse: "16.001", unformatted: "不义管家的比喻\n耶稣又对门徒们说：“某财主有个管家，这个管家被人控告挥霍主人的财产。", version_id: 6},
  {id: 157644, book: "Luke", verse: "16.002", unformatted: "财主就把管家叫来，对他说：‘我听到有关你的事，到底是什么？把你所管理的做出交代，因为你不能再当管家了。’", version_id: 6},
  {id: 157645, book: "Luke", verse: "16.003", unformatted: "“那管家心里说：‘主人要把我管家的任务拿去了，我能做什么呢？挖地没有力气，乞讨又感到羞耻。", version_id: 6},
  {id: 157646, book: "Luke", verse: "16.004", unformatted: "我知道我该做什么了，好使我被免除管家的任务以后，人们会接我到他们家里去。’", version_id: 6},
  {id: 157647, book: "Luke", verse: "16.005", unformatted: "“于是他把自己主人的每一个欠债人都叫了来。他问第一个：‘你欠我主人多少？’", version_id: 6},
  {id: 157648, book: "Luke", verse: "16.006", unformatted: "“他说：‘一百桶油。’\n“管家就对他说：‘拿你的欠条，赶快坐下来写成五十。’", version_id: 6},
  {id: 157649, book: "Luke", verse: "16.007", unformatted: "“他问另一个说：‘你呢，你欠多少？’\n“他说：‘一百担麦子。’\n“管家说：‘拿你的欠条，写成八十。’", version_id: 6},
  {id: 157650, book: "Luke", verse: "16.008", unformatted: "“于是主人因这个不义的管家做事精明，就称赞他。这样看来，今世的儿女在自己的世代里，比光明的儿女还要聪明。", version_id: 6},
  {id: 157651, book: "Luke", verse: "16.009", unformatted: "我告诉你们：要藉着那不义的财富为自己结交朋友，这样当财富无用的时候，你们会被接到永恒的居所里。", version_id: 6},
  {id: 157652, book: "Luke", verse: "16.01", unformatted: "在最小的事上忠心的人，在很多事上也忠心；在最小的事上不义的人，在很多事上也不义。", version_id: 6},
  {id: 157653, book: "Luke", verse: "16.011", unformatted: "因此，你们如果在不义的财富上不忠心，谁会把真正的财富委托给你们呢？", version_id: 6},
  {id: 157654, book: "Luke", verse: "16.012", unformatted: "你们如果在别人的东西上不忠心，谁会把属于你们的交给你们呢？", version_id: 6},
  {id: 157655, book: "Luke", verse: "16.013", unformatted: "一个仆人不能服事两个主人。他不是厌恶这个、喜爱那个，就是忠于这个、轻视那个。你们不能既服事神，又服事财富。”", version_id: 6},
  {id: 157656, book: "Luke", verse: "16.014", unformatted: "神的标准\n爱钱财的法利赛人听到这些一切话，就讥笑耶稣。", version_id: 6},
  {id: 157657, book: "Luke", verse: "16.015", unformatted: "耶稣对他们说：“你们就是那些在人面前显示自己为义的，但是神知道你们的心。人看为高贵的，在神面前却是可憎恶的。", version_id: 6},
  {id: 157658, book: "Luke", verse: "16.016", unformatted: "“律法和先知到约翰为止，从此神国的福音被传扬，人人都在努力争取进入它。", version_id: 6},
  {id: 157659, book: "Luke", verse: "16.017", unformatted: "天地的消逝，也比律法的一笔一画落空更容易呢！", version_id: 6},
  {id: 157660, book: "Luke", verse: "16.018", unformatted: "“凡是休妻另娶的，就是犯通奸罪；凡是娶了被丈夫休的女人的，也是犯通奸罪。", version_id: 6},
  {id: 157661, book: "Luke", verse: "16.019", unformatted: "财主与拉撒路\n“有一个财主，身穿紫色袍子和细麻衣，天天奢华宴乐。", version_id: 6},
  {id: 157662, book: "Luke", verse: "16.02", unformatted: "又有一个穷人，名叫拉撒路，满身脓疮，被放在财主的门口，", version_id: 6},
  {id: 157663, book: "Luke", verse: "16.021", unformatted: "渴望得到财主桌子上掉下来的碎渣充饥，甚至有狗来舔他的疮。", version_id: 6},
  {id: 157664, book: "Luke", verse: "16.022", unformatted: "后来那穷人死了，被天使们带到亚伯拉罕的怀里。接着，那财主也死了，并且被埋葬了。", version_id: 6},
  {id: 157665, book: "Luke", verse: "16.023", unformatted: "财主在阴间的痛苦中，举目远远地望见亚伯拉罕，又望见拉撒路在他怀里，", version_id: 6},
  {id: 157666, book: "Luke", verse: "16.024", unformatted: "就呼唤说：‘我父亚伯拉罕，可怜我吧！请打发拉撒路用指头蘸点水，凉一下我的舌头吧，因为我在这火焰里，极其痛苦！’", version_id: 6},
  {id: 157667, book: "Luke", verse: "16.025", unformatted: "“可是亚伯拉罕说：‘孩子啊，你当想起，你一生怎样享了你的福，而拉撒路怎样受了苦；但如今他在这里受到安慰，你却在痛苦中。", version_id: 6},
  {id: 157668, book: "Luke", verse: "16.026", unformatted: "这一切之外，在我们和你们之间，隔着巨大的深渊，所以人想要从这边过到你们那边是不可能的，从那边过到我们这边也是不可能的。’", version_id: 6},
  {id: 157669, book: "Luke", verse: "16.027", unformatted: "“财主就说：‘我父啊，那么求你打发拉撒路到我父亲家去，", version_id: 6},
  {id: 157670, book: "Luke", verse: "16.028", unformatted: "因为我有五个兄弟，让他去郑重地劝诫他们，免得他们也来到这痛苦的地方。’", version_id: 6},
  {id: 157671, book: "Luke", verse: "16.029", unformatted: "“但是亚伯拉罕说：‘你的兄弟们有摩西和先知们，让你的兄弟们听从他们吧。’", version_id: 6},
  {id: 157672, book: "Luke", verse: "16.03", unformatted: "“财主却说：‘不，我父亚伯拉罕哪！其实如果有人从死人中复活，到他们那里去，他们就会悔改。’", version_id: 6},
  {id: 157673, book: "Luke", verse: "16.031", unformatted: "“可是亚伯拉罕对他说：‘如果他们不听从摩西和先知们，就算有人从死人中复活，他们也不会被说服的。’", version_id: 6},
  {id: 157674, book: "Luke", verse: "17.001", unformatted: "耶稣的警告\n耶稣对他的门徒们说：“那些使人绊脚的事必然出现，不过使它出现的人有祸了。", version_id: 6},
  {id: 157675, book: "Luke", verse: "17.002", unformatted: "对他来说，就算脖子上拴着大磨石，被丢到海里，也比他使这些卑微人中的一个绊倒更好。", version_id: 6},
  {id: 157676, book: "Luke", verse: "17.003", unformatted: "你们要谨慎！如果你的弟兄犯了罪，就告诫他；如果他悔改，就饶恕他。", version_id: 6},
  {id: 157677, book: "Luke", verse: "17.004", unformatted: "如果他一天七次对你犯了罪，又七次回转过来，对你说：‘我悔改！’你要饶恕他。”", version_id: 6},
  {id: 157678, book: "Luke", verse: "17.005", unformatted: "信心与本分\n使徒们对主说：“请你加添我们的信心。”", version_id: 6},
  {id: 157679, book: "Luke", verse: "17.006", unformatted: "主说：“如果你们有像一粒芥菜种子那样的信仰，即使是对这棵桑树说‘愿你被连根拔起，栽到海里去！’它也会听从你们。", version_id: 6},
  {id: 157680, book: "Luke", verse: "17.007", unformatted: "“你们当中谁有奴仆耕田或放羊，从田里回来的时候，会对奴仆说‘立刻过来，坐下吃饭’呢？", version_id: 6},
  {id: 157681, book: "Luke", verse: "17.008", unformatted: "他难道不会对奴仆说‘你预备我所要吃的晚餐，束上腰带伺候我，等我吃喝完了，你才要吃喝’吗？", version_id: 6},
  {id: 157682, book: "Luke", verse: "17.009", unformatted: "奴仆做了所吩咐的事，难道主人要向奴仆怀有感恩的心吗？", version_id: 6},
  {id: 157683, book: "Luke", verse: "17.01", unformatted: "你们也是这样，当你们做完了所吩咐你们的一切事以后，应该说‘我们是无用的奴仆，只是做了自己该做的。’”", version_id: 6},
  {id: 157684, book: "Luke", verse: "17.011", unformatted: "十个麻风病人\n耶稣往耶路撒冷去的时候，经过撒马利亚和加利利的中间地带。", version_id: 6},
  {id: 157685, book: "Luke", verse: "17.012", unformatted: "他进了一个村子，有十个麻风病人见到他，远远地站着，", version_id: 6},
  {id: 157686, book: "Luke", verse: "17.013", unformatted: "大声说：“耶稣啊，老师啊，可怜我们吧！”", version_id: 6},
  {id: 157687, book: "Luke", verse: "17.014", unformatted: "耶稣看见了，就对他们说：“你们去，让祭司检查你们吧！”结果在他们去的时候，就被洁净了。", version_id: 6},
  {id: 157688, book: "Luke", verse: "17.015", unformatted: "其中有一个人看见自己得了痊愈，就回来大声地荣耀神，", version_id: 6},
  {id: 157689, book: "Luke", verse: "17.016", unformatted: "并且把脸伏在耶稣脚前，感谢他。这人是个撒马利亚人。", version_id: 6},
  {id: 157690, book: "Luke", verse: "17.017", unformatted: "耶稣回答说：“被洁净的不是有十个人吗？另外九个在哪里呢？", version_id: 6},
  {id: 157691, book: "Luke", verse: "17.018", unformatted: "除了这个外邦人，难道没有人回来把荣耀归给神吗？”", version_id: 6},
  {id: 157692, book: "Luke", verse: "17.019", unformatted: "然后耶稣对他说：“起来，走吧！你的信救了你。”", version_id: 6},
  {id: 157693, book: "Luke", verse: "17.02", unformatted: "天国的来临\n有一次，法利赛人问耶稣：“神的国什么时候到来呢？”耶稣回答说：“神国的到来，不是眼所能见的。", version_id: 6},
  {id: 157694, book: "Luke", verse: "17.021", unformatted: "没有人能说‘看，在这里！’或‘在那里！’因为看哪，神的国就在你们中间。”", version_id: 6},
  {id: 157695, book: "Luke", verse: "17.022", unformatted: "耶稣又对门徒们说：“日子将要来到，那时候你们会渴望看见人子日子中的一天，可是看不到。", version_id: 6},
  {id: 157696, book: "Luke", verse: "17.023", unformatted: "他们会告诉你们‘看，在那里！’或‘看，在这里！’你们不要出去，也不要追随他们；", version_id: 6},
  {id: 157697, book: "Luke", verse: "17.024", unformatted: "因为就像闪电一闪，从天这边照耀到天那边，人子在他的日子也将要这样。", version_id: 6},
  {id: 157698, book: "Luke", verse: "17.025", unformatted: "不过他必须先受很多苦害，又被这世代所弃绝。", version_id: 6},
  {id: 157699, book: "Luke", verse: "17.026", unformatted: "“在挪亚的那些日子里发生的怎样，在人子的那些日子里也将要怎样：", version_id: 6},
  {id: 157700, book: "Luke", verse: "17.027", unformatted: "人们继续吃、喝、嫁、娶，直到挪亚进方舟的那一天，洪水到来，把一切都毁灭了。", version_id: 6},
  {id: 157701, book: "Luke", verse: "17.028", unformatted: "同样，就像在罗得的那些日子里发生的那样：人们继续吃喝、买卖、耕种、建造，", version_id: 6},
  {id: 157702, book: "Luke", verse: "17.029", unformatted: "到了罗得离开所多玛的那一天，烈火和硫磺就从天而降，把一切都毁灭了。", version_id: 6},
  {id: 157703, book: "Luke", verse: "17.03", unformatted: "人子显现的那一天，也将是同样的。", version_id: 6},
  {id: 157704, book: "Luke", verse: "17.031", unformatted: "在那一天，在屋顶上的人，如果东西在屋子里，不要下来拿；同样，在田里的人，也不要转回到背后的事上。", version_id: 6},
  {id: 157705, book: "Luke", verse: "17.032", unformatted: "你们当记得罗得的妻子！", version_id: 6},
  {id: 157706, book: "Luke", verse: "17.033", unformatted: "凡想要保全自己生命的，将失去生命；凡失去自己生命的，将保存生命。", version_id: 6},
  {id: 157707, book: "Luke", verse: "17.034", unformatted: "我告诉你们：在那一夜，两个人在一张床上，一个会被接去，另一个会被留下；", version_id: 6},
  {id: 157708, book: "Luke", verse: "17.035", unformatted: "两个女人在一起推磨，一个会被接去，另一个会被留下。”", version_id: 6},
  {id: 157709, book: "Luke", verse: "17.036", unformatted: "两个人在田里，一个会被接去，另一个会被留下。”", version_id: 6},
  {id: 157710, book: "Luke", verse: "17.037", unformatted: "门徒们问耶稣，说：“主啊，在哪里呢？”\n耶稣对他们说：“尸体在哪里，秃鹰也就聚集在哪里。”", version_id: 6},
  {id: 157711, book: "Luke", verse: "18.001", unformatted: "寡妇与法官的比喻\n耶稣对他们讲了一个比喻，说他们必须常常祷告，不要丧胆。", version_id: 6},
  {id: 157712, book: "Luke", verse: "18.002", unformatted: "他说：“某城里有一个审判官，他既不敬畏神，也不尊重人。", version_id: 6},
  {id: 157713, book: "Luke", verse: "18.003", unformatted: "那城里有一个寡妇，经常到他那里去，说：‘请为我向我的对头讨个公道。’", version_id: 6},
  {id: 157714, book: "Luke", verse: "18.004", unformatted: "“法官一直不肯，但是后来心里说：‘即使我不敬畏神，也不尊重人，", version_id: 6},
  {id: 157715, book: "Luke", verse: "18.005", unformatted: "还是因为这寡妇不断地烦扰我，我就为她讨个公道，免得她一直来，到最后使我疲惫不堪！’”", version_id: 6},
  {id: 157716, book: "Luke", verse: "18.006", unformatted: "主接着说：“你们当听这个不公义的审判官所说的！", version_id: 6},
  {id: 157717, book: "Luke", verse: "18.007", unformatted: "难道神绝不会给那些他所拣选、日夜向他呼求的人主持公道吗？难道神会耽误帮助他们吗？", version_id: 6},
  {id: 157718, book: "Luke", verse: "18.008", unformatted: "我告诉你们：他会很快地给他们主持公道。不过人子回来的时候，难道在地上能找到这样的信仰吗？”", version_id: 6},
  {id: 157719, book: "Luke", verse: "18.009", unformatted: "法利赛人与税吏的比喻\n耶稣还对某些自以为义而藐视其他人的，讲了这个比喻：", version_id: 6},
  {id: 157720, book: "Luke", verse: "18.01", unformatted: "“有两个人上圣殿去祷告。一个是法利赛人，另一个是税吏。", version_id: 6},
  {id: 157721, book: "Luke", verse: "18.011", unformatted: "那法利赛人站着，向自己祷告这些话：‘神哪，我感谢你，因为我不像其他人那样：勒索、不义、通奸，也不像这个税吏。", version_id: 6},
  {id: 157722, book: "Luke", verse: "18.012", unformatted: "我一周禁食两次，并奉献我所有收入的十分之一。’", version_id: 6},
  {id: 157723, book: "Luke", verse: "18.013", unformatted: "“那税吏却远远地站着，甚至不敢举目望天，可是捶着胸说：‘神哪，你宽恕我这个罪人吧！’", version_id: 6},
  {id: 157724, book: "Luke", verse: "18.014", unformatted: "我告诉你们：下去回家时已经被称为义的是这个税吏，而不是那个法利赛人。因为每一个自我高举的都会被降卑；而自我降卑的都会被高举。”", version_id: 6},
  {id: 157725, book: "Luke", verse: "18.015", unformatted: "祝福小孩子\n有些人把婴孩也带到耶稣那里，好让耶稣摸他们一下。门徒们看见了却责备那些人。", version_id: 6},
  {id: 157726, book: "Luke", verse: "18.016", unformatted: "但耶稣把他们叫过来，说：“让小孩子到我这里来，不要阻止他们，因为神的国正属于这样的人。", version_id: 6},
  {id: 157727, book: "Luke", verse: "18.017", unformatted: "我确实地告诉你们：无论谁不像小孩子一样接受神的国，就绝不能进去。”", version_id: 6},
  {id: 157728, book: "Luke", verse: "18.018", unformatted: "富有的青年官员\n有一个首领问耶稣，说：“良善的老师，我该做什么才会继承永恒的生命呢？”", version_id: 6},
  {id: 157729, book: "Luke", verse: "18.019", unformatted: "耶稣对他说：“你为什么称我是良善的呢？除了神一位之外，没有一个是良善的。", version_id: 6},
  {id: 157730, book: "Luke", verse: "18.02", unformatted: "你是知道这些诫命的：\n‘不可通奸、\n不可杀人、\n不可偷窃、\n不可做伪证、\n要孝敬父母。’”", version_id: 6},
  {id: 157731, book: "Luke", verse: "18.021", unformatted: "那个人说：“这一切我从年少就遵守了。”", version_id: 6},
  {id: 157732, book: "Luke", verse: "18.022", unformatted: "耶稣听了就对他说：“你还缺少一件：把你所拥有的一切都卖掉，分给穷人，这样你将有财宝在天上；然后你来跟从我。”", version_id: 6},
  {id: 157733, book: "Luke", verse: "18.023", unformatted: "那个人听了这些话就变得很忧愁，因为他极其富有。", version_id: 6},
  {id: 157734, book: "Luke", verse: "18.024", unformatted: "财富与天国\n耶稣看见他变得很忧愁，就说：“有钱财的人进入神的国，是多么困难！", version_id: 6},
  {id: 157735, book: "Luke", verse: "18.025", unformatted: "骆驼穿过针眼，比富有的人进入神的国更容易呢！”", version_id: 6},
  {id: 157736, book: "Luke", verse: "18.026", unformatted: "听见的人就说：“那么，谁能得救呢？”", version_id: 6},
  {id: 157737, book: "Luke", verse: "18.027", unformatted: "耶稣说：“在人不可能的事，在神都是可能的。”", version_id: 6},
  {id: 157738, book: "Luke", verse: "18.028", unformatted: "彼得说：“看，我们舍弃自己所拥有的，跟从了你！”", version_id: 6},
  {id: 157739, book: "Luke", verse: "18.029", unformatted: "耶稣对他们说：“我确实地告诉你们：没有谁为神国的缘故舍弃房屋，或妻子，或兄弟，或父母，或儿女，", version_id: 6},
  {id: 157740, book: "Luke", verse: "18.03", unformatted: "而不会在今世得到许多倍，在来世得到永恒生命的。”", version_id: 6},
  {id: 157741, book: "Luke", verse: "18.031", unformatted: "第三次预言受难\n耶稣把十二使徒带到一边，对他们说：“看，我们现在上耶路撒冷去，那藉着先知们所写的一切有关人子的事，都要成就。", version_id: 6},
  {id: 157742, book: "Luke", verse: "18.032", unformatted: "要知道，他将被交给外邦人，被戏弄、受凌辱、被人吐唾沫；", version_id: 6},
  {id: 157743, book: "Luke", verse: "18.033", unformatted: "他们要鞭打他，并杀害他；然后在第三天，他要复活。”", version_id: 6},
  {id: 157744, book: "Luke", verse: "18.034", unformatted: "但门徒们一点也不领悟这些话。这话对他们是隐藏的，他们不知道他在说什么。", version_id: 6},
  {id: 157745, book: "Luke", verse: "18.035", unformatted: "治愈耶利哥的盲人\n耶稣快到耶利哥的时候，有一个瞎眼的坐在路边乞讨。", version_id: 6},
  {id: 157746, book: "Luke", verse: "18.036", unformatted: "他听见人群经过那里，就问到底是什么事。", version_id: 6},
  {id: 157747, book: "Luke", verse: "18.037", unformatted: "有人告诉他：“拿撒勒人耶稣经过这里。”", version_id: 6},
  {id: 157748, book: "Luke", verse: "18.038", unformatted: "他就呼喊说：“耶稣啊，大卫的后裔，可怜我吧！”", version_id: 6},
  {id: 157749, book: "Luke", verse: "18.039", unformatted: "走在前头的人就责备他，要他安静。可是，他却更加大声呼叫：“大卫的后裔，可怜我吧！”", version_id: 6},
  {id: 157750, book: "Luke", verse: "18.04", unformatted: "耶稣就停住，吩咐把他带到他面前来；那人走近了，耶稣就问他：", version_id: 6},
  {id: 157751, book: "Luke", verse: "18.041", unformatted: "“你要我为你做什么呢？”\n他说：“主啊，我要重见光明！”", version_id: 6},
  {id: 157752, book: "Luke", verse: "18.042", unformatted: "耶稣对他说：“你重见光明吧！你的信救了你。”", version_id: 6},
  {id: 157753, book: "Luke", verse: "18.043", unformatted: "他立刻重见光明，于是跟随耶稣，一路上荣耀神。民众都看到了，就赞美神。", version_id: 6},
  {id: 157754, book: "Luke", verse: "19.001", unformatted: "耶稣与撒该\n耶稣进了耶利哥，从城里经过，", version_id: 6},
  {id: 157755, book: "Luke", verse: "19.002", unformatted: "看哪，有一个名叫撒该的人，是个税吏长，很富有。", version_id: 6},
  {id: 157756, book: "Luke", verse: "19.003", unformatted: "他很想看看耶稣是什么样的人，可是因为他身材矮小，从人群中无法看到耶稣。", version_id: 6},
  {id: 157757, book: "Luke", verse: "19.004", unformatted: "于是他跑到前头，爬上一棵桑树，要看耶稣，因为耶稣就要从那里经过。", version_id: 6},
  {id: 157758, book: "Luke", verse: "19.005", unformatted: "耶稣来到那地方时，抬头一看，对他说：“撒该，快下来！因为我今天必须住在你家里。”", version_id: 6},
  {id: 157759, book: "Luke", verse: "19.006", unformatted: "撒该就急忙下来，快快乐乐地接待耶稣。", version_id: 6},
  {id: 157760, book: "Luke", verse: "19.007", unformatted: "大家看见，就抱怨说：“他竟然到一个罪人家里去借宿。”", version_id: 6},
  {id: 157761, book: "Luke", verse: "19.008", unformatted: "撒该站着对主说：“主啊，请看，我要把我财产的一半分给穷人。我如果勒索过谁的东西，就偿还四倍。”", version_id: 6},
  {id: 157762, book: "Luke", verse: "19.009", unformatted: "耶稣对他说：“今天救恩临到了这一家，因为他也是亚伯拉罕的子孙。", version_id: 6},
  {id: 157763, book: "Luke", verse: "19.01", unformatted: "要知道，人子来，是为了寻找并拯救迷失的人。”", version_id: 6},
  {id: 157764, book: "Luke", verse: "19.011", unformatted: "一千银币的比喻\n大家正听这些话的时候，耶稣再讲了一个比喻，因为他已经离耶路撒冷不远，而且他们以为神的国快要显现了。", version_id: 6},
  {id: 157765, book: "Luke", verse: "19.012", unformatted: "他说：“有一个出身高贵的人，要往遥远的地方去接受王位，然后再回来。", version_id: 6},
  {id: 157766, book: "Luke", verse: "19.013", unformatted: "他叫来自己的十个奴仆，给他们一千银币，对他们说：‘你们去做生意，直到我回来。’", version_id: 6},
  {id: 157767, book: "Luke", verse: "19.014", unformatted: "“他本国的人却憎恨他，就差派特使在他背后去，说：‘我们不要这个人做王统治我们。’", version_id: 6},
  {id: 157768, book: "Luke", verse: "19.015", unformatted: "“当他接受了王位回来的时候，就吩咐把这些曾领了银子的奴仆叫来，要知道他们做生意赚了多少。", version_id: 6},
  {id: 157769, book: "Luke", verse: "19.016", unformatted: "第一个前来说：‘主啊，你的那一百银币，已经赚了一千。’", version_id: 6},
  {id: 157770, book: "Luke", verse: "19.017", unformatted: "“主人对他说：‘做得好，好奴仆！因为你在最小的事上忠心，你就有权掌管十个城。’", version_id: 6},
  {id: 157771, book: "Luke", verse: "19.018", unformatted: "“第二个上前来说：‘主啊，你的那一百银币，已经赚了五百。’", version_id: 6},
  {id: 157772, book: "Luke", verse: "19.019", unformatted: "“主人也对这个人说：‘你掌管五个城吧！’", version_id: 6},
  {id: 157773, book: "Luke", verse: "19.02", unformatted: "“另一个上前来说：‘主啊，请看，你的那一百银币，我一直保存在手巾里。", version_id: 6},
  {id: 157774, book: "Luke", verse: "19.021", unformatted: "我怕你，因为你是个严厉的人，不是你存放的，你提取；不是你播种的，你收获。’", version_id: 6},
  {id: 157775, book: "Luke", verse: "19.022", unformatted: "“主人对他说：‘你这恶奴！我要凭你的口定你的罪。你既然知道我是个严厉的人，不是我存放的，我提取；不是我播种的，我收获，", version_id: 6},
  {id: 157776, book: "Luke", verse: "19.023", unformatted: "那么你为什么没有把我的银子交给钱庄呢？这样我回来的时候，可以连本带利收回来。’", version_id: 6},
  {id: 157777, book: "Luke", verse: "19.024", unformatted: "于是他对站在旁边的人说：‘把那一百银币从他那里拿走，给那个有一千的。’", version_id: 6},
  {id: 157778, book: "Luke", verse: "19.025", unformatted: "“他们说：‘主啊，他已经有一千银币了！’", version_id: 6},
  {id: 157779, book: "Luke", verse: "19.026", unformatted: "“我告诉你们：凡是有的，还要赐给他；那没有的，连他有的也将从他那里被拿走。", version_id: 6},
  {id: 157780, book: "Luke", verse: "19.027", unformatted: "不过我的这些敌人，就是不要我做王统治他们的人，把他们带到这里来，在我面前杀掉！”", version_id: 6},
  {id: 157781, book: "Luke", verse: "19.028", unformatted: "光荣进圣城\n说完这些话，耶稣就继续往前走，上耶路撒冷去。", version_id: 6},
  {id: 157782, book: "Luke", verse: "19.029", unformatted: "快到伯法其和伯大尼，那叫做“橄榄园”的山，耶稣派了两个门徒，", version_id: 6},
  {id: 157783, book: "Luke", verse: "19.03", unformatted: "说：“你们到对面的村子里去。你们一进村，就会发现一头驴驹拴在那里，是从来没有人骑过的。你们把它解开牵过来。", version_id: 6},
  {id: 157784, book: "Luke", verse: "19.031", unformatted: "如果有人问你们为什么解开它，你们就这样说‘主需要它。’”", version_id: 6},
  {id: 157785, book: "Luke", verse: "19.032", unformatted: "被差派的人去了，发现事情正如耶稣所告诉他们的。", version_id: 6},
  {id: 157786, book: "Luke", verse: "19.033", unformatted: "在他们解开驴驹的时候，驴的主人们问他们：“你们为什么解开这驴驹呢？”", version_id: 6},
  {id: 157787, book: "Luke", verse: "19.034", unformatted: "他们就说：“主需要它。”", version_id: 6},
  {id: 157788, book: "Luke", verse: "19.035", unformatted: "他们牵着驴驹到耶稣那里，把自己的外衣搭在驴驹上，扶着耶稣骑上去。", version_id: 6},
  {id: 157789, book: "Luke", verse: "19.036", unformatted: "耶稣前行的时候，人们把自己的外衣铺在路上。", version_id: 6},
  {id: 157790, book: "Luke", verse: "19.037", unformatted: "耶稣已经快到橄榄山的下坡附近的时候，全体门徒为了他们所看见的一切神迹，开始欢乐起来，大声赞美神，", version_id: 6},
  {id: 157791, book: "Luke", verse: "19.038", unformatted: "说：\n“奉主名而来的那位君王，\n是蒙祝福的！\n在天上有和平，\n在至高之处有荣耀！”", version_id: 6},
  {id: 157792, book: "Luke", verse: "19.039", unformatted: "人群中有一些法利赛人对耶稣说：“老师，责备你的门徒吧！”", version_id: 6},
  {id: 157793, book: "Luke", verse: "19.04", unformatted: "耶稣回答说：“我告诉你们：这些人如果不做声，石头都会呼喊起来！”", version_id: 6},
  {id: 157794, book: "Luke", verse: "19.041", unformatted: "为耶路撒冷哭泣\n耶稣快到耶路撒冷的时候，看见那城，就为她哭泣，", version_id: 6},
  {id: 157795, book: "Luke", verse: "19.042", unformatted: "说：“巴不得你在这日子里明白关乎你和平的事，但如今这事在你眼前被隐藏了。", version_id: 6},
  {id: 157796, book: "Luke", verse: "19.043", unformatted: "因为日子将临到你，你的敌人要筑起壁垒包围你，四面困住你。", version_id: 6},
  {id: 157797, book: "Luke", verse: "19.044", unformatted: "而且，他们要将你和你里面的儿女猛摔在地，不容你里面有一块石头留在另一块石头上，因为你没有认出神临到你的时候。”", version_id: 6},
  {id: 157798, book: "Luke", verse: "19.045", unformatted: "洁净圣殿\n耶稣进了圣殿，开始把那些做买卖的人赶出去，", version_id: 6},
  {id: 157799, book: "Luke", verse: "19.046", unformatted: "对他们说：“经上记着：‘我的殿将成为祷告的殿’，而你们却使它成为贼窝了！”", version_id: 6},
  {id: 157800, book: "Luke", verse: "19.047", unformatted: "耶稣天天在圣殿里教导人。祭司长们、经文士们和民间的首领们想要杀害他，", version_id: 6},
  {id: 157801, book: "Luke", verse: "19.048", unformatted: "可是他们不知道怎样下手，因为民众都倾心听他。", version_id: 6},
  {id: 157802, book: "Luke", verse: "20.001", unformatted: "质疑耶稣的权柄\n就在那些日子里，有一天耶稣在圣殿里教导民众、传福音的时候，祭司长们、经文士们和长老们一起上前来，", version_id: 6},
  {id: 157803, book: "Luke", verse: "20.002", unformatted: "对他说：“请告诉我们：你凭什么权柄做这些事？是谁给你这权柄的？”", version_id: 6},
  {id: 157804, book: "Luke", verse: "20.003", unformatted: "耶稣回答他们，说：“我也要问你们一个问题，你们告诉我：", version_id: 6},
  {id: 157805, book: "Luke", verse: "20.004", unformatted: "约翰的洗礼是来自天上，还是来自人间呢？”", version_id: 6},
  {id: 157806, book: "Luke", verse: "20.005", unformatted: "他们就彼此商量，说：“如果我们说‘是来自天上’，他就会问‘你们为什么不相信他呢？’", version_id: 6},
  {id: 157807, book: "Luke", verse: "20.006", unformatted: "但如果说‘是来自人间’，那么民众都会用石头砸我们，因为他们深信约翰是先知。”", version_id: 6},
  {id: 157808, book: "Luke", verse: "20.007", unformatted: "于是他们回答：“不知道是从哪里来的。”", version_id: 6},
  {id: 157809, book: "Luke", verse: "20.008", unformatted: "耶稣就对他们说：“我也不告诉你们我凭什么权柄做这些事。”", version_id: 6},
  {id: 157810, book: "Luke", verse: "20.009", unformatted: "葡萄园主的比喻\n接着，耶稣开始对民众讲这个比喻：“有一个人栽种了一个葡萄园，把它租给一些农夫，然后就出外旅行，去了许久。", version_id: 6},
  {id: 157811, book: "Luke", verse: "20.01", unformatted: "到了收获的季节，他就派了一个奴仆到那些农夫那里去，好让他们从葡萄园的果子中把当纳的交给他。但那些农夫打了他，叫他空手回去。", version_id: 6},
  {id: 157812, book: "Luke", verse: "20.011", unformatted: "园主又派另一个奴仆去，可是他们把那个人也打了，并且侮辱他，叫他空手回去。", version_id: 6},
  {id: 157813, book: "Luke", verse: "20.012", unformatted: "于是，他又派了第三个人去，他们又把这个人打伤，赶了出去。", version_id: 6},
  {id: 157814, book: "Luke", verse: "20.013", unformatted: "“葡萄园的主人就说：‘我该做什么呢？我要派遣我的爱子，或许他们会尊重他。’", version_id: 6},
  {id: 157815, book: "Luke", verse: "20.014", unformatted: "“可是那些农夫看见他，就彼此讨论说：‘这是继承人。我们把他杀了，那继业就成为我们的了。’", version_id: 6},
  {id: 157816, book: "Luke", verse: "20.015", unformatted: "于是他们把他扔出葡萄园外，杀了。\n“那么，葡萄园的主人会怎么对待他们呢？", version_id: 6},
  {id: 157817, book: "Luke", verse: "20.016", unformatted: "他会来除灭这些农夫，把葡萄园交给别人。”\n大家听了就说：“绝不可有这样的事！”", version_id: 6},
  {id: 157818, book: "Luke", verse: "20.017", unformatted: "耶稣注视着他们，说：“那么，经上记的‘工匠所弃绝的石头，它已经成了房角的头块石头’这话是什么意思呢？", version_id: 6},
  {id: 157819, book: "Luke", verse: "20.018", unformatted: "所有跌落在那石头上的，就将被破碎；那石头落在谁的身上，就会把谁砸得稀烂。”", version_id: 6},
  {id: 157820, book: "Luke", verse: "20.019", unformatted: "经文士和祭司长们知道这个比喻是针对他们而说的，当时就想下手捉拿耶稣，可是又怕民众。", version_id: 6},
  {id: 157821, book: "Luke", verse: "20.02", unformatted: "神与凯撒\n于是，他们密切注意，派了一些奸细装做好人，要抓耶稣的话柄，好把他交在总督的管理和权柄之下。", version_id: 6},
  {id: 157822, book: "Luke", verse: "20.021", unformatted: "那些奸细问耶稣，说：“老师，我们知道你所说所教导的都是对的，你也不顾人的情面，而是按真理教导神的道。", version_id: 6},
  {id: 157823, book: "Luke", verse: "20.022", unformatted: "请问：我们向凯撒纳税，可以不可以呢？”", version_id: 6},
  {id: 157824, book: "Luke", verse: "20.023", unformatted: "耶稣看出他们的诡诈，就对他们说：", version_id: 6},
  {id: 157825, book: "Luke", verse: "20.024", unformatted: "“拿一个银币给我看。这上面是谁的像和名号？”\n他们说：“是凯撒的。”", version_id: 6},
  {id: 157826, book: "Luke", verse: "20.025", unformatted: "耶稣对他们说：“因此，凯撒的归给凯撒；神的归给神。”", version_id: 6},
  {id: 157827, book: "Luke", verse: "20.026", unformatted: "他们在民众面前抓不到耶稣的话柄，又对他的答复感到惊奇，就哑口无言了。", version_id: 6},
  {id: 157828, book: "Luke", verse: "20.027", unformatted: "撒都该人与复活\n撒都该人来到耶稣那里，他们一向说没有复活的事。他们问耶稣，", version_id: 6},
  {id: 157829, book: "Luke", verse: "20.028", unformatted: "说：“老师，摩西为我们写道：如果一个人的兄弟死了，留下妻子而没有孩子，他就应该娶这妇人，为他兄弟留后裔。", version_id: 6},
  {id: 157830, book: "Luke", verse: "20.029", unformatted: "曾经有兄弟七人，第一个娶了妻子，没有孩子就死了。", version_id: 6},
  {id: 157831, book: "Luke", verse: "20.03", unformatted: "第二个、", version_id: 6},
  {id: 157832, book: "Luke", verse: "20.031", unformatted: "第三个也娶了她；七个人都是这样，没有留下儿女就死了。", version_id: 6},
  {id: 157833, book: "Luke", verse: "20.032", unformatted: "后来这妇人也死了。", version_id: 6},
  {id: 157834, book: "Luke", verse: "20.033", unformatted: "既然这七个人都娶过她为妻，那么，在复活的时候，这妇人是谁的妻子呢？”", version_id: 6},
  {id: 157835, book: "Luke", verse: "20.034", unformatted: "耶稣对他们说：“这世代的儿女又娶又嫁；", version_id: 6},
  {id: 157836, book: "Luke", verse: "20.035", unformatted: "但那些算是配得上承受那世代，又配得上从死人中复活的人，既不娶也不嫁。", version_id: 6},
  {id: 157837, book: "Luke", verse: "20.036", unformatted: "事实上，他们再也不会死，因为就与天使一样；他们既然是复活的儿女，就是神的儿女了。", version_id: 6},
  {id: 157838, book: "Luke", verse: "20.037", unformatted: "至于死人复活的事，摩西在‘荆棘篇’上也指明了，正如称主是‘亚伯拉罕的神、以撒的神、雅各的神。’", version_id: 6},
  {id: 157839, book: "Luke", verse: "20.038", unformatted: "所以神不是死人的神，而是活人的神；在神，人都是活着的。”", version_id: 6},
  {id: 157840, book: "Luke", verse: "20.039", unformatted: "有一些经文士应声说：“老师，你说得好。”", version_id: 6},
  {id: 157841, book: "Luke", verse: "20.04", unformatted: "从此他们再也不敢质问他什么了。", version_id: 6},
  {id: 157842, book: "Luke", verse: "20.041", unformatted: "有关基督的问题\n耶稣对他们说：“人怎么说基督是大卫的后裔呢？", version_id: 6},
  {id: 157843, book: "Luke", verse: "20.042", unformatted: "大卫自己在诗篇里说：\n‘主对我主说：\n你坐在我的右边，", version_id: 6},
  {id: 157844, book: "Luke", verse: "20.043", unformatted: "等我把你的敌人放在你的脚下做脚凳。’", version_id: 6},
  {id: 157845, book: "Luke", verse: "20.044", unformatted: "大卫既然称基督为‘主’，基督又怎么会是大卫的后裔呢？”", version_id: 6},
  {id: 157846, book: "Luke", verse: "20.045", unformatted: "要防备经文士\n全体民众正在听的时候，耶稣对他的门徒说：", version_id: 6},
  {id: 157847, book: "Luke", verse: "20.046", unformatted: "“你们要提防经文士。他们喜欢穿着长袍走来走去，喜爱街市上的致敬问候，又喜爱会堂里的首位、宴席中的上座。", version_id: 6},
  {id: 157848, book: "Luke", verse: "20.047", unformatted: "他们侵吞寡妇的家产，又假意做很长的祷告。这些人将受到更重的惩罚。”", version_id: 6},
  {id: 157849, book: "Luke", verse: "21.001", unformatted: "寡妇的奉献\n耶稣抬头观看，见富有的人把他们的奉献投入奉献箱里，", version_id: 6},
  {id: 157850, book: "Luke", verse: "21.002", unformatted: "又见一个穷寡妇投进两个小钱。", version_id: 6},
  {id: 157851, book: "Luke", verse: "21.003", unformatted: "于是他说：“我确实地告诉你们：这穷寡妇所投的，比所有的人更多。", version_id: 6},
  {id: 157852, book: "Luke", verse: "21.004", unformatted: "因为那些人都是从自己的富余中拿出来，当做奉献来投入；而这寡妇是从自己的缺乏中，把她拥有的养生费用全都投进去了。”", version_id: 6},
  {id: 157853, book: "Luke", verse: "21.005", unformatted: "预言圣殿被毁\n有些人谈论到圣殿，说它是用精美的石头和供奉的礼物装饰的。耶稣就说：", version_id: 6},
  {id: 157854, book: "Luke", verse: "21.006", unformatted: "“你们现在所看见的这些，当那些日子来到的时候，将不会有一块石头留在另一块石头上而不被拆下。”", version_id: 6},
  {id: 157855, book: "Luke", verse: "21.007", unformatted: "末日的预兆\n他们就问耶稣，说：“老师，那么，这些事什么时候会发生呢？这些事将要发生的时候，会有什么预兆呢？”", version_id: 6},
  {id: 157856, book: "Luke", verse: "21.008", unformatted: "耶稣说：“你们要当心，免得被迷惑，因为许多人会以我的名而来，声称‘我就是那一位’，又说‘时候快到了。’你们不要跟从他们。", version_id: 6},
  {id: 157857, book: "Luke", verse: "21.009", unformatted: "当你们听见有战争和混乱的时候，不可惊慌，因为这些事必须先发生，不过结局不会立刻就到。”", version_id: 6},
  {id: 157858, book: "Luke", verse: "21.01", unformatted: "耶稣接着对他们说：“一个民族要起来反对另一个民族，一个国家要起来反对另一个国家；", version_id: 6},
  {id: 157859, book: "Luke", verse: "21.011", unformatted: "到处都会有大地震、饥荒和瘟疫；会有恐怖的景象和天上来的大征兆。", version_id: 6},
  {id: 157860, book: "Luke", verse: "21.012", unformatted: "不过在所有这些事以前，他们将对你们下手，逼迫你们，把你们送交会堂，送进监狱；为我名的缘故，你们将被带到君王和总督面前。", version_id: 6},
  {id: 157861, book: "Luke", verse: "21.013", unformatted: "但这却要成为你们做见证的机会。", version_id: 6},
  {id: 157862, book: "Luke", verse: "21.014", unformatted: "所以你们应当心里镇定，不用预先思虑怎样申辩。", version_id: 6},
  {id: 157863, book: "Luke", verse: "21.015", unformatted: "因为我将赐给你们口才和智慧，是所有反对你们的人都不能抵挡、不能驳倒的。", version_id: 6},
  {id: 157864, book: "Luke", verse: "21.016", unformatted: "你们甚至将被父母、兄弟、亲戚和朋友出卖，他们还会害死你们当中的一些人。", version_id: 6},
  {id: 157865, book: "Luke", verse: "21.017", unformatted: "为了我的名，你们将被所有的人憎恨，", version_id: 6},
  {id: 157866, book: "Luke", verse: "21.018", unformatted: "可是你们连一根头发也绝不会失去。", version_id: 6},
  {id: 157867, book: "Luke", verse: "21.019", unformatted: "你们当在你们的忍耐中获得自己的生命。", version_id: 6},
  {id: 157868, book: "Luke", verse: "21.02", unformatted: "预言圣城被毁\n“当你们看见耶路撒冷被军队包围的时候，你们那时就应当知道它的毁灭近了。", version_id: 6},
  {id: 157869, book: "Luke", verse: "21.021", unformatted: "那时，在犹太的人应当逃到山里；在城里的人应当出来；在乡下的人不要进城；", version_id: 6},
  {id: 157870, book: "Luke", verse: "21.022", unformatted: "因为这是报应的日子，为要应验经上所记载的一切。", version_id: 6},
  {id: 157871, book: "Luke", verse: "21.023", unformatted: "在那些日子里，孕妇和哺乳的女人有祸了！因为将有大苦难临到这地，也有震怒临到这民。", version_id: 6},
  {id: 157872, book: "Luke", verse: "21.024", unformatted: "他们将倒在刀口下，被掳到各国去。耶路撒冷将要被外邦人践踏，直到外邦人的日期满了。", version_id: 6},
  {id: 157873, book: "Luke", verse: "21.025", unformatted: "人子的来临\n“在太阳、月亮和星辰中将有征兆出现；在地上，因海洋的咆哮和翻腾，列国的人就惊慌失措，陷入困惑混乱之中；", version_id: 6},
  {id: 157874, book: "Luke", verse: "21.026", unformatted: "人们预料到将要临到世界的事，就吓得魂不附体；因为诸天的各势力都将被震动。", version_id: 6},
  {id: 157875, book: "Luke", verse: "21.027", unformatted: "那时候，人们将要看见人子带着极大的权能和荣耀，在云彩中来临。", version_id: 6},
  {id: 157876, book: "Luke", verse: "21.028", unformatted: "这些事一开始发生，你们就当挺起身、抬起头，因为你们的救赎近了。”", version_id: 6},
  {id: 157877, book: "Luke", verse: "21.029", unformatted: "无花果树的比喻\n耶稣又给他们讲了一个比喻：“你们看无花果树和其他所有的树，", version_id: 6},
  {id: 157878, book: "Luke", verse: "21.03", unformatted: "当它们长出叶子的时候，你们一看就知道夏天已经快到了。", version_id: 6},
  {id: 157879, book: "Luke", verse: "21.031", unformatted: "照样，当你们看见这些事发生的时候，你们应当知道：神的国快到了。", version_id: 6},
  {id: 157880, book: "Luke", verse: "21.032", unformatted: "我确实地告诉你们：在这一切事发生之前，这世代绝不会过去。", version_id: 6},
  {id: 157881, book: "Luke", verse: "21.033", unformatted: "天和地将要消逝，但是我的话绝不会消逝。", version_id: 6},
  {id: 157882, book: "Luke", verse: "21.034", unformatted: "要时刻警醒\n“你们要谨慎自守，免得你们的心因宴乐、醉酒和今生的忧虑变得迟钝，那日子就会像网罗一样突然临到你们，", version_id: 6},
  {id: 157883, book: "Luke", verse: "21.035", unformatted: "因为那日子将要临到所有住在全地面上的人。", version_id: 6},
  {id: 157884, book: "Luke", verse: "21.036", unformatted: "你们要时刻警醒、祷告，使你们能够逃避这一切将要发生的事，好站立在人子面前。”", version_id: 6},
  {id: 157885, book: "Luke", verse: "21.037", unformatted: "耶稣白天在圣殿里教导人，夜间出去到那叫做“橄榄园”的山里过夜。", version_id: 6},
  {id: 157886, book: "Luke", verse: "21.038", unformatted: "全体民众清早起来上圣殿，到耶稣那里听他讲说。", version_id: 6},
  {id: 157887, book: "Luke", verse: "22.001", unformatted: "阴谋杀害耶稣\n除酵节——又叫逾越节——快到了。", version_id: 6},
  {id: 157888, book: "Luke", verse: "22.002", unformatted: "祭司长们和经文士们在图谋怎样能杀掉耶稣，因为他们怕民众。", version_id: 6},
  {id: 157889, book: "Luke", verse: "22.003", unformatted: "那时，撒旦进入了那称为加略人的犹大里面。犹大算是十二使徒中的一个。", version_id: 6},
  {id: 157890, book: "Luke", verse: "22.004", unformatted: "他去跟祭司长们和守卫长们商量怎样把耶稣交给他们。", version_id: 6},
  {id: 157891, book: "Luke", verse: "22.005", unformatted: "他们就欢喜，并且说好要给他银钱。", version_id: 6},
  {id: 157892, book: "Luke", verse: "22.006", unformatted: "犹大同意了，就开始找机会，要趁众人不在的时候，把耶稣交给他们。", version_id: 6},
  {id: 157893, book: "Luke", verse: "22.007", unformatted: "预备逾越节的晚餐\n除酵节的第一日到了，在这一天必须宰杀逾越节的羔羊。", version_id: 6},
  {id: 157894, book: "Luke", verse: "22.008", unformatted: "耶稣差派彼得和约翰，说：“你们去为我们预备逾越节的晚餐，好让我们能吃上。”", version_id: 6},
  {id: 157895, book: "Luke", verse: "22.009", unformatted: "他们问耶稣：“你要我们在哪里预备呢？”", version_id: 6},
  {id: 157896, book: "Luke", verse: "22.01", unformatted: "耶稣对他们说：“看哪！你们进了城，会有一个人提着一罐水迎面而来，你们就跟着他，到他所进的那一家去，", version_id: 6},
  {id: 157897, book: "Luke", verse: "22.011", unformatted: "对那家的主人说：‘老师问你：我与我的门徒们一起吃逾越节晚餐的客房在哪里呢？’", version_id: 6},
  {id: 157898, book: "Luke", verse: "22.012", unformatted: "他会给你们看楼上一间布置好的大房间，你们就在那里预备吧。”", version_id: 6},
  {id: 157899, book: "Luke", verse: "22.013", unformatted: "他们去了，看到的正如耶稣对他们所说的。他们就预备了逾越节的晚餐。", version_id: 6},
  {id: 157900, book: "Luke", verse: "22.014", unformatted: "第一次圣餐\n时候一到，耶稣就坐席，十二使徒也和他同坐。", version_id: 6},
  {id: 157901, book: "Luke", verse: "22.015", unformatted: "耶稣对他们说：“我一直非常渴望在我受难之前，与你们一起吃这逾越节的晚餐。", version_id: 6},
  {id: 157902, book: "Luke", verse: "22.016", unformatted: "我告诉你们：我绝不吃这晚餐，直等到逾越节在神的国里得以成就。”", version_id: 6},
  {id: 157903, book: "Luke", verse: "22.017", unformatted: "耶稣拿起杯来，祝谢了，说：“你们拿这个，大家分着喝。", version_id: 6},
  {id: 157904, book: "Luke", verse: "22.018", unformatted: "我告诉你们：从今以后我绝不喝这葡萄汁，直到神的国来临。”", version_id: 6},
  {id: 157905, book: "Luke", verse: "22.019", unformatted: "他又拿起饼来，祝谢了，掰开递给他们，说：“这是我的身体，为你们献上的；你们应当如此行，为的是记念我。”", version_id: 6},
  {id: 157906, book: "Luke", verse: "22.02", unformatted: "晚餐后，他也照样拿起杯来，说：“这杯是用我的血所立的新约，为你们所流的。", version_id: 6},
  {id: 157907, book: "Luke", verse: "22.021", unformatted: "不过看哪，那卖我之人的手，与我的手一同在这桌子上，", version_id: 6},
  {id: 157908, book: "Luke", verse: "22.022", unformatted: "因为人子确实要按照所决定的离去。不过出卖人子的那个人有祸了。”", version_id: 6},
  {id: 157909, book: "Luke", verse: "22.023", unformatted: "于是他们开始彼此议论，他们当中究竟是谁会做这事。", version_id: 6},
  {id: 157910, book: "Luke", verse: "22.024", unformatted: "争论谁最大\n门徒之间也起了争论：他们当中谁算是更大的。", version_id: 6},
  {id: 157911, book: "Luke", verse: "22.025", unformatted: "耶稣对他们说：“外邦人有君王辖制他们，他们的掌权者被称为恩主。", version_id: 6},
  {id: 157912, book: "Luke", verse: "22.026", unformatted: "但你们却不是这样；相反，你们当中谁更大，谁就该变得像年幼的那样；领头的，该像服事人的。", version_id: 6},
  {id: 157913, book: "Luke", verse: "22.027", unformatted: "坐席的或服事的，到底哪一个更大呢？难道不是坐席的吗？然而我在你们中间，是像服事人的。", version_id: 6},
  {id: 157914, book: "Luke", verse: "22.028", unformatted: "不过我在试炼当中，始终与我在一起的就是你们；", version_id: 6},
  {id: 157915, book: "Luke", verse: "22.029", unformatted: "并且我把国度赐给你们，就像我父赐给了我那样，", version_id: 6},
  {id: 157916, book: "Luke", verse: "22.03", unformatted: "使你们在我的国度里，在我的筵席上吃喝；并且坐在宝座上审判以色列的十二个支派。”", version_id: 6},
  {id: 157917, book: "Luke", verse: "22.031", unformatted: "预言彼得不认主\n主又说：“西门，西门，你看，撒旦想要得着你们，要筛你们像筛麦子一样。", version_id: 6},
  {id: 157918, book: "Luke", verse: "22.032", unformatted: "但是，我已经为你祈求，使你不失去信仰。所以当你回转过来的时候，要坚固你的弟兄们。”", version_id: 6},
  {id: 157919, book: "Luke", verse: "22.033", unformatted: "可是彼得对耶稣说：“主啊，我预备好要与你一起去监狱、一起去死！”", version_id: 6},
  {id: 157920, book: "Luke", verse: "22.034", unformatted: "耶稣却说：“彼得，我告诉你：今天鸡叫以前，你会三次否定认识我。”", version_id: 6},
  {id: 157921, book: "Luke", verse: "22.035", unformatted: "钱包、行囊、刀\n接着耶稣问他们：“我从前差派你们出去的时候，你们不带钱包、行囊、鞋子，你们缺少过什么没有？”\n他们说：“没有。”", version_id: 6},
  {id: 157922, book: "Luke", verse: "22.036", unformatted: "耶稣却对他们说：“但现在，有钱包的应当带着；同样，有行囊的也应当带着；没有刀的，应当卖掉自己的外衣去买一把。", version_id: 6},
  {id: 157923, book: "Luke", verse: "22.037", unformatted: "我告诉你们：经上记着：‘他被列在不法之徒当中’，这话必须在我身上成就，因为那有关我的事就要有个结局了。”", version_id: 6},
  {id: 157924, book: "Luke", verse: "22.038", unformatted: "他们说：“主啊，你看，这里有两把刀。”\n耶稣说：“实在够了。”", version_id: 6},
  {id: 157925, book: "Luke", verse: "22.039", unformatted: "园中的祷告\n耶稣出来，照他的习惯往橄榄山去，门徒们也跟着他。", version_id: 6},
  {id: 157926, book: "Luke", verse: "22.04", unformatted: "到了那地方，耶稣对他们说：“你们要祷告，免得陷入试探。”", version_id: 6},
  {id: 157927, book: "Luke", verse: "22.041", unformatted: "然后他抽出身，离他们约有扔一块石头的距离，跪下祷告，", version_id: 6},
  {id: 157928, book: "Luke", verse: "22.042", unformatted: "说：“父啊，如果你愿意，请把这杯从我这里拿走吧！但不要成就我的意思，而要成就你的意思。”", version_id: 6},
  {id: 157929, book: "Luke", verse: "22.043", unformatted: "有一位天使从天上向他显现，加添他的力量。", version_id: 6},
  {id: 157930, book: "Luke", verse: "22.044", unformatted: "耶稣在极大的痛苦中，祷告更加恳切；他的汗珠变得像大血滴，落在地上。", version_id: 6},
  {id: 157931, book: "Luke", verse: "22.045", unformatted: "祷告完了，他起身到门徒那里，看见他们因为忧伤都睡着了，", version_id: 6},
  {id: 157932, book: "Luke", verse: "22.046", unformatted: "就对他们说：“你们为什么睡觉呢？起来祷告，免得陷入试探。”", version_id: 6},
  {id: 157933, book: "Luke", verse: "22.047", unformatted: "犹大亲吻的暗号\n耶稣还在说话的时候，忽然来了一群人。十二使徒中的一个，就是那称为犹大的，走在他们前头。他靠近耶稣，要亲吻他。", version_id: 6},
  {id: 157934, book: "Luke", verse: "22.048", unformatted: "耶稣对他说：“犹大，你用亲吻礼来出卖人子吗？”", version_id: 6},
  {id: 157935, book: "Luke", verse: "22.049", unformatted: "耶稣周围的人看到要发生的事，就说：“主啊，我们要用刀砍吗？”", version_id: 6},
  {id: 157936, book: "Luke", verse: "22.05", unformatted: "其中一个人向大祭司的奴仆砍去，削掉了他的右耳。", version_id: 6},
  {id: 157937, book: "Luke", verse: "22.051", unformatted: "耶稣应声说：“住手，到此为止！”然后摸一下那个人的耳朵，使他痊愈了。", version_id: 6},
  {id: 157938, book: "Luke", verse: "22.052", unformatted: "耶稣对前来对付他的祭司长们、圣殿守卫长们和长老们说：“你们带着刀剑和棍棒出来，就像对付强盗那样吗？", version_id: 6},
  {id: 157939, book: "Luke", verse: "22.053", unformatted: "我天天在圣殿里与你们在一起的时候，你们不向我下手。不过现在是你们的时候了，是黑暗掌权的时候了！”", version_id: 6},
  {id: 157940, book: "Luke", verse: "22.054", unformatted: "彼得不认主\n他们抓住耶稣，把他带走，押到大祭司的家里。彼得远远地跟着。", version_id: 6},
  {id: 157941, book: "Luke", verse: "22.055", unformatted: "他们在院子当中生了火，一起坐着。彼得也坐在他们中间。", version_id: 6},
  {id: 157942, book: "Luke", verse: "22.056", unformatted: "有一个女仆看见彼得坐在火光中，就注视着他，说：“这个人也与他是一伙的。”", version_id: 6},
  {id: 157943, book: "Luke", verse: "22.057", unformatted: "彼得却否认说：“你这个女人哪，我不认识他！”", version_id: 6},
  {id: 157944, book: "Luke", verse: "22.058", unformatted: "过了一会儿，另一个人看见他，就说：“你也是与他们一伙的！”\n彼得说：“你这个人哪，我不是！”", version_id: 6},
  {id: 157945, book: "Luke", verse: "22.059", unformatted: "大约过了一个小时，又有一个人肯定地说：“这个人真的也是与他一伙的，因为他也是加利利人。”", version_id: 6},
  {id: 157946, book: "Luke", verse: "22.06", unformatted: "彼得说：“你这个人哪，我不知道你在说什么！”他正说着的时候，鸡立时就叫了。", version_id: 6},
  {id: 157947, book: "Luke", verse: "22.061", unformatted: "主转过身来注视彼得，彼得就想起主对他说过的话：“今天鸡叫以前，你会三次不认我。”", version_id: 6},
  {id: 157948, book: "Luke", verse: "22.062", unformatted: "他就到外面去，痛哭起来。", version_id: 6},
  {id: 157949, book: "Luke", verse: "22.063", unformatted: "被戏弄与鞭打\n看守耶稣的人戏弄他、殴打他，", version_id: 6},
  {id: 157950, book: "Luke", verse: "22.064", unformatted: "又蒙住他的眼睛问他，说：“你说预言吧！打你的是谁？”", version_id: 6},
  {id: 157951, book: "Luke", verse: "22.065", unformatted: "他们还说了很多别的话辱骂他。", version_id: 6},
  {id: 157952, book: "Luke", verse: "22.066", unformatted: "在议员面前受审\n一到天亮，民间的长老议会，包括祭司长们和经文士们，就聚集在一起。他们把耶稣带到议会里，", version_id: 6},
  {id: 157953, book: "Luke", verse: "22.067", unformatted: "说：“你如果是基督，就告诉我们吧。”\n耶稣对他们说：“就算我告诉你们，你们也绝不会相信；", version_id: 6},
  {id: 157954, book: "Luke", verse: "22.068", unformatted: "就算我问你们，你们也绝不会回答。", version_id: 6},
  {id: 157955, book: "Luke", verse: "22.069", unformatted: "但从今以后，人子要坐在全能神的右边。”", version_id: 6},
  {id: 157956, book: "Luke", verse: "22.07", unformatted: "他们都说：“那么，你是神的儿子吗？”\n耶稣对他们说：“是你们说的，我就是。”", version_id: 6},
  {id: 157957, book: "Luke", verse: "22.071", unformatted: "他们说：“难道我们还需要什么证据吗？他亲口说的话，我们都亲耳听见了。”", version_id: 6},
  {id: 157958, book: "Luke", verse: "23.001", unformatted: "在彼拉多面前受审\n于是他们全体民众就起来，把耶稣带到彼拉多面前，", version_id: 6},
  {id: 157959, book: "Luke", verse: "23.002", unformatted: "开始控告耶稣，说：“我们发现这个人在败坏我们的同胞，阻止纳税给凯撒，还自称是基督、是王。”", version_id: 6},
  {id: 157960, book: "Luke", verse: "23.003", unformatted: "彼拉多问耶稣：“你是犹太人的王吗？”\n耶稣回答说：“是你说的。”", version_id: 6},
  {id: 157961, book: "Luke", verse: "23.004", unformatted: "彼拉多对祭司长们和众人说：“我查不出这个人有什么罪。”", version_id: 6},
  {id: 157962, book: "Luke", verse: "23.005", unformatted: "他们却更加坚持说：“他在犹太全地教导、煽动民众，从加利利开始，一直到这里。”", version_id: 6},
  {id: 157963, book: "Luke", verse: "23.006", unformatted: "在希律前受审\n彼拉多听了，就问：“这人是不是加利利人？”", version_id: 6},
  {id: 157964, book: "Luke", verse: "23.007", unformatted: "当他了解耶稣属于希律管辖，就把他送到希律那里。在那些日子里，希律正好也在耶路撒冷。", version_id: 6},
  {id: 157965, book: "Luke", verse: "23.008", unformatted: "希律见到耶稣就极其欢喜，因为他听说过耶稣的事，早就想要见他，一直希望看他行个神迹。", version_id: 6},
  {id: 157966, book: "Luke", verse: "23.009", unformatted: "于是他问了耶稣许多话，但是耶稣什么都不回答。", version_id: 6},
  {id: 157967, book: "Luke", verse: "23.01", unformatted: "祭司长们和经文士们都站在那里，极力控告他。", version_id: 6},
  {id: 157968, book: "Luke", verse: "23.011", unformatted: "希律和他的军兵就藐视耶稣，戏弄他，给他披上华丽的衣服，然后把他送回彼拉多那里。", version_id: 6},
  {id: 157969, book: "Luke", verse: "23.012", unformatted: "在这一天，希律和彼拉多竟互相成了朋友；原来他们两个人一向是彼此有仇的。", version_id: 6},
  {id: 157970, book: "Luke", verse: "23.013", unformatted: "耶稣或巴拉巴\n彼拉多召集了祭司长们、首领们和民众，", version_id: 6},
  {id: 157971, book: "Luke", verse: "23.014", unformatted: "对他们说：“你们把这个人带到我这里来，说他是误导民众造反的。看，我已经当着你们的面审问了他，在他身上却一点也查不出你们所控告的罪。", version_id: 6},
  {id: 157972, book: "Luke", verse: "23.015", unformatted: "其实希律也查不出来，所以把他送回给我们。看，他没有做过什么该死的事。", version_id: 6},
  {id: 157973, book: "Luke", verse: "23.016", unformatted: "因此，惩罚了以后，我要释放他。”", version_id: 6},
  {id: 157974, book: "Luke", verse: "23.017", unformatted: "每逢这节日，总督必须给他们释放一个囚犯。", version_id: 6},
  {id: 157975, book: "Luke", verse: "23.018", unformatted: "他们却一齐喊叫说：“除掉这个人！给我们释放巴拉巴！”", version_id: 6},
  {id: 157976, book: "Luke", verse: "23.019", unformatted: "这巴拉巴是因为在城里发生的一个暴乱和杀人案，被投进监狱的。", version_id: 6},
  {id: 157977, book: "Luke", verse: "23.02", unformatted: "彼拉多想要释放耶稣，就再次向他们呼吁。", version_id: 6},
  {id: 157978, book: "Luke", verse: "23.021", unformatted: "但他们却一直喊叫说：“钉上十字架！把他钉上十字架！”", version_id: 6},
  {id: 157979, book: "Luke", verse: "23.022", unformatted: "彼拉多第三次对他们说：“这个人到底做了什么恶事呢？我查不出他有什么该死的罪。所以，惩罚了以后，我要释放他！”", version_id: 6},
  {id: 157980, book: "Luke", verse: "23.023", unformatted: "他们催逼彼拉多，大声要求把耶稣钉上十字架，他们的喊声终于得胜了。", version_id: 6},
  {id: 157981, book: "Luke", verse: "23.024", unformatted: "于是，彼拉多决定成全他们所求的，", version_id: 6},
  {id: 157982, book: "Luke", verse: "23.025", unformatted: "释放了他们所要的那个因暴乱和杀人被投进监狱的人，却随他们的意思，把耶稣交出去。", version_id: 6},
  {id: 157983, book: "Luke", verse: "23.026", unformatted: "十字架之路\n他们把耶稣带走的时候，抓了一个从乡下来的古利奈人西门，把十字架放在他身上，要他背着，跟在耶稣后面。", version_id: 6},
  {id: 157984, book: "Luke", verse: "23.027", unformatted: "有一大群人跟着耶稣，其中有些妇女为他捶胸哀哭。", version_id: 6},
  {id: 157985, book: "Luke", verse: "23.028", unformatted: "耶稣转过身来，对她们说：“耶路撒冷的女儿啊，不要为我哭，而要为你们自己、为你们的儿女哭！", version_id: 6},
  {id: 157986, book: "Luke", verse: "23.029", unformatted: "看，日子将要来到，那时人们会说：‘不能生育、没有怀过胎和没有乳养过婴儿的，是蒙福的！’", version_id: 6},
  {id: 157987, book: "Luke", verse: "23.03", unformatted: "那时人们将开始对山岭说：‘倒在我们身上吧！’对丘陵说：‘遮盖我们吧！’", version_id: 6},
  {id: 157988, book: "Luke", verse: "23.031", unformatted: "他们在树木青绿的时候尚且做这些事，那么在它枯萎的时候，还会发生什么呢？”", version_id: 6},
  {id: 157989, book: "Luke", verse: "23.032", unformatted: "被钉在罪犯之间\n另有两个囚犯也和耶稣一起被带去处死。", version_id: 6},
  {id: 157990, book: "Luke", verse: "23.033", unformatted: "当他们来到一个地方，叫做“骷髅”，就在那里把耶稣钉上十字架，又钉了那两个囚犯，一个在右边，一个在左边。", version_id: 6},
  {id: 157991, book: "Luke", verse: "23.034", unformatted: "那时，耶稣说：“父啊，赦免他们！因为他们不知道自己在做什么。”士兵们抽签分了他的衣服。", version_id: 6},
  {id: 157992, book: "Luke", verse: "23.035", unformatted: "民众站着观看。首领们也讥笑，说：“他救了别人！如果这个人是神的基督，是蒙拣选的那一位，让他救自己吧！”", version_id: 6},
  {id: 157993, book: "Luke", verse: "23.036", unformatted: "士兵们也上前来戏弄他，给他献上酸酒，", version_id: 6},
  {id: 157994, book: "Luke", verse: "23.037", unformatted: "说：“你如果是犹太人的王，救救你自己吧！”", version_id: 6},
  {id: 157995, book: "Luke", verse: "23.038", unformatted: "在耶稣的上方有一个牌子：“这是犹太人的王。”", version_id: 6},
  {id: 157996, book: "Luke", verse: "23.039", unformatted: "被悬挂的两个囚犯中，有一个辱骂他说：“你不是基督吗？救救你自己，也救救我们吧！”", version_id: 6},
  {id: 157997, book: "Luke", verse: "23.04", unformatted: "另一个就应声斥责他，说：“你既然同样受惩罚，还不怕神吗？", version_id: 6},
  {id: 157998, book: "Luke", verse: "23.041", unformatted: "我们受刑理所应当，因为我们所受的与我们所做的相称。但这个人却没有做过什么坏事。”", version_id: 6},
  {id: 157999, book: "Luke", verse: "23.042", unformatted: "他接着说：“耶稣啊，你进了你国度的时候，求你记念我。”", version_id: 6},
  {id: 158000, book: "Luke", verse: "23.043", unformatted: "耶稣对他说：“我确实地告诉你：今天你要与我一起在乐园里了。”", version_id: 6},
  {id: 158001, book: "Luke", verse: "23.044", unformatted: "耶稣之死\n那时大约是中午十二点，黑暗笼罩了整个大地，一直到下午三点。", version_id: 6},
  {id: 158002, book: "Luke", verse: "23.045", unformatted: "太阳失去了光，同时，圣所里的幔子裂成两半。", version_id: 6},
  {id: 158003, book: "Luke", verse: "23.046", unformatted: "耶稣大声呼唤：“父啊，我把我的灵魂交托在你手中。”说完这话，就断了气。", version_id: 6},
  {id: 158004, book: "Luke", verse: "23.047", unformatted: "百夫长看见所发生的事，就不住地荣耀神，说：“这个人的确是义人。”", version_id: 6},
  {id: 158005, book: "Luke", verse: "23.048", unformatted: "所有聚集见这场面的众人，看到所发生的事，都捶着胸回去了。", version_id: 6},
  {id: 158006, book: "Luke", verse: "23.049", unformatted: "所有熟悉耶稣的人，包括那些从加利利一直跟着他来的妇女，都站在远处看这些事。", version_id: 6},
  {id: 158007, book: "Luke", verse: "23.05", unformatted: "安葬\n当时，有一个名叫约瑟的人，是犹太议会中的议员，为人公义良善。", version_id: 6},
  {id: 158008, book: "Luke", verse: "23.051", unformatted: "这个人并没有赞同他们的计划和行为；他是犹太的亚利马太城的人，一直期待着神的国。", version_id: 6},
  {id: 158009, book: "Luke", verse: "23.052", unformatted: "他来到彼拉多面前，请求要耶稣的遗体。", version_id: 6},
  {id: 158010, book: "Luke", verse: "23.053", unformatted: "他把遗体取下来，用细麻布裹好，安放在一座岩石凿成的墓穴里，那里从来没有安放过人。", version_id: 6},
  {id: 158011, book: "Luke", verse: "23.054", unformatted: "那天是预备日，安息日快要到了。", version_id: 6},
  {id: 158012, book: "Luke", verse: "23.055", unformatted: "那些从加利利陪耶稣一起来的妇女跟在后面。她们看到了那墓穴，也看到了耶稣的遗体怎样被安放，", version_id: 6},
  {id: 158013, book: "Luke", verse: "23.056", unformatted: "就回去预备香料和香液。不过在安息日，她们还是按照诫命休息了。", version_id: 6},
  {id: 158014, book: "Luke", verse: "24.001", unformatted: "复活的清晨\n在一周的头一天，一大清早，妇女们带着预备好的香料来到墓穴那里，", version_id: 6},
  {id: 158015, book: "Luke", verse: "24.002", unformatted: "发现那石头已经从墓穴被滚开了。", version_id: 6},
  {id: 158016, book: "Luke", verse: "24.003", unformatted: "她们就进去，却找不到主耶稣的遗体。", version_id: 6},
  {id: 158017, book: "Luke", verse: "24.004", unformatted: "她们正为这件事心里困惑的时候，忽然有两个人，身穿闪闪发光的衣服，站在她们旁边。", version_id: 6},
  {id: 158018, book: "Luke", verse: "24.005", unformatted: "妇女们吓得把脸伏在地上。\n那两个人对她们说：“你们为什么在死人中寻找活人呢？", version_id: 6},
  {id: 158019, book: "Luke", verse: "24.006", unformatted: "他不在这里，已经复活了！你们当想起他还在加利利的时候怎样告诉你们，", version_id: 6},
  {id: 158020, book: "Luke", verse: "24.007", unformatted: "说‘人子必须被交在罪人手中，被钉十字架，然后在第三天复活。’”", version_id: 6},
  {id: 158021, book: "Luke", verse: "24.008", unformatted: "她们就想起耶稣的话来。", version_id: 6},
  {id: 158022, book: "Luke", verse: "24.009", unformatted: "于是从墓穴回去，把这一切都告诉了那十一个使徒和所有其他的人。", version_id: 6},
  {id: 158023, book: "Luke", verse: "24.01", unformatted: "告诉使徒们这些事的，是茉大拉的玛丽亚、约亚娜、雅各的母亲玛丽亚，还有其余和她们在一起的妇女。", version_id: 6},
  {id: 158024, book: "Luke", verse: "24.011", unformatted: "可是这些话在使徒们看来似乎是胡说，他们就不相信妇女们。", version_id: 6},
  {id: 158025, book: "Luke", verse: "24.012", unformatted: "但彼得却起来，跑到了墓穴。他弯腰去看，只看见那细麻布条。于是他就回去了，对所发生的事感到惊奇。", version_id: 6},
  {id: 158026, book: "Luke", verse: "24.013", unformatted: "以马忤斯路上\n正好在同一天，门徒中有两个人往一个村子去；这村子名叫以马忤斯，离耶路撒冷约有十一公里。", version_id: 6},
  {id: 158027, book: "Luke", verse: "24.014", unformatted: "他们彼此谈论着所发生的这一切事。", version_id: 6},
  {id: 158028, book: "Luke", verse: "24.015", unformatted: "在他们交谈讨论的时候，耶稣亲自走近他们，与他们同行，", version_id: 6},
  {id: 158029, book: "Luke", verse: "24.016", unformatted: "但是他们的眼睛被遮蔽了，认不出他来。", version_id: 6},
  {id: 158030, book: "Luke", verse: "24.017", unformatted: "耶稣问他们：“你们一路上彼此谈论的这些，到底是什么事呢？”他们就停住，面带愁容。", version_id: 6},
  {id: 158031, book: "Luke", verse: "24.018", unformatted: "其中一个名叫克利奥帕的回答耶稣，说：“你在耶路撒冷作客，难道只有你还不知道这几天在那里所发生的事吗？”", version_id: 6},
  {id: 158032, book: "Luke", verse: "24.019", unformatted: "耶稣问他们：“什么事呢？”\n他们对他说：“就是有关拿撒勒人耶稣的事。这个人是一位先知，在神和全体民众面前说话行事都有能力。", version_id: 6},
  {id: 158033, book: "Luke", verse: "24.02", unformatted: "我们的祭司长们和首领们竟把他交出去，判了死罪，钉上十字架。", version_id: 6},
  {id: 158034, book: "Luke", verse: "24.021", unformatted: "不过我们一直希望他就是要救赎以色列的那一位。不但如此，从这些事发生到今天，已经是第三天了。", version_id: 6},
  {id: 158035, book: "Luke", verse: "24.022", unformatted: "还有，我们中间有些妇女也使我们惊讶：她们清早到了墓穴，", version_id: 6},
  {id: 158036, book: "Luke", verse: "24.023", unformatted: "却找不到他的遗体，回来还说她们看到了天使的异象，而天使说耶稣还活着。", version_id: 6},
  {id: 158037, book: "Luke", verse: "24.024", unformatted: "后来，与我们在一起的一些人，到墓穴那里去，发现一切就像妇女们所说的那样。只是他们没有看见耶稣。”", version_id: 6},
  {id: 158038, book: "Luke", verse: "24.025", unformatted: "耶稣对他们说：“唉，你们这些无知的人哪！对于信靠先知们所说的一切话，你们的心太迟钝了！", version_id: 6},
  {id: 158039, book: "Luke", verse: "24.026", unformatted: "基督不是必须这样受难，然后才进入他的荣耀吗？”", version_id: 6},
  {id: 158040, book: "Luke", verse: "24.027", unformatted: "于是耶稣从摩西和所有的先知开始，向他们解释了经上一切关于自己的话。", version_id: 6},
  {id: 158041, book: "Luke", verse: "24.028", unformatted: "他们快到要去的村子了，耶稣好像还要往前走。", version_id: 6},
  {id: 158042, book: "Luke", verse: "24.029", unformatted: "他们就极力挽留他，说：“时候晚了，太阳已经平西了，请与我们一起住下吧！”耶稣就进去，与他们一起住下。", version_id: 6},
  {id: 158043, book: "Luke", verse: "24.03", unformatted: "当耶稣与他们一同坐席的时候，他拿起饼来，祝福了，然后掰开递给他们。", version_id: 6},
  {id: 158044, book: "Luke", verse: "24.031", unformatted: "那时他们的眼睛就开了，这才认出他来，而耶稣从他们眼前消失了。", version_id: 6},
  {id: 158045, book: "Luke", verse: "24.032", unformatted: "他们彼此说：“在路上他对我们说话，为我们讲解经文的时候，我们心里不是在燃烧吗？”", version_id: 6},
  {id: 158046, book: "Luke", verse: "24.033", unformatted: "他们就立刻起身，回耶路撒冷去，见到那十一个使徒和与他们聚集在一起的人，", version_id: 6},
  {id: 158047, book: "Luke", verse: "24.034", unformatted: "大家都在说：“主真的复活了，并且已经向西门显现了！”", version_id: 6},
  {id: 158048, book: "Luke", verse: "24.035", unformatted: "两个人就把在路上所发生的，以及耶稣掰饼的时候怎样被他们认出来的事，都述说了一遍。", version_id: 6},
  {id: 158049, book: "Luke", verse: "24.036", unformatted: "复活的确据\n在他们说这些话的时候，耶稣亲自站在他们当中，对他们说：“愿你们平安！”", version_id: 6},
  {id: 158050, book: "Luke", verse: "24.037", unformatted: "他们就惊慌，感到害怕，以为看到了幽灵。", version_id: 6},
  {id: 158051, book: "Luke", verse: "24.038", unformatted: "耶稣对他们说：“你们为什么惊慌不安？为什么心里起疑惑呢？", version_id: 6},
  {id: 158052, book: "Luke", verse: "24.039", unformatted: "你们看我的手、我的脚，确实是我。摸我看看！幽灵没有骨，也没有肉，你们看，我是有的。”", version_id: 6},
  {id: 158053, book: "Luke", verse: "24.04", unformatted: "说了这话，他就把手和脚给他们看。", version_id: 6},
  {id: 158054, book: "Luke", verse: "24.041", unformatted: "他们又惊又喜，还不敢相信的时候，耶稣对他们说：“你们这里有什么吃的吗？”", version_id: 6},
  {id: 158055, book: "Luke", verse: "24.042", unformatted: "他们递给他一片烤鱼，", version_id: 6},
  {id: 158056, book: "Luke", verse: "24.043", unformatted: "他拿过来，当着他们的面吃了。", version_id: 6},
  {id: 158057, book: "Luke", verse: "24.044", unformatted: "耶稣对他们说：“这就是我还与你们在一起的时候，对你们说过的话：摩西的律法、先知书和诗篇上所记的有关我的一切事，都必须应验。”", version_id: 6},
  {id: 158058, book: "Luke", verse: "24.045", unformatted: "于是耶稣开启他们的理性，使他们能领悟经上的话，", version_id: 6},
  {id: 158059, book: "Luke", verse: "24.046", unformatted: "又对他们说：“经上这样记着：基督必须受难，然后在第三天从死人中复活，", version_id: 6},
  {id: 158060, book: "Luke", verse: "24.047", unformatted: "并且人要奉他的名宣讲为罪得赦免的悔改，从耶路撒冷开始，直到万国。", version_id: 6},
  {id: 158061, book: "Luke", verse: "24.048", unformatted: "你们就是这些事的见证人。", version_id: 6},
  {id: 158062, book: "Luke", verse: "24.049", unformatted: "看哪！我要将我父所应许的差派到你们那里。不过你们要留在城里，直到领受了从上面来的能力。”", version_id: 6},
  {id: 158063, book: "Luke", verse: "24.05", unformatted: "升天\n耶稣领他们出去，到了伯大尼附近，就举起双手祝福他们。", version_id: 6},
  {id: 158064, book: "Luke", verse: "24.051", unformatted: "他在祝福的时候离开他们，被接到天上去了。", version_id: 6},
  {id: 158065, book: "Luke", verse: "24.052", unformatted: "他们就敬拜他，怀着极大的喜乐回耶路撒冷去，", version_id: 6},
  {id: 158066, book: "Luke", verse: "24.053", unformatted: "常常在圣殿里颂赞神。", version_id: 6},
  {id: 158067, book: "John", verse: "1.001", unformatted: "神成为人\n太初有道，\n道与神同在，\n道就是神。", version_id: 6},
  {id: 158068, book: "John", verse: "1.002", unformatted: "这道太初与神同在。", version_id: 6},
  {id: 158069, book: "John", verse: "1.003", unformatted: "万有是藉着他而成的；\n凡所成的，\n没有一样不是藉着他而成的。", version_id: 6},
  {id: 158070, book: "John", verse: "1.004", unformatted: "生命在他里面，\n这生命就是人的光。", version_id: 6},
  {id: 158071, book: "John", verse: "1.005", unformatted: "这光在黑暗中照耀，\n黑暗却没有胜过这光。", version_id: 6},
  {id: 158072, book: "John", verse: "1.006", unformatted: "有一个人受神的差派而来，\n他名叫约翰。", version_id: 6},
  {id: 158073, book: "John", verse: "1.007", unformatted: "他为见证而来，\n是要为这光做见证，\n好让人都藉着他相信。", version_id: 6},
  {id: 158074, book: "John", verse: "1.008", unformatted: "他不是这光，\n而是要为这光做见证。", version_id: 6},
  {id: 158075, book: "John", verse: "1.009", unformatted: "这光是真光，\n来到这世界照亮每个人。", version_id: 6},
  {id: 158076, book: "John", verse: "1.01", unformatted: "他在这世界，\n世界也是藉着他而成的，\n世界却不认识他。", version_id: 6},
  {id: 158077, book: "John", verse: "1.011", unformatted: "他来到自己的地方，\n自己的人却不接受他。", version_id: 6},
  {id: 158078, book: "John", verse: "1.012", unformatted: "但是所有接受他的，\n就是信他名的人，\n他就赐给他们权柄成为神的儿女。", version_id: 6},
  {id: 158079, book: "John", verse: "1.013", unformatted: "他们不是由血缘、\n或肉体的意愿、\n或人的意志所生的，\n而是由神所生的。", version_id: 6},
  {id: 158080, book: "John", verse: "1.014", unformatted: "道成了肉身，\n居住在我们中间。\n我们看到了他的荣耀，\n正是从父而来的独生子的荣耀，\n充满了恩典和真理。", version_id: 6},
  {id: 158081, book: "John", verse: "1.015", unformatted: "约翰为他做见证，呼喊说：\n“这一位就是我曾说过的，\n‘那在我之后来的，已成为在我前面的，\n因为他在我之前就已经存在。’”", version_id: 6},
  {id: 158082, book: "John", verse: "1.016", unformatted: "原来从他的丰盛完美中，\n我们都领受了恩典，而且恩上加恩。", version_id: 6},
  {id: 158083, book: "John", verse: "1.017", unformatted: "因为律法是藉着摩西赐下的，\n恩典和真理是藉着耶稣基督而来的。", version_id: 6},
  {id: 158084, book: "John", verse: "1.018", unformatted: "从来没有人看见神，\n只有在父怀里的那位独生子——神，\n他将神表明了出来。", version_id: 6},
  {id: 158085, book: "John", verse: "1.019", unformatted: "施洗约翰的见证\n以下是约翰的见证。那时候，耶路撒冷的那些犹太人派了一些祭司和利未人到约翰那里去问他：“你是谁？”", version_id: 6},
  {id: 158086, book: "John", verse: "1.02", unformatted: "他就承认，并毫不否认地告白：“我不是基督。”", version_id: 6},
  {id: 158087, book: "John", verse: "1.021", unformatted: "他们又问：“那么你是谁呢？是以利亚吗？”\n他说：“我不是。”\n“是那位先知吗？”\n他回答：“不是。”", version_id: 6},
  {id: 158088, book: "John", verse: "1.022", unformatted: "于是他们问：“你到底是谁？好让我们给那些派我们来的人一个答复。你自己说，你是什么人？”", version_id: 6},
  {id: 158089, book: "John", verse: "1.023", unformatted: "约翰说：“正如先知以赛亚所说的，我就是在旷野里呼喊的声音：‘你们当修直主的道。’”", version_id: 6},
  {id: 158090, book: "John", verse: "1.024", unformatted: "那些被派来的人属于法利赛人，", version_id: 6},
  {id: 158091, book: "John", verse: "1.025", unformatted: "他们又问约翰：“你既不是基督，又不是以利亚，也不是那位先知，那么，你为什么施洗呢？”", version_id: 6},
  {id: 158092, book: "John", verse: "1.026", unformatted: "约翰回答说：“我用水施洗，但有一位站在你们中间，是你们不认识的，", version_id: 6},
  {id: 158093, book: "John", verse: "1.027", unformatted: "他是那在我之后来的，我就是为他解鞋带也不配。”", version_id: 6},
  {id: 158094, book: "John", verse: "1.028", unformatted: "这些事发生在约旦河对岸的伯大尼，就是约翰施洗的地方。", version_id: 6},
  {id: 158095, book: "John", verse: "1.029", unformatted: "神的羔羊\n第二天，约翰看见耶稣向他走来，就说：“看哪，神的羔羊，除去世人罪孽的！", version_id: 6},
  {id: 158096, book: "John", verse: "1.03", unformatted: "这一位就是我所说的‘那在我之后来的人，已成为在我前面的，因为在我之前他就存在了。’", version_id: 6},
  {id: 158097, book: "John", verse: "1.031", unformatted: "我以前也不认识他，但为了让他向以色列显明，所以我来用水施洗。”", version_id: 6},
  {id: 158098, book: "John", verse: "1.032", unformatted: "约翰又见证说：“我看到圣灵好像鸽子从天上降临，停留在他身上。", version_id: 6},
  {id: 158099, book: "John", verse: "1.033", unformatted: "我以前也不认识他，可是那派我用水施洗的对我说：‘如果你看见圣灵降临在谁身上，又停留在谁身上，谁就是用圣灵施洗的那一位。’", version_id: 6},
  {id: 158100, book: "John", verse: "1.034", unformatted: "我看到了，就见证：这一位就是神的儿子。”", version_id: 6},
  {id: 158101, book: "John", verse: "1.035", unformatted: "过了一天，约翰和他的两个门徒又站在那里。", version_id: 6},
  {id: 158102, book: "John", verse: "1.036", unformatted: "约翰看着耶稣走过，就说：“看哪，神的羔羊！”", version_id: 6},
  {id: 158103, book: "John", verse: "1.037", unformatted: "那两个门徒听他说话，就跟从了耶稣。", version_id: 6},
  {id: 158104, book: "John", verse: "1.038", unformatted: "耶稣转过身来，看到他们跟着走，就问：“你们寻找什么呢？”\n他们对耶稣说：“拉比，你住在哪里呢？”——“拉比”翻译出来意思是“老师”。", version_id: 6},
  {id: 158105, book: "John", verse: "1.039", unformatted: "耶稣说：“你们来看吧！”他们去了，看见耶稣住的地方，那天就住在他那里。那时大约是下午四点。", version_id: 6},
  {id: 158106, book: "John", verse: "1.04", unformatted: "听见了约翰的话而跟从耶稣的两个人中，有一个是西门彼得的弟弟安得烈。", version_id: 6},
  {id: 158107, book: "John", verse: "1.041", unformatted: "安得烈先去找自己的哥哥西门，对他说：“我们找到了弥赛亚。”——“弥赛亚”翻译出来就是“基督”。", version_id: 6},
  {id: 158108, book: "John", verse: "1.042", unformatted: "他就带西门到耶稣那里。\n耶稣注视着他，说：“你是约翰的儿子西门，你将被称为矶法。”——“矶法”翻译出来就是“彼得”。", version_id: 6},
  {id: 158109, book: "John", verse: "1.043", unformatted: "腓力与拿旦业\n又过了一天，耶稣决定前往加利利地区。他找到腓力，对他说：“你跟从我！”", version_id: 6},
  {id: 158110, book: "John", verse: "1.044", unformatted: "腓力来自伯赛达，与安得烈和彼得同乡。", version_id: 6},
  {id: 158111, book: "John", verse: "1.045", unformatted: "腓力找到拿旦业，对他说：“我们发现了摩西在律法上所写的，和先知们所记的那一位。他就是约瑟的儿子，拿撒勒人耶稣。”", version_id: 6},
  {id: 158112, book: "John", verse: "1.046", unformatted: "拿旦业对他说：“拿撒勒还能出什么好的吗？”\n腓力说：“你来看吧！”", version_id: 6},
  {id: 158113, book: "John", verse: "1.047", unformatted: "耶稣看见拿旦业向他走来，就指着他说：“看，一个真正的以色列人！他里面没有诡诈。”", version_id: 6},
  {id: 158114, book: "John", verse: "1.048", unformatted: "拿旦业对耶稣说：“你怎么知道我呢？”\n耶稣回答说：“腓力叫你以前，你还在无花果树下，我就看见你了。”", version_id: 6},
  {id: 158115, book: "John", verse: "1.049", unformatted: "拿旦业说：“拉比，你是神的儿子，你是以色列的王！”", version_id: 6},
  {id: 158116, book: "John", verse: "1.05", unformatted: "耶稣对他说：“因为我说‘我看见你在无花果树下’，你就信吗？你将会看到比这更大的事。”", version_id: 6},
  {id: 158117, book: "John", verse: "1.051", unformatted: "耶稣又对他说：“我确确实实地告诉你们：你们将会看见天敞开了，神的天使们在人子上头，上去下来。”", version_id: 6},
  {id: 158118, book: "John", verse: "2.001", unformatted: "变水为酒\n第三天，在加利利的迦拿有一个婚宴。耶稣的母亲在那里，", version_id: 6},
  {id: 158119, book: "John", verse: "2.002", unformatted: "耶稣和他的门徒们也被邀请赴宴。", version_id: 6},
  {id: 158120, book: "John", verse: "2.003", unformatted: "酒用尽了，耶稣的母亲对他说：“他们没有酒了。”", version_id: 6},
  {id: 158121, book: "John", verse: "2.004", unformatted: "耶稣对她说：“母亲，这与你我有什么关系？我的时候还没有到。”", version_id: 6},
  {id: 158122, book: "John", verse: "2.005", unformatted: "他的母亲对仆人们说：“他吩咐你们什么，你们就做什么。”", version_id: 6},
  {id: 158123, book: "John", verse: "2.006", unformatted: "照犹太人洁净的规矩，有六口石缸摆在那里。每缸可容两三桶水。", version_id: 6},
  {id: 158124, book: "John", verse: "2.007", unformatted: "耶稣对仆人们说：“把这些缸装满水。”他们就装满了，直到缸沿。", version_id: 6},
  {id: 158125, book: "John", verse: "2.008", unformatted: "耶稣又对他们说：“现在舀出来，送去给管宴席的。”他们就送去了。", version_id: 6},
  {id: 158126, book: "John", verse: "2.009", unformatted: "当管宴席的尝了那水变的酒，并不知道这是从哪里来的，只有舀水的那些仆人知道。管宴席的就叫来新郎，", version_id: 6},
  {id: 158127, book: "John", verse: "2.01", unformatted: "对他说：“人人都是先摆上好酒，等宾客喝醉了，才摆上次等的。你却把好酒留到现在！”", version_id: 6},
  {id: 158128, book: "John", verse: "2.011", unformatted: "耶稣在加利利的迦拿行了这头一件神迹，显明了他的荣耀，他的门徒们就信了他。", version_id: 6},
  {id: 158129, book: "John", verse: "2.012", unformatted: "这事以后，耶稣和他的母亲、兄弟以及他的门徒们，都下到迦百农去，在那里住了几天。", version_id: 6},
  {id: 158130, book: "John", verse: "2.013", unformatted: "洁净圣殿\n犹太人的逾越节快到了，耶稣上耶路撒冷去。", version_id: 6},
  {id: 158131, book: "John", verse: "2.014", unformatted: "他看见圣殿里有卖牛、羊、鸽子的，还有兑换银币的人坐在那里。", version_id: 6},
  {id: 158132, book: "John", verse: "2.015", unformatted: "耶稣就用绳子做了一条鞭子，把所有的牛、羊都从圣殿里赶出去，倒出兑换银币之人的银币，并推翻了他们的桌子，", version_id: 6},
  {id: 158133, book: "John", verse: "2.016", unformatted: "又对卖鸽子的人说：“把这些东西从这里拿走！不要把我父的殿当成做买卖的地方！”", version_id: 6},
  {id: 158134, book: "John", verse: "2.017", unformatted: "他的门徒们就想起经上记着：“我为你的殿，心火焚烧。”", version_id: 6},
  {id: 158135, book: "John", verse: "2.018", unformatted: "于是那些犹太人问耶稣说：“你做这些事，有什么权柄的凭据给我们看呢？”", version_id: 6},
  {id: 158136, book: "John", verse: "2.019", unformatted: "耶稣回答说：“你们把这圣所毁了吧！三天内我要把它建起来。”", version_id: 6},
  {id: 158137, book: "John", verse: "2.02", unformatted: "那些犹太人说：“这圣所建了四十六年，而你能在三天内把它建起来吗？”", version_id: 6},
  {id: 158138, book: "John", verse: "2.021", unformatted: "其实耶稣所说的圣所，就是指他自己的身体。", version_id: 6},
  {id: 158139, book: "John", verse: "2.022", unformatted: "当他从死人中复活以后，他的门徒们才想起他说过这话，就相信了经上的话和耶稣所说的话。", version_id: 6},
  {id: 158140, book: "John", verse: "2.023", unformatted: "当耶稣在耶路撒冷过逾越节的时候，许多人看见他所行的这些神迹，就信了他的名。", version_id: 6},
  {id: 158141, book: "John", verse: "2.024", unformatted: "但耶稣自己却信不过他们，因为他了解所有的人。", version_id: 6},
  {id: 158142, book: "John", verse: "2.025", unformatted: "他也不需要谁来见证人的事，因为他知道人里面是什么。", version_id: 6},
  {id: 158143, book: "John", verse: "3.001", unformatted: "耶稣与尼克迪莫\n有一个人，属于法利赛人，名叫尼克迪莫，是犹太人的一位首领。", version_id: 6},
  {id: 158144, book: "John", verse: "3.002", unformatted: "他夜里来到耶稣那里，说：“拉比，我们知道你是从神那里来的老师，因为你所行的这些神迹，如果没有神的同在，没有人能做得到。”", version_id: 6},
  {id: 158145, book: "John", verse: "3.003", unformatted: "耶稣回答说：“我确确实实地告诉你：一个人如果不重生，就不能见神的国。”", version_id: 6},
  {id: 158146, book: "John", verse: "3.004", unformatted: "尼克迪莫问：“人老了，怎么能出生呢？他难道能再进母腹又出生吗？”", version_id: 6},
  {id: 158147, book: "John", verse: "3.005", unformatted: "耶稣回答：“我确确实实地告诉你：一个人如果不是由水和圣灵所生的，就不能进入神的国。", version_id: 6},
  {id: 158148, book: "John", verse: "3.006", unformatted: "由肉体所生的，就是肉体；由圣灵所生的，就是灵。", version_id: 6},
  {id: 158149, book: "John", verse: "3.007", unformatted: "我说‘你们必须重生’，你不要感到惊奇。", version_id: 6},
  {id: 158150, book: "John", verse: "3.008", unformatted: "风随意而吹，你听见风声，却不知道它从哪里来，往哪里去。所有由圣灵所生的，也是这样。”", version_id: 6},
  {id: 158151, book: "John", verse: "3.009", unformatted: "尼克迪莫又问：“怎么能有这些事呢？”", version_id: 6},
  {id: 158152, book: "John", verse: "3.01", unformatted: "耶稣回答说：“你是以色列的老师，还不明白这些事吗？", version_id: 6},
  {id: 158153, book: "John", verse: "3.011", unformatted: "我确确实实地告诉你：我们讲论我们所知道的，我们见证我们所看到的，而你们却不接受我们的见证。", version_id: 6},
  {id: 158154, book: "John", verse: "3.012", unformatted: "我告诉你们地上的事，你们尚且不信，如果我告诉你们天上的事，你们怎么会信呢？", version_id: 6},
  {id: 158155, book: "John", verse: "3.013", unformatted: "除了从天上降下来的人子，没有人升过天。", version_id: 6},
  {id: 158156, book: "John", verse: "3.014", unformatted: "摩西在旷野怎样把铜蛇举起，人子也必须照样被举起来，", version_id: 6},
  {id: 158157, book: "John", verse: "3.015", unformatted: "好使所有相信他的人，都得到永恒的生命。", version_id: 6},
  {id: 158158, book: "John", verse: "3.016", unformatted: "“神爱世人，甚至赐下他的独生子，好让所有信他的人不至于灭亡，反得永恒的生命，", version_id: 6},
  {id: 158159, book: "John", verse: "3.017", unformatted: "因为神差派他的儿子来到世上，不是为了定世人的罪，而是为了世人藉着他得救。", version_id: 6},
  {id: 158160, book: "John", verse: "3.018", unformatted: "信他的人，不被定罪；不信的人，已经被定罪了，因为他不信神独生子的名。", version_id: 6},
  {id: 158161, book: "John", verse: "3.019", unformatted: "“所定的罪是这样的：光来到了世界，人们却因自己的行为邪恶，不喜爱光，反喜爱黑暗。", version_id: 6},
  {id: 158162, book: "John", verse: "3.02", unformatted: "事实上，所有作恶的人，都憎恨光，不来到光那里，免得自己的行为被揭露出来；", version_id: 6},
  {id: 158163, book: "John", verse: "3.021", unformatted: "但行真理的人，却来到光那里，为了显明自己所做的是在神里面被做成的。”", version_id: 6},
  {id: 158164, book: "John", verse: "3.022", unformatted: "耶稣与施洗约翰\n这些事以后，耶稣和他的门徒们来到犹太地区，住在那里，并且给人施洗。", version_id: 6},
  {id: 158165, book: "John", verse: "3.023", unformatted: "约翰也在撒令附近的艾嫩施洗，因为那里水多。众人纷纷而来受洗。", version_id: 6},
  {id: 158166, book: "John", verse: "3.024", unformatted: "原来，约翰那时候还没有被投进监狱。", version_id: 6},
  {id: 158167, book: "John", verse: "3.025", unformatted: "约翰的门徒们与一个犹太人，为了洁净礼起了辩论。", version_id: 6},
  {id: 158168, book: "John", verse: "3.026", unformatted: "他们就到约翰那里，对他说：“拉比，你看，以前与你在约旦河对岸的，就是你所见证的那一位，他也在施洗，而且大家都到他那里去了。”", version_id: 6},
  {id: 158169, book: "John", verse: "3.027", unformatted: "约翰回答说：“如果不是从天上赐下的，人就不能得到什么。", version_id: 6},
  {id: 158170, book: "John", verse: "3.028", unformatted: "你们自己可以为我见证：我曾经说过‘我不是基督，而是奉差派在他前面的。’", version_id: 6},
  {id: 158171, book: "John", verse: "3.029", unformatted: "迎娶新娘的是新郎。新郎的朋友站在那里，听到新郎的声音，就欢喜快乐。这样，我这喜乐得以满足了。", version_id: 6},
  {id: 158172, book: "John", verse: "3.03", unformatted: "他必兴旺，我必衰微。”", version_id: 6},
  {id: 158173, book: "John", verse: "3.031", unformatted: "来自天上的那一位\n那从上面来的，是在万有之上；那从地而出的，属于地，而且所说的也属于地。那从天上来的，是在万有之上，", version_id: 6},
  {id: 158174, book: "John", verse: "3.032", unformatted: "他见证他所看到的、他所听到的，可是没有人接受他的见证。", version_id: 6},
  {id: 158175, book: "John", verse: "3.033", unformatted: "那接受他见证的人，就印证了神是真实的。", version_id: 6},
  {id: 158176, book: "John", verse: "3.034", unformatted: "神所差派的那一位，就说神的话语，因为神不靠着量度赐下圣灵。", version_id: 6},
  {id: 158177, book: "John", verse: "3.035", unformatted: "父爱子，把一切都交在他手里了。", version_id: 6},
  {id: 158178, book: "John", verse: "3.036", unformatted: "信从子的人，就有永恒的生命；但是不肯信从子的人，见不到生命，而且神的震怒也停留在他身上。", version_id: 6},
  {id: 158179, book: "John", verse: "4.001", unformatted: "耶稣与撒马利亚妇人\n耶稣知道，法利赛人听说他收门徒和施洗比约翰更多。", version_id: 6},
  {id: 158180, book: "John", verse: "4.002", unformatted: "其实不是耶稣亲自施洗，而是他的门徒们施洗。", version_id: 6},
  {id: 158181, book: "John", verse: "4.003", unformatted: "于是耶稣离开犹太地区，又前往加利利地区。", version_id: 6},
  {id: 158182, book: "John", verse: "4.004", unformatted: "但他定要经过撒马利亚，", version_id: 6},
  {id: 158183, book: "John", verse: "4.005", unformatted: "这样就来到了撒马利亚的一个城，叫叙加。这城靠近雅各给他儿子约瑟的那块地，", version_id: 6},
  {id: 158184, book: "John", verse: "4.006", unformatted: "雅各井就在那里。耶稣因旅途劳累，就坐在井边。那时大约是中午十二点。", version_id: 6},
  {id: 158185, book: "John", verse: "4.007", unformatted: "有一个撒马利亚妇人来打水。耶稣对她说：“请给我一点水喝。”", version_id: 6},
  {id: 158186, book: "John", verse: "4.008", unformatted: "那时，他的门徒们进城买食物去了。", version_id: 6},
  {id: 158187, book: "John", verse: "4.009", unformatted: "撒马利亚妇人对他说：“你是犹太人，怎么向我一个撒马利亚妇人要水喝呢？”原来犹太人不和撒马利亚人来往。", version_id: 6},
  {id: 158188, book: "John", verse: "4.01", unformatted: "耶稣回答她，说：“如果你明白神的恩赐，也知道是谁对你说‘给我一点水喝’，你早就求他了，他也把活水给你了。”", version_id: 6},
  {id: 158189, book: "John", verse: "4.011", unformatted: "妇人问：“先生，你没有打水的器具，井又深，你从哪里得来那活水呢？", version_id: 6},
  {id: 158190, book: "John", verse: "4.012", unformatted: "难道你比我们的先祖雅各更大吗？他给我们留下了这口井。他自己、他的子孙、他的牲畜都喝这井里的水！”", version_id: 6},
  {id: 158191, book: "John", verse: "4.013", unformatted: "耶稣回答说：“所有喝这水的人，还会再渴。", version_id: 6},
  {id: 158192, book: "John", verse: "4.014", unformatted: "但如果有人喝了我给他的水，就绝不干渴，直到永远。不但如此，我给他的水将要在他里面成为泉源，一直涌流到永恒的生命。”", version_id: 6},
  {id: 158193, book: "John", verse: "4.015", unformatted: "妇人说：“先生，请给我那水，使我不渴，我也不用到这里来打水了。”", version_id: 6},
  {id: 158194, book: "John", verse: "4.016", unformatted: "耶稣说：“去叫你的丈夫，再回到这里来。”", version_id: 6},
  {id: 158195, book: "John", verse: "4.017", unformatted: "妇人回答说：“我没有丈夫。”\n耶稣说：“你说你没有丈夫，这话没错。", version_id: 6},
  {id: 158196, book: "John", verse: "4.018", unformatted: "其实你已经有过五个丈夫，你现在有的，不是你的丈夫。你说的是真的。”", version_id: 6},
  {id: 158197, book: "John", verse: "4.019", unformatted: "妇人对他说：“先生，我看出你是先知！", version_id: 6},
  {id: 158198, book: "John", verse: "4.02", unformatted: "我们的祖先在这山上敬拜，而你们却说敬拜的地方必须在耶路撒冷。”", version_id: 6},
  {id: 158199, book: "John", verse: "4.021", unformatted: "耶稣说：“妇人，你当相信我，时候就要到了！那时你们敬拜父，既不在这山上，也不在耶路撒冷。", version_id: 6},
  {id: 158200, book: "John", verse: "4.022", unformatted: "你们敬拜你们所不认识的；我们却敬拜我们所认识的，因为救恩是出自犹太人的。", version_id: 6},
  {id: 158201, book: "John", verse: "4.023", unformatted: "然而，时候就要到了，现在就是了！那时真正敬拜的人，要在灵里和真理中敬拜父，因为父就是想要这样的人来敬拜他。", version_id: 6},
  {id: 158202, book: "John", verse: "4.024", unformatted: "神是灵，敬拜他的人必须在灵里和真理中敬拜。”", version_id: 6},
  {id: 158203, book: "John", verse: "4.025", unformatted: "妇人说：“我知道那称为基督的弥赛亚要来。他来的时候，就会把一切都传讲给我们。”", version_id: 6},
  {id: 158204, book: "John", verse: "4.026", unformatted: "耶稣说：“这和你说话的我，就是那一位。”", version_id: 6},
  {id: 158205, book: "John", verse: "4.027", unformatted: "庄稼熟了\n正在这时，他的门徒们回来了。他们见耶稣与一个妇人一同说话，就感到惊奇。只是没有人问“你想要什么？”或“你为什么与她说话？”", version_id: 6},
  {id: 158206, book: "John", verse: "4.028", unformatted: "那妇人放下她的水罐，回到城里告诉人们说：", version_id: 6},
  {id: 158207, book: "John", verse: "4.029", unformatted: "“你们来看！有个人把我过去所做的一切都说了出来，这个人会不会就是基督呢？”", version_id: 6},
  {id: 158208, book: "John", verse: "4.03", unformatted: "大家就出城，来到耶稣那里。", version_id: 6},
  {id: 158209, book: "John", verse: "4.031", unformatted: "这其间，门徒们劝耶稣说：“拉比，请吃。”", version_id: 6},
  {id: 158210, book: "John", verse: "4.032", unformatted: "但耶稣说：“我有食物吃，是你们所不知道的。”", version_id: 6},
  {id: 158211, book: "John", verse: "4.033", unformatted: "门徒彼此问：“难道有人拿东西来给他吃了吗？”", version_id: 6},
  {id: 158212, book: "John", verse: "4.034", unformatted: "耶稣对他们说：“我的食物就是遵行那派我来者的旨意，并且完成他的工作。", version_id: 6},
  {id: 158213, book: "John", verse: "4.035", unformatted: "你们不是说‘收割的季节还有四个月才到’吗？看哪，我告诉你们：举目向田里观望，庄稼已经成熟，可以收割了！", version_id: 6},
  {id: 158214, book: "John", verse: "4.036", unformatted: "收割的人得酬报，收集果实归入永恒的生命，好使撒种的和收割的一同快乐。", version_id: 6},
  {id: 158215, book: "John", verse: "4.037", unformatted: "实际上，这样的说法是真实的：‘一人撒种，另一个人收割。’", version_id: 6},
  {id: 158216, book: "John", verse: "4.038", unformatted: "我派你们去收你们未曾劳苦的；别人劳苦了，你们也进入了他们的劳苦中。”", version_id: 6},
  {id: 158217, book: "John", verse: "4.039", unformatted: "救世主\n当时，那城里有许多撒马利亚人信了耶稣。这是因为那妇人见证说：“他把我过去所做的一切都说了出来。”", version_id: 6},
  {id: 158218, book: "John", verse: "4.04", unformatted: "这样，撒马利亚人来到耶稣那里的时候，请求他住在他们那里。于是耶稣在那里住了两天。", version_id: 6},
  {id: 158219, book: "John", verse: "4.041", unformatted: "后来，因着耶稣的话，信的人就更多了。", version_id: 6},
  {id: 158220, book: "John", verse: "4.042", unformatted: "他们告诉那妇人说：“现在我们信，不再是因为你的话，而是因为我们亲耳听到了，并且知道这一位真是世界的救主。”", version_id: 6},
  {id: 158221, book: "John", verse: "4.043", unformatted: "加利利人欢迎耶稣\n两天后，耶稣离开了那里，前往加利利地区。", version_id: 6},
  {id: 158222, book: "John", verse: "4.044", unformatted: "他曾亲自见证说，先知在自己的家乡得不到尊敬。", version_id: 6},
  {id: 158223, book: "John", verse: "4.045", unformatted: "当耶稣来到加利利地区，加利利人就接受他，因为他们也曾经上耶路撒冷去过节，看到耶稣节日期间在那里所行的一切事。", version_id: 6},
  {id: 158224, book: "John", verse: "4.046", unformatted: "治愈大臣的儿子\n耶稣又来到加利利的迦拿，就是他把水变成酒的地方。有一个大臣，他的儿子在迦百农患了病。", version_id: 6},
  {id: 158225, book: "John", verse: "4.047", unformatted: "这个人听说耶稣从犹太来到加利利，就到耶稣那里去，请求他下去治愈他的儿子，因为他的儿子快要死了。", version_id: 6},
  {id: 158226, book: "John", verse: "4.048", unformatted: "耶稣对他说：“你们如果没有看见神迹和奇事，你们就绝不会信！”", version_id: 6},
  {id: 158227, book: "John", verse: "4.049", unformatted: "那大臣对耶稣说：“先生，求你在我的孩子还没有死之前下来吧！”", version_id: 6},
  {id: 158228, book: "John", verse: "4.05", unformatted: "耶稣对他说：“你回去吧，你的儿子活了！”那人相信耶稣的话，就回去了。", version_id: 6},
  {id: 158229, book: "John", verse: "4.051", unformatted: "他正下去的时候，他的奴仆们迎着他来，告诉他孩子活了。", version_id: 6},
  {id: 158230, book: "John", verse: "4.052", unformatted: "他就问孩子好转的时间，他们说：“昨天下午一点，烧就退了。”", version_id: 6},
  {id: 158231, book: "John", verse: "4.053", unformatted: "这位父亲就知道，那正是耶稣说“你的儿子活了”的时间。于是他自己和全家人都信了。", version_id: 6},
  {id: 158232, book: "John", verse: "4.054", unformatted: "这是耶稣从犹太回到加利利以后所行的第二件神迹。", version_id: 6},
  {id: 158233, book: "John", verse: "5.001", unformatted: "治愈池边的病人\n这些事以后，犹太人的一个节日到了，耶稣就上耶路撒冷去。", version_id: 6},
  {id: 158234, book: "John", verse: "5.002", unformatted: "在耶路撒冷的羊门附近有一个池子，希伯来语叫做“贝示达”，那里有五道柱廊。", version_id: 6},
  {id: 158235, book: "John", verse: "5.003", unformatted: "柱廊里躺着一群患病的，有瞎眼的、瘸腿的、瘫痪的。他们正等着池水动起来，", version_id: 6},
  {id: 158236, book: "John", verse: "5.004", unformatted: "因为主的天使按时下到池子里搅动池水。每次水动起来时，第一个下到池子里的人，无论患什么病都会痊愈。", version_id: 6},
  {id: 158237, book: "John", verse: "5.005", unformatted: "那里有一个人，病了三十八年。", version_id: 6},
  {id: 158238, book: "John", verse: "5.006", unformatted: "耶稣见这个人躺在那里，知道他已经病了很久，就问他：“你想痊愈吗？”", version_id: 6},
  {id: 158239, book: "John", verse: "5.007", unformatted: "那个病人回答：“先生，池水被搅动的时候，没有人把我放进池子里；而正当我要下去的时候，别人总比我先下去。”", version_id: 6},
  {id: 158240, book: "John", verse: "5.008", unformatted: "耶稣对他说：“起来，拿起你的垫子走路吧！”", version_id: 6},
  {id: 158241, book: "John", verse: "5.009", unformatted: "那个人立刻痊愈了，就拿起他的垫子开始走路。\n那天是安息日，", version_id: 6},
  {id: 158242, book: "John", verse: "5.01", unformatted: "所以那些犹太人对那得了痊愈的人说：“今天是安息日，你拿着垫子是不可以的！”", version_id: 6},
  {id: 158243, book: "John", verse: "5.011", unformatted: "他就回答：“是那个使我痊愈的人对我说‘拿起你的垫子走路吧。’”", version_id: 6},
  {id: 158244, book: "John", verse: "5.012", unformatted: "他们问：“对你说‘拿起垫子走路’的那个人是谁？”", version_id: 6},
  {id: 158245, book: "John", verse: "5.013", unformatted: "那得了痊愈的人并不知道他是谁，因为那里有一群人，而耶稣已经抽身离去了。", version_id: 6},
  {id: 158246, book: "John", verse: "5.014", unformatted: "这些事以后，耶稣在圣殿里找到他，对他说：“看，你已经痊愈了。不要再犯罪，免得更严重的事临到你。”", version_id: 6},
  {id: 158247, book: "John", verse: "5.015", unformatted: "那个人就去向那些犹太人报告说，使他痊愈的是耶稣。", version_id: 6},
  {id: 158248, book: "John", verse: "5.016", unformatted: "父与子\n那些犹太人之所以逼迫耶稣，是因为他在安息日做这些事。", version_id: 6},
  {id: 158249, book: "John", verse: "5.017", unformatted: "耶稣却对他们说：“直到如今，我父在做工，我也在做工。”", version_id: 6},
  {id: 158250, book: "John", verse: "5.018", unformatted: "为此，那些犹太人更想要杀他，因为他不仅干犯了安息日，还称神是他的父，把自己当做与神同等的。", version_id: 6},
  {id: 158251, book: "John", verse: "5.019", unformatted: "耶稣对他们说：“我确确实实地告诉你们：子凭着自己什么也不能做，只有看见父所做的，子才能做，因为父所做的事，子也同样地做。", version_id: 6},
  {id: 158252, book: "John", verse: "5.02", unformatted: "原来，父喜爱子，把自己所做的一切都给他看，还要把比这些更大的事给他看，使你们感到惊奇。", version_id: 6},
  {id: 158253, book: "John", verse: "5.021", unformatted: "因为父怎样使死人复活，赐他们生命，子也照样按自己的意愿赐人生命。", version_id: 6},
  {id: 158254, book: "John", verse: "5.022", unformatted: "事实上，父并不审判任何人，而把一切审判的事都交给了子，", version_id: 6},
  {id: 158255, book: "John", verse: "5.023", unformatted: "好使所有的人都尊重子，就像尊重父那样。不尊重子的，就是不尊重派他来的父。", version_id: 6},
  {id: 158256, book: "John", verse: "5.024", unformatted: "生命与审判\n“我确确实实地告诉你们：谁听我的话，又相信派我来的那一位，谁就有永恒的生命。他不但不被定罪，而且已经出死入生了。", version_id: 6},
  {id: 158257, book: "John", verse: "5.025", unformatted: "“我确确实实地告诉你们：时候就要到了，现在就是了！当死人听见神儿子的声音时，听到的人就将活了。", version_id: 6},
  {id: 158258, book: "John", verse: "5.026", unformatted: "原来，父怎样在自己里面有生命，也照样使子在自己里面有生命；", version_id: 6},
  {id: 158259, book: "John", verse: "5.027", unformatted: "并且把审判的权柄交给了他，因为他是人子。", version_id: 6},
  {id: 158260, book: "John", verse: "5.028", unformatted: "你们不要为此感到惊奇：时候就要到了！那时所有在坟墓里的人，都会听到他的声音，", version_id: 6},
  {id: 158261, book: "John", verse: "5.029", unformatted: "并且要出来——行善的人复活得生命，作恶的人复活被定罪。", version_id: 6},
  {id: 158262, book: "John", verse: "5.03", unformatted: "“我凭自己什么也不能做。我怎么听见，就怎么审判，而且我的审判是公义的，因为我不求自己的意思，而求那派我来者的意思。", version_id: 6},
  {id: 158263, book: "John", verse: "5.031", unformatted: "为子做见证\n“如果我为自己做见证，我的见证就无效。", version_id: 6},
  {id: 158264, book: "John", verse: "5.032", unformatted: "为我做见证的另有一位，并且我知道，他为我做的见证是有效的。", version_id: 6},
  {id: 158265, book: "John", verse: "5.033", unformatted: "你们曾经派人到约翰那里，他为真理做了见证。", version_id: 6},
  {id: 158266, book: "John", verse: "5.034", unformatted: "其实我所接受的见证不是从人来的。不过我说这些话，是为了要你们得救。", version_id: 6},
  {id: 158267, book: "John", verse: "5.035", unformatted: "约翰是一盏燃烧发光的灯，你们情愿在他的光里快乐一时。", version_id: 6},
  {id: 158268, book: "John", verse: "5.036", unformatted: "“我有比约翰更大的见证，因为父交给我工作要我去完成。我现在做的这些工作正为我见证：就是父差派了我。", version_id: 6},
  {id: 158269, book: "John", verse: "5.037", unformatted: "派我来的父也亲自为我做了见证。你们从来没有听到他的声音，也没有看到他的相貌，", version_id: 6},
  {id: 158270, book: "John", verse: "5.038", unformatted: "更没有把他的话语存在你们里面，因为你们不信他所派来的那一位。", version_id: 6},
  {id: 158271, book: "John", verse: "5.039", unformatted: "你们查考经文，因你们以为从中可以得到永恒的生命。其实这经文就是为我做见证的。", version_id: 6},
  {id: 158272, book: "John", verse: "5.04", unformatted: "可是你们不肯到我这里来得生命。", version_id: 6},
  {id: 158273, book: "John", verse: "5.041", unformatted: "“我不接受从人而来的荣耀。", version_id: 6},
  {id: 158274, book: "John", verse: "5.042", unformatted: "不过我了解你们，你们里面没有属神的爱。", version_id: 6},
  {id: 158275, book: "John", verse: "5.043", unformatted: "我奉我父的名来了，你们却不接受我；如果有别人奉自己的名而来，你们倒会接受他。", version_id: 6},
  {id: 158276, book: "John", verse: "5.044", unformatted: "你们彼此接受荣耀，却不寻求从独一的神而来的荣耀，那怎么能信呢？", version_id: 6},
  {id: 158277, book: "John", verse: "5.045", unformatted: "不要以为我将要在父面前控告你们，有一位控告你们的，就是你们所仰赖的摩西。", version_id: 6},
  {id: 158278, book: "John", verse: "5.046", unformatted: "事实上，如果你们信摩西，就会信我，因为他写了关于我的事；", version_id: 6},
  {id: 158279, book: "John", verse: "5.047", unformatted: "如果你们不相信他所写的，又怎么会相信我的话呢？”", version_id: 6},
  {id: 158280, book: "John", verse: "6.001", unformatted: "五饼二鱼\n这些事以后，耶稣渡过加利利湖，也就是太巴列湖，到对岸去。", version_id: 6},
  {id: 158281, book: "John", verse: "6.002", unformatted: "有一大群人，因为看到他在病人身上所行的神迹，就跟随他。", version_id: 6},
  {id: 158282, book: "John", verse: "6.003", unformatted: "耶稣上了山，与他的门徒们在那里坐下。", version_id: 6},
  {id: 158283, book: "John", verse: "6.004", unformatted: "那时犹太人的逾越节快到了。", version_id: 6},
  {id: 158284, book: "John", verse: "6.005", unformatted: "耶稣举目观看，见一大群人向他走来，就对腓力说：“我们从哪里买饼给这些人吃呢？”", version_id: 6},
  {id: 158285, book: "John", verse: "6.006", unformatted: "他说这话是要试试腓力，其实他自己已经知道要做什么了。", version_id: 6},
  {id: 158286, book: "John", verse: "6.007", unformatted: "腓力回答：“就是两百个银币的饼，也不够每个人吃一小块！”", version_id: 6},
  {id: 158287, book: "John", verse: "6.008", unformatted: "他的一个门徒，就是西门彼得的弟弟安得烈，对耶稣说：", version_id: 6},
  {id: 158288, book: "John", verse: "6.009", unformatted: "“这里有个孩子，带了五个大麦饼和两条鱼。可是对这么多人，这些算什么呢？”", version_id: 6},
  {id: 158289, book: "John", verse: "6.01", unformatted: "耶稣说：“让他们坐下来。”\n那地方草多，他们坐了下来；男人的数目约有五千。", version_id: 6},
  {id: 158290, book: "John", verse: "6.011", unformatted: "耶稣就拿起饼来，祝谢以后，分给那些坐着的人，把鱼也如此分了，他们要多少就给多少。", version_id: 6},
  {id: 158291, book: "John", verse: "6.012", unformatted: "大家吃饱了以后，耶稣就对他的门徒们说：“把剩下的碎块收拾起来，免得浪费。”", version_id: 6},
  {id: 158292, book: "John", verse: "6.013", unformatted: "他们就把大家吃剩的那五个大麦饼的碎块收拾起来，装满了十二个篮子。", version_id: 6},
  {id: 158293, book: "John", verse: "6.014", unformatted: "众人看见耶稣所行的神迹，就说：“这个人确实是那位要来到世上的先知！”", version_id: 6},
  {id: 158294, book: "John", verse: "6.015", unformatted: "耶稣看出他们要来强迫他做王，就又独自退到山上去了。", version_id: 6},
  {id: 158295, book: "John", verse: "6.016", unformatted: "在水面上行走\n到了晚上，他的门徒们下到湖边。", version_id: 6},
  {id: 158296, book: "John", verse: "6.017", unformatted: "他们上了船，开始渡过湖往迦百农去。原来天色已经黑了，而耶稣还没有来到他们那里。", version_id: 6},
  {id: 158297, book: "John", verse: "6.018", unformatted: "当时，大风吹起，湖水翻腾。", version_id: 6},
  {id: 158298, book: "John", verse: "6.019", unformatted: "他们已经划了约有四、五公里，看到耶稣在湖面上行走，逐渐向船靠近，他们就感到惧怕。", version_id: 6},
  {id: 158299, book: "John", verse: "6.02", unformatted: "耶稣对他们说：“是我，不要怕！”", version_id: 6},
  {id: 158300, book: "John", verse: "6.021", unformatted: "门徒们这才愿意接他上船。船立刻就到了他们所要去的地方。", version_id: 6},
  {id: 158301, book: "John", verse: "6.022", unformatted: "生命的粮\n第二天，留在湖对岸的众人，知道在那里除了一条小船之外，没有别的船；而且知道耶稣没有和他的门徒们一起上船，是门徒们自己去的。", version_id: 6},
  {id: 158302, book: "John", verse: "6.023", unformatted: "有几条别的小船从太巴列城来，靠近主祝谢后众人吃饼的地方。", version_id: 6},
  {id: 158303, book: "John", verse: "6.024", unformatted: "这样，众人一发现耶稣已经不在那里，连他的门徒们也不在那里，就上了船，往迦百农去找耶稣。", version_id: 6},
  {id: 158304, book: "John", verse: "6.025", unformatted: "他们在湖的对岸找到了耶稣，就问他：“拉比，你是什么时候到这里来的？”", version_id: 6},
  {id: 158305, book: "John", verse: "6.026", unformatted: "耶稣回答说：“我确确实实地告诉你们：你们找我，并不是因为看见了神迹，而是因为你们吃饼吃饱了。", version_id: 6},
  {id: 158306, book: "John", verse: "6.027", unformatted: "不要为那会腐败的食物做工，而要为那存留到永生的食物做工。这食物就是人子要给你们的，因为人子是父神所印证的。”", version_id: 6},
  {id: 158307, book: "John", verse: "6.028", unformatted: "他们问：“我们应该做什么，才算做神的工作呢？”", version_id: 6},
  {id: 158308, book: "John", verse: "6.029", unformatted: "耶稣回答说：“信神所派来的那一位，就是做神的工作。”", version_id: 6},
  {id: 158309, book: "John", verse: "6.03", unformatted: "他们就问：“那么，你行个什么神迹，好让我们看了就可以信你呢？你可以做什么呢？", version_id: 6},
  {id: 158310, book: "John", verse: "6.031", unformatted: "我们的祖先在旷野吃了吗哪，正如经上所记：‘他把从天上来的粮赐给他们吃。’”", version_id: 6},
  {id: 158311, book: "John", verse: "6.032", unformatted: "耶稣对他们说：“我确确实实地告诉你们：那从天上来的粮不是摩西赐给你们的，而是我父把从天上来的真粮赐给了你们。", version_id: 6},
  {id: 158312, book: "John", verse: "6.033", unformatted: "要知道，神的粮就是从天上降下来、赐生命给世人的那一位。”", version_id: 6},
  {id: 158313, book: "John", verse: "6.034", unformatted: "他们就对耶稣说：“主啊，请你时常把这粮赐给我们！”", version_id: 6},
  {id: 158314, book: "John", verse: "6.035", unformatted: "耶稣对他们说：“我就是生命的粮。到我这里来的人，绝不饥饿；信我的人，永不干渴。", version_id: 6},
  {id: 158315, book: "John", verse: "6.036", unformatted: "但是我告诉过你们，你们虽然看到了我，还是不信。", version_id: 6},
  {id: 158316, book: "John", verse: "6.037", unformatted: "凡是父赐给我的人，都会到我这里来，而到我这里来的人，我绝不丢弃，", version_id: 6},
  {id: 158317, book: "John", verse: "6.038", unformatted: "因为我从天上降下来，不是要行自己的意思，而是要行那派我来者的意思。", version_id: 6},
  {id: 158318, book: "John", verse: "6.039", unformatted: "那派我来者的旨意，就是要我不失去他赐给我的任何人，而且要我在末日使他复活。", version_id: 6},
  {id: 158319, book: "John", verse: "6.04", unformatted: "要知道，我父的旨意正是：所有看见子而信他的人都得到永恒的生命，并且在末日，我将要使他复活。”", version_id: 6},
  {id: 158320, book: "John", verse: "6.041", unformatted: "这时候，一些犹太人因为耶稣说‘我是从天上降下来的粮’，就私下议论他，", version_id: 6},
  {id: 158321, book: "John", verse: "6.042", unformatted: "并说：“这不就是约瑟的儿子耶稣吗？我们不都认识他的父母吗？现在他怎么说‘我是从天上降下来的’呢？”", version_id: 6},
  {id: 158322, book: "John", verse: "6.043", unformatted: "耶稣就对他们说：“你们不要彼此议论了。", version_id: 6},
  {id: 158323, book: "John", verse: "6.044", unformatted: "如果不是派我来的父引领他，没有人能到我这里来；到我这里来的，在末日我还要使他复活。", version_id: 6},
  {id: 158324, book: "John", verse: "6.045", unformatted: "先知书上记着：‘他们都将成为神所教导的人。’任何人听了并领会了从父而来的教导，就会到我这里来。", version_id: 6},
  {id: 158325, book: "John", verse: "6.046", unformatted: "这并不是说，有谁看到父；只有从神而来的那一位，他才看到父。", version_id: 6},
  {id: 158326, book: "John", verse: "6.047", unformatted: "“我确确实实地告诉你们：信我的人，就有永恒的生命。", version_id: 6},
  {id: 158327, book: "John", verse: "6.048", unformatted: "我就是生命的粮。", version_id: 6},
  {id: 158328, book: "John", verse: "6.049", unformatted: "你们的祖先在旷野吃了吗哪，还是死了。", version_id: 6},
  {id: 158329, book: "John", verse: "6.05", unformatted: "那从天上降下来、让人吃了而不死的粮，就在这里。", version_id: 6},
  {id: 158330, book: "John", verse: "6.051", unformatted: "我就是从天上降下来的生命的粮。人如果吃了这粮，就将活着，直到永远。我要赐的粮就是我的肉身，是为了世人的生命所赐的。”", version_id: 6},
  {id: 158331, book: "John", verse: "6.052", unformatted: "于是那些犹太人彼此争论，说：“这个人怎么能把自己的肉身给我们吃呢？”", version_id: 6},
  {id: 158332, book: "John", verse: "6.053", unformatted: "耶稣说：“我确确实实地告诉你们：如果你们不吃人子的肉，不喝人子的血，你们里面就没有生命。", version_id: 6},
  {id: 158333, book: "John", verse: "6.054", unformatted: "吃我肉、喝我血的人，就有永恒的生命，在末日我还要使他复活；", version_id: 6},
  {id: 158334, book: "John", verse: "6.055", unformatted: "因为我的肉是真食物，我的血是真饮物。", version_id: 6},
  {id: 158335, book: "John", verse: "6.056", unformatted: "吃我肉、喝我血的人，住在我里面，我也住在他里面。", version_id: 6},
  {id: 158336, book: "John", verse: "6.057", unformatted: "永生的父怎样差派了我，我又因父而活，照样，以我为食的人也将因我而活。", version_id: 6},
  {id: 158337, book: "John", verse: "6.058", unformatted: "这才是从天上降下来的粮，不像吗哪。你们的祖先吃过吗哪还是死了，而吃这粮的人就将活着，直到永远。”", version_id: 6},
  {id: 158338, book: "John", verse: "6.059", unformatted: "这些话是耶稣在迦百农教导人的时候，在会堂里说的。", version_id: 6},
  {id: 158339, book: "John", verse: "6.06", unformatted: "许多门徒离开耶稣\n他的门徒当中有许多人听了，就说：“这话实在严厉，谁能听呢？”", version_id: 6},
  {id: 158340, book: "John", verse: "6.061", unformatted: "耶稣自己里面知道他的门徒们为这话私下议论，就对他们说：“这话绊倒你们吗？", version_id: 6},
  {id: 158341, book: "John", verse: "6.062", unformatted: "所以，如果你们看见人子升到他原先所在的地方，你们会怎么样呢？", version_id: 6},
  {id: 158342, book: "John", verse: "6.063", unformatted: "赐人生命的是灵，肉体没有什么用。我对你们说的话就是灵，就是生命。", version_id: 6},
  {id: 158343, book: "John", verse: "6.064", unformatted: "然而你们当中有一些不信的人。”原来，耶稣从起初就知道哪些人不信，也知道哪一个人要出卖他。", version_id: 6},
  {id: 158344, book: "John", verse: "6.065", unformatted: "耶稣又说：“所以我告诉过你们：如果不是蒙父所赐，没有人能到我这里来。”", version_id: 6},
  {id: 158345, book: "John", verse: "6.066", unformatted: "从此耶稣的门徒中有许多人离开回到背后的事上，不再与他同行。", version_id: 6},
  {id: 158346, book: "John", verse: "6.067", unformatted: "耶稣就对十二使徒说：“你们也不会想离开吧？”", version_id: 6},
  {id: 158347, book: "John", verse: "6.068", unformatted: "西门彼得回答：“主啊，你有永恒生命的话语，我们还去归从谁呢？", version_id: 6},
  {id: 158348, book: "John", verse: "6.069", unformatted: "我们已经相信，也知道你就是神的那位圣者。”", version_id: 6},
  {id: 158349, book: "John", verse: "6.07", unformatted: "耶稣对他们说：“我不是拣选了你们十二使徒吗？可是你们当中有一个是魔鬼。”", version_id: 6},
  {id: 158350, book: "John", verse: "6.071", unformatted: "耶稣说的是加略人西门的儿子犹大；这个人虽然是十二使徒中的一个，后来却要出卖耶稣。", version_id: 6},
  {id: 158351, book: "John", verse: "7.001", unformatted: "耶稣的弟弟们不信\n这些事以后，耶稣走遍加利利。原来他不愿意在犹太地区往来，因为那些犹太人想要杀害他。", version_id: 6},
  {id: 158352, book: "John", verse: "7.002", unformatted: "犹太人的住棚节快到了，", version_id: 6},
  {id: 158353, book: "John", verse: "7.003", unformatted: "耶稣的弟弟们对他说：“你离开这里到犹太去吧，好让你的门徒们也能看到你所做的事。", version_id: 6},
  {id: 158354, book: "John", verse: "7.004", unformatted: "因为人不会又想要出名，又在隐秘中做事。你既然要做这些事，就把自己显明给世人看吧！”", version_id: 6},
  {id: 158355, book: "John", verse: "7.005", unformatted: "原来连他的弟弟们也不信他。", version_id: 6},
  {id: 158356, book: "John", verse: "7.006", unformatted: "耶稣对他们说：“我的时候还没有来到，而你们的时候随时都方便。", version_id: 6},
  {id: 158357, book: "John", verse: "7.007", unformatted: "世界不能恨你们，却是恨我，因为我见证世界的行为是邪恶的。", version_id: 6},
  {id: 158358, book: "John", verse: "7.008", unformatted: "你们上去过节吧，我现在不上去，因为我的时候还没有到。”", version_id: 6},
  {id: 158359, book: "John", verse: "7.009", unformatted: "说了这些话，耶稣仍然留在加利利地区。", version_id: 6},
  {id: 158360, book: "John", verse: "7.01", unformatted: "耶稣在住棚节\n不过当他的弟弟们上去过节以后，他也上去了，但没有公开，像是悄悄去的。", version_id: 6},
  {id: 158361, book: "John", verse: "7.011", unformatted: "节日期间，那些犹太人搜寻耶稣，说：“那个人在哪里？”", version_id: 6},
  {id: 158362, book: "John", verse: "7.012", unformatted: "人群中有许多人对他议论纷纷；有的说：“他是好人。”也有的说：“不，他是迷惑民众的。”", version_id: 6},
  {id: 158363, book: "John", verse: "7.013", unformatted: "只是没有人公开地讲论他的事，因为怕那些犹太人。", version_id: 6},
  {id: 158364, book: "John", verse: "7.014", unformatted: "可是住棚节已经过了一半，耶稣上圣殿去，开始教导人。", version_id: 6},
  {id: 158365, book: "John", verse: "7.015", unformatted: "那些犹太人感到惊奇，说：“这个人没学过，怎么懂得经文呢？”", version_id: 6},
  {id: 158366, book: "John", verse: "7.016", unformatted: "耶稣回答他们，说：“我的教导不是我自己的，而是那派我来者的。", version_id: 6},
  {id: 158367, book: "John", verse: "7.017", unformatted: "人如果愿意遵行神的旨意，就会明白这教导究竟是从神而来的，还是我凭自己而讲的。", version_id: 6},
  {id: 158368, book: "John", verse: "7.018", unformatted: "凭自己讲话的人，是寻求自己的荣耀；但是，寻求那派他来者之荣耀的，他才是真实的，在他里面没有不义。", version_id: 6},
  {id: 158369, book: "John", verse: "7.019", unformatted: "摩西不是把律法传给你们了吗？你们当中却没有一个人遵行这律法。你们为什么想要杀我呢？”", version_id: 6},
  {id: 158370, book: "John", verse: "7.02", unformatted: "众人回答：“你有鬼魔附身！谁想要杀你？”", version_id: 6},
  {id: 158371, book: "John", verse: "7.021", unformatted: "耶稣对他们说：“我做了一件事，你们都感到惊奇。", version_id: 6},
  {id: 158372, book: "John", verse: "7.022", unformatted: "摩西把割礼传给你们，你们就在安息日给人施行割礼——其实割礼不是从摩西开始的，而是从祖先开始的。", version_id: 6},
  {id: 158373, book: "John", verse: "7.023", unformatted: "既然人在安息日受割礼，是为了不违犯摩西的律法；那么，因为我在安息日使一个人完全康复，你们就对我生气吗？", version_id: 6},
  {id: 158374, book: "John", verse: "7.024", unformatted: "不要按着外表做判断，而要按着公义做判断。”", version_id: 6},
  {id: 158375, book: "John", verse: "7.025", unformatted: "基督的身份\n有些耶路撒冷的人说：“这不是他们想要杀的那个人吗？", version_id: 6},
  {id: 158376, book: "John", verse: "7.026", unformatted: "看，他还在公开讲话，他们也没有对他说什么。难道首领们真的认为这个人就是基督吗？", version_id: 6},
  {id: 158377, book: "John", verse: "7.027", unformatted: "可是，基督出现的时候，没有人会知道他从哪里来；而这个人，我们却很清楚他是从哪里来的。”", version_id: 6},
  {id: 158378, book: "John", verse: "7.028", unformatted: "耶稣在圣殿里教导人，呼喊说：“你们认识我，也知道我是从哪里来的。其实我不是凭自己而来的；派我来的那一位是真实的，你们却不认识他；", version_id: 6},
  {id: 158379, book: "John", verse: "7.029", unformatted: "而我认识他，因为我是从他而来，也是他差派了我。”", version_id: 6},
  {id: 158380, book: "John", verse: "7.03", unformatted: "因此他们想要抓耶稣，只是没有人下手，因为他的时候还没有到。", version_id: 6},
  {id: 158381, book: "John", verse: "7.031", unformatted: "但是人群中有许多人信了耶稣，他们说：“基督来的时候，难道会比这个人行更多的神迹吗？”", version_id: 6},
  {id: 158382, book: "John", verse: "7.032", unformatted: "法利赛人听到了众人私下议论耶稣的这些事。于是祭司长们和法利赛人就派差役们去抓他。", version_id: 6},
  {id: 158383, book: "John", verse: "7.033", unformatted: "耶稣说：“我与你们在一起的时候不多了，我要回到派我来的那一位面前。", version_id: 6},
  {id: 158384, book: "John", verse: "7.034", unformatted: "你们将要寻找我，却找不到；我所在的地方，你们不能去。”", version_id: 6},
  {id: 158385, book: "John", verse: "7.035", unformatted: "那些犹太人彼此问：“这个人要到哪里去，让我们找不到他呢？难道他要往散居在希腊人中的犹太侨民那里，去教导希腊人吗？", version_id: 6},
  {id: 158386, book: "John", verse: "7.036", unformatted: "他所说的‘你们将要寻找我，却找不到；我所在的地方，你们不能去’，这话是什么意思呢？”", version_id: 6},
  {id: 158387, book: "John", verse: "7.037", unformatted: "有关圣灵的应许\n节期的最后一天，也是最盛大的日子，耶稣站起来呼喊说：“如果有人渴了，就到我这里来喝吧！", version_id: 6},
  {id: 158388, book: "John", verse: "7.038", unformatted: "信我的人，就如经上所说的，从他的腹中将要涌流出活水的江河。”", version_id: 6},
  {id: 158389, book: "John", verse: "7.039", unformatted: "耶稣这话是指着信他的人所要领受的圣灵而说的。当时，圣灵还没有临到，因为耶稣还没有得荣耀。", version_id: 6},
  {id: 158390, book: "John", verse: "7.04", unformatted: "群众因耶稣而分歧\n众人听了这话，有些人说：“这个人确实是那位先知！”", version_id: 6},
  {id: 158391, book: "John", verse: "7.041", unformatted: "另有的说：“这个人就是基督。”还有的却说：“难道基督会出自加利利吗？", version_id: 6},
  {id: 158392, book: "John", verse: "7.042", unformatted: "经上不是说，基督是大卫的后裔，是出自大卫的故乡伯利恒吗？”", version_id: 6},
  {id: 158393, book: "John", verse: "7.043", unformatted: "于是众人因耶稣就产生了分裂；", version_id: 6},
  {id: 158394, book: "John", verse: "7.044", unformatted: "其中有些人想要抓他，可是没有人下手。", version_id: 6},
  {id: 158395, book: "John", verse: "7.045", unformatted: "官员因耶稣而争辩\n差役们回到祭司长们和法利赛人那里。那些人问：“为什么没有把他带来？”", version_id: 6},
  {id: 158396, book: "John", verse: "7.046", unformatted: "差役们回答：“从来没有人像这个人那样讲话！”", version_id: 6},
  {id: 158397, book: "John", verse: "7.047", unformatted: "法利赛人说：“难道你们也被迷惑了吗？", version_id: 6},
  {id: 158398, book: "John", verse: "7.048", unformatted: "难道首领或法利赛人中有信他的吗？", version_id: 6},
  {id: 158399, book: "John", verse: "7.049", unformatted: "这不懂律法的民众是被诅咒的！”", version_id: 6},
  {id: 158400, book: "John", verse: "7.05", unformatted: "他们当中有一个人，就是原先来到耶稣那里的尼克迪莫，对他们说：", version_id: 6},
  {id: 158401, book: "John", verse: "7.051", unformatted: "“如果不先听本人的口供，并且了解他做了什么，我们的律法就定不了他的罪，难道不是吗？”", version_id: 6},
  {id: 158402, book: "John", verse: "7.052", unformatted: "他们回答说：“你也不会是加利利人吧？你去查一查看，先知是不会从加利利兴起的！”", version_id: 6},
  {id: 158403, book: "John", verse: "7.053", unformatted: "然后他们每个人都回家去了。", version_id: 6},
  {id: 158404, book: "John", verse: "8.001", unformatted: "耶稣却往橄榄山去。", version_id: 6},
  {id: 158405, book: "John", verse: "8.002", unformatted: "淫妇被赦免\n清晨，耶稣又来到圣殿，全体民众到他那里，他就坐下，开始教导他们。", version_id: 6},
  {id: 158406, book: "John", verse: "8.003", unformatted: "这时，经文士们和法利赛人带来一个在通奸时被抓住的妇人，叫她站在中间。", version_id: 6},
  {id: 158407, book: "John", verse: "8.004", unformatted: "他们对耶稣说：“老师，这妇人是正在通奸的时候被抓住的。", version_id: 6},
  {id: 158408, book: "John", verse: "8.005", unformatted: "摩西在律法上吩咐我们用石头砸死这样的女人，既然如此，你怎么说呢？”", version_id: 6},
  {id: 158409, book: "John", verse: "8.006", unformatted: "他们说这话是要试探耶稣，为要找把柄来控告他。\n耶稣却弯下腰，开始用指头在地上写字。", version_id: 6},
  {id: 158410, book: "John", verse: "8.007", unformatted: "可是当他们不停地追问耶稣的时候，耶稣就直起身来，对他们说：“你们当中谁没有罪，谁就先用石头砸她吧！”", version_id: 6},
  {id: 158411, book: "John", verse: "8.008", unformatted: "然后他又弯下腰去，继续在地上写字。", version_id: 6},
  {id: 158412, book: "John", verse: "8.009", unformatted: "他们听了这话，从年长的开始，一个接一个地走开了。最后，只剩下耶稣和那站在中间的妇人。", version_id: 6},
  {id: 158413, book: "John", verse: "8.01", unformatted: "耶稣直起身来问她：“妇人，他们在哪里？没有人定你的罪吗？”", version_id: 6},
  {id: 158414, book: "John", verse: "8.011", unformatted: "她说：“主啊，没有。”\n耶稣说：“我也不定你的罪。你回去吧！从今以后不要再犯罪了。”", version_id: 6},
  {id: 158415, book: "John", verse: "8.012", unformatted: "世界的光\n耶稣又对众人说：“我就是世界的光。跟从我的人，绝不会在黑暗里行走，却要得到生命的光。”", version_id: 6},
  {id: 158416, book: "John", verse: "8.013", unformatted: "法利赛人对他说：“你为自己做见证，你的见证是无效的。”", version_id: 6},
  {id: 158417, book: "John", verse: "8.014", unformatted: "耶稣回答：“即使我为自己做见证，我的见证也是有效的，因为我知道我从哪里来，往哪里去；而你们却不知道我从哪里来，往哪里去。", version_id: 6},
  {id: 158418, book: "John", verse: "8.015", unformatted: "你们是按人的标准来评断，我却不评断任何人。", version_id: 6},
  {id: 158419, book: "John", verse: "8.016", unformatted: "不过，就算我评断，我的评断也是真实的，因为我不是独自一人，而是有那派我来的父与我同在。", version_id: 6},
  {id: 158420, book: "John", verse: "8.017", unformatted: "连你们的律法上也记载着：两个人的见证才是有效的。", version_id: 6},
  {id: 158421, book: "John", verse: "8.018", unformatted: "我为自己做见证，派我来的父也为我做见证。”", version_id: 6},
  {id: 158422, book: "John", verse: "8.019", unformatted: "他们就问耶稣：“你的‘父’在哪里？”\n耶稣回答：“你们既不认识我，也不认识我的父；如果你们认识我，也就认识我的父。”", version_id: 6},
  {id: 158423, book: "John", verse: "8.02", unformatted: "这些话是耶稣在圣殿的奉献箱附近教导人的时候说的。可是没有人抓他，因为他的时候还没有到。", version_id: 6},
  {id: 158424, book: "John", verse: "8.021", unformatted: "耶稣预言离世\n后来，耶稣又对他们说：“我要去了，你们将要寻找我，并且将死在自己的罪中。我去的地方，你们不能去。”", version_id: 6},
  {id: 158425, book: "John", verse: "8.022", unformatted: "那些犹太人就说：“他说‘我去的地方，你们不能去’，难道他要自杀吗？”", version_id: 6},
  {id: 158426, book: "John", verse: "8.023", unformatted: "耶稣对他们说：“你们从下面来，我从上面来；你们属于这世界，我不属于这世界。", version_id: 6},
  {id: 158427, book: "John", verse: "8.024", unformatted: "所以我对你们说过‘你们将死在自己的罪孽中。’事实上，你们如果不信我就是那一位，就将死在自己的罪孽中。”", version_id: 6},
  {id: 158428, book: "John", verse: "8.025", unformatted: "他们问：“你到底是谁？”\n耶稣说：“我从一开始就告诉你们了。", version_id: 6},
  {id: 158429, book: "John", verse: "8.026", unformatted: "关于你们，我有很多事要说、要评断；不过派我来的那一位是真实的，我从他那里听了这些事，就向世人说出来。”", version_id: 6},
  {id: 158430, book: "John", verse: "8.027", unformatted: "他们不明白耶稣对他们说的是有关父的事。", version_id: 6},
  {id: 158431, book: "John", verse: "8.028", unformatted: "所以耶稣说：“你们举起人子的时候，你们那时就会明白我就是那一位，也会明白我不凭自己做什么，而且我说这些话，也是照着父所教导我的。", version_id: 6},
  {id: 158432, book: "John", verse: "8.029", unformatted: "派我来的那一位与我同在；他没有撇下我，使我独自一人，因为我一直做他所喜悦的事。”", version_id: 6},
  {id: 158433, book: "John", verse: "8.03", unformatted: "真理与自由\n耶稣说这些话的时候，许多人就信了他。", version_id: 6},
  {id: 158434, book: "John", verse: "8.031", unformatted: "于是耶稣对信他的犹太人说：“你们如果住在我的话语中，就真是我的门徒了，", version_id: 6},
  {id: 158435, book: "John", verse: "8.032", unformatted: "并且你们将明白真理，而真理将使你们自由。”", version_id: 6},
  {id: 158436, book: "John", verse: "8.033", unformatted: "他们回答：“我们是亚伯拉罕的后裔，从来没有做过谁的奴仆。你怎么说‘你们将要得自由’呢？”", version_id: 6},
  {id: 158437, book: "John", verse: "8.034", unformatted: "耶稣回答：“我确确实实地告诉你们：所有犯罪的，就是罪的奴隶。", version_id: 6},
  {id: 158438, book: "John", verse: "8.035", unformatted: "奴隶不会永远住在家里，儿子却会永远住在家里。", version_id: 6},
  {id: 158439, book: "John", verse: "8.036", unformatted: "所以，如果子使你们自由，你们就真正自由了。", version_id: 6},
  {id: 158440, book: "John", verse: "8.037", unformatted: "我知道你们是亚伯拉罕的后裔。可是你们想要杀我，因为你们里面容不下我的话语。", version_id: 6},
  {id: 158441, book: "John", verse: "8.038", unformatted: "我所说的，是我从我父那里所看到的；而实际上，你们所做的，是从你们的父那里所听到的。”", version_id: 6},
  {id: 158442, book: "John", verse: "8.039", unformatted: "他们回答说：“亚伯拉罕是我们的父！”\n耶稣说：“如果你们是亚伯拉罕的儿女，就会做亚伯拉罕所做的事。", version_id: 6},
  {id: 158443, book: "John", verse: "8.04", unformatted: "但如今，我把从神那里所听到的真理告诉了你们，你们就想杀我。亚伯拉罕没有做过这种事。", version_id: 6},
  {id: 158444, book: "John", verse: "8.041", unformatted: "你们在做你们的父所做的事。”\n他们回答说：“我们不是由淫乱所生的，我们只有一位父，就是神。”", version_id: 6},
  {id: 158445, book: "John", verse: "8.042", unformatted: "耶稣说：“如果神是你们的父，你们就会爱我，因为我是出于神，并且从神那里来。要知道，我并不是凭自己而来的，而是那一位差派了我。", version_id: 6},
  {id: 158446, book: "John", verse: "8.043", unformatted: "你们为什么不明白我的话呢？这是因为你们听不进我的话语。", version_id: 6},
  {id: 158447, book: "John", verse: "8.044", unformatted: "你们属于你们的父——魔鬼，并且想要随你们父的欲望去做。他从起初就是杀人的，不站在真理之中，因为他里面没有真理。他说虚假之事是出于自己的本性，因为他本来就是个说谎者，并且是说谎者之父。", version_id: 6},
  {id: 158448, book: "John", verse: "8.045", unformatted: "可是因为我讲真理，你们就不相信我！", version_id: 6},
  {id: 158449, book: "John", verse: "8.046", unformatted: "你们当中谁能指证我有罪呢？既然我讲的是真理，你们为什么不相信我呢？", version_id: 6},
  {id: 158450, book: "John", verse: "8.047", unformatted: "属神的人，就听神的话语；你们之所以不听，是因为你们不属于神。”", version_id: 6},
  {id: 158451, book: "John", verse: "8.048", unformatted: "耶稣与亚伯拉罕\n那些犹太人回答说：“我们说你是个撒马利亚人，并且有鬼魔附身，难道说得不对吗？”", version_id: 6},
  {id: 158452, book: "John", verse: "8.049", unformatted: "耶稣回答：“我没有鬼魔附身。我尊重我的父，你们却侮辱我。", version_id: 6},
  {id: 158453, book: "John", verse: "8.05", unformatted: "我不寻求自己的荣耀，但有寻求和判断的那一位。", version_id: 6},
  {id: 158454, book: "John", verse: "8.051", unformatted: "我确确实实地告诉你们：人如果遵守我的话语，就绝不见死亡，直到永远。”", version_id: 6},
  {id: 158455, book: "John", verse: "8.052", unformatted: "犹太人说：“现在我们知道你是有鬼魔附身的。亚伯拉罕死了，先知们也死了，你还说‘人如果遵守我的话语，就绝不会尝到死的滋味，直到永远。’", version_id: 6},
  {id: 158456, book: "John", verse: "8.053", unformatted: "难道你比我们的先祖亚伯拉罕更大吗？他死了，先知们也死了。你把自己当做什么人？”", version_id: 6},
  {id: 158457, book: "John", verse: "8.054", unformatted: "耶稣回答：“如果我荣耀自己，我的荣耀就算不得什么。荣耀我的是我的父，就是你们说‘他是我们的神’的那一位。", version_id: 6},
  {id: 158458, book: "John", verse: "8.055", unformatted: "你们不认识他，我却认识他。如果我说我不认识他，我就成了像你们那样的说谎者。但我认识他，也遵守他的话语。", version_id: 6},
  {id: 158459, book: "John", verse: "8.056", unformatted: "你们的先祖亚伯拉罕因为将要看到我的日子就快乐。他看到了，就欢喜。”", version_id: 6},
  {id: 158460, book: "John", verse: "8.057", unformatted: "犹太人就对他说：“你还不到五十岁，怎么会见过亚伯拉罕？”", version_id: 6},
  {id: 158461, book: "John", verse: "8.058", unformatted: "耶稣对他们说：“我确确实实地告诉你们：亚伯拉罕存在之前，我就存在了。”", version_id: 6},
  {id: 158462, book: "John", verse: "8.059", unformatted: "他们因此拿起石头要砸他。耶稣却被隐藏，离开了圣殿。", version_id: 6},
  {id: 158463, book: "John", verse: "9.001", unformatted: "治愈生来瞎眼的人\n耶稣往前走，看到一个生来就瞎眼的人。", version_id: 6},
  {id: 158464, book: "John", verse: "9.002", unformatted: "他的门徒问他，说：“拉比，这个人生来就瞎眼，是谁犯了罪？是这个人，还是他的父母呢？”", version_id: 6},
  {id: 158465, book: "John", verse: "9.003", unformatted: "耶稣回答：“既不是这个人犯了罪，也不是他的父母犯了罪，而是要让神的作为显明在他身上。", version_id: 6},
  {id: 158466, book: "John", verse: "9.004", unformatted: "趁着白昼，我们必须做那派我来者的工作；黑夜就要到了！那时候就没有人能做工了。", version_id: 6},
  {id: 158467, book: "John", verse: "9.005", unformatted: "我在世上的时候，就是世界的光。”", version_id: 6},
  {id: 158468, book: "John", verse: "9.006", unformatted: "耶稣说了这些话，就吐唾沫在地上，用唾沫和了一点泥，抹在瞎眼之人的眼睛上，", version_id: 6},
  {id: 158469, book: "John", verse: "9.007", unformatted: "然后对他说：“到西罗亚池去洗一洗。”——“西罗亚”翻译出来就是“奉差派”。他去洗了，回来就能看见了。", version_id: 6},
  {id: 158470, book: "John", verse: "9.008", unformatted: "他的邻居和原先看见他讨饭的人就说：“这不是那一向坐着讨饭的人吗？”", version_id: 6},
  {id: 158471, book: "John", verse: "9.009", unformatted: "有的说：“是这个人。”有的说：“不是，只是像他。”\n那个人自己说：“就是我。”", version_id: 6},
  {id: 158472, book: "John", verse: "9.01", unformatted: "他们问他：“那么，你的眼睛是怎么开的呢？”", version_id: 6},
  {id: 158473, book: "John", verse: "9.011", unformatted: "他回答：“那个叫耶稣的人，和了些泥抹在我的眼睛上，吩咐我到西罗亚池子去洗。我去一洗，就得以看见。”", version_id: 6},
  {id: 158474, book: "John", verse: "9.012", unformatted: "他们问：“那个人在哪里？”\n他说：“我不知道。”", version_id: 6},
  {id: 158475, book: "John", verse: "9.013", unformatted: "被治愈之人的见证\n他们把那原来瞎眼的人带到法利赛人那里。", version_id: 6},
  {id: 158476, book: "John", verse: "9.014", unformatted: "耶稣和泥开他眼睛的那一天，正是安息日。", version_id: 6},
  {id: 158477, book: "John", verse: "9.015", unformatted: "法利赛人也再次问他是怎么得以看见的。\n他说：“他把泥抹在我的眼睛上，我一洗，就能看见了。”", version_id: 6},
  {id: 158478, book: "John", verse: "9.016", unformatted: "法利赛人中的有些人说：“那个人不是从神那里来的，因为他不守安息日。”有些人却说：“一个罪人怎么能行这样的神迹呢？”他们之间就产生了分裂。", version_id: 6},
  {id: 158479, book: "John", verse: "9.017", unformatted: "于是他们又问那瞎眼的：“既然他开了你的眼睛，那么你说他是什么人？”\n他说：“是一位先知。”", version_id: 6},
  {id: 158480, book: "John", verse: "9.018", unformatted: "那些犹太人不相信他以前瞎眼而后来得以看见，直等到把他的父母叫来，", version_id: 6},
  {id: 158481, book: "John", verse: "9.019", unformatted: "问他们：“这是你们的儿子吗？你们说他生来是瞎眼的，现在怎么能看见呢？”", version_id: 6},
  {id: 158482, book: "John", verse: "9.02", unformatted: "他的父母回答说：“这个人是我们的儿子，而且生来就瞎眼，这些我们知道。", version_id: 6},
  {id: 158483, book: "John", verse: "9.021", unformatted: "至于他现在怎么能看见，我们就不知道了。是谁开了他的眼睛，我们也不知道。你们问他吧，他已经成年了，他自己会说的。”", version_id: 6},
  {id: 158484, book: "John", verse: "9.022", unformatted: "他的父母说这些话，是因为怕那些犹太人。原来那些犹太人已经说好，如果有人承认耶稣是基督，就要把他赶出会堂。", version_id: 6},
  {id: 158485, book: "John", verse: "9.023", unformatted: "所以他的父母说：“你们问他吧，他已经成年了。”", version_id: 6},
  {id: 158486, book: "John", verse: "9.024", unformatted: "他们再次把那原来瞎眼的人叫来，对他说：“你要把荣耀归给神！我们知道那人是个罪人。”", version_id: 6},
  {id: 158487, book: "John", verse: "9.025", unformatted: "他回答：“他是不是罪人，我不知道。但有一件事我知道：我原来是瞎眼的，现在却能看见了！”", version_id: 6},
  {id: 158488, book: "John", verse: "9.026", unformatted: "他们问：“他对你做了什么？怎么开了你的眼睛？”", version_id: 6},
  {id: 158489, book: "John", verse: "9.027", unformatted: "他回答：“我已经告诉过你们，你们不听，为什么现在又想听呢？难道你们也想成为他的门徒吗？”", version_id: 6},
  {id: 158490, book: "John", verse: "9.028", unformatted: "他们就辱骂他，说：“你才是那个人的门徒！我们是摩西的门徒！", version_id: 6},
  {id: 158491, book: "John", verse: "9.029", unformatted: "我们知道神对摩西说过话，至于这个人，我们就不知道他是从哪里来的！”", version_id: 6},
  {id: 158492, book: "John", verse: "9.03", unformatted: "他回答说：“这就奇怪了！他开了我的眼睛，你们竟然不知道他是从哪里来的！", version_id: 6},
  {id: 158493, book: "John", verse: "9.031", unformatted: "我们知道神是不听罪人的，然而，如果有人敬畏神、遵行他的旨意，神就垂听他。", version_id: 6},
  {id: 158494, book: "John", verse: "9.032", unformatted: "自古以来，没有人听说过谁开了生来就瞎眼之人的眼睛。", version_id: 6},
  {id: 158495, book: "John", verse: "9.033", unformatted: "那个人如果不是从神而来的，他什么也不能做。”", version_id: 6},
  {id: 158496, book: "John", verse: "9.034", unformatted: "他们回答他，说：“你完全是生在罪孽中的，还敢教训我们！”于是把他赶了出去。", version_id: 6},
  {id: 158497, book: "John", verse: "9.035", unformatted: "灵里的瞎眼\n耶稣听说他们把他赶出去了，后来找到他，说：“你信人子吗？”", version_id: 6},
  {id: 158498, book: "John", verse: "9.036", unformatted: "他回答说：“先生，人子是谁呢？愿我能信他。”", version_id: 6},
  {id: 158499, book: "John", verse: "9.037", unformatted: "耶稣说：“你已经看到他了，现在与你说话的，就是那一位。”", version_id: 6},
  {id: 158500, book: "John", verse: "9.038", unformatted: "他说：“主啊，我信！”就向耶稣下拜。", version_id: 6},
  {id: 158501, book: "John", verse: "9.039", unformatted: "耶稣说：“我到这世上来是为了行审判，使看不见的能看见，使看得见的反而瞎了眼。”", version_id: 6},
  {id: 158502, book: "John", verse: "9.04", unformatted: "与耶稣在那里的一些法利赛人听了这些话，就说：“难道我们也是瞎眼的吗？”", version_id: 6},
  {id: 158503, book: "John", verse: "9.041", unformatted: "耶稣对他们说：“如果你们是瞎眼的，就没有罪了。但如今你们说‘我们能看见’，所以你们的罪仍然存在。", version_id: 6},
  {id: 158504, book: "John", verse: "10.001", unformatted: "牧人的比喻\n“我确确实实地告诉你们：那不从门进入羊圈，却从别处爬进去的人，是贼、是强盗。", version_id: 6},
  {id: 158505, book: "John", verse: "10.002", unformatted: "那从门进去的，才是羊的牧人。", version_id: 6},
  {id: 158506, book: "John", verse: "10.003", unformatted: "看门的就给他开门，羊也听他的声音。他一一提名呼唤自己的羊，带领它们出去。", version_id: 6},
  {id: 158507, book: "John", verse: "10.004", unformatted: "他把自己所有的羊带出去的时候，走在它们的前面。羊因为认得他的声音，就跟着他去。", version_id: 6},
  {id: 158508, book: "John", verse: "10.005", unformatted: "羊绝不会跟着陌生人，反而会逃避，因为不认得他的声音。”", version_id: 6},
  {id: 158509, book: "John", verse: "10.006", unformatted: "耶稣对他们说了这个比喻，他们却不明白他说的是什么。", version_id: 6},
  {id: 158510, book: "John", verse: "10.007", unformatted: "好牧人\n因此耶稣又说：“我确确实实地告诉你们：我就是羊的门。", version_id: 6},
  {id: 158511, book: "John", verse: "10.008", unformatted: "所有在我以前来的，是贼、是强盗，因此羊没有听从他们。", version_id: 6},
  {id: 158512, book: "John", verse: "10.009", unformatted: "我就是门。人如果通过我进来，就将得救，而且进进出出，将找到牧草。", version_id: 6},
  {id: 158513, book: "John", verse: "10.01", unformatted: "盗贼来，无非是要偷窃、宰杀、毁灭；而我来是要他们得生命，并且得的更丰盛。", version_id: 6},
  {id: 158514, book: "John", verse: "10.011", unformatted: "“我就是好牧人。好牧人为羊舍弃自己的生命；", version_id: 6},
  {id: 158515, book: "John", verse: "10.012", unformatted: "那雇来的不是牧人，羊也不是自己的，他看见狼来了，撇下羊就逃。这样，狼就抢夺羊，把它们驱散了。", version_id: 6},
  {id: 158516, book: "John", verse: "10.013", unformatted: "这是因为那人是雇来的，并不顾念羊的事。", version_id: 6},
  {id: 158517, book: "John", verse: "10.014", unformatted: "“我就是好牧人。我认识那属于我的，那属于我的也认识我，", version_id: 6},
  {id: 158518, book: "John", verse: "10.015", unformatted: "就像父认识我，我也认识父那样，而且我为羊舍弃自己的生命。", version_id: 6},
  {id: 158519, book: "John", verse: "10.016", unformatted: "我另外有羊，不是这圈里的。我必须把他们带来，他们会听我的声音，并且合成一群，同归一个牧人。", version_id: 6},
  {id: 158520, book: "John", verse: "10.017", unformatted: "父之所以爱我，是因为我舍弃自己的生命，好把它再取回来。", version_id: 6},
  {id: 158521, book: "John", verse: "10.018", unformatted: "没有人夺去我的生命，是我自愿舍弃的。我有权柄舍弃生命，也有权柄再把它取回来。这命令，我已经从我父领受了。”", version_id: 6},
  {id: 158522, book: "John", verse: "10.019", unformatted: "因这些话，犹太人中又产生了分裂。", version_id: 6},
  {id: 158523, book: "John", verse: "10.02", unformatted: "其中有许多人说：“他有鬼魔附身，发疯了！何必听他的呢？”", version_id: 6},
  {id: 158524, book: "John", verse: "10.021", unformatted: "另有些人说：“这些话不是有鬼魔附身的人说的！鬼魔难道能开瞎子的眼睛吗？”", version_id: 6},
  {id: 158525, book: "John", verse: "10.022", unformatted: "耶稣在献殿节\n在耶路撒冷，当时正是献殿节。那时候是冬天。", version_id: 6},
  {id: 158526, book: "John", verse: "10.023", unformatted: "耶稣正在圣殿的所罗门柱廊上走着，", version_id: 6},
  {id: 158527, book: "John", verse: "10.024", unformatted: "犹太人围住了他，说：“你还让我们猜疑多久呢？如果你是基督，就明确地告诉我们吧！”", version_id: 6},
  {id: 158528, book: "John", verse: "10.025", unformatted: "耶稣回答：“我告诉过你们，你们却不相信。我奉我父的名所行的这些事为我做见证；", version_id: 6},
  {id: 158529, book: "John", verse: "10.026", unformatted: "可是你们不信，因为你们不是我的羊。", version_id: 6},
  {id: 158530, book: "John", verse: "10.027", unformatted: "我的羊听我的声音，我也认识他们，他们也跟着我；", version_id: 6},
  {id: 158531, book: "John", verse: "10.028", unformatted: "并且我赐给他们永恒的生命，他们绝不会灭亡，直到永远。谁也不能把他们从我手中夺走。", version_id: 6},
  {id: 158532, book: "John", verse: "10.029", unformatted: "我父，就是把他们赐给我的那一位，他比一切更大。谁也不能把他们从我父的手中夺走。", version_id: 6},
  {id: 158533, book: "John", verse: "10.03", unformatted: "我与父是一体的。”", version_id: 6},
  {id: 158534, book: "John", verse: "10.031", unformatted: "想用石头砸死耶稣\n犹太人又拿起石头要砸死他。", version_id: 6},
  {id: 158535, book: "John", verse: "10.032", unformatted: "耶稣对他们说：“我把很多从父而来的美好之事显给你们看了，你们是为哪一件要用石头砸死我呢？”", version_id: 6},
  {id: 158536, book: "John", verse: "10.033", unformatted: "他们回答：“我们不会为了什么美好之事用石头砸死你，而是为了你说亵渎的话！因为你不过是人，竟然把自己当做神！”", version_id: 6},
  {id: 158537, book: "John", verse: "10.034", unformatted: "耶稣对他们说：“你们的律法上不是写道‘我说你们是神’吗？", version_id: 6},
  {id: 158538, book: "John", verse: "10.035", unformatted: "而经上的话是不能废除的。神既然称那些承受他话语的人为神，", version_id: 6},
  {id: 158539, book: "John", verse: "10.036", unformatted: "那么，难道因为我说了‘我是神的儿子’，你们就对父所分别为圣、并差派到世上来的那一位说‘你说了亵渎的话’吗？", version_id: 6},
  {id: 158540, book: "John", verse: "10.037", unformatted: "如果我没有做我父的事，你们就不该相信我；", version_id: 6},
  {id: 158541, book: "John", verse: "10.038", unformatted: "但如果我做了，你们即使不相信我，也该相信这些事。这样，你们就会知道并体会父在我里面，我也在父里面。”", version_id: 6},
  {id: 158542, book: "John", verse: "10.039", unformatted: "于是他们又试图抓耶稣，他却从他们的手中脱离出来。", version_id: 6},
  {id: 158543, book: "John", verse: "10.04", unformatted: "约旦河对岸有许多人信耶稣\n耶稣又前往约旦河对岸，到约翰当初施洗的地方，住在那里。", version_id: 6},
  {id: 158544, book: "John", verse: "10.041", unformatted: "有许多人来到他那里，说：“虽然约翰没有行过神迹，但约翰所说的有关这个人的事，都是真的。”", version_id: 6},
  {id: 158545, book: "John", verse: "10.042", unformatted: "于是有许多人在那里信了耶稣。", version_id: 6},
  {id: 158546, book: "John", verse: "11.001", unformatted: "拉撒路之死\n有一个叫拉撒路的病人，住在伯大尼，就是玛丽亚和她的姐姐玛妲住的村子。", version_id: 6},
  {id: 158547, book: "John", verse: "11.002", unformatted: "这玛丽亚就是用香液膏抹主，又用自己的头发擦他脚的那一个。是她的弟弟拉撒路患了病。", version_id: 6},
  {id: 158548, book: "John", verse: "11.003", unformatted: "姐妹俩派人到耶稣那里说：“主啊，你看，你所爱的人患了病。”", version_id: 6},
  {id: 158549, book: "John", verse: "11.004", unformatted: "耶稣听了就说：“这病不至于死，而是为了神的荣耀，好使神的儿子由此得荣耀。”", version_id: 6},
  {id: 158550, book: "John", verse: "11.005", unformatted: "耶稣一向爱玛妲和她的妹妹，以及拉撒路。", version_id: 6},
  {id: 158551, book: "John", verse: "11.006", unformatted: "然而当他听说拉撒路患了病，还继续在原来的地方住了两天，", version_id: 6},
  {id: 158552, book: "John", verse: "11.007", unformatted: "此后才对门徒说：“我们再到犹太去吧。”", version_id: 6},
  {id: 158553, book: "John", verse: "11.008", unformatted: "门徒们说：“拉比，近来犹太人想用石头砸死你，你还再到那里去吗？”", version_id: 6},
  {id: 158554, book: "John", verse: "11.009", unformatted: "耶稣回答：“白天不是有十二个小时吗？人如果在白天行走，就不会绊倒，因为他看见这世界的光。", version_id: 6},
  {id: 158555, book: "John", verse: "11.01", unformatted: "但人如果在黑夜里行走，就会绊倒，因为在他里面没有光。”", version_id: 6},
  {id: 158556, book: "John", verse: "11.011", unformatted: "耶稣讲了这些话，然后对他们说：“我们的朋友拉撒路睡了，我要去叫醒他。”", version_id: 6},
  {id: 158557, book: "John", verse: "11.012", unformatted: "门徒们就对他说：“主啊，他如果是睡着了，就有救了！”", version_id: 6},
  {id: 158558, book: "John", verse: "11.013", unformatted: "其实耶稣说的是拉撒路的死，他们却以为他说的是睡觉休息。", version_id: 6},
  {id: 158559, book: "John", verse: "11.014", unformatted: "于是耶稣明确地告诉他们：“拉撒路死了！", version_id: 6},
  {id: 158560, book: "John", verse: "11.015", unformatted: "我为你们欢喜，因为我不在那里，好使你们可以相信。现在我们到他那里去吧。”", version_id: 6},
  {id: 158561, book: "John", verse: "11.016", unformatted: "那叫迪杜马的多马，对其他门徒说：“我们也去吧，让我们与主一起死！”", version_id: 6},
  {id: 158562, book: "John", verse: "11.017", unformatted: "复活与生命\n耶稣到了，知道拉撒路在坟墓里已经有四天了。", version_id: 6},
  {id: 158563, book: "John", verse: "11.018", unformatted: "伯大尼离耶路撒冷不远，约有三公里。", version_id: 6},
  {id: 158564, book: "John", verse: "11.019", unformatted: "许多犹太人到玛妲和玛丽亚那里去，为了她们弟弟的事安慰她们。", version_id: 6},
  {id: 158565, book: "John", verse: "11.02", unformatted: "玛妲一听说耶稣来了，就去迎接他。玛丽亚却仍然坐在家里。", version_id: 6},
  {id: 158566, book: "John", verse: "11.021", unformatted: "玛妲对耶稣说：“主啊，如果你早在这里，我弟弟就不会死了。", version_id: 6},
  {id: 158567, book: "John", verse: "11.022", unformatted: "不过我知道，即使是现在，你无论向神求什么，神都将赐给你的。”", version_id: 6},
  {id: 158568, book: "John", verse: "11.023", unformatted: "耶稣对她说：“你弟弟会复活的。”", version_id: 6},
  {id: 158569, book: "John", verse: "11.024", unformatted: "玛妲说：“我知道在末日复活的时候，他会复活。”", version_id: 6},
  {id: 158570, book: "John", verse: "11.025", unformatted: "耶稣对她说：“我就是复活，我就是生命。信我的人，即使死了，也将要活。", version_id: 6},
  {id: 158571, book: "John", verse: "11.026", unformatted: "所有活着而信我的人绝不会死，直到永远。你信这话吗？”", version_id: 6},
  {id: 158572, book: "John", verse: "11.027", unformatted: "玛妲说：“是的，主啊！我相信你是基督，是神的儿子，就是要到世界上来的那一位。”", version_id: 6},
  {id: 158573, book: "John", verse: "11.028", unformatted: "耶稣流泪\n玛妲说了这话，就回去叫她的妹妹玛丽亚，悄悄地说：“老师来到这里，叫你过去。”", version_id: 6},
  {id: 158574, book: "John", verse: "11.029", unformatted: "玛丽亚一听，就立即起来，往耶稣那里去。", version_id: 6},
  {id: 158575, book: "John", verse: "11.03", unformatted: "当时，耶稣还没有进村子，仍在玛妲迎接他的地方。", version_id: 6},
  {id: 158576, book: "John", verse: "11.031", unformatted: "那些在玛丽亚家里安慰她的犹太人，看见她匆忙起身出去，以为她要到坟墓那里去哭，就跟着她。", version_id: 6},
  {id: 158577, book: "John", verse: "11.032", unformatted: "玛丽亚到了耶稣那里，一看见他，就俯伏在他的脚前，说：“主啊，如果你早在这里，我弟弟就不会死了。”", version_id: 6},
  {id: 158578, book: "John", verse: "11.033", unformatted: "耶稣看见玛丽亚在哭，陪她一起来的犹太人也在哭，就灵里愤怒，内心烦扰。", version_id: 6},
  {id: 158579, book: "John", verse: "11.034", unformatted: "他问：“你们把他安放在哪里？”\n他们回答说：“主啊，请来看。”", version_id: 6},
  {id: 158580, book: "John", verse: "11.035", unformatted: "耶稣流泪了。", version_id: 6},
  {id: 158581, book: "John", verse: "11.036", unformatted: "犹太人就说：“你看，他多么爱拉撒路！”", version_id: 6},
  {id: 158582, book: "John", verse: "11.037", unformatted: "但是其中有些人说：“这个人既然开了瞎子的眼睛，难道就不能使拉撒路不死吗？”", version_id: 6},
  {id: 158583, book: "John", verse: "11.038", unformatted: "使拉撒路复活\n耶稣自己里面再次感到愤怒，来到坟墓那里。那坟墓是个洞穴，有一块石头放置在洞口。", version_id: 6},
  {id: 158584, book: "John", verse: "11.039", unformatted: "耶稣吩咐：“把石头挪开。”\n死者的姐姐玛妲说：“主啊，他已经发臭了，因为是第四天了。”", version_id: 6},
  {id: 158585, book: "John", verse: "11.04", unformatted: "耶稣对她说：“我不是对你说过，你如果信，就会看见神的荣耀吗？”", version_id: 6},
  {id: 158586, book: "John", verse: "11.041", unformatted: "于是他们把石头挪开。耶稣向上举目，说：“父啊，我感谢你，因为你已经垂听了我。", version_id: 6},
  {id: 158587, book: "John", verse: "11.042", unformatted: "我知道你总是在垂听我，但我这么说，是为了这些站在周围的民众，好让他们相信是你差派了我。”", version_id: 6},
  {id: 158588, book: "John", verse: "11.043", unformatted: "耶稣说了这些话，就大声呼唤：“拉撒路，出来！”", version_id: 6},
  {id: 158589, book: "John", verse: "11.044", unformatted: "那死了的人就出来了；手脚缠着布条，脸上包着头巾。耶稣对他们说：“解开他，让他走！”", version_id: 6},
  {id: 158590, book: "John", verse: "11.045", unformatted: "阴谋杀害耶稣\n来到玛丽亚那里的许多犹太人，看到耶稣所做的事，就信了耶稣。", version_id: 6},
  {id: 158591, book: "John", verse: "11.046", unformatted: "然而，其中有些人到法利赛人那里去，向他们报告耶稣所行的事。", version_id: 6},
  {id: 158592, book: "John", verse: "11.047", unformatted: "于是祭司长们和法利赛人召集了议会，说：“这个人行了很多神迹，我们该做什么呢？", version_id: 6},
  {id: 158593, book: "John", verse: "11.048", unformatted: "如果任凭他这样下去，大家都会信他，罗马人就会来，夺取我们的圣地和国家！”", version_id: 6},
  {id: 158594, book: "John", verse: "11.049", unformatted: "其中有一个人叫该亚法，是当年的大祭司，对他们说：“你们什么都不懂！", version_id: 6},
  {id: 158595, book: "John", verse: "11.05", unformatted: "也不想一想，一个人替民众死，免得全民族灭亡，这对你们是有益处的。”", version_id: 6},
  {id: 158596, book: "John", verse: "11.051", unformatted: "他说这话不是出于自己，只因他是当年的大祭司，就预言了耶稣要替全民族而死；", version_id: 6},
  {id: 158597, book: "John", verse: "11.052", unformatted: "而且不仅是替犹太民族死，也是要把神的那些四散的儿女都召集合一。", version_id: 6},
  {id: 158598, book: "John", verse: "11.053", unformatted: "从那天起，他们就商议要把耶稣杀掉。", version_id: 6},
  {id: 158599, book: "John", verse: "11.054", unformatted: "因此，耶稣不再公开地在犹太人当中行事，而是离开那里，到旷野附近的地方，进了一个叫以法莲的城，在那里与门徒们住下了。", version_id: 6},
  {id: 158600, book: "John", verse: "11.055", unformatted: "犹太人的逾越节快到了。许多人从乡下上耶路撒冷去，要在节日前洁净自己。", version_id: 6},
  {id: 158601, book: "John", verse: "11.056", unformatted: "他们寻找耶稣，站在圣殿里彼此谈论说：“你们怎么看，他绝不会来过节吧？”", version_id: 6},
  {id: 158602, book: "John", verse: "11.057", unformatted: "原来，祭司长们和法利赛人早就下令，如果有人知道耶稣在哪里，就要报告，好抓住他。", version_id: 6},
  {id: 158603, book: "John", verse: "12.001", unformatted: "在伯大尼被膏抹\n逾越节前六天，耶稣来到拉撒路所在的伯大尼，就是他使拉撒路从死人中复活的地方。", version_id: 6},
  {id: 158604, book: "John", verse: "12.002", unformatted: "有些人在那里为耶稣预备晚餐，玛妲正伺候着。拉撒路也在与耶稣坐席的人当中。", version_id: 6},
  {id: 158605, book: "John", verse: "12.003", unformatted: "这时候，玛丽亚拿了一斤极其贵重的纯哪哒香液，膏抹耶稣的脚，又用自己的头发擦干，屋子里就充满了香液的气味。", version_id: 6},
  {id: 158606, book: "John", verse: "12.004", unformatted: "他的一个门徒，就是后来要出卖耶稣的那个加略人犹大却说：", version_id: 6},
  {id: 158607, book: "John", verse: "12.005", unformatted: "“为什么不把这香液卖三百个银币去分给穷人呢？”", version_id: 6},
  {id: 158608, book: "John", verse: "12.006", unformatted: "他说这话，并不是因为顾念穷人，而是因为他是个贼。他管钱包，常常拿走其中所存的。", version_id: 6},
  {id: 158609, book: "John", verse: "12.007", unformatted: "耶稣说：“由她吧！她是为我安葬的日子而保存的。", version_id: 6},
  {id: 158610, book: "John", verse: "12.008", unformatted: "要知道，你们总是有穷人与你们在一起，但你们不总是有我。”", version_id: 6},
  {id: 158611, book: "John", verse: "12.009", unformatted: "决定杀害拉撒路\n有一大群犹太人知道耶稣在那里，就来了。他们不仅是为了耶稣的缘故，也是为了看拉撒路，就是耶稣使他从死人中复活的那个人。", version_id: 6},
  {id: 158612, book: "John", verse: "12.01", unformatted: "于是祭司长们就商议，连拉撒路也要杀掉，", version_id: 6},
  {id: 158613, book: "John", verse: "12.011", unformatted: "因为有许多犹太人为了拉撒路的缘故，离开他们，信了耶稣。", version_id: 6},
  {id: 158614, book: "John", verse: "12.012", unformatted: "光荣进圣城\n第二天，一大群上来过节的人听说耶稣要来到耶路撒冷，", version_id: 6},
  {id: 158615, book: "John", verse: "12.013", unformatted: "就拿着棕榈树枝出去迎接他，欢呼说：\n“和散那！\n奉主名而来的那一位，\n以色列的王啊，\n是蒙祝福的！”", version_id: 6},
  {id: 158616, book: "John", verse: "12.014", unformatted: "耶稣找来一头驴驹，骑在上面，正如经上所记：", version_id: 6},
  {id: 158617, book: "John", verse: "12.015", unformatted: "“锡安的女儿啊，不要怕！看哪，你的王骑着驴驹来了！”", version_id: 6},
  {id: 158618, book: "John", verse: "12.016", unformatted: "耶稣的门徒们当初不明白这些事，等到耶稣得了荣耀以后，才想起这些话是指着他写的，人们果然向他做了这些事。", version_id: 6},
  {id: 158619, book: "John", verse: "12.017", unformatted: "当耶稣呼唤拉撒路从坟墓中出来，使他从死人中复活的时候，那些与耶稣在一起的众人就见证了这件事。", version_id: 6},
  {id: 158620, book: "John", verse: "12.018", unformatted: "众人之所以也去迎接他，是因为他们听说他行了这神迹。", version_id: 6},
  {id: 158621, book: "John", verse: "12.019", unformatted: "于是法利赛人互相说：“可见你们真没用！看，这世界都跟随他去了。”", version_id: 6},
  {id: 158622, book: "John", verse: "12.02", unformatted: "预言受难\n当时，上来过节敬拜的人中，有一些希腊人。", version_id: 6},
  {id: 158623, book: "John", verse: "12.021", unformatted: "这些人来到加利利的伯赛达人腓力那里，求他说：“先生，我们想见耶稣。”", version_id: 6},
  {id: 158624, book: "John", verse: "12.022", unformatted: "腓力去告诉安得烈，安得烈和腓力就去告诉耶稣。", version_id: 6},
  {id: 158625, book: "John", verse: "12.023", unformatted: "耶稣回答他们，说：“人子得荣耀的时候到了！", version_id: 6},
  {id: 158626, book: "John", verse: "12.024", unformatted: "我确确实实地告诉你们：一粒麦子如果不落在地里死去，它仍然是一粒；如果死了，就结出很多子粒来。", version_id: 6},
  {id: 158627, book: "John", verse: "12.025", unformatted: "爱惜自己生命的，就失去生命；在这世上恨恶自己生命的，将保全生命到永生。", version_id: 6},
  {id: 158628, book: "John", verse: "12.026", unformatted: "一个人如果要服事我，就当跟从我；我在哪里，我的仆人也将要在哪里。如果有人服事我，父将尊重他。", version_id: 6},
  {id: 158629, book: "John", verse: "12.027", unformatted: "“现在我的灵魂烦扰。我该说什么呢？说‘父啊，救我脱离这时刻’吗？但我正是为了这时刻而来的。", version_id: 6},
  {id: 158630, book: "John", verse: "12.028", unformatted: "父啊，愿你荣耀你的名！”\n这时候，有声音从天上传来：“我已经荣耀了我的名，还要再荣耀！”", version_id: 6},
  {id: 158631, book: "John", verse: "12.029", unformatted: "站在那里的众人听见了，就说：“打雷了。”另有人说：“是天使在对他说话。”", version_id: 6},
  {id: 158632, book: "John", verse: "12.03", unformatted: "耶稣就回答说：“这声音不是为我，而是为你们来的。", version_id: 6},
  {id: 158633, book: "John", verse: "12.031", unformatted: "现在，是这世界受审判的时候了；现在，世界的那掌权者要被赶出去了。", version_id: 6},
  {id: 158634, book: "John", verse: "12.032", unformatted: "至于我，当我从地上被举起来的时候，就会吸引所有的人归向我。”", version_id: 6},
  {id: 158635, book: "John", verse: "12.033", unformatted: "耶稣说这话是指自己将要怎样死。", version_id: 6},
  {id: 158636, book: "John", verse: "12.034", unformatted: "众人回答他：“我们从律法中听到，基督是永远长存的。你怎么说‘人子必须被举起来’呢？这‘人子’到底是谁？”", version_id: 6},
  {id: 158637, book: "John", verse: "12.035", unformatted: "耶稣说：“光在你们当中的时候不多了。要趁着有光的时候行走，免得黑暗抓住你们！因为在黑暗里行走的人，不知道自己往哪里去。", version_id: 6},
  {id: 158638, book: "John", verse: "12.036", unformatted: "你们要趁着有光的时候信这光，好使你们成为光明的儿女。”耶稣说了这些话，就离开他们被隐藏了。", version_id: 6},
  {id: 158639, book: "John", verse: "12.037", unformatted: "应验以赛亚的预言\n耶稣虽然在他们面前行了这么多神迹，他们仍然不信他。", version_id: 6},
  {id: 158640, book: "John", verse: "12.038", unformatted: "这是为要应验先知以赛亚所说的话：\n“主啊，我们所传的，有谁相信了？\n主的膀臂，向谁显示了？”", version_id: 6},
  {id: 158641, book: "John", verse: "12.039", unformatted: "他们之所以不能相信，是因为以赛亚又说过：", version_id: 6},
  {id: 158642, book: "John", verse: "12.04", unformatted: "“神使他们瞎了眼，\n硬了心，\n免得他们眼睛看见，\n心里明白，\n回转过来，\n我就使他们痊愈。”", version_id: 6},
  {id: 158643, book: "John", verse: "12.041", unformatted: "以赛亚说了这些话，是因为看见了耶稣的荣耀，就讲论他的事。", version_id: 6},
  {id: 158644, book: "John", verse: "12.042", unformatted: "不过甚至是首领当中也有许多信耶稣的。但是为了法利赛人的缘故，他们不敢承认，免得被赶出会堂，", version_id: 6},
  {id: 158645, book: "John", verse: "12.043", unformatted: "这是因为他们喜爱人的荣耀，胜过喜爱神的荣耀。", version_id: 6},
  {id: 158646, book: "John", verse: "12.044", unformatted: "耶稣宣告使命\n耶稣喊着说：“信我的人，不是信我，而是信派我来的那一位。", version_id: 6},
  {id: 158647, book: "John", verse: "12.045", unformatted: "谁看见我，就是看见派我来的那一位。", version_id: 6},
  {id: 158648, book: "John", verse: "12.046", unformatted: "我就是光。我来到世上，好让所有信我的人不住在黑暗里。", version_id: 6},
  {id: 158649, book: "John", verse: "12.047", unformatted: "如果有人听了我的话却不遵守，我不定他的罪；因为我来，不是为了定世人的罪，而是为了拯救世人。", version_id: 6},
  {id: 158650, book: "John", verse: "12.048", unformatted: "拒绝我，又不接受我话的人，自有定他罪的：我所讲的话语，在末日要定他的罪。", version_id: 6},
  {id: 158651, book: "John", verse: "12.049", unformatted: "这是因为我没有凭自己讲话，而是派我来的父给了我命令，要我说什么和讲什么。", version_id: 6},
  {id: 158652, book: "John", verse: "12.05", unformatted: "我知道他的命令就是永恒的生命。所以我所讲的，正是照着父所告诉我的来讲的。”", version_id: 6},
  {id: 158653, book: "John", verse: "13.001", unformatted: "耶稣为门徒洗脚\n逾越节前，耶稣知道他离开这世界回到父那里去的时候到了。他一直爱世界上那些属自己的人，并且爱他们到底。", version_id: 6},
  {id: 158654, book: "John", verse: "13.002", unformatted: "晚餐的时候，魔鬼已经把出卖耶稣的念头放在加略人西门的儿子犹大的心里。", version_id: 6},
  {id: 158655, book: "John", verse: "13.003", unformatted: "耶稣知道父把一切交在自己手里，并且知道自己是从神那里来，又要回到神那里去。", version_id: 6},
  {id: 158656, book: "John", verse: "13.004", unformatted: "他从席位上起身，脱了外衣，拿起一条布巾束在自己腰上，", version_id: 6},
  {id: 158657, book: "John", verse: "13.005", unformatted: "然后把水倒进盆里，开始洗门徒们的脚，又用束在腰上的布巾擦干。", version_id: 6},
  {id: 158658, book: "John", verse: "13.006", unformatted: "他来到西门彼得面前，彼得对他说：“主啊，你洗我的脚吗？”", version_id: 6},
  {id: 158659, book: "John", verse: "13.007", unformatted: "耶稣回答说：“我所做的，你现在不理解，不过事后就会明白。”", version_id: 6},
  {id: 158660, book: "John", verse: "13.008", unformatted: "彼得说：“你绝不可洗我的脚，永远不可！”\n耶稣说：“如果我不洗你，你就与我没有份了。”", version_id: 6},
  {id: 158661, book: "John", verse: "13.009", unformatted: "西门彼得说：“主啊！不单我的脚，连我的手、我的头也要洗！”", version_id: 6},
  {id: 158662, book: "John", verse: "13.01", unformatted: "耶稣说：“洗过澡的人，除了脚以外，没有需要洗的；他是完全干净的。你们是干净的，但不是每个人都干净。”", version_id: 6},
  {id: 158663, book: "John", verse: "13.011", unformatted: "原来，耶稣知道要出卖他的是谁，所以说“你们不是每个人都干净。”", version_id: 6},
  {id: 158664, book: "John", verse: "13.012", unformatted: "彼此洗脚的意义\n耶稣洗完了他们的脚以后，穿上外衣，又坐下来，对他们说：“你们明白我为你们做的事吗？", version_id: 6},
  {id: 158665, book: "John", verse: "13.013", unformatted: "你们叫我‘老师’，称呼我‘主’。你们说得对，因为我就是那一位。", version_id: 6},
  {id: 158666, book: "John", verse: "13.014", unformatted: "因此，如果我——你们的主、你们的老师，还洗你们的脚，那么你们也应该彼此洗脚。", version_id: 6},
  {id: 158667, book: "John", verse: "13.015", unformatted: "事实上，我给你们做了榜样，好让你们也照着我为你们所做的去做。", version_id: 6},
  {id: 158668, book: "John", verse: "13.016", unformatted: "“我确确实实地告诉你们：没有一个奴仆大过他的主人，也没有一个被派的大过派他的人。", version_id: 6},
  {id: 158669, book: "John", verse: "13.017", unformatted: "你们既然明白这些事，如果去做，就蒙福了。", version_id: 6},
  {id: 158670, book: "John", verse: "13.018", unformatted: "我不是针对你们每个人而说的。我了解我所拣选的每个人，但这是为要应验经上的话：‘那吃我饼的人，竟抬脚踢我。’", version_id: 6},
  {id: 158671, book: "John", verse: "13.019", unformatted: "“如今，在事情发生以前，我先告诉你们，好让你们在事情发生的时候，能相信我就是那一位。", version_id: 6},
  {id: 158672, book: "John", verse: "13.02", unformatted: "我确确实实地告诉你们：那接受我所派的人，就是接受我；接受我的，就是接受派我来的那一位。”", version_id: 6},
  {id: 158673, book: "John", verse: "13.021", unformatted: "预言犹大卖主\n说完这些话，耶稣灵里烦扰，就指名说：“我确确实实地告诉你们：你们当中有一个人要出卖我。”", version_id: 6},
  {id: 158674, book: "John", verse: "13.022", unformatted: "门徒彼此相看，不知道耶稣说的是谁。", version_id: 6},
  {id: 158675, book: "John", verse: "13.023", unformatted: "他的门徒当中，有一个挨着耶稣坐着，是耶稣所爱的。", version_id: 6},
  {id: 158676, book: "John", verse: "13.024", unformatted: "西门彼得就向他点头示意，要他问耶稣所讲的人到底是谁。", version_id: 6},
  {id: 158677, book: "John", verse: "13.025", unformatted: "于是那门徒贴近耶稣，问他：“主啊，是谁呢？”", version_id: 6},
  {id: 158678, book: "John", verse: "13.026", unformatted: "耶稣回答：“我蘸一块饼给谁，就是谁。”接着，他蘸了一块饼拿给加略人西门的儿子犹大。", version_id: 6},
  {id: 158679, book: "John", verse: "13.027", unformatted: "犹大一接那块饼，撒旦就进了他里面。于是耶稣对他说：“你要做的，就快一点做吧。”", version_id: 6},
  {id: 158680, book: "John", verse: "13.028", unformatted: "在座的人，没有一个明白耶稣为什么对他说这话。", version_id: 6},
  {id: 158681, book: "John", verse: "13.029", unformatted: "因为犹大管钱包，有些人以为耶稣对他说，去买我们过节需要的东西，或是要他给穷人什么东西。", version_id: 6},
  {id: 158682, book: "John", verse: "13.03", unformatted: "犹大接了那块饼，就立刻出去。那时候是夜间了。", version_id: 6},
  {id: 158683, book: "John", verse: "13.031", unformatted: "新的命令\n犹大出去以后，耶稣就说：“现在人子得了荣耀，神藉着人子也得了荣耀。", version_id: 6},
  {id: 158684, book: "John", verse: "13.032", unformatted: "神既然藉着人子得了荣耀，神也将藉着自己荣耀人子，而且立刻就要荣耀他。", version_id: 6},
  {id: 158685, book: "John", verse: "13.033", unformatted: "“孩子们哪，我与你们在一起的时候不多了。你们将寻找我，而且就像我曾经告诉过那些犹太人一样，现在我也告诉你们：我去的地方，你们不能去。", version_id: 6},
  {id: 158686, book: "John", verse: "13.034", unformatted: "“我给你们一条新的命令：就是要你们彼此相爱，就如我爱你们，为要使你们也彼此相爱。", version_id: 6},
  {id: 158687, book: "John", verse: "13.035", unformatted: "如果你们彼此之间有了爱，众人从这一点就会知道你们是我的门徒了。”", version_id: 6},
  {id: 158688, book: "John", verse: "13.036", unformatted: "预言彼得不认主\n西门彼得问：“主啊，你要到哪里去呢？”\n耶稣回答：“我去的地方，你现在不能跟着去，但以后会跟着去。”", version_id: 6},
  {id: 158689, book: "John", verse: "13.037", unformatted: "彼得说：“主啊，为什么我现在就不能跟随你呢？为了你，我愿意舍弃自己的生命！”", version_id: 6},
  {id: 158690, book: "John", verse: "13.038", unformatted: "耶稣回答：“你愿意为我舍命吗？我确确实实地告诉你：鸡叫以前，你必定三次不认我。", version_id: 6},
  {id: 158691, book: "John", verse: "14.001", unformatted: "道路、真理、生命\n“你们心里不要愁烦，要信神，也要信我。", version_id: 6},
  {id: 158692, book: "John", verse: "14.002", unformatted: "在我父家里有很多住处。如果没有，我怎么会告诉你们说，我去是要为你们预备地方呢？", version_id: 6},
  {id: 158693, book: "John", verse: "14.003", unformatted: "我如果去为你们预备了地方，就会再来，接你们到我那里，好使我在哪里，你们也能在哪里。", version_id: 6},
  {id: 158694, book: "John", verse: "14.004", unformatted: "我往哪里去，你们知道；那条路，你们也知道。”", version_id: 6},
  {id: 158695, book: "John", verse: "14.005", unformatted: "多马说：“主啊，我们不知道你要到哪里去，怎么能知道那条路呢？”", version_id: 6},
  {id: 158696, book: "John", verse: "14.006", unformatted: "耶稣说：“我就是道路、真理、生命。要不是藉着我，没有人能到父那里去。", version_id: 6},
  {id: 158697, book: "John", verse: "14.007", unformatted: "耶稣显明父\n“如果你们认识我，也就会认识我的父。从今以后，你们就认识他，并且已经看到他了。”", version_id: 6},
  {id: 158698, book: "John", verse: "14.008", unformatted: "腓力说：“主啊，请给我们看看父，我们就满足了。”", version_id: 6},
  {id: 158699, book: "John", verse: "14.009", unformatted: "耶稣回答说：“腓力，我与你们在一起这么久了，你还不认识我吗？一个人看到了我，就是看到了父。你怎么还说‘请给我们看看父’呢？", version_id: 6},
  {id: 158700, book: "John", verse: "14.01", unformatted: "难道你不相信我就在父里面，父也在我里面吗？我对你们说的话，不是凭自己说的，而是住在我里面的父，在做他的工作。", version_id: 6},
  {id: 158701, book: "John", verse: "14.011", unformatted: "你们当相信我：我在父里面，父也在我里面。即使不信，也当因我所做的事而相信。", version_id: 6},
  {id: 158702, book: "John", verse: "14.012", unformatted: "奉耶稣的名祈求\n“我确确实实地告诉你们：我所做的事，信我的人也要做，而且要做比这些更大的事。这是因为我就要到父那里去了。", version_id: 6},
  {id: 158703, book: "John", verse: "14.013", unformatted: "你们奉我的名无论求什么，我将行这事，好使父藉着子得荣耀。", version_id: 6},
  {id: 158704, book: "John", verse: "14.014", unformatted: "如果你们奉我的名向我求什么，我将成就。", version_id: 6},
  {id: 158705, book: "John", verse: "14.015", unformatted: "应许赐圣灵\n“你们如果爱我，就会遵守我的命令。", version_id: 6},
  {id: 158706, book: "John", verse: "14.016", unformatted: "我也要向父祈求，他就将赐给你们另一位慰助者，让他与你们在一起，直到永远。", version_id: 6},
  {id: 158707, book: "John", verse: "14.017", unformatted: "他就是真理的灵，是世界不能接受的，因为世界看不到他，也不认识他。而你们认识他，因为他与你们同在，并且要在你们里面。", version_id: 6},
  {id: 158708, book: "John", verse: "14.018", unformatted: "我不会撇下你们为孤儿，我要回到你们这里来。", version_id: 6},
  {id: 158709, book: "John", verse: "14.019", unformatted: "父、子、圣灵\n“再过一会儿，世人不再看到我了，而你们将看到我。因为我活着，你们也将活着。", version_id: 6},
  {id: 158710, book: "John", verse: "14.02", unformatted: "在那一天，你们就会明白：我在我父里面，你们在我里面，我也在你们里面。", version_id: 6},
  {id: 158711, book: "John", verse: "14.021", unformatted: "那领受我命令并遵守的人，就是爱我的。爱我的会蒙我父所爱，我也会爱他，并且会向他显明我自己。”", version_id: 6},
  {id: 158712, book: "John", verse: "14.022", unformatted: "不是加略人的那个犹大问耶稣：“主啊，怎么会这样呢？你为什么要向我们显明自己，而不向世人显明呢？”", version_id: 6},
  {id: 158713, book: "John", verse: "14.023", unformatted: "耶稣回答说：“如果有人爱我，他就会遵守我的话语，我父也会爱他，而且我们将要来到他那里，并且在他那里安置我们的住处。", version_id: 6},
  {id: 158714, book: "John", verse: "14.024", unformatted: "不爱我的人，就不遵守我的话语。事实上，你们所听见的话语，不是我的，而是那派我来的父的话语。", version_id: 6},
  {id: 158715, book: "John", verse: "14.025", unformatted: "“我还与你们在一起的时候，已经把这些事告诉了你们。", version_id: 6},
  {id: 158716, book: "John", verse: "14.026", unformatted: "但那位慰助者，就是父以我的名所要派遣的圣灵，将要把一切的事教导你们，并且让你们想起我对你们说过的一切。", version_id: 6},
  {id: 158717, book: "John", verse: "14.027", unformatted: "耶稣赐平安\n“我把平安留给你们，我把我的平安赐给你们。我给你们的，不像世界所给的。你们心里不要愁烦，也不要胆怯。", version_id: 6},
  {id: 158718, book: "John", verse: "14.028", unformatted: "你们听过我对你们说：‘我去了，还要回到你们这里来。’如果你们爱我，就会为了我到父那里去而感到欢喜。这是因为父比我更大。", version_id: 6},
  {id: 158719, book: "John", verse: "14.029", unformatted: "如今，在事情发生以前我已经告诉你们了，好让你们在事情发生的时候能相信。", version_id: 6},
  {id: 158720, book: "John", verse: "14.03", unformatted: "我不再对你们多说了。要知道，世界的那掌权者就要来了，而他对我毫无作用。", version_id: 6},
  {id: 158721, book: "John", verse: "14.031", unformatted: "不过我照着父的吩咐如此去做，是为要世界知道我爱父。\n“起来，我们走吧！", version_id: 6},
  {id: 158722, book: "John", verse: "15.001", unformatted: "葡萄树与枝子\n“我就是真葡萄树，我父是园丁。", version_id: 6},
  {id: 158723, book: "John", verse: "15.002", unformatted: "所有属于我而不结果子的枝条，他就除掉；而所有结果子的，他就修剪干净，好让它结出更多的果子。", version_id: 6},
  {id: 158724, book: "John", verse: "15.003", unformatted: "藉着我给你们讲的话语，你们已经干净了。", version_id: 6},
  {id: 158725, book: "John", verse: "15.004", unformatted: "你们当住在我里面，我就住在你们里面。枝条如果不留在葡萄树上，就不能由自己结出果子；照样，你们如果不住在我里面，你们也是如此。", version_id: 6},
  {id: 158726, book: "John", verse: "15.005", unformatted: "“我就是葡萄树，你们是枝条。那住在我里面、我也在他里面的，他才结出很多果子，因为没有我，你们什么也不能做。", version_id: 6},
  {id: 158727, book: "John", verse: "15.006", unformatted: "一个人如果不住在我里面，就像枝条被丢在外面枯干了，然后被收集扔进火里焚烧。", version_id: 6},
  {id: 158728, book: "John", verse: "15.007", unformatted: "你们如果住在我里面，而我的话也住在你们里面，那么，凡是你们所愿的，当祈求，就将为你们成就。", version_id: 6},
  {id: 158729, book: "John", verse: "15.008", unformatted: "你们结出很多果子，就表明是我的门徒了；从这一点我父也就得荣耀。", version_id: 6},
  {id: 158730, book: "John", verse: "15.009", unformatted: "彼此相爱\n“正如父爱我，我也爱你们。你们当住在我的爱里。", version_id: 6},
  {id: 158731, book: "John", verse: "15.01", unformatted: "如果你们遵守我的命令，就会住在我的爱里；就像我遵守了我父的命令，住在他的爱里那样。", version_id: 6},
  {id: 158732, book: "John", verse: "15.011", unformatted: "“我把这些事告诉了你们，是要我的喜乐在你们里面，并且使你们的喜乐得以满足。", version_id: 6},
  {id: 158733, book: "John", verse: "15.012", unformatted: "这是我的命令：你们要彼此相爱，就像我爱了你们那样。", version_id: 6},
  {id: 158734, book: "John", verse: "15.013", unformatted: "一个人为他的朋友舍弃自己的生命，人的爱没有比这更大的了。", version_id: 6},
  {id: 158735, book: "John", verse: "15.014", unformatted: "你们如果遵行我所吩咐的事，你们就是我的朋友了。", version_id: 6},
  {id: 158736, book: "John", verse: "15.015", unformatted: "我不再称你们为奴仆，因为奴仆不知道主人所做的事。我却称你们为朋友，因为我把从我父所听到的一切，都让你们明白了。", version_id: 6},
  {id: 158737, book: "John", verse: "15.016", unformatted: "不是你们拣选了我，而是我拣选了你们，并指派了你们，好使你们去，让你们结出果子，而且使你们的果子长存。这样，你们奉我的名无论向父求什么，他就会赐给你们。", version_id: 6},
  {id: 158738, book: "John", verse: "15.017", unformatted: "我将这些事吩咐你们，是要让你们彼此相爱。", version_id: 6},
  {id: 158739, book: "John", verse: "15.018", unformatted: "预言受迫害\n“这世界如果恨你们，你们应当知道，世界在恨你们之前已经恨我了。", version_id: 6},
  {id: 158740, book: "John", verse: "15.019", unformatted: "如果你们属于这世界，世界会把你们做为属自己的来爱；但是，因为你们不属于这世界，而是我从世界中拣选了你们，所以世界就恨你们。", version_id: 6},
  {id: 158741, book: "John", verse: "15.02", unformatted: "你们当记住我对你们说过的话：“没有一个奴仆大过他的主人。”他们既然逼迫了我，也会逼迫你们；如果他们遵守了我的话，也就会遵守你们的话。", version_id: 6},
  {id: 158742, book: "John", verse: "15.021", unformatted: "但是，因我名的缘故，他们会对你们做这一切事，因为他们不认识派我来的那一位。", version_id: 6},
  {id: 158743, book: "John", verse: "15.022", unformatted: "如果我没有来，也没有教导他们，他们就没有罪；但如今，他们的罪无可推诿了。", version_id: 6},
  {id: 158744, book: "John", verse: "15.023", unformatted: "恨我的，也恨我的父。", version_id: 6},
  {id: 158745, book: "John", verse: "15.024", unformatted: "如果我没有在他们当中做别人从未做过的事，他们就没有罪；但如今他们虽然看到了，还是恨我和我的父。", version_id: 6},
  {id: 158746, book: "John", verse: "15.025", unformatted: "不过这是为要应验他们律法上所记载的话：‘他们无缘无故地恨我。’", version_id: 6},
  {id: 158747, book: "John", verse: "15.026", unformatted: "做见证与被拒绝\n“我将从父那里派遣慰助者来，他就是从父而来的真理的灵。他来的时候，将为我做见证。", version_id: 6},
  {id: 158748, book: "John", verse: "15.027", unformatted: "并且你们也要做见证，因为你们从一开始就与我在一起。", version_id: 6},
  {id: 158749, book: "John", verse: "16.001", unformatted: "“我把这些事告诉了你们，免得你们被绊倒。", version_id: 6},
  {id: 158750, book: "John", verse: "16.002", unformatted: "他们将要把你们赶出会堂。不但如此，时候就要到了！那杀你们的人，都会以为自己是在事奉神。", version_id: 6},
  {id: 158751, book: "John", verse: "16.003", unformatted: "他们做这些事，是因为不认识父，也不认识我。", version_id: 6},
  {id: 158752, book: "John", verse: "16.004", unformatted: "不过我把这些事告诉了你们，是为了当事情发生的时候，你们会记起我对你们说过了。我当初没有告诉你们这些事，是因为我一直与你们在一起。", version_id: 6},
  {id: 158753, book: "John", verse: "16.005", unformatted: "圣灵的工作\n“现在我要回到派我来的那一位面前，你们当中却没有人问我：‘你要到哪里去？’", version_id: 6},
  {id: 158754, book: "John", verse: "16.006", unformatted: "只因为我对你们说了这些话，你们心里就充满了忧伤。", version_id: 6},
  {id: 158755, book: "John", verse: "16.007", unformatted: "但我把真相告诉你们：我离去对你们是有益处的，因为我如果不去，慰助者就不会到你们这里来；如果我去了，我就会派遣他到你们这里来。", version_id: 6},
  {id: 158756, book: "John", verse: "16.008", unformatted: "他来了，要在关于罪、关于义、关于审判的事上，使世界知罪。", version_id: 6},
  {id: 158757, book: "John", verse: "16.009", unformatted: "关于罪，是因为世界不信我；", version_id: 6},
  {id: 158758, book: "John", verse: "16.01", unformatted: "关于义，是因为我到父那里去，而你们不再看到我了；", version_id: 6},
  {id: 158759, book: "John", verse: "16.011", unformatted: "关于审判，是因为世界的那掌权者已经被定罪了。", version_id: 6},
  {id: 158760, book: "John", verse: "16.012", unformatted: "“我还有很多事要告诉你们，可是你们现在担当不了，", version_id: 6},
  {id: 158761, book: "John", verse: "16.013", unformatted: "不过当那位真理的灵来了，他就会引导你们进入一切真理。那时，他不会凭着自己说话，只把所听到的说出来，并且把那些就要来的事传讲给你们。", version_id: 6},
  {id: 158762, book: "John", verse: "16.014", unformatted: "他将荣耀我，因为他将从我领受，并传讲给你们。", version_id: 6},
  {id: 158763, book: "John", verse: "16.015", unformatted: "父所拥有的一切，都是我的。所以我说，圣灵将从我领受，并传讲给你们。", version_id: 6},
  {id: 158764, book: "John", verse: "16.016", unformatted: "忧伤变成喜乐\n“过一会儿，你们就不再看到我了；再过一会儿，你们又将见到我。”", version_id: 6},
  {id: 158765, book: "John", verse: "16.017", unformatted: "于是他的门徒当中有的彼此问：“他对我们说‘过一会儿，你们就看不到我了；再过一会儿，你们又将见到我’，还说‘是因为我要到父那里去’，这是什么意思呢？”", version_id: 6},
  {id: 158766, book: "John", verse: "16.018", unformatted: "他们又说：“他说的‘过一会儿’是什么意思呢？我们不明白他在说什么。”", version_id: 6},
  {id: 158767, book: "John", verse: "16.019", unformatted: "耶稣知道他们想要问他，就对他们说：“我刚才说‘过一会儿，你们就看不到我了；再过一会儿，你们又将见到我’，你们就为了这话彼此议论吗？", version_id: 6},
  {id: 158768, book: "John", verse: "16.02", unformatted: "“我确确实实地告诉你们：你们将会痛哭、哀号，这世界反而会喜乐；你们将会忧伤，然而你们的忧伤要变成喜乐。", version_id: 6},
  {id: 158769, book: "John", verse: "16.021", unformatted: "妇人生产的时候会有忧虑，因为她的时候到了；但是生了孩子以后，她就因着有人出生到世上的喜乐，而不再记住那痛苦了。", version_id: 6},
  {id: 158770, book: "John", verse: "16.022", unformatted: "这样，你们现在虽然也有忧虑，但是我会再见到你们，你们心里将喜乐，并且没有人能夺去你们的喜乐。", version_id: 6},
  {id: 158771, book: "John", verse: "16.023", unformatted: "在那一天，你们什么也不会问我了。\n“我确确实实地告诉你们：你们奉我的名无论向父求什么，他都将赐给你们。", version_id: 6},
  {id: 158772, book: "John", verse: "16.024", unformatted: "直到如今，你们没有奉我的名求过什么。你们祈求吧！就会得到，好使你们的喜乐得以满足。", version_id: 6},
  {id: 158773, book: "John", verse: "16.025", unformatted: "耶稣胜了世界\n“我用比喻对你们说了这些话。但是时候就要到了！那时我不再用比喻对你们说话，而是要明确地告诉你们有关父的事。", version_id: 6},
  {id: 158774, book: "John", verse: "16.026", unformatted: "在那一天，你们会奉我的名祈求，而且我并不是说我要替你们向父祈求。", version_id: 6},
  {id: 158775, book: "John", verse: "16.027", unformatted: "要知道，父自己爱你们，因为你们已经爱我，并且相信我是从神而来的。", version_id: 6},
  {id: 158776, book: "John", verse: "16.028", unformatted: "我从父而出，来到这世界；又要离开这世界，到父那里去。”", version_id: 6},
  {id: 158777, book: "John", verse: "16.029", unformatted: "他的门徒们说：“看，如今你说得明明白白，而不说比喻了。", version_id: 6},
  {id: 158778, book: "John", verse: "16.03", unformatted: "现在我们已经明白你知道一切，也不需要任何人问你，从这一点我们就相信你是从神而来的。”", version_id: 6},
  {id: 158779, book: "John", verse: "16.031", unformatted: "耶稣对他们说：“你们现在相信吗？", version_id: 6},
  {id: 158780, book: "John", verse: "16.032", unformatted: "看哪，时候就要到了，而且已经到了！你们要被驱散，每个人到自己的地方，留下我一个人。其实我不是独自一人，因为父与我同在。", version_id: 6},
  {id: 158781, book: "John", verse: "16.033", unformatted: "我把这些事告诉了你们，好让你们在我里面有平安。在世上你们有患难，然而你们要鼓起勇气！我已经胜过了这世界。”", version_id: 6},
  {id: 158782, book: "John", verse: "17.001", unformatted: "耶稣为自己祷告\n耶稣说完了这些话，就举目望天，说：\n“父啊，\n时候到了！\n愿你荣耀你的儿子，\n好让儿子也荣耀你，", version_id: 6},
  {id: 158783, book: "John", verse: "17.002", unformatted: "正如你赐给他权柄管辖所有的人，\n好让他把永恒的生命给与一切你所赐给他的人。", version_id: 6},
  {id: 158784, book: "John", verse: "17.003", unformatted: "认识你——独一的真神，\n并认识你所差派的耶稣基督，\n这就是永恒的生命。", version_id: 6},
  {id: 158785, book: "John", verse: "17.004", unformatted: "我完成了你交给我该做的事，\n因而在地上荣耀了你。", version_id: 6},
  {id: 158786, book: "John", verse: "17.005", unformatted: "父啊，\n现在愿你用那荣耀，与你自己一起荣耀我，\n那荣耀是世界存在以前我与你一起就有的。", version_id: 6},
  {id: 158787, book: "John", verse: "17.006", unformatted: "耶稣为门徒祷告\n“你从世上所赐给我的人，\n我已经把你的名向他们显明了。\n他们是你的，\n你把他们赐给了我，\n他们也遵守了你的话语。", version_id: 6},
  {id: 158788, book: "John", verse: "17.007", unformatted: "现在他们已经知道，\n你所赐给我的一切都是从你而来的，", version_id: 6},
  {id: 158789, book: "John", verse: "17.008", unformatted: "因为你所赐给我的话语，\n我已经给了他们。\n他们领受了，\n也确实知道我是从你而来的，\n并且相信是你差派了我。", version_id: 6},
  {id: 158790, book: "John", verse: "17.009", unformatted: "我为他们祈求。\n我不是为世人，\n而是为你所赐给我的人祈求，\n因为他们是你的。", version_id: 6},
  {id: 158791, book: "John", verse: "17.01", unformatted: "一切属于我的都是你的，\n属于你的也是我的，\n并且我在他们里面得了荣耀。", version_id: 6},
  {id: 158792, book: "John", verse: "17.011", unformatted: "今后我不在这世界上了，\n而他们在这世界上，\n我却要到你那里去。\n圣父啊，\n愿你奉你的名，\n就是奉你赐给我的名保守他们，\n使他们合而为一，\n就像我们那样。", version_id: 6},
  {id: 158793, book: "John", verse: "17.012", unformatted: "我与他们在一起的时候，\n我奉你的名，\n就是奉你赐给我的名\n保守了他们，也护卫了他们。\n他们当中除了那灭亡之子以外，\n没有一个人失落。\n这是为要应验经上的话。", version_id: 6},
  {id: 158794, book: "John", verse: "17.013", unformatted: "但如今我就要到你那里去了。\n我在世上说这些话，\n是要让他们里面充满我的喜乐。", version_id: 6},
  {id: 158795, book: "John", verse: "17.014", unformatted: "我把你的话语给了他们，\n而世界恨他们，\n因为他们不属于这世界，\n就像我不属于这世界那样。", version_id: 6},
  {id: 158796, book: "John", verse: "17.015", unformatted: "我不是求你带他们离开这世界，\n而是求你保守他们脱离那恶者。", version_id: 6},
  {id: 158797, book: "John", verse: "17.016", unformatted: "他们不属于这世界，\n就像我不属于这世界那样。", version_id: 6},
  {id: 158798, book: "John", verse: "17.017", unformatted: "愿你在真理中使他们分别为圣，\n你的话语就是真理。", version_id: 6},
  {id: 158799, book: "John", verse: "17.018", unformatted: "正如你差派我到世界上来，\n我也差派他们到世界上去。", version_id: 6},
  {id: 158800, book: "John", verse: "17.019", unformatted: "我为了他们，自己分别为圣，\n好使他们也能在真理中被分别为圣。", version_id: 6},
  {id: 158801, book: "John", verse: "17.02", unformatted: "耶稣为所有信徒祷告\n“我不仅为他们祈求，\n还为那些因他们的话而信我的人祈求，", version_id: 6},
  {id: 158802, book: "John", verse: "17.021", unformatted: "愿他们都合而为一。\n父啊，\n正如你在我里面，我也在你里面，\n愿他们也在我们里面，\n好让世界相信是你差派了我。", version_id: 6},
  {id: 158803, book: "John", verse: "17.022", unformatted: "你赐给我的荣耀，\n我已经给了他们，\n使他们合而为一，\n就像我们是一体的。", version_id: 6},
  {id: 158804, book: "John", verse: "17.023", unformatted: "我在他们里面，你在我里面，\n愿他们能完全合一，\n好让世界知道是你差派了我，\n也知道你爱他们，\n就像爱我那样。", version_id: 6},
  {id: 158805, book: "John", verse: "17.024", unformatted: "父啊，\n我在哪里，愿你所赐给我的人也能与我在哪里，\n好让他们能看见你所赐给我的荣耀，\n因为你在创世以前就爱我了。", version_id: 6},
  {id: 158806, book: "John", verse: "17.025", unformatted: "公义的父啊，\n虽然世界不认识你，我却认识你，\n这些人也知道是你差派了我。", version_id: 6},
  {id: 158807, book: "John", verse: "17.026", unformatted: "我将你的名向他们显明了，\n并且还要显明。\n这样，你爱我的爱就能在他们里面，\n我也能在他们里面。”", version_id: 6},
  {id: 158808, book: "John", verse: "18.001", unformatted: "耶稣被捕\n耶稣说了这些话，就与他的门徒们一起出去，过了汲沦溪谷。在那里有一个园子，耶稣和他的门徒们进去了。", version_id: 6},
  {id: 158809, book: "John", verse: "18.002", unformatted: "出卖耶稣的犹大也知道那地方，因为耶稣经常在那里与他的门徒们一同聚集。", version_id: 6},
  {id: 158810, book: "John", verse: "18.003", unformatted: "于是犹大领着一队士兵，还有祭司长们和法利赛人的差役们，带着灯笼、火把、兵器来到那里。", version_id: 6},
  {id: 158811, book: "John", verse: "18.004", unformatted: "耶稣知道就要临到自己的一切，于是上前对他们说：“你们找谁？”", version_id: 6},
  {id: 158812, book: "John", verse: "18.005", unformatted: "他们回答：“拿撒勒人耶稣。”\n耶稣说：“我就是。”\n出卖他的犹大也与他们站在一起。", version_id: 6},
  {id: 158813, book: "John", verse: "18.006", unformatted: "耶稣一说“我就是”，他们就往后退，倒在地上。", version_id: 6},
  {id: 158814, book: "John", verse: "18.007", unformatted: "于是耶稣又问：“你们找谁？”\n他们说：“拿撒勒人耶稣。”", version_id: 6},
  {id: 158815, book: "John", verse: "18.008", unformatted: "耶稣回答：“我已经告诉你们‘我就是’。因此，你们如果找我，就让这些人走吧！”", version_id: 6},
  {id: 158816, book: "John", verse: "18.009", unformatted: "这是为要应验耶稣说过的话：“你所赐给我的人，我一个也没有丢失。”", version_id: 6},
  {id: 158817, book: "John", verse: "18.01", unformatted: "西门彼得带着一把刀。他拔出刀来，向大祭司的奴仆砍去，削掉了他的右耳。那奴仆的名字是马勒古。", version_id: 6},
  {id: 158818, book: "John", verse: "18.011", unformatted: "耶稣就对彼得说：“把刀收回鞘里！父所赐给我的杯，我难道能不喝吗？”", version_id: 6},
  {id: 158819, book: "John", verse: "18.012", unformatted: "耶稣被带到亚纳面前\n于是那队士兵和千夫长，以及犹太人的差役们，抓住耶稣，把他捆绑起来，", version_id: 6},
  {id: 158820, book: "John", verse: "18.013", unformatted: "先带到亚纳面前，因为亚纳是当年的大祭司该亚法的岳父。", version_id: 6},
  {id: 158821, book: "John", verse: "18.014", unformatted: "该亚法就是曾经向那些犹太人建议“一个人替民众死是有益处的”那个人。", version_id: 6},
  {id: 158822, book: "John", verse: "18.015", unformatted: "彼得不认主\n这时，西门彼得和另一个门徒跟着耶稣。那门徒是大祭司所认识的，所以跟着耶稣进了大祭司的院子；", version_id: 6},
  {id: 158823, book: "John", verse: "18.016", unformatted: "彼得却站在门外。后来，大祭司认识的那个门徒出来，对看门的说了一声，就把彼得带了进去。", version_id: 6},
  {id: 158824, book: "John", verse: "18.017", unformatted: "那看门的女仆问彼得：“你不也是这个人的门徒吗？”\n彼得说：“我不是。”", version_id: 6},
  {id: 158825, book: "John", verse: "18.018", unformatted: "因为当时天气寒冷，奴仆和差役们生了一堆炭火，站着取暖。彼得也与他们站在一起取暖。", version_id: 6},
  {id: 158826, book: "John", verse: "18.019", unformatted: "在亚纳面前受审\n那时，大祭司盘问耶稣有关他的门徒和他教导的事。", version_id: 6},
  {id: 158827, book: "John", verse: "18.02", unformatted: "耶稣回答他：“我一向公开地对世人讲话。我总是在会堂和圣殿，就是所有犹太人聚集的地方教导人。我没有在隐秘中讲过什么。", version_id: 6},
  {id: 158828, book: "John", verse: "18.021", unformatted: "你为什么问我呢？你可以去问那些听见过的人，我对他们讲了什么。你看，我所说的，那些人都知道。”", version_id: 6},
  {id: 158829, book: "John", verse: "18.022", unformatted: "耶稣说了这些话，站在旁边的一个差役就打了他一巴掌，说：“你这样回答大祭司吗？”", version_id: 6},
  {id: 158830, book: "John", verse: "18.023", unformatted: "耶稣回答他：“如果我说得不对，你可以见证哪里不对；如果说得对，你为什么打我呢？”", version_id: 6},
  {id: 158831, book: "John", verse: "18.024", unformatted: "那时候耶稣仍然被捆绑，亚纳把他押送到大祭司该亚法那里去。", version_id: 6},
  {id: 158832, book: "John", verse: "18.025", unformatted: "彼得再次不认主\n西门彼得正站着取暖，有人对他说：“你不也是他的门徒吗？”\n彼得否认说：“我不是。”", version_id: 6},
  {id: 158833, book: "John", verse: "18.026", unformatted: "大祭司的一个奴仆，就是被彼得削掉耳朵的那个人的亲戚，说：“我不是看见你与他一起在园子里吗？”", version_id: 6},
  {id: 158834, book: "John", verse: "18.027", unformatted: "彼得再次否认。立时，鸡就叫了。", version_id: 6},
  {id: 158835, book: "John", verse: "18.028", unformatted: "在彼拉多面前受审\n他们把耶稣从该亚法那里带到总督府，那时是清晨。他们为了不被污秽，能吃逾越节的晚餐，就没有进入总督府。", version_id: 6},
  {id: 158836, book: "John", verse: "18.029", unformatted: "于是彼拉多出来，到他们那里，说：“你们提出什么来控告这个人？”", version_id: 6},
  {id: 158837, book: "John", verse: "18.03", unformatted: "他们回答说：“这个人如果不是作恶的，我们就不会把他交给你了。”", version_id: 6},
  {id: 158838, book: "John", verse: "18.031", unformatted: "彼拉多对他们说：“你们自己把他带走，按照你们的律法审判他吧。”\n那些犹太人回答说：“我们不可以处死人。”", version_id: 6},
  {id: 158839, book: "John", verse: "18.032", unformatted: "这是为要应验耶稣说过的话，就是他指明自己将要怎样死的那些话。", version_id: 6},
  {id: 158840, book: "John", verse: "18.033", unformatted: "彼拉多又进了总督府，把耶稣叫来，问他：“你是犹太人的王吗？”", version_id: 6},
  {id: 158841, book: "John", verse: "18.034", unformatted: "耶稣回答：“这话是你自己说的，还是别人告诉过你关于我的事呢？”", version_id: 6},
  {id: 158842, book: "John", verse: "18.035", unformatted: "彼拉多说：“难道我是犹太人吗？是你本国的人和祭司长们把你交给了我。你到底做了什么？”", version_id: 6},
  {id: 158843, book: "John", verse: "18.036", unformatted: "耶稣回答：“我的国不属于这个世界。如果我的国属于这个世界，我的仆人们早就会争战，不让我被交给那些犹太人。但如今，我的国不在这里。”", version_id: 6},
  {id: 158844, book: "John", verse: "18.037", unformatted: "于是彼拉多问：“那么，你就是王？”\n耶稣回答：“是你说的，我就是王。我为此而生，也为此而来到这世界，是要为真理做见证。凡是属于真理的人，都听我的声音。”", version_id: 6},
  {id: 158845, book: "John", verse: "18.038", unformatted: "彼拉多说：“什么是真理？”\n耶稣或巴拉巴\n说了这话，他又出去对犹太人说：“我查不出他有什么罪。", version_id: 6},
  {id: 158846, book: "John", verse: "18.039", unformatted: "不过你们有一个惯例：每逢逾越节，我可以给你们释放一个囚犯。那么，你们愿意我给你们释放这犹太人的王吗？”", version_id: 6},
  {id: 158847, book: "John", verse: "18.04", unformatted: "他们就再次大声喊叫说：“不要这个人！要巴拉巴！”这巴拉巴其实是个强盗。", version_id: 6},
  {id: 158848, book: "John", verse: "19.001", unformatted: "受鞭打与戏弄\n于是彼拉多吩咐把耶稣带走，鞭打了。", version_id: 6},
  {id: 158849, book: "John", verse: "19.002", unformatted: "士兵们用荆棘编了冠冕，戴在耶稣的头上，又给他披上紫色袍子。", version_id: 6},
  {id: 158850, book: "John", verse: "19.003", unformatted: "他们不断地来到他面前说：“万岁，犹太人的王！”又不住地用手掌打他。", version_id: 6},
  {id: 158851, book: "John", verse: "19.004", unformatted: "彼拉多再次出来对犹太人说：“看！我把他带出来给你们，是要你们知道，我查不出他有什么罪。”", version_id: 6},
  {id: 158852, book: "John", verse: "19.005", unformatted: "彼拉多判耶稣死刑\n这时候，耶稣出来了，他戴着荆棘冠冕，披着紫色袍子。彼拉多对他们说：“看，这个人！”", version_id: 6},
  {id: 158853, book: "John", verse: "19.006", unformatted: "祭司长们和差役们一看见耶稣，就喊叫说：“钉上十字架！钉上十字架！”\n彼拉多对他们说：“你们自己把他带走钉上十字架！要知道，我查不出他有罪。”", version_id: 6},
  {id: 158854, book: "John", verse: "19.007", unformatted: "那些犹太人回答：“我们有律法，按照这律法，他是应该死的，因为他把自己当做神的儿子。”", version_id: 6},
  {id: 158855, book: "John", verse: "19.008", unformatted: "彼拉多一听这话，就更加惧怕了，", version_id: 6},
  {id: 158856, book: "John", verse: "19.009", unformatted: "又进了总督府，问耶稣：“你到底是从哪里来的？”耶稣却没有回答他。", version_id: 6},
  {id: 158857, book: "John", verse: "19.01", unformatted: "彼拉多就说：“你不与我说话吗？难道你不知道我有权释放你，也有权把你钉上十字架吗？”", version_id: 6},
  {id: 158858, book: "John", verse: "19.011", unformatted: "耶稣回答：“如果权柄不是从上面赐给你的，你就对我没有任何权柄。所以，把我交给你的人有更大的罪。”", version_id: 6},
  {id: 158859, book: "John", verse: "19.012", unformatted: "由此，彼拉多就想要释放耶稣。可是犹太人喊叫说：“你如果释放这个人，就不是凯撒的朋友了。所有把自己当做王的，就是反对凯撒的！”", version_id: 6},
  {id: 158860, book: "John", verse: "19.013", unformatted: "彼拉多听了这话，就把耶稣带到外面，并在一个叫“铺石台”的地方坐上了审判席。“铺石台”希伯来语叫做“伽巴达”。", version_id: 6},
  {id: 158861, book: "John", verse: "19.014", unformatted: "那天是逾越节的预备日，大约在中午十二点，彼拉多对犹太人说：“看，你们的王！”", version_id: 6},
  {id: 158862, book: "John", verse: "19.015", unformatted: "他们就大声喊叫：“除掉他！除掉他！把他钉上十字架！”\n彼拉多问：“我可以把你们的王钉上十字架吗？”\n祭司长们回答：“除了凯撒，我们没有王！”", version_id: 6},
  {id: 158863, book: "John", verse: "19.016", unformatted: "于是彼拉多把耶稣交给他们去钉上十字架。\n耶稣被钉十字架\n他们就把耶稣带走了。", version_id: 6},
  {id: 158864, book: "John", verse: "19.017", unformatted: "耶稣自己背着十字架出来，前往一个叫“骷髅地”的地方，希伯来语叫做“各各他”。", version_id: 6},
  {id: 158865, book: "John", verse: "19.018", unformatted: "在那里，他们把耶稣钉上十字架，还把另外两个人与耶稣一起钉上十字架，这边一个，那边一个，耶稣在中间。", version_id: 6},
  {id: 158866, book: "John", verse: "19.019", unformatted: "彼拉多还写了一个牌子，挂在十字架上，写的是：“拿撒勒人耶稣，犹太人的王。”", version_id: 6},
  {id: 158867, book: "John", verse: "19.02", unformatted: "许多犹太人都读了这牌子，因为耶稣被钉十字架的地方离城不远，而且牌子是用希伯来文、拉丁文、希腊文写的。", version_id: 6},
  {id: 158868, book: "John", verse: "19.021", unformatted: "犹太人的祭司长们对彼拉多说：“请不要写‘犹太人的王’，而要写‘这个人说：我是犹太人的王。’”", version_id: 6},
  {id: 158869, book: "John", verse: "19.022", unformatted: "彼拉多回答：“我所写的，我已经写了。”", version_id: 6},
  {id: 158870, book: "John", verse: "19.023", unformatted: "士兵们将耶稣钉上十字架以后，把他的衣服拿来分成四份，每人一份。他们又拿了里衣，这件里衣没有接缝，从上到下由整片织成。", version_id: 6},
  {id: 158871, book: "John", verse: "19.024", unformatted: "他们彼此说：“不要把它撕开，让我们抽签，看它是谁的。”这是为要应验经上的话：“他们分了我的衣服，又为我的衣裳抽签。”士兵们果然做了这些事。", version_id: 6},
  {id: 158872, book: "John", verse: "19.025", unformatted: "儿子与母亲\n在耶稣的十字架旁边，站着他的母亲、他母亲的妹妹、克罗帕的妻子玛丽亚，以及茉大拉的玛丽亚。", version_id: 6},
  {id: 158873, book: "John", verse: "19.026", unformatted: "耶稣看到母亲和他所爱的那门徒站在旁边，就对母亲说：“母亲，看哪，你的儿子！”", version_id: 6},
  {id: 158874, book: "John", verse: "19.027", unformatted: "然后对那门徒说：“看哪，你的母亲！”从那时候起，那门徒就把耶稣的母亲接到自己的家里。", version_id: 6},
  {id: 158875, book: "John", verse: "19.028", unformatted: "耶稣完成使命\n这事以后，耶稣知道一切都已经完成了，为要应验经上的话，就说：“我渴了。”", version_id: 6},
  {id: 158876, book: "John", verse: "19.029", unformatted: "那里放着一个装满酸酒的容器。他们就把蘸满了酸酒的海绵套在牛膝草上，送到他的嘴边。", version_id: 6},
  {id: 158877, book: "John", verse: "19.03", unformatted: "耶稣尝了酸酒后，就说：“成了！”然后垂下头，交出了灵魂。", version_id: 6},
  {id: 158878, book: "John", verse: "19.031", unformatted: "肋旁被扎\n因为那天是预备日，又因为那个安息日是个大节日，那些犹太人为了不让尸体在安息日留在十字架上，就向彼拉多请求，好把他们的腿打断，把尸体拿走。", version_id: 6},
  {id: 158879, book: "John", verse: "19.032", unformatted: "士兵们就来，把与耶稣一起被钉十字架的头一个人的腿打断，又把另一个人的也打断了。", version_id: 6},
  {id: 158880, book: "John", verse: "19.033", unformatted: "可是，当他们来到耶稣那里，看见他已经死了，就没有打断他的腿。", version_id: 6},
  {id: 158881, book: "John", verse: "19.034", unformatted: "不过有一个士兵用长矛刺入耶稣的肋旁，立刻有血和水流了出来。", version_id: 6},
  {id: 158882, book: "John", verse: "19.035", unformatted: "看见这事的那人做了见证——他的见证是真实的，并且他知道他所说的是真实的——这是为了要你们也信。", version_id: 6},
  {id: 158883, book: "John", verse: "19.036", unformatted: "事实上，这些事发生，是为要应验经上的话：“他的骨头，一根也不被折断。”", version_id: 6},
  {id: 158884, book: "John", verse: "19.037", unformatted: "另有一段经文也说：“他们将看见自己所刺的那一位。”", version_id: 6},
  {id: 158885, book: "John", verse: "19.038", unformatted: "安葬\n这些事以后，亚利马太的约瑟请求彼拉多，要领取耶稣的遗体。约瑟也是耶稣的门徒，不过是暗暗地做门徒，因为他怕那些犹太人。彼拉多准许了，他就来把耶稣的遗体领去。", version_id: 6},
  {id: 158886, book: "John", verse: "19.039", unformatted: "当初在夜里来到耶稣那里的尼克迪莫也来了。他带着没药和沉香的混合香料，大约三十公斤。", version_id: 6},
  {id: 158887, book: "John", verse: "19.04", unformatted: "他们领了耶稣的遗体，按犹太人预备葬礼的规矩，用细麻布条和香料把遗体缠裹起来。", version_id: 6},
  {id: 158888, book: "John", verse: "19.041", unformatted: "耶稣被钉十字架的地方有一个园子，园子里有一座新墓穴，从来没有安放过人。", version_id: 6},
  {id: 158889, book: "John", verse: "19.042", unformatted: "因为那天是犹太人的预备日，那墓穴又在附近，他们就把耶稣安放在那里。", version_id: 6},
  {id: 158890, book: "John", verse: "20.001", unformatted: "空墓穴\n在一周头一天的清晨，天还黑的时候，茉大拉的玛丽亚就来到墓穴那里，看见那石头已经从墓穴被挪开了。", version_id: 6},
  {id: 158891, book: "John", verse: "20.002", unformatted: "她就跑到西门彼得和耶稣所爱的那另一个门徒那里去，对他们说：“他们从墓穴里把主挪走了，我们不知道他们把他放在哪里！”", version_id: 6},
  {id: 158892, book: "John", verse: "20.003", unformatted: "于是彼得和那另一个门徒就出来，往墓穴去。", version_id: 6},
  {id: 158893, book: "John", verse: "20.004", unformatted: "两个人一起跑，那另一个门徒跑得比彼得快一点，先到了墓穴。", version_id: 6},
  {id: 158894, book: "John", verse: "20.005", unformatted: "他弯腰去看，见那细麻布条放在那里，不过他没有进去。", version_id: 6},
  {id: 158895, book: "John", verse: "20.006", unformatted: "西门彼得跟着他，也到了，并且进了墓穴，看见那细麻布条放在那里，", version_id: 6},
  {id: 158896, book: "John", verse: "20.007", unformatted: "也看见原来在耶稣头上的那块头巾，没有与那细麻布条放在一起，被卷了起来，分开放在一边。", version_id: 6},
  {id: 158897, book: "John", verse: "20.008", unformatted: "这时候，先来到墓穴的那另一个门徒也进去了。他看见了，就信了。", version_id: 6},
  {id: 158898, book: "John", verse: "20.009", unformatted: "原来，他们从来没有明白过经上所说“他必须从死人中复活”的那句话。", version_id: 6},
  {id: 158899, book: "John", verse: "20.01", unformatted: "于是那些门徒就离开，回到他们的住处去了。", version_id: 6},
  {id: 158900, book: "John", verse: "20.011", unformatted: "向茉大拉的玛丽亚显现\n玛丽亚却站在墓穴外面哭。她哭着，弯腰往墓穴里看，", version_id: 6},
  {id: 158901, book: "John", verse: "20.012", unformatted: "看见两位身穿白衣的天使，坐在原来安放耶稣遗体的地方，一位在头的地方，一位在脚的地方。", version_id: 6},
  {id: 158902, book: "John", verse: "20.013", unformatted: "天使们问她：“妇人，你为什么哭？”\n她说：“因为他们把我的主挪走了，我不知道他们把他放在哪里。”", version_id: 6},
  {id: 158903, book: "John", verse: "20.014", unformatted: "说了这些话，她转过身来，看见耶稣站在那里，却没有认出他就是耶稣。", version_id: 6},
  {id: 158904, book: "John", verse: "20.015", unformatted: "耶稣说：“妇人，你为什么哭呢？你在找谁？”\n玛丽亚以为他是园丁，就对他说：“先生，如果是你把他抬走的，就请告诉我，你把他放在哪里了，我好把他取回来。”", version_id: 6},
  {id: 158905, book: "John", verse: "20.016", unformatted: "耶稣对她说：“玛丽亚！”\n她转过身，用希伯来语对耶稣说：“拉波尼！”——这意思是“老师”。", version_id: 6},
  {id: 158906, book: "John", verse: "20.017", unformatted: "耶稣说：“不要碰我，因为我还没有上到父那里。你到我弟兄们那里去告诉他们：我要上到我的父那里，他也是你们的父；我要上到我的神那里，他也是你们的神。”", version_id: 6},
  {id: 158907, book: "John", verse: "20.018", unformatted: "茉大拉的玛丽亚就去告诉门徒们：“我看到主了！”她又把耶稣对她说的这些话告诉他们。", version_id: 6},
  {id: 158908, book: "John", verse: "20.019", unformatted: "向门徒们显现\n那日，也就是一周的头一天，傍晚的时候，门徒们所在地方的门户已经关上了，因为他们怕那些犹太人。耶稣出现了，站在他们当中，对他们说：“愿你们平安！”", version_id: 6},
  {id: 158909, book: "John", verse: "20.02", unformatted: "说了这话，他把手和肋旁给他们看。门徒们看见了主，就欢喜。", version_id: 6},
  {id: 158910, book: "John", verse: "20.021", unformatted: "耶稣又对他们说：“愿你们平安！正如父差派了我，我也要派遣你们。”", version_id: 6},
  {id: 158911, book: "John", verse: "20.022", unformatted: "说了这话，耶稣向他们吹了一口气，说：“领受圣灵吧！", version_id: 6},
  {id: 158912, book: "John", verse: "20.023", unformatted: "你们赦免谁的罪，谁的罪就已经被赦免了；你们留下谁的罪，谁的罪就已经被留下了。”", version_id: 6},
  {id: 158913, book: "John", verse: "20.024", unformatted: "向多马显现\n当耶稣出现的时候，十二使徒中那叫迪杜马的多马没有与他们在一起。", version_id: 6},
  {id: 158914, book: "John", verse: "20.025", unformatted: "后来，其他的门徒就告诉他：“我们看到主了！”\n他却说：“我如果不看到他手上的钉痕，把指头放进那钉痕，再把手放进他的肋旁，我就绝不相信！”", version_id: 6},
  {id: 158915, book: "John", verse: "20.026", unformatted: "八天后，耶稣的门徒们又在屋里，多马也与他们在一起。门户已经关上了，耶稣却出现，站在他们当中说：“愿你们平安！”", version_id: 6},
  {id: 158916, book: "John", verse: "20.027", unformatted: "然后他对多马说：“把你的指头伸到这里来，看看我的手吧。伸出你的手来，放进我的肋旁吧。不要不信，而要信！”", version_id: 6},
  {id: 158917, book: "John", verse: "20.028", unformatted: "多马回答耶稣，说：“我的主，我的神！”", version_id: 6},
  {id: 158918, book: "John", verse: "20.029", unformatted: "耶稣对他说：“你因为看到了我才信吗？那没有看到就信的人，是蒙福的。”", version_id: 6},
  {id: 158919, book: "John", verse: "20.03", unformatted: "本书的目的\n事实上，耶稣在他的门徒们面前还行了很多别的神迹，没有记在这部书里。", version_id: 6},
  {id: 158920, book: "John", verse: "20.031", unformatted: "但这些事被记下来，是要你们相信耶稣就是基督、是神的儿子；并且使你们因着信，就奉他的名有生命。", version_id: 6},
  {id: 158921, book: "John", verse: "21.001", unformatted: "向门徒们第三次显现\n这些事以后，耶稣在太巴列湖边又向门徒们显明了自己。他是这样显明自己的：", version_id: 6},
  {id: 158922, book: "John", verse: "21.002", unformatted: "当时，西门彼得、那叫迪杜马的多马、加利利的迦拿人拿旦业、西庇太的两个儿子、还有他的另外两位门徒，正在一起。", version_id: 6},
  {id: 158923, book: "John", verse: "21.003", unformatted: "西门彼得对大家说：“我去打鱼。”\n他们说：“我们也和你一起去。”他们就出去，上了船。可是那一夜，他们什么也没有捕到。", version_id: 6},
  {id: 158924, book: "John", verse: "21.004", unformatted: "天快亮的时候，耶稣站在岸上，只是门徒们没有认出他就是耶稣。", version_id: 6},
  {id: 158925, book: "John", verse: "21.005", unformatted: "耶稣对他们说：“孩子们，你们没有鱼吧？”\n他们回答：“没有。”", version_id: 6},
  {id: 158926, book: "John", verse: "21.006", unformatted: "耶稣说：“你们把网撒在船的右边，就会捕到。”他们就把网撒下，竟拉不上来，因为鱼很多。", version_id: 6},
  {id: 158927, book: "John", verse: "21.007", unformatted: "耶稣所爱的那门徒对彼得说：“那是主！”\n当时，西门彼得赤着身子，一听说是主，就把外衣系上，跳进湖里。", version_id: 6},
  {id: 158928, book: "John", verse: "21.008", unformatted: "其他在船上的门徒，拖着满网的鱼过来了。他们离岸不远，约有九十公尺。", version_id: 6},
  {id: 158929, book: "John", verse: "21.009", unformatted: "他们一上岸，就看见有一堆炭火，上面放着鱼，还有饼。", version_id: 6},
  {id: 158930, book: "John", verse: "21.01", unformatted: "耶稣说：“把你们刚捕到的鱼，拿几条过来。”", version_id: 6},
  {id: 158931, book: "John", verse: "21.011", unformatted: "西门彼得就上去，把网拖到岸上，满网大鱼，共一百五十三条。虽然有这么多鱼，网却没有撑破。", version_id: 6},
  {id: 158932, book: "John", verse: "21.012", unformatted: "耶稣对他们说：“来吃早饭吧。”但没有一个门徒敢问他：“你是谁？”因为他们知道是主。", version_id: 6},
  {id: 158933, book: "John", verse: "21.013", unformatted: "耶稣过来，拿起饼分给他们，把鱼也如此分了。", version_id: 6},
  {id: 158934, book: "John", verse: "21.014", unformatted: "耶稣从死人中复活后，这已经是他第三次向门徒们显现了。", version_id: 6},
  {id: 158935, book: "John", verse: "21.015", unformatted: "耶稣与彼得\n大家吃完早饭后，耶稣就问西门彼得：“约翰的儿子西门，你爱我胜过这些吗？”\n彼得说：“是的，主啊！你知道我喜爱你。”\n耶稣对他说：“喂养我的小羊。”", version_id: 6},
  {id: 158936, book: "John", verse: "21.016", unformatted: "耶稣第二次又问他：“约翰的儿子西门，你爱我吗？”\n彼得回答说：“是的，主啊！你知道我喜爱你。”\n耶稣对他说：“牧养我的羊。”", version_id: 6},
  {id: 158937, book: "John", verse: "21.017", unformatted: "耶稣第三次问他：“约翰的儿子西门，你喜爱我吗？”\n彼得感到忧伤，因为耶稣第三次是对他说：“你喜爱我吗？”彼得说：“主啊，你知道一切，你知道我喜爱你。”\n耶稣对他说：“喂养我的羊。", version_id: 6},
  {id: 158938, book: "John", verse: "21.018", unformatted: "我确确实实地告诉你：你年轻的时候，自己束上腰带，到你愿意去的地方；但你年老的时候，要伸出双手，别人要把你束上，带你到不愿意去的地方。”", version_id: 6},
  {id: 158939, book: "John", verse: "21.019", unformatted: "耶稣说这话，是指彼得将会以怎样的死来荣耀神。说了这话，耶稣对他说：“你跟从我！”", version_id: 6},
  {id: 158940, book: "John", verse: "21.02", unformatted: "论耶稣所爱的那门徒\n彼得转过身来，看见耶稣所爱的那门徒跟着走。他就是晚餐时贴近耶稣，问“主啊，出卖你的是谁”的那个门徒。", version_id: 6},
  {id: 158941, book: "John", verse: "21.021", unformatted: "彼得看见他，就问耶稣：“主啊，那么，这个人会怎么样呢？”", version_id: 6},
  {id: 158942, book: "John", verse: "21.022", unformatted: "耶稣说：“就算我愿意他一直留到我来的时候，这与你有什么关系？你跟从我！”", version_id: 6},
  {id: 158943, book: "John", verse: "21.023", unformatted: "于是在弟兄中传出了这样一种说法：“那门徒不会死。”其实耶稣并没有说他不会死，而是说：“就算我愿意他一直留到我来的时候，这与你有什么关系？”", version_id: 6},
  {id: 158944, book: "John", verse: "21.024", unformatted: "后记\n正是这个门徒，为这些事做见证，并记下了这些事，而且我们知道他的见证是真实的。", version_id: 6},
  {id: 158945, book: "John", verse: "21.025", unformatted: "耶稣所行的事，另外还有很多。如果把它们一一记载下来，我想，所写出来的书，连整个世界也容纳不下了。", version_id: 6},
  {id: 158946, book: "Acts", verse: "1.001", unformatted: "引言\n提阿费罗大人哪，在前一卷书中，我确实写了有关耶稣开始做的和教导的一切事，", version_id: 6},
  {id: 158947, book: "Acts", verse: "1.002", unformatted: "一直到他藉着圣灵吩咐了他所拣选的使徒们之后，被接升天的那一天为止。", version_id: 6},
  {id: 158948, book: "Acts", verse: "1.003", unformatted: "耶稣受难之后，用很多确据向这些人显明自己是活着的。他在四十天的时间里向他们显现，并且讲说有关神国的事。", version_id: 6},
  {id: 158949, book: "Acts", verse: "1.004", unformatted: "应许赐圣灵\n耶稣和他们聚集的时候，吩咐他们说：“不要离开耶路撒冷，而要等候父所应许的，就是你们从我听到的，", version_id: 6},
  {id: 158950, book: "Acts", verse: "1.005", unformatted: "因为约翰是用水施洗，但过不了多少日子，你们就要受圣灵的洗。”", version_id: 6},
  {id: 158951, book: "Acts", verse: "1.006", unformatted: "于是聚集的时候，他们就问耶稣说：“主啊！你复兴以色列国，是否在这时候呢？”", version_id: 6},
  {id: 158952, book: "Acts", verse: "1.007", unformatted: "耶稣对他们说：“父凭着自己的权柄所定的时候和日期，不是你们可以知道的。", version_id: 6},
  {id: 158953, book: "Acts", verse: "1.008", unformatted: "不过圣灵临到你们的时候，你们将得着能力，并且要在耶路撒冷，在犹太和撒马利亚全地，直到地极，做我的见证人。”", version_id: 6},
  {id: 158954, book: "Acts", verse: "1.009", unformatted: "升天\n说了这些话，耶稣就在他们的注视中被接升天，有一朵云彩把他接去，他们就看不见他了。", version_id: 6},
  {id: 158955, book: "Acts", verse: "1.01", unformatted: "正当耶稣离去，他们注目望天的时候，忽然有两个身穿白衣的人站在他们旁边，", version_id: 6},
  {id: 158956, book: "Acts", verse: "1.011", unformatted: "说：“各位加利利人哪，你们为什么站在这里望着天呢？这位从你们中间被接到天上去的耶稣，将来也要这样回来，就像你们看到他升天那样。”", version_id: 6},
  {id: 158957, book: "Acts", verse: "1.012", unformatted: "同心祷告\n于是他们从那叫做“橄榄园”的山上回到耶路撒冷。这山在耶路撒冷附近，有安息日允许走的路程。", version_id: 6},
  {id: 158958, book: "Acts", verse: "1.013", unformatted: "他们一进城，就到他们所住的楼上房间。在那里有\n彼得、约翰、\n雅各、安得烈，\n腓力和多马，\n巴多罗迈和马太，\n亚勒腓的儿子雅各、\n激进派的西门、雅各的儿子犹大。", version_id: 6},
  {id: 158959, book: "Acts", verse: "1.014", unformatted: "这些人与一些妇女以及耶稣的母亲玛丽亚、耶稣的兄弟们一起，都同心合意地恒切祷告。", version_id: 6},
  {id: 158960, book: "Acts", verse: "1.015", unformatted: "马提亚被选\n就在那些日子里，聚在一起的人约有一百二十名。彼得在弟兄中间站起来，说：", version_id: 6},
  {id: 158961, book: "Acts", verse: "1.016", unformatted: "“各位弟兄，那段经文是必须应验的，就是圣灵藉着大卫的口所预言有关犹大的事。这犹大，领人抓了耶稣；", version_id: 6},
  {id: 158962, book: "Acts", verse: "1.017", unformatted: "他本来算是我们中间的一个，也在这服事的工作上领受了一份；", version_id: 6},
  {id: 158963, book: "Acts", verse: "1.018", unformatted: "后来，竟然用这个人不义的酬报买了一块地。犹大一头栽倒在那里，肚腹迸裂，肠子都流出来了。", version_id: 6},
  {id: 158964, book: "Acts", verse: "1.019", unformatted: "所有住在耶路撒冷的人都知道了这件事，因此那块地按着他们的本地语言被称为“哈克达玛”——这意思是“血田”。", version_id: 6},
  {id: 158965, book: "Acts", verse: "1.02", unformatted: "因为诗篇上记着：\n‘愿他的住所变为荒场，\n没有人住在其中。’又说：\n‘愿别人得到他监督的职份。’", version_id: 6},
  {id: 158966, book: "Acts", verse: "1.021", unformatted: "“所以，这些人中必须有一个人和我们同做耶稣复活的见证人；这人是主耶稣在我们中间出入的所有时间里——就是从约翰的洗礼开始，到耶稣被接升天离开我们的那天为止——一直与我们在一起的。”", version_id: 6},
  {id: 158967, book: "Acts", verse: "1.023", unformatted: "于是他们推举了两个人，就是那叫巴撒巴，又被称为犹斯托的约瑟，还有马提亚。", version_id: 6},
  {id: 158968, book: "Acts", verse: "1.024", unformatted: "他们就祷告说：“主啊，你洞察每个人的心。求你从这两个人中，指明你所拣选的是哪一位，", version_id: 6},
  {id: 158969, book: "Acts", verse: "1.025", unformatted: "使他得到这服事和使徒的职份。犹大背弃了这职份，去了自己的地方。”", version_id: 6},
  {id: 158970, book: "Acts", verse: "1.026", unformatted: "接着，他们为这两个人抽签，抽到了马提亚，他就与那十一个使徒同列。", version_id: 6},
  {id: 158971, book: "Acts", verse: "2.001", unformatted: "五旬节圣灵降临\n到了五旬节那天，他们都聚集在一起。", version_id: 6},
  {id: 158972, book: "Acts", verse: "2.002", unformatted: "突然，有响声从天上下来，好像狂风吹过，充满了他们所坐的整个房子，", version_id: 6},
  {id: 158973, book: "Acts", verse: "2.003", unformatted: "有舌头像火焰那样，向他们显现，分开留在他们每一个人身上。", version_id: 6},
  {id: 158974, book: "Acts", verse: "2.004", unformatted: "于是他们都被圣灵充满，就照着圣灵赐给他们的话，开始说出别种语言。", version_id: 6},
  {id: 158975, book: "Acts", verse: "2.005", unformatted: "当时，有从天下各国来的虔诚的犹太人，住在耶路撒冷。", version_id: 6},
  {id: 158976, book: "Acts", verse: "2.006", unformatted: "这声音一响，有一群人就聚集而来，非常慌乱，因为各个人都听见使徒们用各个人本地的语言说话。", version_id: 6},
  {id: 158977, book: "Acts", verse: "2.007", unformatted: "他们又惊讶又诧异，说：“看，这些说话的不都是加利利人吗？", version_id: 6},
  {id: 158978, book: "Acts", verse: "2.008", unformatted: "我们怎么会听到我们各自出生地的语言呢？", version_id: 6},
  {id: 158979, book: "Acts", verse: "2.009", unformatted: "我们当中有帕提亚人、米底亚人、埃拦人；也有住在美索不达米亚、犹太和卡帕多细亚、本都和亚细亚、", version_id: 6},
  {id: 158980, book: "Acts", verse: "2.01", unformatted: "弗里吉亚和潘菲利亚、埃及和靠近古利奈的利比亚地区的人；还有从罗马来的旅客，", version_id: 6},
  {id: 158981, book: "Acts", verse: "2.011", unformatted: "包括犹太人和入犹太教的人；克里特人和阿拉伯人。我们都听见他们用我们的语言述说神伟大的作为！”", version_id: 6},
  {id: 158982, book: "Acts", verse: "2.012", unformatted: "众人又惊讶又困惑，彼此问：“这是什么意思呢？”", version_id: 6},
  {id: 158983, book: "Acts", verse: "2.013", unformatted: "可是另有些人嘲笑说：“他们被新酒灌醉了！”", version_id: 6},
  {id: 158984, book: "Acts", verse: "2.014", unformatted: "彼得讲道\n这时，彼得和其他十一个使徒一同站起来，高声对众人说：“各位犹太人和所有住在耶路撒冷的人哪，你们应当知道这件事，也应当仔细听我的话，", version_id: 6},
  {id: 158985, book: "Acts", verse: "2.015", unformatted: "因为这些人不是像你们所想的那样喝醉了，要知道，现在不过是早上九点。", version_id: 6},
  {id: 158986, book: "Acts", verse: "2.016", unformatted: "其实这是先知约珥所说的：", version_id: 6},
  {id: 158987, book: "Acts", verse: "2.017", unformatted: "‘神说，在末后的那些日子里将是这样：\n我要把我的灵倾注在所有人身上，\n你们的儿女要说预言，\n你们的年轻人要见异象，\n你们的老年人要做异梦。", version_id: 6},
  {id: 158988, book: "Acts", verse: "2.018", unformatted: "是的，在那些日子里，\n我也要将我的灵倾注\n在我的奴仆和婢女们身上，\n他们就要说预言。", version_id: 6},
  {id: 158989, book: "Acts", verse: "2.019", unformatted: "在天上我要显奇事；\n在地上我要行神迹——\n有血、有火、有烟雾。", version_id: 6},
  {id: 158990, book: "Acts", verse: "2.02", unformatted: "在主那大而显赫的日子来到之前，\n太阳要变为黑暗，\n月亮要变为血。", version_id: 6},
  {id: 158991, book: "Acts", verse: "2.021", unformatted: "那时候，无论谁求告主名，都将得救。’", version_id: 6},
  {id: 158992, book: "Acts", verse: "2.022", unformatted: "“各位以色列人哪，请听这些话：拿撒勒人耶稣是神向你们所证实的人；神藉着他在你们中间行了大能、神迹和奇事，正如你们自己所知道的。", version_id: 6},
  {id: 158993, book: "Acts", verse: "2.023", unformatted: "这一位照着神设定的计划和先见被交出去；你们就藉着外邦人的手，把他钉上十字架杀了。", version_id: 6},
  {id: 158994, book: "Acts", verse: "2.024", unformatted: "神却解除了死亡的痛苦，使他复活，因为他本来就不能被死亡辖制。", version_id: 6},
  {id: 158995, book: "Acts", verse: "2.025", unformatted: "事实上，大卫指着他说：\n‘我常常看见主在我面前；\n因他在我的右边，\n我就不至于摇动。", version_id: 6},
  {id: 158996, book: "Acts", verse: "2.026", unformatted: "为此我内心欢喜，\n口舌快乐，\n并且我的肉身也要安居在盼望中。", version_id: 6},
  {id: 158997, book: "Acts", verse: "2.027", unformatted: "因为你不会把我的灵魂撇弃在阴间，\n也不会让你的圣者经历腐朽。", version_id: 6},
  {id: 158998, book: "Acts", verse: "2.028", unformatted: "你让我明白了生命之道，\n使我在你面前充满喜乐。’", version_id: 6},
  {id: 158999, book: "Acts", verse: "2.029", unformatted: "“各位兄弟，关于先祖大卫，我可以明确地告诉你们：他不但死了，也被埋葬了，而且他的坟墓到今天还在我们这里。", version_id: 6},
  {id: 159000, book: "Acts", verse: "2.03", unformatted: "既然大卫是先知，也知道神曾向他起誓说，要从他的后裔中兴起一位坐在他的宝座上，", version_id: 6},
  {id: 159001, book: "Acts", verse: "2.031", unformatted: "大卫又预先看见了，就讲论基督复活的事，说：\n‘他既没有被撇弃在阴间，\n他的肉身也没有经历腐朽。’", version_id: 6},
  {id: 159002, book: "Acts", verse: "2.032", unformatted: "“这位耶稣，神已经使他复活了。我们都是这事的见证人。", version_id: 6},
  {id: 159003, book: "Acts", verse: "2.033", unformatted: "他被高举在神的右边，从父领受了所应许的圣灵，就将圣灵倾注了下来。这正是你们现在所看见所听见的。", version_id: 6},
  {id: 159004, book: "Acts", verse: "2.034", unformatted: "大卫并没有升到诸天之上去，可是他说，\n‘主对我主说：\n你坐在我的右边，", version_id: 6},
  {id: 159005, book: "Acts", verse: "2.035", unformatted: "等我把你的敌人放在你的脚下做脚凳。’", version_id: 6},
  {id: 159006, book: "Acts", verse: "2.036", unformatted: "“因此，以色列全家都当确实知道：被你们钉上十字架的这位耶稣，神已经委任他为主、为基督了。”", version_id: 6},
  {id: 159007, book: "Acts", verse: "2.037", unformatted: "奉主的名得赦免\n众人听了这话，觉得扎心，就对彼得和其他的使徒说：“各位兄弟，我们该做什么呢？”", version_id: 6},
  {id: 159008, book: "Acts", verse: "2.038", unformatted: "彼得对他们说：“你们应当悔改，每个人都当奉耶稣基督的名受洗，使你们的罪得赦免，就会领受所赐的圣灵。", version_id: 6},
  {id: 159009, book: "Acts", verse: "2.039", unformatted: "因为这应许是给你们和你们的儿女，以及所有在远处的人，就是主我们的神所召来的人。”", version_id: 6},
  {id: 159010, book: "Acts", verse: "2.04", unformatted: "彼得还说许多别的话郑重地做见证，又劝告他们说：“你们从这扭曲的世代中得救吧！”", version_id: 6},
  {id: 159011, book: "Acts", verse: "2.041", unformatted: "信徒们共同生活\n于是领受他话的人就受了洗；那一天大约增加了三千人。", version_id: 6},
  {id: 159012, book: "Acts", verse: "2.042", unformatted: "他们在使徒们的教导上，在契合、掰饼、祷告之事上恒切专心。", version_id: 6},
  {id: 159013, book: "Acts", verse: "2.043", unformatted: "每个人都起了敬畏之心。使徒们又行了很多奇事和神迹。", version_id: 6},
  {id: 159014, book: "Acts", verse: "2.044", unformatted: "所有信了的人都在一起，凡物共有，", version_id: 6},
  {id: 159015, book: "Acts", verse: "2.045", unformatted: "他们变卖财产和产业，按照各人的需要分给大家。", version_id: 6},
  {id: 159016, book: "Acts", verse: "2.046", unformatted: "他们同心合意，不但天天恒切地在圣殿聚集，还在各家各户掰饼。他们怀着喜乐和诚恳的心用饭，", version_id: 6},
  {id: 159017, book: "Acts", verse: "2.047", unformatted: "赞美神，并且得到全体民众的喜爱。主就在那里使得救的人与日俱增。", version_id: 6},
  {id: 159018, book: "Acts", verse: "3.001", unformatted: "瘸腿的人痊愈\n有一次，在下午三点祷告的时候，彼得和约翰上圣殿去。", version_id: 6},
  {id: 159019, book: "Acts", verse: "3.002", unformatted: "一个生来瘸腿的被人抬来。他天天被放在那叫“美门”的圣殿门口，为要向进圣殿的人乞讨。", version_id: 6},
  {id: 159020, book: "Acts", verse: "3.003", unformatted: "他看见彼得和约翰正要进入圣殿，就求他们施舍。", version_id: 6},
  {id: 159021, book: "Acts", verse: "3.004", unformatted: "彼得和约翰注视着他，彼得说：“你看我们！”", version_id: 6},
  {id: 159022, book: "Acts", verse: "3.005", unformatted: "那人就留意看他们，期待着从他们那里得些什么。", version_id: 6},
  {id: 159023, book: "Acts", verse: "3.006", unformatted: "彼得却说：“金子银子我都没有，只把我有的给你：奉拿撒勒人耶稣基督的名，起来走路吧！”", version_id: 6},
  {id: 159024, book: "Acts", verse: "3.007", unformatted: "彼得抓着他的右手，扶他起来。他的脚和踝骨立刻就健壮了。", version_id: 6},
  {id: 159025, book: "Acts", verse: "3.008", unformatted: "他一跳，站了起来，并且行走，与他们一起进入圣殿，边走边跳，赞美神。", version_id: 6},
  {id: 159026, book: "Acts", verse: "3.009", unformatted: "全体民众都看见他边走边赞美神，", version_id: 6},
  {id: 159027, book: "Acts", verse: "3.01", unformatted: "认出他是坐在圣殿的美门口乞讨的那个人，就对发生在他身上的事满心希奇、惊讶。", version_id: 6},
  {id: 159028, book: "Acts", verse: "3.011", unformatted: "在所罗门柱廊里传道\n那个人紧拉着彼得和约翰的时候，全体民众都一起跑向他们，到叫做“所罗门”的柱廊那里，满心惊奇。", version_id: 6},
  {id: 159029, book: "Acts", verse: "3.012", unformatted: "彼得看见，就对民众说：“各位以色列人哪！你们为什么对这事感到惊奇？为什么注视我们，以为我们是凭着自己的能力或虔诚使这个人走路的呢？", version_id: 6},
  {id: 159030, book: "Acts", verse: "3.013", unformatted: "亚伯拉罕、以撒、雅各的神，也就是我们祖先的神，已经荣耀了他的仆人耶稣。你们竟然把他交出去，并且在彼拉多判定要释放他的时候，在彼拉多面前拒绝了他。", version_id: 6},
  {id: 159031, book: "Acts", verse: "3.014", unformatted: "你们拒绝了神圣、公义的那一位，而要求释放一个杀人犯给你们。", version_id: 6},
  {id: 159032, book: "Acts", verse: "3.015", unformatted: "你们杀害了生命的创始者，神却使他从死人中复活了！我们就是这事的见证人。", version_id: 6},
  {id: 159033, book: "Acts", verse: "3.016", unformatted: "现在，基于对他名的信心，他的名就使你们所看见所认识的这个人健壮了；这由耶稣而来的信心，使他在你们众人面前完全康复了。", version_id: 6},
  {id: 159034, book: "Acts", verse: "3.017", unformatted: "“同胞们，现在我知道，你们所做的是出于愚昧无知，就像你们的首领那样。", version_id: 6},
  {id: 159035, book: "Acts", verse: "3.018", unformatted: "但神藉着众先知的口所预言的，就是基督要受难的事，就这样应验了。", version_id: 6},
  {id: 159036, book: "Acts", verse: "3.019", unformatted: "所以，你们应当悔改，应当回转，使你们的罪孽被抹去，", version_id: 6},
  {id: 159037, book: "Acts", verse: "3.02", unformatted: "这样，更新的日子就会从主面前来到；并且他要将耶稣，就是预先指定给你们的那位基督差来。", version_id: 6},
  {id: 159038, book: "Acts", verse: "3.021", unformatted: "不过天必须留他，直到万有复兴的时候，就是神自古以来藉着圣先知们的口所说的那时候。", version_id: 6},
  {id: 159039, book: "Acts", verse: "3.022", unformatted: "摩西确实说过：\n‘主——你们的神，将要从你们兄弟中给你们兴起一位先知，像兴起我一样。他对你们所说的一切，你们都要听从。", version_id: 6},
  {id: 159040, book: "Acts", verse: "3.023", unformatted: "所有不听从那位先知的人，都要从民中被全然灭绝。’", version_id: 6},
  {id: 159041, book: "Acts", verse: "3.024", unformatted: "“其实从撒母耳以来所有的先知，只要说预言，都同样地预告了这些日子。", version_id: 6},
  {id: 159042, book: "Acts", verse: "3.025", unformatted: "你们是先知们的子孙，也是神与你们祖先所订立之约的子孙。那时神对亚伯拉罕说：‘藉着你的那位后裔，地上万族都要蒙祝福。’", version_id: 6},
  {id: 159043, book: "Acts", verse: "3.026", unformatted: "神兴起了他的仆人，差派他先到你们这里来，要你们每个人转离自己的罪行，好祝福你们。”", version_id: 6},
  {id: 159044, book: "Acts", verse: "4.001", unformatted: "彼得与约翰被捕\n彼得和约翰正向民众讲话的时候，祭司们、圣殿守卫长和撒都该人忽然来了。", version_id: 6},
  {id: 159045, book: "Acts", verse: "4.002", unformatted: "这些人非常恼怒，因为使徒们教导民众，藉着耶稣传讲从死人中复活的事。", version_id: 6},
  {id: 159046, book: "Acts", verse: "4.003", unformatted: "于是他们下手捉拿了彼得和约翰。因为天已经晚了，就把他们关在监里，直到第二天。", version_id: 6},
  {id: 159047, book: "Acts", verse: "4.004", unformatted: "然而听了这话语的人中，有许多人相信了；男人的数目约有五千。", version_id: 6},
  {id: 159048, book: "Acts", verse: "4.005", unformatted: "在犹太领袖面前受审\n到了第二天，犹太人的首领、长老和经文士们都聚集在耶路撒冷。", version_id: 6},
  {id: 159049, book: "Acts", verse: "4.006", unformatted: "大祭司亚纳、该亚法、约翰、亚历山大，以及大祭司家族的人都在那里。", version_id: 6},
  {id: 159050, book: "Acts", verse: "4.007", unformatted: "他们叫彼得和约翰站在中间，质问他们，说：“你们凭着什么能力，以谁的名做这事？”", version_id: 6},
  {id: 159051, book: "Acts", verse: "4.008", unformatted: "那时，彼得被圣灵充满，对他们说：“民众的首领和长老们，", version_id: 6},
  {id: 159052, book: "Acts", verse: "4.009", unformatted: "如果我们今天因在一个残疾人身上所行的善事，被查问这个人怎样得到救治，", version_id: 6},
  {id: 159053, book: "Acts", verse: "4.01", unformatted: "那么，你们大家和所有以色列子民应当知道：这个人健全地站在你们面前，是藉着拿撒勒人耶稣基督的名。这位耶稣基督，你们把他钉上十字架，但是神使他从死人中复活了！", version_id: 6},
  {id: 159054, book: "Acts", verse: "4.011", unformatted: "这一位就是\n‘被你们工匠所藐视的石头，\n它已经成了房角的头块石头。’", version_id: 6},
  {id: 159055, book: "Acts", verse: "4.012", unformatted: "除他以外，没有救恩；因为在天下人间，没有赐下别的名，我们必须靠着得救。”", version_id: 6},
  {id: 159056, book: "Acts", verse: "4.013", unformatted: "传讲主名被禁止\n他们看见彼得和约翰的胆量，又了解他们是没有学问的普通人，就感到惊奇，并且了解他们是曾经与耶稣在一起的；", version_id: 6},
  {id: 159057, book: "Acts", verse: "4.014", unformatted: "又看见那得了痊愈的人和他们一同站着，就无话可说了，", version_id: 6},
  {id: 159058, book: "Acts", verse: "4.015", unformatted: "于是命令他们到议会外面去，就彼此商议，", version_id: 6},
  {id: 159059, book: "Acts", verse: "4.016", unformatted: "说：“我们该怎么处理这些人呢？藉着他们，确实发生了一个明显的神迹，所有住在耶路撒冷的人都清清楚楚；我们也不能否认。", version_id: 6},
  {id: 159060, book: "Acts", verse: "4.017", unformatted: "不过为了避免这事在民间传扬更广，让我们威吓他们，不可再奉耶稣的名对任何人讲话。”", version_id: 6},
  {id: 159061, book: "Acts", verse: "4.018", unformatted: "于是把他们叫来，吩咐他们绝对不可奉耶稣的名讲说或教导人。", version_id: 6},
  {id: 159062, book: "Acts", verse: "4.019", unformatted: "可是彼得和约翰回答说：“听从你们过于听从神，在神面前是理所当然的吗？你们自己判断吧！", version_id: 6},
  {id: 159063, book: "Acts", verse: "4.02", unformatted: "要知道，我们所看见所听见的，我们不能不说。”", version_id: 6},
  {id: 159064, book: "Acts", verse: "4.021", unformatted: "官长们又威吓他们一番，为了民众的缘故把他们释放了。官长们找不出任何理由来惩罚他们，因为众人都为所发生的事不住地荣耀神。", version_id: 6},
  {id: 159065, book: "Acts", verse: "4.022", unformatted: "实际上，因神迹而痊愈的那个人已经四十多岁了。", version_id: 6},
  {id: 159066, book: "Acts", verse: "4.023", unformatted: "祈求主赐胆量\n彼得和约翰被释放了，就回到自己的人那里，把祭司长们和长老们所说的话都告诉他们。", version_id: 6},
  {id: 159067, book: "Acts", verse: "4.024", unformatted: "他们听了，就同心合意地高声向神说：“主啊，你是造了天、地、海和其中万有的那一位。", version_id: 6},
  {id: 159068, book: "Acts", verse: "4.025", unformatted: "你曾经藉着圣灵，用你的仆人——我们先祖大卫的口说：\n‘列国为什么狂怒骚乱，\n众民为什么图谋虚空？", version_id: 6},
  {id: 159069, book: "Acts", verse: "4.026", unformatted: "地上的众君王都起来，\n首领们也聚集在一起，\n抵挡主和他的基督。’", version_id: 6},
  {id: 159070, book: "Acts", verse: "4.027", unformatted: "“事实上，希律和本丢彼拉多，与外邦人和以色列子民果然在这城里一同聚集，对付你所膏立的圣仆耶稣，", version_id: 6},
  {id: 159071, book: "Acts", verse: "4.028", unformatted: "做成了你手和你计划所预定要成就的一切事。", version_id: 6},
  {id: 159072, book: "Acts", verse: "4.029", unformatted: "主啊，现在求你鉴察他们的威吓，使你的奴仆们能满有胆量地传讲你的话语。", version_id: 6},
  {id: 159073, book: "Acts", verse: "4.03", unformatted: "求你伸出你的手，藉着你圣仆耶稣的名，使疾病痊愈，施行神迹和奇事。”", version_id: 6},
  {id: 159074, book: "Acts", verse: "4.031", unformatted: "他们祷告完了，聚集的地方就被震动，大家都被圣灵充满，就坦然无惧地传讲神的话语。", version_id: 6},
  {id: 159075, book: "Acts", verse: "4.032", unformatted: "凡物共有\n于是那一群信徒都同心合意，没有一个人说他所拥有的哪样东西是自己的；相反，他们凡物共有。", version_id: 6},
  {id: 159076, book: "Acts", verse: "4.033", unformatted: "使徒们大有能力地为主耶稣的复活做见证，并且在他们每个人身上都有极大的恩典。", version_id: 6},
  {id: 159077, book: "Acts", verse: "4.034", unformatted: "他们当中没有一个缺乏的人，因为凡是拥有田产或房屋的人，都卖掉了那些东西，把所卖得的钱拿来，", version_id: 6},
  {id: 159078, book: "Acts", verse: "4.035", unformatted: "放在使徒们的脚前，按照各人的需要分给每个人。", version_id: 6},
  {id: 159079, book: "Acts", verse: "4.036", unformatted: "有一个出生在塞浦路斯的利未人约瑟，使徒们称他为巴拿巴——“巴拿巴”翻译出来就是“安慰之子”；", version_id: 6},
  {id: 159080, book: "Acts", verse: "4.037", unformatted: "他有一块田地，也卖掉了，把钱拿来放在使徒们的脚前。", version_id: 6},
  {id: 159081, book: "Acts", verse: "5.001", unformatted: "欺骗圣灵\n有一个名叫阿纳尼亚的人，和他的妻子萨菲拉卖掉了财产。", version_id: 6},
  {id: 159082, book: "Acts", verse: "5.002", unformatted: "但他从价款中私自留下了一部分，把一部分拿来放在使徒们的脚前；他的妻子也知道这事。", version_id: 6},
  {id: 159083, book: "Acts", verse: "5.003", unformatted: "彼得就说：“阿纳尼亚，为什么撒旦充满了你的心，使你对圣灵说谎，从田产的价款中私自留下一些呢？", version_id: 6},
  {id: 159084, book: "Acts", verse: "5.004", unformatted: "田产留着没卖的时候，难道不属于你的吗？卖了以后，价款难道不也是由你做主吗？你心里为什么存有这种念头呢？你不是对人说谎，而是对神说谎了。”", version_id: 6},
  {id: 159085, book: "Acts", verse: "5.005", unformatted: "阿纳尼亚听见这话，就仆倒断了气；所有听到这事的人都大为恐惧。", version_id: 6},
  {id: 159086, book: "Acts", verse: "5.006", unformatted: "有几个年轻人站起来，把他的尸体裹好，抬出去埋葬了。", version_id: 6},
  {id: 159087, book: "Acts", verse: "5.007", unformatted: "隔了大约三个小时，阿纳尼亚的妻子进来，还不知道所发生的事。", version_id: 6},
  {id: 159088, book: "Acts", verse: "5.008", unformatted: "彼得问她：“告诉我，你们卖田产的钱，是不是这么多？”\n她说：“是的，就这么多。”", version_id: 6},
  {id: 159089, book: "Acts", verse: "5.009", unformatted: "彼得就对她说：“你们为什么同心试探主的灵呢？看，埋葬你丈夫之人的脚，已经到门口了，他们也要把你抬出去。”", version_id: 6},
  {id: 159090, book: "Acts", verse: "5.01", unformatted: "她立刻仆倒在彼得脚前，断了气。那些年轻人进来，看见她死了，也把她抬了出去，埋葬在她丈夫的旁边。", version_id: 6},
  {id: 159091, book: "Acts", verse: "5.011", unformatted: "全教会和所有听到这些事的人都大为恐惧。", version_id: 6},
  {id: 159092, book: "Acts", verse: "5.012", unformatted: "使徒们的神迹奇事\n主藉着使徒们的手，在民间行了很多神迹和奇事。信徒们都同心合意地聚集在所罗门柱廊里。", version_id: 6},
  {id: 159093, book: "Acts", verse: "5.013", unformatted: "虽然其他的人没有一个敢与他们交往，但民众都尊敬他们。", version_id: 6},
  {id: 159094, book: "Acts", verse: "5.014", unformatted: "信主的人越发增加，连男带女成群结队。", version_id: 6},
  {id: 159095, book: "Acts", verse: "5.015", unformatted: "人们甚至把病人抬到大街上来，放在床榻上或垫子上，为要在彼得走过来的时候，至少他的影子可以落在一些人身上。", version_id: 6},
  {id: 159096, book: "Acts", verse: "5.016", unformatted: "还有一群人带着病人和被污灵折磨的人，从耶路撒冷周围的城镇聚集而来，这些人都得了痊愈。", version_id: 6},
  {id: 159097, book: "Acts", verse: "5.017", unformatted: "进出监狱\n于是大祭司就采取行动。他和所有与他在一起的人，就是撒都该教派的人，都满腔嫉妒，", version_id: 6},
  {id: 159098, book: "Acts", verse: "5.018", unformatted: "下手捉拿使徒们，把他们关在公众拘留所里。", version_id: 6},
  {id: 159099, book: "Acts", verse: "5.019", unformatted: "但在夜里，主的一位天使打开了监门，领他们出来，说：", version_id: 6},
  {id: 159100, book: "Acts", verse: "5.02", unformatted: "“你们去站在圣殿里，把这生命的话语，都讲给民众听。”", version_id: 6},
  {id: 159101, book: "Acts", verse: "5.021", unformatted: "他们听从这话，在清晨的时候就进入圣殿教导人。\n使徒们再次受审\n这时，大祭司和与他在一起的人来了，他们召集了议会，就是以色列子民长老的全体议会，然后派人到监狱里，要把使徒们带来。", version_id: 6},
  {id: 159102, book: "Acts", verse: "5.022", unformatted: "但差役们到了监狱里，却找不到他们，就回去报告，", version_id: 6},
  {id: 159103, book: "Acts", verse: "5.023", unformatted: "说：“我们看见监狱被关得极其稳妥，卫兵都站在各个门口，可是打开那些门，里面却找不到任何人。”", version_id: 6},
  {id: 159104, book: "Acts", verse: "5.024", unformatted: "当圣殿的守卫长和祭司长们听到这些话的时候，就对使徒们的事感到困惑，不知道这事会如何发展。", version_id: 6},
  {id: 159105, book: "Acts", verse: "5.025", unformatted: "这时，有人来向他们报告说：“看，你们关在监狱里的那些人，正站在圣殿里教导民众呢！”", version_id: 6},
  {id: 159106, book: "Acts", verse: "5.026", unformatted: "于是守卫长与差役们一起去把使徒们带来，并没有用暴力，因为怕民众用石头砸他们。", version_id: 6},
  {id: 159107, book: "Acts", verse: "5.027", unformatted: "他们把使徒们带来后，叫他们站在议会当中。大祭司质问他们，", version_id: 6},
  {id: 159108, book: "Acts", verse: "5.028", unformatted: "说：“我们不是严厉地吩咐你们，不可奉这个人的名教导人吗？可是看哪，你们把你们的教导传遍了耶路撒冷，又想把这个人的血归到我们身上。”", version_id: 6},
  {id: 159109, book: "Acts", verse: "5.029", unformatted: "彼得和使徒们回答说：“我们必须顺从神过于顺从人。", version_id: 6},
  {id: 159110, book: "Acts", verse: "5.03", unformatted: "你们挂在木头上杀害的耶稣，我们祖先的神已经使他复活了。", version_id: 6},
  {id: 159111, book: "Acts", verse: "5.031", unformatted: "神把这一位高举在自己右边做元首、做救主，为了将悔改的心赐给以色列，使罪得赦免。", version_id: 6},
  {id: 159112, book: "Acts", verse: "5.032", unformatted: "我们就是这些事的见证人。神所赐给那些顺从他之人的圣灵也一同做见证。”", version_id: 6},
  {id: 159113, book: "Acts", verse: "5.033", unformatted: "迦玛列的建议\n议会的人听了，勃然大怒，就想要杀了他们。", version_id: 6},
  {id: 159114, book: "Acts", verse: "5.034", unformatted: "但有一个名叫迦玛列的法利赛人，是全体民众所尊敬的律法教师，在议会中站了起来，下令把使徒们暂时带到外面去，", version_id: 6},
  {id: 159115, book: "Acts", verse: "5.035", unformatted: "就对大家说：“各位以色列人哪，如何处置这些人，你们应当谨慎。", version_id: 6},
  {id: 159116, book: "Acts", verse: "5.036", unformatted: "因为这些日子之前，提乌达起来说自己是某某人物，跟从他的人数约有四百。这人被杀了以后，所有信从他的人都四散了，归于无有。", version_id: 6},
  {id: 159117, book: "Acts", verse: "5.037", unformatted: "这人以后，在户口登记期间又有个加利利人犹大起来，拉拢民众跟随他。他也死了，所有信从他的人也都四散了。", version_id: 6},
  {id: 159118, book: "Acts", verse: "5.038", unformatted: "现在我告诉你们：不要管这些人，由他们去吧！因为这计划或这行为，如果是出于人，就会被拆毁；", version_id: 6},
  {id: 159119, book: "Acts", verse: "5.039", unformatted: "但如果是出于神，你们就不能拆毁他们，恐怕你们反而被看做是与神作对了。”于是议会的人被迦玛列说服了，", version_id: 6},
  {id: 159120, book: "Acts", verse: "5.04", unformatted: "就叫来使徒们，鞭打了以后，吩咐他们不可再奉耶稣的名讲说，然后把他们释放了。", version_id: 6},
  {id: 159121, book: "Acts", verse: "5.041", unformatted: "这样使徒们就快快乐乐地离开了议会，因为算是配得上为这名受凌辱。", version_id: 6},
  {id: 159122, book: "Acts", verse: "5.042", unformatted: "他们每天在圣殿和各家各户不住地教导人，传福音说耶稣是基督。", version_id: 6},
  {id: 159123, book: "Acts", verse: "6.001", unformatted: "选出七位执事\n在那些日子里，门徒增多。说希腊语的犹太人抱怨那些说希伯来语的犹太人，因为他们的寡妇在每日的服事上被忽略了。", version_id: 6},
  {id: 159124, book: "Acts", verse: "6.002", unformatted: "于是十二使徒召来全体门徒，说：“我们撇下神的话语，去管理饭食，是不妥当的。", version_id: 6},
  {id: 159125, book: "Acts", verse: "6.003", unformatted: "所以弟兄们，要从你们当中挑选七个有好名声、充满圣灵和智慧的人；我们就委任他们统管这事务。", version_id: 6},
  {id: 159126, book: "Acts", verse: "6.004", unformatted: "这样，我们将可专心于祷告和福音的服事工作。”", version_id: 6},
  {id: 159127, book: "Acts", verse: "6.005", unformatted: "这话使全体会众都喜悦，于是选出了司提反——他是一个充满信仰和圣灵的人，还有腓利、普罗克罗、尼卡诺、提门、帕米纳，以及入犹太教的安提阿人尼古拉斯。", version_id: 6},
  {id: 159128, book: "Acts", verse: "6.006", unformatted: "他们请这些人站在使徒们面前，使徒们就祷告，按手在他们身上。", version_id: 6},
  {id: 159129, book: "Acts", verse: "6.007", unformatted: "这样，神的话语不断扩展，在耶路撒冷门徒的人数大大增多，并且有一大群祭司也听从了这信仰。", version_id: 6},
  {id: 159130, book: "Acts", verse: "6.008", unformatted: "司提反被捕\n司提反满有恩惠和能力，在民间行了许多大奇事和神迹。", version_id: 6},
  {id: 159131, book: "Acts", verse: "6.009", unformatted: "可是一些来自称为“自由人”会堂的人，包括一些古利奈人、亚历山大人，还有来自奇里乞亚省和亚细亚省的人，起来与司提反辩论，", version_id: 6},
  {id: 159132, book: "Acts", verse: "6.01", unformatted: "但司提反以智慧和圣灵说话，他们就抵挡不住。", version_id: 6},
  {id: 159133, book: "Acts", verse: "6.011", unformatted: "于是他们暗中唆使人说：“我们听过这个人针对摩西和神说亵渎的话语。”", version_id: 6},
  {id: 159134, book: "Acts", verse: "6.012", unformatted: "他们又煽动民众、长老和经文士们就忽然来抓住司提反，把他带到议会，", version_id: 6},
  {id: 159135, book: "Acts", verse: "6.013", unformatted: "并且推出假见证人说：“这个人不断地说亵渎神的话语，反对这圣所和律法。", version_id: 6},
  {id: 159136, book: "Acts", verse: "6.014", unformatted: "我们曾听到他说，这个拿撒勒人耶稣要拆毁这地方，也要改变摩西传给我们的规矩。”", version_id: 6},
  {id: 159137, book: "Acts", verse: "6.015", unformatted: "所有坐在议会里的人都注视着司提反，看见他的面容好像天使的面容。", version_id: 6},
  {id: 159138, book: "Acts", verse: "7.001", unformatted: "司提反在议会讲道\n大祭司问司提反：“真有这些事吗？”", version_id: 6},
  {id: 159139, book: "Acts", verse: "7.002", unformatted: "司提反说：“各位父老兄弟，请听！我们的先祖亚伯拉罕住哈兰以前，还在美索不达米亚的时候，荣耀的神向他显现，", version_id: 6},
  {id: 159140, book: "Acts", verse: "7.003", unformatted: "对他说：\n‘你要离开你的故土和亲族，\n到我所要指示你的地方去。’", version_id: 6},
  {id: 159141, book: "Acts", verse: "7.004", unformatted: "“于是亚伯拉罕离开迦勒底人的地方，住在哈兰。他的父亲死了以后，神使他从那里迁移到你们现在所住的这地方。", version_id: 6},
  {id: 159142, book: "Acts", verse: "7.005", unformatted: "在这里，神并没有赐给他继业，连立足的地方也没有。可是神应许要把这地赐给他和他的后裔为业。当时，亚伯拉罕还没有孩子。", version_id: 6},
  {id: 159143, book: "Acts", verse: "7.006", unformatted: "神这样说：\n‘他的后裔要在他人之地做寄居的，\n那里的人要奴役、虐待他们四百年。", version_id: 6},
  {id: 159144, book: "Acts", verse: "7.007", unformatted: "可是我要惩罚那奴役他们的国家。’\n神又说：\n‘这些事以后，他们将要出来，在这地方事奉我。’", version_id: 6},
  {id: 159145, book: "Acts", verse: "7.008", unformatted: "神又赐给亚伯拉罕割礼的约。这样，亚伯拉罕生了以撒，第八天给他行了割礼。后来以撒生了雅各，雅各生了十二位先祖。", version_id: 6},
  {id: 159146, book: "Acts", verse: "7.009", unformatted: "先祖在埃及\n“先祖们嫉妒约瑟，把他卖到埃及去。可是神与他同在，", version_id: 6},
  {id: 159147, book: "Acts", verse: "7.01", unformatted: "救他脱离一切患难，并且使他在埃及王法老面前蒙恩宠、有智慧。法老就委任约瑟做宰相，统管埃及和法老全家。", version_id: 6},
  {id: 159148, book: "Acts", verse: "7.011", unformatted: "后来整个埃及和迦南地区遭遇了饥荒和大灾难，我们的祖先找不到粮食。", version_id: 6},
  {id: 159149, book: "Acts", verse: "7.012", unformatted: "雅各听说埃及有粮食，就派我们的祖先去，这是第一次。", version_id: 6},
  {id: 159150, book: "Acts", verse: "7.013", unformatted: "第二次去的时候，约瑟的身世被他的兄弟们知道了，这样，约瑟的家族就被法老知道了。", version_id: 6},
  {id: 159151, book: "Acts", verse: "7.014", unformatted: "于是约瑟派人把他的父亲雅各和所有的亲属请来，一共七十五人。", version_id: 6},
  {id: 159152, book: "Acts", verse: "7.015", unformatted: "雅各下到埃及，后来他死了，我们的祖先也死了，", version_id: 6},
  {id: 159153, book: "Acts", verse: "7.016", unformatted: "遗体被带回示剑，安放到亚伯拉罕在示剑用一笔银子从哈莫尔的子孙买来的坟墓里。", version_id: 6},
  {id: 159154, book: "Acts", verse: "7.017", unformatted: "拒绝摩西的拯救\n“随着神向亚伯拉罕所宣告的那应许时期临近，以色列子民在埃及兴旺、增多，", version_id: 6},
  {id: 159155, book: "Acts", verse: "7.018", unformatted: "直到另一个不认识约瑟的王起来统治埃及。", version_id: 6},
  {id: 159156, book: "Acts", verse: "7.019", unformatted: "这王狡猾地对待我们的同胞，虐待我们的祖先，强迫他们丢弃自己的婴孩，好使婴孩活不下去。", version_id: 6},
  {id: 159157, book: "Acts", verse: "7.02", unformatted: "就在那时候，摩西出生了。他在神的眼里十分俊美，在父亲家里被抚养了三个月。", version_id: 6},
  {id: 159158, book: "Acts", verse: "7.021", unformatted: "后来他被丢弃的时候，法老的女儿把他捡去，当做自己的儿子抚养。", version_id: 6},
  {id: 159159, book: "Acts", verse: "7.022", unformatted: "摩西在埃及人的一切智慧上受了教育，说话行事都有能力。", version_id: 6},
  {id: 159160, book: "Acts", verse: "7.023", unformatted: "“当他满四十岁的时候，心里决定要去探望自己的同胞以色列子民。", version_id: 6},
  {id: 159161, book: "Acts", verse: "7.024", unformatted: "他看见一个同胞受欺负，就去卫护他，为那受压迫的人报仇，打死了那个埃及人。", version_id: 6},
  {id: 159162, book: "Acts", verse: "7.025", unformatted: "摩西以为自己的同胞会领悟神是藉着他的手拯救他们，但他们却不领悟。", version_id: 6},
  {id: 159163, book: "Acts", verse: "7.026", unformatted: "第二天，一些同胞在争斗，摩西就出来，劝他们和睦，说：‘各位！你们是同胞，为什么互相欺负呢？’", version_id: 6},
  {id: 159164, book: "Acts", verse: "7.027", unformatted: "“但那欺负同伴的人把摩西推开，说：\n‘谁委任你做我们的首领和审判官呢？", version_id: 6},
  {id: 159165, book: "Acts", verse: "7.028", unformatted: "难道你想杀了我，像你昨天杀了那个埃及人一样吗？’", version_id: 6},
  {id: 159166, book: "Acts", verse: "7.029", unformatted: "“摩西听了这话就逃跑了，并寄居在米甸地，在那里生了两个儿子。", version_id: 6},
  {id: 159167, book: "Acts", verse: "7.03", unformatted: "过了四十年，在西奈山的旷野，一位天使在荆棘火焰中向摩西显现。", version_id: 6},
  {id: 159168, book: "Acts", verse: "7.031", unformatted: "摩西见了这景象，感到惊奇。正当他上前要观看的时候，有主的声音说：", version_id: 6},
  {id: 159169, book: "Acts", verse: "7.032", unformatted: "‘我是你祖先的神，就是亚伯拉罕、以撒、雅各的神。’摩西战战兢兢，不敢观看。", version_id: 6},
  {id: 159170, book: "Acts", verse: "7.033", unformatted: "“主对他说：\n‘脱掉你脚上的鞋，因为你站的地方是圣地。", version_id: 6},
  {id: 159171, book: "Acts", verse: "7.034", unformatted: "我的子民在埃及的困苦，我确实看见了；他们的呻吟，我也听见了；于是我下来要救他们。现在你来！我要差派你到埃及去。’", version_id: 6},
  {id: 159172, book: "Acts", verse: "7.035", unformatted: "“这摩西就是他们拒绝说‘谁委任你做首领和审判官’的那位，神却藉着那在荆棘中向他显现的天使的手，差派他做首领、做救赎者。", version_id: 6},
  {id: 159173, book: "Acts", verse: "7.036", unformatted: "这个人带领以色列子民出来，在埃及地、在红海，并且在旷野的四十年间，行了奇事和神迹。", version_id: 6},
  {id: 159174, book: "Acts", verse: "7.037", unformatted: "以色列悖逆神\n“这个人就是摩西，他曾告诉以色列子民：‘神将要从你们兄弟中给你们兴起一位先知，像兴起我一样。’", version_id: 6},
  {id: 159175, book: "Acts", verse: "7.038", unformatted: "这个人曾在旷野的会众当中，与那在西奈山对他讲话的天使在一起，又和我们的祖先在一起。他接受了生命的话语，把它传给了我们。", version_id: 6},
  {id: 159176, book: "Acts", verse: "7.039", unformatted: "我们的祖先不肯顺从，反而弃绝了他，甚至在心里转回埃及去，", version_id: 6},
  {id: 159177, book: "Acts", verse: "7.04", unformatted: "对亚伦说：\n‘为我们造一些神明在我们前面引路吧，因为带领我们出埃及地的那个摩西，我们不知道他出了什么事！’", version_id: 6},
  {id: 159178, book: "Acts", verse: "7.041", unformatted: "在那些日子里，他们造了一个牛犊的像，又向那偶像献上祭物，并且为自己手所做的而庆祝。", version_id: 6},
  {id: 159179, book: "Acts", verse: "7.042", unformatted: "神就转身不顾，任凭他们去事奉天上的万象，正如先知书上所记载的：\n‘以色列家呀！\n你们在旷野的四十年里献上的牺牲和祭物，\n难道是给我的吗？", version_id: 6},
  {id: 159180, book: "Acts", verse: "7.043", unformatted: "你们抬起了摩洛的帐幕和你们理番神的星，\n就是你们为敬拜而造的像。\n因此，我要把你们迁移到巴比伦以外去。’", version_id: 6},
  {id: 159181, book: "Acts", verse: "7.044", unformatted: "真正的会幕\n“我们的祖先在旷野有见证的会幕，是照着对摩西说话的那一位所吩咐的，并按照摩西看到的样式所做成的。", version_id: 6},
  {id: 159182, book: "Acts", verse: "7.045", unformatted: "我们的祖先相继承受了这会幕，并且与约书亚一起把它带进了继业之地，就是神从我们祖先面前赶出的外邦人的地，直到大卫的时代。", version_id: 6},
  {id: 159183, book: "Acts", verse: "7.046", unformatted: "大卫在神面前蒙了恩典，就祈求为雅各的神预备一个居所。", version_id: 6},
  {id: 159184, book: "Acts", verse: "7.047", unformatted: "但为神建造殿宇的却是所罗门。", version_id: 6},
  {id: 159185, book: "Acts", verse: "7.048", unformatted: "其实至高者并不住在人手所造的殿宇里，正如先知所说：", version_id: 6},
  {id: 159186, book: "Acts", verse: "7.049", unformatted: "‘主说：“天是我的宝座，\n地是我的脚凳。\n你们要为我建造什么样的殿宇呢？\n我安息的地方在哪里呢？", version_id: 6},
  {id: 159187, book: "Acts", verse: "7.05", unformatted: "这一切难道不都是我手所造的吗？”’", version_id: 6},
  {id: 159188, book: "Acts", verse: "7.051", unformatted: "抗拒圣灵\n“你们这些顽固不化、心和耳都未受割礼的人哪，你们总是抗拒圣灵！你们的祖先怎样，你们也怎样。", version_id: 6},
  {id: 159189, book: "Acts", verse: "7.052", unformatted: "有哪一位先知，你们的祖先不逼迫呢？他们杀了那些预言那义者要来的先知，现在你们又成为那义者的出卖者和凶手了。", version_id: 6},
  {id: 159190, book: "Acts", verse: "7.053", unformatted: "你们领受了藉天使所设立的律法，却不遵守。”", version_id: 6},
  {id: 159191, book: "Acts", verse: "7.054", unformatted: "司提反殉道\n他们听了这些话就勃然大怒，向司提反咬牙切齿。", version_id: 6},
  {id: 159192, book: "Acts", verse: "7.055", unformatted: "但是司提反充满圣灵，注目望天，看见了神的荣耀，又看见耶稣站在神的右边。", version_id: 6},
  {id: 159193, book: "Acts", verse: "7.056", unformatted: "他说：“看哪，我看见诸天开了，人子站在神的右边。”", version_id: 6},
  {id: 159194, book: "Acts", verse: "7.057", unformatted: "于是他们大声喊叫，捂着耳朵，一齐向他冲过去，", version_id: 6},
  {id: 159195, book: "Acts", verse: "7.058", unformatted: "把他拉到城外，用石头砸他。那些见证人把自己的外衣放在一个称为扫罗的年轻人脚前。", version_id: 6},
  {id: 159196, book: "Acts", verse: "7.059", unformatted: "他们用石头砸司提反的时候，司提反向主呼求，说：“主耶稣，求你接受我的灵魂！”", version_id: 6},
  {id: 159197, book: "Acts", verse: "7.06", unformatted: "然后他跪下，大声呼喊：“主啊，不要把这罪归于他们。”说了这话，他就睡了。", version_id: 6},
  {id: 159198, book: "Acts", verse: "8.001", unformatted: "扫罗迫害信徒\n扫罗也赞同杀害司提反。\n从那一天开始，有大逼迫临到耶路撒冷的教会。除了使徒以外，他们都分散到犹太和撒马利亚地区。", version_id: 6},
  {id: 159199, book: "Acts", verse: "8.002", unformatted: "有些虔诚的人把司提反埋葬了，为他大大地悲伤。", version_id: 6},
  {id: 159200, book: "Acts", verse: "8.003", unformatted: "扫罗却摧残教会，闯进各家各户，无论男女都拉去关进监狱。", version_id: 6},
  {id: 159201, book: "Acts", verse: "8.004", unformatted: "腓利在撒马利亚传福音\n于是那些分散的人就走遍各地传讲福音。", version_id: 6},
  {id: 159202, book: "Acts", verse: "8.005", unformatted: "腓利下到撒马利亚的一个城，向那里的人传讲基督。", version_id: 6},
  {id: 159203, book: "Acts", verse: "8.006", unformatted: "当众人听见又看见腓利所行的神迹，就同心合意，专心听他所说的话。", version_id: 6},
  {id: 159204, book: "Acts", verse: "8.007", unformatted: "那时，有许多人被污灵附身，那些污灵大声喊叫着从他们身上出来了；许多瘫痪的和瘸腿的，也得了痊愈。", version_id: 6},
  {id: 159205, book: "Acts", verse: "8.008", unformatted: "所以那城里，有极大的喜乐。", version_id: 6},
  {id: 159206, book: "Acts", verse: "8.009", unformatted: "巫师西门\n有一个名叫西门的人，自称是个大人物，一向在那城里行邪术，使撒马利亚的居民十分惊讶。", version_id: 6},
  {id: 159207, book: "Acts", verse: "8.01", unformatted: "所有的人，从卑微的到尊贵的，都听从他。他们说：“这个人就是那被称为‘神的大能者’的。”", version_id: 6},
  {id: 159208, book: "Acts", verse: "8.011", unformatted: "他们听从他，因为他长期以来用邪术使他们十分惊讶。", version_id: 6},
  {id: 159209, book: "Acts", verse: "8.012", unformatted: "可是腓利传讲有关神国的福音和耶稣基督之名的时候，他们就相信了，无论男女都受了洗，", version_id: 6},
  {id: 159210, book: "Acts", verse: "8.013", unformatted: "连西门自己也信了。他受洗以后，常常跟着腓利，看见所行出来的神迹和大能，就十分惊讶。", version_id: 6},
  {id: 159211, book: "Acts", verse: "8.014", unformatted: "西门的罪\n在耶路撒冷的使徒们听说撒马利亚人接受了神的话语，就派彼得和约翰到他们那里去。", version_id: 6},
  {id: 159212, book: "Acts", verse: "8.015", unformatted: "两个人下去，就为他们祷告，好使他们领受圣灵，", version_id: 6},
  {id: 159213, book: "Acts", verse: "8.016", unformatted: "因为圣灵还没有降临到他们任何人身上。他们只是受洗归入了主耶稣的名。", version_id: 6},
  {id: 159214, book: "Acts", verse: "8.017", unformatted: "于是彼得和约翰按手在他们身上，他们就领受了圣灵。", version_id: 6},
  {id: 159215, book: "Acts", verse: "8.018", unformatted: "西门看见因着使徒们的按手，有圣灵赐下，就拿了钱给他们，", version_id: 6},
  {id: 159216, book: "Acts", verse: "8.019", unformatted: "说：“请把这权柄也给我，使我按手在谁身上，谁就可以领受圣灵。”", version_id: 6},
  {id: 159217, book: "Acts", verse: "8.02", unformatted: "彼得对他说：“你的银子和你一起灭亡吧！因为你以为神的恩赐可以用钱买到！", version_id: 6},
  {id: 159218, book: "Acts", verse: "8.021", unformatted: "你在这件事上无份无关，因为在神面前你的心不正。", version_id: 6},
  {id: 159219, book: "Acts", verse: "8.022", unformatted: "所以你应当为这恶事悔改，向主祈求，或许你心中的意念因此被赦免。", version_id: 6},
  {id: 159220, book: "Acts", verse: "8.023", unformatted: "因我看出你在苦毒的胆汁中，被不义所捆绑。”", version_id: 6},
  {id: 159221, book: "Acts", verse: "8.024", unformatted: "西门回答说：“请你们为我向主祈求，使你们所说的，没有一样临到我。”", version_id: 6},
  {id: 159222, book: "Acts", verse: "8.025", unformatted: "于是彼得和约翰在那里郑重地做见证，传讲了主的福音，又在撒马利亚人的许多村庄传福音，就回耶路撒冷去了。", version_id: 6},
  {id: 159223, book: "Acts", verse: "8.026", unformatted: "埃塞俄比亚太监信主受洗\n有主的一位天使对腓利说：“你起来，顺着那条从耶路撒冷下到加沙的路，往南走。”那里是旷野。", version_id: 6},
  {id: 159224, book: "Acts", verse: "8.027", unformatted: "腓利就起身去了。看哪，有一个埃塞俄比亚人，是个太监，是埃塞俄比亚坎蒂丝女王的大臣，统管她整个国库。他到耶路撒冷去敬拜后，", version_id: 6},
  {id: 159225, book: "Acts", verse: "8.028", unformatted: "在回去的时候，坐在他的马车里诵读先知以赛亚的书。", version_id: 6},
  {id: 159226, book: "Acts", verse: "8.029", unformatted: "圣灵对腓利说：“你上前去，贴近那马车走。”", version_id: 6},
  {id: 159227, book: "Acts", verse: "8.03", unformatted: "腓利跑过去，听见他正在诵读先知以赛亚的书，就问：“难道你真的明白你所读的吗？”", version_id: 6},
  {id: 159228, book: "Acts", verse: "8.031", unformatted: "太监回答说：“如果没有人指教我，我怎么能明白呢？”他就请腓利上来和他同坐。", version_id: 6},
  {id: 159229, book: "Acts", verse: "8.032", unformatted: "太监诵读的那段经文是这样的：\n“他像羊被牵去宰杀，\n又像羊羔在剪毛的人面前默默无声，\n他也这样不开口。", version_id: 6},
  {id: 159230, book: "Acts", verse: "8.033", unformatted: "他在屈辱中，公义被夺走了，\n谁能述说他的世代呢？\n因为他的生命从地上被夺走了。”", version_id: 6},
  {id: 159231, book: "Acts", verse: "8.034", unformatted: "太监对腓利说：“请问，先知说的这话是关于谁呢？是关于自己，还是关于别人呢？”", version_id: 6},
  {id: 159232, book: "Acts", verse: "8.035", unformatted: "腓利就开口，从这一段经文开始，向他传讲耶稣的福音。", version_id: 6},
  {id: 159233, book: "Acts", verse: "8.036", unformatted: "当他们沿路前行的时候，来到一个有水的地方。太监说：“看哪，有水！有什么能阻止我受洗呢？”", version_id: 6},
  {id: 159234, book: "Acts", verse: "8.037", unformatted: "腓利说：“如果你全心相信就可以受洗。”太监回答说：“我相信耶稣基督是神的儿子。”", version_id: 6},
  {id: 159235, book: "Acts", verse: "8.038", unformatted: "于是他吩咐停下马车，腓利和太监，两个人下到水里，腓利就给他施洗。", version_id: 6},
  {id: 159236, book: "Acts", verse: "8.039", unformatted: "他们从水里上来的时候，主的灵把腓利提走，太监再也看不见他了。太监继续赶路，满心快乐。", version_id: 6},
  {id: 159237, book: "Acts", verse: "8.04", unformatted: "后来，腓利出现在阿佐托。他走遍各个城镇传扬福音，直到他来到凯撒里亚。", version_id: 6},
  {id: 159238, book: "Acts", verse: "9.001", unformatted: "大马士革路上\n扫罗仍然向主的门徒们口吐凶杀威吓的话。他来到大祭司面前，", version_id: 6},
  {id: 159239, book: "Acts", verse: "9.002", unformatted: "求他发一些信函给大马士革各会堂，准许扫罗如果发现什么人属于这道，无论男女，都捆绑起来，带到耶路撒冷。", version_id: 6},
  {id: 159240, book: "Acts", verse: "9.003", unformatted: "当扫罗赶路，快到大马士革的时候，忽然有光从天上下来，四面照射他。", version_id: 6},
  {id: 159241, book: "Acts", verse: "9.004", unformatted: "他仆倒在地上，听见有声音对他说：“扫罗，扫罗！你为什么逼迫我？”", version_id: 6},
  {id: 159242, book: "Acts", verse: "9.005", unformatted: "他问：“主啊，你到底是谁？”\n主说：“我就是你所逼迫的耶稣。", version_id: 6},
  {id: 159243, book: "Acts", verse: "9.006", unformatted: "你起来，进城去！有人会告诉你应该做什么。”", version_id: 6},
  {id: 159244, book: "Acts", verse: "9.007", unformatted: "与扫罗同行的人都站着，哑口无言，他们虽然听见声音，却看不见任何人。", version_id: 6},
  {id: 159245, book: "Acts", verse: "9.008", unformatted: "扫罗从地上爬起来，睁开眼睛，却什么也看不见。他们牵着他的手，把他带进大马士革。", version_id: 6},
  {id: 159246, book: "Acts", verse: "9.009", unformatted: "他三天不能看见，不吃也不喝。", version_id: 6},
  {id: 159247, book: "Acts", verse: "9.01", unformatted: "扫罗受洗\n在大马士革有一个名叫阿纳尼亚的门徒。主在异象中对他说：“阿纳尼亚！”\n他回答说：“主啊！看哪，我在这里。”", version_id: 6},
  {id: 159248, book: "Acts", verse: "9.011", unformatted: "主就对他说：“起来，往那叫做‘直’的街上去，在犹大家里找一个名叫扫罗的塔尔苏人。看哪，原来他正在祷告。", version_id: 6},
  {id: 159249, book: "Acts", verse: "9.012", unformatted: "他在异象中看见了一个名叫阿纳尼亚的人，进来按手在他身上，使他重见光明。”", version_id: 6},
  {id: 159250, book: "Acts", verse: "9.013", unformatted: "阿纳尼亚却回答：“主啊，我听许多人说过有关这个人在耶路撒冷对你的圣徒们所做的种种恶事。", version_id: 6},
  {id: 159251, book: "Acts", verse: "9.014", unformatted: "并且，他得到祭司长们的授权来到这里，要捆绑所有求告你名的人。”", version_id: 6},
  {id: 159252, book: "Acts", verse: "9.015", unformatted: "但是主对他说：“你去吧！因为这个人是我所拣选的器皿，不但要在外邦人和君王面前，还要在以色列子民面前宣扬我的名。", version_id: 6},
  {id: 159253, book: "Acts", verse: "9.016", unformatted: "我要指示他，为了我的名他必须受多大的苦害。”", version_id: 6},
  {id: 159254, book: "Acts", verse: "9.017", unformatted: "阿纳尼亚就去了。他进了那家，按手在扫罗身上，说：“扫罗弟兄，在你来的路上向你显现的主耶稣差派我来，使你重见光明，并且被圣灵充满。”", version_id: 6},
  {id: 159255, book: "Acts", verse: "9.018", unformatted: "立刻有像鳞片一样的东西从扫罗的眼睛上掉了下来，他就重见光明了。于是他起来受了洗；", version_id: 6},
  {id: 159256, book: "Acts", verse: "9.019", unformatted: "吃过饭，体力就恢复了。\n扫罗传讲基督\n扫罗与在大马士革的门徒们住了几天，", version_id: 6},
  {id: 159257, book: "Acts", verse: "9.02", unformatted: "就立刻在各会堂里传讲耶稣，说这一位就是神的儿子。", version_id: 6},
  {id: 159258, book: "Acts", verse: "9.021", unformatted: "所有听见的人都十分惊讶，说：“这不就是那个在耶路撒冷残害了那些求告这名的人吗？而且他来到这里，不就是为要捆绑他们，带到祭司长们那里吗？”", version_id: 6},
  {id: 159259, book: "Acts", verse: "9.022", unformatted: "扫罗反而更加得以刚强，证明这一位就是基督，使住在大马士革的犹太人很狼狈。", version_id: 6},
  {id: 159260, book: "Acts", verse: "9.023", unformatted: "过了许多日子以后，犹太人商议要杀了扫罗，", version_id: 6},
  {id: 159261, book: "Acts", verse: "9.024", unformatted: "但他们的阴谋却被扫罗知道了。于是他们就日夜严守着各城门，要杀掉他。", version_id: 6},
  {id: 159262, book: "Acts", verse: "9.025", unformatted: "然而他的门徒们趁夜把他带去，用筐子把他从城墙上缒了下去。", version_id: 6},
  {id: 159263, book: "Acts", verse: "9.026", unformatted: "扫罗在耶路撒冷\n扫罗来到耶路撒冷，想要和门徒们交往，可是大家都怕他，不相信他成了门徒。", version_id: 6},
  {id: 159264, book: "Acts", verse: "9.027", unformatted: "但是巴拿巴接纳他，把他带到使徒们那里，并且告诉他们扫罗怎样在路上见到主，主怎样对他说了话，以及他怎样在大马士革奉耶稣的名放胆传道。", version_id: 6},
  {id: 159265, book: "Acts", verse: "9.028", unformatted: "于是扫罗在耶路撒冷与他们一起出入，奉主的名放胆传道。", version_id: 6},
  {id: 159266, book: "Acts", verse: "9.029", unformatted: "他对那些说希腊语的犹太人讲道，与他们辩论；他们却图谋要杀了他。", version_id: 6},
  {id: 159267, book: "Acts", verse: "9.03", unformatted: "弟兄们知道了这事，就带他下到凯撒里亚，然后送他往塔尔苏去。", version_id: 6},
  {id: 159268, book: "Acts", verse: "9.031", unformatted: "这样，教会在犹太、加利利、撒马利亚全地得平安、得造就，并且怀着对主的敬畏之心不断地前行，在圣灵的安慰中不断地增长。", version_id: 6},
  {id: 159269, book: "Acts", verse: "9.032", unformatted: "艾尼亚痊愈\n有一次，彼得走遍各地的时候，也下到住在利达的圣徒们那里。", version_id: 6},
  {id: 159270, book: "Acts", verse: "9.033", unformatted: "在那里，他遇见了一个名叫艾尼亚的人。他是个瘫痪的人，躺在垫子上八年了。", version_id: 6},
  {id: 159271, book: "Acts", verse: "9.034", unformatted: "彼得对他说：“艾尼亚，耶稣基督使你痊愈。站起来，为你自己整理垫子吧！”艾尼亚立刻就站起来了。", version_id: 6},
  {id: 159272, book: "Acts", verse: "9.035", unformatted: "于是所有住在利达和沙仑的人看到他，就归向了主。", version_id: 6},
  {id: 159273, book: "Acts", verse: "9.036", unformatted: "塔比莎复活\n在约帕有一个女门徒，名叫塔比莎——“塔比莎”翻译出来意思是“羚羊”。她尽做美善工作，常行施舍。", version_id: 6},
  {id: 159274, book: "Acts", verse: "9.037", unformatted: "就在那些日子里，她患病死了。人们清洗了遗体，就放在楼上的房间。", version_id: 6},
  {id: 159275, book: "Acts", verse: "9.038", unformatted: "利达离约帕不远，门徒们听说彼得在利达，就派了两个人到他那里，恳求他说：“请你来到我们这里，不要耽延！”", version_id: 6},
  {id: 159276, book: "Acts", verse: "9.039", unformatted: "彼得就起身陪他们一起去。到了以后，他们把他领到楼上的房间。所有的寡妇都站在彼得旁边哭，把多卡丝与她们在一起的时候所缝制的衣服和袍子拿给他看。", version_id: 6},
  {id: 159277, book: "Acts", verse: "9.04", unformatted: "彼得叫大家都出去，然后跪下祷告，接着转过身对着遗体，说：“塔比莎，你起来！”那妇人就睁开眼睛，看见彼得，而且坐了起来。", version_id: 6},
  {id: 159278, book: "Acts", verse: "9.041", unformatted: "彼得伸手扶她站起来，又叫圣徒们和寡妇们进来，把活过来的多卡丝交给他们。", version_id: 6},
  {id: 159279, book: "Acts", verse: "9.042", unformatted: "这事传遍了整个约帕，许多人就信了主。", version_id: 6},
  {id: 159280, book: "Acts", verse: "9.043", unformatted: "后来，彼得在约帕的一个硝皮匠西门家里住了许多日子。", version_id: 6},
  {id: 159281, book: "Acts", verse: "10.001", unformatted: "百夫长哥尼流的异象\n在凯撒里亚有一个名叫哥尼流的人，是那叫做“意大利”军团的百夫长，", version_id: 6},
  {id: 159282, book: "Acts", verse: "10.002", unformatted: "他是个敬虔的人，与全家都敬畏神；他慷慨地周济民众，常常向神祷告。", version_id: 6},
  {id: 159283, book: "Acts", verse: "10.003", unformatted: "有一天，大约下午三点，他在异象中清楚地看见神的一位天使来到他面前，对他说：“哥尼流！”", version_id: 6},
  {id: 159284, book: "Acts", verse: "10.004", unformatted: "哥尼流注视着他，感到害怕，说：“主啊，有什么事呢？”\n天使说：“你的祷告和你的施舍，已经上达神的面前蒙记念了。", version_id: 6},
  {id: 159285, book: "Acts", verse: "10.005", unformatted: "现在你要派人到约帕去，请一位称为彼得的西门来。", version_id: 6},
  {id: 159286, book: "Acts", verse: "10.006", unformatted: "他在一个硝皮匠西门的家里作客住宿，房子就在海边。”", version_id: 6},
  {id: 159287, book: "Acts", verse: "10.007", unformatted: "向哥尼流说话的天使离开以后，哥尼流叫来两个家仆和常伺候他的一个敬神的士兵，", version_id: 6},
  {id: 159288, book: "Acts", verse: "10.008", unformatted: "向他们述说这一切以后，就派他们去约帕。", version_id: 6},
  {id: 159289, book: "Acts", verse: "10.009", unformatted: "彼得的异象\n第二天，当他们赶路快到约帕的时候，大约中午十二点，彼得上屋顶去祷告。", version_id: 6},
  {id: 159290, book: "Acts", verse: "10.01", unformatted: "他感到饿了，想吃东西。有人正在给他准备的时候，彼得魂游象外，", version_id: 6},
  {id: 159291, book: "Acts", verse: "10.011", unformatted: "看见天开了，有一样东西降下，好像一块大布，四角捆住，缒到地上。", version_id: 6},
  {id: 159292, book: "Acts", verse: "10.012", unformatted: "里面有地上的各种四足动物、野兽、爬行动物和天空的飞鸟。", version_id: 6},
  {id: 159293, book: "Acts", verse: "10.013", unformatted: "接着有声音对他说：“彼得，起来，宰了吃！”", version_id: 6},
  {id: 159294, book: "Acts", verse: "10.014", unformatted: "彼得却说：“主啊，千万不可！因为我从来没有吃过任何俗物和不洁净的东西。”", version_id: 6},
  {id: 159295, book: "Acts", verse: "10.015", unformatted: "那声音第二次又对他说：“神所洁净的，你不可当做污秽的。”", version_id: 6},
  {id: 159296, book: "Acts", verse: "10.016", unformatted: "这样一连有三次，那东西随即被收回天上去了。", version_id: 6},
  {id: 159297, book: "Acts", verse: "10.017", unformatted: "彼得访问哥尼流\n当彼得心里困惑，不知道所看见的异象是什么意思的时候，看哪，哥尼流派来的人已经打听到了西门的家，站在门口，", version_id: 6},
  {id: 159298, book: "Acts", verse: "10.018", unformatted: "大声问：“是否有一位称为彼得的西门在这里作客住宿？”", version_id: 6},
  {id: 159299, book: "Acts", verse: "10.019", unformatted: "彼得还在思考那异象的时候，圣灵对他说：“看哪，有三个人来找你！", version_id: 6},
  {id: 159300, book: "Acts", verse: "10.02", unformatted: "你起来，下去与他们一起走，不要有任何疑惑，因为是我派他们来的。”", version_id: 6},
  {id: 159301, book: "Acts", verse: "10.021", unformatted: "彼得就下到那些人那里，说：“看，我就是你们要找的人。你们来到这里是为了什么缘故呢？”", version_id: 6},
  {id: 159302, book: "Acts", verse: "10.022", unformatted: "他们说：“百夫长哥尼流是个义人，敬畏神，也受到了犹太全族的赞许。他受一位圣天使的指示：要请你到他家去，听你的话语。”", version_id: 6},
  {id: 159303, book: "Acts", verse: "10.023", unformatted: "彼得就请他们进来，招待了他们。\n第二天，他起身与他们一同去，还有一些约帕的弟兄陪他一起去。", version_id: 6},
  {id: 159304, book: "Acts", verse: "10.024", unformatted: "又过了一天，他们进了凯撒里亚。哥尼流已经召集了他的亲属和好友，等候着他们。", version_id: 6},
  {id: 159305, book: "Acts", verse: "10.025", unformatted: "当彼得进去的时候，哥尼流就迎上去，俯伏在他的脚前拜他。", version_id: 6},
  {id: 159306, book: "Acts", verse: "10.026", unformatted: "彼得扶他起来，说：“你起来，我自己也是个人。”", version_id: 6},
  {id: 159307, book: "Acts", verse: "10.027", unformatted: "彼得与他说着话进去，看见里面聚集了许多人，", version_id: 6},
  {id: 159308, book: "Acts", verse: "10.028", unformatted: "就对他们说：“你们知道，犹太人和外族人交往或亲近是律法所禁止的。但是神指示我，不要把任何人当做俗的或不洁净的。", version_id: 6},
  {id: 159309, book: "Acts", verse: "10.029", unformatted: "所以我一受邀请，没有推辞就来了。那么请问，你们请我来有什么事呢？”", version_id: 6},
  {id: 159310, book: "Acts", verse: "10.03", unformatted: "哥尼流说：“四天前的这个时候，下午三点，我在家里祷告。忽然有一个身穿明亮衣服的人，站在我面前，", version_id: 6},
  {id: 159311, book: "Acts", verse: "10.031", unformatted: "说‘哥尼流，你的祷告已蒙垂听，你的施舍已经在神面前蒙记念了。", version_id: 6},
  {id: 159312, book: "Acts", verse: "10.032", unformatted: "所以你当派人到约帕去，请一位称为彼得的西门来。他在海边硝皮匠西门的家里作客住宿。’", version_id: 6},
  {id: 159313, book: "Acts", verse: "10.033", unformatted: "所以我就立即派人到你那里。你来了真好！现在我们都来到这里，在神的面前，聆听主所吩咐你的一切话。”", version_id: 6},
  {id: 159314, book: "Acts", verse: "10.034", unformatted: "向外邦人传福音\n于是彼得就开口说：“我现在真的了解神是不偏待人的，", version_id: 6},
  {id: 159315, book: "Acts", verse: "10.035", unformatted: "原来在各民族中，那敬畏神而行义的人，都是神所悦纳的。", version_id: 6},
  {id: 159316, book: "Acts", verse: "10.036", unformatted: "神藉着耶稣基督——这一位就是万有之主——传和平的福音，把这话语赐给了以色列子民。", version_id: 6},
  {id: 159317, book: "Acts", verse: "10.037", unformatted: "你们知道在约翰所传讲的洗礼以后，那从加利利开始，在犹太全地所发生的事，", version_id: 6},
  {id: 159318, book: "Acts", verse: "10.038", unformatted: "就是神怎样以圣灵和能力膏立拿撒勒人耶稣，他走遍各地行善，使所有受魔鬼压制的人痊愈，因为神与他同在。", version_id: 6},
  {id: 159319, book: "Acts", verse: "10.039", unformatted: "我们就是他在犹太人之地和耶路撒冷所行的一切事的见证人。他们竟然把他挂在木头上杀了。", version_id: 6},
  {id: 159320, book: "Acts", verse: "10.04", unformatted: "第三天，神使他复活，并且使他显现，", version_id: 6},
  {id: 159321, book: "Acts", verse: "10.041", unformatted: "不是显现给全体民众，而是显现给神预先选定的见证人，就是我们这些在他从死人中复活以后，与他一起吃、一起喝的人。", version_id: 6},
  {id: 159322, book: "Acts", verse: "10.042", unformatted: "耶稣还吩咐我们要向民众传道，郑重地见证他就是神所设定的那一位，活人和死人的审判者。", version_id: 6},
  {id: 159323, book: "Acts", verse: "10.043", unformatted: "所有的先知都为耶稣见证：‘所有信他的人，都将藉着他的名，罪得赦免。’”", version_id: 6},
  {id: 159324, book: "Acts", verse: "10.044", unformatted: "外邦人信主受洗\n彼得还在讲这些话的时候，圣灵降临在每个听这话语的人身上。", version_id: 6},
  {id: 159325, book: "Acts", verse: "10.045", unformatted: "所有陪彼得一起来的割礼派的信徒，看见所赐的圣灵也被倾注在外邦人身上，都十分惊讶，", version_id: 6},
  {id: 159326, book: "Acts", verse: "10.046", unformatted: "因为他们听见外邦人说着殊言，又尊神为大。\n于是彼得说：", version_id: 6},
  {id: 159327, book: "Acts", verse: "10.047", unformatted: "“这些人就像我们一样也领受了圣灵，到底谁能阻止用水给他们施洗呢？”", version_id: 6},
  {id: 159328, book: "Acts", verse: "10.048", unformatted: "他就吩咐他们奉耶稣基督的名受洗；然后他们请求彼得在那里多住几天。", version_id: 6},
  {id: 159329, book: "Acts", verse: "11.001", unformatted: "彼得解释外邦人的得救\n后来，使徒们和在犹太各地的弟兄们听说外邦人也接受了神的话语。", version_id: 6},
  {id: 159330, book: "Acts", verse: "11.002", unformatted: "可是当彼得上到耶路撒冷的时候，那些割礼派的人却批评他，", version_id: 6},
  {id: 159331, book: "Acts", verse: "11.003", unformatted: "说：“你竟然进到没有受割礼之人的家里，又与他们一起吃饭。”", version_id: 6},
  {id: 159332, book: "Acts", verse: "11.004", unformatted: "彼得就开始按着次序向他们讲解，说：", version_id: 6},
  {id: 159333, book: "Acts", verse: "11.005", unformatted: "“我在约帕城里祷告的时候，魂游象外，看见一个异象：有一样东西从天上降下，好像一块大布，四角捆住，缒到我面前来。", version_id: 6},
  {id: 159334, book: "Acts", verse: "11.006", unformatted: "我注目观看它，看见里面有地上的四足动物、野兽、爬行动物和天空的飞鸟；", version_id: 6},
  {id: 159335, book: "Acts", verse: "11.007", unformatted: "那时我也听见有声音对我说：‘彼得，起来，宰了吃！’", version_id: 6},
  {id: 159336, book: "Acts", verse: "11.008", unformatted: "我说：‘主啊，千万不可！因为俗物或不洁净的东西，从来没有入过我的口。’", version_id: 6},
  {id: 159337, book: "Acts", verse: "11.009", unformatted: "那声音第二次从天上传来，说：‘神所洁净的，你不可当做污秽的。’", version_id: 6},
  {id: 159338, book: "Acts", verse: "11.01", unformatted: "这样一连有三次，然后所有的东西又被收回天上去了。", version_id: 6},
  {id: 159339, book: "Acts", verse: "11.011", unformatted: "正在这时，忽然有三个人站在我们所住的房子面前，他们是从凯撒里亚被派到我这里的。", version_id: 6},
  {id: 159340, book: "Acts", verse: "11.012", unformatted: "圣灵吩咐我：要陪他们一起去，不要有任何疑惑。当时与我一起去的，还有这六位弟兄。后来我们就进了那人的家。", version_id: 6},
  {id: 159341, book: "Acts", verse: "11.013", unformatted: "他告诉我们，他怎样看见一位天使站在他家里说：‘你要派人到约帕去，请那称为彼得的西门来；", version_id: 6},
  {id: 159342, book: "Acts", verse: "11.014", unformatted: "他有话要对你说，藉这话你和你全家将要得救。’", version_id: 6},
  {id: 159343, book: "Acts", verse: "11.015", unformatted: "我一开始讲话，圣灵就降临在他们身上，就像当初降临在我们身上一样。", version_id: 6},
  {id: 159344, book: "Acts", verse: "11.016", unformatted: "我就想起了主如此说的话，‘约翰用水施洗，但你们将要受圣灵的洗。’", version_id: 6},
  {id: 159345, book: "Acts", verse: "11.017", unformatted: "因此，神如果给了他们这相同的恩赐，就像在我们信靠主耶稣基督的时候也给了我们一样，那么，我是谁呢？难道能拦阻神吗？”", version_id: 6},
  {id: 159346, book: "Acts", verse: "11.018", unformatted: "他们听了这些话就安静下来，然后就荣耀神，说：“这样，神把悔改的心也赐给外邦人，使他们得生命。”", version_id: 6},
  {id: 159347, book: "Acts", verse: "11.019", unformatted: "安提阿教会\n于是那些因司提反的事受患难而四散的信徒，就一直走到腓尼基地区、塞浦路斯岛和安提阿。他们只向犹太人，而不向任何人传讲福音。", version_id: 6},
  {id: 159348, book: "Acts", verse: "11.02", unformatted: "但其中有一些塞浦路斯人和古利奈人，来到安提阿，也向希腊人讲道，传主耶稣的福音。", version_id: 6},
  {id: 159349, book: "Acts", verse: "11.021", unformatted: "主的手与他们同在，有许许多多的人就相信、归向了主。", version_id: 6},
  {id: 159350, book: "Acts", verse: "11.022", unformatted: "这消息传到在耶路撒冷的教会，他们就派巴拿巴到安提阿去。", version_id: 6},
  {id: 159351, book: "Acts", verse: "11.023", unformatted: "巴拿巴到了，看见神的恩典就感到欢喜，鼓励大家要立定心志，继续靠主，", version_id: 6},
  {id: 159352, book: "Acts", verse: "11.024", unformatted: "——这是因为巴拿巴是个好人，充满圣灵和信仰。于是有一大群人归向了主。", version_id: 6},
  {id: 159353, book: "Acts", verse: "11.025", unformatted: "后来巴拿巴到塔尔苏去找扫罗，", version_id: 6},
  {id: 159354, book: "Acts", verse: "11.026", unformatted: "找到了，就带他到安提阿来。然后有整整一年，两个人与会众聚在一起，教导了一大群人。门徒们第一次被称为“基督徒”是在安提阿。", version_id: 6},
  {id: 159355, book: "Acts", verse: "11.027", unformatted: "赈灾\n在那些日子里，有几个做先知传道的从耶路撒冷下到安提阿。", version_id: 6},
  {id: 159356, book: "Acts", verse: "11.028", unformatted: "其中有一个名叫阿伽布斯的站起来，藉着圣灵指明普天下将有大饥荒。这事在凯撒克劳迪时期果然发生了。", version_id: 6},
  {id: 159357, book: "Acts", verse: "11.029", unformatted: "于是门徒们决定，每个人照着自己的能力来捐献，好送到住在犹太的弟兄们那里。", version_id: 6},
  {id: 159358, book: "Acts", verse: "11.03", unformatted: "他们就这么做了，由巴拿巴和扫罗经手，把所捐献的送到长老们那里。", version_id: 6},
  {id: 159359, book: "Acts", verse: "12.001", unformatted: "雅各殉道，彼得被囚\n大约在此时，希律王下手虐待教会中的一些人，", version_id: 6},
  {id: 159360, book: "Acts", verse: "12.002", unformatted: "用刀杀了约翰的哥哥雅各。", version_id: 6},
  {id: 159361, book: "Acts", verse: "12.003", unformatted: "他见这事使犹太人高兴，就又去抓彼得；那时正是除酵节期间。", version_id: 6},
  {id: 159362, book: "Acts", verse: "12.004", unformatted: "希律抓了彼得，把他关进监狱，交给四班士兵看守，每班四个人，打算在逾越节以后把他提到民众面前审判。", version_id: 6},
  {id: 159363, book: "Acts", verse: "12.005", unformatted: "这样，彼得被拘留在监狱里，教会却为他热切地向神祷告。", version_id: 6},
  {id: 159364, book: "Acts", verse: "12.006", unformatted: "彼得获救\n希律要把彼得提出来审判的前一夜，彼得被两条铁链捆锁，睡在两个士兵中间，又有一些卫兵在监狱门前看守着。", version_id: 6},
  {id: 159365, book: "Acts", verse: "12.007", unformatted: "忽然主的一位天使站在旁边，牢房里有光照耀。天使拍拍彼得的肋旁，叫醒他，说：“赶快起来！”那些铁链立刻就从彼得的手上掉了下来。", version_id: 6},
  {id: 159366, book: "Acts", verse: "12.008", unformatted: "天使对他说：“束上腰带，穿上鞋子！”他就照着做了。天使又说：“披上外衣，跟着我！”", version_id: 6},
  {id: 159367, book: "Acts", verse: "12.009", unformatted: "彼得就出来跟着走，却不知道天使所做的是真的，以为自己是见了异象。", version_id: 6},
  {id: 159368, book: "Acts", verse: "12.01", unformatted: "他们经过第一和第二道岗，来到通往城里的铁门前，铁门就自动为他们开了。他们出去，走过一条街，天使立刻离开了他。", version_id: 6},
  {id: 159369, book: "Acts", verse: "12.011", unformatted: "这时候，彼得清醒过来，说：“现在我确实知道主派了他的天使来，救我脱离希律的手和犹太民众所期待的一切事。”", version_id: 6},
  {id: 159370, book: "Acts", verse: "12.012", unformatted: "他明白之后，就到约翰的母亲玛丽亚家去，这约翰又叫马可。有许多人聚集在那里祷告。", version_id: 6},
  {id: 159371, book: "Acts", verse: "12.013", unformatted: "彼得敲了外面的门，一个叫罗妲的女仆前来应门。", version_id: 6},
  {id: 159372, book: "Acts", verse: "12.014", unformatted: "她一认出是彼得的声音，就高兴得顾不上打开门，跑进去告诉大家说，彼得站在门外。", version_id: 6},
  {id: 159373, book: "Acts", verse: "12.015", unformatted: "他们对罗妲说：“你疯了！”可是她坚持就是如此。他们不住地说：“那是他的天使。”", version_id: 6},
  {id: 159374, book: "Acts", verse: "12.016", unformatted: "这时，彼得不停地敲门。他们就打开了门，看见是他，都十分惊讶。", version_id: 6},
  {id: 159375, book: "Acts", verse: "12.017", unformatted: "彼得挥一下手叫他们安静，告诉他们主怎样领他出了监狱，又说：“你们把这些事告诉雅各和弟兄们。”然后他就出发，往别的地方去了。", version_id: 6},
  {id: 159376, book: "Acts", verse: "12.018", unformatted: "到天亮，士兵们中间起了不小的骚动，不知道彼得出了什么事。", version_id: 6},
  {id: 159377, book: "Acts", verse: "12.019", unformatted: "希律搜寻彼得，却没有找到，就审问卫兵们，下令把他们处决了。后来希律离开犹太，下到凯撒里亚去，住在那里。", version_id: 6},
  {id: 159378, book: "Acts", verse: "12.02", unformatted: "希律之死\n当时，希律一直恼恨提尔和西顿的人。所以他们联合起来到王那里去，先说服了王的内臣布拉斯托，然后向王求和，因为他们地区的人是从王的领地得粮食的。", version_id: 6},
  {id: 159379, book: "Acts", verse: "12.021", unformatted: "在指定的日子，希律穿上王袍，坐在审判席上，向他们公开演说。", version_id: 6},
  {id: 159380, book: "Acts", verse: "12.022", unformatted: "民众一直喊叫：“这是神的声音，不是人的声音。”", version_id: 6},
  {id: 159381, book: "Acts", verse: "12.023", unformatted: "主的一位天使立刻击打希律，因为他没有把荣耀归给神。他被虫咬蚀，就断了气。", version_id: 6},
  {id: 159382, book: "Acts", verse: "12.024", unformatted: "于是神的话语不断扩展，越发广传。", version_id: 6},
  {id: 159383, book: "Acts", verse: "12.025", unformatted: "巴拿巴和扫罗完成了那服事的工作，就带着那称为马可的约翰，从耶路撒冷回去了。", version_id: 6},
  {id: 159384, book: "Acts", verse: "13.001", unformatted: "预备差传\n在安提阿当地的教会中有一些做先知传道的和教师，包括巴拿巴、称为尼格尔的西蒙、古利奈人卢吉斯、与分封王希律一起长大的马纳恩，还有扫罗。", version_id: 6},
  {id: 159385, book: "Acts", verse: "13.002", unformatted: "他们在服事主、禁食的时候，圣灵说：“来，你们把巴拿巴和扫罗为我分别出来，去做我所召唤他们做的工作！”", version_id: 6},
  {id: 159386, book: "Acts", verse: "13.003", unformatted: "于是他们禁食、祷告，按手在巴拿巴和扫罗身上，就送他们走了。", version_id: 6},
  {id: 159387, book: "Acts", verse: "13.004", unformatted: "在塞浦路斯岛传道\n这样，他们两个人受圣灵的差遣，就下到塞琉西亚，又从那里坐船前往塞浦路斯岛，", version_id: 6},
  {id: 159388, book: "Acts", verse: "13.005", unformatted: "到了萨拉米斯，就在犹太人的各会堂里传讲神的话语。他们也有约翰马可做助理。", version_id: 6},
  {id: 159389, book: "Acts", verse: "13.006", unformatted: "他们走遍全岛，直到帕福斯，遇见了一个名叫巴耶苏的犹太人，是个行法术的假先知。", version_id: 6},
  {id: 159390, book: "Acts", verse: "13.007", unformatted: "他和省长瑟吉乌斯保罗常在一起。省长是一个明智的人，他请了巴拿巴和扫罗来，要求听神的话语。", version_id: 6},
  {id: 159391, book: "Acts", verse: "13.008", unformatted: "但那行法术的以路摩——他的名字翻译出来就是“行法术的”——却抵挡他们，企图使省长转离这信仰。", version_id: 6},
  {id: 159392, book: "Acts", verse: "13.009", unformatted: "可是扫罗，也就是保罗，被圣灵充满，注视着以路摩，", version_id: 6},
  {id: 159393, book: "Acts", verse: "13.01", unformatted: "说：“哦，你这个充满一切诡诈、一切奸恶的人哪！魔鬼的儿子，一切公义的仇敌！你歪曲主的正道，还不停止吗？", version_id: 6},
  {id: 159394, book: "Acts", verse: "13.011", unformatted: "看哪，现在主的手临到你，你要瞎眼，暂时看不见日光。”立刻，迷雾和黑暗笼罩了他，他四处摸索，求人牵手领路。", version_id: 6},
  {id: 159395, book: "Acts", verse: "13.012", unformatted: "省长当时看见所发生的事，对主的教导惊叹不已，就信了。", version_id: 6},
  {id: 159396, book: "Acts", verse: "13.013", unformatted: "在彼西底的安提阿传道\n保罗一行从帕福斯起航，来到潘菲利亚省的佩尔格。约翰马可却离开他们，回耶路撒冷去了。", version_id: 6},
  {id: 159397, book: "Acts", verse: "13.014", unformatted: "他们从佩尔格往前行，来到皮西迪亚地区的安提阿，在安息日那天，就进了会堂坐下。", version_id: 6},
  {id: 159398, book: "Acts", verse: "13.015", unformatted: "诵读了律法和先知书以后，会堂主管们派人来对他们说：“各位兄弟，如果有什么鼓励民众的话，就请说吧。”", version_id: 6},
  {id: 159399, book: "Acts", verse: "13.016", unformatted: "保罗就站起来，挥一下手，说：“各位以色列人和敬畏神的人，请听！", version_id: 6},
  {id: 159400, book: "Acts", verse: "13.017", unformatted: "这以色列民族的神拣选了我们的祖先，当他们寄居在埃及地的时候，高举他们，并用大能的膀臂带领他们从那里出来。", version_id: 6},
  {id: 159401, book: "Acts", verse: "13.018", unformatted: "在旷野，神容忍了他们约有四十年之久。", version_id: 6},
  {id: 159402, book: "Acts", verse: "13.019", unformatted: "神消灭了迦南地的七个民族后，把他们的土地赐给我们的先祖为继业，", version_id: 6},
  {id: 159403, book: "Acts", verse: "13.02", unformatted: "约有四百五十年。这些事以后，神又赐给他们士师，直到先知撒母耳为止。", version_id: 6},
  {id: 159404, book: "Acts", verse: "13.021", unformatted: "此后，他们要求立一个王，神就把便雅悯支派的一个人，基士的儿子扫罗给他们为王，共四十年。", version_id: 6},
  {id: 159405, book: "Acts", verse: "13.022", unformatted: "后来神废除了扫罗，兴起大卫做他们的王，并且为他见证说：‘我找到了耶西的儿子大卫，一个合乎我心意的人，他将遵行我的一切旨意。’", version_id: 6},
  {id: 159406, book: "Acts", verse: "13.023", unformatted: "“神照着应许，从这人的后裔中给以色列带来了一位救主，就是耶稣。", version_id: 6},
  {id: 159407, book: "Acts", verse: "13.024", unformatted: "在耶稣公开出来之前，约翰预先向所有以色列子民宣讲了悔改的洗礼。", version_id: 6},
  {id: 159408, book: "Acts", verse: "13.025", unformatted: "当约翰快要跑尽该跑的路程时，说：‘你们认为我是谁？我不是那一位。看哪，他要在我以后来，我就是为他解脚上的鞋带也不配。’", version_id: 6},
  {id: 159409, book: "Acts", verse: "13.026", unformatted: "“各位兄弟，亚伯拉罕家的子孙和你们中间敬畏神的人哪，这救恩的福音是赐给我们的。", version_id: 6},
  {id: 159410, book: "Acts", verse: "13.027", unformatted: "实际上，住在耶路撒冷的人和他们的首领们都不认识这一位，也不明白每逢安息日所诵读的先知们的话，就把他定罪，从而应验了这些话。", version_id: 6},
  {id: 159411, book: "Acts", verse: "13.028", unformatted: "他们虽然找不到处死他的任何理由，还是求彼拉多杀了他。", version_id: 6},
  {id: 159412, book: "Acts", verse: "13.029", unformatted: "当他们做完了经上所记载的一切有关耶稣的事，有些人就把他从木头上取下来，安放在墓穴里。", version_id: 6},
  {id: 159413, book: "Acts", verse: "13.03", unformatted: "但是神使他从死人中复活了！", version_id: 6},
  {id: 159414, book: "Acts", verse: "13.031", unformatted: "一连好几天，他向那些与他一起从加利利上到耶路撒冷的人显现；这些人如今在民间做了他的见证人，", version_id: 6},
  {id: 159415, book: "Acts", verse: "13.032", unformatted: "而我们也把应许给祖先的这福音传给你们，", version_id: 6},
  {id: 159416, book: "Acts", verse: "13.033", unformatted: "因为神使耶稣复活，已经为我们这些做他们儿女的成全了这应许，正如诗篇第二篇也记着：\n‘你是我的儿子，\n我今天生了你。’", version_id: 6},
  {id: 159417, book: "Acts", verse: "13.034", unformatted: "至于神使耶稣从死人中复活、不再归于腐朽的事，神曾这样说过：‘我要把给大卫的那圣洁、信实的祝福赐给你们。’", version_id: 6},
  {id: 159418, book: "Acts", verse: "13.035", unformatted: "所以在另外一处也说：‘你不会让你的圣者经历腐朽。’", version_id: 6},
  {id: 159419, book: "Acts", verse: "13.036", unformatted: "就是说，大卫虽然在自己的世代，遵行了神的计划之后就睡了，归到他祖先那里，并且经历了腐朽，", version_id: 6},
  {id: 159420, book: "Acts", verse: "13.037", unformatted: "但是神使之复活的那一位，没有经历腐朽。", version_id: 6},
  {id: 159421, book: "Acts", verse: "13.038", unformatted: "所以各位兄弟，你们应当知道：罪得赦免，是因着这一位传给你们的。在你们藉着摩西的律法，不能被称为义的一切事上，", version_id: 6},
  {id: 159422, book: "Acts", verse: "13.039", unformatted: "藉着这一位，所有相信的人都被称为义了。", version_id: 6},
  {id: 159423, book: "Acts", verse: "13.04", unformatted: "所以你们要当心，免得先知们所说的话临到你们：", version_id: 6},
  {id: 159424, book: "Acts", verse: "13.041", unformatted: "‘看吧，你们这些好讥诮的人哪！\n惊奇吧，灭亡吧！\n因为在你们的时代里，我要做一件事，\n就算有人向你们详细说明，\n你们也绝不会相信。’”", version_id: 6},
  {id: 159425, book: "Acts", verse: "13.042", unformatted: "扫罗与巴拿巴在安提阿\n保罗和巴拿巴离开会堂的时候，大家请求他们在下一个安息日再向他们讲这些话。", version_id: 6},
  {id: 159426, book: "Acts", verse: "13.043", unformatted: "散会以后，许多犹太人和入犹太教的敬神之人，跟从了保罗和巴拿巴。他们两个人与这些人谈论，劝导他们继续留在神的恩典中。", version_id: 6},
  {id: 159427, book: "Acts", verse: "13.044", unformatted: "到了下一个安息日，全城的人几乎都聚集，听主的福音。", version_id: 6},
  {id: 159428, book: "Acts", verse: "13.045", unformatted: "但犹太人看见这一群人，就满腔嫉妒，说亵渎的话来反对保罗所讲的。", version_id: 6},
  {id: 159429, book: "Acts", verse: "13.046", unformatted: "保罗和巴拿巴就放胆直言，说：“神的话语先传给你们是必须的。既然你们弃绝这道，断定自己不配得永恒的生命，看哪，我们就转向外邦人，", version_id: 6},
  {id: 159430, book: "Acts", verse: "13.047", unformatted: "因为主是这样吩咐我们的：\n‘我已经指派你为外邦人的光，\n使你把救恩带到地极。’”", version_id: 6},
  {id: 159431, book: "Acts", verse: "13.048", unformatted: "外邦人听了这话就感到欢喜，不住地荣耀主的福音，凡是被预定进入永恒生命的人都信了。", version_id: 6},
  {id: 159432, book: "Acts", verse: "13.049", unformatted: "于是主的福音传遍了那整个地区。", version_id: 6},
  {id: 159433, book: "Acts", verse: "13.05", unformatted: "但犹太人煽动一些敬神的尊贵妇女和城里的显要人物，怂恿他们逼迫保罗和巴拿巴，并且把他们驱逐出境。", version_id: 6},
  {id: 159434, book: "Acts", verse: "13.051", unformatted: "两个人对着他们跺掉脚上的尘土，往以哥念去了。", version_id: 6},
  {id: 159435, book: "Acts", verse: "13.052", unformatted: "门徒们都满心喜乐，又被圣灵充满。", version_id: 6},
  {id: 159436, book: "Acts", verse: "14.001", unformatted: "在以哥念传道、受迫害\n有一次在以哥念，保罗和巴拿巴同样进了犹太人的会堂讲道，结果有一大群犹太人和希腊人信了。", version_id: 6},
  {id: 159437, book: "Acts", verse: "14.002", unformatted: "但那些不肯信从的犹太人怂恿外邦人，使他们心里敌视弟兄们。", version_id: 6},
  {id: 159438, book: "Acts", verse: "14.003", unformatted: "于是保罗和巴拿巴就住了很久，靠着主放胆传道。主藉着他们的手行了神迹和奇事，见证了他恩典的话语。", version_id: 6},
  {id: 159439, book: "Acts", verse: "14.004", unformatted: "以哥念城的民众就分裂了，有的赞同犹太人，有的赞同使徒们。", version_id: 6},
  {id: 159440, book: "Acts", verse: "14.005", unformatted: "那时，外邦人和犹太人，与他们的首领们一同企图凌辱使徒们，要用石头砸死他们。", version_id: 6},
  {id: 159441, book: "Acts", verse: "14.006", unformatted: "两个人知道了，就逃往利考尼亚地区的路司得和代尔贝两个城以及周围地区，", version_id: 6},
  {id: 159442, book: "Acts", verse: "14.007", unformatted: "在那里继续传福音。", version_id: 6},
  {id: 159443, book: "Acts", verse: "14.008", unformatted: "在路司得被误认为是神\n在路司得城里坐着一个双脚残废的人，他生来就瘸腿，从来没有走过路；", version_id: 6},
  {id: 159444, book: "Acts", verse: "14.009", unformatted: "这个人听保罗讲道。保罗注视着他，见他存着信可得救治，", version_id: 6},
  {id: 159445, book: "Acts", verse: "14.01", unformatted: "就大声说：“你起来，用你的两脚站直！”那个人就跳起来，开始走路了。", version_id: 6},
  {id: 159446, book: "Acts", verse: "14.011", unformatted: "众人看见保罗所做的，就用利考尼亚话高声说：“神明像人那样降临在我们中间了！”", version_id: 6},
  {id: 159447, book: "Acts", verse: "14.012", unformatted: "他们称巴拿巴为宙斯神，称保罗为赫尔墨斯神，因为保罗是领头说话的人。", version_id: 6},
  {id: 159448, book: "Acts", verse: "14.013", unformatted: "于是城外宙斯神庙的祭司，牵着几头公牛，带着一些花环，到了城门口，要与众人一起来献祭。", version_id: 6},
  {id: 159449, book: "Acts", verse: "14.014", unformatted: "巴拿巴和保罗两位使徒听见这事，就撕裂自己的衣服，冲进人群里面，", version_id: 6},
  {id: 159450, book: "Acts", verse: "14.015", unformatted: "喊着说：“各位，你们为什么做这些事呢？我们和你们同样是人！我们向你们传福音，是要你们离弃这些虚妄的事，归向永生的神，就是造了天、地、海和其中万有的神。", version_id: 6},
  {id: 159451, book: "Acts", verse: "14.016", unformatted: "在过去的各世代，神容忍万民各行其道。", version_id: 6},
  {id: 159452, book: "Acts", verse: "14.017", unformatted: "其实神并不是没有为自己留下证据，因为他常行美善：从天上赐给你们雨水和丰收的季节，使你们食物充足，满心喜乐。”", version_id: 6},
  {id: 159453, book: "Acts", verse: "14.018", unformatted: "两个人说了这些话，好不容易才阻止众人向他们献祭。", version_id: 6},
  {id: 159454, book: "Acts", verse: "14.019", unformatted: "但有些犹太人从安提阿和以哥念来到，挑唆众人，用石头砸了保罗。他们以为他已经死了，就把他拖到城外。", version_id: 6},
  {id: 159455, book: "Acts", verse: "14.02", unformatted: "但门徒们围绕他的时候，他站了起来，进到城里。第二天，他与巴拿巴一起离开，前往代尔贝去。", version_id: 6},
  {id: 159456, book: "Acts", verse: "14.021", unformatted: "鼓励各教会的信徒\n保罗和巴拿巴在代尔贝城传了福音，使许多人成为门徒以后，回到路司得、以哥念、安提阿，", version_id: 6},
  {id: 159457, book: "Acts", verse: "14.022", unformatted: "坚固门徒们的心，鼓励他们持守信仰，说：“我们进入神的国，必须经过很多患难。”", version_id: 6},
  {id: 159458, book: "Acts", verse: "14.023", unformatted: "保罗和巴拿巴在各教会为他们选出了长老，祷告和禁食以后，把他们交托给他们所信靠的主。", version_id: 6},
  {id: 159459, book: "Acts", verse: "14.024", unformatted: "两个人经过皮西迪亚地区，来到潘菲利亚省，", version_id: 6},
  {id: 159460, book: "Acts", verse: "14.025", unformatted: "在佩尔格传了道，就下到阿塔利亚，", version_id: 6},
  {id: 159461, book: "Acts", verse: "14.026", unformatted: "从那里坐船回安提阿。当初，就是在这里，他们被交托在神的恩典中，去从事如今那已完成的工作。", version_id: 6},
  {id: 159462, book: "Acts", verse: "14.027", unformatted: "他们到了，就召集教会，传讲了神与他们所做的一切事，以及神怎样为外邦人打开了信仰之门。", version_id: 6},
  {id: 159463, book: "Acts", verse: "14.028", unformatted: "两个人在那里与门徒们一起住了不少时候。", version_id: 6},
  {id: 159464, book: "Acts", verse: "15.001", unformatted: "在安提阿争辩\n有一些人从犹太下来，教导弟兄们说：“你们如果不按摩西的规矩受割礼，就不能得救。”", version_id: 6},
  {id: 159465, book: "Acts", verse: "15.002", unformatted: "保罗、巴拿巴与他们发生了不小的争执和辩论，他们就指定保罗、巴拿巴和其中的另一些人为这争议的问题，上耶路撒冷到使徒们和长老们那里去。", version_id: 6},
  {id: 159466, book: "Acts", verse: "15.003", unformatted: "于是教会送他们上路；他们就走遍腓尼基和撒马利亚地区，详细说明外邦人悔改的事，给所有弟兄们带来了极大的喜乐。", version_id: 6},
  {id: 159467, book: "Acts", verse: "15.004", unformatted: "来到耶路撒冷，他们受到教会、使徒们和长老们的欢迎，就向大家传讲了神与他们一起做的事。", version_id: 6},
  {id: 159468, book: "Acts", verse: "15.005", unformatted: "但法利赛教派中的一些信徒站起来，说：“必须给外邦人行割礼，并吩咐他们遵守摩西的律法。”", version_id: 6},
  {id: 159469, book: "Acts", verse: "15.006", unformatted: "耶路撒冷的会议\n使徒们和长老们就聚集探讨这件事。", version_id: 6},
  {id: 159470, book: "Acts", verse: "15.007", unformatted: "经过了许多辩论，彼得站起来对他们说：“各位弟兄，你们知道前些时候神在你们中间施行拣选，要外邦人藉着我的口听到福音之道，得以相信。", version_id: 6},
  {id: 159471, book: "Acts", verse: "15.008", unformatted: "于是那洞察人心的神，赐圣灵给他们做了见证，就像为我们做的那样；", version_id: 6},
  {id: 159472, book: "Acts", verse: "15.009", unformatted: "又藉着信洁净了他们的心，在我们和他们之间不做什么区别。", version_id: 6},
  {id: 159473, book: "Acts", verse: "15.01", unformatted: "既然如此，你们现在为什么试探神，把我们的祖先和我们都担当不了的轭，放在那些门徒的脖子上呢？", version_id: 6},
  {id: 159474, book: "Acts", verse: "15.011", unformatted: "相反，我们相信：我们得救是藉着主耶稣的恩典，外邦人也是一样。”", version_id: 6},
  {id: 159475, book: "Acts", verse: "15.012", unformatted: "这时，全体会众都静默无声，听巴拿巴和保罗述说神藉着他们在外邦人中所行的神迹和奇事。", version_id: 6},
  {id: 159476, book: "Acts", verse: "15.013", unformatted: "他们讲完以后，雅各就回应说：“各位弟兄，请听我说。", version_id: 6},
  {id: 159477, book: "Acts", verse: "15.014", unformatted: "西门已经述说了神当初怎样临到外邦人，从他们中间选出子民，归入自己的名。", version_id: 6},
  {id: 159478, book: "Acts", verse: "15.015", unformatted: "连先知们的话也与此相符，正如经上所记：", version_id: 6},
  {id: 159479, book: "Acts", verse: "15.016", unformatted: "‘此后我将要回来，\n重建大卫倒塌的居所；\n我要重建它的破损之处，把它重新竖立起来。", version_id: 6},
  {id: 159480, book: "Acts", verse: "15.017", unformatted: "这样，其余的人，\n就是所有被称为我名下的外邦人，\n都会寻求主。", version_id: 6},
  {id: 159481, book: "Acts", verse: "15.018", unformatted: "这是自古以来，让人知道这些事的主说的。’", version_id: 6},
  {id: 159482, book: "Acts", verse: "15.019", unformatted: "因此，我判定不可为难那些归向神的外邦人，", version_id: 6},
  {id: 159483, book: "Acts", verse: "15.02", unformatted: "不过要写信给他们：要远避偶像的污秽，远避淫乱、勒死的动物和血。", version_id: 6},
  {id: 159484, book: "Acts", verse: "15.021", unformatted: "因为自古以来，在各城里都有人宣讲摩西，每逢安息日在各会堂里都有人诵读他的书。”", version_id: 6},
  {id: 159485, book: "Acts", verse: "15.022", unformatted: "致外邦信徒的书信\n当时，使徒们、长老们和全教会都认为应该从他们当中拣选几个人，派他们与保罗和巴拿巴一起到安提阿去。被选的是那称为巴撒巴的犹大和赛拉斯，这两个人在弟兄中是领头的。", version_id: 6},
  {id: 159486, book: "Acts", verse: "15.023", unformatted: "他们带去的信这样写着：\n“做使徒的和年长的弟兄们，\n问候安提阿、叙利亚省和奇里乞亚省的外邦弟兄们。", version_id: 6},
  {id: 159487, book: "Acts", verse: "15.024", unformatted: "我们听说从我们这里出去的一些人，用一些话使你们不安，搅扰你们的心灵。其实我们没有吩咐他们。", version_id: 6},
  {id: 159488, book: "Acts", verse: "15.025", unformatted: "我们已达成了一致，认为应该拣选几个人，派他们与我们亲爱的巴拿巴和保罗一起到你们那里去。", version_id: 6},
  {id: 159489, book: "Acts", verse: "15.026", unformatted: "这两个人都为我们主耶稣基督的名，已经把自己的生命置之度外。", version_id: 6},
  {id: 159490, book: "Acts", verse: "15.027", unformatted: "所以我们派了犹大和赛拉斯去，他们会亲口报告同样的事，", version_id: 6},
  {id: 159491, book: "Acts", verse: "15.028", unformatted: "因为圣灵和我们都认为，除了以下几件必须遵守的事以外，不该把更多的重担加给你们：", version_id: 6},
  {id: 159492, book: "Acts", verse: "15.029", unformatted: "就是要远避祭过偶像的食物，远避血和勒死的动物，远避淫乱。你们如果保守自己远离这些，就做得很好了。\n祝你们安康！”", version_id: 6},
  {id: 159493, book: "Acts", verse: "15.03", unformatted: "耶路撒冷来信的结果\n于是这些人被派遣，下到安提阿，召集了会众，把信函交给他们。", version_id: 6},
  {id: 159494, book: "Acts", verse: "15.031", unformatted: "读完了信，会众因着那鼓励的话而感到欢喜。", version_id: 6},
  {id: 159495, book: "Acts", verse: "15.032", unformatted: "犹大和赛拉斯自己也是做先知传道的。他们用很多话鼓励弟兄们，坚固他们。", version_id: 6},
  {id: 159496, book: "Acts", verse: "15.033", unformatted: "两个人住了一段时间，弟兄们就打发他们平平安安地回到差派他们的人那里去。", version_id: 6},
  {id: 159497, book: "Acts", verse: "15.034", unformatted: "可是赛拉斯认为应该留在那里。", version_id: 6},
  {id: 159498, book: "Acts", verse: "15.035", unformatted: "保罗和巴拿巴也住在安提阿，与其他许多人一起教导、传扬主的福音。", version_id: 6},
  {id: 159499, book: "Acts", verse: "15.036", unformatted: "保罗与巴拿巴分手\n过了几天，保罗对巴拿巴说：“来，让我们回到我们传过主福音的每一个城，探望弟兄们，看看他们的情况怎么样。”", version_id: 6},
  {id: 159500, book: "Acts", verse: "15.037", unformatted: "巴拿巴一直想要带那称为马可的约翰一起去。", version_id: 6},
  {id: 159501, book: "Acts", verse: "15.038", unformatted: "但是保罗觉得不该带他一起去，因为他曾在潘菲利亚省离开他们，没有陪他们一起去工作。", version_id: 6},
  {id: 159502, book: "Acts", verse: "15.039", unformatted: "两个人起了激烈的争执，以致彼此分手。巴拿巴带着马可，坐船到塞浦路斯岛去；", version_id: 6},
  {id: 159503, book: "Acts", verse: "15.04", unformatted: "保罗却选了赛拉斯。弟兄们把保罗交托于主的恩典之后，他就出发了。", version_id: 6},
  {id: 159504, book: "Acts", verse: "15.041", unformatted: "保罗走遍叙利亚省和奇里乞亚省，坚固各教会。", version_id: 6},
  {id: 159505, book: "Acts", verse: "16.001", unformatted: "保罗拣选提摩太\n保罗到达代尔贝城，又到了路司得城。当时，那里有一个名叫提摩太的门徒，是一个信主的犹太妇人的儿子，他父亲是希腊人。", version_id: 6},
  {id: 159506, book: "Acts", verse: "16.002", unformatted: "路司得和以哥念的弟兄们都赞许他。", version_id: 6},
  {id: 159507, book: "Acts", verse: "16.003", unformatted: "保罗想要带他一起去，但为了那些地方的犹太人的缘故，就给他行了割礼，因为大家都知道他父亲是希腊人。", version_id: 6},
  {id: 159508, book: "Acts", verse: "16.004", unformatted: "当他们经过那些城镇的时候，就把耶路撒冷的使徒们和长老们所定的规条，传达给大家遵守。", version_id: 6},
  {id: 159509, book: "Acts", verse: "16.005", unformatted: "这样，各教会在信仰上得了坚固，人数也天天增加。", version_id: 6},
  {id: 159510, book: "Acts", verse: "16.006", unformatted: "马其顿人的呼声\n圣灵既然阻止他们在亚细亚省传讲福音，他们就走遍弗里吉亚和加拉太地区。", version_id: 6},
  {id: 159511, book: "Acts", verse: "16.007", unformatted: "他们来到密细亚地区的边界，试着要往比提尼亚省去，可是耶稣的灵不允许。", version_id: 6},
  {id: 159512, book: "Acts", verse: "16.008", unformatted: "他们就经过密细亚地区，下到特罗阿斯港。", version_id: 6},
  {id: 159513, book: "Acts", verse: "16.009", unformatted: "夜里，保罗看见了一个异象：一个马其顿人站着恳求他，说：“请你过来，到马其顿来帮助我们。”", version_id: 6},
  {id: 159514, book: "Acts", verse: "16.01", unformatted: "当保罗看见这异象之后，我们就认定是神召唤我们去传福音给他们，于是立刻设法前往马其顿省。", version_id: 6},
  {id: 159515, book: "Acts", verse: "16.011", unformatted: "莉迪娅信主\n我们从特罗阿斯港开船直航，到萨莫特拉岛，第二天到了奈阿波利斯港，", version_id: 6},
  {id: 159516, book: "Acts", verse: "16.012", unformatted: "从那里来到腓立比。那是马其顿省的首要城市，是罗马的殖民地。我们在这城里住了几天。", version_id: 6},
  {id: 159517, book: "Acts", verse: "16.013", unformatted: "安息日那天，我们出了城门，来到河边，想到那里有一个祷告的地方。我们就坐下来，对聚集在那里的妇女们讲道。", version_id: 6},
  {id: 159518, book: "Acts", verse: "16.014", unformatted: "有一个敬拜神的妇人，名叫莉迪娅，是锡亚蒂拉城卖紫色布的商人。她一直在听，主开启她的心窍，使她留心保罗所讲的话。", version_id: 6},
  {id: 159519, book: "Acts", verse: "16.015", unformatted: "当她和她的家人受了洗之后，她请求我们说：“如果你们判断我是主的信徒，请到我家里来住。”于是她极力挽留我们。", version_id: 6},
  {id: 159520, book: "Acts", verse: "16.016", unformatted: "保罗与赛拉斯坐牢\n有一次，我们往祷告的地方去，迎面来了一个有占卜邪灵附身的女仆。她用算命为她的主人们带来大量的生意。", version_id: 6},
  {id: 159521, book: "Acts", verse: "16.017", unformatted: "她跟着保罗和我们，喊叫说：“这些人是至高神的奴仆，向你们传讲救恩的道。”", version_id: 6},
  {id: 159522, book: "Acts", verse: "16.018", unformatted: "她一连几天都这样做。\n保罗厌烦了，就转过身对那邪灵说：“我奉耶稣基督的名，吩咐你从她身上出来！”那邪灵立刻就出来了。", version_id: 6},
  {id: 159523, book: "Acts", verse: "16.019", unformatted: "女仆的主人们看见他们的生意没有希望了，就揪住保罗和赛拉斯，把他们拖到街市上去见首领们。", version_id: 6},
  {id: 159524, book: "Acts", verse: "16.02", unformatted: "把两个人带到官长们面前，说：“这些人是犹太人，在扰乱我们的城市，", version_id: 6},
  {id: 159525, book: "Acts", verse: "16.021", unformatted: "传讲我们罗马人不可以接受、也不能遵守的规矩。”", version_id: 6},
  {id: 159526, book: "Acts", verse: "16.022", unformatted: "那群人就一同起来反对保罗和赛拉斯。官长们就剥了他们的衣服，下令用棍子打，", version_id: 6},
  {id: 159527, book: "Acts", verse: "16.023", unformatted: "打了很多棍以后，把他们投进监狱，吩咐狱卒好好地看守。", version_id: 6},
  {id: 159528, book: "Acts", verse: "16.024", unformatted: "狱卒领了这样的吩咐，就把他们投进了监狱里的最里层，把他们的两脚铐在木枷上。", version_id: 6},
  {id: 159529, book: "Acts", verse: "16.025", unformatted: "深夜获救\n约在半夜，保罗和赛拉斯祷告、唱诗赞美神，其他的囚犯们都侧耳听着。", version_id: 6},
  {id: 159530, book: "Acts", verse: "16.026", unformatted: "突然发生了强烈的地震，结果监狱的地基都被摇动，所有的监门立刻打开了，每个囚犯的锁链也都松开了。", version_id: 6},
  {id: 159531, book: "Acts", verse: "16.027", unformatted: "狱卒醒来，看见监狱的门都开了，以为囚犯们已经逃跑了，就拔出刀来要自杀。", version_id: 6},
  {id: 159532, book: "Acts", verse: "16.028", unformatted: "保罗却大声呼唤说：“不要伤害自己，因为我们都在这里！”", version_id: 6},
  {id: 159533, book: "Acts", verse: "16.029", unformatted: "狱卒叫人拿了灯来，就冲进去，战战兢兢地俯伏在保罗和赛拉斯面前，", version_id: 6},
  {id: 159534, book: "Acts", verse: "16.03", unformatted: "然后领他们出来，说：“先生们，我应该做什么才能得救呢？”", version_id: 6},
  {id: 159535, book: "Acts", verse: "16.031", unformatted: "他们说：“要信靠主耶稣，你和你一家人就将得救。”", version_id: 6},
  {id: 159536, book: "Acts", verse: "16.032", unformatted: "于是他们把主的福音讲给狱卒和他家里所有的人听。", version_id: 6},
  {id: 159537, book: "Acts", verse: "16.033", unformatted: "当夜，就在那时刻，狱卒带他们去清洗伤口，然后他和所有的家人立刻受了洗。", version_id: 6},
  {id: 159538, book: "Acts", verse: "16.034", unformatted: "他把保罗和赛拉斯领到家里，摆上饭食。他和全家人因为信了神，都欢欣跳跃。", version_id: 6},
  {id: 159539, book: "Acts", verse: "16.035", unformatted: "官方道歉\n天亮的时候，官长们派刑杖官来说：“把那两个人放了吧！”", version_id: 6},
  {id: 159540, book: "Acts", verse: "16.036", unformatted: "狱卒就把这话告诉保罗，说：“官长们派人来，要释放你们。所以现在请你们出来，平平安安地去吧！”", version_id: 6},
  {id: 159541, book: "Acts", verse: "16.037", unformatted: "可是保罗对他们说：“我们是罗马公民，并没有被定罪，他们却当众打我们，把我们投进监狱。现在他们还要悄悄地把我们赶出去吗？当然不行！让他们亲自来领我们出去！”", version_id: 6},
  {id: 159542, book: "Acts", verse: "16.038", unformatted: "那些刑杖官把这些话报告给官长们，官长们一听他们是罗马公民，就惧怕了，", version_id: 6},
  {id: 159543, book: "Acts", verse: "16.039", unformatted: "于是来安抚他们，并且把他们领出来，请求他们离开那城。", version_id: 6},
  {id: 159544, book: "Acts", verse: "16.04", unformatted: "保罗和赛拉斯出了监狱，就到莉迪娅家里去，见了弟兄们，鼓励他们一番，然后离开了。", version_id: 6},
  {id: 159545, book: "Acts", verse: "17.001", unformatted: "在帖撒罗尼迦传道\n保罗和赛拉斯经过安菲波利斯和阿波罗尼亚，来到帖撒罗尼迦。那里有一个犹太人的会堂。", version_id: 6},
  {id: 159546, book: "Acts", verse: "17.002", unformatted: "保罗照习惯进了会堂，一连三个安息日，引用经文向他们讲论，", version_id: 6},
  {id: 159547, book: "Acts", verse: "17.003", unformatted: "讲解并说明基督必须受难，然后从死人中复活，并且说：“我传给你们的这位耶稣，就是基督。”", version_id: 6},
  {id: 159548, book: "Acts", verse: "17.004", unformatted: "他们当中有些人被说服了，就跟从了保罗和赛拉斯；不但有一大群敬神的希腊人，还有不少显要的妇女。", version_id: 6},
  {id: 159549, book: "Acts", verse: "17.005", unformatted: "耶森受逼迫\n但那些不肯信从的犹太人嫉妒了，就招聚一些市井无赖，成群结伙，扰乱城市。他们闯进耶森的家，搜寻保罗和赛拉斯，要把他们带到民众那里。", version_id: 6},
  {id: 159550, book: "Acts", verse: "17.006", unformatted: "可是找不到他们，就把耶森和几位弟兄拉到地方官们那里，喊叫说：“这些搅乱天下的，也来到这里，", version_id: 6},
  {id: 159551, book: "Acts", verse: "17.007", unformatted: "耶森竟接待了他们！这些人都违背了凯撒的谕令，说另外有一个王，就是耶稣。”", version_id: 6},
  {id: 159552, book: "Acts", verse: "17.008", unformatted: "民众和地方官们听了这些话就惊慌不安，", version_id: 6},
  {id: 159553, book: "Acts", verse: "17.009", unformatted: "后来，拿了耶森和其他人的保释金，就把他们释放了。", version_id: 6},
  {id: 159554, book: "Acts", verse: "17.01", unformatted: "庇里亚人查考经文\n弟兄们立刻连夜送保罗和赛拉斯往庇里亚城去。两个人一到，就进了犹太人的会堂。", version_id: 6},
  {id: 159555, book: "Acts", verse: "17.011", unformatted: "这里的人比帖撒罗尼迦的人开明。他们满心乐意地接受这福音，天天查考经文，要看这些事是否如此。", version_id: 6},
  {id: 159556, book: "Acts", verse: "17.012", unformatted: "这样，他们当中有许多人相信了，包括尊贵的希腊妇女，以及不少男人。", version_id: 6},
  {id: 159557, book: "Acts", verse: "17.013", unformatted: "可是当帖撒罗尼迦的犹太人知道保罗又在庇里亚城传扬神的话语时，就来到那里煽动、搅扰民众。", version_id: 6},
  {id: 159558, book: "Acts", verse: "17.014", unformatted: "于是弟兄们立刻送保罗往海边去，赛拉斯和提摩太仍然留在庇里亚。", version_id: 6},
  {id: 159559, book: "Acts", verse: "17.015", unformatted: "护送保罗的人一直带他到了雅典，然后领受了要赛拉斯和提摩太尽快到他这里来的指示，就回去了。", version_id: 6},
  {id: 159560, book: "Acts", verse: "17.016", unformatted: "保罗在雅典\n保罗在雅典等候赛拉斯和提摩太的时候，看见满城都是偶像，他灵里被激怒。", version_id: 6},
  {id: 159561, book: "Acts", verse: "17.017", unformatted: "于是他在会堂里向犹太人以及那些敬神的人讲论，每天又向在街市上遇见的人讲论。", version_id: 6},
  {id: 159562, book: "Acts", verse: "17.018", unformatted: "有些伊壁鸠鲁派和斯多亚派的哲学家们也与他辩论。\n有的说：“这胡言乱语的人，究竟想说什么呢？”也有的说：“他看起来是个宣传外国鬼神的人。”这是因为保罗传讲耶稣和复活的福音。", version_id: 6},
  {id: 159563, book: "Acts", verse: "17.019", unformatted: "他们就拉住保罗，带他到亚略巴古，问他：“你所讲论的这新教导，我们可以知道吗？", version_id: 6},
  {id: 159564, book: "Acts", verse: "17.02", unformatted: "既然你带来了一些奇怪的事，传到我们耳中，那么我们想知道这些事到底是什么意思。”", version_id: 6},
  {id: 159565, book: "Acts", verse: "17.021", unformatted: "原来，所有的雅典人以及在那里作客的外国人，不做别的，只花时间说说听听某个较为新奇的事。", version_id: 6},
  {id: 159566, book: "Acts", verse: "17.022", unformatted: "在亚略巴古的讲道\n于是保罗站在亚略巴古当中说：“各位雅典人哪，我看你们在各方面都非常敬畏鬼神，", version_id: 6},
  {id: 159567, book: "Acts", verse: "17.023", unformatted: "因为我边走边观察你们所敬拜的，发现一座祭坛上刻着‘献给不认识的神’；那么，我把你们所不认识而敬拜的这一位，现在传讲给你们。", version_id: 6},
  {id: 159568, book: "Acts", verse: "17.024", unformatted: "造了宇宙和其中万有的神，这一位既然是天地的主，就不住在人手所造的殿宇里，", version_id: 6},
  {id: 159569, book: "Acts", verse: "17.025", unformatted: "也不受人手的服事，好像缺少什么似的，因为他自己把生命、气息和万有赐给万人。", version_id: 6},
  {id: 159570, book: "Acts", verse: "17.026", unformatted: "他从一个人造出人类万族，使他们住在整个地面上，预先设定了他们的年限和所住的疆界，", version_id: 6},
  {id: 159571, book: "Acts", verse: "17.027", unformatted: "好让他们寻求神，或许因此真能摸索而找到他。其实他离我们每一个人并不远，", version_id: 6},
  {id: 159572, book: "Acts", verse: "17.028", unformatted: "因为我们活着、行动、生存，都在于他，正如你们中间的一些诗人也曾经说过，‘原来我们也是他的后裔。’", version_id: 6},
  {id: 159573, book: "Acts", verse: "17.029", unformatted: "所以我们既然是神的后裔，就不应该认为神性是仿佛用人的手艺和心思所雕刻的金、银、石像。", version_id: 6},
  {id: 159574, book: "Acts", verse: "17.03", unformatted: "“虽然神并不鉴察那愚昧无知的时代，但如今却吩咐各处所有的人都要悔改，", version_id: 6},
  {id: 159575, book: "Acts", verse: "17.031", unformatted: "因为他已经确立了日子，将要藉着他所设定的一个人，以公义来审判天下的人，并且藉着使他从死人中复活，给万人带来了确据。”", version_id: 6},
  {id: 159576, book: "Acts", verse: "17.032", unformatted: "他们听到死人复活的事，虽然有些人嘲笑他，但有些人却说：“关于此事，我们还要再听你说！”", version_id: 6},
  {id: 159577, book: "Acts", verse: "17.033", unformatted: "这样，保罗就从他们中间出去了。", version_id: 6},
  {id: 159578, book: "Acts", verse: "17.034", unformatted: "有一些人就跟从了保罗，信了主。其中包括亚略巴古的议员迪奥尼修、一个名叫达玛丽斯的妇人，以及其他与他们在一起的人。", version_id: 6},
  {id: 159579, book: "Acts", verse: "18.001", unformatted: "保罗在哥林多\n这些事以后，保罗离开雅典，来到哥林多。", version_id: 6},
  {id: 159580, book: "Acts", verse: "18.002", unformatted: "他遇见一个出生在本都省的犹太人，名叫阿奎拉，最近与他的妻子普茜拉从意大利来，因为克劳迪命令所有的犹太人都要离开罗马。保罗来到他们那里，", version_id: 6},
  {id: 159581, book: "Acts", verse: "18.003", unformatted: "因为与他们同业，就住在他们那里做工。原来他们是以制帐篷为业的。", version_id: 6},
  {id: 159582, book: "Acts", verse: "18.004", unformatted: "每逢安息日，保罗就在会堂里讲论，劝导犹太人和希腊人。", version_id: 6},
  {id: 159583, book: "Acts", verse: "18.005", unformatted: "赛拉斯和提摩太从马其顿省下来的时候，保罗被这福音所催逼，向犹太人郑重地见证耶稣是基督。", version_id: 6},
  {id: 159584, book: "Acts", verse: "18.006", unformatted: "当他们抗拒、说亵渎话的时候，保罗就抖着衣服对他们说：“你们的血，归在你们自己的头上；我是清白的。从今以后，我要到外邦人那里去。”", version_id: 6},
  {id: 159585, book: "Acts", verse: "18.007", unformatted: "于是他离开那里，进到一个名叫提多犹斯托的人家里。这个人敬拜神，他的家就在会堂隔壁。", version_id: 6},
  {id: 159586, book: "Acts", verse: "18.008", unformatted: "会堂主管克里斯普和他全家都信了主，还有许多哥林多人听了以后就信了，并且受了洗。", version_id: 6},
  {id: 159587, book: "Acts", verse: "18.009", unformatted: "在夜里，主藉着异象对保罗说：“不要怕，只管讲！不要沉默不语，", version_id: 6},
  {id: 159588, book: "Acts", verse: "18.01", unformatted: "因为我与你同在，而且没有人会下手伤害你；原来在这城里有许多我的子民。”", version_id: 6},
  {id: 159589, book: "Acts", verse: "18.011", unformatted: "保罗在那里住了一年零六个月，在他们当中教导神的话语。", version_id: 6},
  {id: 159590, book: "Acts", verse: "18.012", unformatted: "伽利奥任亚该亚省长的时候，犹太人一致起来攻击保罗，把他带到审判台前，", version_id: 6},
  {id: 159591, book: "Acts", verse: "18.013", unformatted: "说：“他煽动人不按律法敬拜神。”", version_id: 6},
  {id: 159592, book: "Acts", verse: "18.014", unformatted: "保罗刚要开口，伽利奥就对犹太人说：“哦，各位犹太人哪！如果这真是什么罪行或邪恶的案情，我理当容忍你们。", version_id: 6},
  {id: 159593, book: "Acts", verse: "18.015", unformatted: "但争议的问题，如果是关于字句、名称，和有关你们的律法，你们自己看着办吧！我不愿意做这些事的审判者。”", version_id: 6},
  {id: 159594, book: "Acts", verse: "18.016", unformatted: "于是把他们从审判台前赶了出去。", version_id: 6},
  {id: 159595, book: "Acts", verse: "18.017", unformatted: "大家都抓住会堂主管索提尼，在审判台前殴打他。但伽利奥一概不管这些事。", version_id: 6},
  {id: 159596, book: "Acts", verse: "18.018", unformatted: "返回安提阿\n保罗在哥林多继续留了许多日子，然后向弟兄们告别，坐船往叙利亚省去；普茜拉和阿奎拉也与他同去。他因为许过愿，就在坚革里港剃了头发。", version_id: 6},
  {id: 159597, book: "Acts", verse: "18.019", unformatted: "他们到达以弗所城，保罗把他们两个人留在那里，自己就进了会堂向犹太人讲论。", version_id: 6},
  {id: 159598, book: "Acts", verse: "18.02", unformatted: "他们请求保罗再多住些时候，他没有答应，", version_id: 6},
  {id: 159599, book: "Acts", verse: "18.021", unformatted: "向他们告别，说：“如果神愿意，我会再回到你们这里来。”就从以弗所起航了。", version_id: 6},
  {id: 159600, book: "Acts", verse: "18.022", unformatted: "保罗在凯撒里亚登陆，上耶路撒冷去问候教会，然后下安提阿去。", version_id: 6},
  {id: 159601, book: "Acts", verse: "18.023", unformatted: "住了一段时间，他又离开那里，按着次序走遍加拉太和弗里吉亚地区，坚固所有的门徒。", version_id: 6},
  {id: 159602, book: "Acts", verse: "18.024", unformatted: "阿波罗\n有一个出生在亚历山大的犹太人，名叫阿波罗，到达了以弗所。他是个有学问的人，在经文方面很强。", version_id: 6},
  {id: 159603, book: "Acts", verse: "18.025", unformatted: "这个人已经在主的道上受了教导，灵里火热，并仔细地讲论和教导有关耶稣的事，可是他只知道约翰的洗礼。", version_id: 6},
  {id: 159604, book: "Acts", verse: "18.026", unformatted: "阿波罗开始在会堂里放胆传道。普茜拉和阿奎拉听了，就接他来，把神的道更详尽地讲解给他听。", version_id: 6},
  {id: 159605, book: "Acts", verse: "18.027", unformatted: "后来阿波罗想到亚该亚省去，弟兄们就鼓励他，写信请门徒们欢迎他。他到了以后，对那些蒙恩的信徒大有帮助，", version_id: 6},
  {id: 159606, book: "Acts", verse: "18.028", unformatted: "因为他当众强有力地驳倒犹太人，用经文证明耶稣是基督。", version_id: 6},
  {id: 159607, book: "Acts", verse: "19.001", unformatted: "保罗在以弗所\n阿波罗在哥林多的时候，保罗经过内陆地区，来到以弗所城，在那里他遇见一些门徒，", version_id: 6},
  {id: 159608, book: "Acts", verse: "19.002", unformatted: "问他们：“你们信的时候，是否领受了圣灵？”\n他们告诉他：“其实我们也没有听说过是否有圣灵。”", version_id: 6},
  {id: 159609, book: "Acts", verse: "19.003", unformatted: "保罗又问：“那么，你们受的是什么洗呢？”\n他们说：“是约翰的洗礼。”", version_id: 6},
  {id: 159610, book: "Acts", verse: "19.004", unformatted: "保罗说：“约翰所施的是悔改的洗礼，他告诉民众当相信在他以后来的那一位，就是相信耶稣。”", version_id: 6},
  {id: 159611, book: "Acts", verse: "19.005", unformatted: "他们听了，就受洗归入了主耶稣的名。", version_id: 6},
  {id: 159612, book: "Acts", verse: "19.006", unformatted: "接着，保罗按手在他们身上，圣灵就临到了他们。他们就说起殊言，又说预言。", version_id: 6},
  {id: 159613, book: "Acts", verse: "19.007", unformatted: "他们一共约有十二个人。", version_id: 6},
  {id: 159614, book: "Acts", verse: "19.008", unformatted: "在特拉诺斯学堂\n保罗进了会堂，放胆传道，一连三个月讲论并劝导人相信那些有关神国的事。", version_id: 6},
  {id: 159615, book: "Acts", verse: "19.009", unformatted: "后来，当有些人心里刚硬、不肯信从，在会众面前诽谤这道的时候，保罗就离开他们，把门徒们分别出来，天天在特拉诺斯讲堂里讲论。", version_id: 6},
  {id: 159616, book: "Acts", verse: "19.01", unformatted: "这样持续了两年，结果所有住在亚细亚省的人，无论是犹太人或希腊人，都听到了主的福音。", version_id: 6},
  {id: 159617, book: "Acts", verse: "19.011", unformatted: "在以弗所驱赶邪灵\n神藉着保罗的手，行了一些不寻常的神迹，", version_id: 6},
  {id: 159618, book: "Acts", verse: "19.012", unformatted: "甚至有人把保罗身上的手巾和围裙拿去，放在病人身上，疾病就从他们身上消退了，邪灵也出去了。", version_id: 6},
  {id: 159619, book: "Acts", verse: "19.013", unformatted: "可是有一些四处游历的犹太驱魔师，向那些有邪灵附身的人，擅自称呼主耶稣的名，说：“我藉着保罗所传的耶稣，吩咐你们出来。”", version_id: 6},
  {id: 159620, book: "Acts", verse: "19.014", unformatted: "有一个名叫斯克瓦的犹太祭司长，他的七个儿子都在做这种事。", version_id: 6},
  {id: 159621, book: "Acts", verse: "19.015", unformatted: "邪灵回答他们，说：“耶稣我认识，保罗我也知道！但你们呢？你们是谁？”", version_id: 6},
  {id: 159622, book: "Acts", verse: "19.016", unformatted: "有邪灵附身的那个人就跳到他们身上，压制了所有的人，胜过了他们，以致他们赤着身带着伤，从那房子逃了出去。", version_id: 6},
  {id: 159623, book: "Acts", verse: "19.017", unformatted: "所有住在以弗所的犹太人和希腊人，都知道了这件事，恐惧就临到了他们每个人，主耶稣的名因此被尊为大。", version_id: 6},
  {id: 159624, book: "Acts", verse: "19.018", unformatted: "而且许多信徒都来，承认并述说自己的所作所为。", version_id: 6},
  {id: 159625, book: "Acts", verse: "19.019", unformatted: "又有许多行邪术的人，把他们的书籍拿到一处，当众烧掉。他们估算了那些书的价值，发现共值五万个银币。", version_id: 6},
  {id: 159626, book: "Acts", verse: "19.02", unformatted: "这样，主的福音就大有能力地不断扩展，不断得胜。", version_id: 6},
  {id: 159627, book: "Acts", verse: "19.021", unformatted: "以弗所的骚乱\n这些事过了以后，保罗受了圣灵的感动决定要经过马其顿省和亚该亚省，到耶路撒冷去。他说：“我到那里以后，也必须去罗马看看。”", version_id: 6},
  {id: 159628, book: "Acts", verse: "19.022", unformatted: "于是他派了提摩太和以拉斯托两个服事他的人到马其顿省去，自己暂时留在亚细亚省。", version_id: 6},
  {id: 159629, book: "Acts", verse: "19.023", unformatted: "就在这时候，为了这道发生了不小的骚动。", version_id: 6},
  {id: 159630, book: "Acts", verse: "19.024", unformatted: "原来，有一个名叫迪米特斯的银匠，是制造阿蒂米丝女神银龛的，给工匠们带来不少的生意。", version_id: 6},
  {id: 159631, book: "Acts", verse: "19.025", unformatted: "迪米特斯召集了他们，以及周围同行的工人，说：“各位，你们知道，我们是由这生意发财的。", version_id: 6},
  {id: 159632, book: "Acts", verse: "19.026", unformatted: "可是你们看见了也听见了，这个保罗不仅在以弗所也几乎在全亚细亚省，说服、引诱了一大群人，说‘人手所造的不是神’。", version_id: 6},
  {id: 159633, book: "Acts", verse: "19.027", unformatted: "这样，不仅我们这行业有被轻蔑的危险，连伟大女神阿蒂米丝的庙也要被看为什么都不是，并且这受全亚细亚省和普天下敬拜的女神的威荣，也要被毁灭了。”", version_id: 6},
  {id: 159634, book: "Acts", verse: "19.028", unformatted: "众人听了，就满腔怒火，喊叫说：“以弗所人伟大的阿蒂米丝！”", version_id: 6},
  {id: 159635, book: "Acts", verse: "19.029", unformatted: "于是满城一片混乱。他们抓住与保罗同来的马其顿人盖尤斯和亚里达古，一齐冲进剧场。", version_id: 6},
  {id: 159636, book: "Acts", verse: "19.03", unformatted: "保罗虽然想要进到民众当中，但门徒们不让他去。", version_id: 6},
  {id: 159637, book: "Acts", verse: "19.031", unformatted: "还有一些亚细亚省的官员，是保罗的朋友，也派人劝他不要自己冒险到剧场去。", version_id: 6},
  {id: 159638, book: "Acts", verse: "19.032", unformatted: "这时候，有人喊这个、有人喊那个，因为集会的人一片混乱，大多数人不知道为了什么缘故聚集。", version_id: 6},
  {id: 159639, book: "Acts", verse: "19.033", unformatted: "犹太人把亚历山大推到前面的时候，人群中有些人要他说话。亚历山大就挥一下手，想要对民众申辩。", version_id: 6},
  {id: 159640, book: "Acts", verse: "19.034", unformatted: "可是大家一认出他是犹太人，就同声呼喊：“以弗所人伟大的阿蒂米丝！”大约喊了两个小时。", version_id: 6},
  {id: 159641, book: "Acts", verse: "19.035", unformatted: "后来，城里的书记官安抚了民众，说：“各位以弗所人哪，到底有什么人不知道以弗所城是伟大阿蒂米丝庙和那从天落下的神像的看守者呢？", version_id: 6},
  {id: 159642, book: "Acts", verse: "19.036", unformatted: "所以这些事既然是不能否认的，你们就必须冷静下来，不要做任何妄为的事。", version_id: 6},
  {id: 159643, book: "Acts", verse: "19.037", unformatted: "你们带来的这些人，既没有偷取庙里的东西，也没有亵渎我们的女神。", version_id: 6},
  {id: 159644, book: "Acts", verse: "19.038", unformatted: "既然如此，如果迪米特斯和与他一起的工匠们要控告谁，法庭是开着的，省长也在，让他们彼此控告吧！", version_id: 6},
  {id: 159645, book: "Acts", verse: "19.039", unformatted: "但如果你们有什么别的要求，要在合法的集会中解决。", version_id: 6},
  {id: 159646, book: "Acts", verse: "19.04", unformatted: "关于今天的事，本来没有正当的理由，我们实际上就有被控告为暴乱的危险。我们无法对这次骚动做出交代。”", version_id: 6},
  {id: 159647, book: "Acts", verse: "19.041", unformatted: "说了这些话，他就遣散了集会的人。", version_id: 6},
  {id: 159648, book: "Acts", verse: "20.001", unformatted: "保罗在马其顿\n骚乱平息以后，保罗请门徒们来，鼓励一番，然后与他们告别，就出发往马其顿省去了。", version_id: 6},
  {id: 159649, book: "Acts", verse: "20.002", unformatted: "他走遍了那些地区，用很多的话鼓励门徒们，就来到希腊，", version_id: 6},
  {id: 159650, book: "Acts", verse: "20.003", unformatted: "在那里住了三个月。他正要起航往叙利亚省去的时候，犹太人设计了一个针对他的阴谋，他就决定从马其顿省回去。", version_id: 6},
  {id: 159651, book: "Acts", verse: "20.004", unformatted: "与他同行的，有庇里亚人普罗斯的儿子索巴特，帖撒罗尼迦人亚里达古和西肯多，也有代尔贝人盖尤斯和提摩太，还有亚细亚省人特其库斯和特罗费摩。", version_id: 6},
  {id: 159652, book: "Acts", verse: "20.005", unformatted: "这些人先走，在特罗阿斯港等我们。", version_id: 6},
  {id: 159653, book: "Acts", verse: "20.006", unformatted: "过了除酵节的日子，我们从腓立比起航，五天后到了特罗阿斯港见他们，在那里住了七天。", version_id: 6},
  {id: 159654, book: "Acts", verse: "20.007", unformatted: "特罗阿斯的犹图克斯复活\n在一周的头一天，门徒们聚集掰饼的时候，保罗向他们讲论，因第二天就要离开，于是一直讲到半夜。", version_id: 6},
  {id: 159655, book: "Acts", verse: "20.008", unformatted: "我们聚集的楼上房间，有许多灯火。", version_id: 6},
  {id: 159656, book: "Acts", verse: "20.009", unformatted: "有一个名叫犹图克斯的年轻人坐在窗台上，陷入了沉睡。保罗又讲论了很久，犹图克斯在沉睡中从三楼掉了下去，被扶起来的时候，已经死了。", version_id: 6},
  {id: 159657, book: "Acts", verse: "20.01", unformatted: "保罗下去，伏在他身上，抱着他说：“不要慌乱，他的灵魂还在身上！”", version_id: 6},
  {id: 159658, book: "Acts", verse: "20.011", unformatted: "保罗再上去，掰饼吃了，又交谈了许久，直到天亮才出发。", version_id: 6},
  {id: 159659, book: "Acts", verse: "20.012", unformatted: "他们把那活生生的年轻人带走，并且受到了不少的安慰。", version_id: 6},
  {id: 159660, book: "Acts", verse: "20.013", unformatted: "从特罗阿斯到米利都\n我们先上船开往阿索，要在那里接保罗；原来他这样安排，是因为自己要走陆路。", version_id: 6},
  {id: 159661, book: "Acts", verse: "20.014", unformatted: "当他在阿索与我们会合的时候，我们接他上船，来到了米蒂利尼。", version_id: 6},
  {id: 159662, book: "Acts", verse: "20.015", unformatted: "我们从那里开船，第二天到达了希俄斯岛的海域，过了一天在萨莫斯岛靠岸，再过一天来到米利都。", version_id: 6},
  {id: 159663, book: "Acts", verse: "20.016", unformatted: "原来保罗已经决定驶过以弗所城，免得在亚细亚省耽延时间，因为他急着赶路，如果有可能，但愿五旬节那一天已经在耶路撒冷了。", version_id: 6},
  {id: 159664, book: "Acts", verse: "20.017", unformatted: "告别以弗所的长老\n保罗从米利都派人到以弗所去，请教会的长老们来。", version_id: 6},
  {id: 159665, book: "Acts", verse: "20.018", unformatted: "他们来了以后，保罗对他们说：“你们知道，自从我踏进亚细亚省的第一天起，我一直怎样与你们共处——", version_id: 6},
  {id: 159666, book: "Acts", verse: "20.019", unformatted: "我在完全的谦卑中、泪水中、以及因犹太人的阴谋而带来的许多试炼中服事主；", version_id: 6},
  {id: 159667, book: "Acts", verse: "20.02", unformatted: "对你们有益的事，在公众场所和各家各户，没有一件我因畏缩而不传讲、不教导给你们；", version_id: 6},
  {id: 159668, book: "Acts", verse: "20.021", unformatted: "无论对犹太人或希腊人，我都郑重地劝诫他们要向神悔改，相信我们的主耶稣。", version_id: 6},
  {id: 159669, book: "Acts", verse: "20.022", unformatted: "“看，现在我灵里受到催逼，要去耶路撒冷。我不知道在那里将会遇到什么事，", version_id: 6},
  {id: 159670, book: "Acts", verse: "20.023", unformatted: "只知道在各城里圣灵向我郑重地做见证说，有捆锁和患难等着我。", version_id: 6},
  {id: 159671, book: "Acts", verse: "20.024", unformatted: "但是我不把自己的生命看为宝贵，为要跑尽我该跑的路程，完成从主耶稣所领受的服事工作，为神恩典的福音郑重地做见证。", version_id: 6},
  {id: 159672, book: "Acts", verse: "20.025", unformatted: "“我曾在你们中间走遍各地，宣讲神的国，但现在，看哪，我知道你们都不会再见到我的面了！", version_id: 6},
  {id: 159673, book: "Acts", verse: "20.026", unformatted: "所以我今天向你们见证：对于任何人的灭亡，我都是清白的！", version_id: 6},
  {id: 159674, book: "Acts", verse: "20.027", unformatted: "因为神的整个计划，我没有一样因畏缩而不传讲给你们。", version_id: 6},
  {id: 159675, book: "Acts", verse: "20.028", unformatted: "你们当为自己谨慎，也当为全体羊群谨慎。圣灵把你们放在这羊群里做监督，为要牧养神的教会，就是他用自己的血所赎来的。", version_id: 6},
  {id: 159676, book: "Acts", verse: "20.029", unformatted: "我知道在我离开以后，凶恶的豺狼将进入你们中间，不会顾惜羊群。", version_id: 6},
  {id: 159677, book: "Acts", verse: "20.03", unformatted: "你们自己当中也会有人起来，讲说一些歪曲的道理，要拉拢门徒跟随他们。", version_id: 6},
  {id: 159678, book: "Acts", verse: "20.031", unformatted: "所以你们要警醒，记住这三年来，我日夜不住地流着泪水劝诫你们每一个人。", version_id: 6},
  {id: 159679, book: "Acts", verse: "20.032", unformatted: "“现在我把你们交托给神和他恩典的话语。这话语能造就你们，并且在所有被分别为圣的人中间赐给你们那继业。", version_id: 6},
  {id: 159680, book: "Acts", verse: "20.033", unformatted: "我没有渴望过任何人的金子、银子或衣服。", version_id: 6},
  {id: 159681, book: "Acts", verse: "20.034", unformatted: "你们自己也知道，我这双手供应了我和与我在一起之人的需要。", version_id: 6},
  {id: 159682, book: "Acts", verse: "20.035", unformatted: "在一切事上，我都给你们做了榜样：必须这样地劳苦做工来扶助软弱的人，并且要记住主耶稣自己说过的话：‘施比受更加蒙福。’”", version_id: 6},
  {id: 159683, book: "Acts", verse: "20.036", unformatted: "保罗说了这些话，就跪下来与大家一起祷告。", version_id: 6},
  {id: 159684, book: "Acts", verse: "20.037", unformatted: "大家都痛哭起来，搂住保罗的脖子亲吻他。", version_id: 6},
  {id: 159685, book: "Acts", verse: "20.038", unformatted: "尤其使他们伤心的，就是他说他们不会再看到他面的那句话。然后他们送他上了船。", version_id: 6},
  {id: 159686, book: "Acts", verse: "21.001", unformatted: "劝阻耶路撒冷之行\n我们与他们惜别以后就开船直航，来到科斯岛。第二天到了罗得岛，又从那里到帕塔拉，", version_id: 6},
  {id: 159687, book: "Acts", verse: "21.002", unformatted: "找到了一条开往腓尼基的船，就上船起航。", version_id: 6},
  {id: 159688, book: "Acts", verse: "21.003", unformatted: "我们望见塞浦路斯岛，驶过的时候它在左边。我们继续向叙利亚省航行，在提尔登陆，因为船要在那里卸货。", version_id: 6},
  {id: 159689, book: "Acts", verse: "21.004", unformatted: "我们找到了一些门徒，就在那里住了七天。他们藉着圣灵，一直告诉保罗不要上耶路撒冷去。", version_id: 6},
  {id: 159690, book: "Acts", verse: "21.005", unformatted: "那些日子过了以后，我们出发前行，大家与妻子和儿女一起送我们到城外。我们都跪在岸上祷告，", version_id: 6},
  {id: 159691, book: "Acts", verse: "21.006", unformatted: "彼此告别。我们上了船，他们就回自己的家去了。", version_id: 6},
  {id: 159692, book: "Acts", verse: "21.007", unformatted: "我们结束了从提尔开始的航程，到达托勒密，就去问候弟兄们，在他们那里住了一天。", version_id: 6},
  {id: 159693, book: "Acts", verse: "21.008", unformatted: "第二天我们离开那里，来到凯撒里亚，进了传福音者腓利的家，住在他那里，他是那七位执事中的一位。", version_id: 6},
  {id: 159694, book: "Acts", verse: "21.009", unformatted: "他有四个女儿，都是童贞女，是做先知传道的。", version_id: 6},
  {id: 159695, book: "Acts", verse: "21.01", unformatted: "我们住了好几天，有一个名叫阿伽布斯的先知从犹太下来了。", version_id: 6},
  {id: 159696, book: "Acts", verse: "21.011", unformatted: "他来到我们这里，拿起保罗的腰带，把自己的手脚捆起来，说：“圣灵这样说，犹太人在耶路撒冷要如此捆绑这腰带的主人，把他交在外邦人的手中。”", version_id: 6},
  {id: 159697, book: "Acts", verse: "21.012", unformatted: "我们一听这些话，就和当地的人恳求保罗不要上耶路撒冷去。", version_id: 6},
  {id: 159698, book: "Acts", verse: "21.013", unformatted: "这时候保罗说：“你们为什么哭，使我心碎呢？我为了主耶稣的名，不仅被捆绑，即使死在耶路撒冷，也都预备好了。”", version_id: 6},
  {id: 159699, book: "Acts", verse: "21.014", unformatted: "我们既然不能说服保罗，就都安静下来，只说：“愿主的旨意成就。”", version_id: 6},
  {id: 159700, book: "Acts", verse: "21.015", unformatted: "有关外邦宣教的争议\n过了几天，我们收拾行李，上耶路撒冷去。", version_id: 6},
  {id: 159701, book: "Acts", verse: "21.016", unformatted: "凯撒里亚的一些门徒也陪我们一起去，带我们到一个塞浦路斯人纳森家里去作客住宿。他做门徒已经很久了。", version_id: 6},
  {id: 159702, book: "Acts", verse: "21.017", unformatted: "我们到了耶路撒冷，弟兄们很高兴地欢迎我们。", version_id: 6},
  {id: 159703, book: "Acts", verse: "21.018", unformatted: "第二天，保罗与我们一起去探望雅各，所有的长老也都来了。", version_id: 6},
  {id: 159704, book: "Acts", verse: "21.019", unformatted: "保罗问候他们后，一一述说神藉着他的服事，在外邦人中所做的事。", version_id: 6},
  {id: 159705, book: "Acts", verse: "21.02", unformatted: "他们听了就不住地荣耀神，又对保罗说：“弟兄，你看犹太人中信主的人有多少万，而且他们都是对律法热心的人。", version_id: 6},
  {id: 159706, book: "Acts", verse: "21.021", unformatted: "他们听到有关你的事，说你教导所有在外邦人中的犹太人离道反教、背弃摩西，叫他们不要给孩子行割礼，也不要按规矩行事。", version_id: 6},
  {id: 159707, book: "Acts", verse: "21.022", unformatted: "他们一定会听到你来这里了。那么该怎么办呢？", version_id: 6},
  {id: 159708, book: "Acts", verse: "21.023", unformatted: "你就照我们告诉你的去做吧。我们这里有四个人，他们都许过愿。", version_id: 6},
  {id: 159709, book: "Acts", verse: "21.024", unformatted: "你带他们去，与他们一起行洁净礼，并且替他们付钱，好让他们剃掉头发。这样，大家就会知道有关你的传闻不是真的，而且你自己也是照着规矩行事、遵守律法的。", version_id: 6},
  {id: 159710, book: "Acts", verse: "21.025", unformatted: "至于已经相信的外邦人，我们照着决定写了信，要他们远避祭过偶像的食物，远避血和勒死的动物，远避淫乱。”", version_id: 6},
  {id: 159711, book: "Acts", verse: "21.026", unformatted: "圣殿里的骚乱\n于是，第二天保罗带了那四个人，与他们一起行了洁净礼，进了圣殿，宣告洁净期满的日子，直到为他们每一个人的供物都被献上为止。", version_id: 6},
  {id: 159712, book: "Acts", verse: "21.027", unformatted: "那七天快要结束的时候，从亚细亚省来的一些犹太人看到保罗在圣殿里，就煽动整个人群，下手捉拿他，", version_id: 6},
  {id: 159713, book: "Acts", verse: "21.028", unformatted: "喊叫：“各位以色列人哪，请帮忙！这个人就是那到处教导大家反对我们的民族、律法，反对这地方的！他甚至把希腊人带进圣殿，玷污了这圣地。”", version_id: 6},
  {id: 159714, book: "Acts", verse: "21.029", unformatted: "原来他们曾经看见以弗所人特罗费摩与保罗一起在城里，就以为保罗带他进了圣殿。", version_id: 6},
  {id: 159715, book: "Acts", verse: "21.03", unformatted: "于是全城的人都被煽动了，民众一起跑来，抓住保罗，把他拖出圣殿，殿门立刻关上了。", version_id: 6},
  {id: 159716, book: "Acts", verse: "21.031", unformatted: "在他们想杀保罗的时候，有消息上报到军团的千夫长，说全耶路撒冷都混乱了。", version_id: 6},
  {id: 159717, book: "Acts", verse: "21.032", unformatted: "千夫长立即带着士兵和几个百夫长跑下去，到了他们那里。他们一看见千夫长和士兵们，就停止殴打保罗。", version_id: 6},
  {id: 159718, book: "Acts", verse: "21.033", unformatted: "于是千夫长上前抓住保罗，下令用两条铁链把他捆起来，然后查问他到底是什么人，做了什么事。", version_id: 6},
  {id: 159719, book: "Acts", verse: "21.034", unformatted: "人群当中，有的喊这个，有的喊那个；千夫长因为骚乱，无法知道真相，就下令把保罗带到营楼里去。", version_id: 6},
  {id: 159720, book: "Acts", verse: "21.035", unformatted: "保罗到了台阶上的时候，由于人群凶猛，士兵们只好把他抬起来走。", version_id: 6},
  {id: 159721, book: "Acts", verse: "21.036", unformatted: "那一群人跟在后面，喊叫：“除掉他！”", version_id: 6},
  {id: 159722, book: "Acts", verse: "21.037", unformatted: "保罗对群众的讲话\n保罗快要被押进营楼的时候，就对千夫长说：“我是否可以对你说句话？”\n千夫长说：“你懂希腊语吗？", version_id: 6},
  {id: 159723, book: "Acts", verse: "21.038", unformatted: "你究竟是不是那个前些日子作乱、带领四千个凶徒进入旷野的埃及人呢？”", version_id: 6},
  {id: 159724, book: "Acts", verse: "21.039", unformatted: "保罗回答说：“我是犹太人，是奇里乞亚省的塔尔苏人，并不是无名之城的公民，请求你准许我向民众说话。”", version_id: 6},
  {id: 159725, book: "Acts", verse: "21.04", unformatted: "千夫长准许了，保罗就站在台阶上，向民众挥一下手，大家都寂静无声，保罗就用希伯来语呼吁，说：", version_id: 6},
  {id: 159726, book: "Acts", verse: "22.001", unformatted: "“各位父老兄弟，现在请听我的申辩。”", version_id: 6},
  {id: 159727, book: "Acts", verse: "22.002", unformatted: "众人听见他用希伯来语向他们呼吁，就更加安静了。保罗说：", version_id: 6},
  {id: 159728, book: "Acts", verse: "22.003", unformatted: "“我是犹太人，出生在奇里乞亚省的塔尔苏，在这城里长大；在迦玛列门下，按照我们先祖的律法受了严格的教育；我对神是热心的人，就像今天你们大家一样。", version_id: 6},
  {id: 159729, book: "Acts", verse: "22.004", unformatted: "我曾经逼迫这道的信徒置于死地，无论男女都捆绑起来，关进监狱。", version_id: 6},
  {id: 159730, book: "Acts", verse: "22.005", unformatted: "这是大祭司和全长老议会的人都可以为我做见证的。我还从他们那里拿到了给同胞们的一些信函往大马士革去，要把那里被捆绑的信徒们，带到耶路撒冷来受刑。", version_id: 6},
  {id: 159731, book: "Acts", verse: "22.006", unformatted: "保罗的见证\n“当我赶路快到大马士革的时候，大约中午，忽然有强烈的光从天上下来，在我周围四面照射。", version_id: 6},
  {id: 159732, book: "Acts", verse: "22.007", unformatted: "我就仆倒在地上，听见有声音对我说：‘扫罗，扫罗！你为什么逼迫我？’", version_id: 6},
  {id: 159733, book: "Acts", verse: "22.008", unformatted: "“我回答：‘主啊！你到底是谁？’\n“他说：‘我就是你所逼迫的拿撒勒人耶稣。’", version_id: 6},
  {id: 159734, book: "Acts", verse: "22.009", unformatted: "与我在一起的人虽然看到了那光，却听不懂那对我说话的声音。", version_id: 6},
  {id: 159735, book: "Acts", verse: "22.01", unformatted: "“我说：‘主啊！我该做什么呢？’\n“主对我说：‘起来，进大马士革城去，在那里你将被告知所指定你做的一切事。’", version_id: 6},
  {id: 159736, book: "Acts", verse: "22.011", unformatted: "“我因那光的荣耀，什么都看不见了，就被同行的人牵着手，进了大马士革。", version_id: 6},
  {id: 159737, book: "Acts", verse: "22.012", unformatted: "有一个名叫阿纳尼亚的人，按照律法，是一个虔诚的人，受到所有住在那里的犹太人赞许。", version_id: 6},
  {id: 159738, book: "Acts", verse: "22.013", unformatted: "他来到我那里，站在旁边对我说：‘扫罗弟兄，你重见光明吧！’就在那一刻，我抬头看见他了。", version_id: 6},
  {id: 159739, book: "Acts", verse: "22.014", unformatted: "他又说：‘我们祖先的神预先指定了你，使你明白他的旨意，看见那位义者，听见他口中传来的声音，", version_id: 6},
  {id: 159740, book: "Acts", verse: "22.015", unformatted: "因为你将要做他的见证人，向所有的人见证你所看到的、你所听到的。", version_id: 6},
  {id: 159741, book: "Acts", verse: "22.016", unformatted: "现在你还等什么呢？起来，当受洗！求告他的名，当洗去你的罪孽！’", version_id: 6},
  {id: 159742, book: "Acts", verse: "22.017", unformatted: "“后来，我回到耶路撒冷，在圣殿里祷告的时候，魂游象外，", version_id: 6},
  {id: 159743, book: "Acts", verse: "22.018", unformatted: "看见主对我说：‘快！赶快离开耶路撒冷，因为他们不会接受你为我做的见证。’", version_id: 6},
  {id: 159744, book: "Acts", verse: "22.019", unformatted: "“我就说：‘主啊，他们知道我曾经到各会堂，把信靠你的人关进监狱，还鞭打他们。", version_id: 6},
  {id: 159745, book: "Acts", verse: "22.02", unformatted: "并且当你的见证人司提反受害流血的时候，我自己也站在旁边赞同，还为那些杀害他的人看守衣服。’", version_id: 6},
  {id: 159746, book: "Acts", verse: "22.021", unformatted: "“主对我说：‘去吧！我要派你到遥远的外邦人那里去。’”", version_id: 6},
  {id: 159747, book: "Acts", verse: "22.022", unformatted: "保罗的罗马公民权\n众人听保罗讲到这句话，就高声说：“这样的人，把他从地上除掉，因为他不该活着！”", version_id: 6},
  {id: 159748, book: "Acts", verse: "22.023", unformatted: "他们喊叫，抛着衣服，向空中扬起尘土。", version_id: 6},
  {id: 159749, book: "Acts", verse: "22.024", unformatted: "千夫长下令把保罗押进营楼，吩咐鞭打拷问他，要了解民众是因什么理由向他这样一直喊叫。", version_id: 6},
  {id: 159750, book: "Acts", verse: "22.025", unformatted: "当士兵们用皮条捆上保罗的时候，他对站在旁边的百夫长说：“你们鞭打一个没有被定罪的罗马公民，可以吗？”", version_id: 6},
  {id: 159751, book: "Acts", verse: "22.026", unformatted: "百夫长听了，就来到千夫长面前，报告说：“你要做什么呢？要知道，这个人是罗马公民。”", version_id: 6},
  {id: 159752, book: "Acts", verse: "22.027", unformatted: "千夫长就前来问保罗：“告诉我，你是罗马公民吗？”\n保罗说：“是的。”", version_id: 6},
  {id: 159753, book: "Acts", verse: "22.028", unformatted: "千夫长说：“我花了一大笔钱，才买到了罗马公民权。”\n保罗说：“我生下来就是。”", version_id: 6},
  {id: 159754, book: "Acts", verse: "22.029", unformatted: "于是那些要拷问他的人，立刻退下了。千夫长既知道他是罗马公民，又因为捆绑了他，就惧怕起来。", version_id: 6},
  {id: 159755, book: "Acts", verse: "22.03", unformatted: "保罗在议会受审\n第二天，千夫长想确切知道保罗为什么被犹太人控告，就解开他，下令召集祭司长们和全议会的人，然后把保罗带来，叫他站在他们面前。", version_id: 6},
  {id: 159756, book: "Acts", verse: "23.001", unformatted: "保罗注视着议会的人，说：“各位兄弟，直到今天，我在神面前，行事为人一向都凭着无愧的良心。”", version_id: 6},
  {id: 159757, book: "Acts", verse: "23.002", unformatted: "大祭司阿纳尼亚就命令站在保罗旁边的人，打他的嘴巴。", version_id: 6},
  {id: 159758, book: "Acts", verse: "23.003", unformatted: "保罗就对他说：“你这粉饰的墙啊，神将要打你！你坐在那里应按照律法审判我，而你却违背律法，下令打我吗？”", version_id: 6},
  {id: 159759, book: "Acts", verse: "23.004", unformatted: "站在旁边的人说：“你竟敢辱骂神的大祭司？”", version_id: 6},
  {id: 159760, book: "Acts", verse: "23.005", unformatted: "保罗就说：“同胞们，我不知道他是大祭司。的确，经上记着：不可说你民中掌权者的坏话。”", version_id: 6},
  {id: 159761, book: "Acts", verse: "23.006", unformatted: "保罗知道有一部分人是撒都该人，另一部分是法利赛人，就在议会中喊着：“各位兄弟，我是法利赛人，也是法利赛人的儿子。我现在受审，是与死人复活的盼望有关。”", version_id: 6},
  {id: 159762, book: "Acts", verse: "23.007", unformatted: "他说了这话，法利赛人和撒都该人就起了争执，会众也分裂了。", version_id: 6},
  {id: 159763, book: "Acts", verse: "23.008", unformatted: "原来撒都该人说没有复活，没有天使，也没有灵，但法利赛人却承认这一切。", version_id: 6},
  {id: 159764, book: "Acts", verse: "23.009", unformatted: "于是他们大大地喧嚷起来。有些法利赛派的经文士们站起来激烈地争辩，说：“我们查不出这个人有什么错。也许有灵或天使对他说过话呢！”", version_id: 6},
  {id: 159765, book: "Acts", verse: "23.01", unformatted: "争执越来越激烈，千夫长担心保罗会被他们撕碎，就命令军队下去，把保罗从他们当中抢出来，带回营楼里。", version_id: 6},
  {id: 159766, book: "Acts", verse: "23.011", unformatted: "阴谋杀害保罗\n当夜，主站在保罗旁边，说：“你要鼓起勇气！你怎样在耶路撒冷郑重地见证有关我的事，也必须照样在罗马做见证。”", version_id: 6},
  {id: 159767, book: "Acts", verse: "23.012", unformatted: "天亮了，犹太人就策划了一起阴谋，并且赌咒起誓说，杀掉保罗以前，就不吃不喝。", version_id: 6},
  {id: 159768, book: "Acts", verse: "23.013", unformatted: "策划这同谋起誓的有四十多人。", version_id: 6},
  {id: 159769, book: "Acts", verse: "23.014", unformatted: "他们来到祭司长们和长老们那里，说：“我们已经赌咒起誓，杀掉保罗以前，就什么都不吃。", version_id: 6},
  {id: 159770, book: "Acts", verse: "23.015", unformatted: "所以你们和议会现在就要告诉千夫长，把保罗带到你们这里来，假装要更详尽地审查他的事；我们预备好了，要在他到达以前就杀了他。”", version_id: 6},
  {id: 159771, book: "Acts", verse: "23.016", unformatted: "保罗的外甥听见了这埋伏的事，就过来进了营楼告诉保罗。", version_id: 6},
  {id: 159772, book: "Acts", verse: "23.017", unformatted: "保罗请来了一个百夫长，说：“请带这年轻人到千夫长那里，因为他有事要向他报告。”", version_id: 6},
  {id: 159773, book: "Acts", verse: "23.018", unformatted: "于是百夫长就带保罗的外甥到千夫长那里去，说：“囚犯保罗请了我去，求我把这个年轻人带到你这里来，他有事要告诉你。”", version_id: 6},
  {id: 159774, book: "Acts", verse: "23.019", unformatted: "千夫长拉着年轻人的手，退到一边，悄悄地问他：“你有什么事要告诉我？”", version_id: 6},
  {id: 159775, book: "Acts", verse: "23.02", unformatted: "他说：“犹太人已经说好，要请求你明天把保罗带到议会去，假装要更详尽地查问他的事。", version_id: 6},
  {id: 159776, book: "Acts", verse: "23.021", unformatted: "所以请你不要被他们说服，因为他们中的四十多人正埋伏等着他。他们已经赌咒起誓说，杀掉保罗以前，就不吃不喝。现在他们预备好了，正期望你答应。”", version_id: 6},
  {id: 159777, book: "Acts", verse: "23.022", unformatted: "千夫长吩咐那年轻人说：“不要对任何人说，你把这些事告诉了我。”于是，就让他走了。", version_id: 6},
  {id: 159778, book: "Acts", verse: "23.023", unformatted: "连夜解送到凯撒里亚\n然后千夫长叫来两个百夫长，说：“预备两百名步兵、七十名骑兵、两百名长枪手，晚上九点出发往凯撒里亚去。", version_id: 6},
  {id: 159779, book: "Acts", verse: "23.024", unformatted: "也要预备牲口，让保罗骑上，护送到总督菲利克斯那里去。”", version_id: 6},
  {id: 159780, book: "Acts", verse: "23.025", unformatted: "他写了一封信函，有这样的内容：", version_id: 6},
  {id: 159781, book: "Acts", verse: "23.026", unformatted: "“克劳迪吕西亚问候总督菲利克斯大人。", version_id: 6},
  {id: 159782, book: "Acts", verse: "23.027", unformatted: "这个人被犹太人抓住，正要被他们杀害的时候，我带军队到场，得知他是罗马公民，就把他救了出来。", version_id: 6},
  {id: 159783, book: "Acts", verse: "23.028", unformatted: "我想了解犹太人控告他的理由，就带他到他们的议会去。", version_id: 6},
  {id: 159784, book: "Acts", verse: "23.029", unformatted: "我发现他被控告，与他们律法上的争议问题有关，并没有任何该死或该被捆锁的罪行。", version_id: 6},
  {id: 159785, book: "Acts", verse: "23.03", unformatted: "后来我被告知将有一个针对这个人的阴谋，就立即把他解送到您那里去，又吩咐原告们到您面前去告他。”", version_id: 6},
  {id: 159786, book: "Acts", verse: "23.031", unformatted: "于是士兵们按照命令，把保罗接走，连夜送到安提帕底。", version_id: 6},
  {id: 159787, book: "Acts", verse: "23.032", unformatted: "第二天，他们让骑兵带着保罗前行，就回到营楼。", version_id: 6},
  {id: 159788, book: "Acts", verse: "23.033", unformatted: "骑兵进了凯撒里亚，把信函呈给总督，又把保罗带到他面前。", version_id: 6},
  {id: 159789, book: "Acts", verse: "23.034", unformatted: "总督读了信，问保罗是哪一个省的人，知道他是奇里乞亚省的人，", version_id: 6},
  {id: 159790, book: "Acts", verse: "23.035", unformatted: "就说：“等你的原告们也到了，我会详细地听你的事。”他下令把保罗看守在希律的王府里。", version_id: 6},
  {id: 159791, book: "Acts", verse: "24.001", unformatted: "对保罗的控告\n过了五天，大祭司阿纳尼亚带着一些长老和一个名叫特图罗斯的律师下来，向总督指控保罗。", version_id: 6},
  {id: 159792, book: "Acts", verse: "24.002", unformatted: "保罗被叫进来，特图罗斯就开始控告，说：“菲利克斯大人！因着您，我们大享和平；因着您的远见，本国也进行了许多改革；", version_id: 6},
  {id: 159793, book: "Acts", verse: "24.003", unformatted: "我们事事处处满怀感谢地领受。", version_id: 6},
  {id: 159794, book: "Acts", verse: "24.004", unformatted: "但为了不过多地烦扰您，我恳求您，以您的仁慈听我们简短地说。", version_id: 6},
  {id: 159795, book: "Acts", verse: "24.005", unformatted: "我们发现这个人像瘟疫一样，煽动普天下犹太人暴乱；他又是拿撒勒人教派的一个头目。", version_id: 6},
  {id: 159796, book: "Acts", verse: "24.006", unformatted: "他甚至试图玷污圣殿，我们就把他拘捕了。我们本来想按照我们的律法审判他，", version_id: 6},
  {id: 159797, book: "Acts", verse: "24.007", unformatted: "可是千夫长吕西亚过来，把他从我们手中强行带走，又吩咐原告到您这里来。", version_id: 6},
  {id: 159798, book: "Acts", verse: "24.008", unformatted: "您亲自审问他，就能了解我们控告他的这一切事了。”", version_id: 6},
  {id: 159799, book: "Acts", verse: "24.009", unformatted: "犹太人也都附和，声称这些事就是如此。", version_id: 6},
  {id: 159800, book: "Acts", verse: "24.01", unformatted: "在菲利克斯面前辩解\n总督点头示意要保罗讲话，保罗就说：“我知道您在本国多年做审判官，所以我乐意为自己申辩。", version_id: 6},
  {id: 159801, book: "Acts", verse: "24.011", unformatted: "您可以了解，自从我上耶路撒冷去敬拜，到现在不超过十二天。", version_id: 6},
  {id: 159802, book: "Acts", verse: "24.012", unformatted: "无论在圣殿里，或在会堂里，或在城里，他们都没有看见我和任何人争论，或煽动民众。", version_id: 6},
  {id: 159803, book: "Acts", verse: "24.013", unformatted: "他们现在也不能向您证实所控告我的事。", version_id: 6},
  {id: 159804, book: "Acts", verse: "24.014", unformatted: "不过这一点我向您承认：我是按照他们称为‘异端’的这道，如此事奉我先祖的神，因为我相信一切合乎律法书和先知书上所记载的事；", version_id: 6},
  {id: 159805, book: "Acts", verse: "24.015", unformatted: "我对神所怀有的盼望也是这些人自己所期望的，就是义人和不义的人将来都要复活。", version_id: 6},
  {id: 159806, book: "Acts", verse: "24.016", unformatted: "因此，在神和人面前，我总是尽力存着无愧的良心。", version_id: 6},
  {id: 159807, book: "Acts", verse: "24.017", unformatted: "过了好几年，我回来了，带着给我同胞的施舍，以及供物；", version_id: 6},
  {id: 159808, book: "Acts", verse: "24.018", unformatted: "正献上供物的时候，他们在圣殿里发现了我，那时我已经行完了洁净礼，既没有人群，也没有骚乱，", version_id: 6},
  {id: 159809, book: "Acts", verse: "24.019", unformatted: "只有一些从亚细亚省来的犹太人——如果对我有什么控告，应该是他们到您面前来控告。", version_id: 6},
  {id: 159810, book: "Acts", verse: "24.02", unformatted: "还是让这些人自己说说，我站在议会前的时候，他们到底查出了我有什么罪行——", version_id: 6},
  {id: 159811, book: "Acts", verse: "24.021", unformatted: "或者，是为了我站在他们中间喊过这样一句话：‘我今天在你们面前受审，是因为死人复活的事！’”", version_id: 6},
  {id: 159812, book: "Acts", verse: "24.022", unformatted: "判决被拖延\n因为菲利克斯比较详尽地了解这道，就推托他们，说：“等千夫长吕西亚下来，我再审断有关你们的事。”", version_id: 6},
  {id: 159813, book: "Acts", verse: "24.023", unformatted: "他吩咐百夫长看守保罗，要宽待他，不要阻止他自己的人来供应他。", version_id: 6},
  {id: 159814, book: "Acts", verse: "24.024", unformatted: "过了几天，菲利克斯与他的犹太人妻子杜茜拉一起来了。他把保罗叫来，听他讲说对基督耶稣的信仰。", version_id: 6},
  {id: 159815, book: "Acts", verse: "24.025", unformatted: "当保罗讲论关于公义、自制和将来的审判时，菲利克斯感到害怕，就说：“你现在可以回去，我有了时间，就会叫你。”", version_id: 6},
  {id: 159816, book: "Acts", verse: "24.026", unformatted: "同时他也希望保罗送他钱，所以经常把他叫来，和他交谈。", version_id: 6},
  {id: 159817, book: "Acts", verse: "24.027", unformatted: "过了两年，波奇乌菲斯特斯接替菲利克斯。菲利克斯想要讨好犹太人，就把保罗留在监狱里。", version_id: 6},
  {id: 159818, book: "Acts", verse: "25.001", unformatted: "向凯撒上诉\n菲斯特斯到省里上任第三天，就从凯撒里亚上耶路撒冷去。", version_id: 6},
  {id: 159819, book: "Acts", verse: "25.002", unformatted: "祭司长们和犹太人的首领们向菲斯特斯指控保罗，并且恳求菲斯特斯，", version_id: 6},
  {id: 159820, book: "Acts", verse: "25.003", unformatted: "请他恩准，把保罗叫到耶路撒冷来。他们策划要在路上埋伏杀了保罗。", version_id: 6},
  {id: 159821, book: "Acts", verse: "25.004", unformatted: "于是菲斯特斯说：“保罗正被拘留在凯撒里亚，我自己也很快就要回去。”", version_id: 6},
  {id: 159822, book: "Acts", verse: "25.005", unformatted: "他又说：“所以你们中间有权的人都与我一起下去；如果这个人有什么过错，就当控告他。”", version_id: 6},
  {id: 159823, book: "Acts", verse: "25.006", unformatted: "菲斯特斯在他们那里住了不超过十天八天，就下到凯撒里亚去了。第二天，他坐在审判席上，下令把保罗带上来。", version_id: 6},
  {id: 159824, book: "Acts", verse: "25.007", unformatted: "保罗一到，从耶路撒冷下来的那些犹太人就站在他周围，用很多严重的罪状来控告他，可是都不能证实。", version_id: 6},
  {id: 159825, book: "Acts", verse: "25.008", unformatted: "保罗申辩说：“无论是对犹太人的律法，或是对圣殿，或是对凯撒，我都没有犯过罪。”", version_id: 6},
  {id: 159826, book: "Acts", verse: "25.009", unformatted: "但菲斯特斯想要讨好犹太人，就问保罗说：“你愿意上耶路撒冷，让我在那里审判这些事吗？”", version_id: 6},
  {id: 159827, book: "Acts", verse: "25.01", unformatted: "保罗就说：“我已经站在凯撒的审判台前，这里就是我应该受审的地方。我没有亏负过犹太人，就像您也很清楚地了解那样。", version_id: 6},
  {id: 159828, book: "Acts", verse: "25.011", unformatted: "既然如此，如果我行了什么不义的事，犯了什么该死的罪，就是死我也不拒绝；但如果这些人对我的控告不是真的，那么谁也不能把我交给他们。我向凯撒上诉！”", version_id: 6},
  {id: 159829, book: "Acts", verse: "25.012", unformatted: "菲斯特斯与参议会商量以后，就回答：“你已经向凯撒上诉了，你就将到凯撒那里去！”", version_id: 6},
  {id: 159830, book: "Acts", verse: "25.013", unformatted: "阿格里帕王与百妮基来访\n过了几天，阿格里帕王和百妮基到达凯撒里亚，问候菲斯特斯。", version_id: 6},
  {id: 159831, book: "Acts", verse: "25.014", unformatted: "他们在那里住了好几天，菲斯特斯就向阿格里帕王陈述了有关保罗的事，说：“这里有一个人，是菲利克斯留下的囚犯。", version_id: 6},
  {id: 159832, book: "Acts", verse: "25.015", unformatted: "我到耶路撒冷去的时候，犹太人的祭司长们和长老们指控他，要求我定他的罪。", version_id: 6},
  {id: 159833, book: "Acts", verse: "25.016", unformatted: "我回答他们：在被告还没有与原告当面对质，并且对被控罪名还没有获得申辩机会以前，就把被告交出去，这不符合罗马人的规矩。", version_id: 6},
  {id: 159834, book: "Acts", verse: "25.017", unformatted: "因此，当他们聚集在这里的时候，我没有耽搁，第二天就坐在审判席上，下令把那个人带来。", version_id: 6},
  {id: 159835, book: "Acts", verse: "25.018", unformatted: "那些原告站起来围着他提出来的罪状，并不是我所认为的恶事。", version_id: 6},
  {id: 159836, book: "Acts", verse: "25.019", unformatted: "不过他们与他有一些争议的问题，是关于自己宗教的事，以及关于一个叫耶稣的人——这个人已经死了，保罗却声称他还活着。", version_id: 6},
  {id: 159837, book: "Acts", verse: "25.02", unformatted: "我对这些辩论也心里困惑，就问保罗是否愿意上耶路撒冷去，在那里为这些事受审。", version_id: 6},
  {id: 159838, book: "Acts", verse: "25.021", unformatted: "可是保罗请求把他留给皇帝审断，我就下令把他留下，等着我送他到凯撒那里去。”", version_id: 6},
  {id: 159839, book: "Acts", verse: "25.022", unformatted: "阿格里帕对菲斯特斯说：“我本来也想亲自听这个人讲说。”\n菲斯特斯说：“明天你就可以听他讲说。”", version_id: 6},
  {id: 159840, book: "Acts", verse: "25.023", unformatted: "在阿格里帕王面前受审\n第二天，阿格里帕和百妮基大张声势而来，当他们与几个军官和城里的显要人物一起进了大厅，菲斯特斯一声下令，保罗就被带上来。", version_id: 6},
  {id: 159841, book: "Acts", verse: "25.024", unformatted: "菲斯特斯说：“阿格里帕王和所有在场的各位，你们所看见的这个人，犹太的全体民众为了他，在耶路撒冷和这里向我陈情，喊着说他不应该再活下去。", version_id: 6},
  {id: 159842, book: "Acts", verse: "25.025", unformatted: "但是我了解他并没有犯过什么该死的罪，不过他自己既然向皇帝上诉了，我就决定把他解送去。", version_id: 6},
  {id: 159843, book: "Acts", verse: "25.026", unformatted: "关于这个人，我没有任何确切的事可以陈奏皇帝。因此我把他带到你们面前，尤其在您阿格里帕王面前，好使审查结束时，我可以有所陈奏，", version_id: 6},
  {id: 159844, book: "Acts", verse: "25.027", unformatted: "因为我认为，解送囚犯而不指明对他的罪状，是没有道理的。”", version_id: 6},
  {id: 159845, book: "Acts", verse: "26.001", unformatted: "在阿格里帕王面前辩解\n阿格里帕对保罗说：“你获准为自己说话。”\n于是保罗伸手开始申辩：", version_id: 6},
  {id: 159846, book: "Acts", verse: "26.002", unformatted: "“阿格里帕王啊，关于犹太人控告我的一切事，今天要在您面前申辩，我认为自己是蒙福的，", version_id: 6},
  {id: 159847, book: "Acts", verse: "26.003", unformatted: "尤其因为您熟悉犹太人一切的规矩和争议的问题，所以我请求您耐心听我说。", version_id: 6},
  {id: 159848, book: "Acts", verse: "26.004", unformatted: "“事实上，我自幼的为人，从起初在自己的同胞中间，以及在耶路撒冷，所有的犹太人都知道。", version_id: 6},
  {id: 159849, book: "Acts", verse: "26.005", unformatted: "他们从一开始就已经认识我，如果他们愿意就可以见证：我是按着我们宗教中最严格的教派，过着法利赛人的生活。", version_id: 6},
  {id: 159850, book: "Acts", verse: "26.006", unformatted: "现在我站在这里受审，是因为盼望神已经赐给我们祖先的那应许。", version_id: 6},
  {id: 159851, book: "Acts", verse: "26.007", unformatted: "为了那应许，我们十二个支派日夜热切地事奉神，盼望得到它。王啊，我被犹太人控告，就是为了这盼望。", version_id: 6},
  {id: 159852, book: "Acts", verse: "26.008", unformatted: "你们为什么判断神使死人复活的事为不可信的呢？", version_id: 6},
  {id: 159853, book: "Acts", verse: "26.009", unformatted: "事实上，我自己曾经认为必须多做反对拿撒勒人耶稣之名的事，", version_id: 6},
  {id: 159854, book: "Acts", verse: "26.01", unformatted: "我在耶路撒冷也是这样做的。我不但从祭司长们得了权柄把许多圣徒关进监狱，而且当他们被杀的时候，我也投了一票。", version_id: 6},
  {id: 159855, book: "Acts", verse: "26.011", unformatted: "我在各会堂里，经常用刑逼他们说亵渎的话，又越发恼恨他们，甚至到国外的城市去逼迫他们。", version_id: 6},
  {id: 159856, book: "Acts", verse: "26.012", unformatted: "保罗见证信主经历与使命\n“那时我得到祭司长们的授权和委任往大马士革去。", version_id: 6},
  {id: 159857, book: "Acts", verse: "26.013", unformatted: "王啊，约在正午，我在路上的时候，看见一道光，比太阳更明亮，从天上四面照射我和与我同行的人。", version_id: 6},
  {id: 159858, book: "Acts", verse: "26.014", unformatted: "我们都仆倒在地。我听见有声音用希伯来语对我说：‘扫罗，扫罗！你为什么逼迫我？你脚踢刺棒，是狂妄的。’", version_id: 6},
  {id: 159859, book: "Acts", verse: "26.015", unformatted: "“我问：‘主啊，你到底是谁？’\n“主说：‘我就是你所逼迫的耶稣。", version_id: 6},
  {id: 159860, book: "Acts", verse: "26.016", unformatted: "你起来，用你的两脚站好！要知道，我向你显现是为这样一个目的：预先指定你做仆人和见证人，就是为你所看见的事，以及我将要显明给你的事做见证人。", version_id: 6},
  {id: 159861, book: "Acts", verse: "26.017", unformatted: "我要从以色列子民和外邦人当中把你救出来。我差派你到他们那里去，", version_id: 6},
  {id: 159862, book: "Acts", verse: "26.018", unformatted: "为了开启他们的眼睛，使他们从黑暗归向光明，从撒旦的权势下归向神，使他们因着信我，罪得赦免，并且与那些被分别为圣的人同得继业。’", version_id: 6},
  {id: 159863, book: "Acts", verse: "26.019", unformatted: "“故此，阿格里帕王啊，我没有悖逆那从天上来的异象。", version_id: 6},
  {id: 159864, book: "Acts", verse: "26.02", unformatted: "我首先在大马士革，然后在耶路撒冷、犹太全地以及外邦人中，告诫人们要悔改，归向神，行事与悔改的心相称。", version_id: 6},
  {id: 159865, book: "Acts", verse: "26.021", unformatted: "犹太人就是因这缘故，在圣殿里抓住我，想要杀害我。", version_id: 6},
  {id: 159866, book: "Acts", verse: "26.022", unformatted: "然而我蒙了从神而来的帮助，直到今天仍能站立得住，不但向卑微的，还向尊贵的做见证。我所讲的，不外乎先知们和摩西所说的那些将要成就的事，", version_id: 6},
  {id: 159867, book: "Acts", verse: "26.023", unformatted: "就是基督要受难，要成为第一个从死人中复活的人，要把光传给这子民和外邦人。”", version_id: 6},
  {id: 159868, book: "Acts", verse: "26.024", unformatted: "听众的反应\n保罗这样申辩的时候，菲斯特斯大声说：“保罗，你疯了！你学问太大，使你癫狂了。”", version_id: 6},
  {id: 159869, book: "Acts", verse: "26.025", unformatted: "保罗说：“菲斯特斯大人，我没有疯。我说的都是真实、合理的话。", version_id: 6},
  {id: 159870, book: "Acts", verse: "26.026", unformatted: "其实王知道这些事，我才对王放胆直言。我深信这些事没有一件瞒得过王，因为这不是在角落里做的。", version_id: 6},
  {id: 159871, book: "Acts", verse: "26.027", unformatted: "阿格里帕王啊，您相信先知吗？我知道您是相信的。”", version_id: 6},
  {id: 159872, book: "Acts", verse: "26.028", unformatted: "阿格里帕对保罗说：“你用几句话就要说服我成为基督徒吗？”", version_id: 6},
  {id: 159873, book: "Acts", verse: "26.029", unformatted: "保罗说：“无论话多话少，我向神祈求的，不仅是您，还有今天所有听我讲话的人都能成为像我这样的人，只是不要有这些锁链！”", version_id: 6},
  {id: 159874, book: "Acts", verse: "26.03", unformatted: "于是王和总督，还有百妮基以及与他们坐在一起的人，都站起来了。", version_id: 6},
  {id: 159875, book: "Acts", verse: "26.031", unformatted: "当离开的时候，彼此谈论说：“这个人没有做任何该死或该被捆锁的事。”", version_id: 6},
  {id: 159876, book: "Acts", verse: "26.032", unformatted: "阿格里帕对菲斯特斯说：“这个人如果没有向凯撒上诉，早就可以释放了。”", version_id: 6},
  {id: 159877, book: "Acts", verse: "27.001", unformatted: "往罗马出航\n既然决定了我们要坐船往意大利去，他们就把保罗和其他一些囚犯交给皇家军团的一个名叫犹利乌斯的百夫长。", version_id: 6},
  {id: 159878, book: "Acts", verse: "27.002", unformatted: "有一条阿德米提的船，要开往亚细亚省沿岸各地，我们就上那船出发了。有一个从帖撒罗尼迦来的马其顿人亚里达古，与我们在一起。", version_id: 6},
  {id: 159879, book: "Acts", verse: "27.003", unformatted: "第二天，我们在西顿靠岸。犹利乌斯宽待保罗，准许他到朋友那里去接受照顾。", version_id: 6},
  {id: 159880, book: "Acts", verse: "27.004", unformatted: "我们从那里起航，因为逆风，就沿着塞浦路斯岛的背风岸航行。", version_id: 6},
  {id: 159881, book: "Acts", verse: "27.005", unformatted: "度过了奇里乞亚省和潘菲利亚省一带的海域，在吕基亚省的每拉登陆。", version_id: 6},
  {id: 159882, book: "Acts", verse: "27.006", unformatted: "百夫长在那里找到了一条亚历山大的船，要开往意大利去，就安排我们上了那船。", version_id: 6},
  {id: 159883, book: "Acts", verse: "27.007", unformatted: "一连好几天，船航行得很慢，好不容易才到了尼多斯港附近。因为风拦阻我们往前，我们就从撒摩尼岬附近经过，沿着克里特岛的背风岸航行。", version_id: 6},
  {id: 159884, book: "Acts", verse: "27.008", unformatted: "我们沿着它航行，好不容易来到一个叫做“美丽港”的地方；离那里不远，有个城叫做拉塞亚。", version_id: 6},
  {id: 159885, book: "Acts", verse: "27.009", unformatted: "不听保罗的建议\n又过了很久，这次航程已经很危险，又因为已经过了禁食日，保罗就劝大家，", version_id: 6},
  {id: 159886, book: "Acts", verse: "27.01", unformatted: "说：“各位，我看这次航程，不仅是货物和船，就是我们的生命财产也将会受到伤害和严重的损失。”", version_id: 6},
  {id: 159887, book: "Acts", verse: "27.011", unformatted: "可是百夫长信任了船长和船主，不信任保罗所说的，", version_id: 6},
  {id: 159888, book: "Acts", verse: "27.012", unformatted: "而且这港口不适合过冬，大多数人就主张从那里开船，或许能够到达菲尼斯过冬。菲尼斯是克里特岛上的一个港口，一面朝西南，一面朝西北。", version_id: 6},
  {id: 159889, book: "Acts", verse: "27.013", unformatted: "风暴中飘摇\n这时，南风微微吹起，他们以为正合所愿，就起了锚，沿着克里特岛航行。", version_id: 6},
  {id: 159890, book: "Acts", verse: "27.014", unformatted: "但是过了不久，一股叫做“东北风”的飓风，从岛上袭来。", version_id: 6},
  {id: 159891, book: "Acts", verse: "27.015", unformatted: "船被困住，顶不住风，我们只好任凭船被刮走。", version_id: 6},
  {id: 159892, book: "Acts", verse: "27.016", unformatted: "我们沿着一个叫做克劳达小岛的背风岸行驶的时候，好不容易才控制住救生艇。", version_id: 6},
  {id: 159893, book: "Acts", verse: "27.017", unformatted: "水手们把救生艇拖上来以后，就用缆绳绕过大船底绑好；又怕在叙尔提斯浅滩上会搁浅，就把锚放低，任船漂流。", version_id: 6},
  {id: 159894, book: "Acts", verse: "27.018", unformatted: "我们被暴风猛烈地袭击。第二天，他们开始把货物抛在海里。", version_id: 6},
  {id: 159895, book: "Acts", verse: "27.019", unformatted: "第三天，他们又亲手把船上的用具抛出去。", version_id: 6},
  {id: 159896, book: "Acts", verse: "27.02", unformatted: "一连好几天见不到太阳和星星，又有暴风雨催逼着，最后我们连获救的希望都断绝了。", version_id: 6},
  {id: 159897, book: "Acts", verse: "27.021", unformatted: "那时，大家已经很久没有吃东西了，保罗站在他们当中说：“哦，各位啊！你们当初应该听我的，不从克里特开船，省得有这样的伤害和损失。", version_id: 6},
  {id: 159898, book: "Acts", verse: "27.022", unformatted: "只是现在我劝你们振作起来，因为你们当中不会有人失去性命，不过会失去这条船。", version_id: 6},
  {id: 159899, book: "Acts", verse: "27.023", unformatted: "要知道，我所归属、所事奉的神，他的一位天使昨天晚上站在我身边，", version_id: 6},
  {id: 159900, book: "Acts", verse: "27.024", unformatted: "说：‘保罗，不要怕！你必须站在凯撒面前。看哪，所有与你同船的人，神都赐给你了。’", version_id: 6},
  {id: 159901, book: "Acts", verse: "27.025", unformatted: "所以请各位振作起来，因为我相信神怎样对我说，事情也会怎样成就。", version_id: 6},
  {id: 159902, book: "Acts", verse: "27.026", unformatted: "不过我们必须搁浅在一个岛上。”", version_id: 6},
  {id: 159903, book: "Acts", verse: "27.027", unformatted: "到了第十四天的晚上，我们在亚得里亚海上飘来飘去。约在半夜，水手们认为已经靠近陆地了，", version_id: 6},
  {id: 159904, book: "Acts", verse: "27.028", unformatted: "就测了水深，发现有三十七公尺；稍往前行，再测一测，发现有二十七公尺。", version_id: 6},
  {id: 159905, book: "Acts", verse: "27.029", unformatted: "他们担心会撞到礁石，就从船尾抛下四个锚，祈盼着天亮。", version_id: 6},
  {id: 159906, book: "Acts", verse: "27.03", unformatted: "水手们想离船逃生，就把救生艇放到海里，却假装要从船头抛锚。", version_id: 6},
  {id: 159907, book: "Acts", verse: "27.031", unformatted: "保罗对百夫长和士兵们说：“这些人如果不留在船上，你们就不能获救。”", version_id: 6},
  {id: 159908, book: "Acts", verse: "27.032", unformatted: "于是士兵们就把救生艇的绳索砍断，由它漂去。", version_id: 6},
  {id: 159909, book: "Acts", verse: "27.033", unformatted: "天快亮的时候，保罗劝大家用饭，说：“你们一直挨饿等候，什么都不吃，今天是第十四天了。", version_id: 6},
  {id: 159910, book: "Acts", verse: "27.034", unformatted: "所以我劝你们用饭，因为这是关于你们得救的事。要知道，你们中间，没有一个人会失去一根头发。”", version_id: 6},
  {id: 159911, book: "Acts", verse: "27.035", unformatted: "说完这些话，保罗就拿起饼，在大家面前感谢神，然后掰开来，开始吃。", version_id: 6},
  {id: 159912, book: "Acts", verse: "27.036", unformatted: "于是大家受到鼓励，也吃了饭。", version_id: 6},
  {id: 159913, book: "Acts", verse: "27.037", unformatted: "我们在船上的，共有两百七十六个人。", version_id: 6},
  {id: 159914, book: "Acts", verse: "27.038", unformatted: "大家吃饱了饭，就把麦子扔到海里，减轻船的负荷。", version_id: 6},
  {id: 159915, book: "Acts", verse: "27.039", unformatted: "船只遇难\n天亮的时候，他们虽然不认识那地方，却看见一个有沙滩的海湾，就决定尽可能地把船拢进去。", version_id: 6},
  {id: 159916, book: "Acts", verse: "27.04", unformatted: "于是他们砍断了锚，把它们丢弃在海里，同时松开舵绳，拉起了前帆，顺着风向岸前进。", version_id: 6},
  {id: 159917, book: "Acts", verse: "27.041", unformatted: "但遇到海水夹流的地方，船就搁了浅，船头被卡住，动弹不得，而船尾也开始被波浪的猛力冲散了。", version_id: 6},
  {id: 159918, book: "Acts", verse: "27.042", unformatted: "士兵们的计划是要把囚犯杀了，免得有人游泳逃脱。", version_id: 6},
  {id: 159919, book: "Acts", verse: "27.043", unformatted: "可是百夫长想救保罗，就拦阻了他们的计划，命令会游泳的人跳下船，先到岸上去，", version_id: 6},
  {id: 159920, book: "Acts", verse: "27.044", unformatted: "其余的人，有的用木板，有的用船上的东西跟着；这样，大家就都获救上岸了。", version_id: 6},
  {id: 159921, book: "Acts", verse: "28.001", unformatted: "在马耳他岛上受到款待\n我们获救以后才知道那岛叫做马耳他，", version_id: 6},
  {id: 159922, book: "Acts", verse: "28.002", unformatted: "那里的土人对我们非常友善。因为当时下着雨，又因为天气冷，他们就生了火接待我们大家。", version_id: 6},
  {id: 159923, book: "Acts", verse: "28.003", unformatted: "保罗收集了一捆柴火，放在火堆上的时候，有一条毒蛇因热钻了出来，咬住他的手。", version_id: 6},
  {id: 159924, book: "Acts", verse: "28.004", unformatted: "那些土人一看见那毒蛇悬在他的手上，就彼此说：“这个人一定是个杀人犯！虽然从海里获救了，但天理还是不让他活着。”", version_id: 6},
  {id: 159925, book: "Acts", verse: "28.005", unformatted: "可是保罗把那毒蛇甩在火里，没有受任何伤害。", version_id: 6},
  {id: 159926, book: "Acts", verse: "28.006", unformatted: "他们本来以为他快要肿起来，或是突然倒下死去，然而等了很久，看见他没有任何异常，就改变了想法，说他是个神。", version_id: 6},
  {id: 159927, book: "Acts", verse: "28.007", unformatted: "在马耳他岛上治病\n在那个地方附近，有些田产是岛上首领普布利乌的。他欢迎我们，热情地招待了我们三天。", version_id: 6},
  {id: 159928, book: "Acts", verse: "28.008", unformatted: "当时，普布利乌的父亲患热病和痢疾，躺在床上。保罗就进去为他祷告，按手在他身上，使他痊愈了。", version_id: 6},
  {id: 159929, book: "Acts", verse: "28.009", unformatted: "这么一来，岛上其他有病的人也都来了，并且都得了痊愈。", version_id: 6},
  {id: 159930, book: "Acts", verse: "28.01", unformatted: "他们十分尊重我们；开船的时候，还送上所需要的东西。", version_id: 6},
  {id: 159931, book: "Acts", verse: "28.011", unformatted: "最终到达罗马\n过了三个月，我们上了一条亚历山大的船起航。这船是在那海岛过冬的，以“宙斯双子神”为船头雕像。", version_id: 6},
  {id: 159932, book: "Acts", verse: "28.012", unformatted: "我们在叙拉古靠岸，停留了三天，", version_id: 6},
  {id: 159933, book: "Acts", verse: "28.013", unformatted: "又从那里绕道航行，到达利基翁。过了一天，刮起了南风，第二天我们就到了普特奥利，", version_id: 6},
  {id: 159934, book: "Acts", verse: "28.014", unformatted: "在那里找到一些弟兄，受邀请与他们同住了七天。\n这样，我们来到了罗马。", version_id: 6},
  {id: 159935, book: "Acts", verse: "28.015", unformatted: "那里的弟兄们听说了有关我们的事，赶到阿比乌市场和三馆来迎接我们。保罗见到他们，就感谢神，得了勇气。", version_id: 6},
  {id: 159936, book: "Acts", verse: "28.016", unformatted: "我们进了罗马以后，保罗获准在一个士兵的看守之下，独自居住。", version_id: 6},
  {id: 159937, book: "Acts", verse: "28.017", unformatted: "第一次会见罗马的犹太人\n第三天，保罗请犹太人的首领们来。他们聚集了，保罗就对他们说：“各位兄弟，我没有做过任何反对我们民族或先祖规矩的事，却做为囚犯从耶路撒冷被交到罗马人手中。", version_id: 6},
  {id: 159938, book: "Acts", verse: "28.018", unformatted: "他们审问我以后，因为没有处死我的任何理由，本来想要释放我。", version_id: 6},
  {id: 159939, book: "Acts", verse: "28.019", unformatted: "但是由于犹太人反对，我被迫向凯撒上诉，并不是有什么事要控告自己的同胞。", version_id: 6},
  {id: 159940, book: "Acts", verse: "28.02", unformatted: "因这理由，我请你们来见面谈话。其实我是为了以色列所盼望的事，才被这锁链捆锁的。”", version_id: 6},
  {id: 159941, book: "Acts", verse: "28.021", unformatted: "他们对保罗说：“我们没有收到从犹太来的有关你的文件。来到这里的同胞中，也没有人报告过或说过任何有关你的坏事。", version_id: 6},
  {id: 159942, book: "Acts", verse: "28.022", unformatted: "不过我们觉得应该听听你的想法，因为我们确实知道有关这教派的事，它到处遭人反对。”", version_id: 6},
  {id: 159943, book: "Acts", verse: "28.023", unformatted: "对保罗传道的反应\n于是他们和保罗定好了日子，就有更多的人来到他的住所。保罗从早到晚对他们讲解，为神的国郑重地做见证，并且引用摩西的律法和先知书上有关耶稣的事来劝导他们。", version_id: 6},
  {id: 159944, book: "Acts", verse: "28.024", unformatted: "对他所说的话，有些人信从了，有些人却不相信。", version_id: 6},
  {id: 159945, book: "Acts", verse: "28.025", unformatted: "他们彼此不一致，开始散去，保罗就说了一句话：“圣灵藉着先知以赛亚对你们祖先所说的是对的。", version_id: 6},
  {id: 159946, book: "Acts", verse: "28.026", unformatted: "他说：\n‘你去告诉这子民：\n你们将听了又听，但绝不会领悟；\n你们将看了又看，但绝不会明白；", version_id: 6},
  {id: 159947, book: "Acts", verse: "28.027", unformatted: "因为这子民的心麻木了，\n他们充耳不闻，眼睛闭着；\n免得他们眼睛看见，耳朵听见，心里领悟，\n回转过来，我就使他们痊愈。’", version_id: 6},
  {id: 159948, book: "Acts", verse: "28.028", unformatted: "所以你们应当知道：神的这救恩已经传给了外邦人，他们反倒会听！”", version_id: 6},
  {id: 159949, book: "Acts", verse: "28.029", unformatted: "保罗说完这些话，犹太人就走了，彼此议论纷纷。", version_id: 6},
  {id: 159950, book: "Acts", verse: "28.03", unformatted: "保罗畅通无阻地传道\n保罗在自己租的房子里住了整整两年，所有来到他那里的人，他都欢迎。", version_id: 6},
  {id: 159951, book: "Acts", verse: "28.031", unformatted: "他宣讲神的国、教导有关主耶稣基督的事，满有胆量、没有拦阻。", version_id: 6},
  {id: 159952, book: "Rom", verse: "1.001", unformatted: "神的福音\n我保罗做基督耶稣的奴仆、蒙召的使徒，是为了神的福音被分别出来的。", version_id: 6},
  {id: 159953, book: "Rom", verse: "1.002", unformatted: "这福音是神先前藉着他的先知们，在圣经上所应许的，", version_id: 6},
  {id: 159954, book: "Rom", verse: "1.003", unformatted: "是关于他的儿子——我们的主耶稣基督的。照着肉身说，他出自大卫的后裔；", version_id: 6},
  {id: 159955, book: "Rom", verse: "1.004", unformatted: "照着圣洁的灵说，藉着从死人中复活，他被显明是神大能的儿子。", version_id: 6},
  {id: 159956, book: "Rom", verse: "1.005", unformatted: "我们从他领受了恩典和使徒的职份，为了他名的缘故，要在万民中带来信仰上的顺从；", version_id: 6},
  {id: 159957, book: "Rom", verse: "1.006", unformatted: "其中也有你们这些蒙召唤、属耶稣基督的人。", version_id: 6},
  {id: 159958, book: "Rom", verse: "1.007", unformatted: "致所有在罗马、蒙神所爱、蒙召成为圣徒的人：\n愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 159959, book: "Rom", verse: "1.008", unformatted: "保罗渴望到罗马去\n首先，我藉着耶稣基督，为你们各位感谢我的神，因为你们的忠信被传遍全世界。", version_id: 6},
  {id: 159960, book: "Rom", verse: "1.009", unformatted: "事实上，神——就是我在他儿子的福音工作上用心灵所事奉的那一位，可以为我见证：我怎样不住地提到你们，", version_id: 6},
  {id: 159961, book: "Rom", verse: "1.01", unformatted: "在我的祷告中总是祈求，或许可以照着神的旨意，终有一天能顺利地到你们那里去；", version_id: 6},
  {id: 159962, book: "Rom", verse: "1.011", unformatted: "因为我切切地想见到你们，好把一些属灵的恩赐分给你们，使你们得以坚固。", version_id: 6},
  {id: 159963, book: "Rom", verse: "1.012", unformatted: "这就是说，在你们那里，藉着你们和我里面彼此的信仰，大家可以同得鼓励。", version_id: 6},
  {id: 159964, book: "Rom", verse: "1.013", unformatted: "弟兄们，我不愿意你们不明白：我多次计划到你们那里去，为要在你们那里也得一些果实，就像我在其他外邦人中那样，可是直到如今还受到拦阻。", version_id: 6},
  {id: 159965, book: "Rom", verse: "1.014", unformatted: "无论是希腊人或是外族人，有智慧的或是无知的，我都对他们有责任。", version_id: 6},
  {id: 159966, book: "Rom", verse: "1.015", unformatted: "因此，我愿意尽我所能，把福音也传给你们在罗马的人。", version_id: 6},
  {id: 159967, book: "Rom", verse: "1.016", unformatted: "义人将因信而活\n的确，我不以福音为耻，因为这福音是神的大能，把救恩带给一切相信的人，先是犹太人、后是外邦人。", version_id: 6},
  {id: 159968, book: "Rom", verse: "1.017", unformatted: "原来，神的义就在这福音上显明出来——本于信，以至于信，正如经上所记：“义人将因信而活。”", version_id: 6},
  {id: 159969, book: "Rom", verse: "1.018", unformatted: "世人的罪\n要知道，人用不公义抵挡真理，神的震怒就从天上显明在人的一切不敬虔和不公义上。", version_id: 6},
  {id: 159970, book: "Rom", verse: "1.019", unformatted: "实际上，有关神的事，人所能知道的，在他们里面是清清楚楚的，因为神已经向他们显明了。", version_id: 6},
  {id: 159971, book: "Rom", verse: "1.02", unformatted: "原来，自从创世以来，神那不可见的本性，就是他永恒的大能和神性，都藉着所造之物，被人明白、被人看见，以致使人无法推诿。", version_id: 6},
  {id: 159972, book: "Rom", verse: "1.021", unformatted: "所以人是已经知道神的，但是却不把他当做神来荣耀他，也不感谢他；相反，他们在思想上变得虚妄，他们无知的心就昏暗了。", version_id: 6},
  {id: 159973, book: "Rom", verse: "1.022", unformatted: "他们自称是有智慧的，却成了愚拙，", version_id: 6},
  {id: 159974, book: "Rom", verse: "1.023", unformatted: "甚至用偶像，就是会朽坏的人、飞禽、走兽、爬行动物的形像，来取代那不朽之神的荣耀。", version_id: 6},
  {id: 159975, book: "Rom", verse: "1.024", unformatted: "因此，神任凭他们顺着心中的情欲去做污秽的事，以致彼此玷污自己的身体。", version_id: 6},
  {id: 159976, book: "Rom", verse: "1.025", unformatted: "他们用虚假取代神的真理，去崇拜、事奉被造之物，而不事奉造物之主——主是当受颂赞的，直到永远！阿们。", version_id: 6},
  {id: 159977, book: "Rom", verse: "1.026", unformatted: "人类的堕落\n为此，神任凭他们陷入可耻的情欲，连他们的女人也把天性的功用变为违反天性的；", version_id: 6},
  {id: 159978, book: "Rom", verse: "1.027", unformatted: "同样，男人也放弃了女人天性的功用，彼此之间欲火中烧，男人与男人做出羞耻的事，就在自己身上受到他们的妄为所应得的报应。", version_id: 6},
  {id: 159979, book: "Rom", verse: "1.028", unformatted: "既然人不愿意真正认识神，神就任凭他们存败坏的理性，去做那些不该做的事。", version_id: 6},
  {id: 159980, book: "Rom", verse: "1.029", unformatted: "他们充满了各种不义、邪恶、贪心、恶毒；满心是嫉妒、凶杀、纷争、欺诈、狠毒；他们搬弄是非、", version_id: 6},
  {id: 159981, book: "Rom", verse: "1.03", unformatted: "诽谤人、憎恨神、侮慢人、骄傲、自夸、制造恶行、悖逆父母、", version_id: 6},
  {id: 159982, book: "Rom", verse: "1.031", unformatted: "愚昧无知、不守信用、没有亲情、毫无怜悯。", version_id: 6},
  {id: 159983, book: "Rom", verse: "1.032", unformatted: "人虽然知道神的公义规定是：行这样事的人是该死的；然而他们不仅自己去做，还赞同做这些事的人。", version_id: 6},
  {id: 159984, book: "Rom", verse: "2.001", unformatted: "不要轻视神\n哦，人哪，所以你是无法推诿的！一切评断人的啊，你在什么事上评断别人，就在什么事上定自己的罪；因为你这评断人的，你自己也在做同样的事。", version_id: 6},
  {id: 159985, book: "Rom", verse: "2.002", unformatted: "况且我们知道，对做这样事的人，神是按照真理来审判他们的。", version_id: 6},
  {id: 159986, book: "Rom", verse: "2.003", unformatted: "哦，人哪！你评断做这样事的人，而自己也做同样的事，你以为你能逃脱神的审判吗？", version_id: 6},
  {id: 159987, book: "Rom", verse: "2.004", unformatted: "难道你轻视神丰富的仁慈、宽容、耐心，不明白他的仁慈是引领你来悔改的吗？", version_id: 6},
  {id: 159988, book: "Rom", verse: "2.005", unformatted: "可是你照着顽固和不悔改的心，为那震怒的日子，就是神公义审判显现的日子，给自己积蓄了震怒。", version_id: 6},
  {id: 159989, book: "Rom", verse: "2.006", unformatted: "神将照着各人的行为回报各人：", version_id: 6},
  {id: 159990, book: "Rom", verse: "2.007", unformatted: "对那些恒心行善，寻求荣耀、尊贵、不朽的，就以永恒的生命回报他们；", version_id: 6},
  {id: 159991, book: "Rom", verse: "2.008", unformatted: "而对那些营私争竞、不肯信从真理、反信从不义的，就以震怒和愤恨回报他们。", version_id: 6},
  {id: 159992, book: "Rom", verse: "2.009", unformatted: "神要把患难和困苦加给一切作恶之人的灵魂，先是犹太人的，后是外邦人的；", version_id: 6},
  {id: 159993, book: "Rom", verse: "2.01", unformatted: "而把荣耀、尊贵、平安赐给所有做美善事的人，先是犹太人，后是外邦人。", version_id: 6},
  {id: 159994, book: "Rom", verse: "2.011", unformatted: "要知道，神是不偏待人的。", version_id: 6},
  {id: 159995, book: "Rom", verse: "2.012", unformatted: "凡是在律法之外犯过罪的，也将要在律法之外灭亡；凡是在律法之下犯过罪的，将要照着律法受审判。", version_id: 6},
  {id: 159996, book: "Rom", verse: "2.013", unformatted: "因为在神面前，不是律法的听者为义，而是律法的实行者被称为义。", version_id: 6},
  {id: 159997, book: "Rom", verse: "2.014", unformatted: "其实没有律法的外邦人，一旦按照本性去行律法上的事，他们虽然没有律法，但自己就是自己的律法了。", version_id: 6},
  {id: 159998, book: "Rom", verse: "2.015", unformatted: "这就显出律法的要求刻在他们的心里，他们的良心也一同作证，而且自己的心思之间，互相控告或辩解。", version_id: 6},
  {id: 159999, book: "Rom", verse: "2.016", unformatted: "正如我的福音，这些事要在那日，神藉着基督耶稣审判各人隐秘事的时候，显明出来。", version_id: 6},
  {id: 160000, book: "Rom", verse: "2.017", unformatted: "犹太人与律法\n你既然称为犹太人，依靠律法，以神夸耀；", version_id: 6},
  {id: 160001, book: "Rom", verse: "2.018", unformatted: "又从律法中受教，明白神的旨意，也能分辨是非；", version_id: 6},
  {id: 160002, book: "Rom", verse: "2.019", unformatted: "又深信自己是瞎子的领路人，是在黑暗中人的光，", version_id: 6},
  {id: 160003, book: "Rom", verse: "2.02", unformatted: "是愚妄人的导师，是小孩子的教师，拥有着律法上知识和真理的规范，", version_id: 6},
  {id: 160004, book: "Rom", verse: "2.021", unformatted: "那么，你这教导别人的，难道不教导自己吗？你这宣讲不可偷窃的，难道自己还偷窃吗？", version_id: 6},
  {id: 160005, book: "Rom", verse: "2.022", unformatted: "你这说不可通奸的，难道自己还通奸吗？你这憎恶偶像的，难道自己还偷取庙里的东西吗？", version_id: 6},
  {id: 160006, book: "Rom", verse: "2.023", unformatted: "你这以律法夸耀的，难道自己还违犯律法侮辱神吗？", version_id: 6},
  {id: 160007, book: "Rom", verse: "2.024", unformatted: "事实上，正如经上所记：“因你们的缘故，神的名在外邦人中受到亵渎。”", version_id: 6},
  {id: 160008, book: "Rom", verse: "2.025", unformatted: "内心的割礼\n你如果是遵行律法的，割礼才有益处；但你如果是违犯律法的人，你的割礼就不是割礼了。", version_id: 6},
  {id: 160009, book: "Rom", verse: "2.026", unformatted: "因此，那没有受割礼的人，如果遵守律法的公义规定，他虽然没有受割礼，难道不是算是受了割礼吗？", version_id: 6},
  {id: 160010, book: "Rom", verse: "2.027", unformatted: "并且，那生来没有受割礼却完全实行律法的人，对你这虽有律法条文和割礼却违犯律法的人，难道不是要施行审判吗？", version_id: 6},
  {id: 160011, book: "Rom", verse: "2.028", unformatted: "其实外表上做犹太人的，并不是犹太人；外表上、肉身上的割礼，也不是割礼。", version_id: 6},
  {id: 160012, book: "Rom", verse: "2.029", unformatted: "然而，在内心做犹太人的才是犹太人；同样，内心的割礼才是割礼——不是靠着律法条文而是靠着圣灵；这样的人得到的称赞不是从人来的，而是从神来的。", version_id: 6},
  {id: 160013, book: "Rom", verse: "3.001", unformatted: "神的信实\n这样说来，犹太人有什么优越呢？割礼有什么益处呢？", version_id: 6},
  {id: 160014, book: "Rom", verse: "3.002", unformatted: "在各方面都有很多。首要的，是神的话语确实委托给他们了。", version_id: 6},
  {id: 160015, book: "Rom", verse: "3.003", unformatted: "即使他们中有些人不信，到底又怎么样呢？难道他们的不信会使神的信实无效吗？", version_id: 6},
  {id: 160016, book: "Rom", verse: "3.004", unformatted: "绝对不会！即使每个人都是说谎者，神还是真实的。正如经上所记：\n“这样，你说话的时候，显为公义；\n被评断的时候，必然得胜。”", version_id: 6},
  {id: 160017, book: "Rom", verse: "3.005", unformatted: "但是，如果我按人的意思来说，我们的不义可以显明神的义，那我们要怎么说呢？难道降震怒的神是不公正的吗？", version_id: 6},
  {id: 160018, book: "Rom", verse: "3.006", unformatted: "绝对不是！否则，神怎么能审判世界呢？", version_id: 6},
  {id: 160019, book: "Rom", verse: "3.007", unformatted: "那么，如果神的真实因我的虚谎使他的荣耀更加丰盛，为什么我还要像罪人一样被定罪呢？", version_id: 6},
  {id: 160020, book: "Rom", verse: "3.008", unformatted: "这样，为什么不说“让我们作恶好带来善”呢？——我们正是如此受到毁谤，也有些人说过我们讲了这话——这些人被定罪倒是应该的。", version_id: 6},
  {id: 160021, book: "Rom", verse: "3.009", unformatted: "没有义人\n那么怎么样呢？我们犹太人比别人好吗？当然不是！我们已经指出：犹太人和外邦人都在罪孽之下。", version_id: 6},
  {id: 160022, book: "Rom", verse: "3.01", unformatted: "正如经上所记：\n“没有义人，连一个也没有；", version_id: 6},
  {id: 160023, book: "Rom", verse: "3.011", unformatted: "没有领悟的，\n没有寻求神的。", version_id: 6},
  {id: 160024, book: "Rom", verse: "3.012", unformatted: "人人都远离了正道，\n一同成了无用的；\n没有仁慈的，\n连一个也没有。", version_id: 6},
  {id: 160025, book: "Rom", verse: "3.013", unformatted: "他们的喉咙是敞开的坟墓，\n他们用舌头玩弄诡诈，\n他们的嘴唇含有蛇毒，", version_id: 6},
  {id: 160026, book: "Rom", verse: "3.014", unformatted: "他们满口是咒骂和苦毒，", version_id: 6},
  {id: 160027, book: "Rom", verse: "3.015", unformatted: "他们的脚步为杀人流血而飞快，", version_id: 6},
  {id: 160028, book: "Rom", verse: "3.016", unformatted: "他们的路上只有毁灭和惨祸；", version_id: 6},
  {id: 160029, book: "Rom", verse: "3.017", unformatted: "和平之路，他们从来不知道。", version_id: 6},
  {id: 160030, book: "Rom", verse: "3.018", unformatted: "他们的眼目中没有对神的敬畏。”", version_id: 6},
  {id: 160031, book: "Rom", verse: "3.019", unformatted: "我们知道，律法上的一切话都是对律法之下的人讲的，好堵住每个人的口，使全世界的人都在神的审判之下有罪，", version_id: 6},
  {id: 160032, book: "Rom", verse: "3.02", unformatted: "因为没有一个人本于律法上的行为在神面前被称为义；原来，藉着律法，只能对罪有真正的认识。", version_id: 6},
  {id: 160033, book: "Rom", verse: "3.021", unformatted: "因信称义\n但如今，神的义已经在律法之外显明出来，被律法和先知们所见证，", version_id: 6},
  {id: 160034, book: "Rom", verse: "3.022", unformatted: "那就是：神的义藉着对耶稣基督的信仰，临到所有信的人，并没有分别。", version_id: 6},
  {id: 160035, book: "Rom", verse: "3.023", unformatted: "要知道，每个人都犯了罪，亏缺了神的荣耀；", version_id: 6},
  {id: 160036, book: "Rom", verse: "3.024", unformatted: "但藉着神的恩典，藉着在基督耶稣里的救赎，却无偿地被称为义。", version_id: 6},
  {id: 160037, book: "Rom", verse: "3.025", unformatted: "神预先定下耶稣为平息祭，是用耶稣的血、藉着人的信，为要显明神自己的义；因为他用忍耐的心，宽容人过去所犯的罪，", version_id: 6},
  {id: 160038, book: "Rom", verse: "3.026", unformatted: "为了在现今的时刻显明他的义，好表明他自己为义，也称信耶稣的人为义。", version_id: 6},
  {id: 160039, book: "Rom", verse: "3.027", unformatted: "没有可夸耀的\n既然如此，还有什么可夸耀的呢？根本没有。凭着什么法则呢？凭着行为的法则吗？不是！而是凭着信的法则。", version_id: 6},
  {id: 160040, book: "Rom", verse: "3.028", unformatted: "就是说，我们认为：人被称为义，是藉着信，与律法上的行为无关。", version_id: 6},
  {id: 160041, book: "Rom", verse: "3.029", unformatted: "难道神只是犹太人的神吗？不也是外邦人的神吗？是的，他也是外邦人的神，", version_id: 6},
  {id: 160042, book: "Rom", verse: "3.03", unformatted: "既然神真是一位；他要使受割礼的人因信称义，也要使没有受割礼的人因信称义。", version_id: 6},
  {id: 160043, book: "Rom", verse: "3.031", unformatted: "那么，我们是藉着信废掉律法吗？绝对不是！反而确立了律法。", version_id: 6},
  {id: 160044, book: "Rom", verse: "4.001", unformatted: "亚伯拉罕因信称义\n那么，我们说我们肉体上的先祖亚伯拉罕得到了什么呢？", version_id: 6},
  {id: 160045, book: "Rom", verse: "4.002", unformatted: "事实上，如果亚伯拉罕本于行为被称为义，他就有可夸耀的；然而在神面前他并没有可夸耀的。", version_id: 6},
  {id: 160046, book: "Rom", verse: "4.003", unformatted: "经上到底是怎么说的呢？\n“亚伯拉罕信神，\n这就被算为他的义。”", version_id: 6},
  {id: 160047, book: "Rom", verse: "4.004", unformatted: "做工的人得工钱，不算是恩典，而是应得的；", version_id: 6},
  {id: 160048, book: "Rom", verse: "4.005", unformatted: "但是，一个人虽没有做工，却信靠那称不敬虔之人为义的神，这人的信就被算为义。", version_id: 6},
  {id: 160049, book: "Rom", verse: "4.006", unformatted: "大卫的话\n正如大卫也论到神所算为义之人的福份，这义与行为无关；他说：", version_id: 6},
  {id: 160050, book: "Rom", verse: "4.007", unformatted: "“罪恶被赦免、\n罪孽被遮盖的人，\n这人是蒙福的。", version_id: 6},
  {id: 160051, book: "Rom", verse: "4.008", unformatted: "主绝不算为有罪的，\n这人是蒙福的。”", version_id: 6},
  {id: 160052, book: "Rom", verse: "4.009", unformatted: "亚伯拉罕在受割礼前被称为义\n既然如此，这福份到底是只赐给那些受割礼的人，还是也赐给那些没有受割礼的人呢？我们说：“亚伯拉罕因着信，就被算为义了。”", version_id: 6},
  {id: 160053, book: "Rom", verse: "4.01", unformatted: "那么，是怎么被算为义的呢？到底是在他受割礼之后，还是在他没有受割礼的时候呢？不是在受割礼之后，而是在没有受割礼的时候。", version_id: 6},
  {id: 160054, book: "Rom", verse: "4.011", unformatted: "并且他领受了割礼的标记，做为他没有受割礼时因信称义的印证，使他成为所有不凭着割礼而信之人的父，也使他们被算为义；", version_id: 6},
  {id: 160055, book: "Rom", verse: "4.012", unformatted: "而且他也成为受割礼之人的父，不仅为那些受了割礼的人，还为那些顺着我们的先祖亚伯拉罕信心之脚踪行走的人；亚伯拉罕的信心在他没有受割礼的时候就已经有了。", version_id: 6},
  {id: 160056, book: "Rom", verse: "4.013", unformatted: "因信承受应许\n原来，赐给亚伯拉罕和他后裔继承世界的应许，不是藉着律法，而是藉着因信而得的义。", version_id: 6},
  {id: 160057, book: "Rom", verse: "4.014", unformatted: "这是因为，如果本于律法的才是继承人，信就落了空，应许也就无效了。", version_id: 6},
  {id: 160058, book: "Rom", verse: "4.015", unformatted: "事实上，律法带来了震怒，所以哪里没有律法，哪里也就没有过犯。", version_id: 6},
  {id: 160059, book: "Rom", verse: "4.016", unformatted: "为此，这应许是本于信，好使这应许照着恩典，能确保给所有的后裔——不仅是给属律法的，也是给属亚伯拉罕信心的。亚伯拉罕在神面前是我们每个人的父，", version_id: 6},
  {id: 160060, book: "Rom", verse: "4.017", unformatted: "正如经上所记：“我已经指派你为众多民族的父。”亚伯拉罕所信的那一位，就是叫死人得生命、使无变为有的神。", version_id: 6},
  {id: 160061, book: "Rom", verse: "4.018", unformatted: "亚伯拉罕在没有盼望的时候，仍然怀着盼望去相信，因而成为众多民族的父，正如先前所说：“你的后裔将要如此众多。”", version_id: 6},
  {id: 160062, book: "Rom", verse: "4.019", unformatted: "他快到一百岁的时候，想到自己的身体如同已经死了，又想到撒拉也已经不能生育，但是他的信仰却没有软弱。", version_id: 6},
  {id: 160063, book: "Rom", verse: "4.02", unformatted: "他也没有因着不信去怀疑神的应许，反而因着信得以刚强，把荣耀归给神，", version_id: 6},
  {id: 160064, book: "Rom", verse: "4.021", unformatted: "并且确信不疑：神所应许的，神也能成就。", version_id: 6},
  {id: 160065, book: "Rom", verse: "4.022", unformatted: "因此，这就被算为他的义了。", version_id: 6},
  {id: 160066, book: "Rom", verse: "4.023", unformatted: "不但如此，“被算为他的义”这句话不仅是为亚伯拉罕写的，", version_id: 6},
  {id: 160067, book: "Rom", verse: "4.024", unformatted: "也是为我们写的；我们信靠使我们的主耶稣从死人中复活的那一位，就将被算为义。", version_id: 6},
  {id: 160068, book: "Rom", verse: "4.025", unformatted: "主耶稣被交出去，是为了我们的过犯；复活，是为了我们称义。", version_id: 6},
  {id: 160069, book: "Rom", verse: "5.001", unformatted: "因信称义的福气\n这样，我们既然因信称义，就藉着我们的主耶稣基督与神和好了；", version_id: 6},
  {id: 160070, book: "Rom", verse: "5.002", unformatted: "我们也藉着他，因信进入了现在所站的这恩典中，并且以盼望神的荣耀而夸耀。", version_id: 6},
  {id: 160071, book: "Rom", verse: "5.003", unformatted: "不仅如此，我们也以患难夸耀，因为我们知道患难生出忍耐，", version_id: 6},
  {id: 160072, book: "Rom", verse: "5.004", unformatted: "忍耐生出品德，品德生出盼望，", version_id: 6},
  {id: 160073, book: "Rom", verse: "5.005", unformatted: "而这盼望不使人羞愧，因为神的爱藉着所赐给我们的圣灵，已经倾注在我们的心里。", version_id: 6},
  {id: 160074, book: "Rom", verse: "5.006", unformatted: "与神和好的人\n原来，当我们还软弱的时候，基督就按照所定的时候为不敬虔的人死了。", version_id: 6},
  {id: 160075, book: "Rom", verse: "5.007", unformatted: "为义人死，是少有的；为好人死，或许有敢做的；", version_id: 6},
  {id: 160076, book: "Rom", verse: "5.008", unformatted: "但是，当我们还是罪人的时候，基督就替我们死了。神的爱就在此向我们显明了。", version_id: 6},
  {id: 160077, book: "Rom", verse: "5.009", unformatted: "所以，我们现在既然藉着基督的血被称为义，难道不更要藉着他，从神的震怒中被拯救出来吗？", version_id: 6},
  {id: 160078, book: "Rom", verse: "5.01", unformatted: "这样，我们做神敌人的时候，尚且能藉着神儿子的死与神和好，何况和好以后，难道不更要藉着神儿子的生命得救吗？", version_id: 6},
  {id: 160079, book: "Rom", verse: "5.011", unformatted: "不仅如此，我们现在藉着主耶稣基督与神和好了，就更要藉着他以神夸耀。", version_id: 6},
  {id: 160080, book: "Rom", verse: "5.012", unformatted: "亚当与基督\n所以，正如罪是藉着一个人进入了世界，并且死是藉着罪进入的；这样，因为人人都犯了罪，死也就临到了所有的人。", version_id: 6},
  {id: 160081, book: "Rom", verse: "5.013", unformatted: "就是说，没有律法以前，罪已经在世上了；不过没有律法，罪就不算为罪。", version_id: 6},
  {id: 160082, book: "Rom", verse: "5.014", unformatted: "然而，从亚当到摩西，死亡掌了权，甚至那些没有按亚当过犯的样式犯罪的人，也在死的权下。这亚当是以后要来的那一位的象征。", version_id: 6},
  {id: 160083, book: "Rom", verse: "5.015", unformatted: "但是恩赐不同于过犯。原来，一个人的过犯尚且使众人都死了，何况神的恩典和耶稣基督一个人恩典里的赏赐，难道不更要丰丰富富地临到众人吗？", version_id: 6},
  {id: 160084, book: "Rom", verse: "5.016", unformatted: "而且，恩赐不同于由一个人犯罪而来的后果。因为，由一次过犯而来的审判虽然导致了定罪，但由很多过犯而来的恩赐却导致了称义。", version_id: 6},
  {id: 160085, book: "Rom", verse: "5.017", unformatted: "实际上，一个人的过犯尚且使死亡藉着这一个人掌了权，何况那些领受丰富之恩典和称义之赏赐的人，难道不更要藉着耶稣基督一个人，在生命里掌权吗？", version_id: 6},
  {id: 160086, book: "Rom", verse: "5.018", unformatted: "由此可见，藉着一次的过犯导致了所有人的定罪；照样，藉着一次的义行给所有的人带来永生的称义。", version_id: 6},
  {id: 160087, book: "Rom", verse: "5.019", unformatted: "就是说，藉着一个人的悖逆，众人怎样都成了罪人；藉着一个人的顺从，众人将怎样成为义人。", version_id: 6},
  {id: 160088, book: "Rom", verse: "5.02", unformatted: "律法出现，为要使过犯增多；可是，罪在哪里增多，恩典就在哪里格外增多。", version_id: 6},
  {id: 160089, book: "Rom", verse: "5.021", unformatted: "这样，就像罪藉着死亡掌权，恩典也照样藉着义掌权，使人藉着我们的主耶稣基督进入永恒的生命。", version_id: 6},
  {id: 160090, book: "Rom", verse: "6.001", unformatted: "在基督里的新生命\n那么，我们要怎么说呢？难道我们可以留在罪中，好让恩典增多吗？", version_id: 6},
  {id: 160091, book: "Rom", verse: "6.002", unformatted: "绝对不可！我们这些向罪已经死了的人，怎么还能活在罪中呢？", version_id: 6},
  {id: 160092, book: "Rom", verse: "6.003", unformatted: "难道你们不明白，所有受洗归入基督耶稣的人，就是受洗归入了他的死吗？", version_id: 6},
  {id: 160093, book: "Rom", verse: "6.004", unformatted: "所以，我们藉着洗礼归入死，和他一同埋葬，好使我们也能以生命的新样式行事为人，就像基督藉着父的荣耀从死人中复活那样。", version_id: 6},
  {id: 160094, book: "Rom", verse: "6.005", unformatted: "就是说，如果我们在基督死的样式上与他联合，也将要在他复活的形式上与他联合。", version_id: 6},
  {id: 160095, book: "Rom", verse: "6.006", unformatted: "我们知道，我们的旧我已经和基督一起被钉十字架，好让罪的身体被废除，使我们不再做罪的奴仆，", version_id: 6},
  {id: 160096, book: "Rom", verse: "6.007", unformatted: "因为一个死了的人，已经脱离了罪。", version_id: 6},
  {id: 160097, book: "Rom", verse: "6.008", unformatted: "那么，如果我们与基督一同死了，就相信也要与他一同活着。", version_id: 6},
  {id: 160098, book: "Rom", verse: "6.009", unformatted: "我们知道，基督既从死人中复活，就不会再死，死亡再也辖制不了他。", version_id: 6},
  {id: 160099, book: "Rom", verse: "6.01", unformatted: "原来他死，是一次性地向罪而死；他活，是向神而活。", version_id: 6},
  {id: 160100, book: "Rom", verse: "6.011", unformatted: "照样，你们就当看自己向罪是死的，但在基督耶稣里向神却是活的。", version_id: 6},
  {id: 160101, book: "Rom", verse: "6.012", unformatted: "所以，不要让罪在你们会死的身体上掌权，使你们顺从身体的私欲；", version_id: 6},
  {id: 160102, book: "Rom", verse: "6.013", unformatted: "也不要把你们身体的任何部分献给罪，做不义的器具；相反，要像从死人中复活的人，把自己献给神，并把身体的各部分做义的器具献给神。", version_id: 6},
  {id: 160103, book: "Rom", verse: "6.014", unformatted: "这样，罪就不能辖制你们了，因为你们不在律法之下，而在恩典之下。", version_id: 6},
  {id: 160104, book: "Rom", verse: "6.015", unformatted: "从罪的奴仆到义的奴仆\n那又怎么样呢？难道因为我们不在律法之下而在恩典之下，就可以犯罪吗？绝对不可！", version_id: 6},
  {id: 160105, book: "Rom", verse: "6.016", unformatted: "你们难道不知道吗？你们把自己献上做奴仆去顺从谁，那么，顺从谁就是谁的奴仆：或做罪的奴仆，以至于死；或做顺从的奴仆，以致称义。", version_id: 6},
  {id: 160106, book: "Rom", verse: "6.017", unformatted: "感谢神！虽然你们过去是罪的奴仆，但现在却从心里顺从了所传给你们的教导内容，", version_id: 6},
  {id: 160107, book: "Rom", verse: "6.018", unformatted: "从罪中得了释放，成为义的奴仆。", version_id: 6},
  {id: 160108, book: "Rom", verse: "6.019", unformatted: "因着你们肉体的软弱，我就按照人的说法来讲，那就是，你们从前怎样把身体的各部分献给污秽和罪恶做奴仆，以致罪恶；现在也要照样把你们身体的各部分献给义做奴仆，以致分别为圣。", version_id: 6},
  {id: 160109, book: "Rom", verse: "6.02", unformatted: "原来，你们做罪的奴仆时，不受义的管束；", version_id: 6},
  {id: 160110, book: "Rom", verse: "6.021", unformatted: "那么，在你们现在看为羞耻的那些事上，你们当时到底得了什么果子呢？那些事的结局就是死。", version_id: 6},
  {id: 160111, book: "Rom", verse: "6.022", unformatted: "但如今，你们既从罪中得了释放，成为神的奴仆，就得了果子，以致分别为圣，那结局就是永恒的生命。", version_id: 6},
  {id: 160112, book: "Rom", verse: "6.023", unformatted: "因为罪的工价就是死；但神的恩赐，在我们的主基督耶稣里，却是永恒的生命。", version_id: 6},
  {id: 160113, book: "Rom", verse: "7.001", unformatted: "以婚姻为例\n弟兄们，我现在对明白律法的人说：难道你们不明白，律法只是在一个人活着的时候辖制这个人吗？", version_id: 6},
  {id: 160114, book: "Rom", verse: "7.002", unformatted: "要知道，已婚的女人，在丈夫活着的时候，是被律法约束的；但如果丈夫死了，她就脱离了那关于丈夫的律法。", version_id: 6},
  {id: 160115, book: "Rom", verse: "7.003", unformatted: "由此可见，丈夫还活着的时候，她如果归向别的男人，就被称为淫妇；但如果丈夫死了，她就脱离那律法得了自由，即使归向别的男人，也不是淫妇。", version_id: 6},
  {id: 160116, book: "Rom", verse: "7.004", unformatted: "因此，我的弟兄们，你们藉着基督的身体，在律法上也被处死了，使你们归向另一位，就是从死人中复活的那一位，好让我们能为神结出果子来。", version_id: 6},
  {id: 160117, book: "Rom", verse: "7.005", unformatted: "原来，当我们还在肉体中的时候，罪的欲望藉着律法在我们身体的各部分做工，以致为死亡结了果子。", version_id: 6},
  {id: 160118, book: "Rom", verse: "7.006", unformatted: "但如今，我们既然在那捆绑我们的律法上死了，就脱离了律法，结果我们得以按照圣灵的新样式来服事，而不按照律法条文的旧样式。", version_id: 6},
  {id: 160119, book: "Rom", verse: "7.007", unformatted: "罪藉着诫命\n那么，我们要怎么说呢？难道律法是罪吗？绝对不是！然而，要不是藉着律法，我就不知道什么是罪。如果律法没有说“不可贪心”，我就不知道什么是贪心；", version_id: 6},
  {id: 160120, book: "Rom", verse: "7.008", unformatted: "但是，罪藉着诫命，趁机在我里面生出种种贪念。原来在律法之外，罪是死的。", version_id: 6},
  {id: 160121, book: "Rom", verse: "7.009", unformatted: "我从前活在律法之外，但是诫命一来，罪就活起来了，", version_id: 6},
  {id: 160122, book: "Rom", verse: "7.01", unformatted: "我却死了。于是我发现，那本该带来生命的诫命，它却导致了死亡。", version_id: 6},
  {id: 160123, book: "Rom", verse: "7.011", unformatted: "事实上，罪藉着诫命趁机欺骗了我，并藉着诫命杀了我。", version_id: 6},
  {id: 160124, book: "Rom", verse: "7.012", unformatted: "因此，律法是神圣的，诫命也是神圣、公义、美善的。", version_id: 6},
  {id: 160125, book: "Rom", verse: "7.013", unformatted: "内在的罪\n那么，是那美善的导致了我的死吗？绝对不是！而是罪。罪为要显出是罪，就藉着那美善的带来了我的死，使罪藉着诫命变得极其邪恶。", version_id: 6},
  {id: 160126, book: "Rom", verse: "7.014", unformatted: "我们知道律法是属灵的；而我是属肉体的，已经卖给罪了。", version_id: 6},
  {id: 160127, book: "Rom", verse: "7.015", unformatted: "实际上，我所做的，我不明白，因为我所愿意的，我没有去做；我所恨恶的，我反而去做。", version_id: 6},
  {id: 160128, book: "Rom", verse: "7.016", unformatted: "那么，如果我做我不愿意做的事，我就赞同了律法是好的。", version_id: 6},
  {id: 160129, book: "Rom", verse: "7.017", unformatted: "所以现在这事就不再是我所做的，而是住在我里面的罪所做的。", version_id: 6},
  {id: 160130, book: "Rom", verse: "7.018", unformatted: "事实上，我知道在我里面，就是在我肉体中，没有良善；因为行善的意愿在我里面，却行不出来。", version_id: 6},
  {id: 160131, book: "Rom", verse: "7.019", unformatted: "这样，我愿意行的善，我没有去行；我不愿意做的恶，我反而去做。", version_id: 6},
  {id: 160132, book: "Rom", verse: "7.02", unformatted: "那么，如果我做我不愿意做的事，这事就不再是我所做的，而是住在我里面的罪所做的。", version_id: 6},
  {id: 160133, book: "Rom", verse: "7.021", unformatted: "因此我发现一个法则：当我愿意行善的时候，恶就在我里面。", version_id: 6},
  {id: 160134, book: "Rom", verse: "7.022", unformatted: "就是说，按着内在的人，我喜爱神的法则，", version_id: 6},
  {id: 160135, book: "Rom", verse: "7.023", unformatted: "然而我发现在我身体中另有一个法则，与我理性的法则交战，把我掳到我身体中罪的法则里。", version_id: 6},
  {id: 160136, book: "Rom", verse: "7.024", unformatted: "我这个人真是可悲呀！谁能救我脱离这属于死亡的身体呢？", version_id: 6},
  {id: 160137, book: "Rom", verse: "7.025", unformatted: "藉着我们的主耶稣基督，感谢归于神！由此可见，我自己一方面在理性上服从神的法则，另一方面在肉体中却服从罪的法则。", version_id: 6},
  {id: 160138, book: "Rom", verse: "8.001", unformatted: "赐生命的灵\n所以现在，那些在基督耶稣里的人，就不被定罪了，", version_id: 6},
  {id: 160139, book: "Rom", verse: "8.002", unformatted: "因为圣灵的生命法则，在基督耶稣里把你从罪和死的法则中释放了出来。", version_id: 6},
  {id: 160140, book: "Rom", verse: "8.003", unformatted: "因肉体软弱的缘故，律法所做不到的，神却做到了——神派遣了自己的儿子成为罪的肉身样式，并且为了赎罪，用肉身判决了罪，", version_id: 6},
  {id: 160141, book: "Rom", verse: "8.004", unformatted: "使律法的公义规定，成全在我们这些不顺着肉体却顺着圣灵行走的人身上。", version_id: 6},
  {id: 160142, book: "Rom", verse: "8.005", unformatted: "要知道，顺着肉体的人，思想属肉体的事；顺着圣灵的人，思想属圣灵的事。", version_id: 6},
  {id: 160143, book: "Rom", verse: "8.006", unformatted: "属肉体的思想带来死亡；属圣灵的思想带来生命和平安。", version_id: 6},
  {id: 160144, book: "Rom", verse: "8.007", unformatted: "原来属肉体的思想是敌对神的，因为它不服从神的律法，其实也不能服从；", version_id: 6},
  {id: 160145, book: "Rom", verse: "8.008", unformatted: "并且，属肉体的人无法得到神的喜悦。", version_id: 6},
  {id: 160146, book: "Rom", verse: "8.009", unformatted: "但既然神的灵真是住在你们里面，你们就不属于肉体，而属于圣灵了。不过如果有人没有基督的灵，这个人就不属于基督了。", version_id: 6},
  {id: 160147, book: "Rom", verse: "8.01", unformatted: "但如果基督在你们里面，那么，你们的身体虽然因罪是死的，灵却因义是活的；", version_id: 6},
  {id: 160148, book: "Rom", verse: "8.011", unformatted: "不但如此，使耶稣从死人中复活的那一位——他的灵如果住在你们里面，那么，使基督从死人中复活的那一位，也将藉着住在你们里面的圣灵，使你们会死的身体得着生命。", version_id: 6},
  {id: 160149, book: "Rom", verse: "8.012", unformatted: "圣灵的工作\n弟兄们，由此可见，我们都是有责任的，但不是对肉体有责任去顺着肉体而活；", version_id: 6},
  {id: 160150, book: "Rom", verse: "8.013", unformatted: "因为你们如果顺着肉体而活，就将死去；但如果顺着圣灵治死身体的所作所为，就将活着。", version_id: 6},
  {id: 160151, book: "Rom", verse: "8.014", unformatted: "原来，凡是蒙神的灵带领的人，他们才是神的儿女。", version_id: 6},
  {id: 160152, book: "Rom", verse: "8.015", unformatted: "就是说，你们所领受的不是一个奴役的灵，使你们再有惧怕；相反，你们所领受的是使你们有儿子名份的圣灵——藉着他，我们呼叫：“阿爸！父啊！”", version_id: 6},
  {id: 160153, book: "Rom", verse: "8.016", unformatted: "圣灵亲自与我们的灵一同作证：我们是神的儿女。", version_id: 6},
  {id: 160154, book: "Rom", verse: "8.017", unformatted: "既然是儿女，也就是继承人；如果我们真是为了与他一同得荣耀而与他一同受苦，我们就是神的继承人，而且与基督同做继承人。", version_id: 6},
  {id: 160155, book: "Rom", verse: "8.018", unformatted: "将来的荣耀\n事实上，我认为现今时刻的苦难，与将来要显明给我们的荣耀，是无法相比的。", version_id: 6},
  {id: 160156, book: "Rom", verse: "8.019", unformatted: "被造之物都热切盼望、热切等待着神的儿女显现出来，", version_id: 6},
  {id: 160157, book: "Rom", verse: "8.02", unformatted: "因为被造之物屈从在虚妄之下，并不是出于自愿，而是出于使它屈从的那一位；", version_id: 6},
  {id: 160158, book: "Rom", verse: "8.021", unformatted: "被造之物自己也盼望着从使之衰朽的奴役中得到释放，归入神儿女荣耀的自由。", version_id: 6},
  {id: 160159, book: "Rom", verse: "8.022", unformatted: "我们知道，所有被造之物都一同呻吟、同受阵痛，直到如今。", version_id: 6},
  {id: 160160, book: "Rom", verse: "8.023", unformatted: "不仅如此，连我们这些有圣灵为初熟果子的人，也在自己里面呻吟叹息，热切等待得到儿子的名份，就是等待我们的身体得蒙救赎。", version_id: 6},
  {id: 160161, book: "Rom", verse: "8.024", unformatted: "因为我们得救在于这盼望。然而，看得见的盼望，就不是盼望了；谁会盼望自己所看得见的呢？", version_id: 6},
  {id: 160162, book: "Rom", verse: "8.025", unformatted: "但如果我们盼望那看不见的，就要藉着忍耐来热切等待。", version_id: 6},
  {id: 160163, book: "Rom", verse: "8.026", unformatted: "况且，圣灵也照样扶助我们的软弱。事实上，我们不知道该祷告什么、如何祷告，但圣灵却亲自用说不出的叹息，为我们代求。", version_id: 6},
  {id: 160164, book: "Rom", verse: "8.027", unformatted: "而且，洞察人心的那一位，知道圣灵的意念是什么，因为圣灵是按神的意思为圣徒代求的。", version_id: 6},
  {id: 160165, book: "Rom", verse: "8.028", unformatted: "我们也知道，神使万事相辅相成，是为了爱神之人的益处，就是那些按照他的心意蒙召之人的益处。", version_id: 6},
  {id: 160166, book: "Rom", verse: "8.029", unformatted: "因为神预先所知道的人，神也预先定下他们和他儿子的形像相似，使他儿子在众多弟兄中做长子；", version_id: 6},
  {id: 160167, book: "Rom", verse: "8.03", unformatted: "而且，神预先所定下的人，神又召他们来；所召来的人，又称他们为义；所称为义的人，又使他们得荣耀。", version_id: 6},
  {id: 160168, book: "Rom", verse: "8.031", unformatted: "信徒的凯旋\n那么，对这些事我们要怎么说呢？\n神如果支持我们，谁还能反对我们呢？", version_id: 6},
  {id: 160169, book: "Rom", verse: "8.032", unformatted: "神既然没有顾惜自己的儿子，\n为我们所有的人舍弃了他，\n难道不会把万有也连同他一起赐给我们吗？", version_id: 6},
  {id: 160170, book: "Rom", verse: "8.033", unformatted: "到底谁能控告那些蒙神拣选的人呢？\n神是称人为义的那一位！", version_id: 6},
  {id: 160171, book: "Rom", verse: "8.034", unformatted: "到底谁能定我们的罪呢？\n基督耶稣是已经死了的那一位——\n但更要说，他已经复活了，\n而且现今在神的右边，还为我们代求！", version_id: 6},
  {id: 160172, book: "Rom", verse: "8.035", unformatted: "到底谁能使我们与基督的爱分开呢？\n难道是患难吗？是困苦吗？是逼迫吗？\n是饥饿吗？是赤身吗？是危险吗？是刀剑吗？", version_id: 6},
  {id: 160173, book: "Rom", verse: "8.036", unformatted: "正如经上所记：\n“为你的缘故，我们终日被置于死地，\n被看为要宰杀的羊。”", version_id: 6},
  {id: 160174, book: "Rom", verse: "8.037", unformatted: "然而，靠着爱我们的那一位，\n我们在这一切事上已经得胜有余了。", version_id: 6},
  {id: 160175, book: "Rom", verse: "8.038", unformatted: "事实上，我深信：\n无论是死、是生、\n是天使、是统治者、\n是现在的事、是将来的事、是有势力的、", version_id: 6},
  {id: 160176, book: "Rom", verse: "8.039", unformatted: "是高处的、是深处的，或是任何别的被造之物，\n都不能使我们与神的爱分开，\n这爱是在我们主基督耶稣里的。", version_id: 6},
  {id: 160177, book: "Rom", verse: "9.001", unformatted: "以色列人拒绝基督\n我在基督里说真话，不是在说谎；我的良心藉着圣灵与我一同作证：", version_id: 6},
  {id: 160178, book: "Rom", verse: "9.002", unformatted: "我极其忧伤，心里时常苦痛。", version_id: 6},
  {id: 160179, book: "Rom", verse: "9.003", unformatted: "为了我的同胞，我的骨肉之亲，就算我自己被诅咒、与基督分离，我也愿意。", version_id: 6},
  {id: 160180, book: "Rom", verse: "9.004", unformatted: "他们是以色列人：儿子的名份、荣耀、诸约、所赐的律法、礼仪、各样应许，都是他们的；", version_id: 6},
  {id: 160181, book: "Rom", verse: "9.005", unformatted: "祖先也是他们的；照着肉身说，基督也是出自他们的——他是那在万有之上当受颂赞的神，直到永远！阿们。", version_id: 6},
  {id: 160182, book: "Rom", verse: "9.006", unformatted: "神拣选以色列人\n但这并不是说神的话语落了空，因为由以色列所生的，并不都是以色列人；", version_id: 6},
  {id: 160183, book: "Rom", verse: "9.007", unformatted: "也不因为是亚伯拉罕的后裔，就都是他的儿女；只有“从以撒生的，才会被称为你的后裔。”", version_id: 6},
  {id: 160184, book: "Rom", verse: "9.008", unformatted: "这就是说，肉身的儿女并不是神的儿女，只有应许的儿女才算是后裔。", version_id: 6},
  {id: 160185, book: "Rom", verse: "9.009", unformatted: "原来所应许的话是这样的：“到明年这时候我要来，撒拉会生一个儿子。”", version_id: 6},
  {id: 160186, book: "Rom", verse: "9.01", unformatted: "不仅如此，丽贝卡也是这样。她由一个人，就是由我们的先祖以撒怀了孕。", version_id: 6},
  {id: 160187, book: "Rom", verse: "9.011", unformatted: "实际上，双子还没有出生，也没有行善或作恶以前——为了要显明神的拣选心意不是本于人的行为，而是出于召唤人的那一位——", version_id: 6},
  {id: 160188, book: "Rom", verse: "9.012", unformatted: "神就对丽贝卡说：“大的将要服事小的。”", version_id: 6},
  {id: 160189, book: "Rom", verse: "9.013", unformatted: "正如经上所记：“我爱雅各，却恨以扫。”", version_id: 6},
  {id: 160190, book: "Rom", verse: "9.014", unformatted: "神公义的拣选\n那么，我们要怎么说呢？难道神不公义吗？绝对不是！", version_id: 6},
  {id: 160191, book: "Rom", verse: "9.015", unformatted: "因为神对摩西说：\n“我要怜悯谁，就怜悯谁；\n我要同情谁，就同情谁。”", version_id: 6},
  {id: 160192, book: "Rom", verse: "9.016", unformatted: "由此可见，这不在于人的意愿，也不在于人的努力，而在于施怜悯的神。", version_id: 6},
  {id: 160193, book: "Rom", verse: "9.017", unformatted: "原来经上有话对法老说：\n“我兴起你，正是为此：\n好让我藉着你来显出我的大能，\n使我的名传遍天下。”", version_id: 6},
  {id: 160194, book: "Rom", verse: "9.018", unformatted: "这样看来，神要怜悯谁，就怜悯谁；要使谁顽固，就使谁顽固。", version_id: 6},
  {id: 160195, book: "Rom", verse: "9.019", unformatted: "那么，你会对我说：“既然如此，神为什么还要指责人呢？到底谁能抵挡他的旨意呢？”", version_id: 6},
  {id: 160196, book: "Rom", verse: "9.02", unformatted: "哦，人哪！你到底是谁，竟然向神顶嘴呢？被造的难道可以对造它的说：“你为什么把我造成这样呢？”", version_id: 6},
  {id: 160197, book: "Rom", verse: "9.021", unformatted: "难道陶匠没有权柄从同一团泥中，既造出贵重的器皿，又造出卑贱的器皿吗？", version_id: 6},
  {id: 160198, book: "Rom", verse: "9.022", unformatted: "但如果神想要显出他的震怒，显明他的能力，就以极大的耐心容忍那些承受震怒的器皿，就是他早已预备要归于灭亡的器皿，那又怎么样呢？", version_id: 6},
  {id: 160199, book: "Rom", verse: "9.023", unformatted: "再者，如果这是为要将他那荣耀的丰盛显明在这些得蒙怜悯的器皿，就是他早已预备要归于荣耀的器皿上，那又怎么样呢？", version_id: 6},
  {id: 160200, book: "Rom", verse: "9.024", unformatted: "这些器皿就是我们这些蒙召的人：不仅是从犹太人中，也是从外邦人中蒙召的人。", version_id: 6},
  {id: 160201, book: "Rom", verse: "9.025", unformatted: "正如神在何西阿书上也说：\n“我将要称那不是我子民的为‘我的子民’，\n那不蒙爱的为‘蒙爱的’。", version_id: 6},
  {id: 160202, book: "Rom", verse: "9.026", unformatted: "从前在什么地方对他们说\n‘你们不是我的子民’，\n就在那里，他们将被称为‘永生神的儿女。’”", version_id: 6},
  {id: 160203, book: "Rom", verse: "9.027", unformatted: "以赛亚论到以色列人，呼喊说：\n“以色列子民的人数虽然多如海沙，\n将要得救的却是剩余的少数。", version_id: 6},
  {id: 160204, book: "Rom", verse: "9.028", unformatted: "主就是要在地上\n彻底、迅速地成就他的话。”", version_id: 6},
  {id: 160205, book: "Rom", verse: "9.029", unformatted: "又如以赛亚预先说过：\n“要不是万军之主给我们留下后裔，\n我们早就变得像所多玛、格摩拉那样了。”", version_id: 6},
  {id: 160206, book: "Rom", verse: "9.03", unformatted: "以色列人的不信\n那么，我们要怎么说呢？那原来没有追求义的外邦人，却得到了义，就是因信而得的义；", version_id: 6},
  {id: 160207, book: "Rom", verse: "9.031", unformatted: "而以色列人追求律法的义，却没有达到律法的义。", version_id: 6},
  {id: 160208, book: "Rom", verse: "9.032", unformatted: "这是为什么呢？因为他们不是本于信，倒像是本于行为去追求的，所以他们就绊倒在那“绊脚的石头”上。", version_id: 6},
  {id: 160209, book: "Rom", verse: "9.033", unformatted: "正如经上所记：\n“看哪，我在锡安放一块绊脚的石头，\n是使人绊倒的磐石；\n可是信靠他的人将不至于蒙羞。”", version_id: 6},
  {id: 160210, book: "Rom", verse: "10.001", unformatted: "出于信的义\n弟兄们，我心里热切盼望，并替以色列人向神祈求，是为了他们的救恩。", version_id: 6},
  {id: 160211, book: "Rom", verse: "10.002", unformatted: "我见证他们对神有热心，但不是按着真正的认识。", version_id: 6},
  {id: 160212, book: "Rom", verse: "10.003", unformatted: "就是说，他们不明白神的义，想要确立自己的义，就不服从神的义了。", version_id: 6},
  {id: 160213, book: "Rom", verse: "10.004", unformatted: "事实上，基督是律法的终结，使所有相信的人都以致称义。", version_id: 6},
  {id: 160214, book: "Rom", verse: "10.005", unformatted: "原来，有关出于律法的义，摩西写道：“遵行这些事的人，将因此而活”；", version_id: 6},
  {id: 160215, book: "Rom", verse: "10.006", unformatted: "而有关本于信的义，却这样吩咐：“你心里不用说：谁要升到天上去？”——这意思是要把基督带下来；", version_id: 6},
  {id: 160216, book: "Rom", verse: "10.007", unformatted: "“也不用说：谁要下到无底坑去？”——这意思是将基督从死人中领上来。", version_id: 6},
  {id: 160217, book: "Rom", verse: "10.008", unformatted: "然而，这是怎么说呢？“这话就在你身边，在你口中，在你心里。”这就是我们所传的有关信仰的话语：", version_id: 6},
  {id: 160218, book: "Rom", verse: "10.009", unformatted: "你如果口里承认耶稣是主，心里相信神使他从死人中复活，就将得救。", version_id: 6},
  {id: 160219, book: "Rom", verse: "10.01", unformatted: "就是说，人心里相信以致称义，口里承认以致救恩，", version_id: 6},
  {id: 160220, book: "Rom", verse: "10.011", unformatted: "因为经上说：“所有信靠他的人将不至于蒙羞。”", version_id: 6},
  {id: 160221, book: "Rom", verse: "10.012", unformatted: "犹太人和外邦人并没有分别，原来，万人共同的主，使所有求告他的人都富足。", version_id: 6},
  {id: 160222, book: "Rom", verse: "10.013", unformatted: "的确，“无论谁求告主名，都将得救。”", version_id: 6},
  {id: 160223, book: "Rom", verse: "10.014", unformatted: "以色列人拒绝福音\n既然如此，人没有相信他，怎么能求告他呢？没有听说过他，怎么能相信他呢？没有人传讲，怎么能听到呢？", version_id: 6},
  {id: 160224, book: "Rom", verse: "10.015", unformatted: "如果没有奉差派，又怎么能去传讲呢？正如经上所记：“传讲美好福音的人，他们的脚踪多么美丽！”", version_id: 6},
  {id: 160225, book: "Rom", verse: "10.016", unformatted: "但是，并不是所有的人都顺从了福音，就如以赛亚所说：“主啊，我们所传的，有谁信了呢？”", version_id: 6},
  {id: 160226, book: "Rom", verse: "10.017", unformatted: "因此，信是由听而来，听是藉着基督的话而来。", version_id: 6},
  {id: 160227, book: "Rom", verse: "10.018", unformatted: "那么我要说，他们难道没有听过吗？当然听过——\n“那声音传遍了天下，\n那话语传到了地极。”", version_id: 6},
  {id: 160228, book: "Rom", verse: "10.019", unformatted: "我再说“以色列人难道不明白吗？”首先，摩西说：\n“我要用不是子民的，激起你们的嫉妒之心，\n用无知的民族激起你们的怒气。”", version_id: 6},
  {id: 160229, book: "Rom", verse: "10.02", unformatted: "后来，以赛亚大胆地说：\n“没有寻找我的，我让他们寻见；\n没有求问我的，我向他们显现。”", version_id: 6},
  {id: 160230, book: "Rom", verse: "10.021", unformatted: "但是关于以色列人，他却说：“我整天向那悖逆、顶嘴的子民伸出双手。”", version_id: 6},
  {id: 160231, book: "Rom", verse: "11.001", unformatted: "神没有完全弃绝以色列人\n那么，我要问，难道神弃绝了他的子民吗？绝对没有！其实我也是以色列人，亚伯拉罕的后裔，属于便雅悯支派。", version_id: 6},
  {id: 160232, book: "Rom", verse: "11.002", unformatted: "神并没有弃绝自己预先所知道的子民。难道你们不知道那有关以利亚的经文怎么说吗？他怎样向神抱怨以色列人呢？", version_id: 6},
  {id: 160233, book: "Rom", verse: "11.003", unformatted: "“主啊，他们杀了你的先知们，拆毁了你的祭坛，\n只剩下我一个人，他们还要追索我的命。”", version_id: 6},
  {id: 160234, book: "Rom", verse: "11.004", unformatted: "但神对他的答复是怎么说的呢？“我为自己保留了七千人，他们是没有向巴力屈膝的。”", version_id: 6},
  {id: 160235, book: "Rom", verse: "11.005", unformatted: "照样，在现今的时刻也有剩余的少数，就是藉着恩典蒙拣选的人。", version_id: 6},
  {id: 160236, book: "Rom", verse: "11.006", unformatted: "既然藉着恩典，就不再本于行为了；否则，恩典就不再是恩典了。", version_id: 6},
  {id: 160237, book: "Rom", verse: "11.007", unformatted: "所以怎么样呢？以色列人并没有获得他们所寻求的，蒙拣选的人却获得了，而其余的人都成了刚硬的，", version_id: 6},
  {id: 160238, book: "Rom", verse: "11.008", unformatted: "正如经上所记：\n“神给了他们昏迷的心灵、\n看不见的眼睛、听不见的耳朵，\n直到今天。”", version_id: 6},
  {id: 160239, book: "Rom", verse: "11.009", unformatted: "大卫也说：\n“让他们的筵席成为网罗、陷阱、绊脚石，\n又成为他们的报应。", version_id: 6},
  {id: 160240, book: "Rom", verse: "11.01", unformatted: "让他们的眼睛昏暗不能看。\n让他们的腰背永远弯曲。”", version_id: 6},
  {id: 160241, book: "Rom", verse: "11.011", unformatted: "神没有最终弃绝以色列人\n所以我要问，难道以色列人跌倒了，是要他们败落吗？绝对不是！相反，因他们的过犯，救恩就临到了外邦人，为要激起以色列人的嫉妒之心。", version_id: 6},
  {id: 160242, book: "Rom", verse: "11.012", unformatted: "既然他们的过犯能使世人富足，他们的失败能使外邦人富足，更何况他们的全数满足呢！", version_id: 6},
  {id: 160243, book: "Rom", verse: "11.013", unformatted: "现在我向你们外邦人说话：既然我确实是外邦人的使徒，我就以我的服事工作为荣耀，", version_id: 6},
  {id: 160244, book: "Rom", verse: "11.014", unformatted: "或许激起我同胞的嫉妒之心，好拯救他们中的一些人。", version_id: 6},
  {id: 160245, book: "Rom", verse: "11.015", unformatted: "实际上，如果他们被丢弃，使世人与神和好了，那么他们蒙接纳，如果不是从死人中复生，又是什么呢？", version_id: 6},
  {id: 160246, book: "Rom", verse: "11.016", unformatted: "如果初熟的麦子是圣洁的，那面团也就圣洁了；如果树根是圣洁的，那树枝也就圣洁了。", version_id: 6},
  {id: 160247, book: "Rom", verse: "11.017", unformatted: "但如果有些树枝被折下来，而你这野橄榄枝被接在其上，并且一同有份于那橄榄树根的丰富养分，", version_id: 6},
  {id: 160248, book: "Rom", verse: "11.018", unformatted: "那么你就不应该向那些树枝自夸了。而就算你自夸，也不是你托着树根，而是树根托着你。", version_id: 6},
  {id: 160249, book: "Rom", verse: "11.019", unformatted: "这样，你会说：“那些树枝被折下来，就是要我能被接上去。”", version_id: 6},
  {id: 160250, book: "Rom", verse: "11.02", unformatted: "不错！他们因不信被折了下来，而你因信已经站立得住；你不可心高气傲，反要惧怕；", version_id: 6},
  {id: 160251, book: "Rom", verse: "11.021", unformatted: "因为神如果没有顾惜原生的树枝，恐怕也不会顾惜你。", version_id: 6},
  {id: 160252, book: "Rom", verse: "11.022", unformatted: "所以看哪！神的仁慈和严厉——对堕落的人，神一定是严厉的，但是对你，如果你留在他的仁慈中，神就是仁慈的；否则，你也将被砍下来。", version_id: 6},
  {id: 160253, book: "Rom", verse: "11.023", unformatted: "而且，那些被折下来的树枝如果不再坚持不信，也将被接上，因为神能够把他们重新接上。", version_id: 6},
  {id: 160254, book: "Rom", verse: "11.024", unformatted: "要知道，如果你从天然的野橄榄树上被砍下来，尚且能违反自然、被接在栽培的橄榄树上，何况那些原生的树枝，难道不更能接在自己的橄榄树上吗？", version_id: 6},
  {id: 160255, book: "Rom", verse: "11.025", unformatted: "弟兄们，我不愿意你们不明白这奥秘，免得你们自以为聪明。这奥秘就是：以色列人中的部分人变得刚硬，直到外邦人的全数满足了，", version_id: 6},
  {id: 160256, book: "Rom", verse: "11.026", unformatted: "以色列全家才会得救，正如经上所记：\n“一位拯救者将从锡安而来，\n他要从雅各除去不敬神的心，", version_id: 6},
  {id: 160257, book: "Rom", verse: "11.027", unformatted: "这就是我的约——\n当我除去他们罪孽的时候，所给他们的约。”", version_id: 6},
  {id: 160258, book: "Rom", verse: "11.028", unformatted: "就福音而言，他们因你们的缘故，固然是神的敌人；但就拣选而言，他们因祖先的缘故，却是蒙爱的，", version_id: 6},
  {id: 160259, book: "Rom", verse: "11.029", unformatted: "因为神的恩赐和召唤是不撤回的。", version_id: 6},
  {id: 160260, book: "Rom", verse: "11.03", unformatted: "正如你们以前不肯信从神而现在却因他们的不信从反而蒙了怜悯；", version_id: 6},
  {id: 160261, book: "Rom", verse: "11.031", unformatted: "照样，他们如今也不肯信从，为要藉着你们所蒙的怜悯，让他们现在也可以蒙怜悯，", version_id: 6},
  {id: 160262, book: "Rom", verse: "11.032", unformatted: "因为神把所有的人都圈在不信从之中，是为了要怜悯所有的人。", version_id: 6},
  {id: 160263, book: "Rom", verse: "11.033", unformatted: "赞美之歌\n哦，深奥啊！神的智慧和知识的财富！\n他的判断是多么地无法探究，\n他的道是多么地无法测度！", version_id: 6},
  {id: 160264, book: "Rom", verse: "11.034", unformatted: "“到底是谁知道主的心意？\n是谁做过他的谋士？", version_id: 6},
  {id: 160265, book: "Rom", verse: "11.035", unformatted: "又是谁首先给了主，\n使自己将来得到回报呢？”", version_id: 6},
  {id: 160266, book: "Rom", verse: "11.036", unformatted: "因为万有都是出于他、藉着他、归于他。\n愿荣耀归于他，直到永远！阿们。", version_id: 6},
  {id: 160267, book: "Rom", verse: "12.001", unformatted: "活的祭物\n所以弟兄们，我藉着神的各样怜悯劝你们，要把自己的身体献上，做为圣洁、蒙神喜悦的活祭；这是你们理所当然的事奉。", version_id: 6},
  {id: 160268, book: "Rom", verse: "12.002", unformatted: "不要效法这世代，要藉着理性的更新而改变，使你们可以分辨什么是神的旨意——那美善、可喜悦、完美的旨意。", version_id: 6},
  {id: 160269, book: "Rom", verse: "12.003", unformatted: "成为一体\n我藉着所赐给我的恩典，对你们中间的每个人说：不要自视过高，过于所当看的，而要照着神所分给每个人信心的尺度，清醒地看自己。", version_id: 6},
  {id: 160270, book: "Rom", verse: "12.004", unformatted: "正如我们一个身体有很多部分，而且各部分都有不同的功用；", version_id: 6},
  {id: 160271, book: "Rom", verse: "12.005", unformatted: "照样，我们这许多人，在基督里是一个身体，而且各个部分都彼此相属。", version_id: 6},
  {id: 160272, book: "Rom", verse: "12.006", unformatted: "照着所赐给我们的恩典，我们各有不同的恩赐：\n如果是做先知传道的恩赐，就按照信心的程度用它；", version_id: 6},
  {id: 160273, book: "Rom", verse: "12.007", unformatted: "如果是服事的，就用在服事上；\n如果是教导的，就用在教导上；", version_id: 6},
  {id: 160274, book: "Rom", verse: "12.008", unformatted: "如果是劝勉的，就用在劝勉上；\n分享的，要慷慨；\n带领的，要殷勤；\n怜悯人的，要甘心乐意。", version_id: 6},
  {id: 160275, book: "Rom", verse: "12.009", unformatted: "基督徒的行为规范\n爱，不要虚假；要厌弃恶、持守善；", version_id: 6},
  {id: 160276, book: "Rom", verse: "12.01", unformatted: "要以弟兄之情彼此相爱，以尊敬之心彼此谦让，", version_id: 6},
  {id: 160277, book: "Rom", verse: "12.011", unformatted: "殷勤而不懒惰，灵里时时火热，常常服事主。", version_id: 6},
  {id: 160278, book: "Rom", verse: "12.012", unformatted: "在盼望中要欢喜，在患难里要忍耐，在祷告上要恒切。", version_id: 6},
  {id: 160279, book: "Rom", verse: "12.013", unformatted: "要分担圣徒的缺乏；尽力款待客旅。", version_id: 6},
  {id: 160280, book: "Rom", verse: "12.014", unformatted: "要祝福那些逼迫你们的人；要祝福，不要诅咒。", version_id: 6},
  {id: 160281, book: "Rom", verse: "12.015", unformatted: "要与快乐的人一同快乐；与哀哭的人一同哀哭。", version_id: 6},
  {id: 160282, book: "Rom", verse: "12.016", unformatted: "要彼此同心；不要心高气傲，而要俯就卑微的人；不要自以为聪明。", version_id: 6},
  {id: 160283, book: "Rom", verse: "12.017", unformatted: "不要对任何人以恶报恶；要敬重所有的人看为美善的事；", version_id: 6},
  {id: 160284, book: "Rom", verse: "12.018", unformatted: "如果有可能，尽量在你们的事上与所有的人和睦。", version_id: 6},
  {id: 160285, book: "Rom", verse: "12.019", unformatted: "各位蒙爱的人哪，不要自己报复，宁可给神的震怒留地步，因为经上记着：“主说：报应在我，我将回报。”", version_id: 6},
  {id: 160286, book: "Rom", verse: "12.02", unformatted: "不但如此，\n“如果你的敌人饿了，就给他吃；\n如果他渴了，就给他喝；\n因为你这样做，就是把炭火堆在他的头上。”", version_id: 6},
  {id: 160287, book: "Rom", verse: "12.021", unformatted: "不要被恶所胜，反要以善胜恶。", version_id: 6},
  {id: 160288, book: "Rom", verse: "13.001", unformatted: "基督徒与社会\n所有的人都当服从在上掌权的，因为没有一个掌权的不是出于神。所有掌权的，都是由神所指定的。", version_id: 6},
  {id: 160289, book: "Rom", verse: "13.002", unformatted: "所以，抗拒掌权的，就是违抗神所设立的；违抗的人，就自招惩罚。", version_id: 6},
  {id: 160290, book: "Rom", verse: "13.003", unformatted: "事实上，对良善的行为，掌权者并不是可怕的；而对邪恶的行为，他才是可怕的。那么，你想要不惧怕掌权的，就应当做良善的事。这样，你就会得到他的称赞。", version_id: 6},
  {id: 160291, book: "Rom", verse: "13.004", unformatted: "要知道，他是神的仆役，对你是有益处的。但你如果作恶，就该惧怕，因为他不是毫无理由的佩带刀剑。他是神的仆役，是报应者，使震怒临到作恶的人。", version_id: 6},
  {id: 160292, book: "Rom", verse: "13.005", unformatted: "因此，你们必须服从，不仅是为了震怒的缘故，也是为了良心。", version_id: 6},
  {id: 160293, book: "Rom", verse: "13.006", unformatted: "其实你们纳税，也是为了这缘故；因为他们是神的仆役，正是为此专心服事。", version_id: 6},
  {id: 160294, book: "Rom", verse: "13.007", unformatted: "每个人所当得的，你们都要给他：当得税的，就给他税；当得捐的，就给他捐；当受敬畏的，就敬畏他；当受尊敬的，就尊敬他。", version_id: 6},
  {id: 160295, book: "Rom", verse: "13.008", unformatted: "爱邻如己\n你们对任何人都不要亏欠什么；唯有在彼此相爱的事上，要常以为亏欠。实际上，爱别人的，就成全了律法。", version_id: 6},
  {id: 160296, book: "Rom", verse: "13.009", unformatted: "这是因为，那“不可通奸，不可杀人，不可偷窃，不可贪心”的诫命，或是别的什么诫命，都包含在“要爱邻如己”这一句话里了。", version_id: 6},
  {id: 160297, book: "Rom", verse: "13.01", unformatted: "爱是对邻人不做恶事，因此爱是成全律法的。", version_id: 6},
  {id: 160298, book: "Rom", verse: "13.011", unformatted: "效法基督\n再者，你们已经看见了这个时代，是你们早应该从沉睡中醒过来的时候，因为比我们初信的时候，我们的救恩现在更近了。", version_id: 6},
  {id: 160299, book: "Rom", verse: "13.012", unformatted: "黑夜已深，白昼近了。所以，我们要脱去黑暗的行为，配上光明的兵器。", version_id: 6},
  {id: 160300, book: "Rom", verse: "13.013", unformatted: "我们要行事端正，好像在白昼中那样：不可荒宴、醉酒；不可淫乱、好色；不可纷争、嫉妒。", version_id: 6},
  {id: 160301, book: "Rom", verse: "13.014", unformatted: "相反，要穿上主耶稣基督；不要为了私欲而在属肉体的事上做任何安排。", version_id: 6},
  {id: 160302, book: "Rom", verse: "14.001", unformatted: "自由的律\n你们要接纳在信仰上软弱的人，避免在有争议的事上辩论。", version_id: 6},
  {id: 160303, book: "Rom", verse: "14.002", unformatted: "虽然有的人相信什么都可以吃，但那软弱的人却只吃蔬菜。", version_id: 6},
  {id: 160304, book: "Rom", verse: "14.003", unformatted: "吃的人不要藐视不吃的人，不吃的人也不要评断吃的人，因为神已经接纳他了。", version_id: 6},
  {id: 160305, book: "Rom", verse: "14.004", unformatted: "你到底是谁，竟然评断别人的仆人？他或站稳或跌倒，是他自己主人的事；而且他会站得住，因为主能使他站得住。", version_id: 6},
  {id: 160306, book: "Rom", verse: "14.005", unformatted: "虽然有的人判定这一天比那一天重要，但有的人却判定每一天都一样。每个人当在自己理性上确信不疑。", version_id: 6},
  {id: 160307, book: "Rom", verse: "14.006", unformatted: "注重某一天的人，是为主而注重的；吃的人，是为主而吃的，因为他感谢神；不吃的人，也是为主不吃的，他也感谢神。", version_id: 6},
  {id: 160308, book: "Rom", verse: "14.007", unformatted: "我们没有一个人是为自己而活，也没有一个人是为自己而死。", version_id: 6},
  {id: 160309, book: "Rom", verse: "14.008", unformatted: "就是说，我们如果活着，是为主而活；如果死了，是为主而死。所以，我们无论是活着，还是死了，总是属于主。", version_id: 6},
  {id: 160310, book: "Rom", verse: "14.009", unformatted: "正是为此，基督死了，而且复活了，要做死人和活人的主。", version_id: 6},
  {id: 160311, book: "Rom", verse: "14.01", unformatted: "至于你，为什么评断你的弟兄呢？又为什么藐视你的弟兄呢？要知道，我们都要站在神的审判台前，", version_id: 6},
  {id: 160312, book: "Rom", verse: "14.011", unformatted: "因为经上记着：\n“主说：我指着我的永生起誓，\n万膝将向我跪下，\n万口将承认神。”", version_id: 6},
  {id: 160313, book: "Rom", verse: "14.012", unformatted: "由此可见，我们每个人都要把自己的事向神做出交代。", version_id: 6},
  {id: 160314, book: "Rom", verse: "14.013", unformatted: "爱的律\n所以，我们不要再彼此评断，倒要下定决心，不给弟兄放下绊跌物或绊脚石。", version_id: 6},
  {id: 160315, book: "Rom", verse: "14.014", unformatted: "我知道，并且在主耶稣里深信：没有一样东西本身是不洁净的。只是，如果有人认为什么是不洁净的，对他来说，那就是不洁净的。", version_id: 6},
  {id: 160316, book: "Rom", verse: "14.015", unformatted: "如果因食物的缘故使你的弟兄忧伤，你就不再是凭着爱行事了。基督已经替他死了，不可因你的食物毁了他。", version_id: 6},
  {id: 160317, book: "Rom", verse: "14.016", unformatted: "所以，不要让你们看为好的事受到毁谤，", version_id: 6},
  {id: 160318, book: "Rom", verse: "14.017", unformatted: "因为神的国不在于吃喝，而在于圣灵里的公义、和睦、喜乐。", version_id: 6},
  {id: 160319, book: "Rom", verse: "14.018", unformatted: "要知道，如此服事基督的人，是蒙神喜悦的，也是受人赞许的。", version_id: 6},
  {id: 160320, book: "Rom", verse: "14.019", unformatted: "这样看来，我们要追求和睦的事和彼此造就的事。", version_id: 6},
  {id: 160321, book: "Rom", verse: "14.02", unformatted: "不要因食物的缘故破坏神的工作。一切固然都是洁净的，但如果有人因吃东西使人绊脚，这就是恶事了。", version_id: 6},
  {id: 160322, book: "Rom", verse: "14.021", unformatted: "不在吃肉、喝酒、或其他事上使你的弟兄绊倒，这才是好的。", version_id: 6},
  {id: 160323, book: "Rom", verse: "14.022", unformatted: "你所持有的信念，自己要在神面前持有；在自己认为对的事上不自责的人是蒙福的。", version_id: 6},
  {id: 160324, book: "Rom", verse: "14.023", unformatted: "但那疑惑的人如果吃了，就被定罪了，因为不是本于信；而一切不是本于信的，就是罪。", version_id: 6},
  {id: 160325, book: "Rom", verse: "15.001", unformatted: "为他人的益处\n我们坚强的人，应该担当不坚强人的软弱，不求自己的喜悦。", version_id: 6},
  {id: 160326, book: "Rom", verse: "15.002", unformatted: "我们每个人当为了邻人的益处让他喜悦，使他得造就。", version_id: 6},
  {id: 160327, book: "Rom", verse: "15.003", unformatted: "因为基督不但不求自己的喜悦，反而像经上所记的：“那些责骂你之人的责骂，都落在我身上了。”", version_id: 6},
  {id: 160328, book: "Rom", verse: "15.004", unformatted: "实际上，凡是先前被记载的，都是为了教导我们而写的，好让我们藉着忍耐和经上的安慰，怀有盼望。", version_id: 6},
  {id: 160329, book: "Rom", verse: "15.005", unformatted: "愿赐忍耐和安慰的神，使你们藉着基督耶稣，彼此同心合意，", version_id: 6},
  {id: 160330, book: "Rom", verse: "15.006", unformatted: "好使你们同心同声荣耀神——我们主耶稣基督的父。", version_id: 6},
  {id: 160331, book: "Rom", verse: "15.007", unformatted: "共同荣耀主\n因此，你们当彼此接纳，就像基督也接纳了你们归于神的荣耀。", version_id: 6},
  {id: 160332, book: "Rom", verse: "15.008", unformatted: "我是说：基督为了神的真理，已成为受割礼之人的仆人，为要证实对祖先的各样应许，", version_id: 6},
  {id: 160333, book: "Rom", verse: "15.009", unformatted: "并且使外邦人因着所蒙的怜悯而荣耀神，正如经上所记：\n“为此，我要在外邦人中承认你，\n歌颂你的名。”", version_id: 6},
  {id: 160334, book: "Rom", verse: "15.01", unformatted: "经上又说：“外邦人哪，你们要与他的子民一同庆祝！”", version_id: 6},
  {id: 160335, book: "Rom", verse: "15.011", unformatted: "又说：\n“万国啊，你们要赞美主！\n万民哪，你们要颂赞他！”", version_id: 6},
  {id: 160336, book: "Rom", verse: "15.012", unformatted: "又有以赛亚说：\n“将有耶西的根，\n就是兴起来统治外邦人的那一位；\n外邦人要仰望他。”", version_id: 6},
  {id: 160337, book: "Rom", verse: "15.013", unformatted: "愿赐盼望的神，因着你们的信，将一切喜乐和平安充满你们，使你们藉着圣灵的能力满怀盼望。", version_id: 6},
  {id: 160338, book: "Rom", verse: "15.014", unformatted: "传扬福音\n我的弟兄们，至于你们，我自己同样深信：你们也满有良善，充满一切知识，又能彼此劝诫。", version_id: 6},
  {id: 160339, book: "Rom", verse: "15.015", unformatted: "不过我要提醒你们，藉着神所赐给我的恩典，我在部分地方写得比较大胆。", version_id: 6},
  {id: 160340, book: "Rom", verse: "15.016", unformatted: "这恩典使我为外邦人成了基督耶稣的仆役、做了神福音的祭司，好让外邦人藉着圣灵被分别为圣，成为蒙悦纳的供物。", version_id: 6},
  {id: 160341, book: "Rom", verse: "15.017", unformatted: "所以，我在基督耶稣里，在属神的事上有可夸耀的。", version_id: 6},
  {id: 160342, book: "Rom", verse: "15.018", unformatted: "我什么都不敢说，只说基督藉着我所完成的事，就是为了外邦人的顺从，他藉着我所说的和所做的事，", version_id: 6},
  {id: 160343, book: "Rom", verse: "15.019", unformatted: "藉着神迹、奇事的能力和圣灵的能力所完成的事。结果我从耶路撒冷及其周围一带直到伊利里亚，把基督的福音都传遍了。", version_id: 6},
  {id: 160344, book: "Rom", verse: "15.02", unformatted: "这样，我立定心志要在基督的名没有被传过的地方传福音，免得建造在别人的根基上，", version_id: 6},
  {id: 160345, book: "Rom", verse: "15.021", unformatted: "正如经上所记：\n“没有听说过他的人，将要看见；\n没有听见过的人，将要领悟。”", version_id: 6},
  {id: 160346, book: "Rom", verse: "15.022", unformatted: "保罗的计划\n所以，我也多次受到拦阻，不能到你们那里去。", version_id: 6},
  {id: 160347, book: "Rom", verse: "15.023", unformatted: "但如今，这些地区再没有可传的地方了，况且多年来我一直渴望见到你们。", version_id: 6},
  {id: 160348, book: "Rom", verse: "15.024", unformatted: "就是说，当我去西班牙的时候，我希望能顺路看你们；先与你们相聚，使我稍得满足；然后由你们在那里送我上路。", version_id: 6},
  {id: 160349, book: "Rom", verse: "15.025", unformatted: "但如今，我要到耶路撒冷去服事圣徒，", version_id: 6},
  {id: 160350, book: "Rom", verse: "15.026", unformatted: "因为马其顿省和亚该亚省的信徒，乐意与耶路撒冷圣徒中的穷人有一些分享。", version_id: 6},
  {id: 160351, book: "Rom", verse: "15.027", unformatted: "确实，他们是乐意的，不过这也是他们的责任，因为外邦人如果分享了犹太人属灵的事，也就应该在物质的事上服事犹太人。", version_id: 6},
  {id: 160352, book: "Rom", verse: "15.028", unformatted: "所以，当我完成了这件事，向他们印证了这事的成果，我就要经过你们那里，往西班牙去。", version_id: 6},
  {id: 160353, book: "Rom", verse: "15.029", unformatted: "我知道，我到你们那里去的时候，一定带着基督丰盛完美的祝福而去。", version_id: 6},
  {id: 160354, book: "Rom", verse: "15.03", unformatted: "弟兄们，藉着我们的主耶稣基督、藉着圣灵的爱，我恳求你们与我一同竭力为我向神祷告：", version_id: 6},
  {id: 160355, book: "Rom", verse: "15.031", unformatted: "愿神救我脱离犹太地区不肯信从的人，也使我为耶路撒冷的这服事可以蒙圣徒们的悦纳，", version_id: 6},
  {id: 160356, book: "Rom", verse: "15.032", unformatted: "好让我能照着神的旨意，快快乐乐地到你们那里去，和你们同得安息。", version_id: 6},
  {id: 160357, book: "Rom", verse: "15.033", unformatted: "愿赐平安的神，与你们大家同在！阿们。", version_id: 6},
  {id: 160358, book: "Rom", verse: "16.001", unformatted: "保罗推荐菲比\n我向你们推荐我们的姐妹菲比，她是坚革里教会的女执事。", version_id: 6},
  {id: 160359, book: "Rom", verse: "16.002", unformatted: "请你们在主里以圣徒配得的方式来欢迎她。无论她在什么事上需要你们，都请你们帮助她，因为她实在帮助了许多人，也帮助了我。", version_id: 6},
  {id: 160360, book: "Rom", verse: "16.003", unformatted: "问候信徒\n请问候普茜拉和阿奎拉；他们是我在基督耶稣里的同工。", version_id: 6},
  {id: 160361, book: "Rom", verse: "16.004", unformatted: "为我的生命，他们将自己的生死置之度外。不单我感激他们，就是外邦的各教会也都感激他们。", version_id: 6},
  {id: 160362, book: "Rom", verse: "16.005", unformatted: "请问候在他们家里的教会。\n请问候我亲爱的以佩尼托，他是亚细亚省归入基督的初熟果子。", version_id: 6},
  {id: 160363, book: "Rom", verse: "16.006", unformatted: "请问候玛丽亚，她为你们多多劳苦。", version_id: 6},
  {id: 160364, book: "Rom", verse: "16.007", unformatted: "请问候与我一起坐过牢的同胞安德罗尼克和犹尼亚，他们在使徒中有名望，也比我先成为在基督里的。", version_id: 6},
  {id: 160365, book: "Rom", verse: "16.008", unformatted: "请问候在主里我亲爱的安普利亚托。", version_id: 6},
  {id: 160366, book: "Rom", verse: "16.009", unformatted: "请问候欧巴诺，他是我们在基督里的同工，也问候我亲爱的斯塔库斯。", version_id: 6},
  {id: 160367, book: "Rom", verse: "16.01", unformatted: "请问候阿比利斯，他在基督里经过了考验。\n请问候亚利多布家里的人。", version_id: 6},
  {id: 160368, book: "Rom", verse: "16.011", unformatted: "请问候我的同胞希罗迪恩。\n请问候纳吉苏家中在主里的人。", version_id: 6},
  {id: 160369, book: "Rom", verse: "16.012", unformatted: "请问候在主里劳苦的特鲁菲娜和特鲁弗萨。\n请问候亲爱的佩西丝，她在主里多多劳苦。", version_id: 6},
  {id: 160370, book: "Rom", verse: "16.013", unformatted: "请问候在主里蒙拣选的鲁弗斯和他的母亲，对我来说他的母亲就是我的母亲。", version_id: 6},
  {id: 160371, book: "Rom", verse: "16.014", unformatted: "请问候阿森克里托、弗勒贡、赫米斯、帕特罗巴、赫马斯，以及与他们在一起的弟兄们。", version_id: 6},
  {id: 160372, book: "Rom", verse: "16.015", unformatted: "请问候非罗罗古和犹莉娅，尼利亚和他的妹妹，以及奥林帕斯，还有与他们在一起的所有圣徒。", version_id: 6},
  {id: 160373, book: "Rom", verse: "16.016", unformatted: "你们要用圣洁的亲吻礼彼此问候。\n基督的各教会都问候你们。", version_id: 6},
  {id: 160374, book: "Rom", verse: "16.017", unformatted: "要留心制造分裂的人\n弟兄们，我劝你们，要留心那些针对你们所受的教导而制造分裂和绊脚石的人。你们要远离他们。", version_id: 6},
  {id: 160375, book: "Rom", verse: "16.018", unformatted: "因为这样的人不是服事我们的主基督，而是服事自己的私欲。他们用花言巧语，欺骗单纯人的心。", version_id: 6},
  {id: 160376, book: "Rom", verse: "16.019", unformatted: "称赞与祝福\n你们的顺从，大家都知道了，所以我为你们而欢喜。但我希望你们在善事上有智慧，在恶事上无知。", version_id: 6},
  {id: 160377, book: "Rom", verse: "16.02", unformatted: "赐平安的神，很快要把撒旦压碎在你们的脚下。愿我们主耶稣的恩典与你们同在！", version_id: 6},
  {id: 160378, book: "Rom", verse: "16.021", unformatted: "我的同工提摩太和我的同胞卢吉斯、耶森、索西巴特都问候你们。", version_id: 6},
  {id: 160379, book: "Rom", verse: "16.022", unformatted: "代笔写这书信的我——特提乌斯，在主里问候你们。", version_id: 6},
  {id: 160380, book: "Rom", verse: "16.023", unformatted: "那接待我，也接待全教会的盖尤斯问候你们。本城的财务官以拉斯托和弟兄库尔托斯问候你们。", version_id: 6},
  {id: 160381, book: "Rom", verse: "16.024", unformatted: "愿我们主耶稣基督的恩典与你们大家同在！阿们。", version_id: 6},
  {id: 160382, book: "Rom", verse: "16.025", unformatted: "荣耀归于神\n愿荣耀归于神——他能藉着我的福音和有关耶稣基督的传道，藉着奥秘的启示坚固你们。这奥秘亘古以来是隐藏的，", version_id: 6},
  {id: 160383, book: "Rom", verse: "16.026", unformatted: "然而如今已经得以显现；并且按照永恒神的命令，藉着先知们的经文显明了出来。这是为要在万民中带来信仰上的顺从——", version_id: 6},
  {id: 160384, book: "Rom", verse: "16.027", unformatted: "愿荣耀，藉着耶稣基督，归于这位独一全智的神，直到永远！阿们。", version_id: 6},
  {id: 160385, book: "1Cor", verse: "1.001", unformatted: "问安\n照着神的旨意蒙召做基督耶稣使徒的保罗，以及弟兄索提尼，", version_id: 6},
  {id: 160386, book: "1Cor", verse: "1.002", unformatted: "致那在哥林多属神的教会，就是在基督耶稣里被分别为圣、蒙召成为圣徒的人，以及所有在各地求告我们主耶稣基督之名的人。基督是他们的主，也是我们的主。", version_id: 6},
  {id: 160387, book: "1Cor", verse: "1.003", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 160388, book: "1Cor", verse: "1.004", unformatted: "感恩\n我常常为你们感谢我的神，因你们在基督耶稣里被赐予了神的恩典，", version_id: 6},
  {id: 160389, book: "1Cor", verse: "1.005", unformatted: "使你们在基督里凡事都得以富足——在一切话语上、一切知识上——", version_id: 6},
  {id: 160390, book: "1Cor", verse: "1.006", unformatted: "正如有关基督的见证在你们中间得以确立，", version_id: 6},
  {id: 160391, book: "1Cor", verse: "1.007", unformatted: "以致你们在任何恩赐上都不缺乏，热切等待着我们主耶稣基督的显现。", version_id: 6},
  {id: 160392, book: "1Cor", verse: "1.008", unformatted: "他也将使你们坚定到底，使你们在我们主耶稣基督的日子无可指责。", version_id: 6},
  {id: 160393, book: "1Cor", verse: "1.009", unformatted: "神是信实的；你们已经被他召唤，进到与他的儿子——我们主耶稣基督的契合里。", version_id: 6},
  {id: 160394, book: "1Cor", verse: "1.01", unformatted: "教会中的纷争\n弟兄们，我藉着我们主耶稣基督的名，恳求你们大家言语一致；你们中间不要分裂，而要在同一个心思、同一个目的上得以完备。", version_id: 6},
  {id: 160395, book: "1Cor", verse: "1.011", unformatted: "我的弟兄们，事实上，克洛伊家的一些人告诉了我有关你们的事，说你们中间有纷争。", version_id: 6},
  {id: 160396, book: "1Cor", verse: "1.012", unformatted: "这就是说：你们各自声称“我是属保罗的”，或“我是属阿波罗的”，或“我是属矶法的”，或“我是属基督的”。", version_id: 6},
  {id: 160397, book: "1Cor", verse: "1.013", unformatted: "难道基督是被分割的吗？难道保罗为你们被钉十字架了吗？或者你们受洗是归入保罗的名吗？", version_id: 6},
  {id: 160398, book: "1Cor", verse: "1.014", unformatted: "我感谢神，除了克里斯普和盖尤斯以外，我没有给你们任何人施过洗，", version_id: 6},
  {id: 160399, book: "1Cor", verse: "1.015", unformatted: "免得有人说，你们是奉我的名受洗。", version_id: 6},
  {id: 160400, book: "1Cor", verse: "1.016", unformatted: "我虽然也给司提法纳的一家人施过洗，此外我却不记得是否给其他人施过洗。", version_id: 6},
  {id: 160401, book: "1Cor", verse: "1.017", unformatted: "要知道，基督差派我，不是为了施洗，而是为了传福音——并不用智慧的话语，免得基督的十字架落了空。", version_id: 6},
  {id: 160402, book: "1Cor", verse: "1.018", unformatted: "基督是神的大能和智慧\n原来，十字架的福音对那些正在灭亡的人来说，是愚拙的；但是对我们这些正在被拯救的人来说，却是神的大能。", version_id: 6},
  {id: 160403, book: "1Cor", verse: "1.019", unformatted: "因为经上记着：\n“我将要毁灭智慧人的智慧，\n废弃聪明人的聪明。”", version_id: 6},
  {id: 160404, book: "1Cor", verse: "1.02", unformatted: "智慧人在哪里？经文士在哪里？这世代的雄辩家在哪里？神难道不使这世界的智慧成了愚拙吗？", version_id: 6},
  {id: 160405, book: "1Cor", verse: "1.021", unformatted: "实际上，在神的智慧中，世界既然凭智慧不认识神，神就乐意藉着传道的愚拙，拯救那些相信的人。", version_id: 6},
  {id: 160406, book: "1Cor", verse: "1.022", unformatted: "虽然犹太人求神迹，希腊人寻智慧，", version_id: 6},
  {id: 160407, book: "1Cor", verse: "1.023", unformatted: "但我们却传扬被钉十字架的基督——对犹太人来说是绊脚石，对外邦人来说是愚拙的；", version_id: 6},
  {id: 160408, book: "1Cor", verse: "1.024", unformatted: "不过对那些蒙召的人，无论是犹太人、或是希腊人，基督总是神的大能、神的智慧。", version_id: 6},
  {id: 160409, book: "1Cor", verse: "1.025", unformatted: "因为神的愚拙比人更智慧，神的软弱比人更强壮。", version_id: 6},
  {id: 160410, book: "1Cor", verse: "1.026", unformatted: "当以主夸耀\n弟兄们，想一想你们所蒙的召唤：按人的标准，你们当中有智慧的不多，有权势的不多，出身高贵的也不多。", version_id: 6},
  {id: 160411, book: "1Cor", verse: "1.027", unformatted: "然而神却拣选了世上愚拙的，为了使那些有智慧的羞愧；神又拣选了世上软弱的，为了使那些强壮的羞愧；", version_id: 6},
  {id: 160412, book: "1Cor", verse: "1.028", unformatted: "神也拣选了世上出身低微的、被人藐视的，就是那些不是什么的，为了要废除那些是个什么的，", version_id: 6},
  {id: 160413, book: "1Cor", verse: "1.029", unformatted: "好使所有的人在神面前都不能自夸，", version_id: 6},
  {id: 160414, book: "1Cor", verse: "1.03", unformatted: "但本于神，你们却是在基督耶稣里的。基督耶稣为我们成了从神而来的智慧、公义、圣洁和救赎，", version_id: 6},
  {id: 160415, book: "1Cor", verse: "1.031", unformatted: "为要像经上所记的：“夸耀的当在主里夸耀。”", version_id: 6},
  {id: 160416, book: "1Cor", verse: "2.001", unformatted: "只传基督与十字架\n弟兄们，我以前到你们那里去的时候，并没有用高言大智对你们传讲神的奥秘，", version_id: 6},
  {id: 160417, book: "1Cor", verse: "2.002", unformatted: "因为我曾下定决心，在你们中间不知道别的，只知道耶稣基督，就是被钉了十字架的这一位。", version_id: 6},
  {id: 160418, book: "1Cor", verse: "2.003", unformatted: "并且我在你们那里的时候，又软弱又惧怕，甚至极其战兢。", version_id: 6},
  {id: 160419, book: "1Cor", verse: "2.004", unformatted: "我说话、我传道，不是藉着有智慧的说服性的话语，而是藉着圣灵和大能的明证，", version_id: 6},
  {id: 160420, book: "1Cor", verse: "2.005", unformatted: "好让你们的信仰不靠人的智慧，而靠神的大能。", version_id: 6},
  {id: 160421, book: "1Cor", verse: "2.006", unformatted: "属灵的智慧\n然而，在成熟的人中间，我们也讲智慧，但不是这世代的智慧，也不是这世代中将要被废除的那些统治者的智慧。", version_id: 6},
  {id: 160422, book: "1Cor", verse: "2.007", unformatted: "我们所讲的是那从前被隐藏在奥秘中属神的智慧，就是神在万世以前为了我们的荣耀所预定的智慧；", version_id: 6},
  {id: 160423, book: "1Cor", verse: "2.008", unformatted: "这世代的那些统治者没有一个知道这智慧，因为他们如果知道，就不会把荣耀的主钉上十字架了。", version_id: 6},
  {id: 160424, book: "1Cor", verse: "2.009", unformatted: "然而正如经上所记：\n“神为爱他的人所预备的，\n是眼睛没有见过，耳朵没有听过，\n人心也没有想过的。”", version_id: 6},
  {id: 160425, book: "1Cor", verse: "2.01", unformatted: "但是神却藉着圣灵向我们启示了，因为圣灵洞察一切，甚至洞察神深奥的事。", version_id: 6},
  {id: 160426, book: "1Cor", verse: "2.011", unformatted: "除了人里面的灵，到底谁知道人的事呢？照样，除了神的灵，也没有人知道神的事。", version_id: 6},
  {id: 160427, book: "1Cor", verse: "2.012", unformatted: "我们所领受的，不是这世界的灵，而是从神而来的灵，好让我们能知道神所赐给我们的这些事；", version_id: 6},
  {id: 160428, book: "1Cor", verse: "2.013", unformatted: "并且我们传讲这些事，不是用人的智慧所教导的话语，而是用圣灵所教导的话语，用属灵的来对比属灵的。", version_id: 6},
  {id: 160429, book: "1Cor", verse: "2.014", unformatted: "不过一个属血气的人不接受属神的灵的这些事，因为对他来说，这些是愚拙的；他也不能明白，因为这些事要用属灵的方式才能洞察。", version_id: 6},
  {id: 160430, book: "1Cor", verse: "2.015", unformatted: "属灵的人能洞察一切，但是没有人能洞察他。", version_id: 6},
  {id: 160431, book: "1Cor", verse: "2.016", unformatted: "“到底谁曾知道主的心意？\n到底谁将教导他呢？”\n但是我们有基督的心意了。", version_id: 6},
  {id: 160432, book: "1Cor", verse: "3.001", unformatted: "嫉妒和纷争的问题\n弟兄们，以往我对你们说话，不能把你们当做属灵的，只能当做属肉体的，当做在基督里的小孩子。", version_id: 6},
  {id: 160433, book: "1Cor", verse: "3.002", unformatted: "我喂给你们吃的是奶，不是饭，原来你们那时还不能吃饭——其实现在也还是不能，", version_id: 6},
  {id: 160434, book: "1Cor", verse: "3.003", unformatted: "因为你们仍然是属肉体的。既然你们中间有嫉妒、纷争和分裂，难道你们不就是属肉体的，按人的意思来行事的吗？", version_id: 6},
  {id: 160435, book: "1Cor", verse: "3.004", unformatted: "当有人说：“我是属保罗的”，另有人说：“我是属阿波罗的”时候，难道你们不就是属肉体的吗？", version_id: 6},
  {id: 160436, book: "1Cor", verse: "3.005", unformatted: "神仆人的职份\n其实阿波罗算什么？保罗算什么？都是仆人；藉着他们，你们信了，是照着主所赐给每个人的。", version_id: 6},
  {id: 160437, book: "1Cor", verse: "3.006", unformatted: "我栽种了，阿波罗浇灌了，然而使之生长的还是神。", version_id: 6},
  {id: 160438, book: "1Cor", verse: "3.007", unformatted: "所以，除了使之生长的神，那栽种的和那浇灌的都算不得什么。", version_id: 6},
  {id: 160439, book: "1Cor", verse: "3.008", unformatted: "栽种的和浇灌的都一样，只是将来每个人要照着自己的劳苦得自己的报偿。", version_id: 6},
  {id: 160440, book: "1Cor", verse: "3.009", unformatted: "实际上，我们是神的同工；你们是神的田地，是神的建筑物。", version_id: 6},
  {id: 160441, book: "1Cor", verse: "3.01", unformatted: "我照着神赐给我的恩典，像一个有智慧的建筑师，立好了根基，然后别人在上面建造；只是每个人应该当心怎样在上面建造，", version_id: 6},
  {id: 160442, book: "1Cor", verse: "3.011", unformatted: "因为除了那已经立好的根基以外，没有人能立别的；那根基就是耶稣基督。", version_id: 6},
  {id: 160443, book: "1Cor", verse: "3.012", unformatted: "如果有人用金、银、宝石，木、草、禾秸在那根基上建造，", version_id: 6},
  {id: 160444, book: "1Cor", verse: "3.013", unformatted: "每个人的工程将来要显露，因为那日子要把它显明；原来它要被火显现出来，这火要考验每个人的工程是怎样的。", version_id: 6},
  {id: 160445, book: "1Cor", verse: "3.014", unformatted: "如果有人在那根基上面所建造的工程能存留得住，这个人就将得到报偿；", version_id: 6},
  {id: 160446, book: "1Cor", verse: "3.015", unformatted: "如果有人的工程被烧掉，他就将受到损失，而他自己却将得救，但是会像从火里经过的那样。", version_id: 6},
  {id: 160447, book: "1Cor", verse: "3.016", unformatted: "你们难道不知道自己就是神的圣所，而且神的灵住在你们里面吗？", version_id: 6},
  {id: 160448, book: "1Cor", verse: "3.017", unformatted: "如果有人毁坏神的圣所，神就要毁坏这个人，因为神的圣所是圣洁的，这圣所就是你们。", version_id: 6},
  {id: 160449, book: "1Cor", verse: "3.018", unformatted: "人智慧的愚拙\n谁都不可自欺！如果你们中间有人自以为在这个世代中是有智慧的，他就应该变为愚拙，好成为有智慧的，", version_id: 6},
  {id: 160450, book: "1Cor", verse: "3.019", unformatted: "因为在神看来，这世界的智慧就是愚拙；原来经上记着：“神使智慧人落入自己的诡计中”；", version_id: 6},
  {id: 160451, book: "1Cor", verse: "3.02", unformatted: "又说：“主知道智慧人的思想是虚妄的。”", version_id: 6},
  {id: 160452, book: "1Cor", verse: "3.021", unformatted: "所以谁都不要以人夸耀；要知道，一切都属于你们：", version_id: 6},
  {id: 160453, book: "1Cor", verse: "3.022", unformatted: "无论是保罗、是阿波罗、是矶法、是世界、是生、是死、是现在的事、是将来的事，全都属于你们；", version_id: 6},
  {id: 160454, book: "1Cor", verse: "3.023", unformatted: "而你们属于基督，基督属于神。", version_id: 6},
  {id: 160455, book: "1Cor", verse: "4.001", unformatted: "忠心的管家\n这样，人应该把我们看做是基督的仆人、神奥秘之事的管家。", version_id: 6},
  {id: 160456, book: "1Cor", verse: "4.002", unformatted: "在这里，进一步要求管家们的是，各人都要被看做是忠心的。", version_id: 6},
  {id: 160457, book: "1Cor", verse: "4.003", unformatted: "至于我被你们评断，或被人审判，对我都是极小的事。其实连我自己也不评断自己。", version_id: 6},
  {id: 160458, book: "1Cor", verse: "4.004", unformatted: "我诚然问心无愧，但也并不因此被称为义，原来评断我的是主。", version_id: 6},
  {id: 160459, book: "1Cor", verse: "4.005", unformatted: "所以时候没有到，在主来临之前，不要评断任何事；主将照亮黑暗中隐秘的事，显明人心里的计划。那时候，称赞将从神那里临到各人。", version_id: 6},
  {id: 160460, book: "1Cor", verse: "4.006", unformatted: "使徒谦卑的榜样\n弟兄们，我把这些事应用到我和阿波罗身上，是为了你们的缘故，好让你们从我们学到“不要超越经上所记的”，免得你们自我膨胀，抬高这个人，贬低那个人。", version_id: 6},
  {id: 160461, book: "1Cor", verse: "4.007", unformatted: "使你与别人不同的到底是谁呢？你有什么不是领受的呢？如果真是领受的，你为什么还自夸，好像不是领受的呢？", version_id: 6},
  {id: 160462, book: "1Cor", verse: "4.008", unformatted: "你们已经饱足了，已经富有了，没有我们，自己就做王了！我真希望你们真做王，好让我们也与你们一同做王！", version_id: 6},
  {id: 160463, book: "1Cor", verse: "4.009", unformatted: "其实我在想，神把我们做使徒的列在最后，好像被定死罪的人；原来我们成了给宇宙、天使、世人的一台戏。", version_id: 6},
  {id: 160464, book: "1Cor", verse: "4.01", unformatted: "我们为基督的缘故是愚拙的，你们在基督里倒是聪明的；我们软弱，你们倒强壮；你们有荣耀，我们倒不受尊重。", version_id: 6},
  {id: 160465, book: "1Cor", verse: "4.011", unformatted: "直到现在这时刻，我们还是又饥又渴，衣不蔽体，被人殴打，居无定所，", version_id: 6},
  {id: 160466, book: "1Cor", verse: "4.012", unformatted: "并且亲手劳苦做工。被人辱骂，我们就祝福；被人逼迫，我们就容忍；", version_id: 6},
  {id: 160467, book: "1Cor", verse: "4.013", unformatted: "受到毁谤，我们就劝导。我们被当做世界的垃圾、万人的渣滓，直到如今。", version_id: 6},
  {id: 160468, book: "1Cor", verse: "4.014", unformatted: "慈父之心\n我写这些，不是要你们感到羞愧，而是把你们当做我亲爱的儿女来劝诫。", version_id: 6},
  {id: 160469, book: "1Cor", verse: "4.015", unformatted: "其实就算你们在基督里有上万的导师，却没有很多的父亲，因为是我在基督耶稣里藉着福音生了你们。", version_id: 6},
  {id: 160470, book: "1Cor", verse: "4.016", unformatted: "所以我劝你们：当效法我。", version_id: 6},
  {id: 160471, book: "1Cor", verse: "4.017", unformatted: "为了这缘故，我派提摩太到你们那里去。他是我在主里又亲爱又忠心的儿子，他会使你们想起我在基督耶稣里的行事为人，正如我在各处、各教会中所教导的。", version_id: 6},
  {id: 160472, book: "1Cor", verse: "4.018", unformatted: "有些人以为我不会到你们那里去，他们就自我膨胀。", version_id: 6},
  {id: 160473, book: "1Cor", verse: "4.019", unformatted: "但如果主愿意，我不久就会到你们那里去；那时候，我所要知道的不是那些自我膨胀之人的话语，而是他们的权能，", version_id: 6},
  {id: 160474, book: "1Cor", verse: "4.02", unformatted: "因为神的国不在于话语，而在于权能。", version_id: 6},
  {id: 160475, book: "1Cor", verse: "4.021", unformatted: "你们要什么呢？要我带着权杖，还是要我带着爱和温柔的心灵到你们那里去呢？", version_id: 6},
  {id: 160476, book: "1Cor", verse: "5.001", unformatted: "淫乱的事件\n到处都有消息说你们中间有淫乱，并且这样的淫乱连外邦人中也没有——竟然有人与他的继母同居。", version_id: 6},
  {id: 160477, book: "1Cor", verse: "5.002", unformatted: "你们却还自我膨胀！难道你们不更应该悲伤，让做这事的人从你们里面被除去吗？", version_id: 6},
  {id: 160478, book: "1Cor", verse: "5.003", unformatted: "要知道，我虽然身体不在你们那里，但灵里却在你们那里；我已经像在你们那里一样审判了做这事的人。", version_id: 6},
  {id: 160479, book: "1Cor", verse: "5.004", unformatted: "当你们聚集，与我的灵和我们主耶稣的权能同在的时候，你们要奉我们主耶稣的名，", version_id: 6},
  {id: 160480, book: "1Cor", verse: "5.005", unformatted: "把这样的人交给撒旦，使他的肉体毁灭，好让他的灵魂在主的日子可以得救。", version_id: 6},
  {id: 160481, book: "1Cor", verse: "5.006", unformatted: "你们夸耀是不好的。难道你们不知道，一点点酵母能使整个面团发酵吗？", version_id: 6},
  {id: 160482, book: "1Cor", verse: "5.007", unformatted: "你们应当把那旧的酵母除净，好使你们成为新的面团，就像你们是无酵的那样；因为实际上，我们逾越节的羔羊——基督已经被献上了。", version_id: 6},
  {id: 160483, book: "1Cor", verse: "5.008", unformatted: "所以让我们来守这节，不可用旧的酵母，也不可用恶毒、恶意的酵母，而要用真诚、真实的无酵饼。", version_id: 6},
  {id: 160484, book: "1Cor", verse: "5.009", unformatted: "教会的管教\n我以前在书信上给你们写过：不可与淫乱的人交往。", version_id: 6},
  {id: 160485, book: "1Cor", verse: "5.01", unformatted: "我当然不是指这世上淫乱的、或贪心的、或勒索人的、或拜偶像的，否则，你们就必须离开这个世界了。", version_id: 6},
  {id: 160486, book: "1Cor", verse: "5.011", unformatted: "但如今我写给你们的是：如果有人被称为弟兄，却是淫乱的、或贪心的、或拜偶像的、或诽谤人的、或酗酒的、或勒索人的，你们不可与他交往，连与这样的人一起吃饭都不可。", version_id: 6},
  {id: 160487, book: "1Cor", verse: "5.012", unformatted: "其实审判外面的人，到底与我有什么关系呢？你们难道不该审判里面的人吗？", version_id: 6},
  {id: 160488, book: "1Cor", verse: "5.013", unformatted: "至于外面的人，神会审判他们；而“你们应当把那邪恶的人从你们里面除去。”", version_id: 6},
  {id: 160489, book: "1Cor", verse: "6.001", unformatted: "信徒间的诉讼\n你们中间，当一个与另一个有了问题，怎么敢告到不义的人面前，而不告到圣徒面前呢？", version_id: 6},
  {id: 160490, book: "1Cor", verse: "6.002", unformatted: "难道你们不知道，圣徒将要审判这世界吗？如果连世界都要被你们审判，难道你们还不配审理这些最小的事吗？", version_id: 6},
  {id: 160491, book: "1Cor", verse: "6.003", unformatted: "难道你们不知道，我们将要审判天使吗？更何况今生的事呢！", version_id: 6},
  {id: 160492, book: "1Cor", verse: "6.004", unformatted: "因此，你们如果真有今生的事要审理，难道让被教会所藐视的这些人来审吗？", version_id: 6},
  {id: 160493, book: "1Cor", verse: "6.005", unformatted: "我说这话，是为了使你们羞愧。难道你们中间没有一个智慧人，能在他的弟兄之间分辨是非吗？", version_id: 6},
  {id: 160494, book: "1Cor", verse: "6.006", unformatted: "你们竟然弟兄控告弟兄，而且告到了不信的人面前！", version_id: 6},
  {id: 160495, book: "1Cor", verse: "6.007", unformatted: "所以，你们彼此告状，对你们已经是一个全然的失败。你们为什么不情愿受委屈呢？为什么不情愿受亏负呢？", version_id: 6},
  {id: 160496, book: "1Cor", verse: "6.008", unformatted: "然而你们却委屈人、亏负人，况且这是对你们的弟兄！", version_id: 6},
  {id: 160497, book: "1Cor", verse: "6.009", unformatted: "难道你们不知道，不义的人不会继承神的国吗？不要被迷惑了！无论是淫乱的、拜偶像的、通奸的、做娈童的、同性恋的、", version_id: 6},
  {id: 160498, book: "1Cor", verse: "6.01", unformatted: "偷窃的、贪心的、酗酒的、诽谤人的、勒索人的，都不会继承神的国。", version_id: 6},
  {id: 160499, book: "1Cor", verse: "6.011", unformatted: "你们有些人过去是这样，但是藉着主耶稣基督的名，藉着我们神的灵，你们已经被洗净了，被分别为圣了，被称为义了。", version_id: 6},
  {id: 160500, book: "1Cor", verse: "6.012", unformatted: "要以身体荣耀神\n“什么事我都可以做”，但不都有益处；“什么事我都可以做”，但我却不受任何事的辖制。", version_id: 6},
  {id: 160501, book: "1Cor", verse: "6.013", unformatted: "“食物是为了肚子，肚子是为了食物”，但神将要把这两样都废除。身体不是为了淫乱，而是为了主；主也是为了身体。", version_id: 6},
  {id: 160502, book: "1Cor", verse: "6.014", unformatted: "神不但使主复活了，也将藉着自己的大能使我们复活。", version_id: 6},
  {id: 160503, book: "1Cor", verse: "6.015", unformatted: "难道你们不知道，你们的身体就是基督的肢体吗？因此，我可以拿基督的肢体当做妓女的肢体吗？绝对不可！", version_id: 6},
  {id: 160504, book: "1Cor", verse: "6.016", unformatted: "难道你们不知道，那与妓女结合的，就是与她成为一体了吗？因为经上说：“两个人就成为一体。”", version_id: 6},
  {id: 160505, book: "1Cor", verse: "6.017", unformatted: "然而，那与主联合的，就是与主成为一灵。", version_id: 6},
  {id: 160506, book: "1Cor", verse: "6.018", unformatted: "你们要逃避淫乱。“人所犯的各样罪都在身体以外”，只有那行淫乱的是对自己的身体犯罪。", version_id: 6},
  {id: 160507, book: "1Cor", verse: "6.019", unformatted: "难道你们不知道，你们的身体就是圣灵的圣所吗？这圣灵住在你们里面，是你们从神领受的。你们不属于自己，", version_id: 6},
  {id: 160508, book: "1Cor", verse: "6.02", unformatted: "因为你们是被重价赎回来的。所以，你们应当用自己的身体荣耀神。", version_id: 6},
  {id: 160509, book: "1Cor", verse: "7.001", unformatted: "婚姻的原则\n关于你们所写的事，你们说“男人不碰女人是好事”；", version_id: 6},
  {id: 160510, book: "1Cor", verse: "7.002", unformatted: "不过为了避免淫乱的事，每个男人都应当有自己的妻子，每个女人也应当有自己的丈夫。", version_id: 6},
  {id: 160511, book: "1Cor", verse: "7.003", unformatted: "丈夫对妻子应当尽自己的本分，妻子对丈夫也应当如此。", version_id: 6},
  {id: 160512, book: "1Cor", verse: "7.004", unformatted: "妻子对自己的身体没有主权，这主权在丈夫；同样，丈夫对自己的身体也没有主权，这主权在妻子。", version_id: 6},
  {id: 160513, book: "1Cor", verse: "7.005", unformatted: "夫妻同房不可拒绝对方，除非为了有一段时间专心祷告；然后仍要在一起，免得撒旦趁着你们不能自制而试探你们。", version_id: 6},
  {id: 160514, book: "1Cor", verse: "7.006", unformatted: "不过我说这话是出于容忍，而不是出于命令。", version_id: 6},
  {id: 160515, book: "1Cor", verse: "7.007", unformatted: "我希望所有的人也都像我一样，但是每个人自己都有从神而来的恩赐：有人是这样，有人是那样。", version_id: 6},
  {id: 160516, book: "1Cor", verse: "7.008", unformatted: "对未婚者的忠告\n我对没有结婚的人和寡妇说，如果他们能像我一样保持现状，对他们来说是好事。", version_id: 6},
  {id: 160517, book: "1Cor", verse: "7.009", unformatted: "但如果他们不能自制，就应该结婚，因为结婚总比心如火烧更好。", version_id: 6},
  {id: 160518, book: "1Cor", verse: "7.01", unformatted: "对已婚者的忠告\n至于对那些已经结婚的人，我吩咐——其实不是我，而是主吩咐：妻子不可离开丈夫；", version_id: 6},
  {id: 160519, book: "1Cor", verse: "7.011", unformatted: "但如果还是离开了，就应当守身不嫁，或者与丈夫和好。丈夫也不可离弃妻子。", version_id: 6},
  {id: 160520, book: "1Cor", verse: "7.012", unformatted: "对其余的人，我说——不是主说：如果某个弟兄有不信的妻子，而妻子情愿与他一起生活，他就不可离弃妻子；", version_id: 6},
  {id: 160521, book: "1Cor", verse: "7.013", unformatted: "同样，如果妻子有不信的丈夫，而丈夫情愿与她一起生活，她也不可离弃丈夫，", version_id: 6},
  {id: 160522, book: "1Cor", verse: "7.014", unformatted: "因为那不信的丈夫因着妻子就得以圣洁了；而那不信的妻子也因着丈夫得以圣洁了；否则，你们的儿女就是不洁净的；但如今他们是圣洁的。", version_id: 6},
  {id: 160523, book: "1Cor", verse: "7.015", unformatted: "可是如果那不信的一方要离开，就让他离开吧。在这样的事上，那弟兄或姐妹就不受束缚了。不过神召唤了我们，是要我们和睦。", version_id: 6},
  {id: 160524, book: "1Cor", verse: "7.016", unformatted: "做妻子的啊，你到底怎么知道将来是否救你的丈夫呢？做丈夫的啊，你怎么知道将来是否救你的妻子呢？", version_id: 6},
  {id: 160525, book: "1Cor", verse: "7.017", unformatted: "人生的不同处境\n只是当照着主所分给每个人的，以及神所召唤每个人的去行事。我在各教会中也是这样吩咐的。", version_id: 6},
  {id: 160526, book: "1Cor", verse: "7.018", unformatted: "如果一个人蒙召的时候已经受了割礼，就不应该取消割礼的记号；如果一个人蒙召的时候没有受割礼，就不应该受割礼。", version_id: 6},
  {id: 160527, book: "1Cor", verse: "7.019", unformatted: "受割礼不重要，不受割礼也不重要，重要的是遵守神的命令。", version_id: 6},
  {id: 160528, book: "1Cor", verse: "7.02", unformatted: "每个人在蒙了召唤的时候怎样，他就应当保持怎样。", version_id: 6},
  {id: 160529, book: "1Cor", verse: "7.021", unformatted: "你蒙召的时候是做奴仆的吗？你不要介意。但如果你真的能成为自由人，还是要把握机会。", version_id: 6},
  {id: 160530, book: "1Cor", verse: "7.022", unformatted: "要知道，做奴仆时在主里蒙召的，就是主的自由人；同样，做自由人时蒙召的，就是基督的奴仆。", version_id: 6},
  {id: 160531, book: "1Cor", verse: "7.023", unformatted: "你们是被重价赎回来的，不要成为人的奴仆。", version_id: 6},
  {id: 160532, book: "1Cor", verse: "7.024", unformatted: "弟兄们，你们每个人蒙召的时候怎样，在神面前就应当保持怎样。", version_id: 6},
  {id: 160533, book: "1Cor", verse: "7.025", unformatted: "未婚和寡居\n关于童贞的女子，我没有从主而来的命令，但是我既然蒙主的怜悯成为忠心的人，我就提供意见。", version_id: 6},
  {id: 160534, book: "1Cor", verse: "7.026", unformatted: "实际上，由于现今的艰难，我认为一个人保持现状是合宜的，这才是好的。", version_id: 6},
  {id: 160535, book: "1Cor", verse: "7.027", unformatted: "你已经有妻子约束着，就不要寻求摆脱；你没有妻子束缚着，就不要寻求妻子。", version_id: 6},
  {id: 160536, book: "1Cor", verse: "7.028", unformatted: "不过你如果还是要结婚，并不是犯罪；童贞女子如果结婚，也不是犯罪。不过这样的人在今生将要受患难，而我顾惜你们。", version_id: 6},
  {id: 160537, book: "1Cor", verse: "7.029", unformatted: "弟兄们，我要说这一点：时候不多了！从此有妻子的，要像没有妻子的；", version_id: 6},
  {id: 160538, book: "1Cor", verse: "7.03", unformatted: "哭泣的，要像没有哭泣的；快乐的，要像没有快乐的；买了东西的，要像没有什么的；", version_id: 6},
  {id: 160539, book: "1Cor", verse: "7.031", unformatted: "利用世界的，要像没有充分利用世界的；因为这世界的形态正在消逝。", version_id: 6},
  {id: 160540, book: "1Cor", verse: "7.032", unformatted: "我希望你们一无挂虑。没有结婚的男人所挂虑的是主的事，是要怎样讨主的喜悦；", version_id: 6},
  {id: 160541, book: "1Cor", verse: "7.033", unformatted: "而结了婚的男人所挂虑的是世界的事，是要怎样讨妻子的喜悦，", version_id: 6},
  {id: 160542, book: "1Cor", verse: "7.034", unformatted: "这样他就分心了。未婚女子和童贞女子挂虑主的事，以致身体和心灵都得以圣洁；而结了婚的妇女挂虑世界的事，是要怎样讨丈夫的喜悦。", version_id: 6},
  {id: 160543, book: "1Cor", verse: "7.035", unformatted: "我说这话是为了你们自己的益处，不是要给你们套上牢笼，而是要你们行事合宜，毫无分心地殷勤服事主。", version_id: 6},
  {id: 160544, book: "1Cor", verse: "7.036", unformatted: "但如果有人认为他对待自己的童贞女儿不合宜，而且女儿也过了花期，事情又该如此行，就当按着自己所愿的去做——他并不是犯罪，应当让两个人结婚。", version_id: 6},
  {id: 160545, book: "1Cor", verse: "7.037", unformatted: "但如果一个人自己心里坚定不移，也不出于勉强，又对自己的意愿有主权，并且自己心里已经决定留下自己的童贞女儿，那么他就做得好。", version_id: 6},
  {id: 160546, book: "1Cor", verse: "7.038", unformatted: "所以，那让自己的童贞女儿结婚的，做得好；那没有让她结婚的，做得更好。", version_id: 6},
  {id: 160547, book: "1Cor", verse: "7.039", unformatted: "丈夫活着的时候，妻子是受约束的；丈夫如果死了，妻子就可以自由地嫁给她所愿意的人，只是要嫁给主里的人。", version_id: 6},
  {id: 160548, book: "1Cor", verse: "7.04", unformatted: "但是照我的意见，如果她能保持现状，就更蒙福了。我想我也有神的灵。", version_id: 6},
  {id: 160549, book: "1Cor", verse: "8.001", unformatted: "祭过偶像的食物\n关于祭过偶像的食物，我们知道“我们都有知识。”知识使人自我膨胀，但是爱能造就人。", version_id: 6},
  {id: 160550, book: "1Cor", verse: "8.002", unformatted: "如果有人自以为知道了什么，照着应该知道的，他还是不知道。", version_id: 6},
  {id: 160551, book: "1Cor", verse: "8.003", unformatted: "但如果有人爱神，这个人就是神所知道的。", version_id: 6},
  {id: 160552, book: "1Cor", verse: "8.004", unformatted: "关于吃祭过偶像的食物，我们知道“世界上的偶像是虚无的”，也知道“除了神一位之外，没有别的神。”", version_id: 6},
  {id: 160553, book: "1Cor", verse: "8.005", unformatted: "因为，就算真有所谓的神明——无论在天上或在地上，好像有许多的“神”和许多的“主”——", version_id: 6},
  {id: 160554, book: "1Cor", verse: "8.006", unformatted: "但对我们来说，\n只有一位神，就是父，\n万有都出于他，我们也归于他；\n并且只有一位主，就是耶稣基督，\n万有都藉着他而有，我们也藉着他而有。", version_id: 6},
  {id: 160555, book: "1Cor", verse: "8.007", unformatted: "然而，不是所有的人都有这知识。有些人因习惯了偶像，至今还以为所吃的是祭过偶像的食物。他们的良心因为软弱，就被玷污了。", version_id: 6},
  {id: 160556, book: "1Cor", verse: "8.008", unformatted: "其实食物不能使我们接近神；我们如果不吃也不会缺少什么，吃了也不会增加什么。", version_id: 6},
  {id: 160557, book: "1Cor", verse: "8.009", unformatted: "不过要当心，免得你们的这权利成了软弱之人的绊跌物。", version_id: 6},
  {id: 160558, book: "1Cor", verse: "8.01", unformatted: "因为，如果有人看见你这有知识的人在偶像的庙里坐席，难道不就鼓励了他软弱的良心，使他也去吃那祭过偶像的食物吗？", version_id: 6},
  {id: 160559, book: "1Cor", verse: "8.011", unformatted: "这样，那软弱的人，就是基督为他而死的那弟兄，就会因你的知识被毁了。", version_id: 6},
  {id: 160560, book: "1Cor", verse: "8.012", unformatted: "当你们这样对弟兄犯罪，打击他们软弱良心的时候，就是对基督犯罪。", version_id: 6},
  {id: 160561, book: "1Cor", verse: "8.013", unformatted: "所以，如果食物使我的弟兄绊倒，我就绝不吃肉直到永远，免得使我的弟兄绊倒。", version_id: 6},
  {id: 160562, book: "1Cor", verse: "9.001", unformatted: "使徒的榜样\n我不是自由的吗？我不是使徒吗？我不是看到我们的主耶稣吗？你们不是我在主里的工作吗？", version_id: 6},
  {id: 160563, book: "1Cor", verse: "9.002", unformatted: "即使我对别人不是使徒，但至少对你们还是使徒，因为你们是我在主里做使徒的印证。", version_id: 6},
  {id: 160564, book: "1Cor", verse: "9.003", unformatted: "对那些评断我的人，我的辩护是这样的：", version_id: 6},
  {id: 160565, book: "1Cor", verse: "9.004", unformatted: "难道我们没有权利吃喝吗？", version_id: 6},
  {id: 160566, book: "1Cor", verse: "9.005", unformatted: "难道我们没有权利像其他的使徒，像主的弟弟们，像矶法那样，也带着信主的妻子一同往来吗？", version_id: 6},
  {id: 160567, book: "1Cor", verse: "9.006", unformatted: "难道只有我和巴拿巴没有不做工的权利吗？", version_id: 6},
  {id: 160568, book: "1Cor", verse: "9.007", unformatted: "曾经有谁当兵自备粮饷呢？有谁栽种葡萄园不吃园中的果子呢？有谁牧养羊群不喝羊群的奶呢？", version_id: 6},
  {id: 160569, book: "1Cor", verse: "9.008", unformatted: "我说这些话，难道是按人的意思吗？律法不也是说这些话吗？", version_id: 6},
  {id: 160570, book: "1Cor", verse: "9.009", unformatted: "事实上，摩西的律法上记着：“牛踹谷的时候，不可笼住它的嘴。”难道神顾念的是牛吗？", version_id: 6},
  {id: 160571, book: "1Cor", verse: "9.01", unformatted: "还是特别为我们说的呢？这样写下来，当然是为我们的缘故！因为耕田的应该怀着希望去耕田，打场的应该怀着一同分享粮食的希望去打场。", version_id: 6},
  {id: 160572, book: "1Cor", verse: "9.011", unformatted: "既然我们为你们播下了属灵的种子，那么，如果我们从你们收取物质上的东西，这算过份吗？", version_id: 6},
  {id: 160573, book: "1Cor", verse: "9.012", unformatted: "如果别人能从你们那里分享这权利，我们不是更能分享吗？\n然而，我们并没有用过这权利，反而凡事包容，免得给基督的福音带来阻碍。", version_id: 6},
  {id: 160574, book: "1Cor", verse: "9.013", unformatted: "难道你们不知道，在圣殿供职的人，都吃圣殿中的食物；侍候祭坛的人，都一同享用祭坛上的供物吗？", version_id: 6},
  {id: 160575, book: "1Cor", verse: "9.014", unformatted: "照样，主也吩咐那些传福音的人，靠福音生活。", version_id: 6},
  {id: 160576, book: "1Cor", verse: "9.015", unformatted: "但是，这些权利我一个也没有用过。我写这些并不是要人这样对待我，因为我宁愿死，也不愿意任何人使我所夸耀的落了空。", version_id: 6},
  {id: 160577, book: "1Cor", verse: "9.016", unformatted: "原来，就算我传福音，也没有可夸耀的，因为传福音的需要催逼着我；我如果不传福音，我就有祸了；", version_id: 6},
  {id: 160578, book: "1Cor", verse: "9.017", unformatted: "如果我自愿做这事，就有报偿；如果我不自愿，这任务也已经委托给我了。", version_id: 6},
  {id: 160579, book: "1Cor", verse: "9.018", unformatted: "那么，我的报偿到底是什么呢？就是：我传福音的时候，能免费地提供福音，好使我不会充分利用在福音工作上属自己的权利。", version_id: 6},
  {id: 160580, book: "1Cor", verse: "9.019", unformatted: "我诚然是自由的，不属于任何人，但我使自己成为所有人的奴仆，为了赢得更多的人。", version_id: 6},
  {id: 160581, book: "1Cor", verse: "9.02", unformatted: "对犹太人，我就成为一个像犹太人那样的人，为了赢得犹太人；对律法之下的人，虽然我自己不在律法之下，我却成为一个像是在律法之下的人，为了赢得在律法之下的人；", version_id: 6},
  {id: 160582, book: "1Cor", verse: "9.021", unformatted: "对没有律法的人，我就成为一个像是没有律法的人那样，为了赢得没有律法的人——对神，我不是没有律法的，而是在基督的律法之下。", version_id: 6},
  {id: 160583, book: "1Cor", verse: "9.022", unformatted: "对软弱的人，我就成为软弱的人，为了赢得软弱的人。对什么样的人，我就成为什么样的人，一定要拯救一些人。", version_id: 6},
  {id: 160584, book: "1Cor", verse: "9.023", unformatted: "并且我做这一切，都是为了福音的缘故，好使我能成为在福音上一同有份的人。", version_id: 6},
  {id: 160585, book: "1Cor", verse: "9.024", unformatted: "难道你们不知道，赛场上奔跑的人虽然都在跑，但是得奖的只有一个吗？你们也应当这样奔跑，好使你们得着奖赏。", version_id: 6},
  {id: 160586, book: "1Cor", verse: "9.025", unformatted: "每一个参加比赛的人，都要在一切事上自制；不过他们这样做是为了得到会朽坏的冠冕，而我们是为了得到不会朽坏的冠冕。", version_id: 6},
  {id: 160587, book: "1Cor", verse: "9.026", unformatted: "因此，我奔跑不像没有目标的；我斗拳不像打空气的；", version_id: 6},
  {id: 160588, book: "1Cor", verse: "9.027", unformatted: "然而我不断克制自己的身体，使它服从我，免得我传道给别人，自己反而经不起考验了。", version_id: 6},
  {id: 160589, book: "1Cor", verse: "10.001", unformatted: "历史的鉴戒\n弟兄们，我不愿意你们不明白：我们的祖先曾经都在那云彩之下，都从那海中经过，", version_id: 6},
  {id: 160590, book: "1Cor", verse: "10.002", unformatted: "并且都在那云里和海里受洗归于摩西。", version_id: 6},
  {id: 160591, book: "1Cor", verse: "10.003", unformatted: "他们都吃了同样的属灵食物，", version_id: 6},
  {id: 160592, book: "1Cor", verse: "10.004", unformatted: "也都喝了同样的属灵之水。原来他们所喝的是出于那随着他们的属灵磐石；那磐石就是基督。", version_id: 6},
  {id: 160593, book: "1Cor", verse: "10.005", unformatted: "但是神不喜悦他们中的大多数人，所以他们倒毙在旷野。", version_id: 6},
  {id: 160594, book: "1Cor", verse: "10.006", unformatted: "这些事的发生，是为了我们的鉴戒，好使我们不像他们成为贪恋恶事的人。", version_id: 6},
  {id: 160595, book: "1Cor", verse: "10.007", unformatted: "你们也不可像他们中的有些人那样，成为拜偶像的，正如经上所记：“民众坐下吃喝，起来玩乐。”", version_id: 6},
  {id: 160596, book: "1Cor", verse: "10.008", unformatted: "我们也不可行淫乱，像他们中的有些人行淫乱，结果一天就倒毙了两万三千人。", version_id: 6},
  {id: 160597, book: "1Cor", verse: "10.009", unformatted: "我们也不可试探基督，像他们中的有些人试探了，结果就被蛇所灭。", version_id: 6},
  {id: 160598, book: "1Cor", verse: "10.01", unformatted: "你们也不可抱怨，像他们中的有些人抱怨了，结果就被那毁灭者所灭。", version_id: 6},
  {id: 160599, book: "1Cor", verse: "10.011", unformatted: "这些事发生在他们身上，做为鉴戒；并且被记载下来，是为了警戒我们这些面临万世结局的人。", version_id: 6},
  {id: 160600, book: "1Cor", verse: "10.012", unformatted: "所以，那自以为站得住的，应该当心，免得跌倒。", version_id: 6},
  {id: 160601, book: "1Cor", verse: "10.013", unformatted: "你们所遭受的试探，无非是人所遭受的。神是信实的，他不会让你们受试探过于你们所能受的；而且在你们受试探的时候，他会给你们开一条出路，使你们能忍受得住。", version_id: 6},
  {id: 160602, book: "1Cor", verse: "10.014", unformatted: "远离拜偶像的事\n所以，我亲爱的各位，你们要逃避拜偶像的事。", version_id: 6},
  {id: 160603, book: "1Cor", verse: "10.015", unformatted: "我就像对聪明人在说话；我所说的，你们应当自己判断。", version_id: 6},
  {id: 160604, book: "1Cor", verse: "10.016", unformatted: "我们所祝谢的福杯，难道不是在基督之血里的一种契合吗？我们所掰的饼，难道不是在基督身体里的一种契合吗？", version_id: 6},
  {id: 160605, book: "1Cor", verse: "10.017", unformatted: "这是因为饼只有一个，而我们这许多人是一个身体——就是说，我们全都共享这一个饼。", version_id: 6},
  {id: 160606, book: "1Cor", verse: "10.018", unformatted: "你们看世上的以色列人吧，难道那些吃祭物的人不是与祭坛有份的人吗？", version_id: 6},
  {id: 160607, book: "1Cor", verse: "10.019", unformatted: "那么，我怎么说呢？难道说祭过偶像的食物算得了什么吗？或说，偶像算得了什么吗？", version_id: 6},
  {id: 160608, book: "1Cor", verse: "10.02", unformatted: "不，我是说，外邦人所献的祭是献给鬼魔的，不是献给神的；而我不愿意你们成为与鬼魔有份的人。", version_id: 6},
  {id: 160609, book: "1Cor", verse: "10.021", unformatted: "你们不能既喝主的杯，又喝鬼魔的杯；不能既吃主的筵席，又吃鬼魔的筵席。", version_id: 6},
  {id: 160610, book: "1Cor", verse: "10.022", unformatted: "难道我们要激起主的嫉恨吗？难道我们比他更强吗？", version_id: 6},
  {id: 160611, book: "1Cor", verse: "10.023", unformatted: "基督徒的自由\n“什么事都可以做”，但不都有益处；“什么事都可以做”，但不都造就人。", version_id: 6},
  {id: 160612, book: "1Cor", verse: "10.024", unformatted: "谁都不应当求自己的益处，而应当求别人的益处。", version_id: 6},
  {id: 160613, book: "1Cor", verse: "10.025", unformatted: "肉市上所卖的一切，你们都可以吃；不要为了良心的缘故而问什么，", version_id: 6},
  {id: 160614, book: "1Cor", verse: "10.026", unformatted: "因为“大地和其中的一切都属于主。”", version_id: 6},
  {id: 160615, book: "1Cor", verse: "10.027", unformatted: "如果有不信的人请你们吃饭，而你们也愿意去，那么，凡是摆在你们面前的都可以吃，不要为了良心的缘故而问什么。", version_id: 6},
  {id: 160616, book: "1Cor", verse: "10.028", unformatted: "但如果有人对你们说：“这是祭过偶像的食物”，那么，为了那指明这事的人和良心的缘故，你们就不要吃——", version_id: 6},
  {id: 160617, book: "1Cor", verse: "10.029", unformatted: "我说的良心不是你自己的，而是那个人的，因为我的自由何必被别人的良心评断呢？", version_id: 6},
  {id: 160618, book: "1Cor", verse: "10.03", unformatted: "如果我怀着感恩的心领受，难道有什么能使我为所感谢的东西而受到毁谤吗？", version_id: 6},
  {id: 160619, book: "1Cor", verse: "10.031", unformatted: "因此，你们无论或吃、或喝、或做什么，一切都要为神的荣耀而做。", version_id: 6},
  {id: 160620, book: "1Cor", verse: "10.032", unformatted: "无论对犹太人、对外邦人，或对神的教会，你们都不可成为绊跌物；", version_id: 6},
  {id: 160621, book: "1Cor", verse: "10.033", unformatted: "要像我那样，也在一切事上使所有的人喜悦——不求自己的益处，而求多人的益处，好让他们都能得救。", version_id: 6},
  {id: 160622, book: "1Cor", verse: "11.001", unformatted: "你们要效法我，就像我效法基督一样。", version_id: 6},
  {id: 160623, book: "1Cor", verse: "11.002", unformatted: "有关蒙头的教导\n我称赞你们，因为你们在一切事上都记念我，并且照着我所传给你们的来持守传统。", version_id: 6},
  {id: 160624, book: "1Cor", verse: "11.003", unformatted: "但是我愿你们知道：基督是每一个男人的头，男人是女人的头，而神是基督的头。", version_id: 6},
  {id: 160625, book: "1Cor", verse: "11.004", unformatted: "凡是男人在祷告或做先知传道的时候，如果蒙着头，就是羞辱自己的头；", version_id: 6},
  {id: 160626, book: "1Cor", verse: "11.005", unformatted: "凡是女人在祷告或做先知传道的时候，如果不蒙着头，就是羞辱自己的头，因为这与头发被剃掉了是同样一件事。", version_id: 6},
  {id: 160627, book: "1Cor", verse: "11.006", unformatted: "要知道，女人如果不遮盖自己，她就应该连头发也剪掉；但既然女人剪掉头发或剃掉头发是可耻的，她就应该遮盖自己。", version_id: 6},
  {id: 160628, book: "1Cor", verse: "11.007", unformatted: "男人本来不应该遮盖着头，因为他是神的形像和荣耀，而女人是男人的荣耀。", version_id: 6},
  {id: 160629, book: "1Cor", verse: "11.008", unformatted: "原来男人不是由女人而出，女人却是由男人而出；", version_id: 6},
  {id: 160630, book: "1Cor", verse: "11.009", unformatted: "并且男人不是为女人而造，女人却是为男人而造。", version_id: 6},
  {id: 160631, book: "1Cor", verse: "11.01", unformatted: "因此，为了天使的缘故，应该有权柄在女人的头之上。", version_id: 6},
  {id: 160632, book: "1Cor", verse: "11.011", unformatted: "不过在主里，没有女人就没有男人，没有男人也就没有女人。", version_id: 6},
  {id: 160633, book: "1Cor", verse: "11.012", unformatted: "因为就像女人出于男人，照样，男人也来自女人，而一切都是出于神。", version_id: 6},
  {id: 160634, book: "1Cor", verse: "11.013", unformatted: "你们自己判断吧：女人不蒙着头向神祷告是合宜的吗？", version_id: 6},
  {id: 160635, book: "1Cor", verse: "11.014", unformatted: "人的本性不也教导你们：男人如果留长发就是他的羞耻，", version_id: 6},
  {id: 160636, book: "1Cor", verse: "11.015", unformatted: "而女人如果留长发就是她的荣耀吗？因为头发是赐给女人当做盖头的。", version_id: 6},
  {id: 160637, book: "1Cor", verse: "11.016", unformatted: "但如果有人想争辩，我们却没有这样的惯例；神的各教会也没有。", version_id: 6},
  {id: 160638, book: "1Cor", verse: "11.017", unformatted: "圣餐\n现在我吩咐以下的事，并不是在称赞你们，因为你们聚集所导致的不是更好，而是更糟。", version_id: 6},
  {id: 160639, book: "1Cor", verse: "11.018", unformatted: "首先，我听说你们做为一个教会聚集的时候，在你们中间有分裂，我也部分地相信这话。", version_id: 6},
  {id: 160640, book: "1Cor", verse: "11.019", unformatted: "实际上，你们当中必定也会有结派的事，好让那些经过考验的人在你们中间能显露出来。", version_id: 6},
  {id: 160641, book: "1Cor", verse: "11.02", unformatted: "这样说来，你们聚集在一起，所吃的并不是主的晚餐，", version_id: 6},
  {id: 160642, book: "1Cor", verse: "11.021", unformatted: "因为你们吃的时候，每个人都争先吃自己的晚餐，甚至有人饿着，有人醉了。", version_id: 6},
  {id: 160643, book: "1Cor", verse: "11.022", unformatted: "难道你们没有家，可以在那里吃喝吗？还是你们轻视神的教会，羞辱那些没有什么的人呢？我要对你们怎么说呢？要称赞你们吗？在这事上，我不称赞你们。", version_id: 6},
  {id: 160644, book: "1Cor", verse: "11.023", unformatted: "我当时传授给你们的是我从主所领受的：主耶稣在被出卖的那一夜，拿起饼来，", version_id: 6},
  {id: 160645, book: "1Cor", verse: "11.024", unformatted: "祝谢了，就掰开，说：“这是我的身体，为了你们的缘故；你们应当如此行，为的是记念我。”", version_id: 6},
  {id: 160646, book: "1Cor", verse: "11.025", unformatted: "晚餐后，他也照样拿起杯来，说：“这杯是用我的血所立的新约。你们每逢喝的时候，应当如此行，为的是记念我。”", version_id: 6},
  {id: 160647, book: "1Cor", verse: "11.026", unformatted: "你们每逢吃这饼、喝这杯，就是传扬主的死，直到主来。", version_id: 6},
  {id: 160648, book: "1Cor", verse: "11.027", unformatted: "自省\n因此无论谁，不合宜地吃主的饼、喝主的杯，就是干犯主的身体、主的血。", version_id: 6},
  {id: 160649, book: "1Cor", verse: "11.028", unformatted: "这样，一个人应当省察自己，然后才吃这饼、喝这杯。", version_id: 6},
  {id: 160650, book: "1Cor", verse: "11.029", unformatted: "因为那吃喝的人，如果不分辨主的身体，就是吃喝对自己的惩罚了。", version_id: 6},
  {id: 160651, book: "1Cor", verse: "11.03", unformatted: "为此，你们中间有很多人是软弱的、患病的，甚至有许多人睡着了。", version_id: 6},
  {id: 160652, book: "1Cor", verse: "11.031", unformatted: "但如果我们省察自己，就不会受惩罚；", version_id: 6},
  {id: 160653, book: "1Cor", verse: "11.032", unformatted: "即使我们受惩罚，也是在受主的管教，免得我们和这世界一同被定罪。", version_id: 6},
  {id: 160654, book: "1Cor", verse: "11.033", unformatted: "所以我的弟兄们，你们聚集吃的时候，应当彼此等候。", version_id: 6},
  {id: 160655, book: "1Cor", verse: "11.034", unformatted: "如果有人饿了，就应当在家里吃，免得你们聚集，反导致惩罚。至于其他的事，当我来的时候才吩咐。", version_id: 6},
  {id: 160656, book: "1Cor", verse: "12.001", unformatted: "属灵的恩赐\n弟兄们，我不愿意你们不明白有关属灵的事。", version_id: 6},
  {id: 160657, book: "1Cor", verse: "12.002", unformatted: "你们知道，当你们还是外邦人的时候，是怎样被引诱、被带到那些不能说话的偶像那里的。", version_id: 6},
  {id: 160658, book: "1Cor", verse: "12.003", unformatted: "因此我要你们明白：藉着神的灵说话的，没有一个人会说“耶稣该受诅咒”；同样，如果不藉着圣灵，也没有一个人能说：“耶稣是主。”", version_id: 6},
  {id: 160659, book: "1Cor", verse: "12.004", unformatted: "恩赐虽然有所不同，但圣灵却是同一位；", version_id: 6},
  {id: 160660, book: "1Cor", verse: "12.005", unformatted: "服事工作虽然有所不同，但主却是同一位；", version_id: 6},
  {id: 160661, book: "1Cor", verse: "12.006", unformatted: "功用虽然有所不同，但在每个人里面运作万事的神却是同一位。", version_id: 6},
  {id: 160662, book: "1Cor", verse: "12.007", unformatted: "不过圣灵显明在每个人的身上，是为了共同的益处，因为：", version_id: 6},
  {id: 160663, book: "1Cor", verse: "12.008", unformatted: "有人藉着圣灵被赐予了智慧的话语，\n又有人藉着同一位圣灵被赐予了知识的话语，", version_id: 6},
  {id: 160664, book: "1Cor", verse: "12.009", unformatted: "另有人藉着同一位圣灵被赐予了信仰，\n又有人藉着这独一的圣灵被赐予了使人痊愈的恩赐，", version_id: 6},
  {id: 160665, book: "1Cor", verse: "12.01", unformatted: "又有人被赐予了大能的功用，\n又有人被赐予了做先知传道的恩赐，\n又有人被赐予了对不同灵的分辨力，\n另有人被赐予了各种殊言，\n又有人被赐予了翻译殊言的恩赐。", version_id: 6},
  {id: 160666, book: "1Cor", verse: "12.011", unformatted: "然而，这一切都是这同一位圣灵，就是独一的圣灵所运作的；他照着自己的旨意，个别地分给每个人。", version_id: 6},
  {id: 160667, book: "1Cor", verse: "12.012", unformatted: "身体是一个\n要知道，正如身体虽然是一个，却有很多部分，而身体的各部分虽然有很多，却是一个身体；基督也是如此。", version_id: 6},
  {id: 160668, book: "1Cor", verse: "12.013", unformatted: "实际上，我们受洗也都是藉着一位圣灵成为一个身体——无论是犹太人或外邦人，无论是奴隶或自由人——并且我们都是受了一位圣灵的浇灌。", version_id: 6},
  {id: 160669, book: "1Cor", verse: "12.014", unformatted: "这样，身体也不是只有一个部分，而是有很多部分。", version_id: 6},
  {id: 160670, book: "1Cor", verse: "12.015", unformatted: "如果脚说：“因为我不是手，我就不属于身体”，难道它因此就不属于身体吗？", version_id: 6},
  {id: 160671, book: "1Cor", verse: "12.016", unformatted: "如果耳朵说：“因为我不是眼睛，我就不属于身体”，难道它因此就不属于身体吗？", version_id: 6},
  {id: 160672, book: "1Cor", verse: "12.017", unformatted: "如果整个身体是个眼睛，那么听觉在哪里呢？如果整个身体是个耳朵，那么嗅觉在哪里呢？", version_id: 6},
  {id: 160673, book: "1Cor", verse: "12.018", unformatted: "所以现在神照着自己的意愿，把各部分一一安置在身体上了。", version_id: 6},
  {id: 160674, book: "1Cor", verse: "12.019", unformatted: "如果全都是一个部分，那么身体在哪里呢？", version_id: 6},
  {id: 160675, book: "1Cor", verse: "12.02", unformatted: "但如今，身体的各部分虽然很多，身体却是一个。", version_id: 6},
  {id: 160676, book: "1Cor", verse: "12.021", unformatted: "所以眼睛不能对手说：“我不需要你！”头也不能对两脚说：“我不需要你们！”", version_id: 6},
  {id: 160677, book: "1Cor", verse: "12.022", unformatted: "相反，身体上那些所谓比较软弱的部分，难道不更是必要的吗？", version_id: 6},
  {id: 160678, book: "1Cor", verse: "12.023", unformatted: "并且身体上那些我们认为不太受尊重的部分，我们加添给它们格外的尊重；这样我们不俊美的部分，就有了格外的俊美。", version_id: 6},
  {id: 160679, book: "1Cor", verse: "12.024", unformatted: "至于我们那些俊美的部分，就不需要加添什么了；而神搭配了身体，把格外的尊重赐给那有缺乏的，", version_id: 6},
  {id: 160680, book: "1Cor", verse: "12.025", unformatted: "免得身体中有分争，好让各部分彼此之间都有同样的关顾。", version_id: 6},
  {id: 160681, book: "1Cor", verse: "12.026", unformatted: "如果一个部分受苦，所有的部分就一同受苦；如果一个部分得荣耀，所有的部分就一同欢乐。", version_id: 6},
  {id: 160682, book: "1Cor", verse: "12.027", unformatted: "你们就是基督的身体，而且是各个部分中的一个。", version_id: 6},
  {id: 160683, book: "1Cor", verse: "12.028", unformatted: "在教会中，神所安排的：\n首先是使徒，第二是做先知传道的，第三是教师，然后是有大能的；\n随后是使人痊愈的恩赐、帮助的恩赐、管理的恩赐、各种殊言的恩赐。", version_id: 6},
  {id: 160684, book: "1Cor", verse: "12.029", unformatted: "难道都是使徒吗？都是做先知传道的吗？\n都是教师吗？都是有大能的吗？", version_id: 6},
  {id: 160685, book: "1Cor", verse: "12.03", unformatted: "都有使人痊愈的恩赐吗？都说殊言吗？\n都翻译殊言吗？", version_id: 6},
  {id: 160686, book: "1Cor", verse: "12.031", unformatted: "其实你们要渴慕那些更大的恩赐；而我还要把极其高超的道指示给你们。", version_id: 6},
  {id: 160687, book: "1Cor", verse: "13.001", unformatted: "爱——极其高超的道\n如果我能说人间和天使的各种殊言，却没有爱，\n我就成了嘈杂的锣、鸣响的钹；", version_id: 6},
  {id: 160688, book: "1Cor", verse: "13.002", unformatted: "如果我有做先知传道的恩赐，\n也明白一切奥秘、一切知识，\n并有全备的信，以致能移山，\n却没有爱，我就一无所是；", version_id: 6},
  {id: 160689, book: "1Cor", verse: "13.003", unformatted: "即使我把自己所拥有的一切分给穷人，\n又舍弃自己的身体被人焚烧，\n却没有爱，对我也毫无益处。", version_id: 6},
  {id: 160690, book: "1Cor", verse: "13.004", unformatted: "爱是恒久忍耐，又是仁慈；\n爱是不嫉妒；\n爱是不自夸、不自大；", version_id: 6},
  {id: 160691, book: "1Cor", verse: "13.005", unformatted: "不做不合宜的事，不求自己的益处，\n不轻易动怒，不计算人的恶，", version_id: 6},
  {id: 160692, book: "1Cor", verse: "13.006", unformatted: "不为不义欢喜，而与真理同乐；", version_id: 6},
  {id: 160693, book: "1Cor", verse: "13.007", unformatted: "凡事包容，凡事相信，\n凡事盼望，凡事忍耐。", version_id: 6},
  {id: 160694, book: "1Cor", verse: "13.008", unformatted: "爱是永不止息；\n而做先知传道的恩赐将被废除，\n殊言也将会停止，\n知识也将被废除，", version_id: 6},
  {id: 160695, book: "1Cor", verse: "13.009", unformatted: "因为我们所知道的有限，\n我们做先知所传的也有限；", version_id: 6},
  {id: 160696, book: "1Cor", verse: "13.01", unformatted: "但那完全的来到时，\n这有限的就将被废除。", version_id: 6},
  {id: 160697, book: "1Cor", verse: "13.011", unformatted: "当我做孩子的时候，说话像孩子，\n心思像孩子，想法像孩子；\n当我成人以后，就把那些属孩子的事都废除了；", version_id: 6},
  {id: 160698, book: "1Cor", verse: "13.012", unformatted: "如今我们对着镜子观看，模糊不清，\n但那时候就要面对面了；\n如今我所知道的有限，\n但那时候就要完全知道了，就像我已经被完全知道那样；", version_id: 6},
  {id: 160699, book: "1Cor", verse: "13.013", unformatted: "所以现在常存的有信、望、爱这三样；\n而其中更大的是爱。", version_id: 6},
  {id: 160700, book: "1Cor", verse: "14.001", unformatted: "先知传道——更超卓的恩赐\n你们要追求爱，也要渴慕属灵的事，但更要渴慕做先知传道。", version_id: 6},
  {id: 160701, book: "1Cor", verse: "14.002", unformatted: "原来那说殊言的，不是对人说，而是对神说，因为没有人听得懂；他是在灵里讲说各样的奥秘。", version_id: 6},
  {id: 160702, book: "1Cor", verse: "14.003", unformatted: "可是那做先知传道的是对人说，为要造就、鼓励和安慰人。", version_id: 6},
  {id: 160703, book: "1Cor", verse: "14.004", unformatted: "那说殊言的是造就自己，但那做先知传道的是造就教会。", version_id: 6},
  {id: 160704, book: "1Cor", verse: "14.005", unformatted: "我虽然希望你们都说殊言，但更希望你们都做先知传道。因为，除非说殊言的能把殊言翻译出来使教会得到造就，否则那做先知传道的就比说殊言的更大了。", version_id: 6},
  {id: 160705, book: "1Cor", verse: "14.006", unformatted: "所以弟兄们，就算我现在到你们那里去，用殊言说话，但如果不用启示、知识、预言、或教导来对你们说话，那么我对你们有什么益处呢？", version_id: 6},
  {id: 160706, book: "1Cor", verse: "14.007", unformatted: "甚至是那些没有生命却能发出声音的东西，例如笛子或竖琴，如果发出的音调没有分别，怎么能使人知道所吹所弹的是什么呢？", version_id: 6},
  {id: 160707, book: "1Cor", verse: "14.008", unformatted: "实际上，如果号角发出的声音真的不清楚，谁会准备好出战呢？", version_id: 6},
  {id: 160708, book: "1Cor", verse: "14.009", unformatted: "照样，如果你们用舌头说出不可理解的话，那么所说的怎么能使人明白呢？这样，你们就是向空气说话了。", version_id: 6},
  {id: 160709, book: "1Cor", verse: "14.01", unformatted: "世界上也许有好多种语言，但没有一种是没有意思的。", version_id: 6},
  {id: 160710, book: "1Cor", verse: "14.011", unformatted: "所以，如果我不明白那语言的意思，那么，对于说话的人，我就是个外族人；对于我，那说话的人也是个外族人。", version_id: 6},
  {id: 160711, book: "1Cor", verse: "14.012", unformatted: "你们也是这样：既然你们对属灵的事是热心人，就应当追求造就教会的事，好让你们能丰足有余。", version_id: 6},
  {id: 160712, book: "1Cor", verse: "14.013", unformatted: "所以，说殊言的人应当祷告，好让自己能翻译出来。", version_id: 6},
  {id: 160713, book: "1Cor", verse: "14.014", unformatted: "如果我用殊言祷告，是我的灵在祷告，但我的理性却是结不出果子的。", version_id: 6},
  {id: 160714, book: "1Cor", verse: "14.015", unformatted: "那么该怎么办呢？我要用灵祷告，也要用理性祷告；我要用灵歌颂，也要用理性歌颂。", version_id: 6},
  {id: 160715, book: "1Cor", verse: "14.016", unformatted: "否则，如果你用灵祝谢，在场的那些不明白的人，既然不知道你在说什么，怎么能在你感谢的时候说“阿们”呢？", version_id: 6},
  {id: 160716, book: "1Cor", verse: "14.017", unformatted: "因为你感谢的固然是好，但别人却没有得造就。", version_id: 6},
  {id: 160717, book: "1Cor", verse: "14.018", unformatted: "我感谢神，我说殊言比你们所有人都多；", version_id: 6},
  {id: 160718, book: "1Cor", verse: "14.019", unformatted: "但是在教会里，为了要教导别人，我宁愿用我的理性说五句话，也不愿用殊言说万句话。", version_id: 6},
  {id: 160719, book: "1Cor", verse: "14.02", unformatted: "弟兄们，你们在思想上不要做小孩子，但是在恶事上要做婴孩，在思想上要做成熟的人。", version_id: 6},
  {id: 160720, book: "1Cor", verse: "14.021", unformatted: "律法书上记着：“主说：\n‘我要藉着说别种语言的人，\n藉着别人的嘴唇对这子民说话。\n即使这样，他们也不会听我的。’”", version_id: 6},
  {id: 160721, book: "1Cor", verse: "14.022", unformatted: "这样，说殊言不是给信的人做为标记，而是给不信的人；但做先知传道不是给不信的人做为标记，而是给信的人。", version_id: 6},
  {id: 160722, book: "1Cor", verse: "14.023", unformatted: "所以，如果全教会聚集在一起，大家都说殊言，有不明白殊言的，或不信的人进来，他们难道不会说你们都疯了吗？", version_id: 6},
  {id: 160723, book: "1Cor", verse: "14.024", unformatted: "但如果大家都做先知传道，有一个不信的或不明白的人进来，他就会因这一切而知罪、受到责问，", version_id: 6},
  {id: 160724, book: "1Cor", verse: "14.025", unformatted: "他心中隐秘的事就会显露出来。这样，他就会把脸伏在地上敬拜神，承认说：“神真的是在你们中间！”", version_id: 6},
  {id: 160725, book: "1Cor", verse: "14.026", unformatted: "聚会的秩序\n弟兄们，那么该怎么办呢？你们聚集的时候，各人或有诗歌，或有教导，或有启示，或有殊言，或有翻译出来的话语——一切都要为了造就人。", version_id: 6},
  {id: 160726, book: "1Cor", verse: "14.027", unformatted: "如果有人说殊言，就应该只有两个人，或最多三个人，而且要轮流说，也要有一个人翻译。", version_id: 6},
  {id: 160727, book: "1Cor", verse: "14.028", unformatted: "如果没有翻译的人，他就应当在教会里保持沉默，只对自己和神说话。", version_id: 6},
  {id: 160728, book: "1Cor", verse: "14.029", unformatted: "做先知传道的也应该两个人或三个人讲，其他的人应当分辨。", version_id: 6},
  {id: 160729, book: "1Cor", verse: "14.03", unformatted: "如果在坐的人中另有人得了启示，那先讲的就应当保持沉默。", version_id: 6},
  {id: 160730, book: "1Cor", verse: "14.031", unformatted: "因为你们都可以一个一个地做先知传道，好让大家都能学，都得到鼓励。", version_id: 6},
  {id: 160731, book: "1Cor", verse: "14.032", unformatted: "先知的灵是服从先知的，", version_id: 6},
  {id: 160732, book: "1Cor", verse: "14.033", unformatted: "因为神不是混乱的神，而是和平的神。\n正如在圣徒的各教会那样，", version_id: 6},
  {id: 160733, book: "1Cor", verse: "14.034", unformatted: "妇女在教会应当保持沉默，因为她们没有获准讲话；就像律法上所说的那样，她们要服从。", version_id: 6},
  {id: 160734, book: "1Cor", verse: "14.035", unformatted: "如果她们想请教什么，就应该在家里问自己的丈夫，因为妇女在教会里讲话是可耻的。", version_id: 6},
  {id: 160735, book: "1Cor", verse: "14.036", unformatted: "难道神的话语是从你们出来的吗？难道只是临到你们吗？", version_id: 6},
  {id: 160736, book: "1Cor", verse: "14.037", unformatted: "如果有人自以为是做先知传道的，或是属灵的，就应该承认：我写给你们的是主的命令；", version_id: 6},
  {id: 160737, book: "1Cor", verse: "14.038", unformatted: "但如果有人不承认，他也不会被承认。", version_id: 6},
  {id: 160738, book: "1Cor", verse: "14.039", unformatted: "所以，我的弟兄们，你们要渴慕做先知传道，也不要禁止说殊言。", version_id: 6},
  {id: 160739, book: "1Cor", verse: "14.04", unformatted: "然而，一切事都要行得端正，并且要按着秩序行。", version_id: 6},
  {id: 160740, book: "1Cor", verse: "15.001", unformatted: "复活是福音的核心\n弟兄们，我要你们明白我先前所传给你们的福音；你们也领受了这福音，并且在这福音上站立得住。", version_id: 6},
  {id: 160741, book: "1Cor", verse: "15.002", unformatted: "如果你们不是毫无理由地相信，而是持守我所传给你们的话语，你们也就会藉着它得救。", version_id: 6},
  {id: 160742, book: "1Cor", verse: "15.003", unformatted: "因为我所传给你们的，其中最重要的，也是我所领受的，就是\n基督照着经上所记的，为我们的罪孽死了，", version_id: 6},
  {id: 160743, book: "1Cor", verse: "15.004", unformatted: "而且被埋葬了，\n又照着经上所记的，在第三天复活了，", version_id: 6},
  {id: 160744, book: "1Cor", verse: "15.005", unformatted: "并且向矶法显现，然后向十二使徒显现，", version_id: 6},
  {id: 160745, book: "1Cor", verse: "15.006", unformatted: "后来一次性地向五百多个弟兄显现，\n其中大多数人至今还在，虽然也有些已经睡了；", version_id: 6},
  {id: 160746, book: "1Cor", verse: "15.007", unformatted: "后来他向雅各显现，再向所有的使徒显现，", version_id: 6},
  {id: 160747, book: "1Cor", verse: "15.008", unformatted: "最后也显现给我——这个像是早产婴儿一样的人。", version_id: 6},
  {id: 160748, book: "1Cor", verse: "15.009", unformatted: "原来我是使徒中最小的，不配被称为使徒，因为我曾经逼迫神的教会。", version_id: 6},
  {id: 160749, book: "1Cor", verse: "15.01", unformatted: "但藉着神的恩典，我成了现在的我，并且神赐给我的恩典没有落空。不但如此，我比任何使徒更加劳苦做工；其实不是我，而是与我同在的神的恩典。", version_id: 6},
  {id: 160750, book: "1Cor", verse: "15.011", unformatted: "因此无论是我，或是他们，我们都如此传，你们也就如此信了。", version_id: 6},
  {id: 160751, book: "1Cor", verse: "15.012", unformatted: "复活是信仰的核心\n既然宣讲基督已经从死人中复活了，你们中间怎么还有些人说没有死人复活的事呢？", version_id: 6},
  {id: 160752, book: "1Cor", verse: "15.013", unformatted: "如果没有死人复活的事，基督也就没有复活了。", version_id: 6},
  {id: 160753, book: "1Cor", verse: "15.014", unformatted: "如果基督没有复活，我们传道就是虚空，你们相信也是虚空。", version_id: 6},
  {id: 160754, book: "1Cor", verse: "15.015", unformatted: "这样，我们就被看做是神的假见证人，因为我们见证了有关神的事，说神使基督复活了，如果死人真不会复活，神就没有使基督复活；", version_id: 6},
  {id: 160755, book: "1Cor", verse: "15.016", unformatted: "就是说，如果死人不会复活，基督也就没有复活了。", version_id: 6},
  {id: 160756, book: "1Cor", verse: "15.017", unformatted: "如果基督没有复活，你们的信仰就是虚妄的，你们也仍然在自己的罪孽中；", version_id: 6},
  {id: 160757, book: "1Cor", verse: "15.018", unformatted: "因此在基督里那些睡了的人也就灭亡了。", version_id: 6},
  {id: 160758, book: "1Cor", verse: "15.019", unformatted: "如果我们在基督里的盼望只在于今生，我们就比所有的人更可怜。", version_id: 6},
  {id: 160759, book: "1Cor", verse: "15.02", unformatted: "复活带给我们保障\n但如今，基督已经从死人中复活，成为睡了之人初熟的果子，", version_id: 6},
  {id: 160760, book: "1Cor", verse: "15.021", unformatted: "因为死亡既然是由一个人而来，死人的复活也是由一个人而来。", version_id: 6},
  {id: 160761, book: "1Cor", verse: "15.022", unformatted: "原来，就像在亚当里所有的人都死了，照样，在基督里所有的人也都将得以复活，", version_id: 6},
  {id: 160762, book: "1Cor", verse: "15.023", unformatted: "不过每个人要按着自己的次序：初熟的果子是基督；然后他来临的时候，是那些属于基督的人；", version_id: 6},
  {id: 160763, book: "1Cor", verse: "15.024", unformatted: "再后才是结局。那时候，基督要把国度交给父神；同时，基督要废除一切统治的、掌权的、有势力的，", version_id: 6},
  {id: 160764, book: "1Cor", verse: "15.025", unformatted: "因为基督必须做王，直到神把所有的敌人都放在他的脚下。", version_id: 6},
  {id: 160765, book: "1Cor", verse: "15.026", unformatted: "最后被废除的仇敌就是死亡，", version_id: 6},
  {id: 160766, book: "1Cor", verse: "15.027", unformatted: "因为“神使万有都服从在基督的脚下。”当经上说“万有”都已经服从了他的时候，明显地，使万有服从基督的那一位是不包括在内的。", version_id: 6},
  {id: 160767, book: "1Cor", verse: "15.028", unformatted: "当万有都服从他的时候，子自己也要服从那位使万有都服从他的，这样神就是万有中的万有。", version_id: 6},
  {id: 160768, book: "1Cor", verse: "15.029", unformatted: "复活的力量\n如果不是这样，那些替死人受洗的，将怎么办呢？如果死人根本不会复活，人们为什么还替死人受洗呢？", version_id: 6},
  {id: 160769, book: "1Cor", verse: "15.03", unformatted: "我们又为什么要时时刻刻面临危险呢？", version_id: 6},
  {id: 160770, book: "1Cor", verse: "15.031", unformatted: "弟兄们，藉着我在我们的主基督耶稣里所拥有的，就是我对你们的夸耀，我肯定地说：我天天都在死！", version_id: 6},
  {id: 160771, book: "1Cor", verse: "15.032", unformatted: "我在以弗所与野兽搏斗，如果按人的意思来说，对我有什么好处呢？如果死人不会复活，就“让我们吃吃喝喝吧，因为明天我们就要死了。”", version_id: 6},
  {id: 160772, book: "1Cor", verse: "15.033", unformatted: "你们不要被迷惑了！“结交恶友，败坏善德。”", version_id: 6},
  {id: 160773, book: "1Cor", verse: "15.034", unformatted: "你们应当醒悟过来，不继续犯罪；实际上，有些人不认识神。我说这话，是为了使你们羞愧。", version_id: 6},
  {id: 160774, book: "1Cor", verse: "15.035", unformatted: "复活的身体\n但是有人会说：“死人怎样复活呢？他们以什么样的身体出现呢？”", version_id: 6},
  {id: 160775, book: "1Cor", verse: "15.036", unformatted: "你这愚妄的人哪！你所种的如果不死，就得不到生命；", version_id: 6},
  {id: 160776, book: "1Cor", verse: "15.037", unformatted: "你所种的也不是那将要长成的形体；你所种的不过是一粒赤裸的种子，也许是麦子，或是其他什么。", version_id: 6},
  {id: 160777, book: "1Cor", verse: "15.038", unformatted: "但神照着自己的意愿，给那种子一个形体，并且使每一粒种子各有自己的形体。", version_id: 6},
  {id: 160778, book: "1Cor", verse: "15.039", unformatted: "并不是所有的身体都是同样的：人是一个样子，牲畜的身体是另一个样子，鸟的身体又是一个样子，鱼又是另一个样子。", version_id: 6},
  {id: 160779, book: "1Cor", verse: "15.04", unformatted: "还有天上的形体，也有地上的形体；但天上形体的光辉是一个样子，地上形体的光辉是另一个样子。", version_id: 6},
  {id: 160780, book: "1Cor", verse: "15.041", unformatted: "太阳的光辉是一个样子，月亮的光辉是另一个样子，星辰的光辉又是一个样子；这星与那星的光辉当然有区别。", version_id: 6},
  {id: 160781, book: "1Cor", verse: "15.042", unformatted: "死人的复活也是这样：\n在衰朽中被种下，在不朽中复活；", version_id: 6},
  {id: 160782, book: "1Cor", verse: "15.043", unformatted: "在卑贱中被种下，在荣耀中复活；\n在软弱中被种下，在强壮中复活；", version_id: 6},
  {id: 160783, book: "1Cor", verse: "15.044", unformatted: "被种下的是属血气的身体，复活的是属灵的身体。\n既然有属血气的身体，也就有属灵的身体。", version_id: 6},
  {id: 160784, book: "1Cor", verse: "15.045", unformatted: "经上也这样记着：“第一个人亚当成了有生命的人，最后的亚当成了赐生命的灵。”", version_id: 6},
  {id: 160785, book: "1Cor", verse: "15.046", unformatted: "不过在先的不是属灵的，而是属血气的；在后的才是属灵的。", version_id: 6},
  {id: 160786, book: "1Cor", verse: "15.047", unformatted: "第一个人是出于地，是属尘土的；\n第二个人是出于天。", version_id: 6},
  {id: 160787, book: "1Cor", verse: "15.048", unformatted: "属尘土的那个人怎样，那些属尘土的也就怎样；\n属天的那一位怎样，那些属天的也就怎样。", version_id: 6},
  {id: 160788, book: "1Cor", verse: "15.049", unformatted: "就像我们带有了属尘土的那个人的形像，\n我们也将带有属天的那一位的形像。", version_id: 6},
  {id: 160789, book: "1Cor", verse: "15.05", unformatted: "复活的得胜\n弟兄们，我要说这一点：血肉之体不能继承神的国，衰朽的也不能继承不朽的。", version_id: 6},
  {id: 160790, book: "1Cor", verse: "15.051", unformatted: "看哪，我告诉你们一个奥秘：\n我们并不是都要睡了，而是都要被改变，", version_id: 6},
  {id: 160791, book: "1Cor", verse: "15.052", unformatted: "就在一刹那，眨眼之间，在那最后的号角声中。\n的确，号角要吹响，死人要复活成为不朽坏的，我们也要被改变；", version_id: 6},
  {id: 160792, book: "1Cor", verse: "15.053", unformatted: "因为这会朽坏的，必须穿上不朽坏的；\n这会死的，必须穿上不死的。", version_id: 6},
  {id: 160793, book: "1Cor", verse: "15.054", unformatted: "一旦这会朽坏的穿上那不朽坏的，\n这会死的穿上那不死的，\n经上所记的话就会应验了：\n“死亡被胜利吞灭了！”", version_id: 6},
  {id: 160794, book: "1Cor", verse: "15.055", unformatted: "“死亡啊！你的胜利在哪里？\n死亡啊！你的毒刺在哪里？”", version_id: 6},
  {id: 160795, book: "1Cor", verse: "15.056", unformatted: "死亡的毒刺就是罪，而罪的权势就是律法。", version_id: 6},
  {id: 160796, book: "1Cor", verse: "15.057", unformatted: "但感谢神，他藉着我们的主耶稣基督，\n赐给我们胜利！", version_id: 6},
  {id: 160797, book: "1Cor", verse: "15.058", unformatted: "因此，我亲爱的弟兄们，你们要坚定，不可动摇，常常在主的工作中丰足有余，因为你们知道，你们的劳苦在主里不是虚空。", version_id: 6},
  {id: 160798, book: "1Cor", verse: "16.001", unformatted: "为圣徒捐助\n关于为圣徒收集的事，我从前怎样吩咐加拉太的各教会，你们也当照样做：", version_id: 6},
  {id: 160799, book: "1Cor", verse: "16.002", unformatted: "每周的头一天，你们每个人如果充裕，就应当从中拿出一些积蓄起来，免得我来的时候才开始收集。", version_id: 6},
  {id: 160800, book: "1Cor", verse: "16.003", unformatted: "等我来了，我会派你们所认可的那些人，带着书信，把你们的惠赠送到耶路撒冷。", version_id: 6},
  {id: 160801, book: "1Cor", verse: "16.004", unformatted: "但如果我也该去，他们就要与我一同去。", version_id: 6},
  {id: 160802, book: "1Cor", verse: "16.005", unformatted: "保罗的计划\n我经过马其顿省以后，就会到你们那里去——因为我要经过马其顿省——", version_id: 6},
  {id: 160803, book: "1Cor", verse: "16.006", unformatted: "我也许会在你们那里停留一些时候，甚至过冬。这样，无论我往哪里去，你们都可以送我上路。", version_id: 6},
  {id: 160804, book: "1Cor", verse: "16.007", unformatted: "实际上，我现在并不愿意在赶路的时候见你们；主如果准许，我希望在你们那里住一段时间。", version_id: 6},
  {id: 160805, book: "1Cor", verse: "16.008", unformatted: "只是我会留在以弗所，一直到五旬节。", version_id: 6},
  {id: 160806, book: "1Cor", verse: "16.009", unformatted: "原来，为了有功效的工作，一个大门向我敞开了，不过反对的人也很多。", version_id: 6},
  {id: 160807, book: "1Cor", verse: "16.01", unformatted: "如果提摩太来了，你们要关心，好让他在你们那里无所惧怕，因为他和我一样在做主的工作。", version_id: 6},
  {id: 160808, book: "1Cor", verse: "16.011", unformatted: "所以任何人都不要藐视他。你们要送他平平安安地上路，好让他到我这里来，因为我在等候他与弟兄们一起来。", version_id: 6},
  {id: 160809, book: "1Cor", verse: "16.012", unformatted: "至于阿波罗弟兄，我曾多次劝他要与弟兄们一起到你们那里去，只是他目前实在不愿意去；但有机会的时候，他会去的。", version_id: 6},
  {id: 160810, book: "1Cor", verse: "16.013", unformatted: "结尾的劝勉\n你们要警醒，在信仰上当站立得稳，要做大丈夫，要刚强；", version_id: 6},
  {id: 160811, book: "1Cor", verse: "16.014", unformatted: "你们所做的一切，都要在爱中。", version_id: 6},
  {id: 160812, book: "1Cor", verse: "16.015", unformatted: "弟兄们，你们知道司提法纳一家人是亚该亚初熟的果子，而且他们献上了自己去服事圣徒们。我劝你们，", version_id: 6},
  {id: 160813, book: "1Cor", verse: "16.016", unformatted: "要服从这样的人，以及所有和他们一同工作、一同劳苦的人。", version_id: 6},
  {id: 160814, book: "1Cor", verse: "16.017", unformatted: "我为司提法纳、福特纳托和阿卡伊库的到来感到欢喜，因为他们弥补了你们的空缺；", version_id: 6},
  {id: 160815, book: "1Cor", verse: "16.018", unformatted: "他们使我和你们的心灵得到安宁，因此你们要认可这样的人。", version_id: 6},
  {id: 160816, book: "1Cor", verse: "16.019", unformatted: "结语\n亚细亚省的各教会问候你们。阿奎拉和普茜拉，以及他们家里的教会，在主里热诚地问候你们。", version_id: 6},
  {id: 160817, book: "1Cor", verse: "16.02", unformatted: "所有的弟兄都问候你们。你们要用圣洁的亲吻礼彼此问候。", version_id: 6},
  {id: 160818, book: "1Cor", verse: "16.021", unformatted: "我保罗亲笔问候。", version_id: 6},
  {id: 160819, book: "1Cor", verse: "16.022", unformatted: "如果有人不爱主，他就该受诅咒。愿主快来！", version_id: 6},
  {id: 160820, book: "1Cor", verse: "16.023", unformatted: "愿主耶稣的恩典与你们同在！", version_id: 6},
  {id: 160821, book: "1Cor", verse: "16.024", unformatted: "愿我的爱在基督耶稣里与你们大家同在！", version_id: 6},
  {id: 160822, book: "2Cor", verse: "1.001", unformatted: "问安\n照着神的旨意做基督耶稣使徒的保罗，以及弟兄提摩太，\n致那在哥林多属神的教会，以及在全亚该亚省所有的圣徒：", version_id: 6},
  {id: 160823, book: "2Cor", verse: "1.002", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 160824, book: "2Cor", verse: "1.003", unformatted: "赐安慰的神\n愿颂赞归于神——我们主耶稣基督的父！他是各样怜悯的父、一切安慰的神。", version_id: 6},
  {id: 160825, book: "2Cor", verse: "1.004", unformatted: "在我们一切的患难中，他安慰我们，使我们自己能用从神所受的安慰去安慰那些在各样患难中的人，", version_id: 6},
  {id: 160826, book: "2Cor", verse: "1.005", unformatted: "因为基督的苦难怎样充充满满临到我们，藉着基督，我们的安慰也照样充充满满。", version_id: 6},
  {id: 160827, book: "2Cor", verse: "1.006", unformatted: "我们如果受患难，是为了你们的安慰和救恩；我们如果受安慰，也是为了你们的安慰；这安慰有功效，使你们能忍耐与我们所受的同样苦难。", version_id: 6},
  {id: 160828, book: "2Cor", verse: "1.007", unformatted: "而且我们对你们的盼望是确定的，因为知道你们怎样在苦难中与我们有份，也怎样在安慰中与我们有份。", version_id: 6},
  {id: 160829, book: "2Cor", verse: "1.008", unformatted: "弟兄们，我们不愿意你们不明白我们在亚细亚省所遭受的患难：我们所受的压力远远过于我们的承受能力，甚至连活下去的希望也没有了。", version_id: 6},
  {id: 160830, book: "2Cor", verse: "1.009", unformatted: "然而我们在自己里面已经承受了死亡的判决，好让我们不依靠自己，而依靠那使死人复活的神。", version_id: 6},
  {id: 160831, book: "2Cor", verse: "1.01", unformatted: "他救我们脱离了如此致命的危险，并且将来要救我们——我们盼望他将来还要救我们。", version_id: 6},
  {id: 160832, book: "2Cor", verse: "1.011", unformatted: "你们也能通过祈祷一同帮助我们，好让许多人因着那临到我们的恩惠为我们献上感谢；这恩惠是通过许多人临到我们的。", version_id: 6},
  {id: 160833, book: "2Cor", verse: "1.012", unformatted: "清洁的良心\n我们所夸耀的是这一点：我们的良心见证了我们在世上行事为人带着属神的忠实和真诚，不是靠属肉体的智慧，而是靠神的恩典；对你们更是如此。", version_id: 6},
  {id: 160834, book: "2Cor", verse: "1.013", unformatted: "其实我们写给你们的，无非是你们能读、能了解的，而且我希望你们能彻底地了解，", version_id: 6},
  {id: 160835, book: "2Cor", verse: "1.014", unformatted: "就像你们对我们已经有一些了解那样，因为在我们主耶稣的日子，我们是你们的夸耀，正如你们是我们的夸耀。", version_id: 6},
  {id: 160836, book: "2Cor", verse: "1.015", unformatted: "到访推延\n藉着这样的确信，我早就打算先到你们那里去，使你们能得到两次的恩典：", version_id: 6},
  {id: 160837, book: "2Cor", verse: "1.016", unformatted: "就是经过你们那里往马其顿省去，再从马其顿省回到你们那里，然后由你们送我上路去犹太。", version_id: 6},
  {id: 160838, book: "2Cor", verse: "1.017", unformatted: "那么，我这样打算，难道就是轻率行事吗？难道我所计划的，是顺着肉体的计划，以致在我忽是忽非吗？", version_id: 6},
  {id: 160839, book: "2Cor", verse: "1.018", unformatted: "神是信实的，我们向你们传的话语，并没有“是”而又“非”。", version_id: 6},
  {id: 160840, book: "2Cor", verse: "1.019", unformatted: "要知道，藉着我们，就是我、赛拉斯和提摩太，在你们中间所传扬的神儿子耶稣基督，并没有“是”而又“非”；相反，在他只有“是”，", version_id: 6},
  {id: 160841, book: "2Cor", verse: "1.02", unformatted: "因为神的一切应许，在基督里都成为“是”。所以，说“阿们”也是藉着基督，使荣耀藉着我们归于神。", version_id: 6},
  {id: 160842, book: "2Cor", verse: "1.021", unformatted: "原来将我们与你们在基督里一起确立，并且膏立了我们大家的那一位，就是神。", version_id: 6},
  {id: 160843, book: "2Cor", verse: "1.022", unformatted: "他又在我们身上盖了印，并且赐圣灵在我们心里做为预付凭据。", version_id: 6},
  {id: 160844, book: "2Cor", verse: "1.023", unformatted: "我求告神为我的灵魂见证：我没有再往哥林多去，是因为顾惜你们。", version_id: 6},
  {id: 160845, book: "2Cor", verse: "1.024", unformatted: "我们并不是要辖制你们的信仰，而是要做你们的同工，使你们喜乐；因为你们已经因信站立得住了。", version_id: 6},
  {id: 160846, book: "2Cor", verse: "2.001", unformatted: "所以，我自己这样决定：不再带着忧伤到你们那里去。", version_id: 6},
  {id: 160847, book: "2Cor", verse: "2.002", unformatted: "因为如果我使你们忧伤，那么，除了我使他忧伤的那个人，谁能使我欢喜呢？", version_id: 6},
  {id: 160848, book: "2Cor", verse: "2.003", unformatted: "我写了那封信，免得我来的时候，那些本应该使我喜乐的人使我忧伤，因为我深信你们大家以我的喜乐为你们大家的喜乐。", version_id: 6},
  {id: 160849, book: "2Cor", verse: "2.004", unformatted: "我在极大的患难和内心的痛苦中，流了许多泪水写给你们，并不是要使你们忧伤，而是要你们知道我对你们怀有更多的爱。", version_id: 6},
  {id: 160850, book: "2Cor", verse: "2.005", unformatted: "饶恕犯罪的人\n如果有人使人忧伤，他不是使我忧伤，而是在某种程度上使你们大家忧伤——我这样说，免得说得太严重。", version_id: 6},
  {id: 160851, book: "2Cor", verse: "2.006", unformatted: "这样的人，受了大多数人的那惩罚已经够了。", version_id: 6},
  {id: 160852, book: "2Cor", verse: "2.007", unformatted: "所以，你们反而更要饶恕他、安慰他，免得他被过度的忧伤吞没了。", version_id: 6},
  {id: 160853, book: "2Cor", verse: "2.008", unformatted: "因此，我恳求你们向他证实你们的爱。", version_id: 6},
  {id: 160854, book: "2Cor", verse: "2.009", unformatted: "其实我也为此写了信，就是要知道你们经过考验的品德：你们是否在一切事上都顺从。", version_id: 6},
  {id: 160855, book: "2Cor", verse: "2.01", unformatted: "你们在任何事上饶恕了谁，我也饶恕谁；实际上，我如果饶恕了什么，我所饶恕的，就是在基督面前为了你们的缘故而饶恕的，", version_id: 6},
  {id: 160856, book: "2Cor", verse: "2.011", unformatted: "免得被撒旦占了上风，因为我们不是不明白它的意图。", version_id: 6},
  {id: 160857, book: "2Cor", verse: "2.012", unformatted: "马其顿之行\n我为了基督的福音来到特罗阿斯的时候，虽然主为我开了门，", version_id: 6},
  {id: 160858, book: "2Cor", verse: "2.013", unformatted: "我还是灵里不安，因为我没有见到我的弟兄提多。因此我向那里的人告别，往马其顿省去了。", version_id: 6},
  {id: 160859, book: "2Cor", verse: "2.014", unformatted: "生命与死亡的香气\n但是感谢神！他总是率领我们在基督里夸胜，并且藉着我们，在各地传扬因认识基督而有的香气。", version_id: 6},
  {id: 160860, book: "2Cor", verse: "2.015", unformatted: "因为在神面前，无论在得救的人中间，还是在灭亡的人中间，我们都是基督的馨香：", version_id: 6},
  {id: 160861, book: "2Cor", verse: "2.016", unformatted: "对灭亡的人，我们是死亡的香气以至于死亡；对得救的人，我们是生命的香气以至于生命。对这些事，到底谁配得上呢？", version_id: 6},
  {id: 160862, book: "2Cor", verse: "2.017", unformatted: "实际上，我们不像许多人那样，用神的话语来做生意；相反，我们是出于真诚、出于神，在神面前、在基督里讲话。", version_id: 6},
  {id: 160863, book: "2Cor", verse: "3.001", unformatted: "活的推荐信\n我们又开始推荐自己吗？难道我们像有些人那样，需要给你们推荐信，或向你们要推荐信吗？", version_id: 6},
  {id: 160864, book: "2Cor", verse: "3.002", unformatted: "你们就是我们的推荐信，写在我们的心里，被人人所知道、所诵读；", version_id: 6},
  {id: 160865, book: "2Cor", verse: "3.003", unformatted: "因为很明显，你们是基督的书信，由我们所经手；不是用墨写的，而是用永生神的灵写的；不是刻在石版上，而是刻在人的心版上。", version_id: 6},
  {id: 160866, book: "2Cor", verse: "3.004", unformatted: "因基督而有的能力\n我们藉着基督，对神有这样的确信，", version_id: 6},
  {id: 160867, book: "2Cor", verse: "3.005", unformatted: "并不是说，我们凭自己配得上把什么算做出于自己；而是说，我们之所以配得上是出于神。", version_id: 6},
  {id: 160868, book: "2Cor", verse: "3.006", unformatted: "他也使我们能够做新约的仆人——不属于律法条文，而属于圣灵；因为律法条文带来死亡，而圣灵却赐予生命。", version_id: 6},
  {id: 160869, book: "2Cor", verse: "3.007", unformatted: "新约的仆人\n如果，那以条文刻在石版上、属死亡的服事工作尚且带有荣光——以致以色列子民因为摩西脸上那渐渐消失的荣光，不能注视他的脸——", version_id: 6},
  {id: 160870, book: "2Cor", verse: "3.008", unformatted: "何况那属圣灵的服事工作，难道不更带有荣光吗？", version_id: 6},
  {id: 160871, book: "2Cor", verse: "3.009", unformatted: "因为那带来定罪的服事工作尚且有荣光，何况那带来称义的服事工作，难道不更充满了荣光吗？", version_id: 6},
  {id: 160872, book: "2Cor", verse: "3.01", unformatted: "事实上，在这种情况下，那先前有荣光的，因这无限的荣光，就成了没有荣光的。", version_id: 6},
  {id: 160873, book: "2Cor", verse: "3.011", unformatted: "这样，那渐渐消失的尚且带有荣光，何况这长存的，难道不更带有荣光吗？", version_id: 6},
  {id: 160874, book: "2Cor", verse: "3.012", unformatted: "因此，我们既然怀有这样的盼望，就大有胆量地行事。", version_id: 6},
  {id: 160875, book: "2Cor", verse: "3.013", unformatted: "我们不像摩西那样，他把帕子蒙在脸上，使以色列子民不能注视那渐渐消失的荣光的结局。", version_id: 6},
  {id: 160876, book: "2Cor", verse: "3.014", unformatted: "可是，他们的心思刚硬；事实上，直到今天诵读旧约的时候，同样的帕子仍然存在，没有被揭去，因为那帕子只有在基督里才被除去。", version_id: 6},
  {id: 160877, book: "2Cor", verse: "3.015", unformatted: "的确，直到今天，摩西书无论什么时候被诵读，那帕子仍然盖在他们的心上。", version_id: 6},
  {id: 160878, book: "2Cor", verse: "3.016", unformatted: "可是一个人什么时候归向主，那帕子就被除掉了。", version_id: 6},
  {id: 160879, book: "2Cor", verse: "3.017", unformatted: "主就是圣灵，主的灵在哪里，哪里就有自由；", version_id: 6},
  {id: 160880, book: "2Cor", verse: "3.018", unformatted: "而且脸上的帕子既然被揭去了，我们大家就像镜子返照出主的荣光，正在被改变成与主同样的形像，从荣耀归入荣耀，这正是出于主——圣灵。", version_id: 6},
  {id: 160881, book: "2Cor", verse: "4.001", unformatted: "福音的光\n所以，我们既然得了这服事的工作，正如我们蒙了怜悯，我们就不丧胆；", version_id: 6},
  {id: 160882, book: "2Cor", verse: "4.002", unformatted: "相反，把那些可耻的隐秘之事弃绝了；不以诡诈行事，也不歪曲神的话语；反而藉着显明真理，在神面前把自己推荐给每个人的良心。", version_id: 6},
  {id: 160883, book: "2Cor", verse: "4.003", unformatted: "但如果我们的福音真的被遮盖了，也是在那些正灭亡的人中被遮盖的。", version_id: 6},
  {id: 160884, book: "2Cor", verse: "4.004", unformatted: "对于他们，这世代的神把这些不信之人的心眼弄瞎了，使他们不能洞察基督荣耀的福音之光。基督就是神的形像。", version_id: 6},
  {id: 160885, book: "2Cor", verse: "4.005", unformatted: "实际上，我们不是传扬自己，而是传扬主耶稣基督，并且为了耶稣的缘故，我们自己做了你们的奴仆；", version_id: 6},
  {id: 160886, book: "2Cor", verse: "4.006", unformatted: "因为那吩咐“要有光从黑暗里照耀出来”的神，已经照耀了我们的心，为要发出神荣耀的知识之光——这光就在耶稣基督的脸上。", version_id: 6},
  {id: 160887, book: "2Cor", verse: "4.007", unformatted: "在瓦器中的宝贝\n不过我们有这宝物在瓦器里，为要显明这极大的能力是出于神，不是出于我们。", version_id: 6},
  {id: 160888, book: "2Cor", verse: "4.008", unformatted: "我们处处受患难，却不被压碎；心里困惑，却没有绝望；", version_id: 6},
  {id: 160889, book: "2Cor", verse: "4.009", unformatted: "受逼迫，却不被撇弃；被打倒，却没有灭亡。", version_id: 6},
  {id: 160890, book: "2Cor", verse: "4.01", unformatted: "我们身上总是带着耶稣的死，好让耶稣的生命也在我们身上显明出来。", version_id: 6},
  {id: 160891, book: "2Cor", verse: "4.011", unformatted: "的确，我们这些活着的人，为了耶稣的缘故，总是被置于死地，好让耶稣的生命也显明在我们这会死的肉体上。", version_id: 6},
  {id: 160892, book: "2Cor", verse: "4.012", unformatted: "这样，死亡在我们里面做工，而生命却在你们里面做工。", version_id: 6},
  {id: 160893, book: "2Cor", verse: "4.013", unformatted: "照着经上所记的：“我信，故我说”，我们既然有同一个信仰的灵，我们也就信，所以也就说，", version_id: 6},
  {id: 160894, book: "2Cor", verse: "4.014", unformatted: "因为我们知道：使主耶稣复活的那一位，也要使我们与耶稣一同复活，并且使我们和你们一起站在他面前。", version_id: 6},
  {id: 160895, book: "2Cor", verse: "4.015", unformatted: "的确，这一切都是为了你们的缘故，好让恩典藉着更多的人而增多，使感谢充实满溢，归于神的荣耀。", version_id: 6},
  {id: 160896, book: "2Cor", verse: "4.016", unformatted: "因此我们不丧胆。即使我们外在的人渐渐衰朽，我们内在的人还是日日被更新。", version_id: 6},
  {id: 160897, book: "2Cor", verse: "4.017", unformatted: "原来我们这暂时、轻微的患难，正为我们带来极重无比、永恒的荣耀。", version_id: 6},
  {id: 160898, book: "2Cor", verse: "4.018", unformatted: "我们所注重的不是看得见的，而是看不见的；因为看得见的是暂时的，看不见的却是永恒的。", version_id: 6},
  {id: 160899, book: "2Cor", verse: "5.001", unformatted: "死后的盼望\n我们知道，当我们属地的家——这帐篷被拆毁的时候，我们就有从神而来的殿宇，不是人手所造的，而是在天上的永恒的家。", version_id: 6},
  {id: 160900, book: "2Cor", verse: "5.002", unformatted: "实际上，我们就是在这帐篷里呻吟叹息，切切地想穿上我们那从天上而来的居所。", version_id: 6},
  {id: 160901, book: "2Cor", verse: "5.003", unformatted: "如果真的穿上，我们将来被看见时，就不是赤身的。", version_id: 6},
  {id: 160902, book: "2Cor", verse: "5.004", unformatted: "原来我们在这帐篷里的人，承受着压力，甚至呻吟叹息；不是因为想要脱下，而是想要穿上，好让这会死的被生命所吞没。", version_id: 6},
  {id: 160903, book: "2Cor", verse: "5.005", unformatted: "为此而预备我们的就是神；他赐给我们圣灵做为预付凭据。", version_id: 6},
  {id: 160904, book: "2Cor", verse: "5.006", unformatted: "因此我们总是满怀勇气，尽管知道我们还住在这身体里的时候，是与主相离的；", version_id: 6},
  {id: 160905, book: "2Cor", verse: "5.007", unformatted: "原来我们行事是藉着信，不是藉着眼见。", version_id: 6},
  {id: 160906, book: "2Cor", verse: "5.008", unformatted: "是的，我们满怀勇气，更乐意离开这身体，与主同住。", version_id: 6},
  {id: 160907, book: "2Cor", verse: "5.009", unformatted: "所以我们立定心志，无论是住在身体里，还是离开身体，都要得主的喜悦，", version_id: 6},
  {id: 160908, book: "2Cor", verse: "5.01", unformatted: "因为我们大家都必须站在基督的审判台前，好使每个人都为自己藉着身体所做的，或善或恶，领受回报。", version_id: 6},
  {id: 160909, book: "2Cor", verse: "5.011", unformatted: "既然如此，我们因为知道敬畏主，就劝导人。我们在神面前是显明的，但我希望在你们的良心里，也是显明的。", version_id: 6},
  {id: 160910, book: "2Cor", verse: "5.012", unformatted: "我们不是再次向你们推荐自己，而是给你们一个夸耀我们的机会，好让你们可以应对那些只凭外貌、不凭内心夸耀的人，", version_id: 6},
  {id: 160911, book: "2Cor", verse: "5.013", unformatted: "因为我们如果癫狂，是为了神；如果清醒，是为了你们。", version_id: 6},
  {id: 160912, book: "2Cor", verse: "5.014", unformatted: "实际上，基督的爱催逼着我们，因为我们如此判定：既然一个人替所有的人死了，所有的人就都死了；", version_id: 6},
  {id: 160913, book: "2Cor", verse: "5.015", unformatted: "基督替所有的人死了，好让那些活着的人不再为自己活，却为替他们死而复活的基督而活。", version_id: 6},
  {id: 160914, book: "2Cor", verse: "5.016", unformatted: "劝人与神和好的服事\n所以，从今以后，我们不按人的标准看任何人了。虽然我们曾经按人的标准认识基督，但现在我们不再这样认识他了。", version_id: 6},
  {id: 160915, book: "2Cor", verse: "5.017", unformatted: "因此，如果有人在基督里，他就是新造的人；旧的已经过去，看哪，新的已经来临。", version_id: 6},
  {id: 160916, book: "2Cor", verse: "5.018", unformatted: "这一切都是出于神，他藉着基督使我们与他自己和好，并且把这劝人与他和好的服事工作赐给了我们。", version_id: 6},
  {id: 160917, book: "2Cor", verse: "5.019", unformatted: "这就是说，在基督里，神使世人与自己和好，不把他们的过犯算在他们的身上，又把这和好的福音托付了我们。", version_id: 6},
  {id: 160918, book: "2Cor", verse: "5.02", unformatted: "因此我们是基督的特使，就像神藉着我们在劝告人——我们代替基督请求说：“与神和好吧！”", version_id: 6},
  {id: 160919, book: "2Cor", verse: "5.021", unformatted: "神使那位没有罪的替我们成为罪，好使我们在他里面成为神的义。", version_id: 6},
  {id: 160920, book: "2Cor", verse: "6.001", unformatted: "我们做为神的同工，也恳求你们：不可白受神的恩典。", version_id: 6},
  {id: 160921, book: "2Cor", verse: "6.002", unformatted: "因为他说：\n“在悦纳的时候，我垂听了你；\n在拯救的日子，我帮助了你。”\n看哪！现在就是悦纳的时候；看哪！现在就是拯救的日子。", version_id: 6},
  {id: 160922, book: "2Cor", verse: "6.003", unformatted: "服事的特性\n我们不在任何事上给任何人跌倒的借口，免得这服事的工作被人挑剔；", version_id: 6},
  {id: 160923, book: "2Cor", verse: "6.004", unformatted: "反而在一切事上都证明自己是神的仆人：\n在极大的忍耐中，在各种患难、艰难、困苦中，", version_id: 6},
  {id: 160924, book: "2Cor", verse: "6.005", unformatted: "在鞭打、监禁、混乱中，\n在劳苦、失眠、饥饿中，", version_id: 6},
  {id: 160925, book: "2Cor", verse: "6.006", unformatted: "在纯洁、知识、耐心、仁慈中，\n藉着圣灵，在不虚假的爱中，", version_id: 6},
  {id: 160926, book: "2Cor", verse: "6.007", unformatted: "在真理的话语中，在神的大能中，\n藉着右手左手的公义兵器，", version_id: 6},
  {id: 160927, book: "2Cor", verse: "6.008", unformatted: "藉着荣耀和羞辱，藉着坏名声和好名声；\n像是迷惑人的，却是真诚的；", version_id: 6},
  {id: 160928, book: "2Cor", verse: "6.009", unformatted: "像是人所不知的，却是人所共知的；\n像是快要死的，可是看哪，我们是活着的；\n像是受惩罚的，却是不被治死；", version_id: 6},
  {id: 160929, book: "2Cor", verse: "6.01", unformatted: "像是忧伤的，却总是快乐；\n像是贫穷的，却使许多人富足；\n像是一无所有，却是样样都有。", version_id: 6},
  {id: 160930, book: "2Cor", verse: "6.011", unformatted: "哥林多人哪，我们对你们，口是坦率的，心是敞开的。", version_id: 6},
  {id: 160931, book: "2Cor", verse: "6.012", unformatted: "你们并不是被我们压制了，而是被自己的情感压制了。", version_id: 6},
  {id: 160932, book: "2Cor", verse: "6.013", unformatted: "所以你们要同样回报我们——我如同对儿女说——你们也当敞开！", version_id: 6},
  {id: 160933, book: "2Cor", verse: "6.014", unformatted: "分别为圣\n你们不可与不信的人同负一轭。公义和罪恶到底有什么相通呢？光明与黑暗有什么相合呢？", version_id: 6},
  {id: 160934, book: "2Cor", verse: "6.015", unformatted: "基督与彼列有什么相融呢？信的与不信的有什么关系呢？", version_id: 6},
  {id: 160935, book: "2Cor", verse: "6.016", unformatted: "神的圣所与偶像有什么相同呢？要知道，我们是永生神的圣所，正如神所说：\n“我将要在他们中间居住，在他们中间往来；\n我将要做他们的神，他们将要做我的子民。”", version_id: 6},
  {id: 160936, book: "2Cor", verse: "6.017", unformatted: "“所以主说：\n你们要从他们中间出来，\n要被分别出来；\n不要碰不洁净的东西，\n我就接纳你们”，", version_id: 6},
  {id: 160937, book: "2Cor", verse: "6.018", unformatted: "“我将要做你们的父亲，\n你们将要做我的儿女。\n这是主、全能者说的。”", version_id: 6},
  {id: 160938, book: "2Cor", verse: "7.001", unformatted: "所以各位蒙爱的人哪，我们既然拥有这些应许，就让我们洁净自己，除去肉体和灵魂的一切污秽，怀着敬畏神的心来达成圣洁。", version_id: 6},
  {id: 160939, book: "2Cor", verse: "7.002", unformatted: "悔改与喜乐\n请你们接受我们。我们没有亏负过谁，没有败坏过谁，也没有对不起谁。", version_id: 6},
  {id: 160940, book: "2Cor", verse: "7.003", unformatted: "我这样说，不是为定你们的罪，因为我先前说过：你们常在我们心中，我们甚至可以与你们同生共死。", version_id: 6},
  {id: 160941, book: "2Cor", verse: "7.004", unformatted: "我对你们大有确信，为你们大大夸耀；我充满了安慰，在我们一切的患难中，喜乐格外增多。", version_id: 6},
  {id: 160942, book: "2Cor", verse: "7.005", unformatted: "实际上，我们以前到马其顿省的时候，不但身体得不到安宁，也处处受患难，外有争战，内有惧怕。", version_id: 6},
  {id: 160943, book: "2Cor", verse: "7.006", unformatted: "然而那安慰卑微之人的神，藉着提多的到来安慰了我们；", version_id: 6},
  {id: 160944, book: "2Cor", verse: "7.007", unformatted: "不仅藉着他的到来，也藉着他从你们所受的安慰，安慰了我们，因他向我们报告了你们的渴望、你们的伤痛、你们对我的热心，以致使我更加欢喜。", version_id: 6},
  {id: 160945, book: "2Cor", verse: "7.008", unformatted: "即使我在那封信中使你们忧伤，我也不后悔了；因为就算我后悔过——我知道那封信使你们忧伤，虽然只是暂时的——", version_id: 6},
  {id: 160946, book: "2Cor", verse: "7.009", unformatted: "现在我还是感到欢喜。这并不是因为你们忧伤，而是因为你们忧伤以至于悔改。实际上，你们按神的意思忧伤，使你们在任何事上都不会因我们而受到损失；", version_id: 6},
  {id: 160947, book: "2Cor", verse: "7.01", unformatted: "因为按神的意思忧伤，就生出悔改通向救恩，是不懊悔的；可是属世界的忧伤，却带来死亡。", version_id: 6},
  {id: 160948, book: "2Cor", verse: "7.011", unformatted: "看哪，原来正是这件事——你们按神的意思忧伤——在你们里面带来了何等的殷勤、分辩、义愤、敬畏、渴望、热心和惩罚。你们在各方面都证明了自己在那件事上是纯洁的。", version_id: 6},
  {id: 160949, book: "2Cor", verse: "7.012", unformatted: "因此我虽然给你们写了信，但不是为了那亏负人的，也不是为了那受亏负的，而是为了在神面前把你们对我们的殷勤，向你们显明出来。", version_id: 6},
  {id: 160950, book: "2Cor", verse: "7.013", unformatted: "为此，我们受到了安慰。\n在这安慰之外，我们又因着提多的喜乐更是格外喜乐，因为他的心灵被你们大家所安抚。", version_id: 6},
  {id: 160951, book: "2Cor", verse: "7.014", unformatted: "原来，即使我向提多夸耀过你们什么，我也不至于蒙羞；其实就像我们对你们所讲的一切都是真实的，照样我们向提多所夸耀你们的也是真实的；", version_id: 6},
  {id: 160952, book: "2Cor", verse: "7.015", unformatted: "而且当提多想起你们大家的顺从，想起你们怎样怀着敬畏和战兢的心接受他，他对你们的情感就越发增多了。", version_id: 6},
  {id: 160953, book: "2Cor", verse: "7.016", unformatted: "我如今欢喜的是，在一切事上我都因你们满怀勇气。", version_id: 6},
  {id: 160954, book: "2Cor", verse: "8.001", unformatted: "鼓励信徒捐助\n弟兄们，我们要你们明白神在马其顿省各教会中所赐的恩典：", version_id: 6},
  {id: 160955, book: "2Cor", verse: "8.002", unformatted: "在患难的极大考验中，他们的喜乐之满和贫穷之深都流露出来，归入他们慷慨之心的财富。", version_id: 6},
  {id: 160956, book: "2Cor", verse: "8.003", unformatted: "因为我可以见证他们是按着自己的能力，甚至超过了自己的能力，甘心乐意，", version_id: 6},
  {id: 160957, book: "2Cor", verse: "8.004", unformatted: "用许多乞求的话，恳请我们在对圣徒的服事上给与他们恩准和分享的机会；", version_id: 6},
  {id: 160958, book: "2Cor", verse: "8.005", unformatted: "他们不但照我们所盼望的，而且照着神的旨意，首先把自己献给了主，又献给了我们。", version_id: 6},
  {id: 160959, book: "2Cor", verse: "8.006", unformatted: "我们于是劝提多，他怎样开始了这恩惠之事，就当照样在你们那里把它完成；", version_id: 6},
  {id: 160960, book: "2Cor", verse: "8.007", unformatted: "而且你们怎样在信仰、言语、知识、殷勤和你们对我们的爱心上都丰足有余，你们也当怎样在这恩惠之事上丰足有余。", version_id: 6},
  {id: 160961, book: "2Cor", verse: "8.008", unformatted: "我说这话不是出于命令，而是要藉着别人的殷勤来考验你们爱的真诚，", version_id: 6},
  {id: 160962, book: "2Cor", verse: "8.009", unformatted: "因为你们知道我们主耶稣基督的恩典：他虽然富足，却为你们成了贫穷，好使你们藉着他的贫穷可以成为富足。", version_id: 6},
  {id: 160963, book: "2Cor", verse: "8.01", unformatted: "我在这件事上提供意见，因为这对你们是有益处的。从去年开始，你们不仅做了这件事，而且愿意去做，", version_id: 6},
  {id: 160964, book: "2Cor", verse: "8.011", unformatted: "所以现在你们就当完成这事，好使你们怎样有愿做的乐意之心，就怎样以你们所拥有的完成它；", version_id: 6},
  {id: 160965, book: "2Cor", verse: "8.012", unformatted: "因为人如果有乐意之心，就会照着他所拥有的蒙悦纳，并不是照着他所没有的蒙悦纳。", version_id: 6},
  {id: 160966, book: "2Cor", verse: "8.013", unformatted: "其实我这样说不是为了让别人轻松、你们为难，而是为了公平——", version_id: 6},
  {id: 160967, book: "2Cor", verse: "8.014", unformatted: "在现今的时刻，你们的富余可以补他们的缺乏，好使将来他们的富余也可以补你们的缺乏，这样就公平了。", version_id: 6},
  {id: 160968, book: "2Cor", verse: "8.015", unformatted: "正如经上所记：\n“多收的没有增多，\n少收的也没有缺少。”", version_id: 6},
  {id: 160969, book: "2Cor", verse: "8.016", unformatted: "捐助的工作\n感谢神，他为了你们，把同样的殷勤放在提多的心里。", version_id: 6},
  {id: 160970, book: "2Cor", verse: "8.017", unformatted: "提多一方面接受了我们的鼓励，另一方面自己更是殷勤，自愿到你们那里去。", version_id: 6},
  {id: 160971, book: "2Cor", verse: "8.018", unformatted: "我们也打发了一位弟兄与他一起去。这位弟兄在福音工作上受到各教会的称赞；", version_id: 6},
  {id: 160972, book: "2Cor", verse: "8.019", unformatted: "不仅如此，他也被各教会选出，与我们一起带着惠赠同行。我们这服事为的是主自己的荣耀，也为了表明我们的乐意之心。", version_id: 6},
  {id: 160973, book: "2Cor", verse: "8.02", unformatted: "我们这样谨慎，免得有人在我们所经手的这丰厚之事上挑剔我们，", version_id: 6},
  {id: 160974, book: "2Cor", verse: "8.021", unformatted: "因为我们不仅要敬重主看为美善的事，也要敬重人看为美善的事。", version_id: 6},
  {id: 160975, book: "2Cor", verse: "8.022", unformatted: "我们还打发了另一位弟兄和他们一起去。我们在很多事上多次验证过这位弟兄是殷勤的，而现在他因为深深地信任你们，就更加殷勤了。", version_id: 6},
  {id: 160976, book: "2Cor", verse: "8.023", unformatted: "论到提多，他是我的同伴，也是为着你们的同工。至于那两位弟兄，他们是各教会的使者，是基督的荣耀。", version_id: 6},
  {id: 160977, book: "2Cor", verse: "8.024", unformatted: "所以，你们要在各教会面前，向他们显出你们爱心的凭证，以及我们所夸耀你们的凭据。", version_id: 6},
  {id: 160978, book: "2Cor", verse: "9.001", unformatted: "捐助的动机\n关于服事圣徒的事，其实我不必给你们写什么，", version_id: 6},
  {id: 160979, book: "2Cor", verse: "9.002", unformatted: "因为我知道你们的乐意之心。为此，我向马其顿人夸耀你们，说：“亚该亚人从去年就已经准备好了。”你们的热心激励了他们中的大多数人。", version_id: 6},
  {id: 160980, book: "2Cor", verse: "9.003", unformatted: "不过我派这些弟兄去，是为了我们在这方面对你们的夸耀不至于落空，好让你们能照着我所说的准备好；", version_id: 6},
  {id: 160981, book: "2Cor", verse: "9.004", unformatted: "免得万一有些马其顿人和我一起来，发现你们并没有预备好，我们就会因着对你们的确信而蒙羞，更不用说你们了。", version_id: 6},
  {id: 160982, book: "2Cor", verse: "9.005", unformatted: "所以，我认为必须鼓励这些弟兄先到你们那里去，把你们已经许诺的惠赠提前安排好，这样就表明你们所预备好的就是惠赠，而不是被强取的。", version_id: 6},
  {id: 160983, book: "2Cor", verse: "9.006", unformatted: "又有这话：播撒吝啬的人，也收获吝啬；播撒祝福的人，也收获祝福。", version_id: 6},
  {id: 160984, book: "2Cor", verse: "9.007", unformatted: "每个人要照着心里预先定好的，不要为难，也不要勉强，因为神喜爱那乐意给与的人。", version_id: 6},
  {id: 160985, book: "2Cor", verse: "9.008", unformatted: "神能使一切的恩典丰丰富富地临到你们，使你们总是在一切事上都能知足，在一切美善的工作上充实满溢。", version_id: 6},
  {id: 160986, book: "2Cor", verse: "9.009", unformatted: "正如经上所记：\n“他施舍，赐予穷人；\n他的义行永远常存。”", version_id: 6},
  {id: 160987, book: "2Cor", verse: "9.01", unformatted: "供应种子给撒种的，又供应粮食给人吃的那一位，将要供给并增加你们的种子，而且增多你们义行的果子，", version_id: 6},
  {id: 160988, book: "2Cor", verse: "9.011", unformatted: "使你们在一切事上都得以富足，以致十分慷慨，就藉着我们生出对神的感谢之心。", version_id: 6},
  {id: 160989, book: "2Cor", verse: "9.012", unformatted: "因为这服事的工作，不仅补足了圣徒们的缺乏，并且能藉着对神的许多感谢而充实满溢。", version_id: 6},
  {id: 160990, book: "2Cor", verse: "9.013", unformatted: "他们藉着这次服事的凭证，因你们顺服了那为基督福音所做的告白，也因你们给他们和众人的慷慨分享，他们就会荣耀神；", version_id: 6},
  {id: 160991, book: "2Cor", verse: "9.014", unformatted: "并且他们因着神赐给你们这无限的恩典，就切切地想念你们，为你们祈祷。", version_id: 6},
  {id: 160992, book: "2Cor", verse: "9.015", unformatted: "感谢神，因他有无法描述的恩赐！", version_id: 6},
  {id: 160993, book: "2Cor", verse: "10.001", unformatted: "使徒的权柄\n我保罗与你们面对面的时候是卑微的，不在你们那里的时候向你们却是大胆的。现在我亲自以基督的温柔与谦和恳求你们：", version_id: 6},
  {id: 160994, book: "2Cor", verse: "10.002", unformatted: "我请求，当我在你们那里的时候，我不需要大胆自信；我本来打算用这样的勇气来对付那些人——他们把我们看做是顺着肉体行事的。", version_id: 6},
  {id: 160995, book: "2Cor", verse: "10.003", unformatted: "我们诚然在肉体中行事，但不凭着肉体争战，", version_id: 6},
  {id: 160996, book: "2Cor", verse: "10.004", unformatted: "因为我们争战所用的兵器不是属肉体的，而是有属神的能力，可以拆毁堡垒——拆毁各样的心思，", version_id: 6},
  {id: 160997, book: "2Cor", verse: "10.005", unformatted: "拆毁所有为抵挡神的知识而树立起来的高墙；又虏获各样的心意，达到属基督的顺从；", version_id: 6},
  {id: 160998, book: "2Cor", verse: "10.006", unformatted: "而且我们预备好了，要在你们完全顺从以后，惩罚所有的悖逆行为。", version_id: 6},
  {id: 160999, book: "2Cor", verse: "10.007", unformatted: "你们是根据外表看事物。如果有人深信自己是属于基督的，他自己就应该再想一想：他怎样属于基督，我们也怎样属于基督。", version_id: 6},
  {id: 161000, book: "2Cor", verse: "10.008", unformatted: "主所赐给我们的权柄是为了造就你们，不是为了拆毁你们。其实，就算我为这权柄有些过度的夸耀，我也不至于蒙羞。", version_id: 6},
  {id: 161001, book: "2Cor", verse: "10.009", unformatted: "我不愿看起来像是在用书信恐吓你们。", version_id: 6},
  {id: 161002, book: "2Cor", verse: "10.01", unformatted: "因为有人说：“保罗的书信又严厉又强硬，但他亲身在的时候却是软弱的人，他的言语也被藐视。”", version_id: 6},
  {id: 161003, book: "2Cor", verse: "10.011", unformatted: "说这样话的人应该想一想：我们不在你们那里的时候，书信上的话语怎样；我们在的时候，我们的行为也会怎样。", version_id: 6},
  {id: 161004, book: "2Cor", verse: "10.012", unformatted: "其实我们不敢把自己与某些自我推荐的人同列或比较。当他们以自己来衡量自己，以自己来比较自己的时候，他们是不领悟的。", version_id: 6},
  {id: 161005, book: "2Cor", verse: "10.013", unformatted: "但我们不会在界限以外夸耀，而是在神分给我们的界限范围内夸耀，这界限甚至一直达到你们那里。", version_id: 6},
  {id: 161006, book: "2Cor", verse: "10.014", unformatted: "的确，我们没有过于扩张自己，好像达不到你们那里，实际上我们带着基督的福音已经来了，甚至远到你们那里。", version_id: 6},
  {id: 161007, book: "2Cor", verse: "10.015", unformatted: "我们并没有在界限以外，以别人的劳苦夸耀。我们只是希望，随着你们信仰的增长，我们的范围就会因着你们而更加扩大，", version_id: 6},
  {id: 161008, book: "2Cor", verse: "10.016", unformatted: "以致把福音传到你们以外的地方；我们不是在别人的范围之内，为那些现成的事来夸耀。", version_id: 6},
  {id: 161009, book: "2Cor", verse: "10.017", unformatted: "不过“夸耀的当在主里夸耀。”", version_id: 6},
  {id: 161010, book: "2Cor", verse: "10.018", unformatted: "因为那经过考验的人，不是自我推荐的，而是主所推荐的。", version_id: 6},
  {id: 161011, book: "2Cor", verse: "11.001", unformatted: "保罗与假使徒\n我真希望你们能容忍我这一点点的愚妄！的确，你们是容忍我的。", version_id: 6},
  {id: 161012, book: "2Cor", verse: "11.002", unformatted: "我以神的热心热爱你们，因为我已经把你们许配给一位丈夫，做为贞洁的童女献给基督。", version_id: 6},
  {id: 161013, book: "2Cor", verse: "11.003", unformatted: "可是我担心，恐怕你们的思想受到败坏，离开对基督的忠实和贞洁，就像蛇用它的诡计欺骗了夏娃那样。", version_id: 6},
  {id: 161014, book: "2Cor", verse: "11.004", unformatted: "实际上，如果有人真的来传另一个耶稣，不是我们所传过的；或者你们领受另外一个灵，不是你们所领受过的；或者接受另外一个福音，不是你们所接受过的——你们竟然容忍他！", version_id: 6},
  {id: 161015, book: "2Cor", verse: "11.005", unformatted: "实际上，我认为自己没有一点赶不上那些“超级使徒”。", version_id: 6},
  {id: 161016, book: "2Cor", verse: "11.006", unformatted: "即使我在言语上没有技巧，但在知识上并不是这样。其实我们在各个方面、在所有的事上都已经向你们显明了这一点。", version_id: 6},
  {id: 161017, book: "2Cor", verse: "11.007", unformatted: "我降卑自己，好使你们被高举，因为我把神的福音无偿地传给你们，难道我这样就犯了罪吗？", version_id: 6},
  {id: 161018, book: "2Cor", verse: "11.008", unformatted: "我“掠夺了”别的教会，拿了他们的工价来服事你们。", version_id: 6},
  {id: 161019, book: "2Cor", verse: "11.009", unformatted: "我在你们那里有缺乏的时候，也没有成为任何人的负担，因为从马其顿省来的弟兄们补足了我的缺乏。在一切事上我都留意，不让自己成为你们的负担，将来也会留意。", version_id: 6},
  {id: 161020, book: "2Cor", verse: "11.01", unformatted: "我指着在我里面基督的真理说，在亚该亚地区，谁也不能阻止我如此夸耀。", version_id: 6},
  {id: 161021, book: "2Cor", verse: "11.011", unformatted: "为什么呢？难道因为我不爱你们吗？神是知道的！", version_id: 6},
  {id: 161022, book: "2Cor", verse: "11.012", unformatted: "我现在所做的，将来还要做，为要断绝那些人的机会——他们想找一个机会，好让别人以为他们所夸耀的与我们一样，", version_id: 6},
  {id: 161023, book: "2Cor", verse: "11.013", unformatted: "因为这样的人是假使徒，是诡诈的工人，他们装做基督的使徒。", version_id: 6},
  {id: 161024, book: "2Cor", verse: "11.014", unformatted: "这并不足为奇，原来撒旦自己也装做光明的天使；", version_id: 6},
  {id: 161025, book: "2Cor", verse: "11.015", unformatted: "所以，即使他的仆人把自己装做义的仆人，也不足为怪；他们的结局必然照着他们的行为。", version_id: 6},
  {id: 161026, book: "2Cor", verse: "11.016", unformatted: "为主受患难\n我再说：谁都不要以为我是愚妄的人！否则，至少该接受我这个像是愚妄的人，好让我也可以稍微自夸。", version_id: 6},
  {id: 161027, book: "2Cor", verse: "11.017", unformatted: "我现在所说的，不是按照主说的，而是像一个愚妄人那样自信夸耀。", version_id: 6},
  {id: 161028, book: "2Cor", verse: "11.018", unformatted: "既然有许多人按人的标准自夸，我也要自夸了，", version_id: 6},
  {id: 161029, book: "2Cor", verse: "11.019", unformatted: "因为你们做为聪明人，乐意容忍愚妄的人！", version_id: 6},
  {id: 161030, book: "2Cor", verse: "11.02", unformatted: "实际上，就算有人奴役你们，或侵吞你们，或榨取你们，或压制你们，或打你们的脸，你们也能容忍。", version_id: 6},
  {id: 161031, book: "2Cor", verse: "11.021", unformatted: "说来惭愧，我们倒好像是软弱的。\n但如果有人在什么事上大胆——我说句愚妄的话——我也大胆！", version_id: 6},
  {id: 161032, book: "2Cor", verse: "11.022", unformatted: "他们是希伯来人吗？我也是。\n他们是以色列人吗？我也是。\n他们是亚伯拉罕的后裔吗？我也是。", version_id: 6},
  {id: 161033, book: "2Cor", verse: "11.023", unformatted: "他们是基督的仆人吗？\n我狂妄地说：我更是！\n我受了更多的劳苦，更多的监禁，\n更多的鞭打，经常面临死亡，", version_id: 6},
  {id: 161034, book: "2Cor", verse: "11.024", unformatted: "我在犹太人的手中，遭受了五次“四十减一”的鞭打，", version_id: 6},
  {id: 161035, book: "2Cor", verse: "11.025", unformatted: "被棍子打过三次，\n被石头砸过一次，\n遭遇海难三次，\n在深海里度过了一昼一夜；", version_id: 6},
  {id: 161036, book: "2Cor", verse: "11.026", unformatted: "我经常行远路，\n遭遇江河的危险、强盗的危险、\n来自同胞的危险、来自外邦人的危险、\n在城里的危险、在旷野中的危险、\n在海上的危险、在假弟兄中的危险；", version_id: 6},
  {id: 161037, book: "2Cor", verse: "11.027", unformatted: "我辛苦劳碌，经常失眠，\n又饥又渴，经常缺食，\n遭受寒冷，衣不蔽体。", version_id: 6},
  {id: 161038, book: "2Cor", verse: "11.028", unformatted: "除了这些外在的事，还有对各教会的挂虑，天天压在我身上。", version_id: 6},
  {id: 161039, book: "2Cor", verse: "11.029", unformatted: "有谁软弱，我不软弱呢？有谁被绊倒，我不心如火烧呢？", version_id: 6},
  {id: 161040, book: "2Cor", verse: "11.03", unformatted: "如果必须夸耀，我就夸耀自己的那些软弱。", version_id: 6},
  {id: 161041, book: "2Cor", verse: "11.031", unformatted: "主耶稣的神与父——永远当受颂赞的那一位，知道我不是在说谎。", version_id: 6},
  {id: 161042, book: "2Cor", verse: "11.032", unformatted: "在大马士革，阿瑞塔斯王的总督看守了大马士革城，要抓我，", version_id: 6},
  {id: 161043, book: "2Cor", verse: "11.033", unformatted: "可是我被人用筐子从城墙的窗口缒下来，逃脱了他的手。", version_id: 6},
  {id: 161044, book: "2Cor", verse: "12.001", unformatted: "恩典够用\n夸耀虽然没有益处，但是必须的；我要说到主的异象和启示：", version_id: 6},
  {id: 161045, book: "2Cor", verse: "12.002", unformatted: "我知道一个在基督里的人，他十四年前被提到第三层天上。他是否在身体里，我不知道；是否在身体外，我也不知道；只有神知道。", version_id: 6},
  {id: 161046, book: "2Cor", verse: "12.003", unformatted: "不过我知道这样一个人——他是否在身体里，还是在身体外，我都不知道，只有神知道——", version_id: 6},
  {id: 161047, book: "2Cor", verse: "12.004", unformatted: "他被提到乐园里去，听见了无法描述的话语，是人不可以说出来的。", version_id: 6},
  {id: 161048, book: "2Cor", verse: "12.005", unformatted: "为这样一个人，我要夸耀，但为我自己，除了有关那些软弱的事以外，我不夸耀。", version_id: 6},
  {id: 161049, book: "2Cor", verse: "12.006", unformatted: "其实就算我愿意夸耀，也不算是愚妄的人，因为我会讲真理。可是我要克制，免得有人把我看高了，过于他在我身上所看到的，或是从我所听到的。", version_id: 6},
  {id: 161050, book: "2Cor", verse: "12.007", unformatted: "并且为了不让我因这些极大的启示而高抬自己，有一根刺加在我的肉体上，就是撒旦的使者来击打我，免得我高抬自己。", version_id: 6},
  {id: 161051, book: "2Cor", verse: "12.008", unformatted: "为了这事，我三次求主使这根刺离开我。", version_id: 6},
  {id: 161052, book: "2Cor", verse: "12.009", unformatted: "但他对我说：“我的恩典是够你用的，因为我的大能在软弱中得以完全。”因此，我反而极其乐意地夸耀我的那些软弱，好让基督的能力遮盖在我身上。", version_id: 6},
  {id: 161053, book: "2Cor", verse: "12.01", unformatted: "所以，为了基督的缘故，我在那些软弱中、凌辱中、艰难中、在逼迫和困苦中，都感到喜悦；因为我什么时候软弱，什么时候就刚强了。", version_id: 6},
  {id: 161054, book: "2Cor", verse: "12.011", unformatted: "使徒的标记\n我成了愚妄的人，是你们逼我的。其实我应该受到你们的推荐，因为即使我一无所是，我也没有一点赶不上那些“超级使徒”。", version_id: 6},
  {id: 161055, book: "2Cor", verse: "12.012", unformatted: "我在你们中间以极大的忍耐，用神迹、奇事和大能，确实显出了使徒的标记。", version_id: 6},
  {id: 161056, book: "2Cor", verse: "12.013", unformatted: "除了我自己没有成为你们的负担以外，你们所受的对待，到底有什么不如其他教会呢？请你们饶恕我这点不公平吧！", version_id: 6},
  {id: 161057, book: "2Cor", verse: "12.014", unformatted: "对哥林多信徒的关心\n看哪，这是我第三次预备好到你们那里去，而且我不会成为你们的负担，因为我想要的不是你们的东西，而是你们自己。本来，不应该是儿女为父母积蓄财富，而是父母为儿女积蓄财富。", version_id: 6},
  {id: 161058, book: "2Cor", verse: "12.015", unformatted: "所以为了你们的灵魂，我会极其乐意地付出，甚至完全付出。难道我爱你们越多，就越少得到你们的爱吗？", version_id: 6},
  {id: 161059, book: "2Cor", verse: "12.016", unformatted: "不过就算如此，我也没有成为你们的负担——难道我是狡猾的人，用诡计牢笼了你们吗？", version_id: 6},
  {id: 161060, book: "2Cor", verse: "12.017", unformatted: "难道我曾经藉着派到你们那里去的人，对不起你们吗？", version_id: 6},
  {id: 161061, book: "2Cor", verse: "12.018", unformatted: "我鼓励提多去，又打发那位弟兄与他一起去。难道提多对不起你们吗？我们行事，难道不是同一个心灵吗？难道不是同一个步调吗？", version_id: 6},
  {id: 161062, book: "2Cor", verse: "12.019", unformatted: "你们一直以为我们是在向你们申辩吗？我们是在基督里，在神面前说话。各位蒙爱的人哪，这一切都是为了造就你们。", version_id: 6},
  {id: 161063, book: "2Cor", verse: "12.02", unformatted: "其实我担心，恐怕我来到的时候会发现你们不像我所希望的那样，而你们发现我也不像你们所希望的那样；又恐怕有纷争、嫉妒、暴怒、争竞、诽谤、谗言、自大和混乱；", version_id: 6},
  {id: 161064, book: "2Cor", verse: "12.021", unformatted: "恐怕我来的时候，我的神会使我在你们面前再一次蒙羞，而且因为许多人从前犯了罪，又不肯为他们所行的污秽、淫乱和好色的事而悔改，我就会悲伤。", version_id: 6},
  {id: 161065, book: "2Cor", verse: "13.001", unformatted: "结尾的警告与劝勉\n这是我第三次到你们那里去。“任何事，凭两个或三个见证人的口，才能成立。”", version_id: 6},
  {id: 161066, book: "2Cor", verse: "13.002", unformatted: "我先前说过了，现在虽然我不在你们那里，还要像第二次在你们那里的时候一样，预先警告那些从前犯了罪的人和所有其余的人：我如果再来，不会顾惜！", version_id: 6},
  {id: 161067, book: "2Cor", verse: "13.003", unformatted: "这是因为你们寻找基督在我里面说话的凭证。基督对你们并不是软弱的；相反，他在你们中间是有大能的。", version_id: 6},
  {id: 161068, book: "2Cor", verse: "13.004", unformatted: "实际上，基督虽然在软弱中被钉十字架，但他却在神的大能中活着。的确，我们虽然在基督里也软弱，但为了你们，我们却在神的大能中，将要与基督一同活着。", version_id: 6},
  {id: 161069, book: "2Cor", verse: "13.005", unformatted: "你们要察验自己是否在信仰中，要考验自己。难道你们不了解耶稣基督在你们里面吗？除非你们是经不起考验的。", version_id: 6},
  {id: 161070, book: "2Cor", verse: "13.006", unformatted: "但是我希望你们明白，我们不是经不起考验的。", version_id: 6},
  {id: 161071, book: "2Cor", verse: "13.007", unformatted: "我们祈求神，使你们不做任何恶事。这不是要显明我们是经过考验的，而是要你们行美善的事，就算我们可能像是经不起考验的。", version_id: 6},
  {id: 161072, book: "2Cor", verse: "13.008", unformatted: "因为我们做任何事都不能抵挡真理，只能拥护真理。", version_id: 6},
  {id: 161073, book: "2Cor", verse: "13.009", unformatted: "实际上，当我们软弱而你们刚强的时候，我们就欢喜；我们还祈求这一点：就是你们得以完全。", version_id: 6},
  {id: 161074, book: "2Cor", verse: "13.01", unformatted: "为此，我不在你们那里的时候写了这些事，好让我来到的时候，不必用主所赐给我的权柄严厉地对待你们。这权柄是为了造就人，并不是为了拆毁人。", version_id: 6},
  {id: 161075, book: "2Cor", verse: "13.011", unformatted: "最后，弟兄们，你们要欢喜！愿你们得以完全，愿你们得到安慰！你们要同心合意，要彼此和睦。这样，慈爱与平安的神就会与你们同在。", version_id: 6},
  {id: 161076, book: "2Cor", verse: "13.012", unformatted: "你们要用圣洁的亲吻礼彼此问候。所有的圣徒都问候你们。", version_id: 6},
  {id: 161077, book: "2Cor", verse: "13.013", unformatted: "愿主耶稣基督的恩典、神的慈爱、圣灵的契合与你们大家同在！", version_id: 6},
  {id: 161078, book: "Gal", verse: "1.001", unformatted: "问安\n我保罗做使徒，既不是出于人，也不是藉着人，而是藉着耶稣基督和使他从死人中复活的父神；", version_id: 6},
  {id: 161079, book: "Gal", verse: "1.002", unformatted: "我和所有与我在一起的弟兄致加拉太的各教会：", version_id: 6},
  {id: 161080, book: "Gal", verse: "1.003", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 161081, book: "Gal", verse: "1.004", unformatted: "基督照着神我们父的旨意，已经为我们的罪孽献上了自己，为要把我们从现今这邪恶的世代救出来。", version_id: 6},
  {id: 161082, book: "Gal", verse: "1.005", unformatted: "愿荣耀归于他，直到永永远远！阿们。", version_id: 6},
  {id: 161083, book: "Gal", verse: "1.006", unformatted: "没有别的福音\n我感到惊奇，你们这么轻易地离弃了藉着基督的恩典召唤你们的那一位，转向了别的福音。", version_id: 6},
  {id: 161084, book: "Gal", verse: "1.007", unformatted: "其实那并不是另一个福音，而是有些人在搅扰你们，想要篡改基督的福音。", version_id: 6},
  {id: 161085, book: "Gal", verse: "1.008", unformatted: "然而，就算是我们，或是天上来的使者，如果向你们传讲的福音，与我们先前所传给你们的不同，他就该受诅咒。", version_id: 6},
  {id: 161086, book: "Gal", verse: "1.009", unformatted: "我们先前说过，现在我再说：如果有人向你们传讲的福音，与你们先前所领受的不同，他就该受诅咒。", version_id: 6},
  {id: 161087, book: "Gal", verse: "1.01", unformatted: "我现在到底是要得人的信任，还是要得神的信任呢？难道我是想讨人的喜悦吗？如果我仍然讨人的喜悦，我就不是基督的奴仆了。", version_id: 6},
  {id: 161088, book: "Gal", verse: "1.011", unformatted: "为使徒身份辩护\n弟兄们，我要你们明白，我所传的福音不是按人的意思，", version_id: 6},
  {id: 161089, book: "Gal", verse: "1.012", unformatted: "因为我既不是从人领受的，也不是受人教导的，而是藉着耶稣基督的启示领受的。", version_id: 6},
  {id: 161090, book: "Gal", verse: "1.013", unformatted: "你们听说过我以前在犹太教中的行事为人：我曾极力地逼迫神的教会，残害她。", version_id: 6},
  {id: 161091, book: "Gal", verse: "1.014", unformatted: "我在犹太教中，比我本族的许多同辈更进一步，是一个对我祖先的传统格外热心的人。", version_id: 6},
  {id: 161092, book: "Gal", verse: "1.015", unformatted: "然而神，就是当我在母腹里时就把我分别出来、又藉着他的恩典召唤我的那一位，乐意", version_id: 6},
  {id: 161093, book: "Gal", verse: "1.016", unformatted: "将他的儿子启示在我里面，好使我在外邦人中传扬他。我没有立刻与人商量，", version_id: 6},
  {id: 161094, book: "Gal", verse: "1.017", unformatted: "也没有上耶路撒冷去见那些比我先做使徒的，而是往阿拉伯去，然后再回到大马士革。", version_id: 6},
  {id: 161095, book: "Gal", verse: "1.018", unformatted: "三年以后，我才上耶路撒冷去见矶法，在他那里住了十五天。", version_id: 6},
  {id: 161096, book: "Gal", verse: "1.019", unformatted: "至于其他的使徒，除了主的弟弟雅各，我都没有见过。", version_id: 6},
  {id: 161097, book: "Gal", verse: "1.02", unformatted: "我现在所写给你们的，看哪，是在神面前写的，我不是在说谎。", version_id: 6},
  {id: 161098, book: "Gal", verse: "1.021", unformatted: "后来，我到了叙利亚和奇里乞亚地区。", version_id: 6},
  {id: 161099, book: "Gal", verse: "1.022", unformatted: "那时，在基督里的犹太各教会，还没有见过我的面。", version_id: 6},
  {id: 161100, book: "Gal", verse: "1.023", unformatted: "他们只是听说：“以前逼迫我们的那个人，现在却在传扬他曾经残害过的信仰。”", version_id: 6},
  {id: 161101, book: "Gal", verse: "1.024", unformatted: "他们就因我的缘故不住地荣耀神。", version_id: 6},
  {id: 161102, book: "Gal", verse: "2.001", unformatted: "在耶路撒冷的辩护\n过了十四年，我与巴拿巴一起再次上耶路撒冷，并且带着提多一起去。", version_id: 6},
  {id: 161103, book: "Gal", verse: "2.002", unformatted: "我是照着启示上去的，向他们陈述了我在外邦人中所传的福音，不过是单独地向那些公认是人物的做了陈述，免得我过去或是现在所奔跑的都落了空。", version_id: 6},
  {id: 161104, book: "Gal", verse: "2.003", unformatted: "与我在一起的提多，虽然是希腊人，也没有被迫受割礼。", version_id: 6},
  {id: 161105, book: "Gal", verse: "2.004", unformatted: "其实提到这件事，是因着一些偷偷进来的假弟兄的缘故；他们混进来窥视我们在基督耶稣里所拥有的自由，为了要奴役我们。", version_id: 6},
  {id: 161106, book: "Gal", verse: "2.005", unformatted: "我们对这些人，一刻也没有屈服妥协，好使福音的真理在你们中间继续存留。", version_id: 6},
  {id: 161107, book: "Gal", verse: "2.006", unformatted: "至于那些公认是人物的——无论他们从前什么样，都对我没有区别；神不以外貌取人。那些公认是人物的并没有给我增添什么；", version_id: 6},
  {id: 161108, book: "Gal", verse: "2.007", unformatted: "不过他们反而看出来：我受了委托去传福音给没有受割礼的人，正如彼得受了委托去传福音给受割礼的人，", version_id: 6},
  {id: 161109, book: "Gal", verse: "2.008", unformatted: "因为在彼得里面做工、使他做受割礼之人使徒的那一位，也在我里面做工、使我做外邦人的使徒。", version_id: 6},
  {id: 161110, book: "Gal", verse: "2.009", unformatted: "公认是柱石的雅各、矶法和约翰，既然明白了神所赐给我的恩典，就向巴拿巴和我伸出右手行契合之礼，要我们往外邦人那里去，而他们往受割礼的人那里去；", version_id: 6},
  {id: 161111, book: "Gal", verse: "2.01", unformatted: "只是要我们记得穷人，而这一点也正是我一向努力做的。", version_id: 6},
  {id: 161112, book: "Gal", verse: "2.011", unformatted: "从律法下得自由\n不过矶法来到安提阿的时候，我曾当面抵挡他，因为他有该责备的地方。", version_id: 6},
  {id: 161113, book: "Gal", verse: "2.012", unformatted: "原来，从雅各那里来的一些人到达之前，矶法与外邦人一起吃饭；可是那些人一到，他因为怕那些割礼派的人，就退缩，与外邦人分开了。", version_id: 6},
  {id: 161114, book: "Gal", verse: "2.013", unformatted: "其余的犹太人也跟着他装假，以致连巴拿巴也被他们的伪善引入歧途。", version_id: 6},
  {id: 161115, book: "Gal", verse: "2.014", unformatted: "但我一看见他们的行为不符合福音的真理，就在大家面前对矶法说：“你身为犹太人，如果像外邦人而不像犹太人那样生活，怎么还要强求外邦人像犹太人一样呢？”", version_id: 6},
  {id: 161116, book: "Gal", verse: "2.015", unformatted: "我们生来是犹太人，不是“外邦中的罪人”，", version_id: 6},
  {id: 161117, book: "Gal", verse: "2.016", unformatted: "不过我们知道：人被称为义不是本于律法上的行为，而是藉着信耶稣基督。于是我们信了基督耶稣，为要因信基督称义，而不是本于律法上的行为，因为没有一个人本于律法上的行为会被称为义。", version_id: 6},
  {id: 161118, book: "Gal", verse: "2.017", unformatted: "但如果我们自己寻求在基督里被称为义，却被发现还是罪人，那么，难道基督就成了罪的仆人吗？绝对不是！", version_id: 6},
  {id: 161119, book: "Gal", verse: "2.018", unformatted: "因为我如果重建自己以前所拆毁的，就证明我是违犯律法的人。", version_id: 6},
  {id: 161120, book: "Gal", verse: "2.019", unformatted: "原来藉着律法，我已经向律法死了，使我能向神活着。我已经和基督一起被钉十字架，", version_id: 6},
  {id: 161121, book: "Gal", verse: "2.02", unformatted: "所以现在活着的不再是我，而是基督在我里面活着；并且如今我在肉体中活着，是因信神的儿子而活；他爱我，甚至为我舍弃了自己。", version_id: 6},
  {id: 161122, book: "Gal", verse: "2.021", unformatted: "我不弃绝神的恩典，因为义如果是藉着律法而来的，那么，基督就白白地死了。", version_id: 6},
  {id: 161123, book: "Gal", verse: "3.001", unformatted: "因信称义\n唉，无知的加拉太人哪！是谁迷惑了你们呢？被钉十字架的耶稣基督，难道不是先前被描绘在你们眼前的吗？", version_id: 6},
  {id: 161124, book: "Gal", verse: "3.002", unformatted: "我只想请教你们这一点：你们领受了圣灵，是本于律法上的行为，还是本于信而听从呢？", version_id: 6},
  {id: 161125, book: "Gal", verse: "3.003", unformatted: "你们是这样的无知吗？你们以圣灵开始，现在却要以肉体完成吗？", version_id: 6},
  {id: 161126, book: "Gal", verse: "3.004", unformatted: "你们受了这么多的苦，都没有意义吗？会不会真的没有意义呢！", version_id: 6},
  {id: 161127, book: "Gal", verse: "3.005", unformatted: "那么，神赐给你们圣灵，又在你们中间行神迹，是本于你们在律法上的行为，还是本于你们信而听从呢？", version_id: 6},
  {id: 161128, book: "Gal", verse: "3.006", unformatted: "正如“亚伯拉罕信神，这就被算为他的义”，", version_id: 6},
  {id: 161129, book: "Gal", verse: "3.007", unformatted: "因此你们应当知道：那些本于信的，他们才是亚伯拉罕的子孙。", version_id: 6},
  {id: 161130, book: "Gal", verse: "3.008", unformatted: "既然经文预先看见神要使外邦人因信称义，就预先把福音传给了亚伯拉罕，说：“万国都要藉着你蒙祝福。”", version_id: 6},
  {id: 161131, book: "Gal", verse: "3.009", unformatted: "这样，那些有信仰的人，与有信仰的亚伯拉罕一同蒙祝福。", version_id: 6},
  {id: 161132, book: "Gal", verse: "3.01", unformatted: "律法与应许\n凡是本于律法上行为的人，都在诅咒之下，因为经上记着：“任何人不持守、不遵行律法书上所记载的一切，都是被诅咒的。”", version_id: 6},
  {id: 161133, book: "Gal", verse: "3.011", unformatted: "所以，明显地没有人能藉着律法在神面前被称为义，因为经上说：“义人将因信而活。”", version_id: 6},
  {id: 161134, book: "Gal", verse: "3.012", unformatted: "律法不是本于信，但是“遵行这些事的人，将因此而活。”", version_id: 6},
  {id: 161135, book: "Gal", verse: "3.013", unformatted: "基督替我们成了诅咒，从律法的诅咒中救赎了我们，因为经上记着：“凡是被挂在木头上的，都是被诅咒的。”", version_id: 6},
  {id: 161136, book: "Gal", verse: "3.014", unformatted: "这是为了让亚伯拉罕所蒙的祝福，能在基督耶稣里临到外邦人，好使我们能藉着信领受所应许的圣灵。", version_id: 6},
  {id: 161137, book: "Gal", verse: "3.015", unformatted: "弟兄们，我按人的意思说话：甚至是人的约，一旦证实了，也没有人能废弃或加添。", version_id: 6},
  {id: 161138, book: "Gal", verse: "3.016", unformatted: "神的各样应许，原是对亚伯拉罕和他的一个后裔说的。神并没有说“后裔们”，像是指着许多人；而是说“你的一个后裔”，像是指着一个人，这一位就是基督。", version_id: 6},
  {id: 161139, book: "Gal", verse: "3.017", unformatted: "我说的这话是：四百三十年以后出现的律法，不能废弃神预先立好的约，使那应许无效。", version_id: 6},
  {id: 161140, book: "Gal", verse: "3.018", unformatted: "如果继业是本于律法，就不再是本于应许了；但是神把继业赐给亚伯拉罕是藉着应许。", version_id: 6},
  {id: 161141, book: "Gal", verse: "3.019", unformatted: "律法的目的\n那么，为什么要有律法呢？律法是为了过犯的缘故被加上的，直到那蒙应许的后裔来临；律法是藉着天使，通过中保的手所规定的。", version_id: 6},
  {id: 161142, book: "Gal", verse: "3.02", unformatted: "不过中保没有为一方的，神却是一位。", version_id: 6},
  {id: 161143, book: "Gal", verse: "3.021", unformatted: "那么，律法和神的各样应许是对立的吗？绝对不是！如果一个能使人得生命的律法被赐下，义就真是本于律法了。", version_id: 6},
  {id: 161144, book: "Gal", verse: "3.022", unformatted: "然而，经上的话把万物都圈在罪孽之下，为要使这因信耶稣基督而来的应许，赐给那些相信的人。", version_id: 6},
  {id: 161145, book: "Gal", verse: "3.023", unformatted: "只是在这信仰临到之前，我们都被圈在律法之下受看守，直到这将要来临的信仰被启示出来。", version_id: 6},
  {id: 161146, book: "Gal", verse: "3.024", unformatted: "这样，律法一向是我们的导师，直到基督，好使我们能因信称义。", version_id: 6},
  {id: 161147, book: "Gal", verse: "3.025", unformatted: "不过，这信仰既然已经临到，我们就不在导师之下了，", version_id: 6},
  {id: 161148, book: "Gal", verse: "3.026", unformatted: "因为你们藉着信，在基督耶稣里，都是神的儿女。", version_id: 6},
  {id: 161149, book: "Gal", verse: "3.027", unformatted: "后裔与继承人\n实际上，你们所有受洗归入基督的，就穿上了基督，", version_id: 6},
  {id: 161150, book: "Gal", verse: "3.028", unformatted: "其中没有犹太人或希腊人，没有为奴的或自由的，没有男的或女的，因为你们在基督耶稣里都合而为一了。", version_id: 6},
  {id: 161151, book: "Gal", verse: "3.029", unformatted: "而且，如果你们属于基督，你们就是亚伯拉罕的后裔，是照着应许的继承人。", version_id: 6},
  {id: 161152, book: "Gal", verse: "4.001", unformatted: "我说：继承人虽然是一切的主人，但是当他还是小孩子的时候，与奴仆没有区别；", version_id: 6},
  {id: 161153, book: "Gal", verse: "4.002", unformatted: "他是在监护人和管家之下，直到父亲所预定的时候来到。", version_id: 6},
  {id: 161154, book: "Gal", verse: "4.003", unformatted: "我们也是这样：我们还是小孩子的时候，在世界的原则下受奴役。", version_id: 6},
  {id: 161155, book: "Gal", verse: "4.004", unformatted: "但是等到时候一满足，神就派遣他的儿子，由女人所生，生在律法之下，", version_id: 6},
  {id: 161156, book: "Gal", verse: "4.005", unformatted: "为要救赎律法之下的人，好使我们得到儿子的名份。", version_id: 6},
  {id: 161157, book: "Gal", verse: "4.006", unformatted: "而且因你们是儿子，神就派遣他儿子的灵进入你们的心，呼叫：“阿爸！父啊！”", version_id: 6},
  {id: 161158, book: "Gal", verse: "4.007", unformatted: "这样，你不再是奴仆，却是儿子了；既然是儿子，就藉着神，也是继承人。", version_id: 6},
  {id: 161159, book: "Gal", verse: "4.008", unformatted: "对加拉太信徒的关心\n从前，你们不认识神的时候，固然给那些本性上不是神的做了奴仆，", version_id: 6},
  {id: 161160, book: "Gal", verse: "4.009", unformatted: "但如今，你们认识了神——不过更要说，被神所认识——怎么能再回到那无能、无用的原则里去呢？你们愿意再重新做它们的奴仆吗？", version_id: 6},
  {id: 161161, book: "Gal", verse: "4.01", unformatted: "你们竟严守着某些日子、月份、节期、年份！", version_id: 6},
  {id: 161162, book: "Gal", verse: "4.011", unformatted: "我替你们担心，恐怕我为你们的劳苦毫无意义了。", version_id: 6},
  {id: 161163, book: "Gal", verse: "4.012", unformatted: "弟兄们，我请求你们要像我一样，因为我也像你们一样。你们一点也没有亏负过我。", version_id: 6},
  {id: 161164, book: "Gal", verse: "4.013", unformatted: "你们知道，我原先传福音给你们，是身体有病的时候。", version_id: 6},
  {id: 161165, book: "Gal", verse: "4.014", unformatted: "虽然我的身体对你们是个试炼，但你们并没有藐视我，也没有唾弃我，反而接受我如同接受神的使者、如同接受基督耶稣。", version_id: 6},
  {id: 161166, book: "Gal", verse: "4.015", unformatted: "你们当时的福份到哪里去了呢？我可以给你们做见证：那时如果有可能，你们连自己的眼睛都会挖出来给我！", version_id: 6},
  {id: 161167, book: "Gal", verse: "4.016", unformatted: "我对你们说真话，结果就成了你们的敌人吗？", version_id: 6},
  {id: 161168, book: "Gal", verse: "4.017", unformatted: "那些人对你们热心，并不是出于好意，而是想要离间你们，好让你们对他们热心。", version_id: 6},
  {id: 161169, book: "Gal", verse: "4.018", unformatted: "不过在善事上常有热心，却是好的，但不要仅仅是我与你们在一起的时候才这样。", version_id: 6},
  {id: 161170, book: "Gal", verse: "4.019", unformatted: "我的孩子们哪，为了你们，我再次经受临产的阵痛，直到基督在你们里面成形！", version_id: 6},
  {id: 161171, book: "Gal", verse: "4.02", unformatted: "我现在想要与你们在一起，也要改变我的语气，因为我为你们的缘故心里困惑。", version_id: 6},
  {id: 161172, book: "Gal", verse: "4.021", unformatted: "两个妇人的寓意\n你们这些愿意在律法之下的人哪，请告诉我，难道你们没有听过律法吗？", version_id: 6},
  {id: 161173, book: "Gal", verse: "4.022", unformatted: "因为经上记着：亚伯拉罕有两个儿子，一个出于女仆，一个出于自由女人。", version_id: 6},
  {id: 161174, book: "Gal", verse: "4.023", unformatted: "那出于女仆的是由肉体生的，那出于自由女人的是藉着应许生的。", version_id: 6},
  {id: 161175, book: "Gal", verse: "4.024", unformatted: "这些都是有寓意的。就是说，这两个女人是两个约。一个出于西奈山，生子为奴，她就是夏甲。", version_id: 6},
  {id: 161176, book: "Gal", verse: "4.025", unformatted: "这夏甲是指着阿拉伯的西奈山，相当于现在的耶路撒冷，因为耶路撒冷和她的儿女都是为奴的。", version_id: 6},
  {id: 161177, book: "Gal", verse: "4.026", unformatted: "但那在上面的耶路撒冷是自由女人；她是我们的母亲。", version_id: 6},
  {id: 161178, book: "Gal", verse: "4.027", unformatted: "因为经上记着：\n“不能生育、没有生产的女子啊，\n你要欢喜！\n没有经历临产阵痛的女子啊，\n你要放声呼喊！\n因为没有丈夫的女子，\n比有丈夫的女子有更多的儿女。”", version_id: 6},
  {id: 161179, book: "Gal", verse: "4.028", unformatted: "不过弟兄们，你们就像以撒一样，是应许的儿女。", version_id: 6},
  {id: 161180, book: "Gal", verse: "4.029", unformatted: "但正如那由肉体生的当时逼迫了那由圣灵生的，现在也是这样。", version_id: 6},
  {id: 161181, book: "Gal", verse: "4.03", unformatted: "然而经上是怎么说的呢？\n“把女仆和她的儿子赶出去！因为女仆的儿子，绝不可以与自由女人的儿子一同做继承人。”", version_id: 6},
  {id: 161182, book: "Gal", verse: "4.031", unformatted: "因此，弟兄们，我们不是女仆的儿女，而是自由女人的儿女。", version_id: 6},
  {id: 161183, book: "Gal", verse: "5.001", unformatted: "基督徒的自由\n基督释放了我们，使我们得自由。所以你们当站立得稳，不要再被奴役的轭所束缚。", version_id: 6},
  {id: 161184, book: "Gal", verse: "5.002", unformatted: "看，我保罗告诉你们：如果你们要受割礼，基督就对你们毫无益处了。", version_id: 6},
  {id: 161185, book: "Gal", verse: "5.003", unformatted: "我再次忠告每一个受割礼的人：他有责任遵行全部律法。", version_id: 6},
  {id: 161186, book: "Gal", verse: "5.004", unformatted: "你们这些藉着律法被称为义的人，你们是与基督隔绝，从恩典中堕落了。", version_id: 6},
  {id: 161187, book: "Gal", verse: "5.005", unformatted: "要知道，我们藉着圣灵，本于信，满怀着称义的盼望；", version_id: 6},
  {id: 161188, book: "Gal", verse: "5.006", unformatted: "因为在基督耶稣里，受割礼或不受割礼都没有用处，只有藉着爱行出来的信仰才有用处。", version_id: 6},
  {id: 161189, book: "Gal", verse: "5.007", unformatted: "你们一向跑得很好，到底是谁拦阻了你们，使你们不信从真理呢？", version_id: 6},
  {id: 161190, book: "Gal", verse: "5.008", unformatted: "这种劝诱不是出于召唤你们的那一位。", version_id: 6},
  {id: 161191, book: "Gal", verse: "5.009", unformatted: "一点点酵母能使整个面团发酵。", version_id: 6},
  {id: 161192, book: "Gal", verse: "5.01", unformatted: "我在主里深信你们不会有别的思想；不过那搅扰你们的，无论他是谁，都将受到惩罚。", version_id: 6},
  {id: 161193, book: "Gal", verse: "5.011", unformatted: "弟兄们，如果我仍然传讲割礼，我为什么还受逼迫呢？如果我这样做，十字架使人绊脚的地方早就没有了。", version_id: 6},
  {id: 161194, book: "Gal", verse: "5.012", unformatted: "我真希望那些搅乱你们的人把自己阉割了！", version_id: 6},
  {id: 161195, book: "Gal", verse: "5.013", unformatted: "弟兄们，你们蒙召是为了得自由，只是不可用这自由给肉体一个机会，而是要藉着爱彼此服事，", version_id: 6},
  {id: 161196, book: "Gal", verse: "5.014", unformatted: "因为全部律法都在“要爱邻如己”这一句话里得以满足了。", version_id: 6},
  {id: 161197, book: "Gal", verse: "5.015", unformatted: "你们要当心！如果你们继续相咬相吞，恐怕就要彼此毁灭了。", version_id: 6},
  {id: 161198, book: "Gal", verse: "5.016", unformatted: "圣灵与肉体\n所以我说：你们应当顺着圣灵行走，这样就绝不会去满足肉体的欲望了；", version_id: 6},
  {id: 161199, book: "Gal", verse: "5.017", unformatted: "因为肉体的欲望与圣灵对立，圣灵也与肉体对立。这两者彼此反对，使你们不能做自己所愿意做的。", version_id: 6},
  {id: 161200, book: "Gal", verse: "5.018", unformatted: "但如果你们被圣灵带领，就不在律法之下了。", version_id: 6},
  {id: 161201, book: "Gal", verse: "5.019", unformatted: "肉体的行为是清清楚楚的，它们就是：通奸、淫乱、污秽、好色、", version_id: 6},
  {id: 161202, book: "Gal", verse: "5.02", unformatted: "拜偶像、行邪术、仇恨、纷争、嫉恨、暴怒、争竞、分裂、结派、", version_id: 6},
  {id: 161203, book: "Gal", verse: "5.021", unformatted: "嫉妒、凶杀、醉酒、荒宴以及类似这样的事。对这些事，我现在要预先告诉你们，就像我以前告诉过你们的那样：行这些事的人不会继承神的国。", version_id: 6},
  {id: 161204, book: "Gal", verse: "5.022", unformatted: "然而，圣灵的果子是：爱心、喜乐、和平、忍耐、仁慈、良善、忠信、", version_id: 6},
  {id: 161205, book: "Gal", verse: "5.023", unformatted: "温柔、自制。这样的事，没有律法反对。", version_id: 6},
  {id: 161206, book: "Gal", verse: "5.024", unformatted: "那些属于基督耶稣的人，已经把肉体与肉体的渴望和情欲一同钉上十字架了。", version_id: 6},
  {id: 161207, book: "Gal", verse: "5.025", unformatted: "我们既然藉着圣灵而活，就让我们靠着圣灵行走。", version_id: 6},
  {id: 161208, book: "Gal", verse: "5.026", unformatted: "我们不可贪图虚荣，彼此惹气，互相嫉妒。", version_id: 6},
  {id: 161209, book: "Gal", verse: "6.001", unformatted: "互相分担重担\n弟兄们，一个人无论被任何过犯所胜，你们属灵的人都要以温柔的心灵把这样的人挽回过来；但是你自己要留心，免得你也受试探。", version_id: 6},
  {id: 161210, book: "Gal", verse: "6.002", unformatted: "你们各人的重担要互相担当，如此，就将成全基督的律法。", version_id: 6},
  {id: 161211, book: "Gal", verse: "6.003", unformatted: "人本来算不得什么；如果自以为是什么，就是在欺骗自己。", version_id: 6},
  {id: 161212, book: "Gal", verse: "6.004", unformatted: "每个人都当省察自己的工作；这样，他就只在自我比较的时候，将有可夸耀的，而不与别人比较了，", version_id: 6},
  {id: 161213, book: "Gal", verse: "6.005", unformatted: "因为每个人都要担当自己的担子。", version_id: 6},
  {id: 161214, book: "Gal", verse: "6.006", unformatted: "在这话语上受教导的，就应该将一切美物与教导他的人分享。", version_id: 6},
  {id: 161215, book: "Gal", verse: "6.007", unformatted: "你们不要被迷惑了！神是轻慢不得的。要知道，一个人种什么，就收什么。", version_id: 6},
  {id: 161216, book: "Gal", verse: "6.008", unformatted: "因为顺着自己肉体撒种的，就会从肉体收衰朽；顺着圣灵撒种的，就会从圣灵收永恒的生命。", version_id: 6},
  {id: 161217, book: "Gal", verse: "6.009", unformatted: "我们行美善的事不要丧胆；如果不放弃，到了所定的时候就会有收获。", version_id: 6},
  {id: 161218, book: "Gal", verse: "6.01", unformatted: "由此可见，我们一有机会，就要向众人做美善的事，尤其要对信徒家里的人。", version_id: 6},
  {id: 161219, book: "Gal", verse: "6.011", unformatted: "最终的劝勉\n你们看，我亲手写给你们的字，是多么地大。", version_id: 6},
  {id: 161220, book: "Gal", verse: "6.012", unformatted: "那些想在肉体上体面的，他们才强求你们受割礼，他们只是要避免为基督的十字架受逼迫。", version_id: 6},
  {id: 161221, book: "Gal", verse: "6.013", unformatted: "事实上，那些受割礼的人自己也不遵守律法。他们要你们受割礼，是为了以你们的肉体夸耀。", version_id: 6},
  {id: 161222, book: "Gal", verse: "6.014", unformatted: "至于我，除了我们主耶稣基督的十字架以外，我绝不以别的夸耀。因这十字架，对我来说，世界已经被钉十字架了；对世界来说，我也已经被钉十字架了。", version_id: 6},
  {id: 161223, book: "Gal", verse: "6.015", unformatted: "因为受割礼或不受割礼，都算不了什么；重要的是成为新造的人。", version_id: 6},
  {id: 161224, book: "Gal", verse: "6.016", unformatted: "凡是照着这原则行走的人，愿平安和怜悯临到他们，也临到神的以色列民！", version_id: 6},
  {id: 161225, book: "Gal", verse: "6.017", unformatted: "从今以后，谁都不要烦扰我，因为我身上带着耶稣的印记。", version_id: 6},
  {id: 161226, book: "Gal", verse: "6.018", unformatted: "弟兄们，愿我们主耶稣基督的恩典与你们的灵同在！阿们。", version_id: 6},
  {id: 161227, book: "Eph", verse: "1.001", unformatted: "问安\n照着神的旨意做基督耶稣使徒的保罗，\n致住在以弗所的圣徒，就是在基督耶稣里的信徒们：", version_id: 6},
  {id: 161228, book: "Eph", verse: "1.002", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 161229, book: "Eph", verse: "1.003", unformatted: "神丰盛的恩典\n愿颂赞归于神——我们主耶稣基督的父！他在基督里，以天上各样属灵的福气祝福了我们，", version_id: 6},
  {id: 161230, book: "Eph", verse: "1.004", unformatted: "就如他从创世以前，在基督里拣选了我们，使我们在他面前成为圣洁、毫无瑕疵。他在爱中，", version_id: 6},
  {id: 161231, book: "Eph", verse: "1.005", unformatted: "照着自己心愿的美意，预定我们藉着耶稣基督得着儿子的名份，归于他自己，", version_id: 6},
  {id: 161232, book: "Eph", verse: "1.006", unformatted: "归于他恩典荣耀的称赞，这恩典是他在爱子里特别赐给我们的。", version_id: 6},
  {id: 161233, book: "Eph", verse: "1.007", unformatted: "在他里面，我们藉着他的血，得蒙救赎、过犯得到赦免，都是出于神恩典的丰盛。", version_id: 6},
  {id: 161234, book: "Eph", verse: "1.008", unformatted: "神以一切的智慧和聪明，使这恩典丰丰富富地临到我们，", version_id: 6},
  {id: 161235, book: "Eph", verse: "1.009", unformatted: "照着他在基督里预先定下的美意，让我们明白了他旨意的奥秘，", version_id: 6},
  {id: 161236, book: "Eph", verse: "1.01", unformatted: "到了那计划日期满足的时候，就使天上和地上的万有，都包含在基督里。", version_id: 6},
  {id: 161237, book: "Eph", verse: "1.011", unformatted: "在基督里，我们也得了继业，是照着那一位的心意而预先定下的，他按着自己旨意的计划来运作万事，", version_id: 6},
  {id: 161238, book: "Eph", verse: "1.012", unformatted: "以致我们这些早已在基督里有盼望的人，能归于他荣耀的称赞。", version_id: 6},
  {id: 161239, book: "Eph", verse: "1.013", unformatted: "在基督里，你们也听从了真理的话语，就是那使你们得救的福音，并且信了基督，就蒙了所应许的圣灵为印记。", version_id: 6},
  {id: 161240, book: "Eph", verse: "1.014", unformatted: "圣灵是我们得继业的预付凭据，直到属神的子民得赎，归于他荣耀的称赞。", version_id: 6},
  {id: 161241, book: "Eph", verse: "1.015", unformatted: "为属灵的见识祷告\n为此，我既然也听说了有关你们对主耶稣的信仰，以及对所有圣徒的爱心，", version_id: 6},
  {id: 161242, book: "Eph", verse: "1.016", unformatted: "就不断地为你们献上感谢，在祷告中提到你们，", version_id: 6},
  {id: 161243, book: "Eph", verse: "1.017", unformatted: "愿我们主耶稣基督的神——荣耀的父，赐给你们智慧和启示的灵，使你们能真正地认识他。", version_id: 6},
  {id: 161244, book: "Eph", verse: "1.018", unformatted: "我也求神照亮你们心中的眼睛，使你们知道：属他召唤的盼望到底是什么；在圣徒中间，他继业荣耀的丰盛到底是什么；", version_id: 6},
  {id: 161245, book: "Eph", verse: "1.019", unformatted: "并且在我们这些相信之人的身上，照着他力量的权能作为，他能力的无限伟大到底是什么。", version_id: 6},
  {id: 161246, book: "Eph", verse: "1.02", unformatted: "神在基督里的能力\n神曾将这能力运作在基督的身上，使基督从死人中复活，并且使他在天上坐在自己的右边，", version_id: 6},
  {id: 161247, book: "Eph", verse: "1.021", unformatted: "远远高过一切统治的、掌权的、有势力的、有主权的，和一切被称的名号，不仅在这世代，而且也在那将要来临的世代。", version_id: 6},
  {id: 161248, book: "Eph", verse: "1.022", unformatted: "神又使万有都服从在基督的脚下，使基督为教会做了超越万有的元首。", version_id: 6},
  {id: 161249, book: "Eph", verse: "1.023", unformatted: "教会是他的身体，是在万有中充满万有的那一位的丰盛完美。", version_id: 6},
  {id: 161250, book: "Eph", verse: "2.001", unformatted: "出死入生\n你们因着自己的过犯和罪孽，原是死的；", version_id: 6},
  {id: 161251, book: "Eph", verse: "2.002", unformatted: "在其中，你们从前行事为人，是顺着这世界的潮流，顺着那空中势力的掌权者，就是现今在那些不信从的儿女里面做工的灵。", version_id: 6},
  {id: 161252, book: "Eph", verse: "2.003", unformatted: "我们从前也都生活在他们中间，生活在自己肉体的欲望里，行肉体和意念所愿的事。我们正如其他人，生来也是震怒之下的儿女。", version_id: 6},
  {id: 161253, book: "Eph", verse: "2.004", unformatted: "不过神有丰富的怜悯，因着他爱我们的大爱，", version_id: 6},
  {id: 161254, book: "Eph", verse: "2.005", unformatted: "甚至当我们还死在过犯中的时候，就使我们与基督一同活过来——你们得救是藉着恩典！", version_id: 6},
  {id: 161255, book: "Eph", verse: "2.006", unformatted: "神又使我们在基督耶稣里，与基督一同复活、一同坐在天上，", version_id: 6},
  {id: 161256, book: "Eph", verse: "2.007", unformatted: "为了在将来的各世代中，在基督耶稣里，藉着对我们的仁慈，可以显出他那无限恩典的丰盛。", version_id: 6},
  {id: 161257, book: "Eph", verse: "2.008", unformatted: "你们得救确实是藉着恩典、藉着信，这不是出于你们自己，而是神的恩赐；", version_id: 6},
  {id: 161258, book: "Eph", verse: "2.009", unformatted: "也不是本于行为，免得有人自夸。", version_id: 6},
  {id: 161259, book: "Eph", verse: "2.01", unformatted: "实际上，我们是神的创作，是为了美善的工作在基督耶稣里被造成的；神早已预备好了美善工作，要我们在其中行事。", version_id: 6},
  {id: 161260, book: "Eph", verse: "2.011", unformatted: "在基督里合而为一\n所以你们当记住：从前，你们照着肉体说是外邦人，被那些所谓“受割礼的”称为“没受割礼的”；割礼是人手在肉体上所行的。", version_id: 6},
  {id: 161261, book: "Eph", verse: "2.012", unformatted: "那时候，你们与基督无关；被排除在以色列国民之外；在那些所应许的诸约上是外人；在这世界上没有盼望、没有神。", version_id: 6},
  {id: 161262, book: "Eph", verse: "2.013", unformatted: "但如今，在基督耶稣里，你们这些从前在远处的人，藉着基督的血，已经被带到近处了。", version_id: 6},
  {id: 161263, book: "Eph", verse: "2.014", unformatted: "因为他自己是我们的和平，他使两者合而为一，废除了那隔在中间仇恨的墙；以自己的身体，", version_id: 6},
  {id: 161264, book: "Eph", verse: "2.015", unformatted: "废除了谕令中诫命的律法，为了把两者在他自己里面造成一个新人，缔造和平。", version_id: 6},
  {id: 161265, book: "Eph", verse: "2.016", unformatted: "他在十字架上既然消除了仇恨，就藉着十字架使两者得以在一个身体里，与神和好；", version_id: 6},
  {id: 161266, book: "Eph", verse: "2.017", unformatted: "并且来传和平的福音给你们在远处的人，也给那些在近处的人。", version_id: 6},
  {id: 161267, book: "Eph", verse: "2.018", unformatted: "因为藉着基督，我们两者能够在同一位圣灵里，来到父面前。", version_id: 6},
  {id: 161268, book: "Eph", verse: "2.019", unformatted: "由此可见，你们不再是外人，或寄居的；你们是圣徒的同国之民，是神家里的人，", version_id: 6},
  {id: 161269, book: "Eph", verse: "2.02", unformatted: "并且被建造在使徒和先知的根基上，有基督耶稣自己为房角石。", version_id: 6},
  {id: 161270, book: "Eph", verse: "2.021", unformatted: "在基督耶稣里，全部建筑互相连接，渐渐成为在主里的圣殿；", version_id: 6},
  {id: 161271, book: "Eph", verse: "2.022", unformatted: "在他里面，你们也一同被建造，藉着圣灵成为神居住的地方。", version_id: 6},
  {id: 161272, book: "Eph", verse: "3.001", unformatted: "对外邦人的服事\n因此，我保罗为你们外邦人做了基督耶稣的囚犯。", version_id: 6},
  {id: 161273, book: "Eph", verse: "3.002", unformatted: "你们一定听说过，为了你们的缘故，神那恩典的任务已经赐给我了；", version_id: 6},
  {id: 161274, book: "Eph", verse: "3.003", unformatted: "也就是说，藉着启示，那奥秘已经显明给我，就像我先前简略写过的那样。", version_id: 6},
  {id: 161275, book: "Eph", verse: "3.004", unformatted: "你们读了，就能明白我在基督奥秘上的悟性。", version_id: 6},
  {id: 161276, book: "Eph", verse: "3.005", unformatted: "这奥秘在别的各世代并不显明给人类，不像如今藉着圣灵启示给了他的圣使徒和先知们。", version_id: 6},
  {id: 161277, book: "Eph", verse: "3.006", unformatted: "这奥秘就是：外邦人在基督耶稣里，藉着福音，成为共同的继承人，同属一体、同蒙应许。", version_id: 6},
  {id: 161278, book: "Eph", verse: "3.007", unformatted: "我成了这福音的仆人，是照着神恩典的赏赐；这赏赐是照着他大能的作为赐给我的。", version_id: 6},
  {id: 161279, book: "Eph", verse: "3.008", unformatted: "我在所有圣徒中，比最小的还小，但被赐予了这恩典，是为了把基督那无法测度的丰盛传给外邦人；", version_id: 6},
  {id: 161280, book: "Eph", verse: "3.009", unformatted: "并且使所有的人都明白这奥秘的计划到底是什么，这奥秘自古以来被隐藏在创造万有的神里面，", version_id: 6},
  {id: 161281, book: "Eph", verse: "3.01", unformatted: "为要使天上的统治者和掌权者，如今藉着教会能明白神各种各样的智慧。", version_id: 6},
  {id: 161282, book: "Eph", verse: "3.011", unformatted: "这都是照着他在我们主基督耶稣里所成就的永恒心意。", version_id: 6},
  {id: 161283, book: "Eph", verse: "3.012", unformatted: "在基督里，藉着信基督，我们能坦然无惧、确信无疑地来到神面前。", version_id: 6},
  {id: 161284, book: "Eph", verse: "3.013", unformatted: "所以，我请求你们，不要因我为你们所受的患难而丧胆；这患难是你们的荣耀。", version_id: 6},
  {id: 161285, book: "Eph", verse: "3.014", unformatted: "祈求属灵的能力\n因此，我屈膝在父面前——", version_id: 6},
  {id: 161286, book: "Eph", verse: "3.015", unformatted: "天上地上的万族都是从他得名的，", version_id: 6},
  {id: 161287, book: "Eph", verse: "3.016", unformatted: "愿他按照他那荣耀的丰盛，藉着他的灵，以大能使你们内在的人刚强起来；", version_id: 6},
  {id: 161288, book: "Eph", verse: "3.017", unformatted: "使基督藉着你们的信仰，住在你们心里；使你们在爱中扎根，根基稳固，", version_id: 6},
  {id: 161289, book: "Eph", verse: "3.018", unformatted: "好让你们有能力与所有圣徒一起了解基督的爱是多么长、阔、高、深——", version_id: 6},
  {id: 161290, book: "Eph", verse: "3.019", unformatted: "明白那超越了人所能理解的基督之爱，好让你们得以满足，达到神一切的丰盛完美。", version_id: 6},
  {id: 161291, book: "Eph", verse: "3.02", unformatted: "神能照着在我们里面做工的大能，成就那远超过我们所求所想的一切事——", version_id: 6},
  {id: 161292, book: "Eph", verse: "3.021", unformatted: "愿荣耀在教会里、在基督耶稣里归于神，直到世世代代，永永远远！阿们。", version_id: 6},
  {id: 161293, book: "Eph", verse: "4.001", unformatted: "在基督身体里的关系\n所以，我这个在主里的囚犯劝你们：行事为人要配得上你们所蒙的召唤，", version_id: 6},
  {id: 161294, book: "Eph", verse: "4.002", unformatted: "要以完全的谦卑和温柔，以耐心，在爱中彼此容忍；", version_id: 6},
  {id: 161295, book: "Eph", verse: "4.003", unformatted: "以和平的联结，努力保持属圣灵的合一：", version_id: 6},
  {id: 161296, book: "Eph", verse: "4.004", unformatted: "一个身体、一位圣灵，就像你们蒙召时也被召入一个盼望，", version_id: 6},
  {id: 161297, book: "Eph", verse: "4.005", unformatted: "一位主、一个信仰、一个洗礼、", version_id: 6},
  {id: 161298, book: "Eph", verse: "4.006", unformatted: "一位神——就是万有之父，他超越万有，贯通万有，在万有之中。", version_id: 6},
  {id: 161299, book: "Eph", verse: "4.007", unformatted: "然而，我们每一个人都按照基督恩赐的尺度被赐予了恩典。", version_id: 6},
  {id: 161300, book: "Eph", verse: "4.008", unformatted: "所以经上说：\n他升上高天的时候，掳掠了俘虏，\n把各样的恩惠赐给了人。", version_id: 6},
  {id: 161301, book: "Eph", verse: "4.009", unformatted: "那么“他升上”这话是什么意思呢？难道不也是说他曾降到地的低层吗？", version_id: 6},
  {id: 161302, book: "Eph", verse: "4.01", unformatted: "降下来的那一位，也就是为要充满万有而升到诸天之上的那一位。", version_id: 6},
  {id: 161303, book: "Eph", verse: "4.011", unformatted: "他赐下一些人做使徒，一些人做先知传道，一些人做传福音者，一些人做牧人或教师，", version_id: 6},
  {id: 161304, book: "Eph", verse: "4.012", unformatted: "为了要装备圣徒去做服事的工作，以建立基督的身体；", version_id: 6},
  {id: 161305, book: "Eph", verse: "4.013", unformatted: "直到我们大家达到在信仰上以及对神儿子真正认识上的合一，达到成熟人的地步，达到基督那丰盛完美的身量。", version_id: 6},
  {id: 161306, book: "Eph", verse: "4.014", unformatted: "这样，我们就不再是小孩子，在人的狡诈和诡计中陷入那迷惑人的骗局，被各种教义之风摇动，飘来飘去。", version_id: 6},
  {id: 161307, book: "Eph", verse: "4.015", unformatted: "我们却要在爱中说真话，在一切事上向着他长进；他就是头，是基督。", version_id: 6},
  {id: 161308, book: "Eph", verse: "4.016", unformatted: "本于他，全身藉着每一个关节的支持，照着每一个部分适当的功用，互相连接，结合在一起，使身体渐渐成长，以致在爱中建立自己。", version_id: 6},
  {id: 161309, book: "Eph", verse: "4.017", unformatted: "新人新生活\n所以，我这样说，并且在主里忠告：你们行事不要再像外邦人那样，在自己虚妄的理性中行事；", version_id: 6},
  {id: 161310, book: "Eph", verse: "4.018", unformatted: "他们的意念变为黑暗，因着那在自己里面的愚昧无知和心里的刚硬，就与神的生命隔绝了；", version_id: 6},
  {id: 161311, book: "Eph", verse: "4.019", unformatted: "他们麻木不仁，任凭自己好色，以致贪婪地行出各样污秽的事。", version_id: 6},
  {id: 161312, book: "Eph", verse: "4.02", unformatted: "但你们学了基督，就不是这样——", version_id: 6},
  {id: 161313, book: "Eph", verse: "4.021", unformatted: "如果你们真的听从了他，又照着在耶稣里的真理，在他里面受了教导，", version_id: 6},
  {id: 161314, book: "Eph", verse: "4.022", unformatted: "为了你们脱去在过去行为上的旧人。这旧人顺着迷惑人的私欲受败坏，", version_id: 6},
  {id: 161315, book: "Eph", verse: "4.023", unformatted: "而你们在自己的心灵里得以更新，", version_id: 6},
  {id: 161316, book: "Eph", verse: "4.024", unformatted: "并且穿上新人；这新人是照着神的形像，在真理的公义和圣洁中被造成的。", version_id: 6},
  {id: 161317, book: "Eph", verse: "4.025", unformatted: "所以，你们既然脱去虚假，每个人就要与自己邻人说真话，因为我们都是身体的一部分，彼此相属。", version_id: 6},
  {id: 161318, book: "Eph", verse: "4.026", unformatted: "你们发怒却不可继续犯罪，不可生气到日落，", version_id: 6},
  {id: 161319, book: "Eph", verse: "4.027", unformatted: "也不可给魔鬼留地步。", version_id: 6},
  {id: 161320, book: "Eph", verse: "4.028", unformatted: "偷窃的，不可再偷，反而要劳苦做工，亲手做有益处的事，好使自己能有所得，分给有需要的人。", version_id: 6},
  {id: 161321, book: "Eph", verse: "4.029", unformatted: "任何坏话都不可出口，而是按着需要说造就人的好话，使听的人得到益处。", version_id: 6},
  {id: 161322, book: "Eph", verse: "4.03", unformatted: "不要让神的圣灵忧伤；你们蒙了他的印记，直到得赎的日子。", version_id: 6},
  {id: 161323, book: "Eph", verse: "4.031", unformatted: "你们要除掉一切苦毒、暴怒、愤怒、喧嚷、毁谤，以及一切的恶毒；", version_id: 6},
  {id: 161324, book: "Eph", verse: "4.032", unformatted: "要以仁慈彼此相待，心存怜悯，互相饶恕，就像神在基督里饶恕了你们那样。", version_id: 6},
  {id: 161325, book: "Eph", verse: "5.001", unformatted: "所以，做为蒙爱的儿女，你们要效法神；", version_id: 6},
  {id: 161326, book: "Eph", verse: "5.002", unformatted: "要在爱中行事，就像基督也爱了我们，为我们舍弃了自己，做供物和祭物献给神，成为馨香的气味。", version_id: 6},
  {id: 161327, book: "Eph", verse: "5.003", unformatted: "至于淫乱和一切污秽，或是贪心，在你们中间连提都不可提，这样才与圣徒相宜。", version_id: 6},
  {id: 161328, book: "Eph", verse: "5.004", unformatted: "污秽的言行、愚妄的话语、下流的笑话，这些都不合宜；反而倒要说感谢的话。", version_id: 6},
  {id: 161329, book: "Eph", verse: "5.005", unformatted: "实际上你们应当清楚知道这一点：所有淫乱的、污秽的，或贪心的——就是拜偶像的——在基督和神的国度里都得不到继业。", version_id: 6},
  {id: 161330, book: "Eph", verse: "5.006", unformatted: "光明与黑暗\n不要让任何人用虚空的话把你们欺骗了；正是为了这些，神的震怒就临到那些不信从的儿女身上。", version_id: 6},
  {id: 161331, book: "Eph", verse: "5.007", unformatted: "所以，不要与他们同伙。", version_id: 6},
  {id: 161332, book: "Eph", verse: "5.008", unformatted: "你们从前原是黑暗，但如今在主里就是光；你们要像属于光的儿女行走，", version_id: 6},
  {id: 161333, book: "Eph", verse: "5.009", unformatted: "原来属于光的果子，就是一切良善、公义、真理；", version_id: 6},
  {id: 161334, book: "Eph", verse: "5.01", unformatted: "要分辨什么是主所喜悦的事。", version_id: 6},
  {id: 161335, book: "Eph", verse: "5.011", unformatted: "你们不要参与那些属黑暗结不出果子的事，反而要把那些事揭露出来，", version_id: 6},
  {id: 161336, book: "Eph", verse: "5.012", unformatted: "因为他们暗中所做的，连说出来也是可耻的。", version_id: 6},
  {id: 161337, book: "Eph", verse: "5.013", unformatted: "然而，一切被光所揭露的事，都要成为明显的，", version_id: 6},
  {id: 161338, book: "Eph", verse: "5.014", unformatted: "因为使一切成为明显的，就是光。所以有话说：\n“醒过来！睡着的人哪，从死人中起来吧！\n基督就要光照你了！”", version_id: 6},
  {id: 161339, book: "Eph", verse: "5.015", unformatted: "基督徒生活的一致性\n所以，你们要仔细察看怎样行事为人，不要像没有智慧的人，而要像有智慧的人；", version_id: 6},
  {id: 161340, book: "Eph", verse: "5.016", unformatted: "要把握机会，因为这时代是邪恶的。", version_id: 6},
  {id: 161341, book: "Eph", verse: "5.017", unformatted: "为此，不要做愚妄的人，而要领悟主的旨意是什么。", version_id: 6},
  {id: 161342, book: "Eph", verse: "5.018", unformatted: "不要醉酒，醉酒带来放荡，而要被圣灵充满：", version_id: 6},
  {id: 161343, book: "Eph", verse: "5.019", unformatted: "用诗篇、圣诗、属灵的诗歌彼此对说，\n从你们的心里，歌颂赞美主；", version_id: 6},
  {id: 161344, book: "Eph", verse: "5.02", unformatted: "奉我们主耶稣基督的名，\n为一切常常感谢父神；", version_id: 6},
  {id: 161345, book: "Eph", verse: "5.021", unformatted: "怀着敬畏基督的心，彼此服从。", version_id: 6},
  {id: 161346, book: "Eph", verse: "5.022", unformatted: "妻子与丈夫\n你们做妻子的，要服从自己的丈夫，如同服从主，", version_id: 6},
  {id: 161347, book: "Eph", verse: "5.023", unformatted: "因为丈夫是妻子的头，正如基督是教会的头；他也是这身体的救主。", version_id: 6},
  {id: 161348, book: "Eph", verse: "5.024", unformatted: "而且教会怎样服从基督，妻子也照样要在一切事上服从丈夫。", version_id: 6},
  {id: 161349, book: "Eph", verse: "5.025", unformatted: "你们做丈夫的，要爱自己的妻子，就像基督也爱了教会，为她舍弃了自己。", version_id: 6},
  {id: 161350, book: "Eph", verse: "5.026", unformatted: "基督这么做，是要藉着话语，用水的洗涤而洁净教会，使她分别为圣，", version_id: 6},
  {id: 161351, book: "Eph", verse: "5.027", unformatted: "为要给自己预备一个荣耀的教会，没有污渍、皱纹或任何这类的事，却要她成为圣洁、毫无瑕疵。", version_id: 6},
  {id: 161352, book: "Eph", verse: "5.028", unformatted: "照样，丈夫也应该爱自己的妻子，就像爱自己的身体一样；那爱自己妻子的，就是爱自己。", version_id: 6},
  {id: 161353, book: "Eph", verse: "5.029", unformatted: "从来没有人恨恶自己的身体，总是保养、顾惜它，就像基督对待教会那样，", version_id: 6},
  {id: 161354, book: "Eph", verse: "5.03", unformatted: "因为我们是基督身体的各个部分。", version_id: 6},
  {id: 161355, book: "Eph", verse: "5.031", unformatted: "“为这缘故，人将离开父母，\n与妻子结合，\n两个人就成为一体。”", version_id: 6},
  {id: 161356, book: "Eph", verse: "5.032", unformatted: "这是极大的奥秘，但我是指着基督和教会说的。", version_id: 6},
  {id: 161357, book: "Eph", verse: "5.033", unformatted: "不过，你们每一个人也要爱自己的妻子，就像爱自己一样；而妻子要敬重丈夫。", version_id: 6},
  {id: 161358, book: "Eph", verse: "6.001", unformatted: "儿女与父母\n你们做儿女的，要在主里顺从自己的父母，因为这是理所当然的。", version_id: 6},
  {id: 161359, book: "Eph", verse: "6.002", unformatted: "“要孝敬父母，使你得福，在地上长寿。”这是第一条带着应许的诫命。", version_id: 6},
  {id: 161360, book: "Eph", verse: "6.004", unformatted: "你们做父亲的，不要惹自己儿女生气，而要照着主的训练和警戒养育他们。", version_id: 6},
  {id: 161361, book: "Eph", verse: "6.005", unformatted: "奴仆与主人\n你们做奴仆的，要怀着敬畏和战兢，以忠实的心顺从自己在世上的主人，就像听从基督一样。", version_id: 6},
  {id: 161362, book: "Eph", verse: "6.006", unformatted: "不要只在人眼前服事，像是讨人的欢心，而要像基督的奴仆，从心里遵行神的旨意。", version_id: 6},
  {id: 161363, book: "Eph", verse: "6.007", unformatted: "要甘心乐意地服事，如同服事主，不是服事人，", version_id: 6},
  {id: 161364, book: "Eph", verse: "6.008", unformatted: "因为你们知道：每个人，无论是奴隶或是自由人，如果做了什么美善的事，就将从主那里领受这事的回报。", version_id: 6},
  {id: 161365, book: "Eph", verse: "6.009", unformatted: "你们做主人的，对奴仆也要这样做，不要威吓他们，因为你们知道，他们和你们同样有一位主人在天上，而且他是不偏待人的。", version_id: 6},
  {id: 161366, book: "Eph", verse: "6.01", unformatted: "属灵争战\n最后，你们要在主里藉着他力量的权能得以刚强。", version_id: 6},
  {id: 161367, book: "Eph", verse: "6.011", unformatted: "你们要配上神的全副武装，使你们得以站立，对付魔鬼的骗局，", version_id: 6},
  {id: 161368, book: "Eph", verse: "6.012", unformatted: "因为我们不是与血肉之体搏斗，而是与那些统治的、掌权的、管辖这黑暗世界的，以及天上邪恶的属灵势力搏斗。", version_id: 6},
  {id: 161369, book: "Eph", verse: "6.013", unformatted: "为此，要拿起神的全副武装，好使你们在邪恶的日子能够抵挡，并且在完成一切以后，还站立得住。", version_id: 6},
  {id: 161370, book: "Eph", verse: "6.014", unformatted: "所以，你们要站立得住：\n用真理束上腰，穿上公义的护胸甲；", version_id: 6},
  {id: 161371, book: "Eph", verse: "6.015", unformatted: "把所预备好的和平福音，穿在脚上；", version_id: 6},
  {id: 161372, book: "Eph", verse: "6.016", unformatted: "在任何情况下，要拿起信仰的盾牌；\n你们藉着它就能消灭那恶者一切燃烧的飞箭；", version_id: 6},
  {id: 161373, book: "Eph", verse: "6.017", unformatted: "并且要接受救恩的头盔和圣灵的宝剑——就是神的话语。", version_id: 6},
  {id: 161374, book: "Eph", verse: "6.018", unformatted: "你们要以各样的祷告和祈求，藉着圣灵时刻祈祷；而且为此警醒，以极大的忍耐，为所有圣徒祈求，", version_id: 6},
  {id: 161375, book: "Eph", verse: "6.019", unformatted: "也为我祈求，好使我在开口的时候被赐予话语，能坦然无惧地让人明白福音的奥秘——", version_id: 6},
  {id: 161376, book: "Eph", verse: "6.02", unformatted: "为此，我成了带锁链的特使。请为我祈求，使我能照着应该说的，放胆传道。", version_id: 6},
  {id: 161377, book: "Eph", verse: "6.021", unformatted: "信末的问候\n在主里蒙爱的弟兄、忠心的仆人特其库斯，会让你们明白一切，好让你们也知道有关我的事和我现在所做的事。", version_id: 6},
  {id: 161378, book: "Eph", verse: "6.022", unformatted: "我正是为此派他到你们那里去，好让你们知道有关我们的事，并且让他安慰你们的心。", version_id: 6},
  {id: 161379, book: "Eph", verse: "6.023", unformatted: "愿平安以及随着信仰的爱，从父神和主耶稣基督临到弟兄们！", version_id: 6},
  {id: 161380, book: "Eph", verse: "6.024", unformatted: "愿恩典与所有爱我们主耶稣基督的人同在——直到永远！", version_id: 6},
  {id: 161381, book: "Phil", verse: "1.001", unformatted: "问安\n基督耶稣的奴仆保罗和提摩太，\n致住在腓立比，在基督耶稣里的所有圣徒，包括各位监督和执事：", version_id: 6},
  {id: 161382, book: "Phil", verse: "1.002", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 161383, book: "Phil", verse: "1.003", unformatted: "感恩与祷告\n每当想到你们，我就感谢我的神；", version_id: 6},
  {id: 161384, book: "Phil", verse: "1.004", unformatted: "每次为你们大家祈祷的时候，我总是怀着喜乐的心祈祷，", version_id: 6},
  {id: 161385, book: "Phil", verse: "1.005", unformatted: "因你们从第一天到如今，一直在福音工作上相契合。", version_id: 6},
  {id: 161386, book: "Phil", verse: "1.006", unformatted: "我正是深信这一点：就是在你们里面开始了美善工作的那一位，将会完成这工作，直到基督耶稣的日子。", version_id: 6},
  {id: 161387, book: "Phil", verse: "1.007", unformatted: "我理当这样思念你们大家，因为你们常把我放在心上；无论我是在捆锁当中，还是在为福音辩护或做见证的时候，你们都是在恩典上与我一同有份。", version_id: 6},
  {id: 161388, book: "Phil", verse: "1.008", unformatted: "实际上，神可以为我见证：我是怎样以基督耶稣的情感，切切地想念你们大家的。", version_id: 6},
  {id: 161389, book: "Phil", verse: "1.009", unformatted: "我是这样祷告的：愿你们的爱心能在真正的知识和一切分辨力上，越来越丰足有余，", version_id: 6},
  {id: 161390, book: "Phil", verse: "1.01", unformatted: "使你们能分辨是非，好在基督的日子里是纯洁、无可指责的，", version_id: 6},
  {id: 161391, book: "Phil", verse: "1.011", unformatted: "并且充满了那藉着耶稣基督而来的义行果子，归于神的荣耀和称赞。", version_id: 6},
  {id: 161392, book: "Phil", verse: "1.012", unformatted: "福音得以广传\n弟兄们，我愿你们知道，我的遭遇反而促进了福音的进展，", version_id: 6},
  {id: 161393, book: "Phil", verse: "1.013", unformatted: "以致整个王宫卫队和所有其余的人，都清楚我是为基督的缘故被捆锁的；", version_id: 6},
  {id: 161394, book: "Phil", verse: "1.014", unformatted: "并且大多数的弟兄因着我所受的捆锁，就在主里深信不疑、无所惧怕，更加大胆地传讲这话语。", version_id: 6},
  {id: 161395, book: "Phil", verse: "1.015", unformatted: "有些人传扬基督固然是出于嫉妒和纷争，但也有些人是出于好意。", version_id: 6},
  {id: 161396, book: "Phil", verse: "1.016", unformatted: "后者是出于爱，知道我是为辩护福音而受委派的；", version_id: 6},
  {id: 161397, book: "Phil", verse: "1.017", unformatted: "前者传讲基督是出于争竞，不是出于真诚，想要在我的捆锁中，增加我的患难。", version_id: 6},
  {id: 161398, book: "Phil", verse: "1.018", unformatted: "这到底有什么关系呢？只是在各种方法中，无论是假意还是真心，基督都被传开了；我为此就欢喜，并且还要欢喜，", version_id: 6},
  {id: 161399, book: "Phil", verse: "1.019", unformatted: "因为我知道，藉着你们的祈祷和耶稣基督之灵的帮助，事情将为我转变以致我获救。", version_id: 6},
  {id: 161400, book: "Phil", verse: "1.02", unformatted: "我的热切盼望和期待是：我在任何事上都将不至于蒙羞；而现在也能像往常一样，无论藉着生，还是藉着死，总要满有胆量地让基督在我身上被尊为大。", version_id: 6},
  {id: 161401, book: "Phil", verse: "1.021", unformatted: "活着就是基督\n因为对我来说，活着就是基督，死去就有益处。", version_id: 6},
  {id: 161402, book: "Phil", verse: "1.022", unformatted: "但如果我继续活在世上，这就使我有工作的成果，于是我不明白该怎样选择了。", version_id: 6},
  {id: 161403, book: "Phil", verse: "1.023", unformatted: "我正处在两难之间：我渴望离世与基督在一起，因为这是好得无比的；", version_id: 6},
  {id: 161404, book: "Phil", verse: "1.024", unformatted: "可是为了你们的缘故，我更需要留在世上。", version_id: 6},
  {id: 161405, book: "Phil", verse: "1.025", unformatted: "既然我深信这一点，就知道我会活下去，并且将继续和你们大家在一起，好使你们在信仰上有长进、有喜乐。", version_id: 6},
  {id: 161406, book: "Phil", verse: "1.026", unformatted: "以致因着我再次到你们那里去，你们在基督耶稣里，对我的夸耀能充实满溢。", version_id: 6},
  {id: 161407, book: "Phil", verse: "1.027", unformatted: "只是你们行事为人要配得上基督的福音，好让我无论是来见你们，还是不在你们那里的时候，都可以听到有关你们的事，就是你们怀有一个心志站立得稳，同心合意地为福音的信仰一起奋战；", version_id: 6},
  {id: 161408, book: "Phil", verse: "1.028", unformatted: "在任何事上都不被反对你们的人吓倒——这对他们是灭亡的证明，而对你们却是得救的证明，并且这是出于神。", version_id: 6},
  {id: 161409, book: "Phil", verse: "1.029", unformatted: "原来你们为基督所蒙的恩是：不仅要信基督，而且还要为他受苦，", version_id: 6},
  {id: 161410, book: "Phil", verse: "1.03", unformatted: "去经历你们在我身上所看到、现在又听到的那同样的争战。", version_id: 6},
  {id: 161411, book: "Phil", verse: "2.001", unformatted: "基督徒的谦卑\n所以，你们如果在基督里有任何鼓励、任何爱的安慰、任何圣灵里的契合、任何情感和怜悯，", version_id: 6},
  {id: 161412, book: "Phil", verse: "2.002", unformatted: "你们就要同心合意——爱心一致、灵里一致、意念一致，好使我的喜乐得以满足。", version_id: 6},
  {id: 161413, book: "Phil", verse: "2.003", unformatted: "做任何事都不要出于争竞，也不可出于虚荣，而要以谦卑的心，各人看别人比自己强；", version_id: 6},
  {id: 161414, book: "Phil", verse: "2.004", unformatted: "每个人不要只注重自己的事，也要注重别人的事。", version_id: 6},
  {id: 161415, book: "Phil", verse: "2.005", unformatted: "基督的榜样\n你们应当有这样的意念，这也是基督耶稣的意念。", version_id: 6},
  {id: 161416, book: "Phil", verse: "2.006", unformatted: "虽然他就是神本体的存在，\n却不把与神同等看做是一件要强抓不放的事，", version_id: 6},
  {id: 161417, book: "Phil", verse: "2.007", unformatted: "反而倒空自己，\n取了奴仆的形像，成为人的样式，\n以人的形态出现，", version_id: 6},
  {id: 161418, book: "Phil", verse: "2.008", unformatted: "降卑自己，顺从至死，\n甚至死在十字架上！", version_id: 6},
  {id: 161419, book: "Phil", verse: "2.009", unformatted: "因此神使他升为至高，\n赐给他那超越万名之上的名，", version_id: 6},
  {id: 161420, book: "Phil", verse: "2.01", unformatted: "好使天上、地上和地底下的一切，\n因耶稣的名都屈膝，", version_id: 6},
  {id: 161421, book: "Phil", verse: "2.011", unformatted: "万口为了父神的荣耀\n要承认耶稣基督是主。", version_id: 6},
  {id: 161422, book: "Phil", verse: "2.012", unformatted: "做世界的光\n因此，我亲爱的各位，既然你们一直是顺从的——不仅我在的时候如此，现今我不在的时候更加顺从——就当怀着敬畏和战兢的心，把你们的救恩行出来，", version_id: 6},
  {id: 161423, book: "Phil", verse: "2.013", unformatted: "因为是神在你们里面运作，使你们愿意并且能行出他的美意。", version_id: 6},
  {id: 161424, book: "Phil", verse: "2.014", unformatted: "做任何事都不要抱怨或争论，", version_id: 6},
  {id: 161425, book: "Phil", verse: "2.015", unformatted: "好使你们成为无可指责、纯洁无伪的人，在这扭曲、败坏的世代中，做神毫无瑕疵的儿女；在这世代中，要像星星的光辉一样照耀在世上。", version_id: 6},
  {id: 161426, book: "Phil", verse: "2.016", unformatted: "你们要持守生命的话语，好让我在基督的日子里可以夸耀我所奔跑的没有落空，我所劳苦的也没有落空。", version_id: 6},
  {id: 161427, book: "Phil", verse: "2.017", unformatted: "然而，即使我被做为奠祭，浇在你们信仰的祭物和服事上，我也欢喜，并且和你们大家一同欢喜；", version_id: 6},
  {id: 161428, book: "Phil", verse: "2.018", unformatted: "你们也应该照样欢喜，并且与我一同欢喜。", version_id: 6},
  {id: 161429, book: "Phil", verse: "2.019", unformatted: "提摩太与以帕弗迪托\n我在主耶稣里，希望不久能派提摩太到你们那里去，好让我也知道有关你们的事，可以心情愉快。", version_id: 6},
  {id: 161430, book: "Phil", verse: "2.02", unformatted: "实际上，我没有别的人与我同心，真正地挂虑你们的事；", version_id: 6},
  {id: 161431, book: "Phil", verse: "2.021", unformatted: "因为大家都寻求自己的事，不寻求耶稣基督的事。", version_id: 6},
  {id: 161432, book: "Phil", verse: "2.022", unformatted: "但是，你们是知道提摩太那经过考验的品德。因为他在福音工作上与我一起服事，就像儿子与父亲那样。", version_id: 6},
  {id: 161433, book: "Phil", verse: "2.023", unformatted: "所以我希望，一旦知道我的事有什么结局，就立即派他去。", version_id: 6},
  {id: 161434, book: "Phil", verse: "2.024", unformatted: "不过我在主里深信，我自己不久也会去的。", version_id: 6},
  {id: 161435, book: "Phil", verse: "2.025", unformatted: "然而，我认为必须派以帕弗迪托到你们那里去；他是我的弟兄、同工、战友，也是你们的使者，是为我的需要而来的服事者，", version_id: 6},
  {id: 161436, book: "Phil", verse: "2.026", unformatted: "因为他一直切切地想念你们大家，并且因你们听说他患了病，他就极其难过。", version_id: 6},
  {id: 161437, book: "Phil", verse: "2.027", unformatted: "事实上，他虽然患了病几乎死去，可是神怜悯了他；不仅怜悯他，也怜悯了我，免得我忧上加忧。", version_id: 6},
  {id: 161438, book: "Phil", verse: "2.028", unformatted: "所以，我就更迫切地派他去，好让你们见到他，能够再次欢喜，而我也可以少些挂虑。", version_id: 6},
  {id: 161439, book: "Phil", verse: "2.029", unformatted: "因此，你们要在主里满怀喜乐地欢迎他，也要敬重这样的人，", version_id: 6},
  {id: 161440, book: "Phil", verse: "2.03", unformatted: "因为他为了基督的工作，几乎至死，冒了生命的危险，为要弥补你们服事我的缺乏。", version_id: 6},
  {id: 161441, book: "Phil", verse: "3.001", unformatted: "认识基督\n此外，我的弟兄们，你们要在主里欢喜。\n我把同样的话写给你们，一方面对我不是麻烦，另一方面对你们是一种保护。", version_id: 6},
  {id: 161442, book: "Phil", verse: "3.002", unformatted: "你们要当心那些犬类，当心那些作恶的工人，当心那些妄割身体的人。", version_id: 6},
  {id: 161443, book: "Phil", verse: "3.003", unformatted: "实际上，我们才是受过割礼的人，就是藉着神的灵事奉，在基督耶稣里夸耀，不依靠肉体的人。", version_id: 6},
  {id: 161444, book: "Phil", verse: "3.004", unformatted: "其实我也可以依靠肉体；如果另外有人自以为可以依靠肉体，我就更可以了。", version_id: 6},
  {id: 161445, book: "Phil", verse: "3.005", unformatted: "我出生第八天受了割礼；我属于以色列族、便雅悯支派，是希伯来人所生的希伯来人；按照律法说，我是法利赛人；", version_id: 6},
  {id: 161446, book: "Phil", verse: "3.006", unformatted: "按照热心说，我曾经逼迫教会；按照律法上的义说，我是无可指责的。", version_id: 6},
  {id: 161447, book: "Phil", verse: "3.007", unformatted: "然而，以前对我有益的事，现在因基督的缘故，我已经把这些事看做是损失。", version_id: 6},
  {id: 161448, book: "Phil", verse: "3.008", unformatted: "不但如此，我甚至把一切都看做是损失，因为我把认识我主基督耶稣看为是至高无上的。我为基督耶稣损失了一切，而且把一切都看做是粪土，为要赢得基督，", version_id: 6},
  {id: 161449, book: "Phil", verse: "3.009", unformatted: "并且要被认定在他里面——不是拥有那本于律法的自己的义，而是拥有那藉着信基督而来的义，就是来自神、基于信的义；", version_id: 6},
  {id: 161450, book: "Phil", verse: "3.01", unformatted: "并且为要认识基督和他复活的大能，要在他的苦难里相契合，效法他的死，", version_id: 6},
  {id: 161451, book: "Phil", verse: "3.011", unformatted: "或许我能得以从死人中复活。", version_id: 6},
  {id: 161452, book: "Phil", verse: "3.012", unformatted: "向着目标直跑\n这并不是说我已经得到了，或已经得以完全了，而是说我在竭力追求，或许我真的可以得着；为此我也被基督耶稣得着了。", version_id: 6},
  {id: 161453, book: "Phil", verse: "3.013", unformatted: "弟兄们，我不认为自己已经得着了；但是我有一件事：就是忘记背后，争取前面的，", version_id: 6},
  {id: 161454, book: "Phil", verse: "3.014", unformatted: "向着目标竭力追求，为了得到那在基督耶稣里、神至高召唤的奖赏。", version_id: 6},
  {id: 161455, book: "Phil", verse: "3.015", unformatted: "因此，让我们所有成熟的人都这样思想。如果你们有什么不同的思想，神也会把这事启示给你们。", version_id: 6},
  {id: 161456, book: "Phil", verse: "3.016", unformatted: "不过我们达到了什么标准，就让我们照着什么标准行走。", version_id: 6},
  {id: 161457, book: "Phil", verse: "3.017", unformatted: "弟兄们，你们要一同效法我，也要留心看那些像你们一样，以我们为榜样行事的人。", version_id: 6},
  {id: 161458, book: "Phil", verse: "3.018", unformatted: "其实我过去再三地告诉你们，而现在又流着眼泪告诉你们：有许多人的行事为人，是基督十字架的仇敌。", version_id: 6},
  {id: 161459, book: "Phil", verse: "3.019", unformatted: "他们的结局就是灭亡；他们的神就是自己的私欲；他们的荣耀在自己的羞耻之中；他们所思想的都是地上的事。", version_id: 6},
  {id: 161460, book: "Phil", verse: "3.02", unformatted: "然而，我们的国籍在天上；我们也热切等待着救主——主耶稣基督从那里降临。", version_id: 6},
  {id: 161461, book: "Phil", verse: "3.021", unformatted: "他将照着那能使万有都服从他的力量，改变我们这卑贱的身体，要与他那荣耀的身体相似。", version_id: 6},
  {id: 161462, book: "Phil", verse: "4.001", unformatted: "实践性的劝导\n因此，我所爱的、所想念的弟兄们，我的喜乐、我的冠冕哪！各位蒙爱的人，你们当如此在主里站立得稳！", version_id: 6},
  {id: 161463, book: "Phil", verse: "4.002", unformatted: "我恳求犹迪娅，也恳求辛图柯，要在主里同心合意。", version_id: 6},
  {id: 161464, book: "Phil", verse: "4.003", unformatted: "是的，我真诚的同伴哪！我也请求你帮助她们两位。她们在福音工作上曾经与我、克莱门斯，和我的其他同工们一起奋战，这些人的名字都在生命册上。", version_id: 6},
  {id: 161465, book: "Phil", verse: "4.004", unformatted: "你们要在主里常常欢喜。我再说，你们要欢喜！", version_id: 6},
  {id: 161466, book: "Phil", verse: "4.005", unformatted: "要让所有人知道你们的谦和。主快到了！", version_id: 6},
  {id: 161467, book: "Phil", verse: "4.006", unformatted: "在任何事上都不要忧虑；然而要在一切事上，藉着祷告和祈求，以感谢的心把你们所求的告诉神。", version_id: 6},
  {id: 161468, book: "Phil", verse: "4.007", unformatted: "这样，神的平安——那高过一切理性的平安，就会在基督耶稣里保守你们的心、你们的意念。", version_id: 6},
  {id: 161469, book: "Phil", verse: "4.008", unformatted: "最后，弟兄们，凡是真实的、庄重的、公义的、纯洁的、可羡慕的、高尚的，如果有什么美德，如果有什么可称赞的，这些事你们就当思想；", version_id: 6},
  {id: 161470, book: "Phil", verse: "4.009", unformatted: "凡是你们在我身上所学到的、所领受的、所听到的、所看到的，这些事你们就当实行。这样，赐平安的神就会与你们同在。", version_id: 6},
  {id: 161471, book: "Phil", verse: "4.01", unformatted: "为馈赠感恩\n我在主里极其欢喜，因为现在你们终于又萌发了思念我的心。其实你们一直在思念我，只是没有机会表达。", version_id: 6},
  {id: 161472, book: "Phil", verse: "4.011", unformatted: "我这样说，并不是因为我缺少什么；事实上，我已经学会了在任何情况下都知足。", version_id: 6},
  {id: 161473, book: "Phil", verse: "4.012", unformatted: "我知道怎样处卑贱，也知道怎样处丰富；在一切事上，在一切情况下，或饱足、或饥饿，或丰富、或缺乏，我都得了秘诀。", version_id: 6},
  {id: 161474, book: "Phil", verse: "4.013", unformatted: "藉着使我刚强的那一位，我一切都能。", version_id: 6},
  {id: 161475, book: "Phil", verse: "4.014", unformatted: "不过你们分担了我的患难，做得很好。", version_id: 6},
  {id: 161476, book: "Phil", verse: "4.015", unformatted: "腓立比人哪，你们也知道，在福音工作的开始，我离开马其顿省的时候，在供给和领受的事上，除了你们以外，没有别的教会与我分享。", version_id: 6},
  {id: 161477, book: "Phil", verse: "4.016", unformatted: "就是我在帖撒罗尼迦的时候，你们甚至也一再地把我所需要的送来。", version_id: 6},
  {id: 161478, book: "Phil", verse: "4.017", unformatted: "我并不是求什么供应，而是求你们的果子增多，归在你们的账上。", version_id: 6},
  {id: 161479, book: "Phil", verse: "4.018", unformatted: "如今我一切都得到了，并且丰足有余。我已经得以满足了，因为我从以帕弗迪托接受了来自你们的东西；那是馨香的气味，是神所悦纳、所喜悦的祭物。", version_id: 6},
  {id: 161480, book: "Phil", verse: "4.019", unformatted: "我的神将按照他荣耀的丰盛，在基督耶稣里满足你们一切的需要。", version_id: 6},
  {id: 161481, book: "Phil", verse: "4.02", unformatted: "愿荣耀归于神我们的父，直到永永远远！阿们。", version_id: 6},
  {id: 161482, book: "Phil", verse: "4.021", unformatted: "结尾的祝福\n请问候在基督耶稣里的每一位圣徒。与我在一起的弟兄们都问候你们；", version_id: 6},
  {id: 161483, book: "Phil", verse: "4.022", unformatted: "所有的圣徒，尤其是来自凯撒宫里的人，也都问候你们。", version_id: 6},
  {id: 161484, book: "Phil", verse: "4.023", unformatted: "愿主耶稣基督的恩典与你们的灵同在！", version_id: 6},
  {id: 161485, book: "Col", verse: "1.001", unformatted: "问安\n照着神的旨意做基督耶稣使徒的保罗，以及弟兄提摩太，", version_id: 6},
  {id: 161486, book: "Col", verse: "1.002", unformatted: "致歌罗西的圣徒们，就是在基督里忠心的弟兄：\n愿恩典与平安从神我们的父临到你们！", version_id: 6},
  {id: 161487, book: "Col", verse: "1.003", unformatted: "感恩\n我们为你们祷告的时候，总是感谢神我们主耶稣基督的父，", version_id: 6},
  {id: 161488, book: "Col", verse: "1.004", unformatted: "因为我们听说了你们在基督耶稣里的信仰，以及对所有圣徒所怀的爱——", version_id: 6},
  {id: 161489, book: "Col", verse: "1.005", unformatted: "这信心和爱是藉着那为你们存留在天上的盼望，这盼望是你们在传到你们那里的福音真理之话语中所听过的，", version_id: 6},
  {id: 161490, book: "Col", verse: "1.006", unformatted: "正如它在全世界，不断地结果子和扩展；自从你们在真理中听见并了解神恩典的那一天起，在你们中间也像这一样，", version_id: 6},
  {id: 161491, book: "Col", verse: "1.007", unformatted: "正如你们从蒙爱的人，就是与我们同做奴仆的以帕弗拉那里所学到的；他为你们做了基督忠心的仆人，", version_id: 6},
  {id: 161492, book: "Col", verse: "1.008", unformatted: "也把你们在圣灵里的爱告诉了我们。", version_id: 6},
  {id: 161493, book: "Col", verse: "1.009", unformatted: "为属灵增长祈求\n为此，我们自从听到这事的那一天起，就不断地为你们祷告和祈求：愿你们藉着一切属灵的智慧和悟性，能被神旨意的真正知识所充满；", version_id: 6},
  {id: 161494, book: "Col", verse: "1.01", unformatted: "好让你们行事为人配得上主，凡事蒙他喜悦，在一切美善的工作中结出果子，在神的真正知识上不断长进。", version_id: 6},
  {id: 161495, book: "Col", verse: "1.011", unformatted: "愿你们按照他荣耀的权能，藉着一切能力得以刚强，好让你们凡事忍耐宽容，怀着喜乐的心", version_id: 6},
  {id: 161496, book: "Col", verse: "1.012", unformatted: "感谢父——他使你们能够分享圣徒们在光明中的继业；", version_id: 6},
  {id: 161497, book: "Col", verse: "1.013", unformatted: "他救我们脱离了黑暗的权势，把我们迁入他爱子的国度；", version_id: 6},
  {id: 161498, book: "Col", verse: "1.014", unformatted: "在他里面，我们得蒙救赎，罪得赦免。", version_id: 6},
  {id: 161499, book: "Col", verse: "1.015", unformatted: "基督为中心\n爱子是那不可见之神的形像，\n是一切被造之物的长子，", version_id: 6},
  {id: 161500, book: "Col", verse: "1.016", unformatted: "因为万有都是藉着他造的：\n天上和地上的，看得见和看不见的，\n无论王位或主权，统治的或掌权的，\n一切都是藉着他造的，也是为他而造的，", version_id: 6},
  {id: 161501, book: "Col", verse: "1.017", unformatted: "他在万有之先，万有也藉着他而存在，", version_id: 6},
  {id: 161502, book: "Col", verse: "1.018", unformatted: "他是身体的头，就是教会的头，\n他是元始，是从死人中首先复活的，\n为要使他能在万有中居首位，", version_id: 6},
  {id: 161503, book: "Col", verse: "1.019", unformatted: "因为父乐意使一切的丰盛完美都住在他里面，", version_id: 6},
  {id: 161504, book: "Col", verse: "1.02", unformatted: "又藉着他在十字架上所流的血成就了和平，\n就是藉着他使万有——无论地上的或天上的——\n都与他自己和好了。", version_id: 6},
  {id: 161505, book: "Col", verse: "1.021", unformatted: "从前，你们在邪恶的行为中与神隔绝，在意念中与他为敌；", version_id: 6},
  {id: 161506, book: "Col", verse: "1.022", unformatted: "但如今，神在基督的血肉之躯上，藉着死使你们与他自己和好了，并且使你们站在他面前，是圣洁、毫无瑕疵、无可指责的。", version_id: 6},
  {id: 161507, book: "Col", verse: "1.023", unformatted: "可是你们要持守信仰，立定根基，坚定不移，不要从你们所听到的福音盼望上被挪去。这福音是传给天下万人的，我保罗也成了这福音的仆人。", version_id: 6},
  {id: 161508, book: "Col", verse: "1.024", unformatted: "保罗的服事\n如今我为你们受苦难，感到欢喜；并且我为基督的身体，就是教会，要在我的肉体上补满基督患难的缺欠。", version_id: 6},
  {id: 161509, book: "Col", verse: "1.025", unformatted: "神为了你们的缘故赐给了我任务，我按照这任务成为教会的仆人，为要使神的话语完全传开。", version_id: 6},
  {id: 161510, book: "Col", verse: "1.026", unformatted: "这话语就是历世历代被隐藏的奥秘，但如今已经显明给他的圣徒们。", version_id: 6},
  {id: 161511, book: "Col", verse: "1.027", unformatted: "神愿意在外邦人中，让他们明白什么是这奥秘的荣耀的丰盛；那就是：基督在你们里面，他是荣耀的盼望！", version_id: 6},
  {id: 161512, book: "Col", verse: "1.028", unformatted: "我们传扬基督，用一切的智慧劝诫所有人，教导所有人，好让我们使所有的人在基督里成熟，能够献给神。", version_id: 6},
  {id: 161513, book: "Col", verse: "1.029", unformatted: "我也为此劳苦，藉着他在我里面的大能作为而争战。", version_id: 6},
  {id: 161514, book: "Col", verse: "2.001", unformatted: "我希望你们知道，我为你们和那些在劳迪西亚的人，以及一切没有亲自看到我面的人，有多么大的争战，", version_id: 6},
  {id: 161515, book: "Col", verse: "2.002", unformatted: "好让他们的心受到安慰，在爱中结合在一起，以致得到由悟性的确信不疑而来的一切丰盛，直到真正认识神的奥秘，就是基督——", version_id: 6},
  {id: 161516, book: "Col", verse: "2.003", unformatted: "在他里面隐藏着一切智慧和知识的财宝。", version_id: 6},
  {id: 161517, book: "Col", verse: "2.004", unformatted: "提防异端\n我说这话，是免得有人用花言巧语欺骗你们。", version_id: 6},
  {id: 161518, book: "Col", verse: "2.005", unformatted: "因为，我虽然身体不在你们那里，心灵却与你们同在，而且看见你们有秩序，对基督的信仰也坚定不移，我就感到快乐。", version_id: 6},
  {id: 161519, book: "Col", verse: "2.006", unformatted: "所以，你们怎样接受了主基督耶稣，就应当怎样在他里面行事，", version_id: 6},
  {id: 161520, book: "Col", verse: "2.007", unformatted: "照着你们所受的教导，在他里面得以扎根、建造，在信仰上得以确立，在感谢中丰足有余。", version_id: 6},
  {id: 161521, book: "Col", verse: "2.008", unformatted: "你们要当心，免得有人用哲学和虚空的妄言把你们掳去；那些是出于人的传统，出于世界的原则，而不是出于基督。", version_id: 6},
  {id: 161522, book: "Col", verse: "2.009", unformatted: "这是因为神本性的一切丰盛完美都有形有体地居住在基督里面；", version_id: 6},
  {id: 161523, book: "Col", verse: "2.01", unformatted: "你们在他里面已经得以满足，他是一切统治者和掌权者的元首；", version_id: 6},
  {id: 161524, book: "Col", verse: "2.011", unformatted: "在他里面，你们也受了割礼，不是藉着人的手，而是藉着脱去那血肉之躯，藉着基督的割礼。", version_id: 6},
  {id: 161525, book: "Col", verse: "2.012", unformatted: "你们既然在洗礼中与基督一同被埋葬，也就在洗礼中与基督一同复活了，这是藉着相信使基督从死人中复活之神的作为。", version_id: 6},
  {id: 161526, book: "Col", verse: "2.013", unformatted: "你们从前在自己的过犯和没有受割礼的肉体中是死的，神却使你们与基督一同活过来了。神已经赦免了我们的一切过犯，", version_id: 6},
  {id: 161527, book: "Col", verse: "2.014", unformatted: "抹去了在敌对我们的那些规条中反对我们的债务记录，并且把它从中除掉，钉在十字架上。", version_id: 6},
  {id: 161528, book: "Col", verse: "2.015", unformatted: "神既然剥夺了那些统治者和掌权者的权势，就公开羞辱他们；藉着基督向他们夸胜了。", version_id: 6},
  {id: 161529, book: "Col", verse: "2.016", unformatted: "所以，在吃喝的事上，或在有关节日、月朔或安息日的事上，不要让人评断你们。", version_id: 6},
  {id: 161530, book: "Col", verse: "2.017", unformatted: "这些不过是将来之事的影子，而实体却属于基督。", version_id: 6},
  {id: 161531, book: "Col", verse: "2.018", unformatted: "不要让任何人藉着故意谦卑和敬拜天使来夺去你们的奖赏；他们沉迷在自己所看到的幻象中，在自己肉体的理性之下，毫无理由地自我膨胀，", version_id: 6},
  {id: 161532, book: "Col", verse: "2.019", unformatted: "不与元首紧密相连。本于他，全身得到供应，藉着关节和筋络结合在一起，在属神的成长中长大。", version_id: 6},
  {id: 161533, book: "Col", verse: "2.02", unformatted: "你们如果已经和基督一同死了，脱离了世界的原则，为什么还像活在这世俗中一样，拘守那些", version_id: 6},
  {id: 161534, book: "Col", verse: "2.021", unformatted: "“不可碰，不可尝，不可摸”之类的条例呢？", version_id: 6},
  {id: 161535, book: "Col", verse: "2.022", unformatted: "这些东西都是一经使用就要衰朽的，是出于人的规条和教义；", version_id: 6},
  {id: 161536, book: "Col", verse: "2.023", unformatted: "它们虽然藉着自造宗教、谦卑低微、苦待身体，外表有智慧，但在对付放任肉体的事上，没有任何价值。", version_id: 6},
  {id: 161537, book: "Col", verse: "3.001", unformatted: "新人新生命\n所以，你们如果已经与基督一同复活了，就应当寻求上面的事；在那里，基督坐在神的右边。", version_id: 6},
  {id: 161538, book: "Col", verse: "3.002", unformatted: "你们要思想上面的事，不要思想地上的事，", version_id: 6},
  {id: 161539, book: "Col", verse: "3.003", unformatted: "因为你们已经死了，你们的生命与基督一同藏在神里面。", version_id: 6},
  {id: 161540, book: "Col", verse: "3.004", unformatted: "基督是你们的生命，当他显现的时候，你们那时也就和他一同在荣耀里显现。", version_id: 6},
  {id: 161541, book: "Col", verse: "3.005", unformatted: "所以，当把你们属世的各个部分看为死了的，就如淫乱、污秽、邪情、恶欲和贪心，贪心就是拜偶像。", version_id: 6},
  {id: 161542, book: "Col", verse: "3.006", unformatted: "为了这些事，神的震怒就临到那些不信从的儿女身上；", version_id: 6},
  {id: 161543, book: "Col", verse: "3.007", unformatted: "当你们从前生活在这些事中的时候，也在其中生活。", version_id: 6},
  {id: 161544, book: "Col", verse: "3.008", unformatted: "但如今，你们也要脱去一切暴怒、愤恨、恶毒、毁谤，以及从你们口中所出的污秽的话；", version_id: 6},
  {id: 161545, book: "Col", verse: "3.009", unformatted: "不要彼此说谎，因你们已经脱去了旧人和旧人的所作所为，", version_id: 6},
  {id: 161546, book: "Col", verse: "3.01", unformatted: "并且穿上了新人。这新人照着创造他那一位的形像不断地被更新，以致真正认识他。", version_id: 6},
  {id: 161547, book: "Col", verse: "3.011", unformatted: "在这里没有希腊人和犹太人，受割礼的和不受割礼的，外族人、塞西亚人、奴隶、自由人；而基督就是一切，也在一切之中。", version_id: 6},
  {id: 161548, book: "Col", verse: "3.012", unformatted: "基督徒的生活\n因此，你们身为蒙神拣选、圣洁和蒙爱的人，就应当穿上怜悯的心肠，以及仁慈、谦卑、温柔、耐心。", version_id: 6},
  {id: 161549, book: "Col", verse: "3.013", unformatted: "如果一个人对另一个人怀有不满，总要彼此容忍，互相饶恕。正如主饶恕了你们，你们也当如此。", version_id: 6},
  {id: 161550, book: "Col", verse: "3.014", unformatted: "在这一切之上，还要穿上爱——爱就是成熟境界的联结。", version_id: 6},
  {id: 161551, book: "Col", verse: "3.015", unformatted: "又要让基督的和平在你们心里做主；你们也为此蒙召归于一体。你们要有感谢的心。", version_id: 6},
  {id: 161552, book: "Col", verse: "3.016", unformatted: "你们要让基督的话语丰丰富富地住在你们里面，要用一切的智慧彼此教导、劝诫，怀着感恩的心，以诗篇、圣诗、属灵的诗歌来歌颂神。", version_id: 6},
  {id: 161553, book: "Col", verse: "3.017", unformatted: "你们无论做什么，或在言语上或在行为上，都要奉主耶稣的名，藉着他感谢父神。", version_id: 6},
  {id: 161554, book: "Col", verse: "3.018", unformatted: "基督徒家庭的准则\n你们做妻子的，要服从丈夫，这在主里是合宜的；", version_id: 6},
  {id: 161555, book: "Col", verse: "3.019", unformatted: "你们做丈夫的，要爱妻子，不可对她们怀有苦毒；", version_id: 6},
  {id: 161556, book: "Col", verse: "3.02", unformatted: "你们做儿女的，要凡事顺从父母，因为这在主里是蒙喜悦的；", version_id: 6},
  {id: 161557, book: "Col", verse: "3.021", unformatted: "你们做父亲的，不要惹自己儿女生气，免得他们灰心丧志；", version_id: 6},
  {id: 161558, book: "Col", verse: "3.022", unformatted: "你们做奴仆的，凡事都要顺从自己在世上的主人，不要只在人眼前服事，像是讨人的欢心，而要以忠实的心敬畏主。", version_id: 6},
  {id: 161559, book: "Col", verse: "3.023", unformatted: "你们无论做什么，都要从心里去做，像是为主做的，不是为人做的；", version_id: 6},
  {id: 161560, book: "Col", verse: "3.024", unformatted: "因为你们知道，你们将要从主那里得到继业为报偿；你们当服事主基督。", version_id: 6},
  {id: 161561, book: "Col", verse: "3.025", unformatted: "要知道，那行不义的，将要照着他所行的不义来领受回报；主是不偏待人的。", version_id: 6},
  {id: 161562, book: "Col", verse: "4.001", unformatted: "你们做主人的，要公正、公平地对待奴仆，因为你们知道，你们也有一位主人在天上。", version_id: 6},
  {id: 161563, book: "Col", verse: "4.002", unformatted: "信末的教导\n你们要恒切祷告，祷告中要保持警醒，怀着感谢的心。", version_id: 6},
  {id: 161564, book: "Col", verse: "4.003", unformatted: "同时也要为我们祷告：愿神给我们开福音的门，使我们能传讲基督的奥秘——我也是为此被捆锁的，", version_id: 6},
  {id: 161565, book: "Col", verse: "4.004", unformatted: "愿我能照着所当说的，把这奥秘显明出来。", version_id: 6},
  {id: 161566, book: "Col", verse: "4.005", unformatted: "你们要把握机会，对外面的人靠智慧行事。", version_id: 6},
  {id: 161567, book: "Col", verse: "4.006", unformatted: "你们的话语总要带着恩惠，像用盐调和过的，好使你们知道该怎样回答每一个人。", version_id: 6},
  {id: 161568, book: "Col", verse: "4.007", unformatted: "最后的问安\n有关我的一切事，特其库斯会让你们明白。他是蒙爱的弟兄、忠心的仆人，是在主里同做奴仆的。", version_id: 6},
  {id: 161569, book: "Col", verse: "4.008", unformatted: "我正是为此派他到你们那里去，好让你们知道有关我们的事，并且让他安慰你们的心。", version_id: 6},
  {id: 161570, book: "Col", verse: "4.009", unformatted: "与他一起去的奥尼斯莫，是一位忠心、蒙爱的弟兄，是你们那里的人。他们会把这里的一切情况都让你们明白。", version_id: 6},
  {id: 161571, book: "Col", verse: "4.01", unformatted: "与我一起坐牢的亚里达古问候你们。巴拿巴的表弟马可也问候你们。关于马可，你们已经领受了指示：如果他到你们那里去，你们要接受他。", version_id: 6},
  {id: 161572, book: "Col", verse: "4.011", unformatted: "称为犹斯托的耶数也问候你们。在割礼派的人中，只有这几个人是为了神的国与我同工的；他们也成了我的安慰。", version_id: 6},
  {id: 161573, book: "Col", verse: "4.012", unformatted: "以帕弗拉问候你们；他来自你们那里，是基督耶稣的奴仆；他在祷告中，总是为你们竭力祈求：愿你们成熟、站立得住，在神的一切旨意中确信不疑；", version_id: 6},
  {id: 161574, book: "Col", verse: "4.013", unformatted: "其实我可以为他见证：他为了你们和那些在劳迪西亚和希拉波利斯的人，多受劳苦。", version_id: 6},
  {id: 161575, book: "Col", verse: "4.014", unformatted: "蒙爱的医生路加和迪马斯问候你们。", version_id: 6},
  {id: 161576, book: "Col", verse: "4.015", unformatted: "请问候在劳迪西亚的弟兄们，还有宁法以及在她家里的教会。", version_id: 6},
  {id: 161577, book: "Col", verse: "4.016", unformatted: "这封书信在你们那里宣读了以后，也一定要在劳迪西亚的教会中宣读；你们也要宣读从劳迪西亚转来的书信。", version_id: 6},
  {id: 161578, book: "Col", verse: "4.017", unformatted: "请你们告诉阿其波斯：“在主里所领受的服事工作，要留心把它完成。”", version_id: 6},
  {id: 161579, book: "Col", verse: "4.018", unformatted: "我保罗亲笔问候：请你们记住我所受的捆锁。愿恩典与你们同在！", version_id: 6},
  {id: 161580, book: "1Thess", verse: "1.001", unformatted: "问安\n保罗、赛拉斯、提摩太，\n致那在父神和主耶稣基督里的帖撒罗尼迦人的教会：\n愿恩典与平安临到你们！", version_id: 6},
  {id: 161581, book: "1Thess", verse: "1.002", unformatted: "感恩\n我们为你们大家常常感谢神，在祷告中提到你们；", version_id: 6},
  {id: 161582, book: "1Thess", verse: "1.003", unformatted: "在神我们的父面前，不住地记念你们在我们主耶稣基督里的信仰的行为、爱心的劳苦，以及对盼望的忍耐。", version_id: 6},
  {id: 161583, book: "1Thess", verse: "1.004", unformatted: "蒙神所爱的弟兄们，我们知道你们是蒙拣选的，", version_id: 6},
  {id: 161584, book: "1Thess", verse: "1.005", unformatted: "因为我们的福音传到你们那里，不仅是藉着话语，也是藉着能力、圣灵和充分的确信。你们知道，为了你们的缘故，我们在你们中间是怎样为人的。", version_id: 6},
  {id: 161585, book: "1Thess", verse: "1.006", unformatted: "你们效法了我们，也效法了主，在大患难中，怀着圣灵所赐的喜乐接受了这话语。", version_id: 6},
  {id: 161586, book: "1Thess", verse: "1.007", unformatted: "因此，你们成了马其顿和亚该亚省所有信徒的榜样。", version_id: 6},
  {id: 161587, book: "1Thess", verse: "1.008", unformatted: "事实上，主的福音已经从你们那里传扬出去，不仅在马其顿和亚该亚省，也在你们对神的信仰所传开的各个地方；所以我们不需要再说什么了。", version_id: 6},
  {id: 161588, book: "1Thess", verse: "1.009", unformatted: "其实他们自己在宣扬有关我们的事，说我们是怎样进到你们那里，你们是怎样离弃偶像归向神、服事又活又真的神，", version_id: 6},
  {id: 161589, book: "1Thess", verse: "1.01", unformatted: "并且等候他那从天降临的儿子，就是神使他从死人中复活的耶稣——他拯救我们脱离那将要来临的震怒。", version_id: 6},
  {id: 161590, book: "1Thess", verse: "2.001", unformatted: "保罗的服事\n弟兄们，你们自己知道，我们进到你们那里并不是空无果效的；", version_id: 6},
  {id: 161591, book: "1Thess", verse: "2.002", unformatted: "相反，正如你们知道，虽然我们以前在腓立比受了苦、受了凌辱，但是靠着我们的神，我们还是放胆直言，在大争战中把神的福音传给了你们。", version_id: 6},
  {id: 161592, book: "1Thess", verse: "2.003", unformatted: "我们的劝勉不是出于迷惑，不是出于污秽，也不是用诡诈；", version_id: 6},
  {id: 161593, book: "1Thess", verse: "2.004", unformatted: "相反，我们怎样被神考验而受了福音的委托，我们就照样传讲；不是要讨人的喜悦，而是要讨神的喜悦——神是考验我们内心的。", version_id: 6},
  {id: 161594, book: "1Thess", verse: "2.005", unformatted: "实际上，我们从来没有用过谄媚的话，这是你们知道的；也没有怀着贪婪的居心，这是神可以见证的。", version_id: 6},
  {id: 161595, book: "1Thess", verse: "2.006", unformatted: "无论向你们或向其他人，我们也都没有寻求从人来的荣耀。", version_id: 6},
  {id: 161596, book: "1Thess", verse: "2.007", unformatted: "我们身为基督的使徒，虽然可以加给你们重担，但是我们在你们当中成了温柔的，就像哺乳的母亲顾惜自己的儿女那样。", version_id: 6},
  {id: 161597, book: "1Thess", verse: "2.008", unformatted: "我们这样眷爱你们，不仅乐意把神的福音分享给你们，连自己的生命也乐意分享给你们，因为你们成了我们所爱的人。", version_id: 6},
  {id: 161598, book: "1Thess", verse: "2.009", unformatted: "弟兄们，你们记得我们的辛苦和劳碌：我们把神的福音传给你们的时候，我们日夜做工，免得成为你们任何人的负担。", version_id: 6},
  {id: 161599, book: "1Thess", verse: "2.01", unformatted: "你们和神可以见证：我们对你们这些信的人是多么圣洁、公义、无可指责的。", version_id: 6},
  {id: 161600, book: "1Thess", verse: "2.011", unformatted: "正如你们知道我们怎样待你们每一个人，就像父亲待自己的儿女那样；", version_id: 6},
  {id: 161601, book: "1Thess", verse: "2.012", unformatted: "我们鼓励你们，安慰你们，忠告你们，为要你们行事为人配得上那召唤你们进入他国度和荣耀的神。", version_id: 6},
  {id: 161602, book: "1Thess", verse: "2.013", unformatted: "两种态度\n我们之所以也不住地感谢神，是因为你们接受了从我们所听的神的话语，没有把它看做人的话语，而是把它看做神的话语接受了；这确实是神的话语，而且它也正在你们这些信的人里面做工。", version_id: 6},
  {id: 161603, book: "1Thess", verse: "2.014", unformatted: "弟兄们，你们效法了犹太地区在基督耶稣里属神的各教会，因为你们也受了自己同胞的苦害，就像他们受了犹太人的苦害那样。", version_id: 6},
  {id: 161604, book: "1Thess", verse: "2.015", unformatted: "这些犹太人不但杀了主耶稣和先知们，也驱逐了我们，并且不讨神的喜悦，又与所有的人作对，", version_id: 6},
  {id: 161605, book: "1Thess", verse: "2.016", unformatted: "阻止我们向外邦人传道使外邦人得救。这样，他们常常积满自己的罪孽，而神的震怒在最后临到了他们身上！", version_id: 6},
  {id: 161606, book: "1Thess", verse: "2.017", unformatted: "保罗的渴望\n弟兄们，我们被迫暂时与你们分离——身体分离，心却没有；我们非常渴望并且更加努力要见你们的面，", version_id: 6},
  {id: 161607, book: "1Thess", verse: "2.018", unformatted: "所以我们想要到你们那里去。我保罗确实一再地要去，只是撒旦拦阻了我们。", version_id: 6},
  {id: 161608, book: "1Thess", verse: "2.019", unformatted: "我们主耶稣来临的时候，在主面前，到底谁是我们的盼望、喜乐和所夸耀的冠冕呢？难道不也是你们吗？", version_id: 6},
  {id: 161609, book: "1Thess", verse: "2.02", unformatted: "的确，你们就是我们的荣耀和喜乐。", version_id: 6},
  {id: 161610, book: "1Thess", verse: "3.001", unformatted: "派遣提摩太\n所以，我们既然不能再等了，就决意单独留在雅典，", version_id: 6},
  {id: 161611, book: "1Thess", verse: "3.002", unformatted: "并派了我们的弟兄——在基督的福音工作上的神的同工提摩太，好在信仰上坚固你们、鼓励你们，", version_id: 6},
  {id: 161612, book: "1Thess", verse: "3.003", unformatted: "免得有人在这些患难中动摇。因为你们自己知道，我们要受患难是注定的。", version_id: 6},
  {id: 161613, book: "1Thess", verse: "3.004", unformatted: "事实上，我们还在你们那里的时候，早就不断地告诉你们，我们将要受患难，这果然应验了，就像你们所知道的那样。", version_id: 6},
  {id: 161614, book: "1Thess", verse: "3.005", unformatted: "为此，我既然不能再等了，就派提摩太去，要知道你们的信仰怎样，恐怕那试探者诱惑了你们，我们所劳苦的落了空。", version_id: 6},
  {id: 161615, book: "1Thess", verse: "3.006", unformatted: "提摩太带来的安慰\n但是，现在提摩太从你们那里来到我们这里，带来了你们信仰和爱心的好消息，并且说你们总是对我们怀着美好的记忆，切切地想见到我们，就像我们也渴望见到你们那样。", version_id: 6},
  {id: 161616, book: "1Thess", verse: "3.007", unformatted: "所以弟兄们，在我们的一切困苦和患难中，由于你们的缘故，藉着你们的信心，我们就受到安慰，", version_id: 6},
  {id: 161617, book: "1Thess", verse: "3.008", unformatted: "因为，你们如果在主里站立得稳，我们如今就真的活了。", version_id: 6},
  {id: 161618, book: "1Thess", verse: "3.009", unformatted: "我们因着你们，在我们的神面前就喜乐；为这一切的喜乐，我们到底能用什么样的感谢为你们回报神呢？", version_id: 6},
  {id: 161619, book: "1Thess", verse: "3.01", unformatted: "我们日夜迫切祈求，要见你们的面，并且要补全你们在信仰上的缺欠。", version_id: 6},
  {id: 161620, book: "1Thess", verse: "3.011", unformatted: "为教会祈求\n愿神我们的父和我们的主耶稣，亲自引导我们的道路到你们那里去。", version_id: 6},
  {id: 161621, book: "1Thess", verse: "3.012", unformatted: "愿主使你们彼此之间的爱，以及对众人的爱都能增加、充实满溢，就像我们对你们的爱那样；", version_id: 6},
  {id: 161622, book: "1Thess", verse: "3.013", unformatted: "但愿他这样坚固你们的心，使你们在我们主耶稣与他所有的圣徒一起来临的时候，在神我们的父面前，在圣洁中是无可指责的！阿们。", version_id: 6},
  {id: 161623, book: "1Thess", verse: "4.001", unformatted: "做圣洁的人\n此外，弟兄们，我们在主耶稣里请求你们、劝勉你们：既然从我们学到了该怎样行事为人，怎样讨神的喜悦——就像你们也在行的——你们就要更加努力，", version_id: 6},
  {id: 161624, book: "1Thess", verse: "4.002", unformatted: "因为你们知道，我们藉着主耶稣所传给你们的是什么吩咐。", version_id: 6},
  {id: 161625, book: "1Thess", verse: "4.003", unformatted: "神的旨意正是要你们分别为圣：要你们远避淫乱，", version_id: 6},
  {id: 161626, book: "1Thess", verse: "4.004", unformatted: "要你们每个人知道怎样在圣洁、尊贵中持守自己的身体，", version_id: 6},
  {id: 161627, book: "1Thess", verse: "4.005", unformatted: "而不要像那些不认识神的外邦人那样，放纵邪情私欲；", version_id: 6},
  {id: 161628, book: "1Thess", verse: "4.006", unformatted: "不可有人在这事上越轨，对不起他的弟兄，因为关于这一切事，主是报应者，正如我们以前也告诉过、郑重地劝诫过你们。", version_id: 6},
  {id: 161629, book: "1Thess", verse: "4.007", unformatted: "要知道，神召唤我们，不是要我们沾染污秽，而是要我们分别为圣。", version_id: 6},
  {id: 161630, book: "1Thess", verse: "4.008", unformatted: "因此，那拒绝这吩咐的，不是拒绝人，而是拒绝神——就是将他的圣灵赐给你们的那一位。", version_id: 6},
  {id: 161631, book: "1Thess", verse: "4.009", unformatted: "基督徒的行事为人\n关于弟兄之爱，你们不需要我写什么给你们，因为你们自己蒙了神的教导，要彼此相爱。", version_id: 6},
  {id: 161632, book: "1Thess", verse: "4.01", unformatted: "实际上，你们对整个马其顿省的所有弟兄就是这样做的。不过弟兄们，我们劝你们要更加努力。", version_id: 6},
  {id: 161633, book: "1Thess", verse: "4.011", unformatted: "你们要立定心志安静下来、做自己的事、亲手做工，正如我们曾经吩咐过你们的，", version_id: 6},
  {id: 161634, book: "1Thess", verse: "4.012", unformatted: "好使你们在外人面前能够行事端正，不会有什么缺乏。", version_id: 6},
  {id: 161635, book: "1Thess", verse: "4.013", unformatted: "基督再来的盼望\n弟兄们，关于睡了的人，我们不愿意你们不明白，免得你们忧伤，就像其他那些没有盼望的人一样。", version_id: 6},
  {id: 161636, book: "1Thess", verse: "4.014", unformatted: "我们既然相信耶稣死而复活了，照样也相信，那些藉着耶稣已经睡了的人，神将要把他们与耶稣一同带来。", version_id: 6},
  {id: 161637, book: "1Thess", verse: "4.015", unformatted: "的确，我们现在照着主的话告诉你们这一点：我们这些活着存留到主来临的人，绝不会在那些已经睡了的人之先，", version_id: 6},
  {id: 161638, book: "1Thess", verse: "4.016", unformatted: "因为在发令的呼喊声中，在天使长的声音和神的号角声中，主要亲自从天降临，而且那些在基督里死去的人要先复活，", version_id: 6},
  {id: 161639, book: "1Thess", verse: "4.017", unformatted: "然后我们这些还活着存留的人，要与他们一同被提到云里，在空中与主相会。这样，我们就要永远与主在一起了。", version_id: 6},
  {id: 161640, book: "1Thess", verse: "4.018", unformatted: "所以，你们当用这些话彼此鼓励。", version_id: 6},
  {id: 161641, book: "1Thess", verse: "5.001", unformatted: "主的日子\n弟兄们，关于这些时候和日期，你们不需要我写什么给你们了。", version_id: 6},
  {id: 161642, book: "1Thess", verse: "5.002", unformatted: "因为你们自己明确地知道，主的日子来临，就像夜里的贼来临那样。", version_id: 6},
  {id: 161643, book: "1Thess", verse: "5.003", unformatted: "当人们正说“和平”、“安全”的时候，毁灭那时就突然临到他们，就像孕妇的临产阵痛那样，他们绝不能逃脱。", version_id: 6},
  {id: 161644, book: "1Thess", verse: "5.004", unformatted: "不过弟兄们，你们不是在黑暗里，以致那日子像贼一样抓住你们。", version_id: 6},
  {id: 161645, book: "1Thess", verse: "5.005", unformatted: "因为你们都是光明的儿女，白昼的儿女。我们不属于黑夜，也不属于黑暗。", version_id: 6},
  {id: 161646, book: "1Thess", verse: "5.006", unformatted: "由此可见，我们不要像其他人那样睡着了，而要警醒、谨慎。", version_id: 6},
  {id: 161647, book: "1Thess", verse: "5.007", unformatted: "因为睡觉的人是在夜里睡，醉酒的人是在夜里醉。", version_id: 6},
  {id: 161648, book: "1Thess", verse: "5.008", unformatted: "但我们既然属于白昼，就让我们谨慎，以信与爱做为护胸甲穿上，以救恩的盼望做为头盔戴上。", version_id: 6},
  {id: 161649, book: "1Thess", verse: "5.009", unformatted: "因为神不是预定我们到震怒里面，而是预定我们藉着我们的主耶稣基督获得救恩。", version_id: 6},
  {id: 161650, book: "1Thess", verse: "5.01", unformatted: "基督替我们死，为要使我们无论是醒着还是睡着，都与他一同活着。", version_id: 6},
  {id: 161651, book: "1Thess", verse: "5.011", unformatted: "因此，你们要彼此鼓励，互相造就，就像你们现在所做的那样。", version_id: 6},
  {id: 161652, book: "1Thess", verse: "5.012", unformatted: "劝勉与祝福\n弟兄们，我们请求你们：要敬重那些在你们中间劳苦做工、在主里带领你们、劝诫你们的人，", version_id: 6},
  {id: 161653, book: "1Thess", verse: "5.013", unformatted: "因着他们的工作，要在爱中格外地看重他们。你们要彼此和睦。", version_id: 6},
  {id: 161654, book: "1Thess", verse: "5.014", unformatted: "弟兄们，我们劝你们：要劝诫懒散的人，安慰胆怯的人，扶助软弱的人，对所有的人都要恒久忍耐。", version_id: 6},
  {id: 161655, book: "1Thess", verse: "5.015", unformatted: "你们要注意，谁都不要以恶报恶；相反，无论是彼此之间，还是对待众人，总要追求美善。", version_id: 6},
  {id: 161656, book: "1Thess", verse: "5.016", unformatted: "要常常欢喜，", version_id: 6},
  {id: 161657, book: "1Thess", verse: "5.017", unformatted: "不住地祷告，", version_id: 6},
  {id: 161658, book: "1Thess", verse: "5.018", unformatted: "凡事感谢；\n因为这是神在基督耶稣里对你们的旨意。", version_id: 6},
  {id: 161659, book: "1Thess", verse: "5.019", unformatted: "不要消灭圣灵的感动，", version_id: 6},
  {id: 161660, book: "1Thess", verse: "5.02", unformatted: "不要轻视做先知传道，", version_id: 6},
  {id: 161661, book: "1Thess", verse: "5.021", unformatted: "却要凡事分辨，\n要持守美善，", version_id: 6},
  {id: 161662, book: "1Thess", verse: "5.022", unformatted: "连一切恶的样式都要远避。", version_id: 6},
  {id: 161663, book: "1Thess", verse: "5.023", unformatted: "愿赐平安的神亲自使你们完全分别为圣。愿你们的灵、魂、体，在我们的主耶稣基督来临的时候，全然得蒙保守、无可指责。", version_id: 6},
  {id: 161664, book: "1Thess", verse: "5.024", unformatted: "召唤你们的那一位是信实的，他将成就这事。", version_id: 6},
  {id: 161665, book: "1Thess", verse: "5.025", unformatted: "弟兄们，请为我们祷告。", version_id: 6},
  {id: 161666, book: "1Thess", verse: "5.026", unformatted: "你们要用圣洁的亲吻礼问候所有的弟兄。", version_id: 6},
  {id: 161667, book: "1Thess", verse: "5.027", unformatted: "我藉着主吩咐你们，要把这封信读给所有的弟兄听。", version_id: 6},
  {id: 161668, book: "1Thess", verse: "5.028", unformatted: "愿我们主耶稣基督的恩典与你们同在！", version_id: 6},
  {id: 161669, book: "2Thess", verse: "1.001", unformatted: "问安\n保罗、赛拉斯、提摩太，\n致那在神我们的父和主耶稣基督里的帖撒罗尼迦人的教会：", version_id: 6},
  {id: 161670, book: "2Thess", verse: "1.002", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 161671, book: "2Thess", verse: "1.003", unformatted: "主的报偿\n弟兄们，我们应该为你们常常感谢神，这样做是合宜的，因为你们的信仰格外增长，你们大家每一个人彼此的爱也在增加。", version_id: 6},
  {id: 161672, book: "2Thess", verse: "1.004", unformatted: "所以，我们自己在神的各教会中以你们夸耀，因为你们在一切的逼迫和患难中保持着你们的忍耐和信仰。", version_id: 6},
  {id: 161673, book: "2Thess", verse: "1.005", unformatted: "这正是神公义审判的证据，好使你们算是配得上神的国；你们也正是为此而受苦害的。", version_id: 6},
  {id: 161674, book: "2Thess", verse: "1.006", unformatted: "神既然真是公义的，当主耶稣与他有大能的天使们一起从天上显现的时候，他就要以患难回报那些加患难给你们的人，", version_id: 6},
  {id: 161675, book: "2Thess", verse: "1.007", unformatted: "也要使你们这些受患难的人与我们一同得到安息。", version_id: 6},
  {id: 161676, book: "2Thess", verse: "1.008", unformatted: "神要在烈火中报应那些不认识神的人，以及那些不顺从我们主耶稣福音的人；", version_id: 6},
  {id: 161677, book: "2Thess", verse: "1.009", unformatted: "他们将受到刑罚，就是永远的灭亡，从主面前被隔绝，离开他权能的荣耀。", version_id: 6},
  {id: 161678, book: "2Thess", verse: "1.01", unformatted: "在那日，主来临的时候，要藉着他的圣徒得着荣耀，又藉着所有的信徒令人惊奇——因为你们相信了我们对你们所做的见证。", version_id: 6},
  {id: 161679, book: "2Thess", verse: "1.011", unformatted: "为此，我们也常常为你们祷告：愿我们的神把你们看为配得他的召唤，并且以他的大能成全你们一切美好的心愿和一切信仰的行为，", version_id: 6},
  {id: 161680, book: "2Thess", verse: "1.012", unformatted: "好使我们主耶稣的名，照着我们的神和主耶稣基督的恩典，因你们得着荣耀，你们也因他得着荣耀。", version_id: 6},
  {id: 161681, book: "2Thess", verse: "2.001", unformatted: "不法之人显露\n弟兄们，论到我们主耶稣基督的来临，以及我们聚集归于他的事，我们请求你们：", version_id: 6},
  {id: 161682, book: "2Thess", verse: "2.002", unformatted: "无论是藉着灵，还是藉着话语，或藉着一封好像是来自我们的书信，说主的日子已经来到了，你们都不要轻易被摇动而失去理性，也不要惊慌；", version_id: 6},
  {id: 161683, book: "2Thess", verse: "2.003", unformatted: "不要让任何人用任何方法把你们欺骗了；因为那日子来临以前，必定先有离道反教之事来到，并且那不法之人，就是那灭亡之子也会被显露出来。", version_id: 6},
  {id: 161684, book: "2Thess", verse: "2.004", unformatted: "他反对一切被称为神的，或受人敬拜的，他又高抬自己在这一切之上，甚至坐在神的圣所里宣称自己是神。", version_id: 6},
  {id: 161685, book: "2Thess", verse: "2.005", unformatted: "我还在你们那里的时候，一直告诉你们这些事，你们不记得吗？", version_id: 6},
  {id: 161686, book: "2Thess", verse: "2.006", unformatted: "你们也知道，现在什么拦阻了他，好使他在所定的时候被显露出来。", version_id: 6},
  {id: 161687, book: "2Thess", verse: "2.007", unformatted: "其实这不法的奥秘已经在做工，只是要等到那现在拦阻他的不在其中，", version_id: 6},
  {id: 161688, book: "2Thess", verse: "2.008", unformatted: "然后这不法之人要被显露出来。主耶稣要以自己口中的气杀掉他，藉着自己来临的显现除去他。", version_id: 6},
  {id: 161689, book: "2Thess", verse: "2.009", unformatted: "这不法之人的到来，是出于撒旦的作为，对那些正在灭亡的人，使用一切能力、虚假的神迹和奇事，", version_id: 6},
  {id: 161690, book: "2Thess", verse: "2.01", unformatted: "并且使用一切不义的诡计，因为他们不接受那能使他们得救的真理之爱。", version_id: 6},
  {id: 161691, book: "2Thess", verse: "2.011", unformatted: "为此，神就把一种迷惑人的力量送进他们里面，使他们相信虚假之事，", version_id: 6},
  {id: 161692, book: "2Thess", verse: "2.012", unformatted: "好使所有不相信真理而喜悦不义的人，都被定罪。", version_id: 6},
  {id: 161693, book: "2Thess", verse: "2.013", unformatted: "站立稳当\n蒙主所爱的弟兄们，我们应该为你们常常感谢神，因为神为了救恩的缘故，就藉着在圣灵里的分别为圣，以及真理的信仰，拣选了你们做为初熟的果子。", version_id: 6},
  {id: 161694, book: "2Thess", verse: "2.014", unformatted: "为此，神也藉着我们的福音召唤了你们，使你们获得我们主耶稣基督的荣耀。", version_id: 6},
  {id: 161695, book: "2Thess", verse: "2.015", unformatted: "弟兄们，由此可见，你们当站立得稳；你们所领受的传统，无论是藉着我们的话语，还是藉着我们的书信，你们都要持守。", version_id: 6},
  {id: 161696, book: "2Thess", verse: "2.016", unformatted: "愿我们的主耶稣基督自己和神我们的父，就是爱我们又在恩典中把永恒的安慰和美好的盼望赐给我们的那一位，", version_id: 6},
  {id: 161697, book: "2Thess", verse: "2.017", unformatted: "安慰你们的心，并且在一切美善的工作和言语上坚固你们。", version_id: 6},
  {id: 161698, book: "2Thess", verse: "3.001", unformatted: "请求代祷\n最后，弟兄们，请为我们祷告：愿主的福音能迅速传开，并且得着荣耀，就像在你们那里一样；", version_id: 6},
  {id: 161699, book: "2Thess", verse: "3.002", unformatted: "愿神救我们脱离那些无理和邪恶的人，因为不是每个人都有这信仰。", version_id: 6},
  {id: 161700, book: "2Thess", verse: "3.003", unformatted: "不过主是信实的，他会坚固你们、保守你们脱离那恶者。", version_id: 6},
  {id: 161701, book: "2Thess", verse: "3.004", unformatted: "至于你们，我们在主里深信，你们正在遵行，并且也将继续遵行我们所吩咐的。", version_id: 6},
  {id: 161702, book: "2Thess", verse: "3.005", unformatted: "愿主引导你们的心归于神的慈爱，又归于基督的忍耐。", version_id: 6},
  {id: 161703, book: "2Thess", verse: "3.006", unformatted: "劝诫懒惰的人\n弟兄们，我们奉我们主耶稣基督的名吩咐你们，要避开所有过着懒散生活，以及不照着你们从我们领受的传统而生活的弟兄。", version_id: 6},
  {id: 161704, book: "2Thess", verse: "3.007", unformatted: "其实你们自己知道应该怎样效法我们，因为我们在你们那里的时候并没有懒散，", version_id: 6},
  {id: 161705, book: "2Thess", verse: "3.008", unformatted: "也没有白吃过谁的饭。相反，我们辛苦劳碌、日夜做工，免得成为你们任何人的负担。", version_id: 6},
  {id: 161706, book: "2Thess", verse: "3.009", unformatted: "这并不是因为我们没有这权利，而是要亲自给你们做榜样，好让你们效法我们。", version_id: 6},
  {id: 161707, book: "2Thess", verse: "3.01", unformatted: "事实上，我们还在你们那里的时候，一直吩咐你们这一点：如果有人不肯做工，就不应当吃饭。", version_id: 6},
  {id: 161708, book: "2Thess", verse: "3.011", unformatted: "因为我们听说你们中间有些人生活懒散，什么工都不做，却好管闲事。", version_id: 6},
  {id: 161709, book: "2Thess", verse: "3.012", unformatted: "我们在主耶稣基督里吩咐并劝勉这样的人：要安静做工，吃自己的饭。", version_id: 6},
  {id: 161710, book: "2Thess", verse: "3.013", unformatted: "不过弟兄们，你们行美善的事不要丧胆。", version_id: 6},
  {id: 161711, book: "2Thess", verse: "3.014", unformatted: "如果有人不顺从我们这信上的话语，你们就要记住这个人，不可与他交往，好让他感到羞愧；", version_id: 6},
  {id: 161712, book: "2Thess", verse: "3.015", unformatted: "可是不要把他看为敌人，而要劝诫他，如同弟兄那样。", version_id: 6},
  {id: 161713, book: "2Thess", verse: "3.016", unformatted: "结尾的祝福\n愿赐平安的主，常常以各种方法亲自赐给你们平安！愿主与你们大家同在！", version_id: 6},
  {id: 161714, book: "2Thess", verse: "3.017", unformatted: "我保罗亲笔问候，这是我每一封信中的标记，我都是这样写的。", version_id: 6},
  {id: 161715, book: "2Thess", verse: "3.018", unformatted: "愿我们主耶稣基督的恩典与你们大家同在！", version_id: 6},
  {id: 161716, book: "1Tim", verse: "1.001", unformatted: "问安\n照着我们的救主神、我们的盼望基督耶稣的命令，做基督耶稣使徒的保罗，", version_id: 6},
  {id: 161717, book: "1Tim", verse: "1.002", unformatted: "致我在信仰上的真儿子提摩太：\n愿恩典、怜悯、平安从父神和我们的主基督耶稣临到你！", version_id: 6},
  {id: 161718, book: "1Tim", verse: "1.003", unformatted: "错误的教导与滥用律法\n我往马其顿省去的时候，曾经劝你继续留在以弗所，好让你吩咐某些人不可传讲别的教义，", version_id: 6},
  {id: 161719, book: "1Tim", verse: "1.004", unformatted: "也不可关注那些编造的故事和无穷的家谱：这些事只带来疑惑，而无助于神所赐的信仰上的任务。", version_id: 6},
  {id: 161720, book: "1Tim", verse: "1.005", unformatted: "这样吩咐的目的是爱。爱是发自洁净的心、无愧的良心，以及不虚假的信仰。", version_id: 6},
  {id: 161721, book: "1Tim", verse: "1.006", unformatted: "有些人偏离了这些，转向了虚妄的话，", version_id: 6},
  {id: 161722, book: "1Tim", verse: "1.007", unformatted: "想要做律法教师，却不明白自己所讲的，或所强调的是为了什么。", version_id: 6},
  {id: 161723, book: "1Tim", verse: "1.008", unformatted: "我们知道，律法是好的，只要人按着规矩去用它。", version_id: 6},
  {id: 161724, book: "1Tim", verse: "1.009", unformatted: "我们也知道，律法的设立不是为义人，而是为那些不法的和不服的、不敬神的和犯罪的、不圣洁的和亵渎神的、殴打父母的、杀人的、", version_id: 6},
  {id: 161725, book: "1Tim", verse: "1.01", unformatted: "淫乱的、同性恋的、拐卖人口的、说谎的、起假誓的，以及其他任何反对健全教义的人。", version_id: 6},
  {id: 161726, book: "1Tim", verse: "1.011", unformatted: "这教义是照着当受称颂之神的荣耀福音，就是所委托给我的福音。", version_id: 6},
  {id: 161727, book: "1Tim", verse: "1.012", unformatted: "保罗的见证\n我感谢使我刚强的那一位——我们的主基督耶稣，因为他认为我是忠心的，指派我做这服事的工作。", version_id: 6},
  {id: 161728, book: "1Tim", verse: "1.013", unformatted: "虽然我原先是亵渎神的、逼迫人的、侮慢人的，但是我却蒙了怜悯，因为我是在不信、不明白的时候做的；", version_id: 6},
  {id: 161729, book: "1Tim", verse: "1.014", unformatted: "而且我们主的恩典，随着在基督耶稣里的信心和爱心，格外增多。", version_id: 6},
  {id: 161730, book: "1Tim", verse: "1.015", unformatted: "“基督耶稣来到这世界，为要拯救罪人。”这话是信实的，值得完全接受。在罪人中我是首恶，", version_id: 6},
  {id: 161731, book: "1Tim", verse: "1.016", unformatted: "但我之所以蒙了怜悯，是因为基督耶稣要在我这首恶身上显出他一切的耐心，为那些将要信靠他而进入永恒生命的人做榜样。", version_id: 6},
  {id: 161732, book: "1Tim", verse: "1.017", unformatted: "愿尊贵和荣耀归于那万世之王，就是那不朽坏的、不可见的、独一的神，直到永永远远！阿们。", version_id: 6},
  {id: 161733, book: "1Tim", verse: "1.018", unformatted: "属灵的战役\n我儿提摩太啊，我按照以前指着你的那些预言，把这样的吩咐交托给你，为的是要你凭着这些去打那美好的仗，", version_id: 6},
  {id: 161734, book: "1Tim", verse: "1.019", unformatted: "持守信仰和无愧的良心。有些人弃绝了这样的良心，就在信仰上遭到了毁灭，", version_id: 6},
  {id: 161735, book: "1Tim", verse: "1.02", unformatted: "其中有希米奈奥和亚历山大。我已经把他们交给了撒旦，这是为要他们受惩罚不再说亵渎的话。", version_id: 6},
  {id: 161736, book: "1Tim", verse: "2.001", unformatted: "有关祷告的教导\n所以我劝你，首要的是为所有的人，为众君王和所有在上的献上祈求、祷告、代求和感谢，", version_id: 6},
  {id: 161737, book: "1Tim", verse: "2.002", unformatted: "好使我们能完全敬神、庄重地过平静安稳的生活。", version_id: 6},
  {id: 161738, book: "1Tim", verse: "2.003", unformatted: "这在神我们救主的面前是美好的，是蒙悦纳的；", version_id: 6},
  {id: 161739, book: "1Tim", verse: "2.004", unformatted: "他愿意所有的人都得救，来到真理的知识当中。", version_id: 6},
  {id: 161740, book: "1Tim", verse: "2.005", unformatted: "要知道，神只有一位，\n在神和人之间的中保也只有一位，\n就是成为人的基督耶稣；", version_id: 6},
  {id: 161741, book: "1Tim", verse: "2.006", unformatted: "他献上自己，替所有的人做救赎的代价；\n在所定的时候，这事要被见证出来。", version_id: 6},
  {id: 161742, book: "1Tim", verse: "2.007", unformatted: "为此，我受指派做传道者和使徒，在信仰和真理上做外邦人的教师。我说的是真话，没有说谎。", version_id: 6},
  {id: 161743, book: "1Tim", verse: "2.008", unformatted: "有关男女的教导\n所以，我希望在各个地方的男人都举起圣洁的手祷告，不动怒、不争论；", version_id: 6},
  {id: 161744, book: "1Tim", verse: "2.009", unformatted: "照样，我也希望女人衣着端庄，以廉耻和自制来装饰自己；不以编发、金饰、珍珠或极其贵重的衣裳来装饰自己，", version_id: 6},
  {id: 161745, book: "1Tim", verse: "2.01", unformatted: "反而要用美善的行为，与称为敬神的女人相宜。", version_id: 6},
  {id: 161746, book: "1Tim", verse: "2.011", unformatted: "女人要以完全的顺服，安静地领受教导。", version_id: 6},
  {id: 161747, book: "1Tim", verse: "2.012", unformatted: "我不准许女人教导人或管辖男人；相反，女人要安静，", version_id: 6},
  {id: 161748, book: "1Tim", verse: "2.013", unformatted: "因为亚当是先被造的，然后才是夏娃；", version_id: 6},
  {id: 161749, book: "1Tim", verse: "2.014", unformatted: "并且不是亚当受骗，而是那女人受骗、陷在过犯里。", version_id: 6},
  {id: 161750, book: "1Tim", verse: "2.015", unformatted: "不过女人如果在自制中持守信仰、爱心、圣洁，就会在生养儿女中得以保全。", version_id: 6},
  {id: 161751, book: "1Tim", verse: "3.001", unformatted: "做监督的资格\n“如果有人向往监督的职份，他就是在渴望一份美好的工作。”这话是信实的。", version_id: 6},
  {id: 161752, book: "1Tim", verse: "3.002", unformatted: "所以做监督的必须无可指责，只做一个妇人的丈夫，节制、自律、端庄，乐意款待客旅，善于教导，", version_id: 6},
  {id: 161753, book: "1Tim", verse: "3.003", unformatted: "不酗酒、不好斗；反要谦和，不争吵、不贪财，", version_id: 6},
  {id: 161754, book: "1Tim", verse: "3.004", unformatted: "好好带领自己的家，在一切的事上庄重，使儿女顺服。", version_id: 6},
  {id: 161755, book: "1Tim", verse: "3.005", unformatted: "但如果有人不知道怎么带领自己的家，他怎么能照顾神的教会呢？", version_id: 6},
  {id: 161756, book: "1Tim", verse: "3.006", unformatted: "做监督的不可以是新信徒，免得他自以为是，就落在魔鬼所受的惩罚里；", version_id: 6},
  {id: 161757, book: "1Tim", verse: "3.007", unformatted: "同时，做监督的在外人中也必须有好名声，免得受人责骂，落在魔鬼的网罗里。", version_id: 6},
  {id: 161758, book: "1Tim", verse: "3.008", unformatted: "做执事的也一样，必须庄重，不一口两舌，不酗酒，不贪图不正当的利益，", version_id: 6},
  {id: 161759, book: "1Tim", verse: "3.009", unformatted: "以清白的良心持守信仰的奥秘；", version_id: 6},
  {id: 161760, book: "1Tim", verse: "3.01", unformatted: "并且这些人也当先受考验，如果是无可指责的，才可以做执事。", version_id: 6},
  {id: 161761, book: "1Tim", verse: "3.011", unformatted: "照样，他们的妻子也必须庄重，不恶意控告人，有节制，在一切事上都忠心。", version_id: 6},
  {id: 161762, book: "1Tim", verse: "3.012", unformatted: "做执事的只可做一个妇人的丈夫，好好带领儿女和自己的家。", version_id: 6},
  {id: 161763, book: "1Tim", verse: "3.013", unformatted: "因为那好好服事的，就让自己达到美好的境地，并且在基督耶稣里的信仰上大有胆量。", version_id: 6},
  {id: 161764, book: "1Tim", verse: "3.014", unformatted: "敬虔的奥秘\n我把这些事写给你，希望很快到你那里去。", version_id: 6},
  {id: 161765, book: "1Tim", verse: "3.015", unformatted: "就算我耽延了，你也可以知道在神的家里应该怎样行事。这家是永生神的教会，是真理的柱石和根基。", version_id: 6},
  {id: 161766, book: "1Tim", verse: "3.016", unformatted: "正如大家所承认的，敬神的奥秘是极大的：\n神以肉身显现，\n被圣灵证实，\n被天使观看，\n被传于万邦，\n被世人信仰，\n被接在荣耀里。", version_id: 6},
  {id: 161767, book: "1Tim", verse: "4.001", unformatted: "背道的预言\n不过圣灵明确地说，在末后的时代有些人会离开这信仰，听从迷惑人的邪灵和鬼魔的教义。", version_id: 6},
  {id: 161768, book: "1Tim", verse: "4.002", unformatted: "这些教义出于说谎之人的伪善，这些人的良心已经麻木了。", version_id: 6},
  {id: 161769, book: "1Tim", verse: "4.003", unformatted: "他们禁止结婚，要求远避某些食物，就是神所创造的，好让信徒和了解真理的人以感谢的心享用。", version_id: 6},
  {id: 161770, book: "1Tim", verse: "4.004", unformatted: "原来神所造的一切都是美好的；如果以感谢的心领受，就什么也不用丢弃，", version_id: 6},
  {id: 161771, book: "1Tim", verse: "4.005", unformatted: "因为藉着神的话语、藉着代求，这些都被分别为圣了。", version_id: 6},
  {id: 161772, book: "1Tim", verse: "4.006", unformatted: "耶稣基督的好仆人\n如果你把这些事向弟兄们指出来，你就是基督耶稣的好仆人，藉着信仰的话语，以及你所跟从的美善教导得着培养。", version_id: 6},
  {id: 161773, book: "1Tim", verse: "4.007", unformatted: "至于那些不敬神的、像老妇人所编造的故事，你要拒绝。为了敬神，要操练自己，", version_id: 6},
  {id: 161774, book: "1Tim", verse: "4.008", unformatted: "因为\n操练身体，益处还少；\n但是敬神，对一切的事都有益处，\n有今生和来生的应许。", version_id: 6},
  {id: 161775, book: "1Tim", verse: "4.009", unformatted: "这话是信实的，值得完全接受。", version_id: 6},
  {id: 161776, book: "1Tim", verse: "4.01", unformatted: "事实上，我们劳苦、争战正是为此，因为我们把盼望寄托于永生的神——他是所有人的救主，尤其是信徒的救主。", version_id: 6},
  {id: 161777, book: "1Tim", verse: "4.011", unformatted: "有关服事的教导\n你当吩咐并教导这些事。", version_id: 6},
  {id: 161778, book: "1Tim", verse: "4.012", unformatted: "不要让任何人轻看你年少，反要在言语、行为、爱心、信仰、纯洁的事上，都做信徒的榜样。", version_id: 6},
  {id: 161779, book: "1Tim", verse: "4.013", unformatted: "你要专心诵读经文、鼓励和教导人，直到我来。", version_id: 6},
  {id: 161780, book: "1Tim", verse: "4.014", unformatted: "不要忽视在你里面的恩赐；这恩赐是藉着预言，随着长老们的按手赐给你的。", version_id: 6},
  {id: 161781, book: "1Tim", verse: "4.015", unformatted: "你要思想这些事，投身其中，使你的长进在大家面前清清楚楚。", version_id: 6},
  {id: 161782, book: "1Tim", verse: "4.016", unformatted: "你要留意自己和自己的教导，在这些事上要有恒心，因为这样做，不但能救你自己，也能救那些听从你的人。", version_id: 6},
  {id: 161783, book: "1Tim", verse: "5.001", unformatted: "不要严厉责备年长的人，而要劝他像劝父亲那样；对年轻的人要像对兄弟那样；", version_id: 6},
  {id: 161784, book: "1Tim", verse: "5.002", unformatted: "对年长的妇女要像对母亲那样，对年轻的妇女要像对姐妹那样，要怀着完全纯洁的心。", version_id: 6},
  {id: 161785, book: "1Tim", verse: "5.003", unformatted: "帮助寡妇\n要尊重那些真正的寡妇。", version_id: 6},
  {id: 161786, book: "1Tim", verse: "5.004", unformatted: "不过一个寡妇如果有儿女或孙儿女，就应当让他们先学会孝敬自己的家人，报答亲恩，因为这在神面前是蒙悦纳的。", version_id: 6},
  {id: 161787, book: "1Tim", verse: "5.005", unformatted: "那真正的寡妇，无依无靠，已经把盼望寄托于神，日夜不住地祈求祷告；", version_id: 6},
  {id: 161788, book: "1Tim", verse: "5.006", unformatted: "而那享乐度日的寡妇，虽然活着，却是死的。", version_id: 6},
  {id: 161789, book: "1Tim", verse: "5.007", unformatted: "你当吩咐这些事，好使她们无可指责。", version_id: 6},
  {id: 161790, book: "1Tim", verse: "5.008", unformatted: "如果有人不敬重属自己的人，尤其是家里的人，他就否定了信仰，比不信的人更坏。", version_id: 6},
  {id: 161791, book: "1Tim", verse: "5.009", unformatted: "应当算入寡妇之列的，不可小于六十岁；只做过一个男人的妻子；", version_id: 6},
  {id: 161792, book: "1Tim", verse: "5.01", unformatted: "并且必须有美好行为的名声，就如养育孩子、接待客旅、洗圣徒的脚、帮助受患难的人、追随一切美善的工作。", version_id: 6},
  {id: 161793, book: "1Tim", verse: "5.011", unformatted: "至于年轻的寡妇，你要拒绝她们入列；要知道，当她们情欲冲动而违背基督的时候，就想要结婚；", version_id: 6},
  {id: 161794, book: "1Tim", verse: "5.012", unformatted: "她们被定罪，是因为她们弃绝了最初的誓言，", version_id: 6},
  {id: 161795, book: "1Tim", verse: "5.013", unformatted: "同时她们又学会了懒惰，四处串门；不仅懒惰，还好说闲话，好管闲事，说不该说的话。", version_id: 6},
  {id: 161796, book: "1Tim", verse: "5.014", unformatted: "所以，我希望年轻的寡妇结婚，生养儿女，料理家务，不给反对的人辱骂的机会。", version_id: 6},
  {id: 161797, book: "1Tim", verse: "5.015", unformatted: "实际上，已经有一些人转去随从撒旦了。", version_id: 6},
  {id: 161798, book: "1Tim", verse: "5.016", unformatted: "如果一个女信徒，在亲属中有寡妇，她就应该帮助她们，不要让教会承受压力，好使教会能够帮助那些真正的寡妇。", version_id: 6},
  {id: 161799, book: "1Tim", verse: "5.017", unformatted: "尊敬长老\n那些好好带领了信徒的长老，尤其是在传道和教导上劳苦做工的，应该被看为配得加倍的尊重。", version_id: 6},
  {id: 161800, book: "1Tim", verse: "5.018", unformatted: "原来经上说：\n“牛踹谷的时候，不可笼住它的嘴。”\n又说：“工人配得自己的酬报。”", version_id: 6},
  {id: 161801, book: "1Tim", verse: "5.019", unformatted: "除非有两三个见证人，否则你不可受理对长老的控告。", version_id: 6},
  {id: 161802, book: "1Tim", verse: "5.02", unformatted: "对那些继续犯罪的，你要当众责备他们，好使其他的人也有所惧怕。", version_id: 6},
  {id: 161803, book: "1Tim", verse: "5.021", unformatted: "我在神、基督耶稣，和蒙拣选的天使面前郑重地劝诫你：遵守这些话要毫无成见，做任何事都不要偏心。", version_id: 6},
  {id: 161804, book: "1Tim", verse: "5.022", unformatted: "不要轻易给人按手，也不要在别人的罪孽上有份，要保守自己纯洁。", version_id: 6},
  {id: 161805, book: "1Tim", verse: "5.023", unformatted: "由于你胃的问题和常患的那些疾病，你今后不要只喝水，要用一点酒。", version_id: 6},
  {id: 161806, book: "1Tim", verse: "5.024", unformatted: "有些人的罪孽是明显的，要先面对审判；而有些人的罪却是随后跟着的。", version_id: 6},
  {id: 161807, book: "1Tim", verse: "5.025", unformatted: "照样，美好行为也是明显的，即使是那些不明显的，也不能一直隐藏下去。", version_id: 6},
  {id: 161808, book: "1Tim", verse: "6.001", unformatted: "尊敬主人\n凡是负轭做奴仆的，应该看自己的主人配受十分尊重，免得神的名和神的教导受到亵渎。", version_id: 6},
  {id: 161809, book: "1Tim", verse: "6.002", unformatted: "如果奴仆的主人是信徒，不可因为主人是弟兄就轻看他们；反而倒要服事他们，因为从这样的美事而得益处的，是信徒、是蒙爱的人。\n错误的教导与人的贪心\n你要教导、劝勉这些事。", version_id: 6},
  {id: 161810, book: "1Tim", verse: "6.003", unformatted: "如果有人传讲别的教义，不符合我们主耶稣基督的健全话语以及那合乎敬神的教导，", version_id: 6},
  {id: 161811, book: "1Tim", verse: "6.004", unformatted: "那么他就成了自以为是的人，什么都不知道，只对辩论和言词上的争执有病态的嗜好，由此引发嫉妒、纷争、毁谤、恶意的猜疑；", version_id: 6},
  {id: 161812, book: "1Tim", verse: "6.005", unformatted: "他在那些理性败坏、丧失真理、以为敬神是得利之道的人中，引发无休止的争吵。", version_id: 6},
  {id: 161813, book: "1Tim", verse: "6.006", unformatted: "其实敬神加上知足，才是极大的得利之道。", version_id: 6},
  {id: 161814, book: "1Tim", verse: "6.007", unformatted: "实际上，我们没有带什么到世上来，也不能带什么去。", version_id: 6},
  {id: 161815, book: "1Tim", verse: "6.008", unformatted: "不过有了吃的、穿的，我们将以此为满足。", version_id: 6},
  {id: 161816, book: "1Tim", verse: "6.009", unformatted: "至于那些想发财的人就会陷入试探、网罗以及很多无知有害的欲望里；这些欲望使人沉溺在败坏和灭亡中。", version_id: 6},
  {id: 161817, book: "1Tim", verse: "6.01", unformatted: "要知道，贪爱金钱是万恶之根；有些人恋慕金钱，就被迷惑而离开了信仰，用很多苦痛把自己刺透了。", version_id: 6},
  {id: 161818, book: "1Tim", verse: "6.011", unformatted: "打美好的仗\n至于你，哦，属神的人哪，你要逃避这些事！\n你要追求公义、敬神、信仰、\n爱心、忍耐、温柔，", version_id: 6},
  {id: 161819, book: "1Tim", verse: "6.012", unformatted: "要为信仰打那美好的仗，\n要抓住永恒的生命。\n你正是为此蒙召的，\n并且在许多见证人面前\n做了美好的告白。", version_id: 6},
  {id: 161820, book: "1Tim", verse: "6.013", unformatted: "我在那赐生命给万物的神面前，并在那曾经向本丢彼拉多见证了美好告白的基督耶稣面前吩咐你：", version_id: 6},
  {id: 161821, book: "1Tim", verse: "6.014", unformatted: "要毫无玷污、无可指责地遵守这命令，直到我们主耶稣基督的显现。", version_id: 6},
  {id: 161822, book: "1Tim", verse: "6.015", unformatted: "在所定的时候，他将要显明出来\n他就是那当受称颂的、独一的全能者，\n万王之王、万主之主，", version_id: 6},
  {id: 161823, book: "1Tim", verse: "6.016", unformatted: "就是那独一不朽的、\n住在人所不能靠近的光里，\n没有人见过、也没有人能看见的那一位。\n愿尊贵和永恒的权能都归于他！\n阿们。", version_id: 6},
  {id: 161824, book: "1Tim", verse: "6.017", unformatted: "有关富人的教导\n对那些今世富有的人，你当吩咐他们不可高傲自大，也不可把盼望寄托在靠不住的财富上，而要寄托在神身上，神把万物丰丰富富地赐给我们享受。", version_id: 6},
  {id: 161825, book: "1Tim", verse: "6.018", unformatted: "你要吩咐他们常行美善，在美好行为上富足，慷慨施舍，乐意分享；", version_id: 6},
  {id: 161826, book: "1Tim", verse: "6.019", unformatted: "为着那将要来临的，给自己积存美好的基础，使他们抓住那真正的生命。", version_id: 6},
  {id: 161827, book: "1Tim", verse: "6.02", unformatted: "忠于所托\n哦，提摩太呀！你要保守那所受的托付，避开那些不敬神的空谈和假冒知识之名的反对论。", version_id: 6},
  {id: 161828, book: "1Tim", verse: "6.021", unformatted: "有些人自称有这些“知识”，就在信仰上偏离了。\n愿恩典与你们同在！", version_id: 6},
  {id: 161829, book: "2Tim", verse: "1.001", unformatted: "问安\n照着神的旨意，按着在基督耶稣里的生命应许，做基督耶稣使徒的保罗，", version_id: 6},
  {id: 161830, book: "2Tim", verse: "1.002", unformatted: "致亲爱的儿子提摩太：\n愿恩典、怜悯、平安从父神和我们的主基督耶稣临到你！", version_id: 6},
  {id: 161831, book: "2Tim", verse: "1.003", unformatted: "感恩\n我在日夜的祈祷中时常想到你，我感谢神，就是我像先祖那样以清白的良心所事奉的那一位。", version_id: 6},
  {id: 161832, book: "2Tim", verse: "1.004", unformatted: "每当想起你的泪水，我就切切地想去见你，好让我的喜乐得以满足。", version_id: 6},
  {id: 161833, book: "2Tim", verse: "1.005", unformatted: "我想起你里面那不虚假的信仰，这信仰先是在你外祖母罗伊丝和你母亲犹妮丝里面，我深信也在你里面。", version_id: 6},
  {id: 161834, book: "2Tim", verse: "1.006", unformatted: "因这理由，我提醒你：要把神的恩赐再如火挑旺起来；这恩赐是藉着我的按手，在你里面的。", version_id: 6},
  {id: 161835, book: "2Tim", verse: "1.007", unformatted: "实际上，神所赐给我们的，不是胆怯的灵，而是大能、慈爱、自制的灵。", version_id: 6},
  {id: 161836, book: "2Tim", verse: "1.008", unformatted: "不以福音为耻\n所以你不要以有关我们主的见证为耻，也不要以我——这主的囚犯为耻，而要藉着神的大能，为福音与我同受苦难。", version_id: 6},
  {id: 161837, book: "2Tim", verse: "1.009", unformatted: "神拯救了我们，以神圣的呼唤召了我们，\n不是照着我们的行为，而是照着他自己的心意和恩典；\n这恩典是在亘古以前，在基督耶稣里赐给我们的，", version_id: 6},
  {id: 161838, book: "2Tim", verse: "1.01", unformatted: "而现在，藉着我们的救主基督耶稣的显现，已经表明出来；\n基督一方面废除了死亡，另一方面藉着福音彰显了生命和不朽。", version_id: 6},
  {id: 161839, book: "2Tim", verse: "1.011", unformatted: "为了这福音，我受指派做传道者、使徒和教师。", version_id: 6},
  {id: 161840, book: "2Tim", verse: "1.012", unformatted: "因这理由，我也正在经受这些苦；然而我并不以为耻，因为我知道我信的是谁，也深信他能保守我所受的托付，直到那日。", version_id: 6},
  {id: 161841, book: "2Tim", verse: "1.013", unformatted: "忠于信仰\n你要藉着在基督耶稣里的信和爱，把你从我所听到的健全话语，做为规范好好持守。", version_id: 6},
  {id: 161842, book: "2Tim", verse: "1.014", unformatted: "你要藉着住在我们里面的圣灵，保守那所受的美好托付。", version_id: 6},
  {id: 161843, book: "2Tim", verse: "1.015", unformatted: "你知道，所有在亚细亚省的人都背弃了我，其中有弗格洛斯和赫莫杰尼。", version_id: 6},
  {id: 161844, book: "2Tim", verse: "1.016", unformatted: "愿主施怜悯给奥尼斯弗罗的家人，因为他多次使我神清气爽，也没有以我的锁链为耻；", version_id: 6},
  {id: 161845, book: "2Tim", verse: "1.017", unformatted: "相反，他一到罗马，就殷切地寻找我，并且找到了。", version_id: 6},
  {id: 161846, book: "2Tim", verse: "1.018", unformatted: "愿主使他在那日子得到主的怜悯！至于他在以弗所的一切服事工作，你知道得更清楚。", version_id: 6},
  {id: 161847, book: "2Tim", verse: "2.001", unformatted: "在恩典中刚强\n所以我的儿子啊，你要藉着在基督耶稣里的恩典得以刚强。", version_id: 6},
  {id: 161848, book: "2Tim", verse: "2.002", unformatted: "你要把那些在许多见证人面前从我所听见的，交托给那些忠心可靠、又有能力教导别人的人。", version_id: 6},
  {id: 161849, book: "2Tim", verse: "2.003", unformatted: "做为基督耶稣的好士兵，你要与我同受苦难。", version_id: 6},
  {id: 161850, book: "2Tim", verse: "2.004", unformatted: "凡是当兵的，没有一个会让日常事务纠缠自己，这是为要使那招兵的人喜悦。", version_id: 6},
  {id: 161851, book: "2Tim", verse: "2.005", unformatted: "同样，如果有人要参加竞赛，除非按着规矩去竞赛，否则就不能得冠冕。", version_id: 6},
  {id: 161852, book: "2Tim", verse: "2.006", unformatted: "劳苦做工的农夫，应该先享受果实。", version_id: 6},
  {id: 161853, book: "2Tim", verse: "2.007", unformatted: "你要思考我所说的话，主会在一切事上赐给你悟性。", version_id: 6},
  {id: 161854, book: "2Tim", verse: "2.008", unformatted: "你当记住那从死人中复活的耶稣基督，照着我的福音，他出自大卫的后裔。", version_id: 6},
  {id: 161855, book: "2Tim", verse: "2.009", unformatted: "我为这福音受苦，甚至像罪犯一样被捆绑；然而神的话语是不受捆绑的。", version_id: 6},
  {id: 161856, book: "2Tim", verse: "2.01", unformatted: "所以，我为了那些蒙拣选的人忍受一切，好让他们也能得到那在基督耶稣里的救恩和永恒的荣耀。", version_id: 6},
  {id: 161857, book: "2Tim", verse: "2.011", unformatted: "以下的话是信实的：\n我们如果与基督一同死了，就将与他一同活着；", version_id: 6},
  {id: 161858, book: "2Tim", verse: "2.012", unformatted: "我们如果忍耐，就将与他一同做王；\n我们如果不认他，他也将不认我们。", version_id: 6},
  {id: 161859, book: "2Tim", verse: "2.013", unformatted: "我们如果不信实，他仍然是信实的，\n因为他不能否定自己。", version_id: 6},
  {id: 161860, book: "2Tim", verse: "2.014", unformatted: "做无愧的工人\n你要提醒他们这些事，在神面前郑重地劝诫他们：不可在言词上争执，那是毫无益处的，只会败坏听见的人。", version_id: 6},
  {id: 161861, book: "2Tim", verse: "2.015", unformatted: "你要努力地向神显明自己是经过考验的，是无愧的工人，能正确分解真理的话语。", version_id: 6},
  {id: 161862, book: "2Tim", verse: "2.016", unformatted: "至于那些不敬神的空谈，你们要避免，因为说这些话的人将导致更多不敬神的事；", version_id: 6},
  {id: 161863, book: "2Tim", verse: "2.017", unformatted: "他们的话像毒瘤那样将会扩散。这些人中间有希米奈奥和菲利特斯，", version_id: 6},
  {id: 161864, book: "2Tim", verse: "2.018", unformatted: "他们偏离了那有关真理的事，称复活为已经过去的事，结果推翻了一些人的信仰。", version_id: 6},
  {id: 161865, book: "2Tim", verse: "2.019", unformatted: "不过神坚固的根基始终确立，上面有这样的印记：\n“主认识那些属于自己的人”还有\n“所有称呼主名的人都要离开不义”。", version_id: 6},
  {id: 161866, book: "2Tim", verse: "2.02", unformatted: "在大户人家，不仅有金器、银器，也有木器、瓦器；虽然有的为了贵重的用处，但有的却为了卑贱的用处。", version_id: 6},
  {id: 161867, book: "2Tim", verse: "2.021", unformatted: "所以，如果有人洁净自己脱离卑贱的事，他就会成为贵重的器皿，被分别为圣，对主人有用，而且是为着一切美善的工作所预备好的。", version_id: 6},
  {id: 161868, book: "2Tim", verse: "2.022", unformatted: "所以你要逃避年轻人的欲望；要与那些以洁净的心求告主的人一同追求公义、信仰、爱心、和平；", version_id: 6},
  {id: 161869, book: "2Tim", verse: "2.023", unformatted: "要拒绝愚拙无知的辩论，因为你知道这些只会引起争斗，", version_id: 6},
  {id: 161870, book: "2Tim", verse: "2.024", unformatted: "而主的奴仆不应该争斗；相反，要温和地对待所有的人，善于教导，忍受恶待，", version_id: 6},
  {id: 161871, book: "2Tim", verse: "2.025", unformatted: "温柔地管教那些反对的人；或许神会赐给这些人悔改的心，到真理的知识当中。", version_id: 6},
  {id: 161872, book: "2Tim", verse: "2.026", unformatted: "这样，那些被魔鬼掳去、随从魔鬼意思的人，就能醒悟过来，脱离魔鬼的网罗。", version_id: 6},
  {id: 161873, book: "2Tim", verse: "3.001", unformatted: "末后的艰难\n你应当知道这一点：在末后的日子里，将有艰难的时候来到；", version_id: 6},
  {id: 161874, book: "2Tim", verse: "3.002", unformatted: "因为人们将会爱自己、爱钱财，又自夸、又骄傲，亵渎神、悖逆父母、忘恩负义、毫不圣洁、", version_id: 6},
  {id: 161875, book: "2Tim", verse: "3.003", unformatted: "没有亲情、不肯和解、恶意控告、不能自制、残忍凶暴、不爱良善、", version_id: 6},
  {id: 161876, book: "2Tim", verse: "3.004", unformatted: "背叛出卖、任意妄为、自以为是，爱享乐过于爱神，", version_id: 6},
  {id: 161877, book: "2Tim", verse: "3.005", unformatted: "有敬神的外表，却否定敬神的实质；这样的人你要避开。", version_id: 6},
  {id: 161878, book: "2Tim", verse: "3.006", unformatted: "他们当中有些人溜进别人家里，虏获无知的妇女。这些妇女被罪孽压制，被各种欲望牵引，", version_id: 6},
  {id: 161879, book: "2Tim", verse: "3.007", unformatted: "虽然一直学习，却始终不能达到到真理的知识当中。", version_id: 6},
  {id: 161880, book: "2Tim", verse: "3.008", unformatted: "扬尼和扬布列怎样抵挡摩西，这些人也照样抵挡真理；他们理性败坏，在信仰上经不起考验。", version_id: 6},
  {id: 161881, book: "2Tim", verse: "3.009", unformatted: "然而，他们不能再进一步了，因为他们的无知会在众人面前显露出来，就像那两个人一样。", version_id: 6},
  {id: 161882, book: "2Tim", verse: "3.01", unformatted: "圣经都是神所启示的\n至于你，你跟从了我的教导、品行、心意、信仰、宽容、爱心、忍耐，", version_id: 6},
  {id: 161883, book: "2Tim", verse: "3.011", unformatted: "以及我在安提阿、以哥念、路司得所遭受的逼迫和苦难——我忍受了何等的逼迫啊！可是主救我脱离了这一切。", version_id: 6},
  {id: 161884, book: "2Tim", verse: "3.012", unformatted: "其实，所有想要在基督耶稣里过敬神生活的，也都会受到逼迫；", version_id: 6},
  {id: 161885, book: "2Tim", verse: "3.013", unformatted: "而恶人和迷惑人的会越来越坏，他们迷惑人，自己也被迷惑。", version_id: 6},
  {id: 161886, book: "2Tim", verse: "3.014", unformatted: "至于你，你当持守所学到、所确信的，因为你知道是从哪些人学的；", version_id: 6},
  {id: 161887, book: "2Tim", verse: "3.015", unformatted: "并且你从年幼就知道了圣经，这圣经能使你有智慧，藉着在基督耶稣里的信仰，以至于救恩。", version_id: 6},
  {id: 161888, book: "2Tim", verse: "3.016", unformatted: "圣经都是神所默示的，对于教导、责备、规正、在义中的训练都是有益处的，", version_id: 6},
  {id: 161889, book: "2Tim", verse: "3.017", unformatted: "要使属神的人完备，为一切美善的工作，装备齐全。", version_id: 6},
  {id: 161890, book: "2Tim", verse: "4.001", unformatted: "尽力传福音\n我在神、基督耶稣面前，就是将要审判活人、死人的那一位面前，藉着他的显现和他的国度郑重地劝诫你：", version_id: 6},
  {id: 161891, book: "2Tim", verse: "4.002", unformatted: "你要传福音，无论得时不得时，总要坚持；要以极大的耐心和各样的教导责备人、告诫人、鼓励人，", version_id: 6},
  {id: 161892, book: "2Tim", verse: "4.003", unformatted: "因为时候将到，人们容忍不了健全的教义，反要顺着自己的欲望，为自己的耳朵能被呵痒得舒服，去召聚许多教师。", version_id: 6},
  {id: 161893, book: "2Tim", verse: "4.004", unformatted: "他们将掩耳不听真理，却转向编造的故事。", version_id: 6},
  {id: 161894, book: "2Tim", verse: "4.005", unformatted: "至于你，要在一切事上谨慎，忍受苦难，做传福音者的工作，完成你的服事。", version_id: 6},
  {id: 161895, book: "2Tim", verse: "4.006", unformatted: "事实上，我已经在被浇奠了；我离开世界的时候已经临到了。", version_id: 6},
  {id: 161896, book: "2Tim", verse: "4.007", unformatted: "那美好的仗，我已经打过了；该跑的路程，我已经跑尽了；当守的信仰，我已经持守了。", version_id: 6},
  {id: 161897, book: "2Tim", verse: "4.008", unformatted: "此后，有那公义的冠冕为我存留，就是主——公义的审判者要在那一天回报给我的；不仅给我，也要给所有爱慕他显现的人。", version_id: 6},
  {id: 161898, book: "2Tim", verse: "4.009", unformatted: "结尾的教导\n你要尽快到我这里来，", version_id: 6},
  {id: 161899, book: "2Tim", verse: "4.01", unformatted: "因为迪马斯爱上了现今的世代，就离弃我到帖撒罗尼迦去了；克勒斯克到加拉太去，提多到达尔马提亚去了，", version_id: 6},
  {id: 161900, book: "2Tim", verse: "4.011", unformatted: "只有路加与我在一起。你要接上马可，带他一起来，因为他在服事工作上对我很有帮助。", version_id: 6},
  {id: 161901, book: "2Tim", verse: "4.012", unformatted: "我派特其库斯到以弗所去了。", version_id: 6},
  {id: 161902, book: "2Tim", verse: "4.013", unformatted: "你来的时候，要把我在特罗阿斯时留在卡尔坡家里的那件外衣带来，还有那些书卷，尤其是那些羊皮卷，也要带来。", version_id: 6},
  {id: 161903, book: "2Tim", verse: "4.014", unformatted: "铜匠亚历山大对我做了很多恶事；主将照着他的行为回报他。", version_id: 6},
  {id: 161904, book: "2Tim", verse: "4.015", unformatted: "你也要提防他，因为他极力抵挡我们的话。", version_id: 6},
  {id: 161905, book: "2Tim", verse: "4.016", unformatted: "我第一次为自己辩护的时候，没有一个人来支持我，大家都离弃了我；愿这事不算在他们身上。", version_id: 6},
  {id: 161906, book: "2Tim", verse: "4.017", unformatted: "但是主站在我身边，使我刚强，为要藉着我使传道得以完成，让所有外邦人都能听见；我也从狮子口里被救了出来。", version_id: 6},
  {id: 161907, book: "2Tim", verse: "4.018", unformatted: "主将会救我脱离一切凶恶之事，并且救我进入他属天的国度。愿荣耀归于他，直到永永远远！阿们。", version_id: 6},
  {id: 161908, book: "2Tim", verse: "4.019", unformatted: "祝福\n请问候普茜拉和阿奎拉，还有奥尼斯弗罗的家人。", version_id: 6},
  {id: 161909, book: "2Tim", verse: "4.02", unformatted: "以拉斯托在哥林多住下了；特罗费摩患了病，我就留他在米利都。", version_id: 6},
  {id: 161910, book: "2Tim", verse: "4.021", unformatted: "你要在冬天以前尽快赶来。犹布罗斯、普迪斯、利诺斯、克劳迪娅，以及所有的弟兄都问候你。", version_id: 6},
  {id: 161911, book: "2Tim", verse: "4.022", unformatted: "愿主与你的灵同在。愿恩典与你们同在！", version_id: 6},
  {id: 161912, book: "Titus", verse: "1.001", unformatted: "问安\n我保罗做神的奴仆、耶稣基督的使徒，是为了神所拣选之人的信仰，以及那带来敬神的真理知识；", version_id: 6},
  {id: 161913, book: "Titus", verse: "1.002", unformatted: "这是基于那永恒生命的盼望，而这盼望是那不撒谎的神在亘古以前所应许的。", version_id: 6},
  {id: 161914, book: "Titus", verse: "1.003", unformatted: "神在所定的时候，藉着传道把他的话显明了出来；我就是照着神我们救主的命令受了委托。", version_id: 6},
  {id: 161915, book: "Titus", verse: "1.004", unformatted: "致共同信仰上的真儿子提多：\n愿恩典、平安从父神和我们的救主基督耶稣临到你！", version_id: 6},
  {id: 161916, book: "Titus", verse: "1.005", unformatted: "提多的服事\n我曾把你留在克里特岛，为的是这样的缘故：要你处理那些还没有办完的事，并且照着我所吩咐你的，在各城委任长老。", version_id: 6},
  {id: 161917, book: "Titus", verse: "1.006", unformatted: "如果有人是无可指责的；只做一个妇人的丈夫；儿女是信徒，没有被控告为放荡或不服从，你就可以委任。", version_id: 6},
  {id: 161918, book: "Titus", verse: "1.007", unformatted: "因为，身为神的管家，做监督的必须无可指责、不任性、不暴躁、不酗酒、不好斗、不贪图不正当的利益；", version_id: 6},
  {id: 161919, book: "Titus", verse: "1.008", unformatted: "反而乐意款待客旅、喜爱良善、清醒自制、公正、圣洁、节制，", version_id: 6},
  {id: 161920, book: "Titus", verse: "1.009", unformatted: "坚守那合乎教导的信实话语；这是为了要他不但能用健全的教义劝勉人，而且能使那些反对的人知罪。", version_id: 6},
  {id: 161921, book: "Titus", verse: "1.01", unformatted: "因为实在有许多不服从的、讲虚妄话的、欺骗人的，尤其是那些属于割礼派的人；", version_id: 6},
  {id: 161922, book: "Titus", verse: "1.011", unformatted: "必须堵住这些人的口。他们为了不正当的利益，教导不该教导的事，把许多人的整个家庭都破坏了。", version_id: 6},
  {id: 161923, book: "Titus", verse: "1.012", unformatted: "克里特人中有一位，是他们自己的先知，曾经说过：“克里特人总是说谎的，是恶兽，好吃懒做。”", version_id: 6},
  {id: 161924, book: "Titus", verse: "1.013", unformatted: "这见证是真实的。因这理由，你要严厉地责备他们，好使他们在信仰上健全，", version_id: 6},
  {id: 161925, book: "Titus", verse: "1.014", unformatted: "不去听从犹太人编造的故事，以及背弃真理之人的命令。", version_id: 6},
  {id: 161926, book: "Titus", verse: "1.015", unformatted: "对洁净的人，一切都是洁净的；对污秽和不信的人，没有一样是洁净的，就是他们的理性和良心也都被污秽了。", version_id: 6},
  {id: 161927, book: "Titus", verse: "1.016", unformatted: "他们声称认识神，却在行为上否认他。他们是可憎恶的、悖逆的，在任何美善的工作上都经不起考验。", version_id: 6},
  {id: 161928, book: "Titus", verse: "2.001", unformatted: "健全的教训\n至于你，你所讲的要与健全的教义相宜：", version_id: 6},
  {id: 161929, book: "Titus", verse: "2.002", unformatted: "年长的男人要节制、庄重、自律，在信仰、爱心和忍耐上要健全；", version_id: 6},
  {id: 161930, book: "Titus", verse: "2.003", unformatted: "同样，年长的妇女在举止行为上要合乎圣洁、不恶意控告、不成为酒的奴隶，要教导美好的事；", version_id: 6},
  {id: 161931, book: "Titus", verse: "2.004", unformatted: "这样，她们可以训导年轻的妇女爱丈夫、爱儿女、", version_id: 6},
  {id: 161932, book: "Titus", verse: "2.005", unformatted: "自律、贞洁、善理家务、良善、服从自己的丈夫，免得神的话语受到亵渎。", version_id: 6},
  {id: 161933, book: "Titus", verse: "2.006", unformatted: "同样，你要鼓励年轻的男人清醒自制；", version_id: 6},
  {id: 161934, book: "Titus", verse: "2.007", unformatted: "你自己要在一切事上做出美好工作的榜样；教导的时候要正直、庄重，", version_id: 6},
  {id: 161935, book: "Titus", verse: "2.008", unformatted: "要用无可指责的健全话语，使反对的人在我们的事上因没有坏话可说，就感到羞愧。", version_id: 6},
  {id: 161936, book: "Titus", verse: "2.009", unformatted: "奴仆要在一切事上服从自己的主人、讨主人喜悦，不可顶嘴、", version_id: 6},
  {id: 161937, book: "Titus", verse: "2.01", unformatted: "不可私拿东西，反要显出所有美好的忠信，好在一切事上为神我们救主的教导增添光彩。", version_id: 6},
  {id: 161938, book: "Titus", verse: "2.011", unformatted: "事实上，神拯救万民的恩典已经显明出来了。", version_id: 6},
  {id: 161939, book: "Titus", verse: "2.012", unformatted: "这恩典管教我们，使我们拒绝不敬神的事和地上的欲望，在今世能过自制、公义、敬神的生活，", version_id: 6},
  {id: 161940, book: "Titus", verse: "2.013", unformatted: "期待那蒙福的盼望，以及我们那伟大的神、救主耶稣基督荣耀的显现。", version_id: 6},
  {id: 161941, book: "Titus", verse: "2.014", unformatted: "他为我们献上自己，是为了救赎我们脱离一切罪恶，并且洁净我们做他自己特选的子民，做美好工作的热心人。", version_id: 6},
  {id: 161942, book: "Titus", verse: "2.015", unformatted: "你要讲说这些，充分地使用权威来劝勉、责备；不要让任何人轻看你。", version_id: 6},
  {id: 161943, book: "Titus", verse: "3.001", unformatted: "做善工的重要性\n你提醒他们要服从统治者和掌权者，要顺从，并要为一切美善的工作做好准备；", version_id: 6},
  {id: 161944, book: "Titus", verse: "3.002", unformatted: "不要毁谤人、不要争吵；要谦和，在一切事上对所有的人都要显出温柔。", version_id: 6},
  {id: 161945, book: "Titus", verse: "3.003", unformatted: "其实，我们从前也是无知、悖逆、被迷惑的，做各种欲望和享乐的奴仆，活在恶毒和嫉妒中，是可憎恶的，又彼此相恨。", version_id: 6},
  {id: 161946, book: "Titus", verse: "3.004", unformatted: "但是，当神我们救主的仁慈和他对人的爱显明的时候，", version_id: 6},
  {id: 161947, book: "Titus", verse: "3.005", unformatted: "他拯救了我们；\n这不是本于我们所做的义行，\n而是照着他的怜悯，\n藉着重生的洗涤和圣灵的更新。", version_id: 6},
  {id: 161948, book: "Titus", verse: "3.006", unformatted: "神藉着我们的救主耶稣基督，\n将圣灵丰丰富富地倾注在我们身上，", version_id: 6},
  {id: 161949, book: "Titus", verse: "3.007", unformatted: "使我们藉着他的恩典被称为义，\n可以照着永恒生命的盼望成为继承人。", version_id: 6},
  {id: 161950, book: "Titus", verse: "3.008", unformatted: "这话是信实的。我希望你强调这些事，好让那些信了神的人专心投入美好的工作；这些事对人是好事，是有益处的。", version_id: 6},
  {id: 161951, book: "Titus", verse: "3.009", unformatted: "至于愚拙的辩论、家谱、纷争和律法上的争执，你都要避免；因为这些都是无用的，是虚妄的。", version_id: 6},
  {id: 161952, book: "Titus", verse: "3.01", unformatted: "对那制造分裂的人，在第一次、第二次的警戒以后，就要弃绝他，", version_id: 6},
  {id: 161953, book: "Titus", verse: "3.011", unformatted: "因为你知道，这样的人已经背道，一直犯罪，定了自己的罪。", version_id: 6},
  {id: 161954, book: "Titus", verse: "3.012", unformatted: "结尾的教导\n我派阿特马斯或特其库斯到你那里以后，你要尽快到尼科波利斯来见我，因为我已经决定在那里过冬。", version_id: 6},
  {id: 161955, book: "Titus", verse: "3.013", unformatted: "你要尽快送齐纳斯律师和阿波罗上路，好让他们一无所缺。", version_id: 6},
  {id: 161956, book: "Titus", verse: "3.014", unformatted: "我们自己的人，为了紧要的需用也应该学会投入美好的工作，免得成为不结果子的人。", version_id: 6},
  {id: 161957, book: "Titus", verse: "3.015", unformatted: "所有与我在一起的人都问候你。请问候那些在信仰上爱我们的人。愿恩典与你们大家同在！", version_id: 6},
  {id: 161958, book: "Phlm", verse: "1.001", unformatted: "问安\n基督耶稣的囚犯保罗，以及弟兄提摩太，\n致我们亲爱的弟兄和同工腓利门，", version_id: 6},
  {id: 161959, book: "Phlm", verse: "1.002", unformatted: "还有姐妹阿菲娅和我们的战友阿其波斯，以及在你家里的教会：", version_id: 6},
  {id: 161960, book: "Phlm", verse: "1.003", unformatted: "愿恩典与平安从神我们的父和主耶稣基督临到你们！", version_id: 6},
  {id: 161961, book: "Phlm", verse: "1.004", unformatted: "为腓利门感恩\n我在祷告中提到你的时候，总是感谢我的神，", version_id: 6},
  {id: 161962, book: "Phlm", verse: "1.005", unformatted: "因为我听说了你对主耶稣和所有圣徒所怀的爱心和信仰。", version_id: 6},
  {id: 161963, book: "Phlm", verse: "1.006", unformatted: "藉着认识在我们中间为基督所做的一切美善之事，愿你在信仰上的契合能发挥功效。", version_id: 6},
  {id: 161964, book: "Phlm", verse: "1.007", unformatted: "实际上，我由于你的爱得了极大的喜乐和鼓励，因为弟兄啊，圣徒们的情感藉着你得了安宁。", version_id: 6},
  {id: 161965, book: "Phlm", verse: "1.008", unformatted: "保罗的请求\n所以，虽然我在基督里大有胆量，可以吩咐你去做合宜的事，", version_id: 6},
  {id: 161966, book: "Phlm", verse: "1.009", unformatted: "但为了爱的缘故，我宁愿恳求你。我保罗——像我这样一个老人，而现在又是基督耶稣的囚犯——", version_id: 6},
  {id: 161967, book: "Phlm", verse: "1.01", unformatted: "为我在捆锁中所生的孩子奥尼斯莫，恳求你。", version_id: 6},
  {id: 161968, book: "Phlm", verse: "1.011", unformatted: "他从前对你没有益处，但如今对你、对我都有益处了。", version_id: 6},
  {id: 161969, book: "Phlm", verse: "1.012", unformatted: "我把他本人送回你那里，就是说，把我的情感送回你那里。", version_id: 6},
  {id: 161970, book: "Phlm", verse: "1.013", unformatted: "我本来想把他留在自己身边，好让他在我为福音被捆锁的期间，能替你服事我。", version_id: 6},
  {id: 161971, book: "Phlm", verse: "1.014", unformatted: "但是没有你的同意，我就不愿意做什么，好让你的美善之事不是出于勉强，而是出于自愿。", version_id: 6},
  {id: 161972, book: "Phlm", verse: "1.015", unformatted: "其实，他暂时离开你，或许正是为了使你可以永远得着他——", version_id: 6},
  {id: 161973, book: "Phlm", verse: "1.016", unformatted: "不再像一个奴仆，而是高于奴仆，是一位蒙爱的弟兄。他对我尤其是这样，更何况对你呢！无论是在肉体上或在主里面都是这样。", version_id: 6},
  {id: 161974, book: "Phlm", verse: "1.017", unformatted: "因此，你如果把我当做同伴，就请接纳他，像接纳我一样。", version_id: 6},
  {id: 161975, book: "Phlm", verse: "1.018", unformatted: "如果他亏负过你什么，或现在欠了你什么，请把它算在我的账上。", version_id: 6},
  {id: 161976, book: "Phlm", verse: "1.019", unformatted: "我保罗亲手写下：我必偿还——免得我对你说，连你自己也是欠我的呢！", version_id: 6},
  {id: 161977, book: "Phlm", verse: "1.02", unformatted: "是的，弟兄！愿我在主里从你得到喜乐，请你使我的情感在基督里得到安宁。", version_id: 6},
  {id: 161978, book: "Phlm", verse: "1.021", unformatted: "我深信你会顺从，就写了信给你，因为我知道你所做的甚至会超过我所求的。", version_id: 6},
  {id: 161979, book: "Phlm", verse: "1.022", unformatted: "同时还请你为我预备住处，因为我希望藉着你们的祷告，能回到你们那里去。", version_id: 6},
  {id: 161980, book: "Phlm", verse: "1.023", unformatted: "结尾的祝福\n在基督耶稣里与我一起坐牢的以帕弗拉问候你。", version_id: 6},
  {id: 161981, book: "Phlm", verse: "1.024", unformatted: "我的同工马可、亚里达古、迪马斯、路加也都问候你。", version_id: 6},
  {id: 161982, book: "Phlm", verse: "1.025", unformatted: "愿我们主耶稣基督的恩典与你们的灵同在！", version_id: 6},
  {id: 161983, book: "Heb", verse: "1.001", unformatted: "圣子的本质\n过去，神在不同的时代，用不同的方式，藉着先知们对祖先说话，", version_id: 6},
  {id: 161984, book: "Heb", verse: "1.002", unformatted: "但在末后的这些日子里，神藉着他的儿子向我们说话。神预定了他为万有的继承人，也藉着他造了宇宙。", version_id: 6},
  {id: 161985, book: "Heb", verse: "1.003", unformatted: "他是神荣耀的光辉，是神本体的真像，以自己大能的话语托住万有。他成就了洁净罪孽的事，就坐在高天至尊者的右边。", version_id: 6},
  {id: 161986, book: "Heb", verse: "1.004", unformatted: "他远比天使更美好，就像他所继承的名比天使的名更尊贵。", version_id: 6},
  {id: 161987, book: "Heb", verse: "1.005", unformatted: "圣子高过天使\n从前神到底对哪一位天使说过“你是我的儿子，我今天生了你”，又说“我将成为他的父亲，他成为我的儿子”呢？", version_id: 6},
  {id: 161988, book: "Heb", verse: "1.006", unformatted: "不过当神再次带领长子进入世界的时候却说：“神所有的天使都当敬拜他。”", version_id: 6},
  {id: 161989, book: "Heb", verse: "1.007", unformatted: "关于天使，他确实说：\n“神使他的天使为风，\n使他的仆役为烈火。”", version_id: 6},
  {id: 161990, book: "Heb", verse: "1.008", unformatted: "但是关于儿子，他却说：\n“神哪，你的宝座是永永远远的；\n你国度的权杖是正义的权杖。", version_id: 6},
  {id: 161991, book: "Heb", verse: "1.009", unformatted: "你爱公义，恨罪恶，\n为此，神——你的神用喜乐的油来膏立你，\n而不膏立你的同伴。”", version_id: 6},
  {id: 161992, book: "Heb", verse: "1.01", unformatted: "他又说：\n“主啊，你在起初立定了大地的根基，\n诸天也是你手所造的。", version_id: 6},
  {id: 161993, book: "Heb", verse: "1.011", unformatted: "天地将要毁灭，你却继续存留；\n天地都要像衣服那样渐渐破旧。", version_id: 6},
  {id: 161994, book: "Heb", verse: "1.012", unformatted: "你将要把天地像外衣那样卷起来，\n天地也要像衣服那样被更换；\n你却永不改变，\n你的岁月没有穷尽。”", version_id: 6},
  {id: 161995, book: "Heb", verse: "1.013", unformatted: "从前神到底对哪一位天使说过\n：“你坐在我的右边，\n等我把你的敌人放在你的脚下做脚凳”呢？", version_id: 6},
  {id: 161996, book: "Heb", verse: "1.014", unformatted: "难道所有的天使不都是服事的灵、奉差派服事那些将要继承救恩的人吗？", version_id: 6},
  {id: 161997, book: "Heb", verse: "2.001", unformatted: "不可忽视救恩\n因此，我们必须更加留心所听过的事，免得随流漂去。", version_id: 6},
  {id: 161998, book: "Heb", verse: "2.002", unformatted: "要知道，那藉着天使所说的话既然是确定的，连一切过犯和悖逆都受到了应得的报应，", version_id: 6},
  {id: 161999, book: "Heb", verse: "2.003", unformatted: "那么，如果我们忽视了如此伟大的救恩，又怎么能逃脱报应呢？这救恩起初由主传讲，后来由那些听见的人向我们证实。", version_id: 6},
  {id: 162000, book: "Heb", verse: "2.004", unformatted: "神又照着自己的旨意，用神迹、奇事和各种大能，以及圣灵的各种恩赐，与他们一同见证。", version_id: 6},
  {id: 162001, book: "Heb", verse: "2.005", unformatted: "耶稣与谦卑\n事实上，我们所说的那“将要来的世界”，神并没有使它服从天使，", version_id: 6},
  {id: 162002, book: "Heb", verse: "2.006", unformatted: "但是有人在经上某处郑重地见证说：\n“人算什么，你竟然顾念他？\n人之子算什么，你竟然照顾他？", version_id: 6},
  {id: 162003, book: "Heb", verse: "2.007", unformatted: "你使他暂时比天使低微；\n你赐他荣耀和尊贵为冠冕；", version_id: 6},
  {id: 162004, book: "Heb", verse: "2.008", unformatted: "你使万有都服从在他的脚下。”\n既然神使万有都服从他，就没有留下一样不服从他；但如今，我们还没有看见万有都服从他。", version_id: 6},
  {id: 162005, book: "Heb", verse: "2.009", unformatted: "不过我们看见的是：那暂时比天使低微的耶稣，因着死亡的痛苦被赐予荣耀和尊贵为冠冕，好使他藉着神的恩典，替所有的人尝了死的滋味。", version_id: 6},
  {id: 162006, book: "Heb", verse: "2.01", unformatted: "万有因他而有、万有藉他而成的那一位，既然要带领许多儿女归于荣耀，就藉着苦难使他们救恩的元首得以成全，这是合宜的，", version_id: 6},
  {id: 162007, book: "Heb", verse: "2.011", unformatted: "因为那使人分别为圣的，和那些被分别为圣的，都是出于同一位。因这理由，他称他们为弟兄，并不以为耻。", version_id: 6},
  {id: 162008, book: "Heb", verse: "2.012", unformatted: "他说：\n“我要向我的弟兄们宣扬你的名，\n我要在会众面前歌颂你。”", version_id: 6},
  {id: 162009, book: "Heb", verse: "2.013", unformatted: "他又说：“我要依靠他。”还说：“看哪，我和神所赐给我的孩子们都在这里！”", version_id: 6},
  {id: 162010, book: "Heb", verse: "2.014", unformatted: "所以，孩子们既然同属血肉之体，他也照样同有一样的血肉之体，为要藉着死亡来废除那掌握死亡权势的——就是魔鬼，", version_id: 6},
  {id: 162011, book: "Heb", verse: "2.015", unformatted: "并且释放那些因怕死而终生被束缚为奴役的人。", version_id: 6},
  {id: 162012, book: "Heb", verse: "2.016", unformatted: "显然，他并不是要救拔天使，而是救拔亚伯拉罕的后裔。", version_id: 6},
  {id: 162013, book: "Heb", verse: "2.017", unformatted: "故此，他必须在各方面都像他的弟兄们那样，为要在属神的事上成为怜悯人的、忠心的大祭司，好为子民的罪孽献上平息祭。", version_id: 6},
  {id: 162014, book: "Heb", verse: "2.018", unformatted: "既然他自己在被试探的时候受了苦，他就能帮助那些受试探的人。", version_id: 6},
  {id: 162015, book: "Heb", verse: "3.001", unformatted: "我们的主\n故此，蒙了属天召唤的同伴们，圣洁的弟兄们！你们应该思想我们信仰告白中的使者和大祭司——耶稣。", version_id: 6},
  {id: 162016, book: "Heb", verse: "3.002", unformatted: "他对委任他的那一位是忠心的，正如摩西在神的全家也是忠心的。", version_id: 6},
  {id: 162017, book: "Heb", verse: "3.003", unformatted: "事实上，这个人被看为比摩西配得更大的荣耀，就像建造房屋的人比房屋本身更有尊荣那样，", version_id: 6},
  {id: 162018, book: "Heb", verse: "3.004", unformatted: "因为每一座房屋都是由人建造的，而建造万有的就是神。", version_id: 6},
  {id: 162019, book: "Heb", verse: "3.005", unformatted: "摩西是做为仆人，对神的全家忠心，为将被述说的事做见证；", version_id: 6},
  {id: 162020, book: "Heb", verse: "3.006", unformatted: "但基督是做为儿子，忠心管理神的家。如果我们持守所确信的和所夸耀的盼望，我们就是神的家。", version_id: 6},
  {id: 162021, book: "Heb", verse: "3.007", unformatted: "提防不信\n因此，就像圣灵所说的：\n“今天，你们如果听见他的声音，", version_id: 6},
  {id: 162022, book: "Heb", verse: "3.008", unformatted: "就不可硬着你们的心，\n如同在那悖逆的时候，也像在那旷野试探的日子。", version_id: 6},
  {id: 162023, book: "Heb", verse: "3.009", unformatted: "在那里，你们的祖先考验我、试探我，\n并且看了我的作为四十年之久。", version_id: 6},
  {id: 162024, book: "Heb", verse: "3.01", unformatted: "所以，我向那世代发怒，说：\n‘他们的心总是被迷惑，\n他们不认识我的道路’，", version_id: 6},
  {id: 162025, book: "Heb", verse: "3.011", unformatted: "所以，我在自己的震怒中起誓说：\n‘他们绝不能进入我的安息。’”", version_id: 6},
  {id: 162026, book: "Heb", verse: "3.012", unformatted: "弟兄们，你们要当心，免得你们中间有人存着不信的邪恶之心，以致离开永生的神。", version_id: 6},
  {id: 162027, book: "Heb", verse: "3.013", unformatted: "只要日子还被称为“今天”，你们就要天天互相鼓励，免得你们当中有人被罪迷惑，心里刚硬。", version_id: 6},
  {id: 162028, book: "Heb", verse: "3.014", unformatted: "因为我们如果把那起初的确信，坚定地持守到底，我们就成为基督的同伴了。", version_id: 6},
  {id: 162029, book: "Heb", verse: "3.015", unformatted: "经上说：\n“今天，你们如果听见他的声音，\n就不可硬着你们的心，如同在那悖逆的时候。”", version_id: 6},
  {id: 162030, book: "Heb", verse: "3.016", unformatted: "到底是谁听见却悖逆了呢？难道不是被摩西从埃及带领出来的那一切人吗？", version_id: 6},
  {id: 162031, book: "Heb", verse: "3.017", unformatted: "神又向谁发怒了四十年之久呢？难道不是那些犯了罪、尸首倒在旷野的人吗？", version_id: 6},
  {id: 162032, book: "Heb", verse: "3.018", unformatted: "他又向谁起誓说，他们绝不能进入他的安息呢？难道不是向那些不肯信从的人吗？", version_id: 6},
  {id: 162033, book: "Heb", verse: "3.019", unformatted: "这样我们就看出来：他们不能进入，是因不信的缘故。", version_id: 6},
  {id: 162034, book: "Heb", verse: "4.001", unformatted: "竭力进入安息\n所以，既然那进入神安息的应许还存留，我们就该惧怕，免得我们当中有人看来达不到了。", version_id: 6},
  {id: 162035, book: "Heb", verse: "4.002", unformatted: "实际上，我们也是得以听到福音的，就像他们那样；但是他们所听的话语对他们没有益处，因为他们没有因着信，与所听的相配合。", version_id: 6},
  {id: 162036, book: "Heb", verse: "4.003", unformatted: "其实，我们这些相信的才能进入那安息，正如神所说：\n“所以我在自己的震怒中起誓：\n‘他们绝不能进入我的安息。’”\n事实上，神的工作从创世以来，就已经完成了。", version_id: 6},
  {id: 162037, book: "Heb", verse: "4.004", unformatted: "原来关于第七日，他在经上某处这样说：\n“神在第七日，\n歇了他所有的工作。”", version_id: 6},
  {id: 162038, book: "Heb", verse: "4.005", unformatted: "在这里他又说：“他们绝不能进入我的安息。”", version_id: 6},
  {id: 162039, book: "Heb", verse: "4.006", unformatted: "所以，既然这安息为了一些进入它的人被保留下来，可是由于那些原先得以听到福音的人，因着不信从而没有进去，", version_id: 6},
  {id: 162040, book: "Heb", verse: "4.007", unformatted: "神就再次设定了一个日子——“今天”，就是在很久以后藉着大卫所说的，像预先说过的那样：\n“今天，你们如果听见他的声音，\n就不可硬着你们的心。”", version_id: 6},
  {id: 162041, book: "Heb", verse: "4.008", unformatted: "如果约书亚已经使他们得了安息，神后来就不会再说到另一个日子了。", version_id: 6},
  {id: 162042, book: "Heb", verse: "4.009", unformatted: "这样，就有一个“安息日的安息”为了神的子民被保留下来。", version_id: 6},
  {id: 162043, book: "Heb", verse: "4.01", unformatted: "原来，那进了神安息的人，也歇了自己的工作，就像神歇了自己的工作那样。", version_id: 6},
  {id: 162044, book: "Heb", verse: "4.011", unformatted: "所以，我们要努力进入那安息，免得有人照着那同样不信从的样式跌落了。", version_id: 6},
  {id: 162045, book: "Heb", verse: "4.012", unformatted: "神的话语是有生命的，是有功效的；比任何双刃的剑更锋利，能刺透到魂和灵的分界，以及骨节和骨髓的分界，也能辨明心中的思想和意念；", version_id: 6},
  {id: 162046, book: "Heb", verse: "4.013", unformatted: "并且被造之物在神面前，没有一样不是显明的；万有在他眼前，都是赤裸敞开的；我们都要向他交代。", version_id: 6},
  {id: 162047, book: "Heb", verse: "4.014", unformatted: "我们的大祭司\n所以，我们既然有一位已经越过了诸天的、尊贵的大祭司——神的儿子耶稣，我们就要持守信仰告白；", version_id: 6},
  {id: 162048, book: "Heb", verse: "4.015", unformatted: "因为我们的大祭司不是不能同情我们的种种软弱，而是在各方面照着与我们相同的样式受过试探，只是没有犯罪。", version_id: 6},
  {id: 162049, book: "Heb", verse: "4.016", unformatted: "因此，让我们坦然无惧地来到恩典的宝座前，为要得着怜悯，寻见恩典，做为及时的帮助。", version_id: 6},
  {id: 162050, book: "Heb", verse: "5.001", unformatted: "麦基洗德\n每一个从人间选出来的大祭司，都是受委任替人办理属神的事，要为罪孽献上礼物和祭物。", version_id: 6},
  {id: 162051, book: "Heb", verse: "5.002", unformatted: "他能体谅那些不明白而被迷惑的人，因为他自己也被软弱所困。", version_id: 6},
  {id: 162052, book: "Heb", verse: "5.003", unformatted: "因此，他怎样替子民为罪献祭，也必须照样替自己为罪献祭。", version_id: 6},
  {id: 162053, book: "Heb", verse: "5.004", unformatted: "没有任何人能为自己取得这尊荣，除非蒙神召唤，就像亚伦那样。", version_id: 6},
  {id: 162054, book: "Heb", verse: "5.005", unformatted: "同样，也不是基督荣耀了自己成为大祭司，而是曾经对他说“你是我的儿子，我今天生了你”的那一位荣耀了他；", version_id: 6},
  {id: 162055, book: "Heb", verse: "5.006", unformatted: "正如在另外一处也说：“你是照着麦基洗德的等级做祭司，直到永远。”", version_id: 6},
  {id: 162056, book: "Heb", verse: "5.007", unformatted: "基督在他肉身的日子里，曾经用强烈的呼号和泪水，向能救他脱离死亡的那一位献上了祈祷和恳求，就因着他的虔诚蒙了垂听。", version_id: 6},
  {id: 162057, book: "Heb", verse: "5.008", unformatted: "他虽然是儿子，还是从他所受的苦难中，学了顺从。", version_id: 6},
  {id: 162058, book: "Heb", verse: "5.009", unformatted: "他既然得以完全，就为所有顺从他的人成了永恒救恩的本源，", version_id: 6},
  {id: 162059, book: "Heb", verse: "5.01", unformatted: "并且被神称为“照着麦基洗德之等级的大祭司”。", version_id: 6},
  {id: 162060, book: "Heb", verse: "5.011", unformatted: "不成熟的问题\n关于这事，我们还有很多话要说，可是很难解释，因为你们听不进去了。", version_id: 6},
  {id: 162061, book: "Heb", verse: "5.012", unformatted: "其实从时间来说，你们早就应该做教师了，却还需要有人把神话语的基本原则重新教导你们；你们成了那需要吃奶、而不能吃干粮的人。", version_id: 6},
  {id: 162062, book: "Heb", verse: "5.013", unformatted: "要知道，凡是吃奶的人，都对称义的道理不熟悉，因为他是个小孩子；", version_id: 6},
  {id: 162063, book: "Heb", verse: "5.014", unformatted: "不过干粮是给成熟之人吃的——他们经过实践，识别能力得到操练，以致能分辨善恶。", version_id: 6},
  {id: 162064, book: "Heb", verse: "6.001", unformatted: "警告离弃正道\n所以，让我们从基督话语的基本原则出发，被神带到那成熟的境界；而不用重新打基础，就如悔改那些致死的行为、对神的信仰、", version_id: 6},
  {id: 162065, book: "Heb", verse: "6.002", unformatted: "各样的洗净礼、按手礼、死人复活、永远的惩罚等教导上。", version_id: 6},
  {id: 162066, book: "Heb", verse: "6.003", unformatted: "神如果准许，我们就这么做。", version_id: 6},
  {id: 162067, book: "Heb", verse: "6.004", unformatted: "原来，如果有人蒙过一次光照、尝过属天恩赐的滋味、曾经与圣灵有份、", version_id: 6},
  {id: 162068, book: "Heb", verse: "6.005", unformatted: "尝过神美善的话语和将要来临之世代的权能，", version_id: 6},
  {id: 162069, book: "Heb", verse: "6.006", unformatted: "却背弃了真理，就不可能再次更新以至于悔改了。因为他们亲自把神的儿子再次钉上十字架，公然羞辱他。", version_id: 6},
  {id: 162070, book: "Heb", verse: "6.007", unformatted: "要知道，当一块田地吸收了常常降在它上面的雨水，并且为种地的人长出有用菜蔬时，它就享受从神而来的祝福；", version_id: 6},
  {id: 162071, book: "Heb", verse: "6.008", unformatted: "但如果它长出荆棘和蒺藜，就没有用处了，很快就要被诅咒，它的结局就是被焚烧。", version_id: 6},
  {id: 162072, book: "Heb", verse: "6.009", unformatted: "各位蒙爱的人哪，我们即使这样说，还是深信那关于你们的更好之事，并且是有关救恩的，", version_id: 6},
  {id: 162073, book: "Heb", verse: "6.01", unformatted: "因为神不是不公正的，以致忘记你们的工作和你们为他的名所显出的爱，就是你们先前和如今服事圣徒时所显出的爱。", version_id: 6},
  {id: 162074, book: "Heb", verse: "6.011", unformatted: "我们渴望你们每个人都显出同样的殷勤，使你们对所盼望的有完全的确信，一直到底；", version_id: 6},
  {id: 162075, book: "Heb", verse: "6.012", unformatted: "这样，你们就不会变得懈怠，反而会效法那些藉着信仰和耐心去继承各样应许的人。", version_id: 6},
  {id: 162076, book: "Heb", verse: "6.013", unformatted: "承受应许\n神向亚伯拉罕应许的时候，因为没有比自己更大的可以指着起誓，就指着自己起誓，", version_id: 6},
  {id: 162077, book: "Heb", verse: "6.014", unformatted: "说：\n“我必定大大赐福给你，\n多多地增加你的子孙。”", version_id: 6},
  {id: 162078, book: "Heb", verse: "6.015", unformatted: "这样，亚伯拉罕恒久忍耐，就获得了神所应许的。", version_id: 6},
  {id: 162079, book: "Heb", verse: "6.016", unformatted: "人本来指着比自己更大的起誓；对他们，所起的誓就是了结一切争执的确据；", version_id: 6},
  {id: 162080, book: "Heb", verse: "6.017", unformatted: "为此，神想要对那些继承应许的人格外表明他的计划是不能更改的，就用所起的誓来保证。", version_id: 6},
  {id: 162081, book: "Heb", verse: "6.018", unformatted: "藉着这两件不能更改的事——在这些事上，神不可能说谎——使我们这些逃往避难所、抓住那摆在前面盼望的人，可以得到极大的鼓励。", version_id: 6},
  {id: 162082, book: "Heb", verse: "6.019", unformatted: "我们所拥有的这盼望，像灵魂的锚，又可靠又坚固，并且进到幔子最里层的那至圣所。", version_id: 6},
  {id: 162083, book: "Heb", verse: "6.02", unformatted: "耶稣已经为我们做先锋进入了那里，照着麦基洗德的等级做大祭司，直到永远。", version_id: 6},
  {id: 162084, book: "Heb", verse: "7.001", unformatted: "麦基洗德的地位\n原来这麦基洗德——\n撒冷之王，至高神的祭司，\n在亚伯拉罕杀败众王回来的时候，去迎接他、祝福他，", version_id: 6},
  {id: 162085, book: "Heb", verse: "7.002", unformatted: "亚伯拉罕就从所得来的一切中，把十分之一献给他。\n“麦基洗德”翻译出来，首先是“公义之王”，\n然后又是“撒冷之王”——这意思是“和平之王”。", version_id: 6},
  {id: 162086, book: "Heb", verse: "7.003", unformatted: "他没有父亲，没有母亲，没有族谱，\n没有年岁的开始，也没有生命的终结，\n与神的儿子相似，持久做祭司，直到永远。", version_id: 6},
  {id: 162087, book: "Heb", verse: "7.004", unformatted: "你们当想一想，这个人多么伟大！连先祖亚伯拉罕也从最好的战利品中把十分之一献给他。", version_id: 6},
  {id: 162088, book: "Heb", verse: "7.005", unformatted: "虽然利未子孙中那些领受祭司职务的，从亚伯拉罕的后裔中出来，他们还是奉命按照律法向子民，就是自己的同胞，收取十分之一。", version_id: 6},
  {id: 162089, book: "Heb", verse: "7.006", unformatted: "可是，不出于利未世系的那一位，却从亚伯拉罕收取了十分之一，并且祝福了那拥有各样应许的亚伯拉罕。", version_id: 6},
  {id: 162090, book: "Heb", verse: "7.007", unformatted: "原来，位分小的从位分大的接受祝福，是毫无争议的事。", version_id: 6},
  {id: 162091, book: "Heb", verse: "7.008", unformatted: "在这里，收取十分之一的人们固然都会死去；但在那里，他却被见证说是永生的。", version_id: 6},
  {id: 162092, book: "Heb", verse: "7.009", unformatted: "甚至从某种意义上可以说：连那收取十分之一的利未，也藉着亚伯拉罕交纳了十分之一，", version_id: 6},
  {id: 162093, book: "Heb", verse: "7.01", unformatted: "因为麦基洗德迎接亚伯拉罕的时候，利未还在他先祖的身体里面。", version_id: 6},
  {id: 162094, book: "Heb", verse: "7.011", unformatted: "完美的祭司\n既然如此，如果藉着利未人的祭司职份能达到完全——子民本来在这职份下领受了律法——那么，为什么还需要照着麦基洗德的等级，而不照着所谓“亚伦的等级”兴起另一位祭司呢？", version_id: 6},
  {id: 162095, book: "Heb", verse: "7.012", unformatted: "祭司职份被更换，律法也必须有所更换。", version_id: 6},
  {id: 162096, book: "Heb", verse: "7.013", unformatted: "其实这些话所论到的那一位是属于别的支派，这支派中从来没有人在祭坛供过职；", version_id: 6},
  {id: 162097, book: "Heb", verse: "7.014", unformatted: "就是说，我们的主明显是从犹大支派兴起的，而对于这个支派，摩西从来没有提到有关祭司的事。", version_id: 6},
  {id: 162098, book: "Heb", verse: "7.015", unformatted: "如果照着麦基洗德的相同样式另有一位祭司兴起来，那么，这件事就更加明显了。", version_id: 6},
  {id: 162099, book: "Heb", verse: "7.016", unformatted: "他成为祭司，不是照着有关肉体条例的法则，而是照着那不能毁灭的生命大能，", version_id: 6},
  {id: 162100, book: "Heb", verse: "7.017", unformatted: "因为他被见证说：\n“你是照着麦基洗德的等级做祭司，直到永远。”", version_id: 6},
  {id: 162101, book: "Heb", verse: "7.018", unformatted: "原来，一方面，先前的条例因着本身的软弱和无用，就被废弃了，", version_id: 6},
  {id: 162102, book: "Heb", verse: "7.019", unformatted: "因为律法没有使任何事物完全；另一方面，一个更美好的盼望却被带来了；藉着这个盼望，我们可以亲近神。", version_id: 6},
  {id: 162103, book: "Heb", verse: "7.02", unformatted: "再者，所发生的事并不是没有神的誓言保证。就是说，其他人成为祭司是没有誓言保证的，", version_id: 6},
  {id: 162104, book: "Heb", verse: "7.021", unformatted: "可是耶稣藉着对他说话的那一位是有誓言保证的：\n“主起了誓，不会后悔：\n‘你是祭司，直到永远。’”", version_id: 6},
  {id: 162105, book: "Heb", verse: "7.022", unformatted: "既然如此，耶稣也就成了更美好之约的保证人。", version_id: 6},
  {id: 162106, book: "Heb", verse: "7.023", unformatted: "那些以前成为祭司的固然人数众多，因为死亡阻止了他们继续留任，", version_id: 6},
  {id: 162107, book: "Heb", verse: "7.024", unformatted: "但由于耶稣永远长存，就拥有永恒不变的祭司职份；", version_id: 6},
  {id: 162108, book: "Heb", verse: "7.025", unformatted: "故此，他也能彻底拯救那些藉着他来到神面前的人；因为他一直活着，为他们代求。", version_id: 6},
  {id: 162109, book: "Heb", verse: "7.026", unformatted: "这样一位圣洁、没有邪恶、毫无玷污、与罪人分开、高过诸天的大祭司，对我们实在是合宜的。", version_id: 6},
  {id: 162110, book: "Heb", verse: "7.027", unformatted: "他不需要像其他大祭司那样天天献祭：先为自己的罪孽，然后为子民；因为当他把自己献上的时候，就一次性地把这事完成了。", version_id: 6},
  {id: 162111, book: "Heb", verse: "7.028", unformatted: "律法委任的大祭司都有软弱，但在律法以后，誓言保证的话语所委任的儿子，却永远完全。", version_id: 6},
  {id: 162112, book: "Heb", verse: "8.001", unformatted: "属天的祭司\n以上所说的要点是：我们有这样的一位大祭司，已经坐在诸天之上至尊者的宝座右边，", version_id: 6},
  {id: 162113, book: "Heb", verse: "8.002", unformatted: "他是圣所和真会幕的服事者。这会幕是主所搭建的，不是人所搭建的。", version_id: 6},
  {id: 162114, book: "Heb", verse: "8.003", unformatted: "每一位大祭司受委任，都是为了献上礼物和祭物；故此，这一位也必须有所献上。", version_id: 6},
  {id: 162115, book: "Heb", verse: "8.004", unformatted: "既然如此，如果他在地上，就不会做祭司，因为地上已经有照着律法献祭物的祭司。", version_id: 6},
  {id: 162116, book: "Heb", verse: "8.005", unformatted: "那些祭司所事奉的，是天上事物的模型和影子，就如摩西将要完成会幕的时候，得了神的指示，说：“你要注意，一切都要按照在山上指示你的样式去造。”", version_id: 6},
  {id: 162117, book: "Heb", verse: "8.006", unformatted: "但如今，耶稣得了更尊贵的服事工作，就像他也是更美好之约的中保那样；这约建立在各样更美好的应许之上。", version_id: 6},
  {id: 162118, book: "Heb", verse: "8.007", unformatted: "完美的新约\n原来，如果那第一个约是无可指责的，就没有寻求第二个约的必要了，", version_id: 6},
  {id: 162119, book: "Heb", verse: "8.008", unformatted: "因为神指责说：\n“看哪，时候就要到了！\n主说，我要与以色列家\n和犹大家订立新约。", version_id: 6},
  {id: 162120, book: "Heb", verse: "8.009", unformatted: "这约不像从前我拉着他们祖先的手，\n在领他们出埃及地的日子里\n与他们所立的约。\n因为他们没有持守我的约，\n我就不理他们——这是主说的。", version_id: 6},
  {id: 162121, book: "Heb", verse: "8.01", unformatted: "主又说，在那些日子以后，\n我要与以色列家订立这样的约：\n我要把我的法则放在他们的意念中，\n刻在他们的心上；\n我将要做他们的神，\n他们将要做我的子民。", version_id: 6},
  {id: 162122, book: "Heb", verse: "8.011", unformatted: "他们绝不需要教导各自的同胞、各自的兄弟，说：\n‘你要认识主’——\n因为他们所有的人，\n从卑微的到尊贵的，\n都将认识我；", version_id: 6},
  {id: 162123, book: "Heb", verse: "8.012", unformatted: "又因为我要宽恕他们的不义，\n绝不再想起他们的罪孽。”", version_id: 6},
  {id: 162124, book: "Heb", verse: "8.013", unformatted: "当神说到“新约”，就已经把第一个当做旧的；而那渐渐变旧、渐渐变老的，很快就要消逝了。", version_id: 6},
  {id: 162125, book: "Heb", verse: "9.001", unformatted: "旧约的事奉\n这样，第一个约也包括了事奉礼的规定和地上的圣所，", version_id: 6},
  {id: 162126, book: "Heb", verse: "9.002", unformatted: "因为有预备好的会幕：第一间里有灯台、桌子和陈设饼，这叫做圣所；", version_id: 6},
  {id: 162127, book: "Heb", verse: "9.003", unformatted: "会幕第二层幔子后面，叫做至圣所，", version_id: 6},
  {id: 162128, book: "Heb", verse: "9.004", unformatted: "里面有金香坛和四周包金的约柜；柜里有盛着吗哪的金罐、亚伦那根发过芽的杖和两块约版；", version_id: 6},
  {id: 162129, book: "Heb", verse: "9.005", unformatted: "约柜上方有荣耀的基路伯，遮蔽着施恩座。关于这些，现在就不一一细说了。", version_id: 6},
  {id: 162130, book: "Heb", verse: "9.006", unformatted: "既然这些东西都这样预备好了，祭司们就常常进入第一间会幕行事奉礼。", version_id: 6},
  {id: 162131, book: "Heb", verse: "9.007", unformatted: "至于第二间会幕，只有大祭司一年进去一次，没有不带着血的；这血是为自己和子民无意中犯的罪所献上的。", version_id: 6},
  {id: 162132, book: "Heb", verse: "9.008", unformatted: "圣灵指明这一点：只要第一间会幕还存在，进入至圣所的道路就还没有显明出来。", version_id: 6},
  {id: 162133, book: "Heb", verse: "9.009", unformatted: "这会幕是现今时代的一个象征，在这里所献上的礼物和祭物，并不能使事奉者的良心得以完全，", version_id: 6},
  {id: 162134, book: "Heb", verse: "9.01", unformatted: "因为这些只是关于饮食和各种洗净礼，是属肉体的规定，一直实施到更新的时候为止。", version_id: 6},
  {id: 162135, book: "Heb", verse: "9.011", unformatted: "新约的事奉\n不过基督已经来到，做了那些已经发生的美善之事的大祭司，经过了那更大、更完美的会幕。那会幕不是人手所造的；就是说，不属于这被造的世界。", version_id: 6},
  {id: 162136, book: "Heb", verse: "9.012", unformatted: "他也没有藉着山羊和牛犊的血，而是藉着他自己的血，一次性地进入圣所，成就了永恒的救赎。", version_id: 6},
  {id: 162137, book: "Heb", verse: "9.013", unformatted: "实际上，如果山羊和公牛的血，以及母牛犊的灰，洒在那些污秽的人身上，能使他们分别为圣、肉身洁净，", version_id: 6},
  {id: 162138, book: "Heb", verse: "9.014", unformatted: "更何况基督的血呢！基督藉着永恒的圣灵，将自己毫无瑕疵地献给神，他的血难道不能洁净我们的良心脱离致死的行为，使我们事奉永生的神吗？", version_id: 6},
  {id: 162139, book: "Heb", verse: "9.015", unformatted: "为此，基督做了新约的中保，藉着死赎清了人在第一个约之下的过犯，好使那些蒙召的人可以得到所应许的永恒继业。", version_id: 6},
  {id: 162140, book: "Heb", verse: "9.016", unformatted: "要知道，遗嘱在哪里生效，立遗嘱之人的死亡就必须得到证实。", version_id: 6},
  {id: 162141, book: "Heb", verse: "9.017", unformatted: "事实上，人死了，遗嘱才能确立，因为立遗嘱之人活着的时候，遗嘱绝对不能生效。", version_id: 6},
  {id: 162142, book: "Heb", verse: "9.018", unformatted: "原来不用血，第一个约也不能成立，", version_id: 6},
  {id: 162143, book: "Heb", verse: "9.019", unformatted: "就是说，摩西按照律法向全体民众宣讲了所有的诫命之后，拿了牛犊和山羊的血，掺上水，用深红色的羊毛和牛膝草，洒在那书卷上和全体民众身上，", version_id: 6},
  {id: 162144, book: "Heb", verse: "9.02", unformatted: "说：“这是立约的血，是神吩咐给你们的。”", version_id: 6},
  {id: 162145, book: "Heb", verse: "9.021", unformatted: "然后，他又同样地把血洒在会幕和服事用的所有器皿上。", version_id: 6},
  {id: 162146, book: "Heb", verse: "9.022", unformatted: "按照律法，几乎一切都是用血洁净的；没有流血，就没有赦免。", version_id: 6},
  {id: 162147, book: "Heb", verse: "9.023", unformatted: "既然那些诸天之上事物的模型，必须用这些祭物来洁净，那天上事物的本体就必须用比这些更好的祭物来洁净，", version_id: 6},
  {id: 162148, book: "Heb", verse: "9.024", unformatted: "因为基督不是进入了人手所造的圣所——那不过是真圣所的象征——而是进入了上天本身，如今替我们显现在神的面前。", version_id: 6},
  {id: 162149, book: "Heb", verse: "9.025", unformatted: "他也不用再三地将自己献上，像大祭司年年带着牛羊的血进入圣所那样，", version_id: 6},
  {id: 162150, book: "Heb", verse: "9.026", unformatted: "否则，从创世以来，他就必须再三地受难了。但如今，他在万世的末期显现了一次，好藉着献上自己为祭，把罪除掉。", version_id: 6},
  {id: 162151, book: "Heb", verse: "9.027", unformatted: "就如人人注定都有一死，而且死后有审判，", version_id: 6},
  {id: 162152, book: "Heb", verse: "9.028", unformatted: "照样，基督为了担当众人的罪孽，也被献上一次；将来还要再一次显现，不是为了罪，而是为了使那些热切等待他的人得到救恩。", version_id: 6},
  {id: 162153, book: "Heb", verse: "10.001", unformatted: "完美的祭\n既然律法带有那将要来的美好事物的影子，它不是那些事物的本像，就绝不能藉着年年不断地献上同样的祭物，使近前来的人得以完全。", version_id: 6},
  {id: 162154, book: "Heb", verse: "10.002", unformatted: "否则，事奉的人因为一次性地被洁净，不再觉得有罪孽，难道不早就停止献祭了吗？", version_id: 6},
  {id: 162155, book: "Heb", verse: "10.003", unformatted: "然而这些祭物使人年年都想起罪孽来，", version_id: 6},
  {id: 162156, book: "Heb", verse: "10.004", unformatted: "因为公牛和山羊的血，不可能把罪孽除去。", version_id: 6},
  {id: 162157, book: "Heb", verse: "10.005", unformatted: "所以，基督进入世界的时候，他说：\n“神哪，祭物和供物不是你所希望的；\n你为我预备了一个身体。", version_id: 6},
  {id: 162158, book: "Heb", verse: "10.006", unformatted: "燔祭和赎罪祭不是你所喜悦的；", version_id: 6},
  {id: 162159, book: "Heb", verse: "10.007", unformatted: "于是我说：‘看哪，我来了！\n关于我的事，\n经卷上已经记载了。\n神哪，我来是要遵行你的旨意！’”", version_id: 6},
  {id: 162160, book: "Heb", verse: "10.008", unformatted: "上文说：祭物、供物、燔祭和赎罪祭不是你所希望的，也不是你所喜悦的，尽管这些都是照着律法献上的；", version_id: 6},
  {id: 162161, book: "Heb", verse: "10.009", unformatted: "接着又说：“看哪，我来了！是要遵行你的旨意！”神废除前者，是为了确立后者。", version_id: 6},
  {id: 162162, book: "Heb", verse: "10.01", unformatted: "我们凭着这旨意，藉着耶稣基督身体做供物，就已经一次性地被分别为圣了。", version_id: 6},
  {id: 162163, book: "Heb", verse: "10.011", unformatted: "所有的祭司天天都站着服事，再三地献上同样的祭物，但这些祭物绝不能把罪孽除掉，", version_id: 6},
  {id: 162164, book: "Heb", verse: "10.012", unformatted: "而这一位为赎罪孽献上了一个永远的祭物，就在神的右边坐下了，", version_id: 6},
  {id: 162165, book: "Heb", verse: "10.013", unformatted: "此后一直等候他的敌人被放在他的脚下做脚凳。", version_id: 6},
  {id: 162166, book: "Heb", verse: "10.014", unformatted: "事实上，他藉着一个供物，使那些被分别为圣的人永远得以完全。", version_id: 6},
  {id: 162167, book: "Heb", verse: "10.015", unformatted: "圣灵也向我们做见证，因为他说过：", version_id: 6},
  {id: 162168, book: "Heb", verse: "10.016", unformatted: "“主说：‘在那些日子以后，\n我要与他们订立这样的约：\n我要把我的法则放在他们的心上，\n刻在他们的意念中。’”", version_id: 6},
  {id: 162169, book: "Heb", verse: "10.017", unformatted: "然后又说：\n“我绝不再想起他们的罪孽和他们的罪恶。”", version_id: 6},
  {id: 162170, book: "Heb", verse: "10.018", unformatted: "所以在哪里这些罪得了赦免，在哪里就不再有赎罪的供物了。", version_id: 6},
  {id: 162171, book: "Heb", verse: "10.019", unformatted: "敬神的劝勉\n因此，弟兄们，我们藉着耶稣的血，可以坦然无惧地进入至圣所。", version_id: 6},
  {id: 162172, book: "Heb", verse: "10.02", unformatted: "他为我们开辟了一条又新又活的道路，从幔子经过；这幔子就是他的身体。", version_id: 6},
  {id: 162173, book: "Heb", verse: "10.021", unformatted: "既然我们有一位伟大的祭司管理神的家，", version_id: 6},
  {id: 162174, book: "Heb", verse: "10.022", unformatted: "既然我们的心被血洒过，脱离了罪恶感，身体也被清水洗净了，就让我们怀着真诚的心，以确信不移的信仰近前来；", version_id: 6},
  {id: 162175, book: "Heb", verse: "10.023", unformatted: "让我们毫不动摇地持守所告白的盼望，因为向我们应许的那一位是信实的；", version_id: 6},
  {id: 162176, book: "Heb", verse: "10.024", unformatted: "让我们彼此看顾，好激励爱心和美好的工作。", version_id: 6},
  {id: 162177, book: "Heb", verse: "10.025", unformatted: "你们不要放弃自己的聚会，像某些人所习惯的那样，而要彼此鼓励；你们既然看见那日子临近，就更应该这样。", version_id: 6},
  {id: 162178, book: "Heb", verse: "10.026", unformatted: "警告明知故犯的罪\n要知道，我们领受了真理的知识以后，如果还故意犯罪，就不再有赎罪的祭物被保留下来；", version_id: 6},
  {id: 162179, book: "Heb", verse: "10.027", unformatted: "只保留了对审判的可怕等待，和那将要吞灭敌对者的嫉恨之火。", version_id: 6},
  {id: 162180, book: "Heb", verse: "10.028", unformatted: "任何拒绝摩西律法的人，凭两个或三个见证人的见证，就得不到怜悯而死，", version_id: 6},
  {id: 162181, book: "Heb", verse: "10.029", unformatted: "何况一个人践踏了神的儿子，把那使自己成圣的立约之血看做是俗物，又侮辱了恩典的圣灵，你们想想，这个人难道不该受更厉害的惩罚吗？", version_id: 6},
  {id: 162182, book: "Heb", verse: "10.03", unformatted: "因为我们认识曾说过“报应在我，我将回报”的那一位，他又说过“主要审判自己的子民。”", version_id: 6},
  {id: 162183, book: "Heb", verse: "10.031", unformatted: "落在永生神的手中实在可怕。", version_id: 6},
  {id: 162184, book: "Heb", verse: "10.032", unformatted: "你们要回想原先的日子：当你们蒙了光照以后，忍受了许多痛苦挣扎；", version_id: 6},
  {id: 162185, book: "Heb", verse: "10.033", unformatted: "有时候当众受责骂、受患难；有时候陪伴那些受到同样遭遇的人。", version_id: 6},
  {id: 162186, book: "Heb", verse: "10.034", unformatted: "的确，你们不但同情了那些被囚禁的人，而且当自己所拥有的被夺去时，你们也甘心接受了，因为知道你们在天上有更好的、长存的产业。", version_id: 6},
  {id: 162187, book: "Heb", verse: "10.035", unformatted: "所以，不要丢弃你们的确信，这确信带来极大的报偿。", version_id: 6},
  {id: 162188, book: "Heb", verse: "10.036", unformatted: "其实，你们需要忍耐，好在你们行完神的旨意以后，可以领受所应许的。", version_id: 6},
  {id: 162189, book: "Heb", verse: "10.037", unformatted: "要知道，还有一点点时候，\n“要来的那一位就要来临，他不会迟延。", version_id: 6},
  {id: 162190, book: "Heb", verse: "10.038", unformatted: "我的义人将因信而活；\n如果他退缩，\n我的心就不喜悦他。”", version_id: 6},
  {id: 162191, book: "Heb", verse: "10.039", unformatted: "然而，我们不是退缩以致灭亡的人，而是有信仰以致保全灵魂的人。", version_id: 6},
  {id: 162192, book: "Heb", verse: "11.001", unformatted: "信心的伟人\n信，是对所盼望的事有把握，对看不见的事有确据。", version_id: 6},
  {id: 162193, book: "Heb", verse: "11.002", unformatted: "古人因着这信曾受到了赞许。", version_id: 6},
  {id: 162194, book: "Heb", verse: "11.003", unformatted: "因着信，我们明白宇宙是藉着神的话语形成的，从而明白那看得见的事是从看不见的事造出来的。", version_id: 6},
  {id: 162195, book: "Heb", verse: "11.004", unformatted: "因着信，亚伯向神献上的祭物比该隐所献的更好，就藉着信受到了赞许成为义人，因为神赞许他的礼物。他虽然死了，但藉着信仍然说话。", version_id: 6},
  {id: 162196, book: "Heb", verse: "11.005", unformatted: "因着信，以诺被接去而没有经历死亡，人也找不到他，因为神把他接去了。原来他被接去以前，已经蒙神喜悦而受到赞许。", version_id: 6},
  {id: 162197, book: "Heb", verse: "11.006", unformatted: "没有信，就不可能蒙神喜悦，因为来到神面前的人，必须相信神存在，并且相信他报偿那些寻求他的人。", version_id: 6},
  {id: 162198, book: "Heb", verse: "11.007", unformatted: "因着信，挪亚在那还看不见的事上得了神的指示，就动了敬畏的心，建造了一只方舟，以至于他的家人获救。藉着信，他定了世界的罪，自己也继承了从信而来的义。", version_id: 6},
  {id: 162199, book: "Heb", verse: "11.008", unformatted: "因着信，亚伯拉罕蒙召的时候，就顺从出发，往他将要得为继业的地方去。他出发的时候，还不知道往哪里去。", version_id: 6},
  {id: 162200, book: "Heb", verse: "11.009", unformatted: "因着信，他做为外人客居在应许之地，住在帐幕里，与同一个应许的共同继承人以撒、雅各一样，", version_id: 6},
  {id: 162201, book: "Heb", verse: "11.01", unformatted: "因为他等待着那座有根基的城；那城是神所设计、所建造的。", version_id: 6},
  {id: 162202, book: "Heb", verse: "11.011", unformatted: "同样因着信，不能生育的撒拉，自己虽然过了年龄，还是领受了怀孕生子的能力，原来她认为向她应许的那一位是信实的。", version_id: 6},
  {id: 162203, book: "Heb", verse: "11.012", unformatted: "所以从一个如同已经死了的人，就生出了许多人，像天空的星那样众多，像海边的沙那样无法数算。", version_id: 6},
  {id: 162204, book: "Heb", verse: "11.013", unformatted: "这些人都怀着信仰死去，没有得到那些所应许的，却远远地看见并且欢迎它们，又承认自己在地上是异乡人，是寄居的。", version_id: 6},
  {id: 162205, book: "Heb", verse: "11.014", unformatted: "实际上，说这样话的人，是表明他们在寻求一个家乡。", version_id: 6},
  {id: 162206, book: "Heb", verse: "11.015", unformatted: "如果他们真是想念自己所离开的地方，他们还有机会回去；", version_id: 6},
  {id: 162207, book: "Heb", verse: "11.016", unformatted: "但如今，他们所向往的是一个更美好的，就是在天上的家乡。因此，神被称为“他们的神”并不以为耻；原来他已经为他们预备了一座城。", version_id: 6},
  {id: 162208, book: "Heb", verse: "11.017", unformatted: "因着信，亚伯拉罕在受考验的时候，献上了以撒；这位领受了各样应许的人，献上了自己的独生儿子——", version_id: 6},
  {id: 162209, book: "Heb", verse: "11.018", unformatted: "关于这儿子，神曾经说过：“从以撒生的，将被称为你的后裔。”", version_id: 6},
  {id: 162210, book: "Heb", verse: "11.019", unformatted: "亚伯拉罕认为，神真的能使人从死人中复活。故此，用比喻来说，他真的从死人中得回了以撒。", version_id: 6},
  {id: 162211, book: "Heb", verse: "11.02", unformatted: "同样因着信，以撒指着将来的事祝福了雅各和以扫。", version_id: 6},
  {id: 162212, book: "Heb", verse: "11.021", unformatted: "因着信，雅各临死的时候，祝福了约瑟的每个儿子，又倚着自己的杖头敬拜神。", version_id: 6},
  {id: 162213, book: "Heb", verse: "11.022", unformatted: "因着信，约瑟临终的时候，提到以色列子民出埃及的事，也为自己的骸骨留下了遗言。", version_id: 6},
  {id: 162214, book: "Heb", verse: "11.023", unformatted: "因着信，摩西出生以后，被父母藏了三个月，原来他们看见孩子俊美；他们不怕王的命令。", version_id: 6},
  {id: 162215, book: "Heb", verse: "11.024", unformatted: "因着信，摩西长大以后，拒绝被称为法老女儿的儿子。", version_id: 6},
  {id: 162216, book: "Heb", verse: "11.025", unformatted: "他宁愿选择与神的子民同受虐待，也不愿享受暂时的罪中之乐。", version_id: 6},
  {id: 162217, book: "Heb", verse: "11.026", unformatted: "他把为基督受的责骂看做是比埃及的财宝更大的财富，因为他注目那将来的报偿。", version_id: 6},
  {id: 162218, book: "Heb", verse: "11.027", unformatted: "因着信，他不怕王的愤怒，离开了埃及，因为他恒心忍耐，如同看见了不可见的那一位。", version_id: 6},
  {id: 162219, book: "Heb", verse: "11.028", unformatted: "因着信，他立了逾越节和洒血的礼，免得那毁灭长子的碰以色列人。", version_id: 6},
  {id: 162220, book: "Heb", verse: "11.029", unformatted: "因着信，他们走过了红海，像走过干地那样；埃及人试着这样做，就被吞灭了。", version_id: 6},
  {id: 162221, book: "Heb", verse: "11.03", unformatted: "因着信，他们围绕耶利哥城七天，城墙就倒塌了。", version_id: 6},
  {id: 162222, book: "Heb", verse: "11.031", unformatted: "因着信，妓女瑞荷在平安中接纳了侦察的人，没有和那些不肯信从的人一同灭亡。", version_id: 6},
  {id: 162223, book: "Heb", verse: "11.032", unformatted: "我还要再说什么呢？如果我一一述说基甸、巴拉克、参孙、耶弗达、大卫、撒母耳和先知们的事，时间就不够了。", version_id: 6},
  {id: 162224, book: "Heb", verse: "11.033", unformatted: "他们藉着信，征服了列国，行了公义，获得了那些应许；他们堵住了狮子的口，", version_id: 6},
  {id: 162225, book: "Heb", verse: "11.034", unformatted: "灭了烈火的威力，逃脱了刀剑的锋刃，从软弱中得以刚强，在战争中得以强大，击败了外国的军队。", version_id: 6},
  {id: 162226, book: "Heb", verse: "11.035", unformatted: "有些妇女得回了那些从死人中复活的亲人；有些人受酷刑至死也不肯接受释放，为要得到更美好的复活；", version_id: 6},
  {id: 162227, book: "Heb", verse: "11.036", unformatted: "另有些人经受了戏弄、鞭打的试炼；还有些人受了捆锁、监禁的试炼；", version_id: 6},
  {id: 162228, book: "Heb", verse: "11.037", unformatted: "他们被石头砸死，被锯成两截，被刀剑杀死，披着绵羊、山羊的皮四处漂泊，遭受穷困、患难和虐待。", version_id: 6},
  {id: 162229, book: "Heb", verse: "11.038", unformatted: "他们是这世界不配有的。他们在旷野、山岭、岩穴、地洞中漂流无定。", version_id: 6},
  {id: 162230, book: "Heb", verse: "11.039", unformatted: "这些人虽然都藉着信受到了赞许，还是没有领受到所应许的，", version_id: 6},
  {id: 162231, book: "Heb", verse: "11.04", unformatted: "因为神为我们预备了更美好的事，使他们没有我们，就不能得以完全。", version_id: 6},
  {id: 162232, book: "Heb", verse: "12.001", unformatted: "要坚忍到底\n因此，我们既然有这么一大群见证人，如同云彩围绕着我们，就让我们脱去各样的重担和容易缠累我们的罪，藉着忍耐去跑那摆在我们前面的赛程，", version_id: 6},
  {id: 162233, book: "Heb", verse: "12.002", unformatted: "仰望信仰的创始者和成终者耶稣。他为了那摆在他前面的喜乐，就轻看羞辱，忍受了十字架，如今坐在神宝座的右边。", version_id: 6},
  {id: 162234, book: "Heb", verse: "12.003", unformatted: "为父的管教\n其实，你们应当仔细地思想耶稣如何忍受了罪人的顶撞，这样你们心里就不会因丧气而疲倦。", version_id: 6},
  {id: 162235, book: "Heb", verse: "12.004", unformatted: "你们与罪争战，还没有抵抗到流血的地步。", version_id: 6},
  {id: 162236, book: "Heb", verse: "12.005", unformatted: "难道你们已经忘记那安慰的话吗？就是他对你们如同对儿女那样讲论的话：\n“我儿，你不可轻看主的管教；\n被他责备的时候，也不可丧气。", version_id: 6},
  {id: 162237, book: "Heb", verse: "12.006", unformatted: "因为主所爱的人，主都管教；\n他所接纳的每一个儿子，他都鞭打。”", version_id: 6},
  {id: 162238, book: "Heb", verse: "12.007", unformatted: "对管教的事，你们要忍受；神对待你们，就像对待儿女那样。到底哪一个儿子不受父亲的管教呢？", version_id: 6},
  {id: 162239, book: "Heb", verse: "12.008", unformatted: "大家都有份受管教；你们如果没有受管教，那么，你们就是私生子，不是儿子了。", version_id: 6},
  {id: 162240, book: "Heb", verse: "12.009", unformatted: "再者，我们有肉身的父亲来管教我们，我们尚且敬重他们，何况对这位万灵之父，我们难道不更要服从他而活着吗？", version_id: 6},
  {id: 162241, book: "Heb", verse: "12.01", unformatted: "肉身的父亲在短暂的日子里，固然照着自己的意思来管教我们；但万灵之父是为了我们的益处管教我们，好使我们在他的圣洁上有份。", version_id: 6},
  {id: 162242, book: "Heb", verse: "12.011", unformatted: "不过所有的管教，当时虽然不觉得是愉快的事，反觉得是忧伤的事，而后来却为那些由此受到操练的人，结出平安和义行的果子。", version_id: 6},
  {id: 162243, book: "Heb", verse: "12.012", unformatted: "因此，你们要把发软的手、发酸的腿伸直吧！", version_id: 6},
  {id: 162244, book: "Heb", verse: "12.013", unformatted: "也要为你们的脚把道路修直，好使瘸腿的不至扭脚，反得痊愈。", version_id: 6},
  {id: 162245, book: "Heb", verse: "12.014", unformatted: "警告弃绝神恩典的人\n你们要追求与众人和睦，要追求圣洁。任何不圣洁的人，都不能见到主。", version_id: 6},
  {id: 162246, book: "Heb", verse: "12.015", unformatted: "你们要谨慎，免得有人亏缺了神的恩典；免得有苦毒的根长起来搅扰你们，使许多人因此被污秽了；", version_id: 6},
  {id: 162247, book: "Heb", verse: "12.016", unformatted: "又免得有淫乱的，或像以扫那样不敬神的。以扫为了一点食物，就把自己长子的名份卖了；", version_id: 6},
  {id: 162248, book: "Heb", verse: "12.017", unformatted: "你们知道，后来他想继承祝福，却被弃绝了，虽然他流着泪水寻求，还是找不到悔改的机会。", version_id: 6},
  {id: 162249, book: "Heb", verse: "12.018", unformatted: "你们并不是来到了摸得着的山、燃烧着的火、阴云、幽暗、风暴、", version_id: 6},
  {id: 162250, book: "Heb", verse: "12.019", unformatted: "号角的响声和说话的声音面前——那些听见这声音的人，都恳求不要再向他们说话了，", version_id: 6},
  {id: 162251, book: "Heb", verse: "12.02", unformatted: "因为他们担当不了那吩咐他们的话：“即使是野兽触摸这山，也要用石头砸死。”", version_id: 6},
  {id: 162252, book: "Heb", verse: "12.021", unformatted: "那景象实在可怕，连摩西也说：“我非常害怕战兢。”", version_id: 6},
  {id: 162253, book: "Heb", verse: "12.022", unformatted: "相反，你们却是来到了锡安山、永生神的城、天上的耶路撒冷面前；来到了千万天使的盛会面前；", version_id: 6},
  {id: 162254, book: "Heb", verse: "12.023", unformatted: "来到了长子们的教会面前——他们的名字已经登记在天上；来到了神面前——他是万人的审判者；来到了义人的灵魂面前——他们得以完全；", version_id: 6},
  {id: 162255, book: "Heb", verse: "12.024", unformatted: "来到了耶稣面前——他是新约的中保；来到了所洒的血面前——这血比亚伯的血所述说的更美好。", version_id: 6},
  {id: 162256, book: "Heb", verse: "12.025", unformatted: "你们要当心，不要拒绝向你们说话的那一位。要知道，那些拒绝了在地上警告他们的，尚且无法逃脱惩罚，更何况我们背弃从天上警告我们的那一位呢！", version_id: 6},
  {id: 162257, book: "Heb", verse: "12.026", unformatted: "那时候，他的声音震动了大地，但如今他应许说：“我不仅要再一次震动地，而且还要震动天。”", version_id: 6},
  {id: 162258, book: "Heb", verse: "12.027", unformatted: "“再一次”这句话表明了那些能够被震动的，就如被造之物，都要被挪去，好使那些不能被震动的可以存留。", version_id: 6},
  {id: 162259, book: "Heb", verse: "12.028", unformatted: "因此，我们既然在领受一个不能被震动的国度，就让我们怀有感恩的心，藉此以虔诚和敬畏，照着神所喜悦的来事奉他。", version_id: 6},
  {id: 162260, book: "Heb", verse: "12.029", unformatted: "要知道，我们的神实在是吞噬的烈火。", version_id: 6},
  {id: 162261, book: "Heb", verse: "13.001", unformatted: "结尾的劝勉\n你们当常存弟兄之爱。", version_id: 6},
  {id: 162262, book: "Heb", verse: "13.002", unformatted: "不要忘记款待客旅，因为曾经有些人这样做，在无意中就招待了天使。", version_id: 6},
  {id: 162263, book: "Heb", verse: "13.003", unformatted: "你们要顾念那些被囚禁的人，好像与他们一同被囚禁；要记念那些受虐待的人，好像你们自己也是在肉体中受虐待。", version_id: 6},
  {id: 162264, book: "Heb", verse: "13.004", unformatted: "婚姻当受所有人尊重，床也不可玷污，因为神将要审判淫乱和通奸的人。", version_id: 6},
  {id: 162265, book: "Heb", verse: "13.005", unformatted: "你们行事为人不要爱钱财，要以现有的为满足，因为神自己说过：“我绝不撇下你，也绝不离弃你。”", version_id: 6},
  {id: 162266, book: "Heb", verse: "13.006", unformatted: "所以，我们可以满怀勇气地说：\n“主是我的帮助，\n我就不惧怕，\n人能把我怎么样呢？”", version_id: 6},
  {id: 162267, book: "Heb", verse: "13.007", unformatted: "你们当记住那些带领你们、把神的话语传给你们的人；要仔细观察他们行事为人的结果，效法他们的信仰。", version_id: 6},
  {id: 162268, book: "Heb", verse: "13.008", unformatted: "耶稣基督昨天、今天、直到永远，都是一样的。", version_id: 6},
  {id: 162269, book: "Heb", verse: "13.009", unformatted: "你们不要被各种怪异的教导引入歧途，因为人的心靠恩典，不靠食物规定得以确立才是好的；那靠食物规定而行事的人，从来没有得到什么益处。", version_id: 6},
  {id: 162270, book: "Heb", verse: "13.01", unformatted: "我们有一个祭坛，这坛上的祭物，是那些在会幕中事奉的人没有权利吃的。", version_id: 6},
  {id: 162271, book: "Heb", verse: "13.011", unformatted: "原来动物的血由大祭司带进圣所，是为了赎罪，而动物的身体却在营地外面被烧掉；", version_id: 6},
  {id: 162272, book: "Heb", verse: "13.012", unformatted: "所以耶稣为了藉着自己的血使子民分别为圣，也在城门外受难。", version_id: 6},
  {id: 162273, book: "Heb", verse: "13.013", unformatted: "因此，让我们到营地外面他那里去，担当他所受的责骂。", version_id: 6},
  {id: 162274, book: "Heb", verse: "13.014", unformatted: "因为在这里，我们没有长存的城；相反，我们是在寻求那将要来临的城。", version_id: 6},
  {id: 162275, book: "Heb", verse: "13.015", unformatted: "这样，让我们藉着耶稣，常常以颂赞为祭物献给神，这祭物就是承认他名的嘴唇所结的果子。", version_id: 6},
  {id: 162276, book: "Heb", verse: "13.016", unformatted: "你们不可忘记行善和分享，因为这样的祭物是蒙神喜悦的。", version_id: 6},
  {id: 162277, book: "Heb", verse: "13.017", unformatted: "你们要信任那些带领你们的人，要服从他们，因为他们做为要交账的人，要为你们的灵魂时刻警醒。你们要让他们能怀着喜乐的心做这事，而不至叹息，因为让他们叹息，对你们并没有益处。", version_id: 6},
  {id: 162278, book: "Heb", verse: "13.018", unformatted: "请为我们祷告；因为我们深信自己有无愧的良心，愿意在一切事上行事正直。", version_id: 6},
  {id: 162279, book: "Heb", verse: "13.019", unformatted: "我特别请求你们祷告，好让我能快一点回到你们那里。", version_id: 6},
  {id: 162280, book: "Heb", verse: "13.02", unformatted: "问候与祝福\n愿赐平安的神，就是藉着永恒之约的血，把群羊的大牧人我们的主耶稣从死人中领上来的那一位，", version_id: 6},
  {id: 162281, book: "Heb", verse: "13.021", unformatted: "在一切美善之事上使你们完备，好遵行他的旨意。愿神藉着耶稣基督，在我们里面行他看为喜悦的事。愿荣耀归于他，直到永永远远！阿们。", version_id: 6},
  {id: 162282, book: "Heb", verse: "13.022", unformatted: "弟兄们，我恳求你们容忍我这劝勉的话，因为我只是简略地写信给你们。", version_id: 6},
  {id: 162283, book: "Heb", verse: "13.023", unformatted: "你们要知道，我们的弟兄提摩太已经被释放了。如果他能快一点来，我就与他一起去看你们。", version_id: 6},
  {id: 162284, book: "Heb", verse: "13.024", unformatted: "请问候所有带领你们的人和所有的圣徒。从意大利来的那些人也问候你们。", version_id: 6},
  {id: 162285, book: "Heb", verse: "13.025", unformatted: "愿恩典与你们大家同在！", version_id: 6},
  {id: 162286, book: "Jas", verse: "1.001", unformatted: "问安\n神和主耶稣基督的奴仆雅各，\n问候散居在各地的十二个支派。", version_id: 6},
  {id: 162287, book: "Jas", verse: "1.002", unformatted: "经试炼而完备\n我的弟兄们，你们遇到各种试炼的时候，应当看做是极大的喜乐，", version_id: 6},
  {id: 162288, book: "Jas", verse: "1.003", unformatted: "因为你们知道，你们的信仰经过考验就生出忍耐；", version_id: 6},
  {id: 162289, book: "Jas", verse: "1.004", unformatted: "但要让忍耐发挥完全的功效，好使你们成熟、完备，在任何事上都没有缺欠。", version_id: 6},
  {id: 162290, book: "Jas", verse: "1.005", unformatted: "如果你们中间有人缺乏智慧，他就应当向那慷慨赐予万人又不责骂人的神祈求；神就会赐予他。", version_id: 6},
  {id: 162291, book: "Jas", verse: "1.006", unformatted: "不过他要凭着信仰祈求，不要有任何疑惑，因为疑惑的人就像海中的波浪，被风吹动翻腾。", version_id: 6},
  {id: 162292, book: "Jas", verse: "1.007", unformatted: "这样的人不要想从主那里得到什么，", version_id: 6},
  {id: 162293, book: "Jas", verse: "1.008", unformatted: "他是心怀二意的人，在他所有的道路上都摇摆不定。", version_id: 6},
  {id: 162294, book: "Jas", verse: "1.009", unformatted: "卑微的弟兄应当以自己被高举而夸耀；", version_id: 6},
  {id: 162295, book: "Jas", verse: "1.01", unformatted: "富有的应当以自己被降卑而夸耀，因为他将要像草上的花那样消逝。", version_id: 6},
  {id: 162296, book: "Jas", verse: "1.011", unformatted: "太阳带着热气升起，草地枯萎，草上的花也凋谢，它表面的美丽也就消逝了；照样，富有的人在他的追求中，也将衰残。", version_id: 6},
  {id: 162297, book: "Jas", verse: "1.012", unformatted: "忍受试炼的人是蒙福的；因为他经过考验以后，就要得到生命的冠冕，就是主应许给那些爱他之人的。", version_id: 6},
  {id: 162298, book: "Jas", verse: "1.013", unformatted: "人受诱惑的时候，不可说：“我被神诱惑”，因为神是不能被邪恶诱惑的，神也不诱惑任何人。", version_id: 6},
  {id: 162299, book: "Jas", verse: "1.014", unformatted: "而每个人受诱惑，都是被自己的欲望所牵扯、所引诱的；", version_id: 6},
  {id: 162300, book: "Jas", verse: "1.015", unformatted: "一旦欲望怀了胎，就生下罪来；罪长成了，就生出死亡。", version_id: 6},
  {id: 162301, book: "Jas", verse: "1.016", unformatted: "我亲爱的弟兄们，你们不要被迷惑了！", version_id: 6},
  {id: 162302, book: "Jas", verse: "1.017", unformatted: "一切美好的赏赐和各样完美的恩赐，都是从上面、从众光之父降下来的；在他没有改变，也没有转动的影子。", version_id: 6},
  {id: 162303, book: "Jas", verse: "1.018", unformatted: "他照着自己的旨意，藉着真理的话语生了我们，要使我们在他所造的万物中成为一种初熟的果子。", version_id: 6},
  {id: 162304, book: "Jas", verse: "1.019", unformatted: "行道胜于听道\n我亲爱的弟兄们，你们应当知道：每个人都该快快地听，不急于发言、不急于动怒，", version_id: 6},
  {id: 162305, book: "Jas", verse: "1.02", unformatted: "因为人的愤怒不能成就神的义。", version_id: 6},
  {id: 162306, book: "Jas", verse: "1.021", unformatted: "因此，你们要脱去一切的污秽和满盈的恶毒，要以温柔的心接受那栽种在你们里面的话语，就是能拯救你们灵魂的话语。", version_id: 6},
  {id: 162307, book: "Jas", verse: "1.022", unformatted: "你们要成为这话语的实行者，不要只成为听者而欺骗自己。", version_id: 6},
  {id: 162308, book: "Jas", verse: "1.023", unformatted: "因为，如果有人是这话语的听者，而不是实行者，这个人就像人对着镜子观看自己生来的面貌，", version_id: 6},
  {id: 162309, book: "Jas", verse: "1.024", unformatted: "看了就走开，立刻忘了自己是什么样子；", version_id: 6},
  {id: 162310, book: "Jas", verse: "1.025", unformatted: "但是详细察看那使人得自由的完美律法，并且继续持守的人，因他不是健忘的听者，而是行为的实行者，这个人就将要在他所行的事上蒙祝福。", version_id: 6},
  {id: 162311, book: "Jas", verse: "1.026", unformatted: "如果有人自以为是虔诚的，却不克制自己的舌头，反而欺骗自己的心，这个人的虔诚就是虚妄的。", version_id: 6},
  {id: 162312, book: "Jas", verse: "1.027", unformatted: "在父神看来，纯洁而没有玷污的虔诚是这样的：照顾患难中的孤儿寡妇，并且保守自己不受世界的玷污。", version_id: 6},
  {id: 162313, book: "Jas", verse: "2.001", unformatted: "不可偏待人\n我的弟兄们，你们应当持守对我们荣耀的主耶稣基督的信仰，不要怀着偏待人的心。", version_id: 6},
  {id: 162314, book: "Jas", verse: "2.002", unformatted: "如果有一个戴着金戒指、穿着华丽衣服的人进了你们的会堂，又有一个穿着肮脏衣服的穷人也进来了，", version_id: 6},
  {id: 162315, book: "Jas", verse: "2.003", unformatted: "你们却看重那穿华丽衣服的人，对他说“请你坐在这好位子上”，而对那穷人说“你站在那里”，或说“坐在我的脚凳下边”；", version_id: 6},
  {id: 162316, book: "Jas", verse: "2.004", unformatted: "难道你们中间不就有了歧视，而你们也成了心怀恶念的审判官吗？", version_id: 6},
  {id: 162317, book: "Jas", verse: "2.005", unformatted: "我亲爱的弟兄们，请听！神难道不是拣选了世上的那些贫穷人，使他们在信仰上富有，并且做他国度的继承人吗？这国度是他应许给爱他之人的。", version_id: 6},
  {id: 162318, book: "Jas", verse: "2.006", unformatted: "但你们却侮辱了这贫穷人！那欺压你们、把你们拉上法庭的，难道不是那些富有的人吗？", version_id: 6},
  {id: 162319, book: "Jas", verse: "2.007", unformatted: "那亵渎你们所敬奉的美好之名的，难道不是他们吗？", version_id: 6},
  {id: 162320, book: "Jas", verse: "2.008", unformatted: "不过，你们如果照着经上那条至尊的律法“要爱邻如己”完全实行，你们就做得很好了。", version_id: 6},
  {id: 162321, book: "Jas", verse: "2.009", unformatted: "但如果你们偏待人，就是犯罪，并被律法指证是违犯律法的人。", version_id: 6},
  {id: 162322, book: "Jas", verse: "2.01", unformatted: "因为那遵守整个律法的人，只在一条上跌倒，就成了干犯所有律法的了。", version_id: 6},
  {id: 162323, book: "Jas", verse: "2.011", unformatted: "原来那位说过“不可通奸”的，也说过“不可杀人”。你即使不通奸，却杀人，你还是成了违犯律法的人。", version_id: 6},
  {id: 162324, book: "Jas", verse: "2.012", unformatted: "既然你们将要照着那使人得自由的律法受审判，就应当照此说话、行事。", version_id: 6},
  {id: 162325, book: "Jas", verse: "2.013", unformatted: "要知道，那不施怜悯的人，要受毫无怜悯的审判；怜悯胜过审判。", version_id: 6},
  {id: 162326, book: "Jas", verse: "2.014", unformatted: "信仰和行为\n我的弟兄们，如果有人说自己有信仰，但却没有行为，这有什么好处呢？难道这种信仰能救他吗？", version_id: 6},
  {id: 162327, book: "Jas", verse: "2.015", unformatted: "如果有弟兄或姐妹衣不蔽体，又缺乏日用的食物，", version_id: 6},
  {id: 162328, book: "Jas", verse: "2.016", unformatted: "而你们当中有人对他们说：“平平安安地去吧！愿你们穿得暖、吃得饱！”，却不给他们身体所需要的，这有什么好处呢？", version_id: 6},
  {id: 162329, book: "Jas", verse: "2.017", unformatted: "信仰也是这样：如果没有行为，这信仰就是死的。", version_id: 6},
  {id: 162330, book: "Jas", verse: "2.018", unformatted: "不过有人会说：“你有信仰；我有行为。”请给我看看你没有行为的信仰，我也本于自己的行为给你看看我的信仰。", version_id: 6},
  {id: 162331, book: "Jas", verse: "2.019", unformatted: "你相信神只有一位，你就做得很好！连鬼魔也相信，并且恐惧战兢。", version_id: 6},
  {id: 162332, book: "Jas", verse: "2.02", unformatted: "唉，你这虚空的人哪！你想知道没有行为的信仰是无用的吗？", version_id: 6},
  {id: 162333, book: "Jas", verse: "2.021", unformatted: "我们的先祖亚伯拉罕把他的儿子以撒献在祭坛上，难道不是本于行为被称为义吗？", version_id: 6},
  {id: 162334, book: "Jas", verse: "2.022", unformatted: "你看，信仰和他的行为相辅相成，而且信仰是本于行为得以完全的。", version_id: 6},
  {id: 162335, book: "Jas", verse: "2.023", unformatted: "这就应验了经上所说的：“亚伯拉罕信神，这就被算为他的义。”他又被称为“神的朋友”。", version_id: 6},
  {id: 162336, book: "Jas", verse: "2.024", unformatted: "可见人被称为义是本于行为，不仅是本于信仰。", version_id: 6},
  {id: 162337, book: "Jas", verse: "2.025", unformatted: "同样，妓女瑞荷接待了那些使者，又从另一条路把他们送走，难道不也是本于行为被称为义吗？", version_id: 6},
  {id: 162338, book: "Jas", verse: "2.026", unformatted: "要知道，就像身体没有灵魂是死的，照样，信仰没有行为也是死的。", version_id: 6},
  {id: 162339, book: "Jas", verse: "3.001", unformatted: "要控制舌头\n我的弟兄们，你们不应该有很多人做教师，因为你们知道，我们做教师的将受更严厉的审判。", version_id: 6},
  {id: 162340, book: "Jas", verse: "3.002", unformatted: "实际上，我们每一个人在很多事上都会跌倒。如果有人在言语上不跌倒，这个人就是一个成熟的人，也能够克制全身。", version_id: 6},
  {id: 162341, book: "Jas", verse: "3.003", unformatted: "我们如果把嚼子放在马嘴里，使它们服从，就可以驾驭它们的全身。", version_id: 6},
  {id: 162342, book: "Jas", verse: "3.004", unformatted: "再看船只，虽然它那么大，又被狂风吹逐，但是无论舵手的意图想去哪里，它都能被小小的舵来驾驭。", version_id: 6},
  {id: 162343, book: "Jas", verse: "3.005", unformatted: "照样，舌头虽然是身体上一个小小的部分，却能大大地自夸。看哪，那么小的火，能点燃那么大的森林！", version_id: 6},
  {id: 162344, book: "Jas", verse: "3.006", unformatted: "舌头是火，它在我们身体的各部分中，是个不公义的世界；它玷污全身，焚烧人生的路程，而且自己却被地狱焚烧。", version_id: 6},
  {id: 162345, book: "Jas", verse: "3.007", unformatted: "原来，各类野兽、飞禽、爬行动物和水族都能被制伏，也已经被人类制伏了，", version_id: 6},
  {id: 162346, book: "Jas", verse: "3.008", unformatted: "可是没有人能制伏舌头；它是不止息的邪恶，充满着致死的毒。", version_id: 6},
  {id: 162347, book: "Jas", verse: "3.009", unformatted: "我们用这舌头颂赞我们的主——我们的父，又用这舌头来诅咒照着神的形像被造的人。", version_id: 6},
  {id: 162348, book: "Jas", verse: "3.01", unformatted: "颂赞和诅咒从同一张嘴里出来！我的弟兄们，这些事不应该如此！", version_id: 6},
  {id: 162349, book: "Jas", verse: "3.011", unformatted: "难道从同一个洞里涌出来的泉源，能又甜又苦吗？", version_id: 6},
  {id: 162350, book: "Jas", verse: "3.012", unformatted: "我的弟兄们，难道无花果树能结出橄榄，葡萄树能结出无花果吗？咸水也不能产生甜水。", version_id: 6},
  {id: 162351, book: "Jas", verse: "3.013", unformatted: "属天的智慧\n到底你们中间谁是有智慧、有见识的呢？他就应当藉着良好的品行，以智慧的温柔，使自己的行为显明出来；", version_id: 6},
  {id: 162352, book: "Jas", verse: "3.014", unformatted: "但如果你们心里怀着苦毒的嫉妒和争竞，就不要自夸、说谎而抵挡真理。", version_id: 6},
  {id: 162353, book: "Jas", verse: "3.015", unformatted: "这种智慧不是从上面下来的，而是属地的，属血气的，属鬼魔的。", version_id: 6},
  {id: 162354, book: "Jas", verse: "3.016", unformatted: "要知道，哪里有嫉妒和争竞，哪里就有混乱和各样的恶事。", version_id: 6},
  {id: 162355, book: "Jas", verse: "3.017", unformatted: "至于从上面来的智慧，首先是纯洁的，然后是和平的、谦和的、温顺的，满有怜悯和美好的果子的，是不偏私、不虚假的；", version_id: 6},
  {id: 162356, book: "Jas", verse: "3.018", unformatted: "并且义行的果子，是由那些缔造和平的人，在和平中被种下的。", version_id: 6},
  {id: 162357, book: "Jas", verse: "4.001", unformatted: "骄傲与谦卑\n你们中间的冲突是从哪里来的？争执是从哪里来的？难道不是从你们身体各部分中交战的私欲而来的吗？", version_id: 6},
  {id: 162358, book: "Jas", verse: "4.002", unformatted: "你们渴望而不能拥有，就杀人；你们嫉妒而不能获得，就争斗、作战。你们不能拥有，是因为你们不求；", version_id: 6},
  {id: 162359, book: "Jas", verse: "4.003", unformatted: "你们求也不能得到，是因为你们求的动机不对，为了要花费在自己的私欲上。", version_id: 6},
  {id: 162360, book: "Jas", verse: "4.004", unformatted: "你们这些淫乱的人哪！难道你们不知道，与世界为友，就是与神为敌吗？所以，如果有人想要和世界做朋友，他就成了神的仇敌！", version_id: 6},
  {id: 162361, book: "Jas", verse: "4.005", unformatted: "或者，你们以为经上所说的“神使圣灵住在我们里面，他爱我们到嫉妒的地步”是空话吗？", version_id: 6},
  {id: 162362, book: "Jas", verse: "4.006", unformatted: "不过他赐下更大的恩典，所以经上说：\n“神抵挡骄傲的人，\n赐恩典给谦卑的人。”", version_id: 6},
  {id: 162363, book: "Jas", verse: "4.007", unformatted: "因此，你们要服从神，要抵挡魔鬼，魔鬼就会逃避你们。", version_id: 6},
  {id: 162364, book: "Jas", verse: "4.008", unformatted: "你们要亲近神，神就会亲近你们。你们这些罪人哪，当使你们的手洁净！心怀二意的人哪，当使你们的心纯洁！", version_id: 6},
  {id: 162365, book: "Jas", verse: "4.009", unformatted: "你们应当难过、悲伤、痛哭；把你们的欢笑变为悲伤，快乐变为忧闷。", version_id: 6},
  {id: 162366, book: "Jas", verse: "4.01", unformatted: "你们当在主面前降卑，他就会高举你们。", version_id: 6},
  {id: 162367, book: "Jas", verse: "4.011", unformatted: "弟兄们，你们不可彼此诽谤。诽谤弟兄或评断自己弟兄的，就是诽谤律法、评断律法。如果你评断律法，你就不是实行律法的人，而是个审判者了。", version_id: 6},
  {id: 162368, book: "Jas", verse: "4.012", unformatted: "那设立律法的和审判者，只有一位；他就是既能拯救又能毁灭的那一位。而你到底是谁，竟然评断邻人呢？", version_id: 6},
  {id: 162369, book: "Jas", verse: "4.013", unformatted: "不要自夸\n现在来吧！你们这些人哪，你们说“今天或明天，我们要到某某城去，要在那里住上一年，做生意赚钱。”", version_id: 6},
  {id: 162370, book: "Jas", verse: "4.014", unformatted: "你们不知道明天是什么、生命是什么！你们不过是一阵云雾，出现片刻，随后就消失了。", version_id: 6},
  {id: 162371, book: "Jas", verse: "4.015", unformatted: "你们倒应该说：“主如果愿意，我们就将活着，做这事或做那事。”", version_id: 6},
  {id: 162372, book: "Jas", verse: "4.016", unformatted: "但如今，你们在自己的骄傲中夸耀。凡是这样的夸耀，都是邪恶的。", version_id: 6},
  {id: 162373, book: "Jas", verse: "4.017", unformatted: "所以，那知道该行美善的事而不去行的，对他来说，这就是罪了。", version_id: 6},
  {id: 162374, book: "Jas", verse: "5.001", unformatted: "警告富有的人\n现在来吧，你们这些富有的人哪！应当为那些将要临到你们的惨祸而痛哭、哀号！", version_id: 6},
  {id: 162375, book: "Jas", verse: "5.002", unformatted: "你们的财富腐烂了；你们的衣服被虫蛀了；", version_id: 6},
  {id: 162376, book: "Jas", verse: "5.003", unformatted: "你们的金银也生锈了，这锈将成为控告你们的见证，又要像火那样吃你们的肉。在这末后的日子里，你们竟然积蓄财富！", version_id: 6},
  {id: 162377, book: "Jas", verse: "5.004", unformatted: "看哪！工人收割你们的田地，你们所克扣他们的工钱正在呼叫，收割者的冤声也已经达到万军之主的耳中了。", version_id: 6},
  {id: 162378, book: "Jas", verse: "5.005", unformatted: "你们在地上奢侈生活、享乐度日，为宰杀的日子养肥了自己的心！", version_id: 6},
  {id: 162379, book: "Jas", verse: "5.006", unformatted: "你们把义人定了罪、把他杀害了，他没有抗拒你们。", version_id: 6},
  {id: 162380, book: "Jas", verse: "5.007", unformatted: "耐心等候主\n所以弟兄们，你们当恒久忍耐，直到主的来临。看哪，农夫等待着田地里宝贵的果实，为此恒久忍耐，直到果实得着秋雨春雨。", version_id: 6},
  {id: 162381, book: "Jas", verse: "5.008", unformatted: "你们也当恒久忍耐，坚固你们的心，因为主的来临已经近了。", version_id: 6},
  {id: 162382, book: "Jas", verse: "5.009", unformatted: "弟兄们，不要彼此埋怨，免得你们受审判。看哪，审判者已经站在门口了！", version_id: 6},
  {id: 162383, book: "Jas", verse: "5.01", unformatted: "弟兄们，你们要把那些奉主名讲话的先知当做受苦和忍耐的榜样。", version_id: 6},
  {id: 162384, book: "Jas", verse: "5.011", unformatted: "看哪，那些忍耐过的人，我们称他们有福！你们听说过约伯的忍耐，也看见了主所赐给他的结局，知道主是充满怜悯和仁慈的。", version_id: 6},
  {id: 162385, book: "Jas", verse: "5.012", unformatted: "实事求是\n我的弟兄们，最要紧的是：不可起誓。不可指着天起誓，不可指着地起誓，也不可用任何别的誓言起誓。你们说话，是，就说是；不是，就说不是；免得落在审判之下。", version_id: 6},
  {id: 162386, book: "Jas", verse: "5.013", unformatted: "有功效的祷告\n你们中间谁受苦，就应该祷告；谁欢喜，就应该歌颂。", version_id: 6},
  {id: 162387, book: "Jas", verse: "5.014", unformatted: "你们中间谁患病，就应该请教会的长老们来，让他们奉主的名用油来膏抹他、为他祷告。", version_id: 6},
  {id: 162388, book: "Jas", verse: "5.015", unformatted: "出于信的祈求，将会救那病人；主将使他起来；即使他犯了罪，也将被赦免。", version_id: 6},
  {id: 162389, book: "Jas", verse: "5.016", unformatted: "所以你们当彼此认罪，彼此代求，好使你们得痊愈。义人所做的祈祷是大有力量的。", version_id: 6},
  {id: 162390, book: "Jas", verse: "5.017", unformatted: "以利亚和我们同样是人，但他用祷告来祈求不要降雨，果然地上三年零六个月没有降雨。", version_id: 6},
  {id: 162391, book: "Jas", verse: "5.018", unformatted: "后来他又祷告，天就降下雨水，地也产出果实来。", version_id: 6},
  {id: 162392, book: "Jas", verse: "5.019", unformatted: "我的弟兄们，如果你们中间有人被迷惑离开了真理，而有人使他回转过来，", version_id: 6},
  {id: 162393, book: "Jas", verse: "5.02", unformatted: "这人应当知道：那使一个罪人从他的迷途中回转过来的，将拯救他的灵魂脱离死亡，并且遮盖众多的罪孽。", version_id: 6},
  {id: 162394, book: "1Pet", verse: "1.001", unformatted: "问安\n耶稣基督的使徒彼得，\n致那些蒙拣选，分散在本都、加拉太、卡帕多细亚、亚细亚、比提尼亚寄居的人，", version_id: 6},
  {id: 162395, book: "1Pet", verse: "1.002", unformatted: "就是按照父神的预知，藉着圣灵分别为圣，以致顺从耶稣基督，又蒙他宝血所洒的人：\n愿恩典与平安多多地加给你们！", version_id: 6},
  {id: 162396, book: "1Pet", verse: "1.003", unformatted: "活的盼望\n愿颂赞归于神——我们主耶稣基督的父！他照着自己极大的怜悯，藉着耶稣基督从死人中的复活，使我们重生，得着永生的盼望，", version_id: 6},
  {id: 162397, book: "1Pet", verse: "1.004", unformatted: "得着不会朽坏、不会玷污、不会衰残、保存在天上的继业；", version_id: 6},
  {id: 162398, book: "1Pet", verse: "1.005", unformatted: "你们藉着信，在神的大能中蒙保守，以至于那预备好要在末世显现的救恩。", version_id: 6},
  {id: 162399, book: "1Pet", verse: "1.006", unformatted: "为此，你们欢欣跳跃，但如今必须在种种试炼中暂时忧伤，", version_id: 6},
  {id: 162400, book: "1Pet", verse: "1.007", unformatted: "好让你们的信仰经过考验，就比那被火炼过仍然会朽坏的金子更加宝贵，能在耶稣基督显现的时候，被看为是可以得着称赞、荣耀和尊贵的。", version_id: 6},
  {id: 162401, book: "1Pet", verse: "1.008", unformatted: "你们虽然没有见过他，却爱他；虽然如今看不到他，却信他，并且怀着无法描述、充满荣耀的喜乐而欢欣跳跃，", version_id: 6},
  {id: 162402, book: "1Pet", verse: "1.009", unformatted: "因为你们正在领受你们信仰的结果，就是灵魂的救恩。", version_id: 6},
  {id: 162403, book: "1Pet", verse: "1.01", unformatted: "关于这救恩，那些预言你们要得着这恩典的先知，曾经寻求过，详细查考过。", version_id: 6},
  {id: 162404, book: "1Pet", verse: "1.011", unformatted: "他们查考了自己里面基督之灵，当他预言有关基督的苦难和此后的荣耀时，究竟指明的是什么时候、或什么样的情况。", version_id: 6},
  {id: 162405, book: "1Pet", verse: "1.012", unformatted: "他们蒙了启示，知道所服事的这一切，不是为自己，而是为你们。如今，这些事藉着从天上派来的圣灵，通过那些向你们传福音的人，传讲给了你们；对于这些事，天使们也渴望能详细察看。", version_id: 6},
  {id: 162406, book: "1Pet", verse: "1.013", unformatted: "应当过圣洁的生活\n因此，你们要约束自己的意念，要谨慎，要把盼望完全寄托在耶稣基督显现时带给你们的恩典上。", version_id: 6},
  {id: 162407, book: "1Pet", verse: "1.014", unformatted: "做为顺从的儿女，不可效法你们原先愚昧无知时的那些欲望；", version_id: 6},
  {id: 162408, book: "1Pet", verse: "1.015", unformatted: "相反，正如召唤你们的那一位是圣洁的，你们在一切行为上也当成为圣洁的，", version_id: 6},
  {id: 162409, book: "1Pet", verse: "1.016", unformatted: "因为经上记着：“你们当成为圣洁的，因为我是圣洁的。”", version_id: 6},
  {id: 162410, book: "1Pet", verse: "1.017", unformatted: "你们既然称不偏待人、只按每个人的行为施行审判的那一位为父，就应当怀着敬畏的心，过你们寄居的日子，", version_id: 6},
  {id: 162411, book: "1Pet", verse: "1.018", unformatted: "因为你们知道，你们被救赎，脱离祖先所传下来的虚妄行为，不是凭着会朽坏的东西，如银子或金子，", version_id: 6},
  {id: 162412, book: "1Pet", verse: "1.019", unformatted: "而是凭着那像羔羊一样毫无瑕疵、毫无玷污的基督宝血。", version_id: 6},
  {id: 162413, book: "1Pet", verse: "1.02", unformatted: "基督在创世以前就已经被神预先知道，又在这末后的时代为你们的缘故显明出来。", version_id: 6},
  {id: 162414, book: "1Pet", verse: "1.021", unformatted: "藉着基督，你们信靠那使他从死人中复活、又赐他荣耀的神，以致你们的信仰和盼望也都在于神。", version_id: 6},
  {id: 162415, book: "1Pet", verse: "1.022", unformatted: "你们既然藉着顺从真理，使自己的灵魂纯洁，以致有了不虚假的弟兄之爱，就应当以洁净的心，彼此热切地相爱。", version_id: 6},
  {id: 162416, book: "1Pet", verse: "1.023", unformatted: "你们得以重生，不是出于会朽坏的种子，而是出于不朽坏的种子，是藉着神永活长存的话语，", version_id: 6},
  {id: 162417, book: "1Pet", verse: "1.024", unformatted: "因为，\n“所有的人都像草一样，\n他们的一切荣耀就像草上的花；\n草会枯干，花会凋谢，", version_id: 6},
  {id: 162418, book: "1Pet", verse: "1.025", unformatted: "但主的话却永远长存。”\n所传给你们的福音就是这话。", version_id: 6},
  {id: 162419, book: "1Pet", verse: "2.001", unformatted: "活石和圣洁的国度\n所以，你们要脱去一切恶毒、一切诡诈、伪善、嫉妒，以及一切的诽谤，", version_id: 6},
  {id: 162420, book: "1Pet", verse: "2.002", unformatted: "要像刚出生的婴孩一样，爱慕那纯净的话语之奶，好使你们藉着它成长，以至于救恩，", version_id: 6},
  {id: 162421, book: "1Pet", verse: "2.003", unformatted: "因为你们尝过主仁慈的滋味。", version_id: 6},
  {id: 162422, book: "1Pet", verse: "2.004", unformatted: "你们来到那活石面前——他虽然被人弃绝，却是神所拣选、所珍贵的——", version_id: 6},
  {id: 162423, book: "1Pet", verse: "2.005", unformatted: "你们也就像活石，被建造成一座属灵的殿宇，进入圣洁的祭司体系，好藉着耶稣基督献上蒙神悦纳的属灵祭物，", version_id: 6},
  {id: 162424, book: "1Pet", verse: "2.006", unformatted: "因为经上记着：\n“看哪，我在锡安放一块\n蒙拣选的、珍贵的房角石，\n信靠他的人绝不至于蒙羞。”", version_id: 6},
  {id: 162425, book: "1Pet", verse: "2.007", unformatted: "这样，对你们信的人来说，他是尊贵的；但是对那些不信的人来说，却是\n“工匠所弃绝的石头，\n它已经成了房角的头块石头。”", version_id: 6},
  {id: 162426, book: "1Pet", verse: "2.008", unformatted: "并且是\n“一块绊脚的石头，\n使人绊倒的磐石。”\n他们绊倒是因为不肯信从这话语，而这也是被预定的。", version_id: 6},
  {id: 162427, book: "1Pet", verse: "2.009", unformatted: "但你们属于蒙拣选的族类、君王的祭司体系、\n圣洁的国度，是属神的子民；\n为要使你们宣扬曾召唤你们的那一位的美德——\n他召唤你们出黑暗，入他奇妙的光明。", version_id: 6},
  {id: 162428, book: "1Pet", verse: "2.01", unformatted: "你们从前不是子民，\n但如今却是神的子民；\n从前没有蒙怜悯，\n但如今却蒙了怜悯。", version_id: 6},
  {id: 162429, book: "1Pet", verse: "2.011", unformatted: "要有好品行\n各位蒙爱的人哪，我劝你们：做为过客和寄居的，要远避那与你们灵魂争战的肉体欲望。", version_id: 6},
  {id: 162430, book: "1Pet", verse: "2.012", unformatted: "你们在外邦人中，要有良好的品行；这样，当他们诽谤你们是作恶之人的时候，因看到你们美好的行为，就在神临到的日子荣耀神。", version_id: 6},
  {id: 162431, book: "1Pet", verse: "2.013", unformatted: "为了主的缘故，你们要服从人的一切制度；无论是在上的君王，", version_id: 6},
  {id: 162432, book: "1Pet", verse: "2.014", unformatted: "或是他所委派来惩恶赏善的那些官员；", version_id: 6},
  {id: 162433, book: "1Pet", verse: "2.015", unformatted: "因为神的旨意就是要你们藉着行善，堵住那些愚妄人无知的口。", version_id: 6},
  {id: 162434, book: "1Pet", verse: "2.016", unformatted: "做为自由人，你们不可拿自由当做恶事的借口，却要做神的奴仆。", version_id: 6},
  {id: 162435, book: "1Pet", verse: "2.017", unformatted: "你们应当尊重所有的人，爱弟兄姐妹，敬畏神，尊重君王。", version_id: 6},
  {id: 162436, book: "1Pet", verse: "2.018", unformatted: "奴仆服从主人\n你们做家仆的，应当以完全敬畏的心服从主人；不仅要服从善良温和的，就是乖僻的，也要服从。", version_id: 6},
  {id: 162437, book: "1Pet", verse: "2.019", unformatted: "因为人如果为了在神面前良心的缘故，在不公正的苦害中忍受忧患，这是可称赞的；", version_id: 6},
  {id: 162438, book: "1Pet", verse: "2.02", unformatted: "就是说，如果你们因犯罪受责打而忍耐，有什么可称赞的呢？但如果你们因行善受苦害而忍耐，这在神看来是可称赞的。", version_id: 6},
  {id: 162439, book: "1Pet", verse: "2.021", unformatted: "原来你们是为此蒙召的，\n因为基督也为你们受了苦害，\n给你们留下了榜样，\n好让你们追随他的脚踪。", version_id: 6},
  {id: 162440, book: "1Pet", verse: "2.022", unformatted: "他没有犯过罪，\n在他口中也找不出诡诈；", version_id: 6},
  {id: 162441, book: "1Pet", verse: "2.023", unformatted: "他被辱骂的时候，不还口；\n受苦害的时候，不说威吓的话，\n而是把自己交托给以公义施行审判的那一位。", version_id: 6},
  {id: 162442, book: "1Pet", verse: "2.024", unformatted: "他在木头上，以自己的身体亲自担当了我们的罪孽，\n好使我们既然向罪而死，\n就能向义而活；\n因他受的鞭伤，你们得了痊愈。", version_id: 6},
  {id: 162443, book: "1Pet", verse: "2.025", unformatted: "要知道，你们从前像是迷失的羊，\n但现在却回到了\n你们灵魂的牧人和监督的身旁。", version_id: 6},
  {id: 162444, book: "1Pet", verse: "3.001", unformatted: "妻子与丈夫\n同样，你们做妻子的，要服从自己的丈夫。这样，即使有不肯信从这话语的丈夫，他们也会被感化过来，不是因着妻子的话语，而是因着妻子的品行，", version_id: 6},
  {id: 162445, book: "1Pet", verse: "3.002", unformatted: "因为看见了你们怀有敬畏之心的贞洁品行。", version_id: 6},
  {id: 162446, book: "1Pet", verse: "3.003", unformatted: "你们的美丽之处不应该是外在的，如编头发、戴金饰、穿华服，", version_id: 6},
  {id: 162447, book: "1Pet", verse: "3.004", unformatted: "而应该是心里面内在的人，带着不朽坏的品性，出于谦和、安静的灵；这在神面前是极其贵重的。", version_id: 6},
  {id: 162448, book: "1Pet", verse: "3.005", unformatted: "实际上，从前盼望神的那些圣洁妇女，也是这样装饰自己，服从自己丈夫的；", version_id: 6},
  {id: 162449, book: "1Pet", verse: "3.006", unformatted: "就像撒拉顺从亚伯拉罕，称他为主那样；当你们行善而不怕任何惊扰的时候，就成为撒拉的儿女了。", version_id: 6},
  {id: 162450, book: "1Pet", verse: "3.007", unformatted: "同样，你们做丈夫的，要按情理与妻子同住，将女性当做比较软弱的器皿而尊重她们，也当做是生命之恩的共同继承人。这样，你们的祷告就不会受拦阻。", version_id: 6},
  {id: 162451, book: "1Pet", verse: "3.008", unformatted: "不要作恶\n总而言之，你们大家都要心思一致，互相同情，相爱如弟兄，心存怜悯，满有谦卑。", version_id: 6},
  {id: 162452, book: "1Pet", verse: "3.009", unformatted: "不要以恶报恶，以辱骂还辱骂，反要祝福，因为你们是为此蒙召的，好使你们继承祝福。", version_id: 6},
  {id: 162453, book: "1Pet", verse: "3.01", unformatted: "要知道，“那愿意爱惜生命、\n愿意见到好日子的人，\n要停止舌头出恶言，\n不让嘴唇说诡诈的话；", version_id: 6},
  {id: 162454, book: "1Pet", verse: "3.011", unformatted: "要远离邪恶，要行美善，\n要寻求和睦，要竭力追求；", version_id: 6},
  {id: 162455, book: "1Pet", verse: "3.012", unformatted: "因为主的眼睛看顾义人，\n主的耳朵垂听他们的祈祷；\n但主的脸却敌对作恶的人。”", version_id: 6},
  {id: 162456, book: "1Pet", verse: "3.013", unformatted: "为义受苦\n如果你们是为美善而热心的人，谁会伤害你们呢？", version_id: 6},
  {id: 162457, book: "1Pet", verse: "3.014", unformatted: "然而，即使你们为义受苦，也是蒙福的。不要怕他们所惧怕的，也不要惊慌不安，", version_id: 6},
  {id: 162458, book: "1Pet", verse: "3.015", unformatted: "只要你们心里尊主基督为圣，无论谁要你们对你们里面的盼望做个交代，你们都要随时预备好答复他；", version_id: 6},
  {id: 162459, book: "1Pet", verse: "3.016", unformatted: "不过要以温柔、敬畏的心答复，并且持守无愧的良心，以致谁诋毁你们在基督里的良好品行，谁就要在你们受到诽谤的事上蒙羞。", version_id: 6},
  {id: 162460, book: "1Pet", verse: "3.017", unformatted: "要知道，即使神的旨意要你们因行善而受苦，也比因作恶而受苦更好，", version_id: 6},
  {id: 162461, book: "1Pet", verse: "3.018", unformatted: "因为基督也曾一次为了赎罪而受苦，\n就是义的代替不义的，\n为要把你们带到神的面前。\n照着肉身说，基督固然被处死了；\n但照着灵说，他却得以复活了。", version_id: 6},
  {id: 162462, book: "1Pet", verse: "3.019", unformatted: "藉着灵，他也曾去传道给那些被监禁的灵魂，", version_id: 6},
  {id: 162463, book: "1Pet", verse: "3.02", unformatted: "就是从前在挪亚建造方舟的那些日子里，在神耐心等待的时候那些不肯信从的灵魂。当时进入方舟，藉着水获救的人很少，只有八个人。", version_id: 6},
  {id: 162464, book: "1Pet", verse: "3.021", unformatted: "这水所象征的洗礼，如今也藉着耶稣基督的复活拯救你们，不是清除肉体的污秽，而是向神恳求无愧的良心。", version_id: 6},
  {id: 162465, book: "1Pet", verse: "3.022", unformatted: "耶稣基督已经升天，就在神的右边，天使们、掌权的、有势力的，都服从了他。", version_id: 6},
  {id: 162466, book: "1Pet", verse: "4.001", unformatted: "跟随基督\n所以，基督既然在肉身上受过苦，你们也应该以同样的心志装备自己，因为那在肉身上受过苦的，就已经与罪断绝了，", version_id: 6},
  {id: 162467, book: "1Pet", verse: "4.002", unformatted: "以致不再顺着人的欲望，而是顺着神的旨意，在世上度余下的时间。", version_id: 6},
  {id: 162468, book: "1Pet", verse: "4.003", unformatted: "要知道，过往的日子，你们做了外邦人愿意做的事，活在好色、私欲、酗酒、荒宴、狂饮和律法所禁止的拜偶像之中——这已经够了。", version_id: 6},
  {id: 162469, book: "1Pet", verse: "4.004", unformatted: "如今在这些事上，因你们不与他们一起跑进那同样放荡的洪流中，他们就感到奇怪，毁谤你们。", version_id: 6},
  {id: 162470, book: "1Pet", verse: "4.005", unformatted: "他们将要向那预备好审判活人和死人的神做出交代。", version_id: 6},
  {id: 162471, book: "1Pet", verse: "4.006", unformatted: "原来福音也是为此而传给了那些死去的人，以致照着肉身说，他们按人的意思被定罪；但照着灵说，却按神的意思活着。", version_id: 6},
  {id: 162472, book: "1Pet", verse: "4.007", unformatted: "末世的道德规范\n万物的结局临近了，所以你们为了祷告的缘故应当清醒、谨慎。", version_id: 6},
  {id: 162473, book: "1Pet", verse: "4.008", unformatted: "最要紧的是：要持守彼此之间热切的爱，因为爱能遮盖众多的罪孽。", version_id: 6},
  {id: 162474, book: "1Pet", verse: "4.009", unformatted: "你们要毫无怨言地彼此款待。", version_id: 6},
  {id: 162475, book: "1Pet", verse: "4.01", unformatted: "每个人要照着自己所领受的恩赐来彼此服事，做神各种恩典的好管家。", version_id: 6},
  {id: 162476, book: "1Pet", verse: "4.011", unformatted: "如果有人讲说，要照着神的话语讲说；如果有人服事，要照着神所赐的力量服事，好让神在一切事上藉着耶稣基督得荣耀。愿荣耀和权能都归于他，直到永永远远！阿们。", version_id: 6},
  {id: 162477, book: "1Pet", verse: "4.012", unformatted: "为基督受苦\n各位蒙爱的人哪，当火一样的考验临到你们中间，给你们试炼的时候，你们不要感到奇怪，好像发生了怪异的事。", version_id: 6},
  {id: 162478, book: "1Pet", verse: "4.013", unformatted: "相反，你们既然在基督的苦难上有份，就应该照此欢喜，好使你们在他荣耀显现的时候，也可以欢喜、快乐。", version_id: 6},
  {id: 162479, book: "1Pet", verse: "4.014", unformatted: "你们如果因基督的名受责骂，就是蒙福的，因为荣耀的灵，就是神的灵，住在你们身上。", version_id: 6},
  {id: 162480, book: "1Pet", verse: "4.015", unformatted: "你们当中不可有人因杀人、偷窃、作恶、好管闲事而受苦；", version_id: 6},
  {id: 162481, book: "1Pet", verse: "4.016", unformatted: "但如果有人因为是基督徒而受苦，他不应该感到羞耻，反要奉这名来荣耀神；", version_id: 6},
  {id: 162482, book: "1Pet", verse: "4.017", unformatted: "因为时候到了，审判要从神的家开始。如果先从我们开始，那么，那些不肯信从神福音的人会有什么样的结局呢？", version_id: 6},
  {id: 162483, book: "1Pet", verse: "4.018", unformatted: "“如果连义人都好不容易得救，\n那么，不敬神的人和罪人将会怎么样呢？”", version_id: 6},
  {id: 162484, book: "1Pet", verse: "4.019", unformatted: "因此，那些照着神的旨意受苦的人，要藉着行善把自己的灵魂交托给那位信实的造物主。", version_id: 6},
  {id: 162485, book: "1Pet", verse: "5.001", unformatted: "对长老的劝勉\n所以，我这同做长老的、做基督苦难见证人的、也在那将要显现的荣耀中有份的，我恳求你们中间的长老们：", version_id: 6},
  {id: 162486, book: "1Pet", verse: "5.002", unformatted: "你们应当牧养你们中间神的羊群，要按神的意思照顾他们，不是出于勉强，而是出于甘心；也不是出于贪财，而是出于乐意；", version_id: 6},
  {id: 162487, book: "1Pet", verse: "5.003", unformatted: "也不要压制所分配给你们的那些人，而要做羊群的榜样。", version_id: 6},
  {id: 162488, book: "1Pet", verse: "5.004", unformatted: "这样，在大牧者显现的时候，你们将领受那永不衰残的荣耀冠冕。", version_id: 6},
  {id: 162489, book: "1Pet", verse: "5.005", unformatted: "同样，你们年轻的，当服从年长的。每个人当彼此以谦卑束腰，因为\n“神抵挡骄傲的人，\n赐恩典给谦卑的人。”", version_id: 6},
  {id: 162490, book: "1Pet", verse: "5.006", unformatted: "所以，你们当在神大能的手下降卑，好让他在所定的时候高举你们。", version_id: 6},
  {id: 162491, book: "1Pet", verse: "5.007", unformatted: "你们要把一切忧虑都卸给神，因为他顾念你们。", version_id: 6},
  {id: 162492, book: "1Pet", verse: "5.008", unformatted: "结语\n你们要谨慎，要警醒。你们的仇敌魔鬼就像咆哮的狮子走来走去，寻找可吞吃的人。", version_id: 6},
  {id: 162493, book: "1Pet", verse: "5.009", unformatted: "你们要用坚固的信仰来抵挡它，因为你们知道，你们在世上的弟兄姐妹也都经历着同样的苦难。", version_id: 6},
  {id: 162494, book: "1Pet", verse: "5.01", unformatted: "然而，那赐一切恩典的神，就是在基督耶稣里召唤你们进入他永恒荣耀的那一位，在你们暂时受苦之后，将亲自使你们完备、坚固、刚强、根基稳固。", version_id: 6},
  {id: 162495, book: "1Pet", verse: "5.011", unformatted: "愿权能归于他，直到永远！阿们。", version_id: 6},
  {id: 162496, book: "1Pet", verse: "5.012", unformatted: "藉着我看为忠心的弟兄赛拉斯，我简要地写了这封信，劝勉你们，并且证明这是神真实的恩典。你们要在这恩典中站立得住。", version_id: 6},
  {id: 162497, book: "1Pet", verse: "5.013", unformatted: "那与你们同蒙拣选的、在巴比伦的妇人问候你们。我儿子马可也问候你们。", version_id: 6},
  {id: 162498, book: "1Pet", verse: "5.014", unformatted: "你们要用爱心的亲吻礼彼此问候。愿平安临到你们所有在基督里的人！", version_id: 6},
  {id: 162499, book: "2Pet", verse: "1.001", unformatted: "问安\n耶稣基督的奴仆和使徒西门彼得，\n致那些藉着我们的神、救主耶稣基督的义，与我们领受同样宝贵信仰的人：", version_id: 6},
  {id: 162500, book: "2Pet", verse: "1.002", unformatted: "愿恩典与平安，藉着你们真正认识神，我们的主耶稣，多多地加给你们！", version_id: 6},
  {id: 162501, book: "2Pet", verse: "1.003", unformatted: "在信心里成长\n主耶稣神性的大能，因我们真正认识以自己的荣耀和美德召唤我们的那一位，就把一切有关生命和敬神的事都赐给了我们。", version_id: 6},
  {id: 162502, book: "2Pet", verse: "1.004", unformatted: "藉着这荣耀和美德，他把那些又宝贵又极大的应许赐给了我们，好使你们能藉着这些应许，逃脱世上因欲望而来的衰朽，成为在神的本性上有份的人。", version_id: 6},
  {id: 162503, book: "2Pet", verse: "1.005", unformatted: "是的，也正是为此，你们应当尽量地殷勤，在信仰上加美德；在美德上加知识；", version_id: 6},
  {id: 162504, book: "2Pet", verse: "1.006", unformatted: "在知识上加自制；在自制上加忍耐；在忍耐上加敬神；", version_id: 6},
  {id: 162505, book: "2Pet", verse: "1.007", unformatted: "在敬神上加弟兄之情；在弟兄之情上加爱；", version_id: 6},
  {id: 162506, book: "2Pet", verse: "1.008", unformatted: "因为你们如果有了这些，并且不断增多这些，它们就会使你们在真正认识我们主耶稣基督的事上，不会成为懒惰的或不结果子的人；", version_id: 6},
  {id: 162507, book: "2Pet", verse: "1.009", unformatted: "就是说，没有具备这些的就是瞎眼的，他只看得见眼前的，忘记自己过去的罪孽已经得了洁净。", version_id: 6},
  {id: 162508, book: "2Pet", verse: "1.01", unformatted: "所以弟兄们，你们当更加努力地确定你们所蒙的召唤和拣选。要知道，如果你们如此行，就绝不会跌倒；", version_id: 6},
  {id: 162509, book: "2Pet", verse: "1.011", unformatted: "而这样，你们就丰丰富富地得到供应，进入我们的主、救主耶稣基督永恒的国度。", version_id: 6},
  {id: 162510, book: "2Pet", verse: "1.012", unformatted: "所以，虽然你们已经知道这些事，又在你们已有的真理上得了坚固，我还是要不断地提醒你们。", version_id: 6},
  {id: 162511, book: "2Pet", verse: "1.013", unformatted: "我认为，只要我还在这帐篷里，就理当用提醒的话来激发你们，", version_id: 6},
  {id: 162512, book: "2Pet", verse: "1.014", unformatted: "因为我知道我脱离这帐篷的时候快到了，就像我们的主耶稣基督也曾向我指明的。", version_id: 6},
  {id: 162513, book: "2Pet", verse: "1.015", unformatted: "我也要努力，使你们在我离世以后，时常想起这些事。", version_id: 6},
  {id: 162514, book: "2Pet", verse: "1.016", unformatted: "可信的预言\n要知道，我们从前让你们明白我们主耶稣基督的大能和他来临的事，并不是随从巧言编造的故事，相反，我们是亲眼见过他威荣的人。", version_id: 6},
  {id: 162515, book: "2Pet", verse: "1.017", unformatted: "他从父神领受尊贵和荣耀的时候，从那威严的荣耀中，有这样的声音向他传来，说：\n“这是我的爱子，\n我所喜悦的。”", version_id: 6},
  {id: 162516, book: "2Pet", verse: "1.018", unformatted: "这从天上传来的声音，是我们与他一起在圣山上的时候听见的，", version_id: 6},
  {id: 162517, book: "2Pet", verse: "1.019", unformatted: "并且我们有先知更确切的话语。你们如果留心这些话语，把它当做照耀在暗处的灯，一直到天光破晓、晨星在你们心中升起，你们就做得很好了。", version_id: 6},
  {id: 162518, book: "2Pet", verse: "1.02", unformatted: "你们首先要知道这一点：经上的一切预言都不是出于人自己的解释；", version_id: 6},
  {id: 162519, book: "2Pet", verse: "1.021", unformatted: "因为预言从来不是藉着人的意思传来的，而是人受圣灵的感动说出从神而来的话。", version_id: 6},
  {id: 162520, book: "2Pet", verse: "2.001", unformatted: "对假教师的审判\n不过，在子民当中也出现过假先知；照样，在你们当中也会有假教师。他们会偷偷引进使人灭亡的异端，甚至否认那救赎他们的主，给自己招来快速的灭亡。", version_id: 6},
  {id: 162521, book: "2Pet", verse: "2.002", unformatted: "许多人会随从他们好色的事；真理之道也会因他们的缘故，受到亵渎。", version_id: 6},
  {id: 162522, book: "2Pet", verse: "2.003", unformatted: "他们因着贪心，会用捏造的话利用你们。对他们的惩罚，从太古的时候就没有失效，他们的灭亡也不耽延。", version_id: 6},
  {id: 162523, book: "2Pet", verse: "2.004", unformatted: "要知道：既然神没有顾惜那些犯罪的天使，把他们扔进地狱，囚禁在幽暗的坑里，拘留到审判的时候；", version_id: 6},
  {id: 162524, book: "2Pet", verse: "2.005", unformatted: "既然神也没有顾惜上古的世界，使洪水临到那不敬神的世界，只保守了义的传道者挪亚一家八口；", version_id: 6},
  {id: 162525, book: "2Pet", verse: "2.006", unformatted: "既然神又定了所多玛和格摩拉的罪，把这二城倾覆，烧成灰烬，做为以后不敬神之人的鉴戒；", version_id: 6},
  {id: 162526, book: "2Pet", verse: "2.007", unformatted: "既然神拯救了那因恶人的好色行为而哀伤的义人罗得——", version_id: 6},
  {id: 162527, book: "2Pet", verse: "2.008", unformatted: "因为这义人住在他们中间，看见又听见他们的不法行为，他公义的心天天受到折磨——", version_id: 6},
  {id: 162528, book: "2Pet", verse: "2.009", unformatted: "那么，主就知道怎样拯救敬神的人脱离试炼，却把不义的人留在惩罚之下，直到审判的日子，", version_id: 6},
  {id: 162529, book: "2Pet", verse: "2.01", unformatted: "尤其对那些随从肉体而在污秽的欲望中生活、轻视主权者的人，更是这样。\n这些人胆大、任性，毫不战兢地亵渎那些有荣耀的。", version_id: 6},
  {id: 162530, book: "2Pet", verse: "2.011", unformatted: "就算是天使们，虽然有更大的力量和权能，也不在主面前用毁谤的话控告他们。", version_id: 6},
  {id: 162531, book: "2Pet", verse: "2.012", unformatted: "但这些人就像没有理性的动物，生来就是靠本能的，是为了捕捉和宰杀的；他们亵渎自己所不明白的，也将在自己的衰朽中被毁灭。", version_id: 6},
  {id: 162532, book: "2Pet", verse: "2.013", unformatted: "他们行不义，就得不义的酬报；他们把白昼的放荡看做是享乐；他们是污渍和瑕疵；他们与你们一起吃喝的时候，以自己的诡诈为乐；", version_id: 6},
  {id: 162533, book: "2Pet", verse: "2.014", unformatted: "他们眼中充满了淫欲和无止境的罪；他们引诱不坚定的人；他们心中习惯了贪婪；他们是被诅咒的儿女；", version_id: 6},
  {id: 162534, book: "2Pet", verse: "2.015", unformatted: "他们离开了正道就被迷惑，随从了比珥的儿子巴兰的路——这巴兰喜爱不义的酬报，", version_id: 6},
  {id: 162535, book: "2Pet", verse: "2.016", unformatted: "却为自己的过犯受了责备：一头不能说话的驴，竟用人的声音说出话来，拦阻了这先知的狂妄。", version_id: 6},
  {id: 162536, book: "2Pet", verse: "2.017", unformatted: "这些人是无水的泉源，是被暴风吹逐的云雾；有漆黑的幽暗为他们存留。", version_id: 6},
  {id: 162537, book: "2Pet", verse: "2.018", unformatted: "他们说虚妄夸大的话，以肉体的欲望和好色的事来引诱人；这些被引诱的人好不容易才逃脱那些生活在迷途中的人；", version_id: 6},
  {id: 162538, book: "2Pet", verse: "2.019", unformatted: "他们许诺给这些人自由，自己却是衰朽的奴隶。要知道，一个人被什么制伏，就成为什么的奴隶。", version_id: 6},
  {id: 162539, book: "2Pet", verse: "2.02", unformatted: "原来，他们如果藉着认识我们的主、救主耶稣基督而逃脱这世界的污秽，却又被这些污秽纠缠、制伏了，那么，他们最后的情况就比先前更坏了；", version_id: 6},
  {id: 162540, book: "2Pet", verse: "2.021", unformatted: "因为认识了义的道路以后，背离那交托给他们的神圣诫命，对他们来说，倒不如不认识还好。", version_id: 6},
  {id: 162541, book: "2Pet", verse: "2.022", unformatted: "在他们身上正应验了这真实的俗语：“狗转身回到自己所吐的东西；猪洗干净了又到污泥中打滚。”", version_id: 6},
  {id: 162542, book: "2Pet", verse: "3.001", unformatted: "主的日子\n各位蒙爱的人哪，这已经是我写给你们的第二封信。在这两封信中，我都用提醒的话来激发你们纯洁的意念，", version_id: 6},
  {id: 162543, book: "2Pet", verse: "3.002", unformatted: "要使你们想起圣先知们曾经说过的话，以及你们的使徒们所传的主、救主的命令。", version_id: 6},
  {id: 162544, book: "2Pet", verse: "3.003", unformatted: "你们首先要知道这一点：在末后的日子里，将有好讥诮的人出来讥诮，他们顺着自己的欲望生活，", version_id: 6},
  {id: 162545, book: "2Pet", verse: "3.004", unformatted: "并且说：“他来临的应许在哪里呢？其实自从祖先睡了以来，一切都继续存留，与创世之初一样。”", version_id: 6},
  {id: 162546, book: "2Pet", verse: "3.005", unformatted: "他们故意忽略这一点：就是从太古的时候，藉着神的话语有了诸天，也有了从水而出、藉水而成的大地；", version_id: 6},
  {id: 162547, book: "2Pet", verse: "3.006", unformatted: "藉着神的话语和水，当时的世界被水淹没就毁灭了；", version_id: 6},
  {id: 162548, book: "2Pet", verse: "3.007", unformatted: "但现在的天和地，藉着他的话语得以存留，一直留到不敬神的人受审判和灭亡的日子，被火焚烧。", version_id: 6},
  {id: 162549, book: "2Pet", verse: "3.008", unformatted: "可是，各位蒙爱的人哪，你们不可忽略这一点：在主看来，一日如千年，千年如一日。", version_id: 6},
  {id: 162550, book: "2Pet", verse: "3.009", unformatted: "主没有像有些人认为的那样，拖延所应许的；相反，他对你们恒久忍耐，不愿任何人灭亡，而愿所有人都来悔改。", version_id: 6},
  {id: 162551, book: "2Pet", verse: "3.01", unformatted: "不过，主的日子要来临，就像贼来临那样。在那日，诸天要轰然一声地消逝，天体将被火焚烧而废除，地和地上的作为也都将被暴露出来。", version_id: 6},
  {id: 162552, book: "2Pet", verse: "3.011", unformatted: "既然这一切都要如此被废除，那么，你们应该做什么样的人呢？应该行为圣洁、敬畏神，", version_id: 6},
  {id: 162553, book: "2Pet", verse: "3.012", unformatted: "等候并催促神的日子来临。因为在那日，诸天将被焚烧而废除，天体要被火焚烧而熔化。", version_id: 6},
  {id: 162554, book: "2Pet", verse: "3.013", unformatted: "但照着他的应许，我们等候着一个新天新地，有公义居住在那里。", version_id: 6},
  {id: 162555, book: "2Pet", verse: "3.014", unformatted: "结语\n所以，各位蒙爱的人哪，你们既然等候这些事，就当努力地使自己被神看做是与他和好、毫无玷污、毫无瑕疵的；", version_id: 6},
  {id: 162556, book: "2Pet", verse: "3.015", unformatted: "并且你们要把我们主的耐心看做救恩，就像我们亲爱的弟兄保罗，照着所赐他的智慧写给你们的那样。", version_id: 6},
  {id: 162557, book: "2Pet", verse: "3.016", unformatted: "他在所有的信中也都说到这些事。那些没有知识、不坚定的人曲解了他信中一些难懂的地方，正如他们也曲解其他经文，就导致了他们自己的灭亡。", version_id: 6},
  {id: 162558, book: "2Pet", verse: "3.017", unformatted: "因此，各位蒙爱的人哪，你们既然预先知道了这些，就应该警惕，免得被那些恶人的迷惑引入歧途，从自己坚固的地步坠落。", version_id: 6},
  {id: 162559, book: "2Pet", verse: "3.018", unformatted: "你们要在我们的主、救主耶稣基督的恩典和知识中不断长进。愿荣耀归于他，从现在直到永远之日！阿们。", version_id: 6},
  {id: 162560, book: "1John", verse: "1.001", unformatted: "生命之道\n有关生命之道，\n是从太初就有的，\n是我们所听到、\n所亲眼看到、\n所观察过、\n又亲手摸过的——", version_id: 6},
  {id: 162561, book: "1John", verse: "1.002", unformatted: "的确，这生命已经显明出来；\n我们看到了，\n现在也做见证，\n并向你们宣扬这永恒的生命；\n这生命原来与父同在、\n又向我们显明出来了。", version_id: 6},
  {id: 162562, book: "1John", verse: "1.003", unformatted: "我们把所看到、\n所听到的也向你们宣扬，\n好使你们也能够与我们相契合；\n而我们又是与父\n和他的儿子耶稣基督相契合的。", version_id: 6},
  {id: 162563, book: "1John", verse: "1.004", unformatted: "我们写这些事，\n好使我们大家的喜乐得以满足。", version_id: 6},
  {id: 162564, book: "1John", verse: "1.005", unformatted: "与神相契合\n我们从基督所听到，现在又传讲给你们的信息是这样的：神就是光，在他里面毫无黑暗。", version_id: 6},
  {id: 162565, book: "1John", verse: "1.006", unformatted: "如果我们说自己与神相契合，却仍然在黑暗中行走，我们就是在说谎，没有遵行真理；", version_id: 6},
  {id: 162566, book: "1John", verse: "1.007", unformatted: "但如果我们在光明中行走，就像神在光明中那样，我们就是彼此相契合的；而且神的儿子耶稣的血也洁净我们脱离一切的罪。", version_id: 6},
  {id: 162567, book: "1John", verse: "1.008", unformatted: "如果我们说自己没有罪，我们就是在欺骗自己，真理就不在我们里面了；", version_id: 6},
  {id: 162568, book: "1John", verse: "1.009", unformatted: "如果我们承认自己的罪孽，神是信实的、公义的，他就会赦免我们的罪孽，洁净我们脱离一切的不义。", version_id: 6},
  {id: 162569, book: "1John", verse: "1.01", unformatted: "如果我们说自己没有犯罪，我们就是把神当做说谎者，他的话语就不在我们里面了。", version_id: 6},
  {id: 162570, book: "1John", verse: "2.001", unformatted: "我的孩子们哪，我把这些事写给你们，免得你们犯罪。但如果有人犯了罪，在父那里我们有一位辩护者，就是那义者耶稣基督。", version_id: 6},
  {id: 162571, book: "1John", verse: "2.002", unformatted: "他自己为我们的罪孽做了平息祭，不仅是为我们的罪，也是为全世界的罪。", version_id: 6},
  {id: 162572, book: "1John", verse: "2.003", unformatted: "神的命令\n如果我们遵守基督的命令，从这一点我们就知道我们已经认识他。", version_id: 6},
  {id: 162573, book: "1John", verse: "2.004", unformatted: "那说“我已经认识他”，却不遵守他命令的，就是说谎者，真理也不在这个人里面；", version_id: 6},
  {id: 162574, book: "1John", verse: "2.005", unformatted: "但如果有人遵守他的话语，属神的爱就确实在这个人里面得以完全了；从这一点我们就知道我们是在他里面。", version_id: 6},
  {id: 162575, book: "1John", verse: "2.006", unformatted: "那说自己住在他里面的，自己也应该按基督所行的照样去行。", version_id: 6},
  {id: 162576, book: "1John", verse: "2.007", unformatted: "各位蒙爱的人哪，我写给你们的不是一条新命令，而是你们从起初就有的旧命令；这条旧命令就是你们所听过的那话。", version_id: 6},
  {id: 162577, book: "1John", verse: "2.008", unformatted: "另一方面，我写给你们的也是一条新命令；这命令在他、在你们，都是真实的，因为黑暗正在消逝，真光已经在照耀。", version_id: 6},
  {id: 162578, book: "1John", verse: "2.009", unformatted: "那说自己在光里却恨自己弟兄的，至今还在黑暗里；", version_id: 6},
  {id: 162579, book: "1John", verse: "2.01", unformatted: "那爱自己弟兄的，就住在光里，而且在他里面没有什么绊脚的事；", version_id: 6},
  {id: 162580, book: "1John", verse: "2.011", unformatted: "但那恨自己弟兄的，就是在黑暗里，并且在黑暗里行走，也不知道自己往哪里去，因为黑暗使他的眼睛瞎了。", version_id: 6},
  {id: 162581, book: "1John", verse: "2.012", unformatted: "写信的理由\n孩子们哪，我写信给你们，\n因为你们的罪孽藉着基督的名已经被赦免了；", version_id: 6},
  {id: 162582, book: "1John", verse: "2.013", unformatted: "父老们哪，我写信给你们，\n因为你们已经认识了从太初就有的那一位；\n年轻人哪，我写信给你们，\n因为你们已经胜过了那恶者。", version_id: 6},
  {id: 162583, book: "1John", verse: "2.014", unformatted: "孩子们哪，我写了信给你们，\n因为你们已经认识了父；\n父老们哪，我写了信给你们，\n因为你们已经认识了从太初就有的那一位；\n年轻人哪，我写了信给你们，\n因为你们是强壮的，\n神的话语住在你们里面，\n你们也已经胜过了那恶者。", version_id: 6},
  {id: 162584, book: "1John", verse: "2.015", unformatted: "不要爱世界\n你们不要爱世界，也不要爱世界上的事物。如果有人爱世界，父的爱就不在他里面了。", version_id: 6},
  {id: 162585, book: "1John", verse: "2.016", unformatted: "原来世界上一切肉体的欲望、眼目的欲望、今生的骄傲，都不是出于父，而是出于世界的。", version_id: 6},
  {id: 162586, book: "1John", verse: "2.017", unformatted: "世界和属世的欲望正在消逝；但那遵行神旨意的，却永远长存。", version_id: 6},
  {id: 162587, book: "1John", verse: "2.018", unformatted: "末后的时刻\n孩子们哪，现在是末后的时期了。正如你们听说过敌基督的要来，现在确实已经有许多敌基督的出现了；故此我们知道，现在是末后的时期了。", version_id: 6},
  {id: 162588, book: "1John", verse: "2.019", unformatted: "他们从我们中间出去，但他们本来就不属于我们，因为他们如果属于我们，就会留下与我们在一起；不过这样就显明出，他们都不属于我们。", version_id: 6},
  {id: 162589, book: "1John", verse: "2.02", unformatted: "至于你们，你们从那位圣者领受了恩膏，并且你们大家都已经知道了这事。", version_id: 6},
  {id: 162590, book: "1John", verse: "2.021", unformatted: "我写了信给你们，不是因为你们不认识真理，而是因为你们已经认识了真理，又因为一切虚假都不是出于真理。", version_id: 6},
  {id: 162591, book: "1John", verse: "2.022", unformatted: "谁是说谎者呢？难道不是那否认耶稣是基督的吗？那否认父和子的，这个人就是敌基督的。", version_id: 6},
  {id: 162592, book: "1John", verse: "2.023", unformatted: "所有否认子的，就没有父；那承认子的，连父也有了。", version_id: 6},
  {id: 162593, book: "1John", verse: "2.024", unformatted: "留在神里面\n你们应当使那从起初所听到的，住在你们里面。如果那从起初所听到的住在你们里面，你们就会住在子里面，也住在父里面。", version_id: 6},
  {id: 162594, book: "1John", verse: "2.025", unformatted: "这就是那应许，是他所应许我们的，就是永恒的生命。", version_id: 6},
  {id: 162595, book: "1John", verse: "2.026", unformatted: "我写给你们的这些，是关于迷惑你们之人的。", version_id: 6},
  {id: 162596, book: "1John", verse: "2.027", unformatted: "至于你们，你们从那一位所领受的恩膏住在你们里面，这样你们就不需要人来教导你们。其实，就像他的恩膏在一切事上所教导你们的——是真实的，不是虚假的——又像他曾经所教导的，你们要住在他里面。", version_id: 6},
  {id: 162597, book: "1John", verse: "2.028", unformatted: "神的儿女\n如今，孩子们哪，你们当住在他里面，好使他显现的时候，我们可以坦然无惧，他来临的时候，我们可以在他面前不至于蒙羞。", version_id: 6},
  {id: 162598, book: "1John", verse: "2.029", unformatted: "你们如果知道他是公义的，也就知道所有行义的人，都是由他所生的。", version_id: 6},
  {id: 162599, book: "1John", verse: "3.001", unformatted: "你们看，父所赐给我们的是何等的爱，为要使我们被称为“神的儿女”，而我们就是神的儿女。世人之所以不认识我们，是因为不认识父。", version_id: 6},
  {id: 162600, book: "1John", verse: "3.002", unformatted: "各位蒙爱的人哪，现在我们是神的儿女，将来会怎样，尚未显明。我们已经知道的是：基督显现的时候，我们就会像他，因为我们将看见他的本相。", version_id: 6},
  {id: 162601, book: "1John", verse: "3.003", unformatted: "每一个对基督怀有这盼望的，都会使自己纯洁，正如基督是纯洁的。", version_id: 6},
  {id: 162602, book: "1John", verse: "3.004", unformatted: "凡是犯罪的，也是行恶的；罪就是恶。", version_id: 6},
  {id: 162603, book: "1John", verse: "3.005", unformatted: "你们知道，基督曾经显现，是为了除去罪孽；在他里面并没有罪。", version_id: 6},
  {id: 162604, book: "1John", verse: "3.006", unformatted: "凡是住在基督里面的，就不继续犯罪；凡是继续犯罪的，就未曾看到他，也未曾认识他。", version_id: 6},
  {id: 162605, book: "1John", verse: "3.007", unformatted: "孩子们哪，不要让任何人迷惑你们。行义的人是公义的，正如基督是公义的。", version_id: 6},
  {id: 162606, book: "1John", verse: "3.008", unformatted: "继续犯罪的人属于魔鬼，因为魔鬼从起初就一直犯罪。为此，神的儿子显现了，是为了废除魔鬼的作为。", version_id: 6},
  {id: 162607, book: "1John", verse: "3.009", unformatted: "凡是由神所生的，不继续犯罪，因为神的种子住在他里面；他也不能继续犯罪，因为他是由神所生的。", version_id: 6},
  {id: 162608, book: "1John", verse: "3.01", unformatted: "从这一点，谁是神的儿女，谁是魔鬼的儿女就清清楚楚了。\n爱的命令\n凡是不行公义的，就不属于神；不爱自己弟兄的，也不属于神。", version_id: 6},
  {id: 162609, book: "1John", verse: "3.011", unformatted: "因为你们从起初所听见的信息是这样的：我们要彼此相爱。", version_id: 6},
  {id: 162610, book: "1John", verse: "3.012", unformatted: "不要像该隐，他属于那恶者，杀了自己的弟弟。他为什么杀弟弟呢？因为他自己的行为是恶的，而他弟弟的行为是义的。", version_id: 6},
  {id: 162611, book: "1John", verse: "3.013", unformatted: "弟兄们，如果世界恨你们，不要感到惊奇。", version_id: 6},
  {id: 162612, book: "1John", verse: "3.014", unformatted: "我们知道，因为我们爱弟兄，我们就已经出死入生了；那不爱弟兄的，就住在死亡中。", version_id: 6},
  {id: 162613, book: "1John", verse: "3.015", unformatted: "凡是恨自己弟兄的，就是杀人的，而你们知道：凡是杀人的，就没有永恒的生命住在他里面。", version_id: 6},
  {id: 162614, book: "1John", verse: "3.016", unformatted: "彼此相爱\n我们从这一点就知道了爱：基督为我们舍弃了自己的生命，我们也应该为弟兄舍弃生命。", version_id: 6},
  {id: 162615, book: "1John", verse: "3.017", unformatted: "但如果有人拥有世界的财产，看见自己的弟兄有需要，却封闭情感，那么，属神的爱怎么能住在他里面呢？", version_id: 6},
  {id: 162616, book: "1John", verse: "3.018", unformatted: "孩子们哪，让我们不在语言或口头上相爱，而在行为和真理中相爱，", version_id: 6},
  {id: 162617, book: "1John", verse: "3.019", unformatted: "从这一点我们就会知道我们属于真理。", version_id: 6},
  {id: 162618, book: "1John", verse: "3.02", unformatted: "就算我们的心责备自己，我们在神面前也可以安心，因为神比我们的心更大，并且他知道一切。", version_id: 6},
  {id: 162619, book: "1John", verse: "3.021", unformatted: "各位蒙爱的人哪，如果我们的心不责备我们，我们在神面前就可以坦然无惧，", version_id: 6},
  {id: 162620, book: "1John", verse: "3.022", unformatted: "这样，凡是我们所求的，就会从他得着；因为我们遵守他的命令，行他看为可喜悦的事。", version_id: 6},
  {id: 162621, book: "1John", verse: "3.023", unformatted: "而神的命令是这样的：我们要信他儿子耶稣基督的名，并且照着他所赐给我们的命令彼此相爱。", version_id: 6},
  {id: 162622, book: "1John", verse: "3.024", unformatted: "遵守神命令的，就住在神里面，神也住在他里面；而且我们从这一点就知道神住在我们里面，是藉着他所赐给我们的圣灵。", version_id: 6},
  {id: 162623, book: "1John", verse: "4.001", unformatted: "神的灵和敌基督的灵\n各位蒙爱的人哪，你们不要每一个灵都相信，而要分辨这些灵是否出于神，因为有许多假先知已经在世上出现了。", version_id: 6},
  {id: 162624, book: "1John", verse: "4.002", unformatted: "你们从这一点就认出神的灵：任何灵承认耶稣基督是以肉身而来的，这灵就是出于神；", version_id: 6},
  {id: 162625, book: "1John", verse: "4.003", unformatted: "任何灵不承认耶稣的，这灵就不是出于神，而是那敌基督者的灵。你们听说过这灵要来，而它现在已经在世上了。", version_id: 6},
  {id: 162626, book: "1John", verse: "4.004", unformatted: "孩子们哪，你们属于神，而且你们已经胜过了假先知们，因为那在你们里面的，比那在世界上的更大。", version_id: 6},
  {id: 162627, book: "1John", verse: "4.005", unformatted: "他们属于世界，所以他们说的属于世界，而世界也听从他们。", version_id: 6},
  {id: 162628, book: "1John", verse: "4.006", unformatted: "我们属于神，那认识神的，就听从我们；那不属于神的，就不听从我们。由此，我们就认出真理的灵和迷惑人的灵。", version_id: 6},
  {id: 162629, book: "1John", verse: "4.007", unformatted: "在爱中认识神\n各位蒙爱的人哪，让我们彼此相爱，因为爱是出于神的。凡是有爱的，都由神所生，并且认识神；", version_id: 6},
  {id: 162630, book: "1John", verse: "4.008", unformatted: "那没有爱的，就不认识神，因为神就是爱。", version_id: 6},
  {id: 162631, book: "1John", verse: "4.009", unformatted: "神差派了自己的独生子到世上来，好使我们能藉着他而活；如此，属神的爱就在我们里面显明了。", version_id: 6},
  {id: 162632, book: "1John", verse: "4.01", unformatted: "并不是我们爱了神，而是神爱了我们，差派他的儿子来，为我们的罪孽做了平息祭——在这里就是爱了。", version_id: 6},
  {id: 162633, book: "1John", verse: "4.011", unformatted: "各位蒙爱的人哪，既然神如此爱我们，我们也应该彼此相爱。", version_id: 6},
  {id: 162634, book: "1John", verse: "4.012", unformatted: "从来没有人见过神；如果我们彼此相爱，神就住在我们里面，他的爱也就在我们里面得以完全。", version_id: 6},
  {id: 162635, book: "1John", verse: "4.013", unformatted: "我们从这一点知道：我们住在他里面，而他也住在我们里面，是因为神把他的灵赐给了我们；", version_id: 6},
  {id: 162636, book: "1John", verse: "4.014", unformatted: "我们已经见过，并且现在见证：父差派子做世界的救主，", version_id: 6},
  {id: 162637, book: "1John", verse: "4.015", unformatted: "凡是承认耶稣是神儿子的，神就住在他里面，他也住在神里面。", version_id: 6},
  {id: 162638, book: "1John", verse: "4.016", unformatted: "这样，我们已经认识并相信了神对我们所怀的爱。神就是爱；那住在爱里面的，就住在神里面，神也住在他里面。", version_id: 6},
  {id: 162639, book: "1John", verse: "4.017", unformatted: "从这一点，爱在我们里面得以完全，使我们在审判的日子可以坦然无惧，因为基督怎样，我们在这世上也怎样。", version_id: 6},
  {id: 162640, book: "1John", verse: "4.018", unformatted: "爱里没有惧怕；相反，完美的爱能除去惧怕，因为惧怕带有惩罚；而那惧怕的，在爱里没有得以完全。", version_id: 6},
  {id: 162641, book: "1John", verse: "4.019", unformatted: "我们爱，是因为他先爱了我们。", version_id: 6},
  {id: 162642, book: "1John", verse: "4.02", unformatted: "遵守神的命令\n如果有人说“我爱神”，却恨自己的弟兄，他就是说谎者，因为一个人不爱自己所看到的弟兄，就不能爱没有看到的神。", version_id: 6},
  {id: 162643, book: "1John", verse: "4.021", unformatted: "这是我们从基督所领受的命令：爱神的，也要爱自己的弟兄。", version_id: 6},
  {id: 162644, book: "1John", verse: "5.001", unformatted: "凡是相信耶稣是基督的，都是由神所生的。凡是爱那生身之父的，也都爱那由父所生的。", version_id: 6},
  {id: 162645, book: "1John", verse: "5.002", unformatted: "当我们爱神，并遵行他命令的时候，从这一点就知道我们爱神的儿女了。", version_id: 6},
  {id: 162646, book: "1John", verse: "5.003", unformatted: "原来属神的爱是这样的，就是我们要遵守他的命令；而他的命令并不是沉重的，", version_id: 6},
  {id: 162647, book: "1John", verse: "5.004", unformatted: "因为凡是由神所生的，就胜过世界；而这胜过世界的胜利，就是我们的信仰。", version_id: 6},
  {id: 162648, book: "1John", verse: "5.005", unformatted: "胜过世界的到底是谁呢？难道不是那相信耶稣是神儿子的吗？", version_id: 6},
  {id: 162649, book: "1John", verse: "5.006", unformatted: "从神来的见证\n藉着水和血而来的那一位，就是耶稣基督，不仅藉着水，也藉着水和血；并且圣灵是做见证的那一位，因为圣灵就是真理。", version_id: 6},
  {id: 162650, book: "1John", verse: "5.007", unformatted: "原来，做见证的有三者：", version_id: 6},
  {id: 162651, book: "1John", verse: "5.008", unformatted: "圣灵、水和血；而这三者是一致的。", version_id: 6},
  {id: 162652, book: "1John", verse: "5.009", unformatted: "我们既然接受人的见证，神的见证就更应该接受了，因为这是神的见证，就是神为他的儿子所做的见证。", version_id: 6},
  {id: 162653, book: "1John", verse: "5.01", unformatted: "那信神儿子的，就有这见证在他里面；那不信神的，就把神当做说谎者，因为他不相信神为自己的儿子所做的见证。", version_id: 6},
  {id: 162654, book: "1John", verse: "5.011", unformatted: "这见证就是：神把永恒的生命赐给了我们，并且这生命在他儿子里面。", version_id: 6},
  {id: 162655, book: "1John", verse: "5.012", unformatted: "那有了神儿子的，就有了这生命；那没有神儿子的，就没有这生命。", version_id: 6},
  {id: 162656, book: "1John", verse: "5.013", unformatted: "我把这些事写给你们信神儿子之名的人，好让你们知道：你们有永恒的生命。", version_id: 6},
  {id: 162657, book: "1John", verse: "5.014", unformatted: "有功效的祷告\n我们对神所怀的确信是这样的：如果我们按照他的旨意求什么，他就垂听我们。", version_id: 6},
  {id: 162658, book: "1John", verse: "5.015", unformatted: "如果我们已经知道凡是我们所求的他都垂听，我们就知道我们已经拥有了向他所求的。", version_id: 6},
  {id: 162659, book: "1John", verse: "5.016", unformatted: "如果有人看见自己的弟兄在犯不至于死的罪，就应该为他祈求；这样，神将把生命赐给他，就是给那些犯不至于死的罪的人。有一种罪是至于死的；关于那种罪我不说要祈求。", version_id: 6},
  {id: 162660, book: "1John", verse: "5.017", unformatted: "一切不义都是罪，也有不至于死的罪。", version_id: 6},
  {id: 162661, book: "1John", verse: "5.018", unformatted: "结语\n我们知道：凡是由神所生的人，就不会继续犯罪；原来，由神所生的那一位保守着他，那恶者也就不碰他。", version_id: 6},
  {id: 162662, book: "1John", verse: "5.019", unformatted: "我们知道：我们属于神，但整个世界都伏在那恶者之下。", version_id: 6},
  {id: 162663, book: "1John", verse: "5.02", unformatted: "我们也知道：神的儿子已经来了，并且把领悟的心赐给了我们，使我们能认识真实的那一位。我们在真实的那一位里面，也就是在他的儿子耶稣基督里面——这一位就是真神，就是永恒的生命。", version_id: 6},
  {id: 162664, book: "1John", verse: "5.021", unformatted: "孩子们哪，你们要保守自己远离偶像。", version_id: 6},
  {id: 162665, book: "2John", verse: "1.001", unformatted: "问安\n我这做长老的，\n致蒙拣选的夫人和她的儿女：\n你们就是我在真理中所爱的，不单是我，也是所有认识真理的人所爱的，", version_id: 6},
  {id: 162666, book: "2John", verse: "1.002", unformatted: "因为真理住在我们里面，也将与我们同在，直到永远。", version_id: 6},
  {id: 162667, book: "2John", verse: "1.003", unformatted: "从父神和父的儿子耶稣基督而来的恩典、怜悯、平安，在真理和爱中，将与我们同在。", version_id: 6},
  {id: 162668, book: "2John", verse: "1.004", unformatted: "真理与欺骗\n我极其欢喜，因为我看见你的儿女当中有人照着我们从父所领受的命令，在真理中行事。", version_id: 6},
  {id: 162669, book: "2John", verse: "1.005", unformatted: "夫人哪，现在我请求你：让我们大家彼此相爱；我写给你的，不是一条新命令，而是我们从起初就有的命令。", version_id: 6},
  {id: 162670, book: "2John", verse: "1.006", unformatted: "我们要按照他的命令行事，这就是爱；你们要照着你们从起初所听见的在其中行事，这就是命令。", version_id: 6},
  {id: 162671, book: "2John", verse: "1.007", unformatted: "原来，有许多迷惑人的已经在世上出现，他们不承认耶稣基督是以肉身而来的；这样的人是迷惑人的，敌基督的。", version_id: 6},
  {id: 162672, book: "2John", verse: "1.008", unformatted: "你们自己要当心，免得失去我们大家为之所做的工，而要得着你们完满的报偿。", version_id: 6},
  {id: 162673, book: "2John", verse: "1.009", unformatted: "凡是越过基督的教导，不住在这教导中的，就没有神；那住在这教导中的，他才有了父，也有了子。", version_id: 6},
  {id: 162674, book: "2John", verse: "1.01", unformatted: "如果有人到你们那里去，不带着这教导，你们就不要接他到家里，也不要问候他，", version_id: 6},
  {id: 162675, book: "2John", verse: "1.011", unformatted: "因为问候他的，就在他的恶行上有份了。", version_id: 6},
  {id: 162676, book: "2John", verse: "1.012", unformatted: "信末的问候\n我虽然有很多话要写给你们，但不想用纸墨，只希望到你们那里去，并且与你们面对面交谈，好使我们的喜乐得以满足。", version_id: 6},
  {id: 162677, book: "2John", verse: "1.013", unformatted: "你那蒙拣选的姐妹的儿女问候你。", version_id: 6},
  {id: 162678, book: "3John", verse: "1.001", unformatted: "问安\n我这做长老的，\n致蒙爱的盖尤斯，就是我在真理中所爱的：", version_id: 6},
  {id: 162679, book: "3John", verse: "1.002", unformatted: "蒙爱的人哪，我愿你凡事安顺，身体健康，正如你的灵魂安顺。", version_id: 6},
  {id: 162680, book: "3John", verse: "1.003", unformatted: "有些弟兄来到这里，见证了你对真理的忠信，就是你怎样在真理中行事，这使我极其欢喜。", version_id: 6},
  {id: 162681, book: "3John", verse: "1.004", unformatted: "听到我的儿女在真理中行事，我的喜乐没有比这更大的了。", version_id: 6},
  {id: 162682, book: "3John", verse: "1.005", unformatted: "接待神的工人\n蒙爱的人哪，你为弟兄们，尤其是为异乡的弟兄们所做的，都是忠心的。", version_id: 6},
  {id: 162683, book: "3John", verse: "1.006", unformatted: "他们在教会面前见证了你的爱。你如果以神喜悦的方式送他们上路，就做得很好了，", version_id: 6},
  {id: 162684, book: "3John", verse: "1.007", unformatted: "因为他们为了基督的名出去，并没有从外邦人接受什么。", version_id: 6},
  {id: 162685, book: "3John", verse: "1.008", unformatted: "所以，我们应该帮助这样的人，好让我们为真理成为同工。", version_id: 6},
  {id: 162686, book: "3John", verse: "1.009", unformatted: "迪奥特菲与迪米特斯\n我写了信给教会，但是他们中那喜欢为首的迪奥特菲不接纳我们。", version_id: 6},
  {id: 162687, book: "3John", verse: "1.01", unformatted: "所以，如果我来了，我一定要提起他所做的事，就是他用坏话污蔑我们；而且他在这些事上还不满足，不但自己不接纳弟兄们，也阻止那些愿意接纳的人，甚至把他们赶出教会。", version_id: 6},
  {id: 162688, book: "3John", verse: "1.011", unformatted: "蒙爱的人哪，不要效法恶，而要效法善。那行善的属于神；那作恶的未曾看见神。", version_id: 6},
  {id: 162689, book: "3John", verse: "1.012", unformatted: "迪米特斯受到了大家的赞许，也得了真理本身的赞许；我们也为他做见证，而且你知道我们的见证是真实的。", version_id: 6},
  {id: 162690, book: "3John", verse: "1.013", unformatted: "信末的问候\n我有很多话要写给你，但不愿意用笔墨写给你。", version_id: 6},
  {id: 162691, book: "3John", verse: "1.014", unformatted: "我希望立刻见到你，并且面对面交谈。", version_id: 6},
  {id: 162692, book: "3John", verse: "1.015", unformatted: "愿你平安！朋友们都问候你。请你一一提名问候各位朋友。", version_id: 6},
  {id: 162693, book: "Jude", verse: "1.001", unformatted: "问安\n耶稣基督的奴仆、雅各的弟弟犹大，\n致那些蒙召、在父神里蒙爱、在耶稣基督里蒙保守的人：", version_id: 6},
  {id: 162694, book: "Jude", verse: "1.002", unformatted: "愿怜悯、平安、慈爱多多地加给你们！", version_id: 6},
  {id: 162695, book: "Jude", verse: "1.003", unformatted: "写信的目的\n各位蒙爱的人哪，我一直十分殷切地要写信给你们，谈论我们共有的救恩；我感到有必要写信鼓励你们，为曾经一次性地托付给圣徒们的信仰而努力争战，", version_id: 6},
  {id: 162696, book: "Jude", verse: "1.004", unformatted: "因为有些人已经偷偷进来了。他们是先前早就被记载的要受那惩罚的人；他们是不敬神的人，把我们神的恩典变为好色的借口，并且否认那独一的主宰——我们的主耶稣基督。", version_id: 6},
  {id: 162697, book: "Jude", verse: "1.005", unformatted: "以史为戒\n你们虽然知道这一切，我还想提醒你们：主一次性地把子民从埃及地救了出来，后来把那些不信的人都毁灭了，", version_id: 6},
  {id: 162698, book: "Jude", verse: "1.006", unformatted: "并且用永恒的锁链把那些不守本位、离开自己居所的天使拘留在幽暗里，直到那大日子的审判。", version_id: 6},
  {id: 162699, book: "Jude", verse: "1.007", unformatted: "同样，所多玛、格摩拉及其周围的城市，照着他们的样子放纵行淫，随从变态的肉欲，就遭受永火的刑罚，成为我们的鉴戒。", version_id: 6},
  {id: 162700, book: "Jude", verse: "1.008", unformatted: "不过，这些做梦的人一方面也同样地污秽身体，另一方面拒绝有主权的，又亵渎那些有荣耀的。", version_id: 6},
  {id: 162701, book: "Jude", verse: "1.009", unformatted: "即使是天使长米迦勒，当他与魔鬼争辩，为摩西的尸体争论的时候，也不敢用毁谤的话定它的罪，只说：“愿主责备你！”", version_id: 6},
  {id: 162702, book: "Jude", verse: "1.01", unformatted: "但这些人一方面亵渎自己所不知道的事，另一方面对自己本能所知道的事，像没有理性的动物那样；他们就在这些事上被毁灭。", version_id: 6},
  {id: 162703, book: "Jude", verse: "1.011", unformatted: "他们有祸了！因为他们走了“该隐的路”，又为财利奔上“巴兰的迷途”，并且在“科拉的背叛”中灭亡了。", version_id: 6},
  {id: 162704, book: "Jude", verse: "1.012", unformatted: "叛离者的结局\n这些人在你们的爱筵中是污点，与你们一起吃喝也无所惧怕；他们只牧养自己；他们是无水的云，随风飘荡；是深秋不结果子的树，死了又死，被连根拔起；", version_id: 6},
  {id: 162705, book: "Jude", verse: "1.013", unformatted: "是海中的狂浪，涌出自己可耻的泡沫；是流荡的星辰，有漆黑的幽暗永远为他们存留。", version_id: 6},
  {id: 162706, book: "Jude", verse: "1.014", unformatted: "亚当的第七代子孙以诺也曾预言这些人，说：\n“看哪，主带着他的千万圣者来临，", version_id: 6},
  {id: 162707, book: "Jude", verse: "1.015", unformatted: "要对所有的人施行审判，\n又要指证每个人以不敬神的方式所做的一切不敬神的事，\n以及不敬神的罪人所说的一切顶撞神的狂妄话。”", version_id: 6},
  {id: 162708, book: "Jude", verse: "1.016", unformatted: "这些人满腹抱怨，责怪他人，顺着自己的欲望生活；他们嘴里说夸大的话，为了利益谄媚他人。", version_id: 6},
  {id: 162709, book: "Jude", verse: "1.017", unformatted: "至于你们蒙爱的人哪，你们当想起我们主耶稣基督的使徒们先前所说的话。", version_id: 6},
  {id: 162710, book: "Jude", verse: "1.018", unformatted: "他们曾对你们说过：“在末后的时代，将有好讥诮的人顺着自己不敬神的欲望生活。”", version_id: 6},
  {id: 162711, book: "Jude", verse: "1.019", unformatted: "这些人是制造分裂的、属血气的、没有圣灵的。", version_id: 6},
  {id: 162712, book: "Jude", verse: "1.02", unformatted: "劝勉与祝福\n至于你们蒙爱的人哪，要在你们至圣的信仰上造就自己，藉着圣灵祷告，", version_id: 6},
  {id: 162713, book: "Jude", verse: "1.021", unformatted: "保守自己在神的爱中，仰望我们主耶稣基督的怜悯，进入永恒的生命。", version_id: 6},
  {id: 162714, book: "Jude", verse: "1.022", unformatted: "有些人疑惑不定，你们要怜悯他们；", version_id: 6},
  {id: 162715, book: "Jude", verse: "1.023", unformatted: "有些人，你们要把他们从火里抢出来，拯救他们；有些人，你们要怀着惧怕的心怜悯他们，连他们被肉欲玷污的衣服也要恨恶。", version_id: 6},
  {id: 162716, book: "Jude", verse: "1.024", unformatted: "那位独一的神能够保守你们不失足，并且使你们毫无瑕疵、怀着喜乐站在他的荣耀面前；", version_id: 6},
  {id: 162717, book: "Jude", verse: "1.025", unformatted: "愿荣耀、尊严、大能、权柄，藉着我们的主耶稣基督归于独一的神我们的救主，从万古以前到现在，直到永永远远！阿们。", version_id: 6},
  {id: 162718, book: "Rev", verse: "1.001", unformatted: "耶稣基督的启示\n耶稣基督的启示，就是神赐给他，要他指示给他奴仆们的那些必须快发生的事。基督藉着他的天使，向他的奴仆约翰传达并指明了那些事。", version_id: 6},
  {id: 162719, book: "Rev", verse: "1.002", unformatted: "约翰把神的话语和有关耶稣基督的见证，凡是他所看到的，都见证出来。", version_id: 6},
  {id: 162720, book: "Rev", verse: "1.003", unformatted: "那宣读这预言之话的，那些听见这预言之话并遵守其中所记载的，都是蒙福的，因为时候快到了。", version_id: 6},
  {id: 162721, book: "Rev", verse: "1.004", unformatted: "约翰，\n致那在亚细亚省的七个教会：\n愿恩典与平安，从今在、昔在、将要来临的那一位，从他宝座前的七个灵，", version_id: 6},
  {id: 162722, book: "Rev", verse: "1.005", unformatted: "从那信实的见证者、死人中首先复活的、地上众君王的元首耶稣基督，临到你们！\n他爱我们，用自己的血，把我们从我们的罪孽中释放出来，", version_id: 6},
  {id: 162723, book: "Rev", verse: "1.006", unformatted: "又使我们成为国度，做他父神的祭司。愿荣耀和权能都归于他，直到永永远远！阿们。", version_id: 6},
  {id: 162724, book: "Rev", verse: "1.007", unformatted: "“看哪，他在云彩中来临，\n所有人的眼睛都将看见他，\n连那些刺他的人也将看见他；\n地上的万族都要因他捶胸哀哭。”\n确实如此，阿们。", version_id: 6},
  {id: 162725, book: "Rev", verse: "1.008", unformatted: "主、神说：“我就是‘阿尔法’，也是‘欧米伽’，是今在、昔在、将要来临的那一位，是全能者。”", version_id: 6},
  {id: 162726, book: "Rev", verse: "1.009", unformatted: "约翰见主的异象\n我约翰，是你们的弟兄，是与你们在耶稣的患难、国度、忍耐里一同有份的，为神的话语和有关耶稣的见证，曾经住在那叫做帕特莫斯的海岛上。", version_id: 6},
  {id: 162727, book: "Rev", verse: "1.01", unformatted: "有一个主日，我在灵里，听见我后面有一个大声音，如同号角声，", version_id: 6},
  {id: 162728, book: "Rev", verse: "1.011", unformatted: "说：“把你所看见的写在书卷上，送到那七个教会：以弗所、士每拿、佩尔伽马、锡亚蒂拉、萨迪斯、费拉德菲亚和劳迪西亚。”", version_id: 6},
  {id: 162729, book: "Rev", verse: "1.012", unformatted: "我转过身，要看看谁的声音对我说话。我转过身就看见了七座金灯台。", version_id: 6},
  {id: 162730, book: "Rev", verse: "1.013", unformatted: "灯台中间有一位仿佛人子的，身穿垂到脚的长袍，胸间束着金带。", version_id: 6},
  {id: 162731, book: "Rev", verse: "1.014", unformatted: "他的头和头发洁白，如白羊毛、如雪；他的眼睛如同火焰；", version_id: 6},
  {id: 162732, book: "Rev", verse: "1.015", unformatted: "他的两脚仿佛在炉中被炼过的精铜；他的声音好像众水的声音；", version_id: 6},
  {id: 162733, book: "Rev", verse: "1.016", unformatted: "在他的右手中拿着七颗星；从他口中发出一把双刃的利剑；他的面容好像烈日发光。", version_id: 6},
  {id: 162734, book: "Rev", verse: "1.017", unformatted: "我一看见他，就仆倒在他的脚前，像死人那样。他把右手放在我身上，说：“不要怕！我就是首先的，是末后的，", version_id: 6},
  {id: 162735, book: "Rev", verse: "1.018", unformatted: "是那永生的。我曾经死过，可是看哪，我现在活着，直到永永远远，并且拿着死亡和阴间的钥匙。", version_id: 6},
  {id: 162736, book: "Rev", verse: "1.019", unformatted: "因此，你要把所看见的，就是现在的事，和此后将要发生的事，都写下来。", version_id: 6},
  {id: 162737, book: "Rev", verse: "1.02", unformatted: "你所看见的，我右手中的七颗星，和这七座金灯台的奥秘就是：七颗星是七个教会的使者，七座灯台是七个教会。", version_id: 6},
  {id: 162738, book: "Rev", verse: "2.001", unformatted: "给七个教会的信\n给以弗所教会的信\n“你要给那在以弗所教会的使者写信说：\n“那位右手握着七颗星、在七座金灯台中间行走的，这样说：", version_id: 6},
  {id: 162739, book: "Rev", verse: "2.002", unformatted: "我知道你的行为，包括你的劳苦和忍耐，也知道你不能容忍恶人。你曾考验那些自称是使徒，其实不是使徒的，而且看出他们是假的。", version_id: 6},
  {id: 162740, book: "Rev", verse: "2.003", unformatted: "你也有忍耐，曾为我的名劳苦，并不疲乏。", version_id: 6},
  {id: 162741, book: "Rev", verse: "2.004", unformatted: "然而，我有事要责备你，就是你放弃了你起初的爱；", version_id: 6},
  {id: 162742, book: "Rev", verse: "2.005", unformatted: "所以你应当记住你是从哪里跌落的，应当悔改，应当行你起初所做的事！否则我就要来到你那里；如果你不悔改，我就要把你的灯台从原处挪去。", version_id: 6},
  {id: 162743, book: "Rev", verse: "2.006", unformatted: "不过，你有这一点可取之处，就是你恨恶尼古拉派的行为，那些行为也是我所恨恶的。", version_id: 6},
  {id: 162744, book: "Rev", verse: "2.007", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！那得胜的，我将要准许他吃神乐园中生命树的果子。", version_id: 6},
  {id: 162745, book: "Rev", verse: "2.008", unformatted: "给士每拿教会的信\n“你要给士每拿教会的使者写信说：\n“那位首先的、末后的，那位曾经死去又活过来的，这样说：", version_id: 6},
  {id: 162746, book: "Rev", verse: "2.009", unformatted: "我知道你的患难和贫穷；其实你是富有的。我也知道从那些自称是犹太人而来的毁谤，其实他们不是犹太人，而是属于撒旦会堂的。", version_id: 6},
  {id: 162747, book: "Rev", verse: "2.01", unformatted: "你不要怕将要受的苦。看哪，魔鬼要把你们当中一些人投进监狱，使你们受考验；你们将受十天的患难。你应当至死忠心，我就把生命的冠冕赐给你。", version_id: 6},
  {id: 162748, book: "Rev", verse: "2.011", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！那得胜的，绝不会被第二次的死亡所伤害。", version_id: 6},
  {id: 162749, book: "Rev", verse: "2.012", unformatted: "给佩尔伽马教会的信\n“你要给那在佩尔伽马教会的使者写信说：\n“那位有双刃利剑的，这样说：", version_id: 6},
  {id: 162750, book: "Rev", verse: "2.013", unformatted: "我知道你居住的地方，就是撒旦宝座所在的地方。不过即使在我忠心的见证人安提帕的那些日子里，你还持守我的名，也没有否认对我的信仰；这安提帕是在你们那里，也就是在撒旦居住的地方被杀害的。", version_id: 6},
  {id: 162751, book: "Rev", verse: "2.014", unformatted: "然而，我有几件事要责备你，就是在你那里有些人持守巴兰的教导。这巴兰曾教唆巴勒把绊脚石放在以色列子民面前，就是使他们吃祭过偶像的食物，并且行淫乱。", version_id: 6},
  {id: 162752, book: "Rev", verse: "2.015", unformatted: "同样，你那里也有些人持守与尼古拉派一样的教导。", version_id: 6},
  {id: 162753, book: "Rev", verse: "2.016", unformatted: "所以，你应当悔改，否则我就会立即来到你那里，用我口中的剑与他们交战。", version_id: 6},
  {id: 162754, book: "Rev", verse: "2.017", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！那得胜的，我将要赐给他那隐藏的吗哪。我还要赐给他一块白石头，石头上有一个写好的新名字，除了那领受的人以外，没有人认识这名字。", version_id: 6},
  {id: 162755, book: "Rev", verse: "2.018", unformatted: "给锡亚蒂拉教会的信\n“你要给那在锡亚蒂拉教会的使者写信说：\n“神的儿子，就是那位眼睛如火焰、两脚仿佛精铜的，这样说：", version_id: 6},
  {id: 162756, book: "Rev", verse: "2.019", unformatted: "我知道你的行为，包括你的爱心、信仰、服事和忍耐，也知道你最近所做的比先前的更多。", version_id: 6},
  {id: 162757, book: "Rev", verse: "2.02", unformatted: "然而，我有事要责备你，就是你容忍那自称是女先知的妇人耶洗别，教唆并迷惑我的奴仆们行淫乱、吃祭过偶像的食物。", version_id: 6},
  {id: 162758, book: "Rev", verse: "2.021", unformatted: "我曾给她时间，好让她悔改，她却不肯悔改离开自己的淫乱。", version_id: 6},
  {id: 162759, book: "Rev", verse: "2.022", unformatted: "看哪，我要把她扔在痛苦的床上。那些与她通奸的人，如果不悔改离开她的行为，我也要把他们扔在大患难中。", version_id: 6},
  {id: 162760, book: "Rev", verse: "2.023", unformatted: "我要用瘟疫击杀她的儿女，这样各教会就将知道我就是洞察情感和心思的那一位；我也要照着你们的行为回报你们每个人。", version_id: 6},
  {id: 162761, book: "Rev", verse: "2.024", unformatted: "至于你们其余在锡亚蒂拉的人，就是那些不接受这教训的、那些不知道所谓“撒旦深奥之事”的，我告诉你们：我不会把另一个重担放在你们身上。", version_id: 6},
  {id: 162762, book: "Rev", verse: "2.025", unformatted: "不过你们应当持守你们所拥有的，直到我来。", version_id: 6},
  {id: 162763, book: "Rev", verse: "2.026", unformatted: "那得胜的，那把我的工作持守到底的，我将要赐给他权柄统管列国——", version_id: 6},
  {id: 162764, book: "Rev", verse: "2.027", unformatted: "他将用铁杖治理列国，\n如同打碎陶器那样——就像我也从我父那里领受了权柄一样；", version_id: 6},
  {id: 162765, book: "Rev", verse: "2.028", unformatted: "我还要把晨星赐给他。", version_id: 6},
  {id: 162766, book: "Rev", verse: "2.029", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！", version_id: 6},
  {id: 162767, book: "Rev", verse: "3.001", unformatted: "给萨迪斯教会的信\n“你要给那在萨迪斯教会的使者写信说：\n“那位有神的七个灵和七颗星的，这样说：我知道你的行为，你名义上是活的，其实你是死的。", version_id: 6},
  {id: 162768, book: "Rev", verse: "3.002", unformatted: "你当警醒，坚固那些剩余的、快要死去的人。实际上，我看出你的行为在我的神面前是不完全的；", version_id: 6},
  {id: 162769, book: "Rev", verse: "3.003", unformatted: "所以你应当记住你所领受、所听见的是什么；你应当持守，应当悔改。如果你不警醒，我就要来临，就像贼来临那样；而你绝不会知道我什么时候临到你。", version_id: 6},
  {id: 162770, book: "Rev", verse: "3.004", unformatted: "然而在萨迪斯，你还有几名是没有玷污自己衣服的，他们将要身穿白衣与我同行，因为他们是配得上的。", version_id: 6},
  {id: 162771, book: "Rev", verse: "3.005", unformatted: "那得胜的，将要同样穿上白衣，我绝不从生命册上抹去他的名字；而且我将要在我父面前、在我父的众天使面前，承认他的名字。", version_id: 6},
  {id: 162772, book: "Rev", verse: "3.006", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！", version_id: 6},
  {id: 162773, book: "Rev", verse: "3.007", unformatted: "给费拉德菲亚教会的信\n“你要给那在费拉德菲亚教会的使者写信说：\n“那位神圣的、真实的，拿着大卫的钥匙、开了就没有人能关、关了就没有人能开的，这样说：", version_id: 6},
  {id: 162774, book: "Rev", verse: "3.008", unformatted: "我知道你的行为。看哪，我在你面前赐下了一个敞开的门，是没有人能关上的；这是因为你能力小，但你遵守了我的话语，没有否认我的名。", version_id: 6},
  {id: 162775, book: "Rev", verse: "3.009", unformatted: "看哪，我要使那些属于撒旦会堂的，就是那些自称是犹太人，其实不是犹太人，而是说谎的人——看哪，我要使他们来，在你脚前下拜；他们就会知道我已经爱了你，", version_id: 6},
  {id: 162776, book: "Rev", verse: "3.01", unformatted: "因为你遵守了我忍耐的教导，我还要保守你脱离那试炼的时刻，就是那将要临到普天下，考验住在地上之人的时刻。", version_id: 6},
  {id: 162777, book: "Rev", verse: "3.011", unformatted: "我快要来了！你应当持守你所拥有的，免得有人拿走你的冠冕。", version_id: 6},
  {id: 162778, book: "Rev", verse: "3.012", unformatted: "那得胜的，我将要让他在我神的圣所中做柱石，他绝不再出去。我要把我神的名，我神之城的名——就是从我的神那里、从天上降下来的新耶路撒冷的名，和我的新名，都写在他身上。", version_id: 6},
  {id: 162779, book: "Rev", verse: "3.013", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！", version_id: 6},
  {id: 162780, book: "Rev", verse: "3.014", unformatted: "给劳迪西亚教会的信\n“你要给那在劳迪西亚教会的使者写信说：\n“那位阿们，就是那信实的、真实的见证者，神创造之物的本源，这样说：", version_id: 6},
  {id: 162781, book: "Rev", verse: "3.015", unformatted: "我知道你的行为，你既不冷也不热；我真希望你或冷或热。", version_id: 6},
  {id: 162782, book: "Rev", verse: "3.016", unformatted: "正因为你是温的，既不热也不冷，我就要把你从我口中吐出去，", version_id: 6},
  {id: 162783, book: "Rev", verse: "3.017", unformatted: "因为你说‘我是富有的；我发了财，什么都不缺’，却不知道你是可悲的、可怜的、贫穷的、瞎眼的、赤身的。", version_id: 6},
  {id: 162784, book: "Rev", verse: "3.018", unformatted: "我建议你向我买被火炼过的金子，使你富足；又买白衣穿上，使你赤身的羞耻不露出来；又买眼药抹你的眼睛，使你能看见。", version_id: 6},
  {id: 162785, book: "Rev", verse: "3.019", unformatted: "凡是我所爱的，我都责备、管教。所以你应当热心，应当悔改。", version_id: 6},
  {id: 162786, book: "Rev", verse: "3.02", unformatted: "看哪，我站在门口敲门。如果有人听见我的声音就开门，我就会进到他那里，而且我与他、他与我，将要一同吃宴席。", version_id: 6},
  {id: 162787, book: "Rev", verse: "3.021", unformatted: "那得胜的，我将要准许他与我一起坐在我的宝座上；就像我已经得胜，与我父一起坐在他的宝座上那样。", version_id: 6},
  {id: 162788, book: "Rev", verse: "3.022", unformatted: "“凡是有耳的，就应当听圣灵向各教会所说的话！”", version_id: 6},
  {id: 162789, book: "Rev", verse: "4.001", unformatted: "天上的宝座\n这些事以后，我观看，看哪，天上有一个敞开的门。我先前所听见的，那如同号角对我说话的声音就说：“你上到这里来！我要把此后必须发生的事指示给你。”", version_id: 6},
  {id: 162790, book: "Rev", verse: "4.002", unformatted: "我立刻就在灵里了，看哪，有一个宝座安放在天上，有一位坐在宝座上。", version_id: 6},
  {id: 162791, book: "Rev", verse: "4.003", unformatted: "坐着的那一位，看起来仿佛碧玉和红宝石；又有一道彩虹围着宝座，看起来仿佛绿宝石。", version_id: 6},
  {id: 162792, book: "Rev", verse: "4.004", unformatted: "座位的周围，还有二十四个座位；在那些宝座上，坐着二十四位长老，身穿白衣，头戴金冠冕；", version_id: 6},
  {id: 162793, book: "Rev", verse: "4.005", unformatted: "从宝座中发出闪电、声音和雷鸣；在宝座前，有七支燃烧着的火把，就是神的七个灵；", version_id: 6},
  {id: 162794, book: "Rev", verse: "4.006", unformatted: "宝座前面，好像玻璃海，仿佛水晶；在宝座中间和宝座周围，有四个活物，遍体前后布满眼睛。", version_id: 6},
  {id: 162795, book: "Rev", verse: "4.007", unformatted: "第一个活物仿佛狮子，第二个活物仿佛牛犊，第三个活物有像人一样的脸，第四个活物仿佛飞翔的鹰。", version_id: 6},
  {id: 162796, book: "Rev", verse: "4.008", unformatted: "那四个活物，各个都有六个翅膀，遍体内外都布满了眼睛。他们日夜不停地说：\n“圣哉！圣哉！圣哉！\n主、神、全能者，\n昔在、今在、将要来临的那一位！”", version_id: 6},
  {id: 162797, book: "Rev", verse: "4.009", unformatted: "每当四个活物把荣耀、尊贵和感谢归给坐在宝座上、活到永永远远的那一位时，", version_id: 6},
  {id: 162798, book: "Rev", verse: "4.01", unformatted: "二十四位长老就俯伏在坐宝座的那一位面前，敬拜活到永永远远的那一位，并且把自己的冠冕放在宝座前，说：", version_id: 6},
  {id: 162799, book: "Rev", verse: "4.011", unformatted: "“我们的主、我们的神哪，\n你配接受荣耀、尊贵和权能，\n因为你创造了万有；\n万有是因着你的旨意而存在、被创造的。”", version_id: 6},
  {id: 162800, book: "Rev", verse: "5.001", unformatted: "羔羊与书卷\n接着，我看见坐在宝座上的那一位右手中拿着一个书卷，内外都写着字，用七个封印封住。", version_id: 6},
  {id: 162801, book: "Rev", verse: "5.002", unformatted: "我又看见一位强有力的天使，大声地宣告：“谁配打开这书卷，解开它的封印呢？”", version_id: 6},
  {id: 162802, book: "Rev", verse: "5.003", unformatted: "可是在天上、在地上、在地底下，都没有一个能打开或阅读那书卷的。", version_id: 6},
  {id: 162803, book: "Rev", verse: "5.004", unformatted: "于是我就大哭，因为找不到配打开或阅读那书卷的。", version_id: 6},
  {id: 162804, book: "Rev", verse: "5.005", unformatted: "可是长老中有一位对我说：“不要哭了！看哪，那出于犹大支派的狮子、大卫的根已经得胜，可以打开那书卷和它的七个封印。”", version_id: 6},
  {id: 162805, book: "Rev", verse: "5.006", unformatted: "随后，我看见在宝座和四个活物当中，在长老们当中，站着一只羔羊，好像是被杀过的。羔羊有七只角和七只眼，那七只眼就是神的七个灵，是奉差派往全地去的。", version_id: 6},
  {id: 162806, book: "Rev", verse: "5.007", unformatted: "羔羊上前去，从坐在宝座上那一位的右手中拿了书卷。", version_id: 6},
  {id: 162807, book: "Rev", verse: "5.008", unformatted: "羔羊配得颂赞\n他一接到书卷，四个活物和二十四位长老就俯伏在羔羊面前，他们各拿着一把竖琴和盛满香的金碗；这些香就是圣徒们的祷告。", version_id: 6},
  {id: 162808, book: "Rev", verse: "5.009", unformatted: "他们唱着一首新歌，说：\n“你配接受那书卷，\n配打开它的封印，\n因为你曾被杀，\n用自己的血，\n从各支派、各语言群体、\n各民族、各国家，\n为神赎回了人，", version_id: 6},
  {id: 162809, book: "Rev", verse: "5.01", unformatted: "使他们成为我们神的国度和祭司；\n他们将要在地上做王。”", version_id: 6},
  {id: 162810, book: "Rev", verse: "5.011", unformatted: "我又观看，就听见在宝座、活物和长老们的周围，有许多天使的声音；他们的数目有千千万万。", version_id: 6},
  {id: 162811, book: "Rev", verse: "5.012", unformatted: "他们大声说：\n“那被杀的羔羊配接受\n权能、财富、智慧、力量、\n尊贵、荣耀和颂赞！”", version_id: 6},
  {id: 162812, book: "Rev", verse: "5.013", unformatted: "我又听见天上、地上、地底下和海中的一切被造之物，以及天地间的万有，都在说：\n“愿颂赞、尊贵、荣耀和权能，\n都归于坐在宝座上的那一位，\n都归于羔羊，直到永永远远！”", version_id: 6},
  {id: 162813, book: "Rev", verse: "5.014", unformatted: "四个活物就说：“阿们！”长老们也都俯伏敬拜。", version_id: 6},
  {id: 162814, book: "Rev", verse: "6.001", unformatted: "打开第一印\n我又观看，羔羊打开七个封印中第一个封印的时候，我就听见四个活物中的一个，用像雷鸣一样的声音说：“来！”", version_id: 6},
  {id: 162815, book: "Rev", verse: "6.002", unformatted: "我又观看，看哪，有一匹白马。那骑马的拿着一张弓；他被赐予了一顶冠冕，就出去，得胜了又要得胜。", version_id: 6},
  {id: 162816, book: "Rev", verse: "6.003", unformatted: "打开第二印\n羔羊打开第二个封印的时候，我听见第二个活物说：“来！”", version_id: 6},
  {id: 162817, book: "Rev", verse: "6.004", unformatted: "就另有一匹马出来，是火红色的。那骑马的被准许从地上夺走和平，使人们互相残杀；他又被赐予了一把大刀。", version_id: 6},
  {id: 162818, book: "Rev", verse: "6.005", unformatted: "打开第三印\n羔羊打开第三个封印的时候，我听见第三个活物说：“来！”我又观看，看哪，有一匹黑色的马。那骑马的，手里拿着天平。", version_id: 6},
  {id: 162819, book: "Rev", verse: "6.006", unformatted: "我就听见在四个活物中间好像有声音说：“一升小麦一个银币，三升大麦一个银币；油和酒不可糟蹋！”", version_id: 6},
  {id: 162820, book: "Rev", verse: "6.007", unformatted: "打开第四印\n羔羊打开第四个封印的时候，我听见第四个活物的声音，说：“来！”", version_id: 6},
  {id: 162821, book: "Rev", verse: "6.008", unformatted: "我又观看，看哪，有一匹灰绿色的马。那骑马的，名叫“死亡”，阴间伴随着他。他们被赐予了权柄可以统管地上的四分之一，用刀剑、饥荒、瘟疫和地上的野兽去杀害人。", version_id: 6},
  {id: 162822, book: "Rev", verse: "6.009", unformatted: "打开第五印\n羔羊打开第五个封印的时候，我看见在祭坛底下有那些为了神的话语、为了自己所持守的见证而被杀之人的灵魂。", version_id: 6},
  {id: 162823, book: "Rev", verse: "6.01", unformatted: "他们大声呼喊说：“神圣、真实的主啊！你不施行审判，不为我们的血向住在地上的人讨回公道，还要多久呢？”", version_id: 6},
  {id: 162824, book: "Rev", verse: "6.011", unformatted: "于是他们每个人都被赐予了一件白袍；有话吩咐他们还要安息一些时候，直到那些与他们同做奴仆的和他们的弟兄，就是那些将要像他们一样被杀的人，满足了数目。", version_id: 6},
  {id: 162825, book: "Rev", verse: "6.012", unformatted: "打开第六印\n我又观看，羔羊打开第六个封印的时候，发生了大地震。太阳变黑像粗毛布，整个月亮变得像血。", version_id: 6},
  {id: 162826, book: "Rev", verse: "6.013", unformatted: "天上的星辰坠落到地上，就像无花果树被大风摇动，落下它未熟的果子那样。", version_id: 6},
  {id: 162827, book: "Rev", verse: "6.014", unformatted: "天被劈裂，好像书卷被卷起来；一切山岭和海岛也都从原处被挪开了。", version_id: 6},
  {id: 162828, book: "Rev", verse: "6.015", unformatted: "地上的众君王、大臣、军官、富有的、有权的、所有的奴隶和自由人，都藏在洞穴里和山岭的岩石间。", version_id: 6},
  {id: 162829, book: "Rev", verse: "6.016", unformatted: "他们对山岭和岩石说：“倒在我们身上吧！把我们藏起来，好躲避坐在宝座上那一位的脸和羔羊的震怒；", version_id: 6},
  {id: 162830, book: "Rev", verse: "6.017", unformatted: "因为他们震怒的大日子到了，谁能站得住呢？”", version_id: 6},
  {id: 162831, book: "Rev", verse: "7.001", unformatted: "以色列人受印\n这事以后，我看见四位天使站在大地的四角上，掌握着大地四方的风，不让风吹在大地、海洋或任何树木上。", version_id: 6},
  {id: 162832, book: "Rev", verse: "7.002", unformatted: "接着，我看见另一位天使，拿着永生神的封印，从东方上来。他对那些被准许伤害大地和海洋的四位天使大声呼喊，", version_id: 6},
  {id: 162833, book: "Rev", verse: "7.003", unformatted: "说：“不要伤害大地、海洋和树木，直到我们在我们神的奴仆们额上盖了印。”", version_id: 6},
  {id: 162834, book: "Rev", verse: "7.004", unformatted: "我又听见了那些被盖上印之人的数目，\n以色列子民各支派中被盖上印的有十四万四千人：", version_id: 6},
  {id: 162835, book: "Rev", verse: "7.005", unformatted: "犹大支派中被盖上印的有一万二千人；\n鲁本支派中有一万二千人；\n迦得支派中有一万二千人；", version_id: 6},
  {id: 162836, book: "Rev", verse: "7.006", unformatted: "亚设支派中有一万二千人；\n拿弗他利支派中有一万二千人；\n玛拿西支派中有一万二千人；", version_id: 6},
  {id: 162837, book: "Rev", verse: "7.007", unformatted: "西缅支派中有一万二千人；\n利未支派中有一万二千人；\n以萨迦支派中有一万二千人；", version_id: 6},
  {id: 162838, book: "Rev", verse: "7.008", unformatted: "西布伦支派中有一万二千人；\n约瑟支派中有一万二千人；\n便雅悯支派中被盖上印的有一万二千人。", version_id: 6},
  {id: 162839, book: "Rev", verse: "7.009", unformatted: "各族各方的群众\n这些事以后，我观看，看哪，有一大群人，没有人能够数过来。他们来自各国家、各支派、各民族、各语言群体。他们身穿白袍，手里拿着棕榈枝，站在宝座和羔羊面前，", version_id: 6},
  {id: 162840, book: "Rev", verse: "7.01", unformatted: "大声呼喊说：\n“救恩属于坐在宝座上我们的神、\n属于羔羊！”", version_id: 6},
  {id: 162841, book: "Rev", verse: "7.011", unformatted: "所有的天使都站在宝座、长老们和四个活物的周围；他们在宝座前，把脸伏在地上，敬拜神，", version_id: 6},
  {id: 162842, book: "Rev", verse: "7.012", unformatted: "说：\n“阿们！愿颂赞、荣耀、智慧、\n感谢、尊贵、权能、力量\n都归于我们的神，直到永永远远！阿们。”", version_id: 6},
  {id: 162843, book: "Rev", verse: "7.013", unformatted: "长老中有一位问我，说：“这些身穿白袍的是谁？他们是从哪里来的？”", version_id: 6},
  {id: 162844, book: "Rev", verse: "7.014", unformatted: "我对他说：“我主啊，你是知道的。”\n他就对我说：\n“这些人都是从大患难中出来的。\n他们用羔羊的血\n洗净了自己的袍子，使它们洁白了。", version_id: 6},
  {id: 162845, book: "Rev", verse: "7.015", unformatted: "因此他们在神的宝座前，\n日夜在他的圣所中事奉他。\n那位坐在宝座上的也将庇护他们：", version_id: 6},
  {id: 162846, book: "Rev", verse: "7.016", unformatted: "他们将不再饥饿，也不再干渴；\n太阳或任何炎热也不会伤到他们；", version_id: 6},
  {id: 162847, book: "Rev", verse: "7.017", unformatted: "因为在宝座中的羔羊将牧养他们，\n领他们到生命水的泉源；\n神要从他们的眼中抹去一切泪水。”", version_id: 6},
  {id: 162848, book: "Rev", verse: "8.001", unformatted: "打开第七印\n羔羊打开第七个封印的时候，天上寂静无声，约有半小时。", version_id: 6},
  {id: 162849, book: "Rev", verse: "8.002", unformatted: "接着，我看见站在神面前的七位天使，被赐予了七支号角。", version_id: 6},
  {id: 162850, book: "Rev", verse: "8.003", unformatted: "随后，另一位天使来了，拿着金香炉，站在祭坛旁边。他被赐予了很多香，要和所有圣徒的祷告一起献在宝座前的金祭坛上。", version_id: 6},
  {id: 162851, book: "Rev", verse: "8.004", unformatted: "那香的烟和圣徒们的祷告，从天使的手中升到神的面前。", version_id: 6},
  {id: 162852, book: "Rev", verse: "8.005", unformatted: "接着，那位天使拿起香炉，把它装满了祭坛上的火，扔到地上；于是就有雷鸣、声音、闪电和地震。", version_id: 6},
  {id: 162853, book: "Rev", verse: "8.006", unformatted: "这时候，拿着七支号角的七位天使就预备要吹响号角。", version_id: 6},
  {id: 162854, book: "Rev", verse: "8.007", unformatted: "吹第一支号\n第一位天使吹响号角，就有冰雹和火，混着血被撒在地上；于是大地的三分之一被烧掉，树木的三分之一被烧掉，所有的青草也都被烧掉了。", version_id: 6},
  {id: 162855, book: "Rev", verse: "8.008", unformatted: "吹第二支号\n第二位天使吹响号角，就有一座像被火燃烧着的大山被丢进海里；于是海的三分之一变成血，", version_id: 6},
  {id: 162856, book: "Rev", verse: "8.009", unformatted: "海中有生命的被造之物的三分之一也死了，众船只的三分之一也被毁了。", version_id: 6},
  {id: 162857, book: "Rev", verse: "8.01", unformatted: "吹第三支号\n第三位天使吹响号角，就有一颗像火把一样燃烧着的大星，从天上坠落，落在江河的三分之一和众水的泉源上。", version_id: 6},
  {id: 162858, book: "Rev", verse: "8.011", unformatted: "这颗星的名字叫“苦艾”。于是众水的三分之一变为苦艾，许多人就因这水变苦而死去了。", version_id: 6},
  {id: 162859, book: "Rev", verse: "8.012", unformatted: "吹第四支号\n第四位天使吹响号角，太阳的三分之一、月亮的三分之一、星辰的三分之一都被击打，以致它们的三分之一变黑了；于是白昼的三分之一没有光，夜晚也是如此。", version_id: 6},
  {id: 162860, book: "Rev", verse: "8.013", unformatted: "我又观看，就听见一只在空中飞翔的鹰大声说：“有祸了！有祸了！住在地上的人有祸了！因为三位天使快要吹响其余的号角。”", version_id: 6},
  {id: 162861, book: "Rev", verse: "9.001", unformatted: "吹第五支号\n第五位天使吹响号角，我就看见从天上坠落到地上的一颗星。它被赐予了无底坑口的钥匙。", version_id: 6},
  {id: 162862, book: "Rev", verse: "9.002", unformatted: "它打开了无底坑口，就有烟从坑口里往上冒，好像大火炉的烟，甚至太阳和天空因坑口的烟而变为黑暗。", version_id: 6},
  {id: 162863, book: "Rev", verse: "9.003", unformatted: "有蝗虫从烟中出来，到了地上；它们被赐予了能力，就像地上的蝎子所具有的能力那样。", version_id: 6},
  {id: 162864, book: "Rev", verse: "9.004", unformatted: "有话吩咐它们不可伤害地上的草、任何绿色植物、任何树木，只可伤害那些额上没有神印记的人；", version_id: 6},
  {id: 162865, book: "Rev", verse: "9.005", unformatted: "可是不准许杀死这些人，只可使他们受折磨五个月。他们所受的折磨，就像人被蝎子螫到时的折磨那样。", version_id: 6},
  {id: 162866, book: "Rev", verse: "9.006", unformatted: "在那些日子里，人们将会求死，却绝不得死；他们渴望死，死却逃避他们。", version_id: 6},
  {id: 162867, book: "Rev", verse: "9.007", unformatted: "这些蝗虫的样式仿佛是所预备好的要出战的马。它们的头上像有类似金子的冠冕；它们的脸面像人的脸面；", version_id: 6},
  {id: 162868, book: "Rev", verse: "9.008", unformatted: "它们有头发像女人的头发；它们的牙齿像狮子的牙齿；", version_id: 6},
  {id: 162869, book: "Rev", verse: "9.009", unformatted: "它们有护胸甲像铁甲；它们翅膀的声音像许多马匹的战车奔驰出战的声音；", version_id: 6},
  {id: 162870, book: "Rev", verse: "9.01", unformatted: "它们有尾巴仿佛蝎子，又有毒刺，它们的威力在它们的尾巴上，可以伤害人五个月。", version_id: 6},
  {id: 162871, book: "Rev", verse: "9.011", unformatted: "有一个王统管它们，就是那无底坑的使者；他的名字，希伯来语是“阿巴顿”，按希腊语的名字是“阿波伦”。", version_id: 6},
  {id: 162872, book: "Rev", verse: "9.012", unformatted: "第一个灾祸过去了。看哪，这些事以后还有两个灾祸就要来临。", version_id: 6},
  {id: 162873, book: "Rev", verse: "9.013", unformatted: "吹第六支号\n第六位天使吹响号角，我就听见一个声音从神面前金祭坛的四只角传来，", version_id: 6},
  {id: 162874, book: "Rev", verse: "9.014", unformatted: "对拿着号角的第六位天使说：“把那捆绑在幼发拉底大河的四个使者释放出来！”", version_id: 6},
  {id: 162875, book: "Rev", verse: "9.015", unformatted: "于是那四个使者就被释放了。他们是为此年、此月、此日、此时所预备好的，为要杀死人类的三分之一。", version_id: 6},
  {id: 162876, book: "Rev", verse: "9.016", unformatted: "骑兵的数目有两亿；我听见了它们的数目。", version_id: 6},
  {id: 162877, book: "Rev", verse: "9.017", unformatted: "我在异象中看见的那些马和骑马的是这样：他们穿着火红色、紫玛瑙色、硫磺色的护胸甲；马的头像狮子的头，从它们的口中有火、烟、硫磺喷出来。", version_id: 6},
  {id: 162878, book: "Rev", verse: "9.018", unformatted: "由于这三样灾害，就是从马口中所喷出的火、烟、硫磺，人类的三分之一被杀了。", version_id: 6},
  {id: 162879, book: "Rev", verse: "9.019", unformatted: "原来这些马的威力在它们的口中和尾巴上，因为它们的尾巴仿佛蛇，有头；它们用这些头来伤害人。", version_id: 6},
  {id: 162880, book: "Rev", verse: "9.02", unformatted: "可是，其余没有被这些灾害所杀的人，还是不悔改离开自己手所做的，不停止拜鬼魔和那些金、银、铜、石、木的偶像——它们既不能看、也不能听、更不能走。", version_id: 6},
  {id: 162881, book: "Rev", verse: "9.021", unformatted: "他们也不悔改离开自己那些杀人、邪术、淫乱、偷窃之事。", version_id: 6},
  {id: 162882, book: "Rev", verse: "10.001", unformatted: "强有力的天使与小书卷\n接着，我看见另一位强有力的天使从天上降下来，披着云彩；又有一道彩虹在他头上。他的脸像太阳，脚像火柱，", version_id: 6},
  {id: 162883, book: "Rev", verse: "10.002", unformatted: "手里拿着一个打开的小书卷。他右脚踩在海上，左脚踩在地上，", version_id: 6},
  {id: 162884, book: "Rev", verse: "10.003", unformatted: "大声呼喊，好像狮子吼叫。他一呼喊，那七个雷就发出自己的声音。", version_id: 6},
  {id: 162885, book: "Rev", verse: "10.004", unformatted: "当那七个雷发出了自己的声音，我正要写下来，却听见有声音从天上传来，说：“你要封住七个雷所说的话，不可写下来。”", version_id: 6},
  {id: 162886, book: "Rev", verse: "10.005", unformatted: "接着，我所看见的那位站在海上和地上的天使，向天举起他的右手，", version_id: 6},
  {id: 162887, book: "Rev", verse: "10.006", unformatted: "指着活到永永远远的那一位，就是创造了天和天上之物、地和地上之物、海和海中之物的那一位，起誓说：“不再有时间了！", version_id: 6},
  {id: 162888, book: "Rev", verse: "10.007", unformatted: "在第七位天使要吹响号角的那些日子里，神的奥秘就要成就，就像神向他的奴仆先知们所宣告的那样。”", version_id: 6},
  {id: 162889, book: "Rev", verse: "10.008", unformatted: "接着，我所听见的那从天上来的声音又对我讲话，说：“你去，从那站在海上和地上的天使手中，把那打开的书卷拿过来。”", version_id: 6},
  {id: 162890, book: "Rev", verse: "10.009", unformatted: "我就走到那位天使面前，请他把小书卷给我。他就对我说：“拿去，把它吃掉！它会使你的肚子感到苦涩，但是在你口中会像蜜一样甘甜。”", version_id: 6},
  {id: 162891, book: "Rev", verse: "10.01", unformatted: "我从天使的手中拿了小书卷，把它吃了；果然在我口中像蜜一样甘甜，可是吃了以后，我的肚子就感到苦涩。", version_id: 6},
  {id: 162892, book: "Rev", verse: "10.011", unformatted: "然后他们对我说：“你必须指着许多民族、国家、语言群体和君王再说预言。”", version_id: 6},
  {id: 162893, book: "Rev", verse: "11.001", unformatted: "两位见证人\n接着，我被赐予了一根仿佛量杖的芦苇。有声音对我说：“起来，量一量神的圣所和祭坛，数一数在那里敬拜的人。", version_id: 6},
  {id: 162894, book: "Rev", verse: "11.002", unformatted: "不过要排除圣所外面的院子，不要测量它，因为那里已经给了外邦人。外邦人将要践踏圣城四十二个月。", version_id: 6},
  {id: 162895, book: "Rev", verse: "11.003", unformatted: "我将赐能力给我的两个见证人，他们将披着麻衣，做先知传道一千二百六十天。”", version_id: 6},
  {id: 162896, book: "Rev", verse: "11.004", unformatted: "这两个人就是站在大地之主面前的那两棵橄榄树、那两座灯台。", version_id: 6},
  {id: 162897, book: "Rev", verse: "11.005", unformatted: "如果有人想要伤害他们，火就从他们的口中出来，吞灭他们的敌人；如果有人想要伤害他们，就必定这样被杀。", version_id: 6},
  {id: 162898, book: "Rev", verse: "11.006", unformatted: "这两个人有权柄闭塞天空，使雨水在他们做先知传道的日子不降下来；又有权柄统管众水，使水变成血，并且能随时随意用各样灾害击打大地。", version_id: 6},
  {id: 162899, book: "Rev", verse: "11.007", unformatted: "见证人殉道\n他们做完了见证以后，那从无底坑里上来的兽将与他们作战，并且将胜过他们，把他们杀死。", version_id: 6},
  {id: 162900, book: "Rev", verse: "11.008", unformatted: "他们的尸体会被抛在大城的街道上。这大城按属灵的意思叫做所多玛，又叫做埃及，也就是他们的主被钉十字架的地方。", version_id: 6},
  {id: 162901, book: "Rev", verse: "11.009", unformatted: "来自各民族、各支派、各语言群体和各国家的人，都要观看他们的尸体三天半，又不准人把他们的尸体安放在坟墓里。", version_id: 6},
  {id: 162902, book: "Rev", verse: "11.01", unformatted: "住在地上的人都因他们的死而欢喜、庆祝，并且要彼此送礼，因为这两位先知曾使住在地上的人受痛苦。", version_id: 6},
  {id: 162903, book: "Rev", verse: "11.011", unformatted: "见证人复活\n可是三天半之后，有从神而来的生命气息进入他们里面，他们就站立起来了。于是强烈的恐惧临到那些观看他们的人。", version_id: 6},
  {id: 162904, book: "Rev", verse: "11.012", unformatted: "随后，两位先知听见一个大声音从天上传来，对他们说：“上到这里来！”他们就在云中升到天上去了，他们的敌人也看见了。", version_id: 6},
  {id: 162905, book: "Rev", verse: "11.013", unformatted: "就在那时刻，发生了大地震，那城的十分之一倒塌了，在地震中死去的有七千人。其余的人都感到害怕，把荣耀归给天上的神。", version_id: 6},
  {id: 162906, book: "Rev", verse: "11.014", unformatted: "第二个灾祸过去了。看哪，第三个灾祸快要来临了。", version_id: 6},
  {id: 162907, book: "Rev", verse: "11.015", unformatted: "吹第七支号\n第七位天使吹响号角，天上就有大声音说：\n“世上的国度已经属于\n我们主和他的基督；\n他将做王，直到永永远远！”", version_id: 6},
  {id: 162908, book: "Rev", verse: "11.016", unformatted: "于是，那些在神面前坐在自己座位上的二十四位长老，就把脸伏在地上，敬拜神，", version_id: 6},
  {id: 162909, book: "Rev", verse: "11.017", unformatted: "说：\n“主、神、全能者啊，今在、昔在的那一位啊！我们感谢你！\n因为你得了你的大权能，做王了。", version_id: 6},
  {id: 162910, book: "Rev", verse: "11.018", unformatted: "列国发怒了，可是你的震怒临到了，\n你审判死人的时候到了，\n你向你的奴仆先知们、圣徒们、敬畏你名的人，\n无论卑微尊贵，赐报偿的时候到了，\n你毁灭那些败坏大地之人的时候也到了。”", version_id: 6},
  {id: 162911, book: "Rev", verse: "11.019", unformatted: "随后，神在天上的圣所开了，他的约柜在他圣所中显现。接着有闪电、声音、雷鸣、地震和大冰雹。", version_id: 6},
  {id: 162912, book: "Rev", verse: "12.001", unformatted: "妇人、孩子和龙\n接着，天上出现了一个大征兆：有一个妇人身披太阳，脚踏月亮，头上戴着十二颗星的冠冕。", version_id: 6},
  {id: 162913, book: "Rev", verse: "12.002", unformatted: "她怀了孕；后来在临产的阵痛和分娩的折磨中喊叫起来。", version_id: 6},
  {id: 162914, book: "Rev", verse: "12.003", unformatted: "随后，天上出现了另一个征兆：看哪，一条火红色的巨龙，有七个头和十只角，七个头上戴着七个王冠。", version_id: 6},
  {id: 162915, book: "Rev", verse: "12.004", unformatted: "它的尾巴拖着天上星辰的三分之一，把它们摔在地上。这龙站在那快要生产的妇人面前，为要在她生产之后，吞吃她的孩子。", version_id: 6},
  {id: 162916, book: "Rev", verse: "12.005", unformatted: "那妇人生了一个男孩子，就是那位将要用铁杖治理万国的。可是这孩子被提到神和他的宝座那里。", version_id: 6},
  {id: 162917, book: "Rev", verse: "12.006", unformatted: "那妇人就逃到旷野去，在那里有神已经为她所预备好的地方，好让她在那里被供养一千二百六十天。", version_id: 6},
  {id: 162918, book: "Rev", verse: "12.007", unformatted: "巨龙战败坠地\n接着，天上发生了战争：米迦勒和他的天使们与龙交战，龙和它的使者们也应战，", version_id: 6},
  {id: 162919, book: "Rev", verse: "12.008", unformatted: "可是龙不能得胜，天上再也没有它们的地方了。", version_id: 6},
  {id: 162920, book: "Rev", verse: "12.009", unformatted: "于是那巨龙被摔了下来。它就是那古蛇，被称为魔鬼和撒旦，那迷惑普天下的。它被摔在地上，它的使者们也与它一起被摔了下来。", version_id: 6},
  {id: 162921, book: "Rev", verse: "12.01", unformatted: "随后，我听见天上有一个大声音说：\n“我们神的救恩、大能、国度，\n和他基督的权柄，现在都来到了！\n因为我们弟兄的控告者，\n就是那日夜在我们神面前控告他们的，\n已经被摔下去了。", version_id: 6},
  {id: 162922, book: "Rev", verse: "12.011", unformatted: "他们藉着羔羊的血，藉着自己所见证的话语而胜过了它\n，他们至死也没有爱惜自己的生命。", version_id: 6},
  {id: 162923, book: "Rev", verse: "12.012", unformatted: "为此，诸天和居住在其中的，你们应当庆祝！\n但是大地和海洋却有祸了，\n因为魔鬼知道自己的时候不多，\n就怀着烈怒下到你们那里去了。”", version_id: 6},
  {id: 162924, book: "Rev", verse: "12.013", unformatted: "巨龙迫害妇人\n那龙一见自己被摔在地上，就追逼那生了男孩的妇人。", version_id: 6},
  {id: 162925, book: "Rev", verse: "12.014", unformatted: "但那妇人被赐予了大鹰的两个翅膀，使她可以飞到旷野，到她自己的地方；在那里她被供养一期，二期，又半期，远离那蛇的面。", version_id: 6},
  {id: 162926, book: "Rev", verse: "12.015", unformatted: "蛇就在那妇人后面，从口里吐出水来，像江河一样，要把她冲走。", version_id: 6},
  {id: 162927, book: "Rev", verse: "12.016", unformatted: "可是大地帮助了那妇人；大地张开口，吞下了从龙口里吐出的河水。", version_id: 6},
  {id: 162928, book: "Rev", verse: "12.017", unformatted: "于是龙就向那妇人发怒，又去与她其余的后裔作战，这后裔就是那些遵守神的命令、持守有关耶稣见证的人。", version_id: 6},
  {id: 162929, book: "Rev", verse: "12.018", unformatted: "然后，龙站在海边的沙滩上。", version_id: 6},
  {id: 162930, book: "Rev", verse: "13.001", unformatted: "海中出来的兽\n接着，我看见一只兽从海里上来。它有十只角和七个头，十只角上戴着十个王冠，七个头上都有亵渎的名号。", version_id: 6},
  {id: 162931, book: "Rev", verse: "13.002", unformatted: "我所看见的这兽，仿佛一只豹，它的脚像熊的脚，口像狮子的口。龙把自己的能力、王位和大权交给了这兽。", version_id: 6},
  {id: 162932, book: "Rev", verse: "13.003", unformatted: "这兽的七个头中有一个好像受了致命伤，可是这致命伤得了痊愈。全地的人都感到惊奇，跟随了这兽。", version_id: 6},
  {id: 162933, book: "Rev", verse: "13.004", unformatted: "他们膜拜龙，因为它把权柄交给了兽。他们也膜拜兽，说：“谁能与这兽相比呢？谁能与它交战呢？”", version_id: 6},
  {id: 162934, book: "Rev", verse: "13.005", unformatted: "那兽被赐予了一张说大话和亵渎话的口；它又被赐予了权柄，可以任意行事四十二个月。", version_id: 6},
  {id: 162935, book: "Rev", verse: "13.006", unformatted: "它就开口向神说亵渎的话，亵渎了他的名和他的居所，亵渎了那些居住在天上的。", version_id: 6},
  {id: 162936, book: "Rev", verse: "13.007", unformatted: "它被准许与圣徒们作战，并胜过他们；它还被赐予了权柄统管各支派、各民族、各语言群体、各国家。", version_id: 6},
  {id: 162937, book: "Rev", verse: "13.008", unformatted: "住在地上的人，凡是名字从创世以来没有记在那被杀羔羊的生命册上的，都将膜拜它。", version_id: 6},
  {id: 162938, book: "Rev", verse: "13.009", unformatted: "凡是有耳的，就应当听！", version_id: 6},
  {id: 162939, book: "Rev", verse: "13.01", unformatted: "如果有人注定被掳，\n他就会被掳去；\n如果有人注定被刀杀，\n他就会被刀杀。\n圣徒们的忍耐和信仰，就在这里。", version_id: 6},
  {id: 162940, book: "Rev", verse: "13.011", unformatted: "地里上来的兽\n接着，我看见另一只兽从地里上来。它有两只角仿佛羊羔，说话却像龙。", version_id: 6},
  {id: 162941, book: "Rev", verse: "13.012", unformatted: "它在第一只兽的面前，施行第一只兽的一切权柄，强迫大地和住在地上的人来膜拜第一只兽，就是致命伤得了痊愈的那兽。", version_id: 6},
  {id: 162942, book: "Rev", verse: "13.013", unformatted: "它又行大奇迹，甚至在众人面前，使火从天降到地上。", version_id: 6},
  {id: 162943, book: "Rev", verse: "13.014", unformatted: "藉着那些奇迹，就是它被准许在第一只兽面前所行的奇迹，它就迷惑了住在地上的人，告诉住在地上的人，要为那受刀伤却活过来的兽，造一个像。", version_id: 6},
  {id: 162944, book: "Rev", verse: "13.015", unformatted: "它又被准许把气息给那兽像，甚至使那兽像能说话，又使所有不膜拜那兽像的人都被杀死。", version_id: 6},
  {id: 162945, book: "Rev", verse: "13.016", unformatted: "它还强迫所有的人，无论卑微的或尊贵的、富有的或贫穷的、自由的或为奴的，都在右手或额上接受印记；", version_id: 6},
  {id: 162946, book: "Rev", verse: "13.017", unformatted: "这样，除了有这印记的人，谁都不可以买或卖，这印记就是兽的名字或它名字的数目。", version_id: 6},
  {id: 162947, book: "Rev", verse: "13.018", unformatted: "智慧就在这里。有理性的人应当计算这兽的数目，因为这是一个人的数目；其数目是六百六十六。", version_id: 6},
  {id: 162948, book: "Rev", verse: "14.001", unformatted: "羔羊和跟随者\n我又观看，看哪，那羔羊站在锡安山上，与他在一起的有十四万四千人，他们的额上都写着他的名和他父的名。", version_id: 6},
  {id: 162949, book: "Rev", verse: "14.002", unformatted: "接着，我听见有声音从天上传来，好像众水的声音，也像大雷鸣的声音。我所听见的这声音，又像琴师们弹奏竖琴的声音。", version_id: 6},
  {id: 162950, book: "Rev", verse: "14.003", unformatted: "他们在宝座前，并四个活物和长老们面前唱歌，像是新歌，这首歌除了从地上被赎回来的那十四万四千人以外，没有人能学会。", version_id: 6},
  {id: 162951, book: "Rev", verse: "14.004", unformatted: "这些人没有与女人在一起而玷污自己，实际上他们是童身；无论羔羊到哪里去，他们都跟随他；这些人是从人间被赎回来，做为初熟的果子归给神和羔羊的；", version_id: 6},
  {id: 162952, book: "Rev", verse: "14.005", unformatted: "在他们口中找不出虚假；他们是毫无瑕疵的。", version_id: 6},
  {id: 162953, book: "Rev", verse: "14.006", unformatted: "三天使的宣告\n随后，我看见另一位天使在空中飞翔；他带着永恒的福音要传给住在地上的人，传给各国家、各支派、各语言群体、各民族。", version_id: 6},
  {id: 162954, book: "Rev", verse: "14.007", unformatted: "他大声说：“你们当敬畏神，把荣耀归给他，因为他审判的时候到了。你们当敬拜造了天、地、海和众水泉源的那一位。”", version_id: 6},
  {id: 162955, book: "Rev", verse: "14.008", unformatted: "另一位天使，就是第二位天使跟着说：“倒塌了！大巴比伦倒塌了！她给万国喝了她淫乱愤怒的酒。”", version_id: 6},
  {id: 162956, book: "Rev", verse: "14.009", unformatted: "另一位天使，就是第三位天使，跟着他们大声说：“如果有人膜拜那兽和兽像，并在自己额上或手上接受印记，", version_id: 6},
  {id: 162957, book: "Rev", verse: "14.01", unformatted: "就要喝神愤怒的酒，就是混合在神震怒之杯中那没有稀释的酒。他将要在圣天使们面前、羔羊面前，在烈火和硫磺中受折磨。", version_id: 6},
  {id: 162958, book: "Rev", verse: "14.011", unformatted: "折磨他们时的烟往上冒，直到永永远远。那些膜拜兽和兽像的，以及任何接受它名字印记的，都将日夜得不到安息。", version_id: 6},
  {id: 162959, book: "Rev", verse: "14.012", unformatted: "遵守神的命令、持守对耶稣信仰的圣徒们的忍耐，就在这里。”", version_id: 6},
  {id: 162960, book: "Rev", verse: "14.013", unformatted: "接着，我听见有声音从天上传来，说：“你当写下：从今以后，在主里死去的人是蒙福的！”\n圣灵说：“是的！他们将脱离自己的劳苦进入安息，因为他们的作为将伴随着他们。”", version_id: 6},
  {id: 162961, book: "Rev", verse: "14.014", unformatted: "收割地上的庄稼\n我又观看，看哪，一朵白云，云上坐着一位仿佛人子的，头上戴着金冠冕，手里拿着一把锋利的镰刀。", version_id: 6},
  {id: 162962, book: "Rev", verse: "14.015", unformatted: "另一位天使从圣所中出来，大声对坐在云上的那一位呼喊：“伸出你的镰刀去收割吧！因为收割的时候到了，地上的庄稼已经熟了。”", version_id: 6},
  {id: 162963, book: "Rev", verse: "14.016", unformatted: "于是坐在云上的那一位向大地挥动他的镰刀，大地的庄稼就被收割了。", version_id: 6},
  {id: 162964, book: "Rev", verse: "14.017", unformatted: "又一位天使从天上的圣所中出来，他也拿着一把锋利的镰刀。", version_id: 6},
  {id: 162965, book: "Rev", verse: "14.018", unformatted: "另一位天使从祭坛那里出来，是有权柄统管火的；他对那位拿着锋利镰刀的大声呼唤说：“伸出你那锋利的镰刀，收取地上葡萄树累累的果子吧！因为葡萄已经熟了。”", version_id: 6},
  {id: 162966, book: "Rev", verse: "14.019", unformatted: "于是那位天使向大地挥动他的镰刀，收取了地上的葡萄，丢进神愤怒的大榨酒池里。", version_id: 6},
  {id: 162967, book: "Rev", verse: "14.02", unformatted: "榨酒池在城外被践踏，就有血从榨酒池里流出来，高到马的嚼子，远达三百公里。", version_id: 6},
  {id: 162968, book: "Rev", verse: "15.001", unformatted: "神愤怒的七个金碗\n接着，我看见天上有另一个大而奇妙的征兆：有七位天使带着最后的七样灾害，因为神的愤怒就在这些灾害中完结了。", version_id: 6},
  {id: 162969, book: "Rev", verse: "15.002", unformatted: "我又看见一片好像混着火的玻璃海；那些胜过了兽和兽像以及它名字数目的人，都拿着神的竖琴，站在玻璃海上；", version_id: 6},
  {id: 162970, book: "Rev", verse: "15.003", unformatted: "他们唱着神的奴仆摩西之歌和羔羊之歌，说：\n“主、神、全能者啊，你的作为又伟大又奇妙！\n列国之王啊，你的道路又公义又真实！", version_id: 6},
  {id: 162971, book: "Rev", verse: "15.004", unformatted: "主啊！谁能不敬畏、不荣耀你的名呢？\n因为只有你是圣洁的，\n因为万民都要来，在你面前敬拜，\n因为你公义的规定已经显明了。”", version_id: 6},
  {id: 162972, book: "Rev", verse: "15.005", unformatted: "这些事以后，我又观看，看哪，天上的圣所，就是那见证的会幕开了。", version_id: 6},
  {id: 162973, book: "Rev", verse: "15.006", unformatted: "那带着七样灾害的七位天使，从圣所中出来了；他们身穿洁白明亮的细麻衣，胸间束着金带。", version_id: 6},
  {id: 162974, book: "Rev", verse: "15.007", unformatted: "四个活物中有一个把七个金碗交给了那七位天使，碗里盛满了活到永永远远之神的愤怒。", version_id: 6},
  {id: 162975, book: "Rev", verse: "15.008", unformatted: "圣所就充满了从神的荣耀和他的大能而来的烟；没有人能进入圣所，直到那七位天使的七样灾害完结了。", version_id: 6},
  {id: 162976, book: "Rev", verse: "16.001", unformatted: "第一灾\n接着，我听见有大声音从圣所中传来，对那七位天使说：“你们去，把神愤怒的七碗倒在地上。”", version_id: 6},
  {id: 162977, book: "Rev", verse: "16.002", unformatted: "第一位天使离去，把他的碗倒在地上，就有又恶又毒的疮，长在那些带有兽的印记并膜拜兽像的人身上。", version_id: 6},
  {id: 162978, book: "Rev", verse: "16.003", unformatted: "第二灾\n第二位天使把他的碗倒在海里，海水就变得像死人的血那样，海中所有的生物都死了。", version_id: 6},
  {id: 162979, book: "Rev", verse: "16.004", unformatted: "第三灾\n第三位天使把他的碗倒在江河和众水的泉源上，水就变成血。", version_id: 6},
  {id: 162980, book: "Rev", verse: "16.005", unformatted: "接着，我听见那位统管众水的天使说：\n“今在、昔在的圣者啊，你是公义的，\n因为你判定了这些事，", version_id: 6},
  {id: 162981, book: "Rev", verse: "16.006", unformatted: "又因为他们曾流了圣徒们和先知们的血，\n现在你给了他们血喝，这是他们该受的。”", version_id: 6},
  {id: 162982, book: "Rev", verse: "16.007", unformatted: "随后，我听见祭坛说：\n“是的，主、神、全能者啊，\n你的审判是真实的、公义的！”", version_id: 6},
  {id: 162983, book: "Rev", verse: "16.008", unformatted: "第四灾\n第四位天使把他的碗倒在太阳上，太阳就被准许用火灼烤人。", version_id: 6},
  {id: 162984, book: "Rev", verse: "16.009", unformatted: "人们被大炎热灼烤，就亵渎了那有权柄统管这些灾害的神的名；他们也不悔改，不把荣耀归给他。", version_id: 6},
  {id: 162985, book: "Rev", verse: "16.01", unformatted: "第五灾\n第五位天使把他的碗倒在那兽的宝座上，兽的国就变成黑暗了，人们因痛苦而咬自己的舌头。", version_id: 6},
  {id: 162986, book: "Rev", verse: "16.011", unformatted: "他们因自己的痛苦、因自己的疮而亵渎了天上的神，还是不悔改离开自己的行为。", version_id: 6},
  {id: 162987, book: "Rev", verse: "16.012", unformatted: "第六灾\n第六位天使把他的碗倒在幼发拉底大河上，河水就干了，好为那些从东方来的众君王预备道路。", version_id: 6},
  {id: 162988, book: "Rev", verse: "16.013", unformatted: "随后，我看见三个污灵，好像青蛙，来自龙的口、兽的口和那假先知的口。", version_id: 6},
  {id: 162989, book: "Rev", verse: "16.014", unformatted: "其实它们是鬼魔的灵，能行奇迹；它们出去到普天下的众君王那里，召集他们参与神、全能者大日子的战争。", version_id: 6},
  {id: 162990, book: "Rev", verse: "16.015", unformatted: "“看哪，我要来临，就像贼来临那样！那警醒并保持衣着在身，免得赤身行走被人看见他羞处的，这样的人是蒙福的。”", version_id: 6},
  {id: 162991, book: "Rev", verse: "16.016", unformatted: "于是，那些鬼魔的灵把众君王都召集到一个地方；希伯来语叫做“哈米吉多顿”。", version_id: 6},
  {id: 162992, book: "Rev", verse: "16.017", unformatted: "第七灾\n第七位天使把他的碗倒在空中，就有大声音从圣所中的宝座上传来，说：“成了！”", version_id: 6},
  {id: 162993, book: "Rev", verse: "16.018", unformatted: "随后有闪电、声音和雷鸣，又发生了大地震，自从地上有人以来，没有发生过如此大、如此厉害的地震。", version_id: 6},
  {id: 162994, book: "Rev", verse: "16.019", unformatted: "那大城裂为三个部分，列国的城市也都倒塌了。神也想起大巴比伦，要把自己烈怒之酒的杯子递给她。", version_id: 6},
  {id: 162995, book: "Rev", verse: "16.02", unformatted: "所有的海岛都逃避了，山岭也不见了。", version_id: 6},
  {id: 162996, book: "Rev", verse: "16.021", unformatted: "又有重约四十公斤的大冰雹，从天上落在人身上；由于这冰雹的灾害，人们就亵渎了神，因为这灾害极其厉害。", version_id: 6},
  {id: 162997, book: "Rev", verse: "17.001", unformatted: "大淫妇与兽\n拿着七碗的七位天使中，有一位过来对我讲话，说：“来，我要给你看那坐在众水之上的大淫妇所要受的审判。", version_id: 6},
  {id: 162998, book: "Rev", verse: "17.002", unformatted: "地上的众君王曾与她行淫乱，住在地上的人也喝醉了她淫乱的酒。”", version_id: 6},
  {id: 162999, book: "Rev", verse: "17.003", unformatted: "于是我在灵里被天使带到旷野。随后，我看见一个女人骑在一只深红色的兽上，这兽有七个头，十只角，遍体布满了亵渎的名号。", version_id: 6},
  {id: 163000, book: "Rev", verse: "17.004", unformatted: "那女人身穿紫色和深红色的衣服，以金子、宝石和珍珠为装饰。她手里拿着金杯，杯中盛满了可憎之物和她淫乱的污秽。", version_id: 6},
  {id: 163001, book: "Rev", verse: "17.005", unformatted: "在她的额上写着一个奥秘的名号：“大巴比伦，地上的淫妇们和可憎之物的母亲。”", version_id: 6},
  {id: 163002, book: "Rev", verse: "17.006", unformatted: "随后，我看见那女人喝醉了圣徒们的血，以及为耶稣做见证之人的血。我看到她就大大惊奇。", version_id: 6},
  {id: 163003, book: "Rev", verse: "17.007", unformatted: "解明奥秘的含义\n那位天使对我说：“你为什么惊奇呢？我要把这女人和驮着她的那七头十角兽的奥秘告诉你。", version_id: 6},
  {id: 163004, book: "Rev", verse: "17.008", unformatted: "你所看见的那兽，先前在而现今不在，并将要从无底坑里上来，然后走向灭亡。住在地上的人，凡是名字从创世以来没有被记在生命册上的，看见那先前在而现今不在，并将要出现的兽时，都会感到惊奇。", version_id: 6},
  {id: 163005, book: "Rev", verse: "17.009", unformatted: "“带有智慧的理性就在这里。那七个头就是女人所坐的七座山，也就是七个君王。", version_id: 6},
  {id: 163006, book: "Rev", verse: "17.01", unformatted: "五个已经倒了，一个还在，另一个还没有到来；他来的时候，必须存留片刻。", version_id: 6},
  {id: 163007, book: "Rev", verse: "17.011", unformatted: "那先前在而现今不在的兽，自己就是第八个，也是属于那七个，并且要走向灭亡。", version_id: 6},
  {id: 163008, book: "Rev", verse: "17.012", unformatted: "你所看见的那十只角，是十个王，他们还没有得到王权，然而他们要得到权柄，与那兽一同做王一段时间。", version_id: 6},
  {id: 163009, book: "Rev", verse: "17.013", unformatted: "他们目的一致，把自己的能力和权柄交给那兽。", version_id: 6},
  {id: 163010, book: "Rev", verse: "17.014", unformatted: "他们将要与羔羊交战，而羔羊将胜过他们，因为他是万主之主，万王之王；与羔羊在一起的，是那些蒙召唤的、蒙拣选的、忠心的人。”", version_id: 6},
  {id: 163011, book: "Rev", verse: "17.015", unformatted: "天使又对我说：“你所看见的众水，那淫妇所坐的地方，就是许多民族、人群、国家和语言群体。", version_id: 6},
  {id: 163012, book: "Rev", verse: "17.016", unformatted: "你所看见的那十只角和那兽，他们将恨恶那淫妇，并且将使她成为荒芜、赤身露体，又要吃她的肉，而且用火烧掉她。", version_id: 6},
  {id: 163013, book: "Rev", verse: "17.017", unformatted: "因为神把意念放在他们心中，要他们实现他的目的，要他们行事目的一致，并把十只角的王权交给那兽，直到神的话语成就。", version_id: 6},
  {id: 163014, book: "Rev", verse: "17.018", unformatted: "你所看见的那女人，就是那有王权统管地上众君王的大城。”", version_id: 6},
  {id: 163015, book: "Rev", verse: "18.001", unformatted: "巴比伦的倾倒\n这些事以后，我看见另一位天使从天上降下来，他拥有大权；大地被他的荣光照亮了。", version_id: 6},
  {id: 163016, book: "Rev", verse: "18.002", unformatted: "他用强有力的声音呼喊说：\n“倒塌了！大巴比伦倒塌了！\n她成了鬼魔居住的地方、\n一切污灵的巢穴、\n一切污秽鸟类的巢穴、\n一切污秽可憎野兽的巢穴。", version_id: 6},
  {id: 163017, book: "Rev", verse: "18.003", unformatted: "原来万国都喝了她淫乱愤怒的酒，\n地上的众君王曾与她行淫乱，\n地上的商人也由于她极度的骄奢淫逸而发了财。”", version_id: 6},
  {id: 163018, book: "Rev", verse: "18.004", unformatted: "接着，我听见另一个声音从天上传来，说：\n“我的子民哪，你们要从她那里出来，\n免得在她的罪孽上有份，\n也免得遭受她所受的灾害，", version_id: 6},
  {id: 163019, book: "Rev", verse: "18.005", unformatted: "因为她的罪孽滔天，\n神也记得她的罪行。", version_id: 6},
  {id: 163020, book: "Rev", verse: "18.006", unformatted: "她怎样待人，你们也要怎样待她，\n并照着她的行为加倍地报应她；\n你们要在她混合酒的杯子里，\n加倍地混合给她喝。", version_id: 6},
  {id: 163021, book: "Rev", verse: "18.007", unformatted: "她怎样炫耀自己、骄奢淫逸，\n你们也要照样给她折磨和悲伤，\n因为她心里说：‘我坐着女王的位子，\n我不是寡妇，绝不会经历悲伤。’", version_id: 6},
  {id: 163022, book: "Rev", verse: "18.008", unformatted: "为此，在一天之内\n她所受的灾害，就是死亡、悲伤和饥荒，\n就要来到。\n她将要在烈火中被烧掉，\n因为主、神——审判她的那一位是强有力的。”", version_id: 6},
  {id: 163023, book: "Rev", verse: "18.009", unformatted: "世界为巴比伦哀哭\n地上的众君王，就是那些与她行淫乱、一同骄奢淫逸的，当看见焚烧她时的烟，就会为她捶胸哀哭。", version_id: 6},
  {id: 163024, book: "Rev", verse: "18.01", unformatted: "他们因惧怕她所受的折磨，就远远地站着，说：\n“有祸了！有祸了！宏大的城，\n强大的巴比伦城啊！\n因为一时之间，\n你的审判就来到了！”", version_id: 6},
  {id: 163025, book: "Rev", verse: "18.011", unformatted: "地上的商人也为她哭泣、悲伤，因为再也没有人买他们的货物了；", version_id: 6},
  {id: 163026, book: "Rev", verse: "18.012", unformatted: "那些货物就是：金、银、宝石、珍珠，细麻布、紫色布、丝绸、深红色布，各种香木，各种象牙制品，各种极贵重的木、铜、铁和大理石的制品，", version_id: 6},
  {id: 163027, book: "Rev", verse: "18.013", unformatted: "肉桂、香料、香、香液、乳香；酒、油、面粉、麦子；牲畜、羊、马、马车；人的肉体和灵魂。", version_id: 6},
  {id: 163028, book: "Rev", verse: "18.014", unformatted: "“你心所贪恋的果子离开了你；\n一切珍馐美味、华丽物品，都从你那里消逝了，\n人绝不能再看到它们了。”", version_id: 6},
  {id: 163029, book: "Rev", verse: "18.015", unformatted: "贩卖这些东西的商人，就是那些由于她而发了财的人，因惧怕她所受的折磨，就会远远地站着，哭泣、悲伤，", version_id: 6},
  {id: 163030, book: "Rev", verse: "18.016", unformatted: "说：\n“有祸了！有祸了！宏大的城，\n那曾身穿细麻、紫色、深红色衣服，\n以金子、宝石、珍珠为装饰的城啊！", version_id: 6},
  {id: 163031, book: "Rev", verse: "18.017", unformatted: "这么多的财富，竟然在一时之间就化为乌有了！”\n所有的船长、所有到处航行的人、水手们、所有靠海为业的人，都远远地站着。", version_id: 6},
  {id: 163032, book: "Rev", verse: "18.018", unformatted: "他们看着焚烧她时的烟，呼喊说：“哪一个城能与这大城相比呢？”", version_id: 6},
  {id: 163033, book: "Rev", verse: "18.019", unformatted: "他们把尘土撒在自己头上，哭泣、悲伤，呼喊说：\n“有祸了！有祸了！宏大的城，\n所有靠着她在海上有船只的人\n都因她的财富发了财。\n她竟然在一时之间就化为乌有了！", version_id: 6},
  {id: 163034, book: "Rev", verse: "18.02", unformatted: "上天哪、圣徒们、使徒们、先知们哪，你们应当因她欢喜！\n因为神已经向她施行审判，为你们惩罚她！”", version_id: 6},
  {id: 163035, book: "Rev", verse: "18.021", unformatted: "巴比伦的结局\n随后，一位强有力的天使拿起一块像大磨石那样的石头，把它丢进海里，说：\n“大巴比伦城也将这样被猛烈地扔下去，\n绝不会再找到了！", version_id: 6},
  {id: 163036, book: "Rev", verse: "18.022", unformatted: "弹琴的、奏乐的、吹笛的、吹号之人的声音，\n在你中间绝不会再听到了！\n各种手艺的工匠，\n在你中间绝不会再找到了！\n磨石的声音，\n在你中间绝不会再听到了！", version_id: 6},
  {id: 163037, book: "Rev", verse: "18.023", unformatted: "灯的亮光，\n在你中间绝不会再照耀了！\n新郎和新娘的声音，\n在你中间绝不会再听到了！\n因为你的商人一向是地上的大人物，\n又因为万国都被你的邪术所迷惑，", version_id: 6},
  {id: 163038, book: "Rev", verse: "18.024", unformatted: "而且先知们、圣徒们以及地上一切被杀之人的血，\n在你那里都被发现了。”", version_id: 6},
  {id: 163039, book: "Rev", verse: "19.001", unformatted: "天上的欢呼\n这些事以后，我听见天上有大声音，好像一大群人的声音，说：\n“哈利路亚！\n救恩、荣耀、权能都属于我们的神！", version_id: 6},
  {id: 163040, book: "Rev", verse: "19.002", unformatted: "因为他的审判是真实的、公义的，\n因为他审判了那用自己的淫乱败坏大地的大淫妇，\n并且为他奴仆们的血，向那淫妇讨回了公道。”", version_id: 6},
  {id: 163041, book: "Rev", verse: "19.003", unformatted: "他们第二次说：\n“哈利路亚！\n焚烧她时的烟往上冒，直到永永远远！”", version_id: 6},
  {id: 163042, book: "Rev", verse: "19.004", unformatted: "二十四位长老和四个活物就俯伏敬拜那坐在宝座上的神，说：\n“阿们！哈利路亚！”", version_id: 6},
  {id: 163043, book: "Rev", verse: "19.005", unformatted: "随后，有声音从宝座上传来，说：\n“神所有的奴仆们哪，\n所有敬畏他的，无论卑微尊贵，\n你们要赞美我们的神！”", version_id: 6},
  {id: 163044, book: "Rev", verse: "19.006", unformatted: "宣告羔羊婚宴\n接着，我听见好像一大群人的声音，也像众水的声音，又像大雷鸣的声音，说：\n“哈利路亚！\n因为我们的主、神、全能者做王了！", version_id: 6},
  {id: 163045, book: "Rev", verse: "19.007", unformatted: "让我们欢喜、快乐，让我们把荣耀归给他！\n因为羔羊婚宴的时候到了，\n他的新娘也把自己预备好了；", version_id: 6},
  {id: 163046, book: "Rev", verse: "19.008", unformatted: "新娘被准许穿戴明亮洁白的细麻衣。”\n原来，这细麻衣代表圣徒们的义行。", version_id: 6},
  {id: 163047, book: "Rev", verse: "19.009", unformatted: "随后，那位天使对我说：“你当写下：那些被邀请赴羔羊婚宴的人是蒙福的！”他又对我说：“这些都是神真实的话语。”", version_id: 6},
  {id: 163048, book: "Rev", verse: "19.01", unformatted: "我就俯伏在他的脚前，要敬拜他。可是他对我说：“你要注意，不可这样！我与你和你的弟兄们——那些持守有关耶稣见证的，都是同做奴仆的。你应当敬拜神！因为有关耶稣的见证，才是预言的灵魂所在。”", version_id: 6},
  {id: 163049, book: "Rev", verse: "19.011", unformatted: "骑白马的主\n接着，我看见天开了，看哪，有一匹白马。骑马的那一位被称为“信实的”和“真实的”；他以公义去审判、去作战。", version_id: 6},
  {id: 163050, book: "Rev", verse: "19.012", unformatted: "他的眼睛像火焰，头上戴着许多王冠。他有一个写好的名字，除了他自己以外，没有人认识这名字。", version_id: 6},
  {id: 163051, book: "Rev", verse: "19.013", unformatted: "他身穿一件浸过血的袍子；他的名字叫“神的话语”。", version_id: 6},
  {id: 163052, book: "Rev", verse: "19.014", unformatted: "天上的军兵，都骑着白马，身穿洁白干净的细麻衣，跟随着他。", version_id: 6},
  {id: 163053, book: "Rev", verse: "19.015", unformatted: "从他口中发出一把利剑，好用它来击打列国。他将用铁杖治理列国，并且要践踏酒榨，就是神、全能者烈怒的酒榨。", version_id: 6},
  {id: 163054, book: "Rev", verse: "19.016", unformatted: "在他的袍子上、大腿上写着一个名号：“万王之王，万主之主。”", version_id: 6},
  {id: 163055, book: "Rev", verse: "19.017", unformatted: "兽的军队战败\n随后，我看见一位天使站在太阳中，对所有在空中飞翔的鸟大声呼喊说：“来吧，聚集起来赴神的大宴席，", version_id: 6},
  {id: 163056, book: "Rev", verse: "19.018", unformatted: "为要吃君王的肉、军官的肉、壮士的肉、马和骑士的肉，以及各种人的肉，包括自由人和奴隶，卑微人和尊贵人的肉。”", version_id: 6},
  {id: 163057, book: "Rev", verse: "19.019", unformatted: "接着，我看见那兽和地上的众君王以及他们的军兵聚集在一起，要与骑白马的那一位和他的军队作战。", version_id: 6},
  {id: 163058, book: "Rev", verse: "19.02", unformatted: "但那兽被抓了；那假先知也与它一起被抓了——他在兽面前行了许多奇迹，藉此迷惑那些接受兽的印记并膜拜兽像的人。兽和那假先知两个都活生生地被丢进燃烧着硫磺的火湖里。", version_id: 6},
  {id: 163059, book: "Rev", verse: "19.021", unformatted: "其余的人，也都被骑白马那一位口中所发出的剑杀死了。所有的鸟都吃饱了他们的肉。", version_id: 6},
  {id: 163060, book: "Rev", verse: "20.001", unformatted: "捆绑撒旦\n接着，我看见一位天使从天上降下来，手里拿着无底坑的钥匙和一条大锁链。", version_id: 6},
  {id: 163061, book: "Rev", verse: "20.002", unformatted: "他抓住了龙——那古蛇，就是魔鬼撒旦，把它捆绑了一千年。", version_id: 6},
  {id: 163062, book: "Rev", verse: "20.003", unformatted: "这位天使把它丢进无底坑里，关上了无底坑，并用印封上，使它不能再迷惑列国，直到那一千年完结。这些事以后，它必须暂时被释放。", version_id: 6},
  {id: 163063, book: "Rev", verse: "20.004", unformatted: "圣徒与主同做王\n随后，我看见一些座位，上面坐着人；这些人被赐予了审判的权柄。我又看见那些为耶稣做见证并为神的话语被斩首之人的灵魂，他们没有膜拜那兽或兽像，也没有在自己额上或手上接受那印记。他们都活了过来，并且与基督一同做王一千年。", version_id: 6},
  {id: 163064, book: "Rev", verse: "20.005", unformatted: "其余死去的人还没有活过来，直到这一千年完结。这是第一次的复活。", version_id: 6},
  {id: 163065, book: "Rev", verse: "20.006", unformatted: "那些在第一次复活中有份的人是蒙福的、是圣洁的！第二次的死没有权柄辖制他们；他们将成为神和基督的祭司，与基督一同做王一千年。", version_id: 6},
  {id: 163066, book: "Rev", verse: "20.007", unformatted: "撒旦的结局\n当那一千年完结了，撒旦就要从它的监禁中被释放出来。", version_id: 6},
  {id: 163067, book: "Rev", verse: "20.008", unformatted: "它将出去迷惑在大地四角中的列国，就是歌革和玛各，召集他们参与战争；他们的人数多如海沙。", version_id: 6},
  {id: 163068, book: "Rev", verse: "20.009", unformatted: "他们上来布满了宽阔的大地，包围了圣徒的营地，就是那蒙爱的城。可是有火从天上降下来，吞灭了他们。", version_id: 6},
  {id: 163069, book: "Rev", verse: "20.01", unformatted: "然后那迷惑他们的魔鬼被丢进烈火和硫磺的湖里，就是那兽和那假先知所在的地方；他们将日夜受折磨，直到永永远远。", version_id: 6},
  {id: 163070, book: "Rev", verse: "20.011", unformatted: "白色大宝座的审判\n接着，我看见一个白色的大宝座和坐在它上面的那一位。地和天都从他面前逃避，连它们的地方也找不到了。", version_id: 6},
  {id: 163071, book: "Rev", verse: "20.012", unformatted: "我又看见死去的人，无论尊贵卑微，都站在那宝座前。书卷都打开了；另有一个书卷也打开了，这就是生命册。死去的人都凭着书卷中所记的，照着他们的行为受审判。", version_id: 6},
  {id: 163072, book: "Rev", verse: "20.013", unformatted: "于是，海洋交出了其中的死人，死亡和阴间也交出了其中的死人；每个人都照着自己的行为受审判。", version_id: 6},
  {id: 163073, book: "Rev", verse: "20.014", unformatted: "然后，死亡和阴间被丢进火湖里，这火湖就是第二次的死亡。", version_id: 6},
  {id: 163074, book: "Rev", verse: "20.015", unformatted: "凡是名字没有被记在生命册上的人，都被丢进了火湖里。", version_id: 6},
  {id: 163075, book: "Rev", verse: "21.001", unformatted: "新天新地\n接着，我看见一个新天新地，因为先前的天和先前的地都已经过去了，海也不再存在了。", version_id: 6},
  {id: 163076, book: "Rev", verse: "21.002", unformatted: "我也看见圣城新耶路撒冷，从神那里、从天上降下来，已经被预备好了，就像为自己的丈夫装饰整齐的新娘那样。", version_id: 6},
  {id: 163077, book: "Rev", verse: "21.003", unformatted: "我又听见有大声音从宝座上传来，说：\n“看哪，神的居所在人间，\n神将要与人一同居住。\n他们将要做他的子民，\n神将要亲自与他们同在，做他们的神。", version_id: 6},
  {id: 163078, book: "Rev", verse: "21.004", unformatted: "神要从他们的眼中抹去一切泪水。\n将来不再有死亡，\n也不再有悲伤、哭泣或痛苦，\n因为先前的事已经过去了。”", version_id: 6},
  {id: 163079, book: "Rev", verse: "21.005", unformatted: "坐在宝座上的那一位说：“看哪，我正在更新一切！”他又说：“你当写下，因为这些话是信实的、真实的。”", version_id: 6},
  {id: 163080, book: "Rev", verse: "21.006", unformatted: "他又对我说：“成了！我就是‘阿尔法’，也是‘欧米伽’；是开始，也是终结。我将要从生命水的泉源把水无偿地赐给干渴的人。", version_id: 6},
  {id: 163081, book: "Rev", verse: "21.007", unformatted: "那得胜的，将要继承这些；我将要做他的神，他将要做我的儿子。", version_id: 6},
  {id: 163082, book: "Rev", verse: "21.008", unformatted: "但那些胆怯的、不信的、可憎的、杀人的、淫乱的、行邪术的、拜偶像的，和所有说谎的人，他们的份就在燃烧着烈火和硫磺的湖里。这是第二次的死亡。”", version_id: 6},
  {id: 163083, book: "Rev", verse: "21.009", unformatted: "新耶路撒冷\n拿着七个盛满最后七灾害之碗的七位天使中，有一位过来对我讲话，说：“来，我要给你看看新娘，就是羔羊的妻子。”", version_id: 6},
  {id: 163084, book: "Rev", verse: "21.01", unformatted: "于是我在灵里被天使带到一座高大的山上。他给我看那从神那里、从天上降下来的圣城耶路撒冷；", version_id: 6},
  {id: 163085, book: "Rev", verse: "21.011", unformatted: "这城有神的荣耀，她的光辉仿佛极贵重的宝石，如同碧玉，清澈如水晶。", version_id: 6},
  {id: 163086, book: "Rev", verse: "21.012", unformatted: "她有又高又大的城墙，有十二个城门，城门前有十二位天使，又有名字刻在上头，是以色列子民十二支派的名字。", version_id: 6},
  {id: 163087, book: "Rev", verse: "21.013", unformatted: "东面三个门，北面三个门，南面三个门，西面三个门。", version_id: 6},
  {id: 163088, book: "Rev", verse: "21.014", unformatted: "城墙有十二座基石，上面有羔羊十二使徒的十二个名字。", version_id: 6},
  {id: 163089, book: "Rev", verse: "21.015", unformatted: "那位对我说话的天使，拿着一根金芦苇的量尺，要测量那城，就是她的城门和城墙。", version_id: 6},
  {id: 163090, book: "Rev", verse: "21.016", unformatted: "那城布置为四方形，长度和宽度一样。天使用芦苇测量那城，有两千两百公里；她的长、宽、高都是相等的。", version_id: 6},
  {id: 163091, book: "Rev", verse: "21.017", unformatted: "天使又测量了她的城墙，按人的尺度有一百四十四肘；这也是天使的尺度。", version_id: 6},
  {id: 163092, book: "Rev", verse: "21.018", unformatted: "她的城墙是用碧玉建造的；城是纯金的，仿佛纯净的玻璃。", version_id: 6},
  {id: 163093, book: "Rev", verse: "21.019", unformatted: "城墙的基石是用各种宝石装饰的：\n第一座基石是碧玉，\n第二座是蓝宝石，\n第三座是绿玛瑙，\n第四座是绿宝石，", version_id: 6},
  {id: 163094, book: "Rev", verse: "21.02", unformatted: "第五座是红玛瑙，\n第六座是红宝石，\n第七座是黄璧玺，\n第八座是水苍玉，\n第九座是黄玉，\n第十座是翡翠，\n第十一座是紫玛瑙，\n第十二座是紫水晶。", version_id: 6},
  {id: 163095, book: "Rev", verse: "21.021", unformatted: "十二个城门是十二颗珍珠；每一个门各自是由一颗珍珠造成的。城里的大街是纯金的，好像透明的玻璃。", version_id: 6},
  {id: 163096, book: "Rev", verse: "21.022", unformatted: "在城中我没有看见圣所，因为主、神、全能者和羔羊就是城的圣所。", version_id: 6},
  {id: 163097, book: "Rev", verse: "21.023", unformatted: "这城不需要太阳和月亮来照耀，因为神的荣耀照亮她，而羔羊就是她的灯。", version_id: 6},
  {id: 163098, book: "Rev", verse: "21.024", unformatted: "列国都将藉着城的光行走；地上的众君王都要把他们的荣耀带进城内。", version_id: 6},
  {id: 163099, book: "Rev", verse: "21.025", unformatted: "她的各城门整天绝不关上，原来那里没有黑夜。", version_id: 6},
  {id: 163100, book: "Rev", verse: "21.026", unformatted: "人们将要把列国的荣耀和尊贵带进城内。", version_id: 6},
  {id: 163101, book: "Rev", verse: "21.027", unformatted: "凡是不洁净的，行可憎和虚假之事的人，绝不能进到城内；唯有名字被记在羔羊生命册上的才能进去。", version_id: 6},
  {id: 163102, book: "Rev", verse: "22.001", unformatted: "生命之源\n接着，天使给我看一条生命水的河，明亮如水晶，从神和羔羊的宝座流出来，", version_id: 6},
  {id: 163103, book: "Rev", verse: "22.002", unformatted: "从城的大街中间穿过。河的两边有生命树，结十二种果子；每个月都结果子，树上的叶子是为了医治万民。", version_id: 6},
  {id: 163104, book: "Rev", verse: "22.003", unformatted: "将来不再有任何诅咒。神和羔羊的宝座将要在城中，他的奴仆们将事奉他，", version_id: 6},
  {id: 163105, book: "Rev", verse: "22.004", unformatted: "他们将要看到他的面容，而他的名字将要写在他们的额上。", version_id: 6},
  {id: 163106, book: "Rev", verse: "22.005", unformatted: "将来不再有黑夜，他们不需要灯光或日光，因为主、神将照亮他们。他们将做王，直到永永远远。", version_id: 6},
  {id: 163107, book: "Rev", verse: "22.006", unformatted: "时候近了\n随后，天使对我说：“这些话是信实的、真实的。主——众先知之灵的神，差派了他的天使，要把这些必须快发生的事指示给他的奴仆们。”", version_id: 6},
  {id: 163108, book: "Rev", verse: "22.007", unformatted: "“看哪，我快要来了！那遵守这书上预言之话的人，他是蒙福的！”", version_id: 6},
  {id: 163109, book: "Rev", verse: "22.008", unformatted: "我约翰，是听见又看见这些事的人。我听见又看见了以后，就俯伏在那位指示我这些事的天使脚前，要敬拜他。", version_id: 6},
  {id: 163110, book: "Rev", verse: "22.009", unformatted: "可是他对我说：“你要注意，不可这样！我和你，以及你的弟兄先知们，还有那些遵守这书上话语的人，都是同做奴仆的。你应当敬拜神！”", version_id: 6},
  {id: 163111, book: "Rev", verse: "22.01", unformatted: "他又对我说：“不可封住这书上的预言之话，因为时候快到了。", version_id: 6},
  {id: 163112, book: "Rev", verse: "22.011", unformatted: "行不义的，让他继续行不义；肮脏的，让他继续肮脏；公义的，让他继续行公义；圣洁的，让他继续被分别为圣。”", version_id: 6},
  {id: 163113, book: "Rev", verse: "22.012", unformatted: "“看哪，我快要来了！我带着报偿，要按照各人的行为回报每个人。", version_id: 6},
  {id: 163114, book: "Rev", verse: "22.013", unformatted: "我是‘阿尔法’，也是‘欧米伽’；是首先的，也是末后的；是开始，也是终结。", version_id: 6},
  {id: 163115, book: "Rev", verse: "22.014", unformatted: "“凡是洗净自己袍子，好使自己有权利到生命树那里，并且得以从城门进到城内的人——他们是蒙福的！", version_id: 6},
  {id: 163116, book: "Rev", verse: "22.015", unformatted: "那些犬类、行邪术的、淫乱的、杀人的、拜偶像的，以及所有爱虚假、行虚假的人，都在外面。", version_id: 6},
  {id: 163117, book: "Rev", verse: "22.016", unformatted: "“我耶稣，派遣了我的使者，为各教会向你们见证了这些事。我就是大卫的根，也是他的后裔，那明亮的晨星。”", version_id: 6},
  {id: 163118, book: "Rev", verse: "22.017", unformatted: "圣灵和新娘都说：“来吧！”那听见的人也当说：“来吧！”干渴的人也当来；愿意的人，当无偿地接受生命之水。", version_id: 6},
  {id: 163119, book: "Rev", verse: "22.018", unformatted: "我向一切听见这书上预言之话的人做见证：如果有人在这些话上加添什么，神将要把这书上所写的灾害加在他身上；", version_id: 6},
  {id: 163120, book: "Rev", verse: "22.019", unformatted: "如果有人从这书上的预言之话中删去什么，神将要从这书上所写的生命树和圣城中删去他的份。", version_id: 6},
  {id: 163121, book: "Rev", verse: "22.02", unformatted: "见证这些事的那一位说：“是的，我快要来了！”\n阿们！主耶稣啊，愿你来！", version_id: 6},
  {id: 163122, book: "Rev", verse: "22.021", unformatted: "愿主耶稣的恩典与所有的圣徒同在！阿们。", version_id: 6}
])
